(14, 'Phần 2: Tuổi trẻ luôn có sai lầm', 'phan-2-tuoi-tre-luon-co-sai-lam', 27, 'Comic_002.png', 0, '', 'Miruku Chan', '2020-09-15 08:29:55', 1, 0, 20),
(15, 'Chương 1 cafe sữa đá', 'chuong-1-cafe-sua-dja', 15, 'Untitled.png', 1, '', 'Cà Di', '2020-10-02 10:07:52', 1, 0, 13),
(16, 'Chương 2 Roger chuyện chưa kể', 'chuong-2-roger-chuyen-chua-ke', 15, 'chuyện_chưa_ke.png', 1, '', 'Cà Di', '2020-10-02 10:06:42', 1, 0, 14);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(17, 'Chương 1: Supergene', 'chuong-1-supergene', 26, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cạnh một lạch đ&aacute; nhỏ trong thung lũng, một ch&agrave;ng trai trẻ </span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(60, 64, 67); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">mặc tr&ecirc;n người chiếc &aacute;o đen</span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\"> đang cầm tr&ecirc;n tay </span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(60, 64, 67); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">một con bọ c&aacute;nh cứng c&oacute; một lớp vỏ bằng kim loại &aacute;nh l&ecirc;n sắc đen tuyền l&oacute;ng l&aacute;nh.</span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">, nh&igrave;n con bọ giống như lai giữa cua v&agrave; con bọ c&aacute;nh cứng Hercules vậy.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cầm tr&ecirc;n tay một con dao găm, Ch&agrave;ng trai nhanh ch&oacute;ng cắt phần ch&acirc;n đang ngọ nguậy của con bọ c&aacute;nh c&aacute;nh l&agrave;m lộ ra những thớ thịt trắng v&agrave; mềm.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kh&ocirc;ng một ch&uacute;t do dự, ch&agrave;ng trai h&uacute;t từng thớ thịt b&ecirc;n trong những c&aacute;i ch&acirc;n của con bọ c&aacute;nh cứng như thể anh ta đang ăn những con cua vậy, </span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(60, 64, 67); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">v&agrave; ăn từ từ đến những miếng thịt to hơn ở b&ecirc;n trong.</span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Đ&atilde; giết được bọ c&aacute;nh cứng đen. Kh&ocirc;ng c&oacute; linh hồn qu&aacute;i th&uacute; được nhận. Ăn thịt của bọ c&aacute;nh cứng đen sẽ nhận ngẫu nhi&ecirc;n từ kh&ocirc;ng đến mười điểm geno&rdquo;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Đ&atilde; ăn thịt bọ c&aacute;nh cứng đen. Kh&ocirc;ng nhận được điểm geno n&agrave;o.&rdquo;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một giọng n&oacute;i b&iacute; ẩn vang trong đầu của Han Sen, v&agrave; những con số cũng hiện l&ecirc;n.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Han Sen: Chưa tiến h&oacute;a.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trang th&aacute;i : Kh&ocirc;ng c&oacute;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thời gian sống : 200 năm.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Số điểm geno cần tiến h&oacute;a : 100 điểm.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Điểm geno nhận được : 79.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&atilde; nhận linh hồn qu&aacute;i th&uacute; : Kh&ocirc;ng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;M&igrave;nh đ&atilde; ăn một mạch ba mươi con bọ c&aacute;nh cứng đen rồi m&agrave; vẫn kh&ocirc;ng nhận điểm geno n&agrave;o. M&igrave;nh đ&atilde; phải ăn qu&aacute; nhiều thịt bọ c&aacute;nh cứng đen để tiến h&oacute;a rồi. Kh&ocirc;ng c&oacute; điểm! Khi n&agrave;o m&igrave;nh mới c&oacute; thể ho&agrave;n th&agrave;nh lần tiến h&oacute;a đầu ti&ecirc;n v&agrave; nhận trạng th&aacute;i đ&acirc;y.&rdquo; Khu&ocirc;n mặt Han Sen lộ l&ecirc;n vẻ bực bội.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hơn 100 năm trước, khi khoa học v&agrave; c&ocirc;ng nghệ đ&atilde; tiến đến tầm cao nhất, </span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(60, 64, 67); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">v&agrave; cuối c&ugrave;ng nh&acirc;n loại cũng đ&atilde; c&oacute; thể sử dụng c&ocirc;ng nghệ dịch chuyển kh&ocirc;ng gian một c&aacute;ch thuần phục.</span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\"> Điều kinh ngạc ở chỗ, khi họ cố gắng để được dịch chuyển, Họ nhận thấy rằng họ kh&ocirc;ng phải dịch chuyển đến qu&aacute; khứ hoặc tương lai. V&agrave; kh&ocirc;ng thể dịch chuyển đến một h&agrave;nh tinh kh&aacute;c m&agrave; l&agrave; một thế giới ho&agrave;n to&agrave;n kh&aacute;c ở đầu b&ecirc;n kia của cổng dịch chuyển.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một thế giới m&agrave; nh&acirc;n loại kh&oacute; c&oacute; thể tưởng tượng được. Ở trong thế giới n&agrave;y, tất cả c&aacute;c c&ocirc;ng nghệ v&agrave; phương tiện ti&ecirc;n tiến đều v&ocirc; dụng: một khẩu s&uacute;ng m&aacute;y c&ograve;n kh&ocirc;ng s&aacute;nh bằng một con dao. T&ecirc;n lửa v&agrave; vũ kh&iacute; hạt nh&acirc;n </span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(60, 64, 67); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">chẳng kh&aacute;c g&igrave; một đống sắt vụn</span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">. Kh&ocirc;ng c&oacute; thiết bị điện hay m&aacute;y m&oacute;c cũng kh&ocirc;ng hoạt động tại thế giới n&agrave;y.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&nbsp;Những sinh vật sống ở thế giới n&agrave;y cũng khủng khiếp kh&ocirc;ng k&eacute;m. Với lo&agrave;i người, Giống lo&agrave;i từng đứng </span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(60, 64, 67); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">đầu</span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\"> chuỗi thức ăn bởi v&igrave; những c&ocirc;ng nghệ hiện đại giờ lại rơi xuống tận đ&aacute;y của chuỗi thức ăn của thế giới n&agrave;y.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng khi con người giết những con vật yếu hơn m&igrave;nh v&agrave; ăn thịt ch&uacute;ng, Họ nhận ra một điều ngạc nhi&ecirc;n rằng cơ thể của họ thay đổi v&agrave; tiến h&oacute;a một c&aacute;ch nhanh ch&oacute;ng đến nổi khoa học kh&ocirc;ng thể giải th&iacute;ch được.&nbsp;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Họ c&ograve;n vui mừng hơn khi biết được rằng ở thế giới n&agrave;y, với sự tiến h&oacute;a của cơ thể, tuổi thọ c&oacute; thể k&eacute;o d&agrave;i theo tiến h&oacute;a, đ&oacute; l&agrave; một điều tuyệt vời đối với lo&agrave;i người.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Theo thời gian, c&agrave;ng ng&agrave;y c&agrave;ng c&oacute; nhiều người </span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(60, 64, 67); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">đặt</span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\"> ch&acirc;n v&agrave;o thế giới m&agrave; họ đặt t&ecirc;n l&agrave; &ldquo;Th&aacute;nh địa của thần&rdquo;, Dần dần con người l&agrave;m quen được với c&aacute;c quy tắc của thế giới n&agrave;y, săn những sinh vật v&agrave; tiến h&oacute;a trong cơ thể họ. Mức độ tiến h&oacute;a vật l&yacute; c&agrave;ng cao, tuổi thọ sẽ c&agrave;ng nhiều. Theo l&yacute; thuyết, nếu cứ tiếp tục tiến h&oacute;a, việc trở n&ecirc;n bất tử cũng c&oacute; thể.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ở thế giới n&agrave;y, Mọi thứ về khoa học v&agrave; c&ocirc;ng nghệ sẽ trở n&ecirc;n v&ocirc; dụng. Thứ m&agrave; c&oacute; thể gi&uacute;p được lo&agrave;i người c&oacute; thể sống s&oacute;t với những kỹ năng sinh tồn. Những m&ocirc;n v&otilde; thuật cổ xưa, thứ m&agrave; đ&atilde; bị l&atilde;ng qu&ecirc;n ở thế giới hiện đại th&igrave; n&oacute; lại tr&ecirc;n hữu dụng hơn bao giờ hết ở thế giới n&agrave;y.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tất cả c&aacute;c loại v&otilde; thuật cổ xưa nay đ&atilde; được ph&aacute;t triển lại, v&agrave; sau hơn 100 năm ph&aacute;t triển, những trường dạy v&otilde; thuật cũng dần h&igrave;nh th&agrave;nh v&agrave; trở n&ecirc;n phổ biến hơn.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngo&agrave;i v&otilde; thuật, ở Th&aacute;nh Địa của Thần sẽ c&oacute; một c&ocirc;ng cụ gi&uacute;p cải tiến th&ecirc;m về bản th&acirc;n đ&oacute; l&agrave; linh hồn qu&aacute;i th&uacute;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi giết một sinh vật ở Th&aacute;nh Địa của Thần, một người sẽ c&oacute; cơ hội nhận được linh hồn qu&aacute;i th&uacute; từ sinh vật m&agrave; m&igrave;nh vừa giết được. Linh hồn qu&aacute;i th&uacute; c&oacute; nhiều loại về h&igrave;nh thức lẫn h&igrave;nh dạng. Một số c&oacute; thể được triệu hồi để trợ gi&uacute;p chiến đấu, v&agrave; số kh&aacute;c c&oacute; thể biến th&agrave;nh h&igrave;nh dạng của &aacute;o gi&aacute;p hoặc vũ kh&iacute;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngo&agrave;i ra, một số linh hồn qu&aacute;i th&uacute; c&ograve;n c&oacute; thể gi&uacute;p con người biến h&igrave;nh th&agrave;nh những con qu&aacute;i th&uacute; mạnh mẽ khủng khiếp, những con chim ph&eacute;p thuật bay giữa thi&ecirc;n đường v&agrave; mặt đất, hoặc những lo&agrave;i c&ocirc;n tr&ugrave;ng đ&agrave;o dưới đất.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng tất cả những v&otilde; thuật hay linh hồn qu&aacute;i th&uacute; Han Sen chưa c&oacute; c&aacute;i g&igrave; cả.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay cả khi ở x&atilde; hội hiện đại, khoa học c&ocirc;ng nghệ ti&ecirc;n tiến cũng chỉ nằm trong tay một số &iacute;t&nbsp; người sử dụng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Han Sen chỉ vừa tốt nghiệp trung học v&agrave; bước v&agrave;o Th&aacute;nh Địa của Thần khi cậu ta bước qua tuổi 16. Những g&igrave; cậu ta học được chỉ l&agrave; những v&otilde; thuật ở cấp độ cho người mới học được.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cũng như linh hồn qu&aacute;i th&uacute;, ch&uacute;ng qu&aacute; đắt so với cậu ta đến nổi kh&ocirc;ng thể trả nổi được c&aacute;i rẻ nhất.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kh&ocirc;ng c&oacute; v&otilde; thuật v&agrave; linh hồn qu&aacute;i th&uacute;, hoặc thậm ch&iacute; những vũ kh&iacute; </span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(60, 64, 67); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">mạnh</span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\"> do con người tạo ra, Han Sen chỉ c&oacute; thể giết được những sinh vật thấp để ăn v&agrave; tiến h&oacute;a, v&agrave; cậu ta đ&atilde;&nbsp; c&oacute; khoảng thời gian kh&oacute; khăn khi ở Th&aacute;nh Địa của Thần.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng c&agrave;ng ăn nhiều thịt sinh vật cấp thấp th&igrave; c&agrave;ng ng&agrave;y cậu ta nhận &iacute;t đi những hiệu ứng tiến h&oacute;a. Đ&atilde; ở trong Th&aacute;nh Địa của Thần ba th&aacute;ng m&agrave; cậu ta chẳng thể ho&agrave;n th&agrave;nh qu&aacute; tr&igrave;nh tiến h&oacute;a vật l&yacute;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Han Sen phải giết những sinh vật mạnh hơn, nhưng ngay cả con sinh vật nguy&ecirc;n thủy yếu nhất, th&uacute; răng đồng cũng su&yacute;t lấy mạng cậu ta. Han Sen phải mất một th&aacute;ng để hồi phục với c&oacute; thể trở lại Th&aacute;nh Địa của Thần.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&uacute;c n&agrave;y, Han Sen đ&atilde; ăn tất cả c&aacute;c lo&agrave;i b&igrave;nh thường xung quanh cậu ta, v&agrave; n&oacute; kh&ocirc;ng thật sự gi&uacute;p cho cậu ta tiến h&oacute;a hơn được nữa. Nếu cậu ta kh&ocirc;ng săn những con qu&aacute;i to hơn th&igrave; cậu ta sẽ kh&ocirc;ng bao giờ m&agrave; ho&agrave;n th&agrave;nh tiến h&oacute;a được.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi Han Sen đang chuẩn bị giết một con th&uacute; răng đồng, cậu ta thấy c&oacute; thứ g&igrave; đ&oacute; đang tr&egrave;o l&ecirc;n từ những gợn s&oacute;ng nhỏ tr&ecirc;n con rạch.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cậu ta chỉ đơn thuần nghĩ chỉ l&agrave; một con bọ c&aacute;nh cứng đen, nhưng cậu ta ngay lập tức nhận ra một điều bất thường: tất cả những con bọ c&aacute;nh cứng đen đều c&oacute; những chiếc vỏ ngo&agrave;i đen kịt, nhưng con n&agrave;y lại c&oacute; lớp vỏ m&agrave;u v&agrave;ng ch&oacute;i mắt.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Han Sen nh&igrave;n chằm chằm v&agrave;o con vật đang lội ra khỏi mặt nước. N&oacute; đ&uacute;ng l&agrave; một con bọ c&aacute;nh cứng đen nhưng kh&aacute;c với những con b&igrave;nh thường do th&acirc;n thể bằng v&agrave;ng to bằng một quả b&oacute;ng rổ vậy. N&oacute; nh&igrave;n giống như một bức tượng l&agrave;m bằng v&agrave;ng, v&agrave; đ&ocirc;i mắt của n&oacute; s&aacute;ng l&ecirc;n như đ&aacute; qu&yacute; vậy. Nh&igrave;n n&oacute; giống kh&ocirc;ng phải sinh vật sống nếu như kh&ocirc;ng quan s&aacute;t n&oacute; kỹ c&agrave;ng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Tại sao con bọ c&aacute;nh cứng n&agrave;y lại kh&aacute;c qu&aacute; vậy?&rdquo; Han Sen nh&igrave;n v&agrave;o con bọ c&aacute;nh cứng bằng v&agrave;ng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cậu ta đ&atilde; quen giết những con bọ c&aacute;nh cứng v&agrave; biết được một v&agrave;i điều về ch&uacute;ng. Thị gi&aacute;c của ch&uacute;ng rất k&eacute;m nhưng b&ugrave; lại th&iacute;nh gi&aacute;c th&igrave; cực k&igrave; nhạy. Chỉ khi cậu ta đứng y&ecirc;n kh&ocirc;ng động đậy th&igrave; con bọ c&aacute;nh cứng sẽ kh&ocirc;ng ch&uacute; &yacute; đến hiện diện của cậu ta.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Han Sen nh&igrave;n chăm ch&uacute; v&agrave;o con bọ c&aacute;nh cứng, v&agrave; thật kh&ocirc;ng ngờ, n&oacute; lại b&ograve; đến gần cậu ta.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kh&ocirc;ng do dự, Khi con bọ c&aacute;nh cứng v&agrave;ng b&ograve; đến kế b&ecirc;n, cậu ta liền lấy một tay đ&egrave; n&oacute; vỏ của n&oacute; xuống c&ograve;n tay kh&aacute;c th&igrave; cầm con dao găm cắt v&agrave;o những khớp dễ vỡ của n&oacute;. Cậu ta cắt ngọt s&aacute;u nh&aacute;t dao từ theo mọi hướng để cắt bỏ những c&aacute;i ch&acirc;n của con bọ c&aacute;nh cứng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Con bọ c&aacute;nh cứng v&agrave;ng v&ugrave;ng vẫy v&agrave; lăn cơ thể b&ecirc;n dưới l&ecirc;n. Nh&acirc;n cơ hội, Han Sen liền đ&acirc;m con dao v&agrave;o phần m&agrave;u trắng tr&ecirc;n bụng của n&oacute; v&agrave; xo&aacute;y thật mạnh. Con bọ c&aacute;nh cứng v&agrave;ng đột ngột bất động lại.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c62fb89c-7fff-7247-00a2-28922832c59a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Bọ c&aacute;nh cứng hiếm đ&atilde; bị giết. Đ&atilde; nhận linh hồn qu&aacute;i th&uacute; của bọ c&aacute;nh cứng hiếm. Ăn thịt bọ c&aacute;nh cứng hiếm sẽ được từ 0 đến 10 điểm geno một c&aacute;ch ngẫu nhi&ecirc;n&rdquo;.</span></span></p>\r\n', 'Phê Kun', '2020-10-06 08:21:48', 1, 0, 20);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(18, 'Chương Khởi đầu', 'chuong-khoi-djau', 4, '', 0, '<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tr&ecirc;n N&uacute;i Abode Thần thoại, V&acirc;n huyền nhai l&agrave; khu vực chết ch&oacute;c nhất trong số Bốn Khu vực chết ch&oacute;c tr&ecirc;n lục địa M&acirc;y Azure. V&acirc;n huyền nhai thường được gọi l&agrave; nghĩa địa của Thần Chết. Trong v&ocirc; số năm, số người rơi xuống v&aacute;ch đ&aacute; n&agrave;y nhiều kh&ocirc;ng đếm xuể; kh&ocirc;ng ai sống s&oacute;t, kể cả ba chuy&ecirc;n gia được xếp hạng Thi&ecirc;n Vương với sức mạnh &aacute;p đảo.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&uacute;ng l&uacute;c n&agrave;y, ở r&igrave;a v&aacute;ch n&uacute;i n&agrave;y, một thanh ni&ecirc;n t&oacute;c đen mắt đen đang dựa v&agrave;o một tảng đ&aacute; c&oacute; chiều cao hai người cộng lại. Tr&ecirc;n bộ quần &aacute;o đen c&oacute; nhiều vết thương hở tr&ecirc;n người. Hắn ta đ&atilde; ở tr&ecirc;n tảng đ&aacute; chỉ trong v&agrave;i nhịp thở nhưng một vũng m&aacute;u đ&atilde; chảy đầy dưới ch&acirc;n hắn ta.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngực hắn phập phồng, c&oacute; thể n&oacute;i nhịp thở của hắn nặng nề đến mức khiến người ta sợ h&atilde;i. To&agrave;n th&acirc;n hắn, từng thớ thịt khẽ run l&ecirc;n chứng tỏ hắn đang kiệt sức v&agrave; mất m&aacute;u rất nhiều. Nếu kh&ocirc;ng c&oacute; tảng đ&aacute; khổng lồ n&agrave;y, c&oacute; lẽ hắn ta thậm ch&iacute; c&ograve;n kh&ocirc;ng thể tự m&igrave;nh đứng vững. Tuy nhi&ecirc;n, hai mắt hắn ta đều lạnh l&ugrave;ng, b&igrave;nh tĩnh như hai lưỡi dao lạnh lẽo, &aacute;nh l&ecirc;n vẻ hung dữ của lo&agrave;i s&oacute;i. Kh&oacute;e miệng nhếch m&eacute;p khinh bỉ cực độ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trước mặt hắn l&agrave; một đ&aacute;m đ&ocirc;ng đen tối đ&atilde; chặn mọi lối tho&aacute;t của anh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;V&acirc;n Triệt, ngươi đ&atilde; bị dồn v&agrave;o ch&acirc;n tường! Nếu ngươi tu&acirc;n theo ch&uacute;ng ta v&agrave; giao Thi&ecirc;n độc ch&acirc;u, c&oacute; lẽ ch&uacute;ng ta c&oacute; thể cho ngươi một con đường sống! &rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;H&ocirc;m nay ch&uacute;ng ta sẽ thay trời thực thi c&ocirc;ng l&yacute; để loại bỏ tai họa n&agrave;y! Nếu ngươi nhanh tay nhanh ch&oacute;ng nhượng Thi&ecirc;n độc ch&acirc;u, ch&uacute;ng ta c&oacute; thể cho ngươi một c&aacute;i chết vui sướng, nếu kh&ocirc;ng sẽ nếm thử đau đớn vạn kiếm xuy&ecirc;n tim. &rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;V&acirc;n Triệt! Đừng cứng đầu như vậy, lối tho&aacute;t duy nhất của ngươi l&agrave; giao nộp Thi&ecirc;n độc ch&acirc;u! ngươi kh&ocirc;ng xứng đ&aacute;ng với một vật th&aacute;nh như vậy. &quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Những l&agrave;n s&oacute;ng gầm th&eacute;t đến từ đ&aacute;m đ&ocirc;ng, mọi người ở đ&oacute; h&eacute;t l&ecirc;n những lời như c&ocirc;ng l&yacute; v&agrave; lẽ phải. Nếu bất cứ ai từ lục địa M&acirc;y Azure đi ngang qua, họ sẽ phải sững sờ trước cảnh tượng n&agrave;y. Đ&aacute;m đ&ocirc;ng b&oacute;ng tối n&agrave;y bao gồm những gia tộc mạnh nhất của lục địa M&acirc;y Azure. C&aacute;c thủ lĩnh của c&aacute;c gia tộc đều c&oacute; mặt, thậm ch&iacute; một số th&agrave;nh vi&ecirc;n cũ bị l&atilde;ng qu&ecirc;n cũng ở đ&oacute;. Kh&ocirc;ng qu&aacute; xa vời khi n&oacute;i rằng nếu chọn ai ra khỏi đ&aacute;m đ&ocirc;ng n&agrave;y, anh ta sẽ l&agrave; người c&oacute; thể l&agrave;m rung chuyển bất kỳ khu vực n&agrave;o.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&acirc;y giờ, tất cả đều được tập hợp lại v&igrave; thanh ni&ecirc;n đ&atilde; bị đẩy đến r&igrave;a của v&aacute;ch đ&aacute; n&agrave;y. Cụ thể, với Thi&ecirc;n độc ch&acirc;u trong tay - th&aacute;nh vật số một của lục địa M&acirc;y Azure.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&nbsp;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong khi từ từ tiếp cận, đ&aacute;m đ&ocirc;ng h&eacute;t l&ecirc;n với sự đe dọa. Khi Thi&ecirc;n độc ch&acirc;u cuối c&ugrave;ng xuất hiện một lần nữa, họ phải đối mặt với kho b&aacute;u kh&ocirc;ng thể cưỡng lại n&agrave;y một lần nữa. Sau khi đi săn trong ba ng&agrave;y, họ kh&ocirc;ng thể chờ đợi để thu hoạch th&agrave;nh quả lao động của m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;C&aacute;c ngươi&hellip; muốn c&aacute;i n&agrave;y&hellip; Thi&ecirc;n độc ch&acirc;u?&rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&nbsp;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&acirc;n Triệt cười lạnh. Khi hắn ta từ từ giơ tay phải l&ecirc;n, một quả cầu m&agrave;u xanh ngọc b&iacute;ch với &aacute;nh h&agrave;o quang mờ ảo xuất hiện trong tay hắn ta. Khoảnh khắc vi&ecirc;n ngọc ch&acirc;u n&agrave;y ph&aacute;t s&aacute;ng, mọi người đều dừng bước. Nh&igrave;n chằm chằm v&agrave;o vi&ecirc;n ngọc xanh, đ&ocirc;i mắt của họ &aacute;nh l&ecirc;n vẻ tham lam.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&nbsp;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đối với V&acirc;n Triệt, những người đủ mạnh để khiến cả thế giới khiếp sợ n&agrave;y tr&ocirc;ng thật bẩn thỉu v&agrave; ranh m&atilde;nh. Hắn từ từ ngước mắt l&ecirc;n. D&ugrave; bị dồn v&agrave;o đường c&ugrave;ng nhưng con ngươi của hắn ta vẫn &aacute;nh l&ecirc;n vẻ ki&ecirc;u ngạo v&agrave; giễu cợt. L&ograve;ng căm th&ugrave; r&otilde; r&agrave;ng hằn s&acirc;u trong mắt hắn. &ldquo;Sư phụ của ta đ&atilde; d&agrave;nh cả cuộc đời m&igrave;nh để cố gắng cứu thế giới; &ocirc;ng ấy đ&atilde; cứu v&ocirc; số người kh&ocirc;ng v&igrave; lợi &iacute;ch cũng như danh vọng, nhưng v&igrave; vi&ecirc;n Thi&ecirc;n độc ch&acirc;u n&agrave;y, c&aacute;c người từ c&aacute;i gọi l&agrave; ch&iacute;nh ph&aacute;i đ&atilde; giết sư phụ của ta bảy năm trước. &rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&nbsp;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Ta gh&eacute;t&hellip; gh&eacute;t bản th&acirc;n m&igrave;nh v&igrave; qu&aacute; v&ocirc; dụng. Rằng ta đ&atilde; kh&ocirc;ng ti&ecirc;u diệt ho&agrave;n to&agrave;n tất cả c&aacute;c ch&iacute;nh ph&aacute;i khốn nạn của c&aacute;c người trong bảy năm n&agrave;y! &quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&nbsp;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mỗi lời n&oacute;i của hắn đều chứa đựng sự căm th&ugrave; s&acirc;u sắc. D&ugrave; đ&atilde; bảy năm tr&ocirc;i qua nhưng chỉ cần nghĩ đến c&aacute;i chết của sư phụ vẫn khiến những giọt nước mắt đẫm m&aacute;u chảy d&agrave;i tr&ecirc;n kh&oacute;e mắt.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&nbsp;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&acirc;n Triệt kh&ocirc;ng biết cha mẹ m&igrave;nh l&agrave; ai khi sư phụ t&igrave;m thấy hắn, c&oacute; vẻ như hắn chỉ mới v&agrave;i ng&agrave;y tuổi. Đ&oacute; l&agrave; s&acirc;u v&agrave;o thời thanh xu&acirc;n khi sư phụ của V&acirc;n Triệt nhặt được hắn. M&acirc;y tan, gi&oacute; nhẹ, n&uacute;i thi&ecirc;ng, nước trong vắt. &Ocirc;ng đặt t&ecirc;n đứa trẻ l&agrave; V&acirc;n Triệt, với hy vọng tr&aacute;i tim của n&oacute; sẽ tinh khiết như m&acirc;y v&agrave; trong như nước; để khi lớn l&ecirc;n, hắn sẽ kế thừa t&agrave;i năng của m&igrave;nh như một người chữa bệnh để giải cứu những người đang hấp hối v&agrave; chữa trị những người bị thương với tr&aacute;i tim nh&acirc;n hậu.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&nbsp;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bất kể bệnh tật hay chấn thương nghi&ecirc;m trọng như thế n&agrave;o, Sư phụ đều c&oacute; thể giải quyết tất cả. Điều n&agrave;y l&agrave; do Thi&ecirc;n độc ch&acirc;u ẩn trong cơ thể &ocirc;ng ta. Ba từ &quot;Thi&ecirc;n độc ch&acirc;u&quot; c&oacute; nghĩa l&agrave; vi&ecirc;n ngọc trai n&agrave;y c&oacute; độc t&iacute;nh cao, nhưng thuốc v&agrave; chất độc c&oacute; c&ugrave;ng nguồn gốc. Sư phụ kh&ocirc;ng bao giờ sử dụng chất độc của n&oacute;; &ocirc;ng chỉ sử dụng thuật giả kim để chiết xuất, nấu chảy v&agrave; cuối c&ugrave;ng l&agrave; sản xuất thuốc th&aacute;nh để cứu sống h&agrave;ng triệu người. &Ocirc;ng ấy đ&atilde; dạy kỹ tất cả c&aacute;c kỹ năng y học của m&igrave;nh cho V&acirc;n Triệt, nhưng bảy năm trước, lời đồn về Thi&ecirc;n độc ch&acirc;u của &ocirc;ng ấy đ&atilde; được lan truyền. &Ocirc;ng ta đưa n&oacute; cho V&acirc;n Triệt v&agrave; bảo hắn ta h&atilde;y trốn tho&aacute;t. &Iacute;t l&acirc;u sau, &Ocirc;ng ta chết dưới tay của c&aacute;c gi&aacute;o ph&aacute;i lớn.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&nbsp;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi biết tin sư phụ qua đời, V&acirc;n Triệt đ&atilde; kh&oacute;c suốt ba ng&agrave;y ba đ&ecirc;m. Cội rễ của l&ograve;ng hận th&ugrave; đ&atilde; ăn s&acirc;u v&agrave;o tr&aacute;i tim hắn v&agrave; hắn kh&ocirc;ng c&ograve;n thực h&agrave;nh trong c&aacute;c loại thuật chữa bệnh nữa. Thay v&agrave;o đ&oacute;, hắn ta hấp thụ chất độc từ vi&ecirc;n ngọc trai; trả th&ugrave; trở th&agrave;nh niềm tin duy nhất của hắn. Sau bảy năm, hắn đ&atilde; l&agrave;m chủ được độc dược v&agrave; nhe nanh trả th&ugrave;. Trong v&ograve;ng chưa đầy mười ng&agrave;y, c&aacute;c chất độc chỉ l&acirc;y lan h&agrave;ng ng&agrave;n dặm, giết chết kh&ocirc;ng &iacute;t người, nhưng cũng đủ g&acirc;y hoảng sợ v&agrave; kinh h&atilde;i trong suốt lục địa M&acirc;y Azure. N&oacute; sở hữu sức mạnh v&ocirc; c&ugrave;ng mạnh mẽ, v&igrave; vậy n&oacute; đ&atilde; thu h&uacute;t người của c&aacute;c ph&aacute;i. Điều n&agrave;y bắt đầu cuộc săn l&ugrave;ng của V&acirc;n Triệt để c&oacute; được Thi&ecirc;n độc ch&acirc;u, dẫn đến t&igrave;nh thế l&uacute;c n&agrave;y.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&nbsp;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn ta trừng mắt nh&igrave;n mọi người trong tầm mắt bằng &aacute;nh mắt căm hận rồi cười lạnh. Ngay khi tiếng cười của hắn ta đạt đến mức lạnh như băng, hắn ta gầm l&ecirc;n, &ldquo;Đồ khốn kiếp, m&agrave;y muốn Thi&ecirc;n độc ch&acirc;u của tao&hellip; M&agrave;y. C&aacute;c ngươi. Tất cả. Mơ mộng !! &rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&nbsp;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi tuy&ecirc;n bố s&acirc;u sắc n&agrave;y, V&acirc;n Triệt đột nhi&ecirc;n giơ tay n&eacute;m Thi&ecirc;n độc ch&acirc;u v&agrave;o miệng. Sau đ&oacute; hắn đẩy vi&ecirc;n ngọc trai xuống cổ họng v&agrave; n&oacute; ngay lập tức lao thẳng v&agrave;o bụng hắn.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;G&igrave;&hellip;. Ngươi đang l&agrave;m g&igrave; đấy!&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Hắn ta thực sự ... đ&atilde; nuốt Thi&ecirc;n độc ch&acirc;u!&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;V&acirc;n Triệt! C&oacute; muốn chết thảm như vậy kh&ocirc;ng! &rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Kh&ocirc;ng sao đ&acirc;u, kh&ocirc;ng c&oacute; g&igrave; to t&aacute;t, ch&uacute;ng ta sẽ giết hắn v&agrave; lấy ngọc!&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thi&ecirc;n độc ch&acirc;u x&acirc;m nhập v&agrave;o cơ thể hắn ta, nhưng chất độc kh&ocirc;ng lan ra trong cơ thể hắn ta v&agrave; giết chết hắn như họ nghĩ. Một &aacute;nh s&aacute;ng xanh yếu ớt ph&aacute;t ra từ bề mặt cơ thể hắn.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Giết hắn ngay b&acirc;y giờ! Nếu kh&ocirc;ng, Thi&ecirc;n độc ch&acirc;u c&oacute; thể thay đổi b&ecirc;n trong cơ thể hắn ta. Đ&oacute; sẽ l&agrave; một vấn đề lớn! &rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Rầm rầm rầm, h&agrave;ng chục người ở tuyến đầu c&ugrave;ng l&uacute;c đi tới chỗ V&acirc;n Triệt. Nh&igrave;n b&oacute;ng d&aacute;ng của những người m&agrave; m&igrave;nh muốn ti&ecirc;u diệt, V&acirc;n Triệt bắt đầu cười. Tiếng cười của hắn ta yếu ớt v&agrave; kh&ocirc; khốc nhưng vẫn rất ki&ecirc;u ngạo, &ldquo;Ta kh&ocirc;ng c&oacute; khả năng giết c&aacute;c người, nhưng đừng nghĩ rằng c&aacute;c người c&oacute; thể giết ta! Những thứ r&aacute;c rưởi n&agrave;y kh&ocirc;ng xứng với Thi&ecirc;n độc ch&acirc;u v&agrave; c&agrave;ng kh&ocirc;ng xứng đ&aacute;ng để giết ta. Nếu ta chết, ta sẽ chết bởi ch&iacute;nh tay m&igrave;nh! Hahahaha&hellip; &rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi cười V&acirc;n Triệt d&ugrave;ng từng ch&uacute;t sức lực cuối c&ugrave;ng của m&igrave;nh để nhảy về ph&iacute;a sau.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ngăn hắn ta lại !!!&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave;i b&agrave;n tay vươn tới V&acirc;n Triệt sau khi nhận ra &yacute; định của hắn nhưng thậm ch&iacute; kh&ocirc;ng thể nắm lấy nửa b&oacute;ng của hắn. Họ chỉ c&oacute; thể đứng nh&igrave;n thi thể hắn rơi tự do xuống thung lũng.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&acirc;n huyền nhai, n&oacute; rất ph&ugrave; hợp với ng&ocirc;i mộ của ta, V&acirc;n Triệt &hellip;&hellip; (TL: V&acirc;n = M&acirc;y)</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kh&ocirc;ng c&oacute; g&igrave; để n&iacute;u k&eacute;o ta v&igrave; ta kh&ocirc;ng c&ograve;n r&agrave;ng buộc tr&ecirc;n thế giới n&agrave;y. Thật kh&ocirc;ng may&hellip; ta kh&ocirc;ng thể trả th&ugrave; cho sư phụ của m&igrave;nh&hellip; cũng như kh&ocirc;ng t&igrave;m thấy cha mẹ ruột của m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.656;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-c7bef902-7fff-ee8f-1a47-4efb8b5c5ec4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&acirc;n Triệt nhẹ nh&agrave;ng cầm mặt d&acirc;y chuyền bạc trước ngực. Đ&oacute; l&agrave; thứ duy nhất hắn mang tr&ecirc;n người khi sư phụ t&igrave;m thấy hắn. Tiếng gi&oacute; g&agrave;o th&eacute;t lướt qua tai hắn, hắn từ từ nhắm mắt lại v&agrave; để cơ thể lao s&acirc;u v&agrave;o vực thẳm tăm tối dường như v&ocirc; tận.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	&nbsp;</p>\r\n', 'Cà Di', '2020-09-25 11:10:25', 1, 0, 510),
(19, 'Chương 1 Câu truyện bên lề', 'chuong-1-cau-truyen-ben-le-1', 17, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-da069dbf-7fff-f2b0-aa72-2122f572268a\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">T&ocirc;i được gọi l&agrave; &quot;Đạo Ho&agrave;i&quot; v&igrave; đ&oacute; l&agrave; t&ecirc;n của t&ocirc;i. T&ecirc;n của t&ocirc;i cũng l&agrave; c&oacute; nghĩa l&agrave; t&acirc;m kiếm.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-da069dbf-7fff-f2b0-aa72-2122f572268a\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">N&oacute; c&oacute; nghĩa l&agrave; t&ocirc;i sẽ lu&ocirc;n mang theo lưỡi kiếm. Cha mẹ t&ocirc;i đặt cho t&ocirc;i c&aacute;i t&ecirc;n n&agrave;y bởi v&igrave; t&ocirc;i được sinh ra với một thanh kiếm thần th&aacute;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-da069dbf-7fff-f2b0-aa72-2122f572268a\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">T&ocirc;i đ&atilde; bắt đầu luyện tập với lưỡi kiếm năm một tuổi v&agrave; đ&atilde; c&oacute; một số th&agrave;nh t&iacute;ch khi l&ecirc;n t&aacute;m. T&ocirc;i bắt đầu kiểm tra đao của m&igrave;nh năm mười lăm tuổi v&agrave; c&oacute; thể trở th&agrave;nh một vị thần v&agrave;o năm ba mươi. Mặc d&ugrave; vậy, t&ocirc;i đ&atilde; được gọi l&agrave; kiếm th&aacute;nh khi t&ocirc;i l&ecirc;n mười. Tất nhi&ecirc;n, t&ocirc;i chưa thực sự trở th&agrave;nh một người như vậy cho đến khi t&ocirc;i ba mươi tuổi.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-da069dbf-7fff-f2b0-aa72-2122f572268a\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tr&ecirc;n thế giới n&agrave;y, mọi người lu&ocirc;n tự nhận m&igrave;nh l&agrave; kiếm th&aacute;nh nhưng trong mắt t&ocirc;i, tất cả đều chỉ l&agrave; những đứa trẻ chơi với kiếm đồ chơi của m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-da069dbf-7fff-f2b0-aa72-2122f572268a\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thanh kiếm l&agrave; vũ kh&iacute; cũng như người bạn th&acirc;n nhất của t&ocirc;i. Quan trọng nhất, đ&oacute; l&agrave; cuộc sống của t&ocirc;i.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-da069dbf-7fff-f2b0-aa72-2122f572268a\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mặc d&ugrave; t&ocirc;i đ&atilde; hơn ba trăm tuổi sau khi trở th&agrave;nh vĩnh sinh đế, nhưng đ&acirc;y kh&ocirc;ng phải l&agrave; th&agrave;nh t&iacute;ch đ&aacute;ng tự h&agrave;o nhất của t&ocirc;i so với việc trở th&agrave;nh kiếm th&aacute;nh. Đ&oacute; l&agrave; sự theo đuổi suốt đời của t&ocirc;i. Vĩnh sinh đế chỉ l&agrave; thứ m&agrave; t&ocirc;i c&oacute; được sau khi v&agrave;o đạo với thanh kiếm của m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-da069dbf-7fff-f2b0-aa72-2122f572268a\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người đời n&oacute;i rằng vĩnh sinh đế l&agrave; đỉnh cao của đại đạo nhưng trong mắt t&ocirc;i, đạo của kiếm l&agrave; v&ocirc; hạn.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-da069dbf-7fff-f2b0-aa72-2122f572268a\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">N&oacute; c&oacute; cuộc sống ri&ecirc;ng của n&oacute;, đẹp hơn bất kỳ người đẹp n&agrave;o tr&ecirc;n thế giới n&agrave;y. Mặc d&ugrave; t&ocirc;i cũng được mệnh danh l&agrave; phu qu&acirc;n của người phụ nữ đẹp nhất trong cửu giới, nhưng vẫn kh&ocirc;ng xinh như lưỡi dao trong tay.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-da069dbf-7fff-f2b0-aa72-2122f572268a\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong cuộc đời t&ocirc;i, nếu t&ocirc;i c&oacute; thể giải quyết điều g&igrave; đ&oacute; bằng một nh&aacute;t ch&eacute;m, th&igrave; một nh&aacute;t ch&eacute;m thứ hai sẽ kh&ocirc;ng bao giờ cần đến. Nhưng t&ocirc;i kh&ocirc;ng biết tại sao cư d&acirc;n của thế giới n&agrave;y chỉ nhớ đến t&ocirc;i như một vĩnh sinh đế m&agrave; kh&ocirc;ng phải l&agrave; thanh kiếm bất khả chiến bại của t&ocirc;i. C&oacute; lẽ đ&oacute; l&agrave; bởi v&igrave; t&ocirc;i đến từ Thi&ecirc;n Ho&agrave;ng m&ocirc;n với tư c&aacute;ch l&agrave; vị ho&agrave;ng đế thứ hai của n&oacute;. Mọi người đ&atilde; ghi nhận xuất th&acirc;n ho&agrave;ng tộc của t&ocirc;i nhưng kh&ocirc;ng phải l&agrave; con dao kh&ocirc;ng thể ngăn cản của t&ocirc;i!</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-da069dbf-7fff-f2b0-aa72-2122f572268a\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">T&ocirc;i l&agrave; Bất Diệt Đế Đạo Ho&agrave;i, sinh ra c&ugrave;ng đao. Với n&oacute; trong tay, t&ocirc;i kh&ocirc;ng c&oacute; g&igrave; phải sợ tr&ecirc;n thế giới n&agrave;y. T&ocirc;i thậm ch&iacute; sẽ chiến đấu chống lại vĩnh sinh đế đ&aacute;ng k&iacute;nh H&agrave;o Hải miễn l&agrave; lưỡi kiếm của t&ocirc;i ở b&ecirc;n cạnh t&ocirc;i!</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-da069dbf-7fff-f2b0-aa72-2122f572268a\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ugh, nhưng lưỡi kiếm của t&ocirc;i đ&atilde; bị phu nh&acirc;n t&ocirc;i lấy mất đ&ecirc;m nay&hellip;. Đ&acirc;y l&agrave; l&yacute; do tại sao t&ocirc;i ở đ&acirc;y tr&ograve; chuyện với mọi người&hellip;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-da069dbf-7fff-f2b0-aa72-2122f572268a\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ồ, t&ocirc;i cũng qu&ecirc;n mất một thứ quan trọng. Vị vĩnh sinh đế n&agrave;y, kh&ocirc;ng, kiếm th&aacute;nh n&agrave;y l&agrave; mỹ nh&acirc;n xinh đẹp thứ hai trong cửu giới nhưng sự thật l&agrave; t&ocirc;i vẫn nghĩ rằng t&ocirc;i đẹp hơn người nương tử đứng số một của m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-da069dbf-7fff-f2b0-aa72-2122f572268a\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Haha, t&ocirc;i chỉ đ&ugrave;a th&ocirc;i. L&agrave;m sao một vĩnh sinh đế xinh đẹp v&agrave; t&agrave;i giỏi như t&ocirc;i lại c&oacute; thể c&oacute; nương tử? Nhưng thay v&agrave;o đ&oacute; t&ocirc;i c&oacute; một t&ecirc;n khốn. Đ&uacute;ng, ch&iacute;nh l&agrave; t&ecirc;n khốn đ&oacute; lu&ocirc;n trốn tr&aacute;nh. Một ng&agrave;y n&agrave;o đ&oacute;, t&ocirc;i sẽ đối ph&oacute; với hắn ta&hellip;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-da069dbf-7fff-f2b0-aa72-2122f572268a\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&ecirc;m nay trăng kh&aacute; tr&ograve;n. Kh&ocirc;ng biết c&oacute; phải t&ecirc;n khốn đ&oacute; lại giả g&aacute;i; một c&ocirc; g&aacute;i lừa đảo nhỏ n&oacute;i: &quot;N&agrave;y, ngươi v&agrave; ta được kết nối bởi số phận ...&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n', 'Cà Di', '2020-09-25 11:11:26', 1, 0, 101);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(20, 'Chương 1: Cửu Dương Xuyên Thời Gian (Phần 1)', 'chuong-1-cuu-duong-xuyen-thoi-gian-phan-1', 1, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Ta đang ở đ&acirc;u? Chẳng phải ta đ&atilde; chết ch&aacute;y sao? &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n thấy m&igrave;nh ở một thế giới ho&agrave;n to&agrave;n xa lạ. Hắn ta đang ở trong một c&aacute;i hang, một c&aacute;i hang đặc biệt s&acirc;u. Lớp băng trong như pha l&ecirc; bao phủ mọi ng&oacute;c ng&aacute;ch.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn ta đ&atilde; ở trong k&yacute; t&uacute;c x&aacute; của một gi&aacute;o vi&ecirc;n nữ ở Đại học Thủy Mạc v&agrave;i ph&uacute;t trước đ&oacute; v&agrave; l&agrave;m t&igrave;nh với c&ocirc; gi&aacute;o ki&ecirc;m bạn g&aacute;i tuyệt đẹp của m&igrave;nh, L&yacute; B&iacute;ch Qu&acirc;n. Khi họ đạt đến cao tr&agrave;o, to&agrave;n bộ cơ thể hắn đ&atilde; s&ocirc;i l&ecirc;n. Sau đ&oacute;, n&oacute; bốc ch&aacute;y, v&agrave; hắn bất tỉnh v&igrave; cơn đau dữ dội.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đến khi tỉnh dậy th&igrave; hắn đ&atilde; ở trong hang động kỳ lạ n&agrave;y rồi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n năm nay bước sang tuổi hai mươi v&agrave; l&agrave; sinh vi&ecirc;n năm hai. Cha của hắn ta l&agrave; một fan h&acirc;m mộ lớn của Kim Dung, v&igrave; vậy &ocirc;ng đ&atilde; đặt cho hắn ta một c&aacute;i t&ecirc;n độc đo&aacute;n như Dương Định Thi&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">(Lưu &yacute;: Kim Dung l&agrave; một tiểu thuyết gia Trung Quốc, người nổi tiếng với những cuốn tiểu thuyết phong lưu. T&ecirc;n của Dương Định Thi&ecirc;n, Định Thi&ecirc;n, tạm dịch l&agrave; &ldquo;chống lại trời&rdquo; hoặc &ldquo;tr&ecirc;n đỉnh trời&rdquo;, mang một &yacute; nghĩa kh&aacute; độc đo&aacute;n trong tiếng Trung. Một nghĩa kh&aacute;c của &ldquo;Ding&rdquo; l&agrave; &ldquo;Chọc&rdquo; n&ecirc;n điều n&agrave;y c&oacute; thể khiến người kh&aacute;c nghĩ theo c&aacute;ch khi&ecirc;u d&acirc;m&hellip;)</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn ta trong kh&aacute; tuấn mỹ, th&ocirc;ng minh, t&igrave;nh cảm, tốt bụng v&agrave; một ch&uacute;t tinh nghịch. Sức mạnh của hắn l&agrave; do Hỏa Dương kh&iacute; mạnh mẽ b&ecirc;n trong hắn. Từ khi c&ograve;n nhỏ, hắn chưa bao giờ sợ lạnh v&agrave; thậm ch&iacute; c&oacute; thể chịu được nhiệt độ dưới 0 độ C khi chỉ mặc một chiếc &aacute;o sơ mi. Hắn ta cũng rất kh&oacute; tăng c&acirc;n d&ugrave; hắn ta ăn rất nhiều v&agrave; lu&ocirc;n th&egrave;m ăn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tất nhi&ecirc;n, đ&acirc;y kh&ocirc;ng phải l&agrave; những điều xấu. D&ugrave; vậy, bố mẹ hắn đ&atilde; đưa hắn đến v&ocirc; số b&aacute;c sĩ v&agrave; họ đều đưa ra kết luận giống nhau. D&ugrave; nhẹ c&acirc;n nhưng hắn ho&agrave;n to&agrave;n khỏe mạnh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một đạo sĩ luộm thuộm đi du lịch về ph&iacute;a T&acirc;y đ&atilde; t&igrave;nh cờ gặp Dương Định Thi&ecirc;n khi người n&agrave;y mới t&aacute;m tuổi. &Ocirc;ng n&oacute;i: &ldquo;Ta kh&ocirc;ng bao giờ nghĩ rằng Cửu Dương Thần huyền thoại thực sự tồn tại&rdquo;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Vị đạo sĩ đ&atilde; n&oacute;i với Dương Định Thi&ecirc;n rằng Hỏa Dương kh&iacute; trong Cửu Dương th&acirc;n của hắn ta vượt xa những người kh&aacute;c nhiều lần. Hắn kh&ocirc;ng bao giờ c&oacute; thể quan hệ t&igrave;nh dục hoặc thậm ch&iacute; thủ d&acirc;m v&igrave; n&oacute;. Ngay cả khi hắn ta ki&ecirc;ng tất cả c&aacute;c hoạt động t&igrave;nh dục, Hỏa Dương kh&iacute; của hắn ta c&oacute; thể vẫn sẽ ti&ecirc;u thụ hắn ta trước khi hắn ta bước sang tuổi 25.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Gia đ&igrave;nh của Dương Định Thi&ecirc;n nghĩ rằng vị đạo sĩ đ&atilde; n&oacute;i dối, v&igrave; vậy họ chỉ phớt lờ hắn ta.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n bắt đầu nhận ra mọi thứ đang trở n&ecirc;n tồi tệ hơn khi hắn bước qua tuổi dậy th&igrave;. Bất cứ khi n&agrave;o hắn ta th&egrave;m muốn một c&ocirc; g&aacute;i trong đời thực, những bức ảnh hoặc video, một ngọn lửa mạnh mẽ sẽ b&ugrave;ng ch&aacute;y trong hắn ta v&agrave; nhiệt độ cơ thể của hắn ta sẽ tăng vọt.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">T&igrave;nh h&igrave;nh c&agrave;ng n&ecirc;n căng thẳng hơn. Dương Định Thi&ecirc;n lần đầu ti&ecirc;n hẹn h&ograve; v&agrave; &acirc;u yếm một c&ocirc; g&aacute;i khi hắn mười t&aacute;m tuổi. Hắn ngất xỉu v&igrave; sốt cao hơn 42 độ C sau chưa đầy một ph&uacute;t. Bạn g&aacute;i của hắn đ&atilde; hoảng sợ đến mức h&eacute;t l&ecirc;n v&agrave; gọi xe cấp cứu, may mắn l&agrave; đ&atilde; cứu sống hắn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chỉ sau đ&oacute;, Dương Định Thi&ecirc;n mới nhớ lại vị đạo sĩ đ&atilde; cảnh b&aacute;o hắn ta kh&ocirc;ng được hoạt động t&igrave;nh dục.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lần thủ d&acirc;m đầu ti&ecirc;n của hắn ta đ&atilde; chứng minh rằng vị đạo sĩ c&oacute; thể đ&atilde; n&oacute;i đ&uacute;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n lu&ocirc;n cố gắng kh&ocirc;ng quan hệ t&igrave;nh dục trước khi hắn bước sang tuổi hai mươi. Hắn kh&ocirc;ng thể giữ n&oacute; l&acirc;u hơn nữa. Bạn g&aacute;i của hắn ta qu&aacute; sexy đến nỗi c&ocirc; đ&atilde; ti&ecirc;u diệt được h&agrave;ng ph&ograve;ng ngự của hắn ta. V&agrave;o đ&ecirc;m sinh nhật của hắn, cuối c&ugrave;ng c&ocirc; ấy cũng đ&atilde; vượt qua được tuyến ph&ograve;ng thủ cuối c&ugrave;ng của hắn với sự trợ gi&uacute;p của rượu, ma t&uacute;y, sự khi&ecirc;u gợi v&agrave; th&egrave;m kh&aacute;t. Họ đ&atilde; bắt đầu một phi&ecirc;n ướt &aacute;t tr&ecirc;n giường.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kết quả đ&atilde; chứng minh vị đạo sĩ đ&uacute;ng. Dương Định Thi&ecirc;n, sở hữu Cửu Dương Th&acirc;n, đ&atilde; thực sự b&ugrave;ng ch&aacute;y khi hắn ta cố gắng gần gũi thể x&aacute;c. Cha hắn dường như c&oacute; thể đo&aacute;n trước được tương lai khi đặt t&ecirc;n cho anh. Dương Định Thi&ecirc;n thực sự c&oacute; t&ecirc;n l&agrave; Dương&hellip;, Thăng Thi&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">(TL note: C&oacute; thể hiểu l&agrave; l&ecirc;n thi&ecirc;n đường).</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay khi Dương Định Thi&ecirc;n bị thi&ecirc;u rụi đến hư v&ocirc;, bất tỉnh v&agrave; ch&igrave;m v&agrave;o b&oacute;ng tối v&ocirc; tận, hắn tỉnh dậy v&agrave; thấy m&igrave;nh đang ở trong một m&ocirc;i trường ho&agrave;n to&agrave;n xa lạ. Hắn ta ở s&acirc;u trong một hang động băng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Đ&acirc;y l&agrave; nơi qu&aacute;i quỷ n&agrave;o vậy?&quot; Dương Định Thi&ecirc;n hơi co người lại. Hắn kh&ocirc;ng thể tin rằng m&igrave;nh thực sự cảm thấy lạnh. Hắn ho&agrave;n to&agrave;n kh&ocirc;ng biết cảm gi&aacute;c lạnh lẽo khi lớn l&ecirc;n l&agrave; như thế n&agrave;o. B&acirc;y giờ hắn đ&atilde; cảm nhận được.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Đ&acirc;y c&oacute; thể l&agrave; một hang động Bắc Cực kh&ocirc;ng?&quot; Dương Định Thi&ecirc;n tự hỏi m&igrave;nh. N&oacute; thực sự giống như Bắc Cực v&igrave; hắn chưa bao giờ cảm thấy lạnh ngay cả khi nhiệt độ dưới 0 độ từ &acirc;m 20 đến 30 độ ở qu&ecirc; hương ph&iacute;a đ&ocirc;ng bắc của hắn. Ở đ&acirc;y phải &iacute;t nhất l&agrave; &acirc;m 50 đến 60 độ C.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n tr&egrave;o l&ecirc;n ch&acirc;n, khỏa th&acirc;n khi bắt đầu kh&aacute;m ph&aacute; hang động. Suy nghĩ cứ quanh quẩn trong đầu hắn tại sao hắn vẫn chưa bị chết thi&ecirc;u m&agrave; lại xuất hiện ở đ&acirc;y một c&aacute;ch kh&oacute; hiểu. Hắn ta c&oacute; lẽ sẽ chết c&oacute;ng chỉ trong v&agrave;i ph&uacute;t nếu kh&ocirc;ng sở hữu Cửu Dương Th&acirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hang động s&acirc;u đến mức v&ocirc; vọng, chỉ c&oacute; thể nh&igrave;n thấy một mảnh bầu trời. Chắc hẳn b&ecirc;n ngo&agrave;i trời đ&atilde; s&aacute;ng. Những tia s&aacute;ng lọt qua lỗ nhỏ v&agrave; phản xạ khỏi lớp băng để chiếu s&aacute;ng to&agrave;n bộ hang động.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n c&oacute; thể n&oacute;i rằng hang động s&acirc;u &iacute;t nhất một ngh&igrave;n m&eacute;t. C&aacute;c bức tường thẳng đứng, nhẵn b&oacute;ng sẽ kh&ocirc;ng thể ph&aacute; bỏ nếu kh&ocirc;ng c&oacute; c&ocirc;ng cụ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&aacute;y hang chỉ khoảng một trăm m&eacute;t vu&ocirc;ng th&ocirc;i</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;&Ocirc;i ch&uacute;a ơi! Đ&acirc;y kh&ocirc;ng phải l&agrave; Tr&aacute;i đất. Tr&aacute;i đất chỉ c&oacute; một mặt trời, nhưng c&oacute; hai ở đ&acirc;y. C-nơi n&agrave;y l&agrave; nơi qu&aacute;i quỷ n&agrave;o vậy? Một h&agrave;nh tinh xa lạ? Một thế giới kh&aacute;c?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Ta đ&atilde; vượt qua thời gian chưa? Ta đ&atilde; thực sự nhảy qua thời gian v&agrave; kh&ocirc;ng gian chưa? &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n lại gục tr&ecirc;n mặt băng. Hắn cảm thấy thế giới quay cuồng như thể hắn sắp ngất đi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Những người kh&aacute;c xuy&ecirc;n kh&ocirc;ng do bị điện giật, thi&ecirc;n tai, bị s&eacute;t đ&aacute;nh hoặc tai nạn m&aacute;y bay l&agrave; những nguy&ecirc;n nh&acirc;n phổ biến nhất đằng sau việc xuy&ecirc;n kh&ocirc;ng. Chỉ c&oacute; hắn ta, Dương Định Thi&ecirc;n, đ&atilde; su&yacute;t chết v&agrave; xuy&ecirc;n kh&ocirc;ng khi đang l&agrave;m t&igrave;nh. Phương ph&aacute;p du h&agrave;nh thời gian n&agrave;y chưa từng c&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Bố, nh&igrave;n c&aacute;i t&ecirc;n đẫm m&aacute;u m&agrave; bố đ&atilde; đặt cho con trai m&igrave;nh đi! Dương Định Thi&ecirc;n! Đ&uacute;ng l&agrave; một c&aacute;i t&ecirc;n độc đo&aacute;n! Con kh&ocirc;ng đủ may mắn để thưởng thức c&aacute;i t&ecirc;n n&agrave;y, nhưng n&oacute; đ&atilde; đưa con đến một thế giới kh&aacute;c!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">********************</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Vĩnh biệt mẹ v&agrave; bố, vĩnh biệt bạn b&egrave; của ta v&agrave; tạm biệt Tr&aacute;i đất!&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Bố mẹ đừng qu&aacute; đau l&ograve;ng. Cả hai&nbsp; người vẫn c&ograve;n trẻ, v&igrave; vậy c&oacute; lẽ hai người c&oacute; thể c&oacute; th&ecirc;m một đứa con &rdquo;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;B&iacute;ch Qu&acirc;n, c&oacute; lẽ anh đ&atilde; chết ch&aacute;y với em, v&igrave; vậy ch&uacute;ng ta c&oacute; thể được coi l&agrave; đ&ocirc;i t&igrave;nh nh&acirc;n đ&atilde; chết trong một trận hỏa hoạn.&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n tựa v&agrave;o tảng băng ở trung t&acirc;m hang động v&agrave; cuối c&ugrave;ng chấp nhận sự thật rằng m&igrave;nh đ&atilde; xuy&ecirc;n kh&ocirc;ng đến một thế giới kh&aacute;c. Hắn cố n&eacute;n nước mắt khi nhớ lại qu&aacute; khứ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Kh&ocirc;ng, ta kh&ocirc;ng thể tiếp tục đau buồn như thế n&agrave;y. Người xuy&ecirc;n kh&ocirc;ng trong tiểu thuyết đạt được th&agrave;nh c&ocirc;ng lớn. Ta kh&ocirc;ng thể chết đ&oacute;i sau khi xuy&ecirc;n kh&ocirc;ng. &quot; Dương Định Thi&ecirc;n đứng l&ecirc;n. Hắn muốn t&igrave;m một lối tho&aacute;t.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cuối c&ugrave;ng, hắn ta đ&atilde; vật lộn trong nhiều giờ v&agrave; kh&ocirc;ng mở rộng được nửa thước tr&ecirc;n tường. Băng qu&aacute; trơn v&agrave; cứng. Chẳng c&oacute; g&igrave; hắn c&oacute; thể l&agrave;m. Hắn lại t&igrave;m kiếm một lối tho&aacute;t, nhưng kết quả vẫn kh&ocirc;ng kh&aacute;c g&igrave;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&uacute; sốc v&agrave; nỗi buồn trong v&agrave;i giờ qua c&ugrave;ng với cuộc đấu tranh khiến hắn kiệt sức. Hắn ta quay trở lại giữa hang v&agrave; lại dựa v&agrave;o tảng băng, nghỉ ngơi trong khi nghĩ ra một lối tho&aacute;t kh&aacute;c.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn chỉ định nghỉ ngơi một l&uacute;c, nhưng cuối c&ugrave;ng hắn lại lăn ra ngủ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thật may mắn khi hắn ta sở hữu được Cửu Dương Th&acirc;n hiếm c&oacute;. Hắn đ&atilde; t&iacute;ch tụ Hỏa Dương kh&iacute; trong cơ thể gần hai chục năm, v&agrave; chỉ một &iacute;t đ&atilde; bị r&ograve; rỉ ra ngo&agrave;i khi hắn l&agrave;m t&igrave;nh với L&yacute; B&iacute;ch Qu&acirc;n ở Tr&aacute;i Đất. Hắn hầu như kh&ocirc;ng cảm thấy lạnh khi ngủ khỏa th&acirc;n trong hang động lạnh lẽo. N&oacute; sẽ kh&ocirc;ng thể cho hắn ta bị đ&oacute;ng băng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cơn đ&oacute;i của Dương Định Thi&ecirc;n đ&atilde; đ&aacute;nh thức hắn ta một l&uacute;c sau. Th&acirc;n thể của hắn y&ecirc;u cầu nhiều thức ăn hơn so với những người b&igrave;nh thường, v&agrave; n&oacute; đ&atilde; trở n&ecirc;n đau đớn sau v&agrave;i chục giờ kh&ocirc;ng ăn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chỉ c&oacute; băng trong địa ngục n&agrave;y. Kh&ocirc;ng c&oacute; thức ăn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n đứng lại v&agrave; thấy hai mặt trời xuất hiện trong c&aacute;i lỗ ph&iacute;a tr&ecirc;n hang động. Hắn đ&atilde; ngủ &iacute;t nhất cả ng&agrave;y, nhưng hắn kh&ocirc;ng chắc đ&oacute; l&agrave; bao l&acirc;u tr&ecirc;n thế giới n&agrave;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi đứng dậy, Dương Định Thi&ecirc;n nhận thấy rằng khối băng ph&iacute;a sau hắn ta c&oacute; một ch&uacute;t kh&aacute;c biệt.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cơ thể giống như c&aacute;i l&ograve; của hắn đ&atilde; l&agrave;m tan chảy n&oacute; trong hơn mười giờ hắn ngủ gần n&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn kh&ocirc;ng ngạc nhi&ecirc;n. Vi&ecirc;n đ&aacute; m&agrave;u xanh pha l&ecirc; trở n&ecirc;n trong suốt hơn khi n&oacute; tan chảy, v&agrave; dường như c&oacute; thứ g&igrave; đ&oacute; b&ecirc;n trong n&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d9392f0b-7fff-b258-1aff-422f558d3692\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&oacute; c&aacute;i g&igrave; đ&oacute; b&ecirc;n trong vi&ecirc;n băng ?!</span></span></p>\r\n', 'Cà Di', '2020-10-02 09:35:51', 1, 0, 210);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(21, 'Chương 1: Chuyên gia vũ khí - Lãnh Thiên', 'chuong-1-chuyen-gia-vu-khi-lanh-thien-1', 5, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong một căn ph&ograve;ng kiểu cổ, L&atilde;nh Thi&ecirc;n giật m&igrave;nh tỉnh giấc.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Kh&ocirc;ng hay rồi ta đ&atilde; rơi v&agrave;o một c&aacute;i bẫy! &quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(60, 64, 67); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn thậm ch&iacute; c&ograve;n kh&ocirc;ng c&oacute; thời gian để mở mắt. Theo phản xạ, hắn ta đập tay xuống đất, định sử dụng lực phản lực tạo ra để nhảy l&ecirc;n v&agrave; chạy trốn.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&acirc;y l&agrave; một phản ứng theo bản năng của một th&agrave;nh vi&ecirc;n xuất sắc thuộc Lực lượng Đặc biệt!</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khoảnh khắc hai tay chạm đất, một cơn đau nh&oacute;i xoẹt qua tay nhanh như tia chớp, c&ugrave;ng với cảm gi&aacute;c v&ocirc; c&ugrave;ng bất lực hiện l&ecirc;n.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&atilde;nh Thi&ecirc;n từ từ mở mắt, t&acirc;m tr&iacute; dần hồi tỉnh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Thi&ecirc;n nhi, ​​cuối c&ugrave;ng th&igrave; con cũng tỉnh rồi.&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giọng n&oacute;i nhẹ nh&agrave;ng v&agrave; đầy ngạc nhi&ecirc;n của một người phụ nữ truyền v&agrave;o tai anh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&atilde;nh Thi&ecirc;n nh&igrave;n về ph&iacute;a ph&aacute;t ra giọng n&oacute;i th&igrave; nh&igrave;n thấy một khu&ocirc;n mặt thanh t&uacute; tr&agrave;n đầy t&igrave;nh y&ecirc;u đang chăm ch&uacute; nh&igrave;n hắn.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(60, 64, 67); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn nh&igrave;n thấy một mỹ nh&acirc;n mang vẻ đẹp cổ điển v&agrave; lộng lẫy đến mức nghẹt thở . Vẻ đẹp n&agrave;y ho&agrave;n to&agrave;n c&oacute; thể g&acirc;y chết người.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong t&acirc;m tr&iacute; của L&atilde;nh Thi&ecirc;n, ​​một d&ograve;ng k&yacute; ức quen thuộc hiện ra&hellip;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&atilde;nh Thi&ecirc;n, ​​c&ocirc;ng d&acirc;n Trung Hoa.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trước đ&acirc;y từng phục vụ tại quốc gia của Lực lượng đặc biệt Chiến lang, Sư đo&agrave;n qu&acirc;n đội Hoa Kỳ, hắn đ&atilde; c&oacute; v&ocirc; số c&ocirc;ng lao trong suốt thời gian phục vụ của m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn đ&atilde; tham gia Cuộc thi Lực lượng Đặc nhiệm Quốc tế, gi&agrave;nh được danh hiệu c&aacute; nh&acirc;n chiến đấu xuất sắc nhất v&agrave; được c&ocirc;ng nhận l&agrave; &ldquo;Vua của c&aacute;c chuy&ecirc;n gia vũ kh&iacute;&rdquo;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau đ&oacute;, trong một nhiệm vụ đa quốc gia được xếp hạng SSS, một sai lầm nhỏ của hắn đ&atilde; dẫn đến sự hy sinh của một đồng đội.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mặc cảm với tội lỗi, hắn quyết định nghỉ hưu v&agrave; rời khỏi Biệt đội chiến lang.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi nghỉ hưu, kh&ocirc;ng c&oacute; tin tức g&igrave; về hắn ta trong một thời gian. Hắn chọn ra nước ngo&agrave;i v&agrave; trở th&agrave;nh con &aacute;t chủ b&agrave;i của một tổ chức đ&aacute;nh thu&ecirc; nổi tiếng quốc tế.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bất kỳ nhiệm vụ n&agrave;o hắn tham gia đều c&oacute; tỷ lệ th&agrave;nh c&ocirc;ng ho&agrave;n mỹ l&agrave; 100%!</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">D&ugrave; đ&atilde; nghỉ hưu ở biệt đội chiến lang nhiều năm trước nhưng tr&aacute;i tim hắn vẫn hướng về qu&ecirc; hương.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chỉ cần một nhiệm vụ c&oacute; hại cho quốc gia Trung Hoa, kh&ocirc;ng những hắn ta sẽ từ chối nhiệm vụ m&agrave; c&ograve;n </span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(60, 64, 67); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">điều tra danh t&iacute;nh của kẻ đưa ra nhiệm vụ rồi t&agrave;n s&aacute;t người đ&oacute; một c&aacute;ch d&atilde; man</span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thời gian tr&ocirc;i qua, hắn trở th&agrave;nh c&aacute;i gai trong phe của mọi quốc gia b&ecirc;n cạnh quốc gia Trung Hoa.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong mắt c&aacute;c quốc gia kh&aacute;c, hắn l&agrave; một quả bom hẹn giờ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">T&agrave;n s&aacute;t v&agrave; đổ m&aacute;u sẽ theo bất cứ nơi n&agrave;o hắn ta đến.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhiều quốc gia bắt đầu &acirc;m mưu chống lại hắn ta, kh&ocirc;ng tiếc tiền thu&ecirc; s&aacute;t thủ tầm cỡ thế giới.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, cuộc sống của L&atilde;nh Thi&ecirc;n trở n&ecirc;n thoải m&aacute;i hơn khi ng&agrave;y th&aacute;ng tr&ocirc;i qua v&agrave; những t&ecirc;n s&aacute;t thủ biến mất trong kh&ocirc;ng kh&iacute; mỏng manh. Cuối c&ugrave;ng, kh&ocirc;ng c&oacute; s&aacute;t thủ n&agrave;o sẵn s&agrave;ng chấp nhận &quot;Nhiệm vụ Tự s&aacute;t&quot; nữa.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cố gắng giết L&atilde;nh Thi&ecirc;n cũng giống như tự s&aacute;t!</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy&ecirc;n bố n&agrave;y đ&atilde; được cả thế giới s&aacute;t thủ v&agrave; l&iacute;nh đ&aacute;nh thu&ecirc; thừa nhận.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn ta, L&atilde;nh Thi&ecirc;n, ​​kh&ocirc;ng chỉ l&agrave; một chuy&ecirc;n gia vũ kh&iacute; đ&aacute;ng gờm, m&agrave; hắn ta c&ograve;n l&agrave; một&nbsp; bậc thầy về Quyền Anh, tuy được xem l&agrave; một bậc thầy nhưng hắn ta vẫn lu&ocirc;n cố gắng trau dồi nội lực của m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn hiếm khi c&oacute; thể gặp một đối thủ tr&ecirc;n khắp thế giới; ngay cả trong những dịp hiếm hoi m&agrave; hắn ta gặp một người, hắn ta chưa bao giờ thua!</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&aacute;ch đ&acirc;y kh&ocirc;ng l&acirc;u, L&atilde;nh Thi&ecirc;n nhận được tin từ người m&ocirc;i giới th&acirc;n t&iacute;n nhất của m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">RNation đ&atilde; tạo ra một loại vũ kh&iacute; sinh học kh&ocirc;ng m&agrave;u v&agrave; kh&ocirc;ng vị. Họ đang l&ecirc;n kế hoạch vận chuyển vũ kh&iacute; v&agrave;o nước Trung Hoa, để thực hiện một &acirc;m mưu t&agrave;n &aacute;c.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đương nhi&ecirc;n L&atilde;nh Thi&ecirc;n sẽ kh&ocirc;ng ngồi y&ecirc;n m&agrave; để chuyện n&agrave;y xảy ra.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn đ&atilde; h&agrave;nh động!</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Với sự gi&uacute;p đỡ của người m&ocirc;i giới, vị tr&iacute; của vũ kh&iacute; sinh học đ&atilde; được x&aacute;c định ch&iacute;nh x&aacute;c.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay khi hắn ta th&agrave;nh c&ocirc;ng trong việc tạo ra một con đường m&aacute;u v&agrave; đi v&agrave;o ph&ograve;ng th&iacute; nghiệm dưới l&ograve;ng đất, ngay khi hắn ta chuẩn bị ph&aacute; hủy l&ocirc; vũ kh&iacute; sinh học&hellip; Ph&ograve;ng th&iacute; nghiệm dưới l&ograve;ng đất đột nhi&ecirc;n ph&aacute;t ra t&iacute;n hiệu x&acirc;m nhập, nhốt hắn ta trong một c&aacute;i lồng ho&agrave;n to&agrave;n k&iacute;n.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi hắn bắt gặp những người đồng hương R lần lượt xuất hiện b&ecirc;n ngo&agrave;i lồng, với s&uacute;ng tr&ecirc;n tay&hellip; L&atilde;nh Thi&ecirc;n hiểu ngay lập tức.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&acirc;y l&agrave; một &acirc;m mưu ... một &acirc;m mưu chống lại hắn ta.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn đ&atilde; bị phản bội bởi ch&iacute;nh người m&agrave; hắn tin tưởng nhất!</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nếu kh&ocirc;ng, với mức độ sắc sảo của m&igrave;nh, &acirc;m mưu của những người đồng hương R sẽ kh&ocirc;ng bao giờ c&oacute; thể th&agrave;nh c&ocirc;ng.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay cả cho đến l&uacute;c chết, L&atilde;nh Thi&ecirc;n vẫn kh&ocirc;ng thể hiểu được điều đ&oacute;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Ha ha ha ha&hellip;&rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi những suy nghĩ lướt qua t&acirc;m tr&iacute;, L&atilde;nh Thi&ecirc;n kh&ocirc;ng thể k&igrave;m lại v&agrave; ph&aacute; l&ecirc;n cười; t&acirc;m trạng của hắn ta tr&agrave;n đầy vui sướng.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Thi&ecirc;n nhi, con bị sao vậy? Đừng l&agrave;m mẫu th&acirc;n con sợ h&atilde;i. &quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giọng n&oacute;i dịu d&agrave;ng lại vang l&ecirc;n, lần n&agrave;y c&oacute; ch&uacute;t lo lắng.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">T&acirc;m tr&iacute; của L&atilde;nh Thi&ecirc;n đang hỗn loạn&hellip;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn ho&agrave;n to&agrave;n chắc chắn rằng hắn kh&ocirc;ng nhận ra người phụ nữ trước mắt m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, &aacute;nh mắt của người phụ nữ n&agrave;y dường như đầy ch&acirc;n th&agrave;nh v&agrave; kh&ocirc;ng hề giả dối, như thể c&ocirc; ấy đang nh&igrave;n người th&acirc;n của m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay khi L&atilde;nh Thi&ecirc;n đang chuẩn bị bắt đầu cuộc tranh luận với người phụ nữ xinh đẹp n&agrave;y, một d&ograve;ng k&yacute; ức xa lạ tr&agrave;n về trong t&acirc;m tr&iacute; hắn ...</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&atilde;nh Thi&ecirc;n cảm thấy như bị s&eacute;t đ&aacute;nh, ho&agrave;n to&agrave;n chết lặng như thể bị &aacute;p lực đ&egrave; l&ecirc;n huyệt đạo</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Điều duy nhất m&agrave; L&atilde;nh Thi&ecirc;n h&agrave;i l&ograve;ng l&agrave; anh ch&agrave;ng n&agrave;y cũng được đặt t&ecirc;n l&agrave; L&atilde;nh Thi&ecirc;n&hellip; &ldquo;Đo&agrave;n L&atilde;nh Thi&ecirc;n&rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đo&agrave;n L&atilde;nh Thi&ecirc;n, ​​15 tuổi.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">T&acirc;n Phong trấn, đệ tử của L&yacute; Gia nhưng kh&aacute;c họ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mẫu th&acirc;n cậu ta, L&yacute; Dung, l&agrave; Cửu trưởng l&atilde;o của L&yacute; Gia.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cha của cậu ấy&hellip; trong k&yacute; ức của hắn ấy, bắt đầu kh&ocirc;ng c&oacute; người như vậy.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong qu&aacute; tr&igrave;nh hợp nhất với đoạn k&yacute; ức ngoại truyện n&agrave;y, L&atilde;nh Thi&ecirc;n đ&atilde; t&igrave;m ra nguy&ecirc;n nh&acirc;n khiến Đo&agrave;n L&atilde;nh Thi&ecirc;n bị thương nặng nằm liệt giường.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">S&aacute;ng nay, con trai &uacute;t của thất trưởng l&atilde;o L&yacute; gia, L&yacute; T&iacute;n, đ&atilde; x&uacute;c phạm Đo&agrave;n L&atilde;nh Thi&ecirc;n bằng c&aacute;ch gọi anh ta l&agrave; đứa con hoang chỉ biết mẹ m&agrave; kh&ocirc;ng biết cha.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đo&agrave;n L&atilde;nh Thi&ecirc;n nổi cơn thịnh nộ v&agrave; bắt đầu chiến đấu với L&yacute; T&iacute;n&hellip;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng l&agrave;m sao Đo&agrave;n L&atilde;nh Thi&ecirc;n, ​​người thậm ch&iacute; c&ograve;n chưa đạt được Giai đoạn luyện cơ thể, lại c&oacute; thể đối đầu với L&yacute; T&iacute;n, người đ&atilde; đạt được giai đoạn luyện th&acirc;n cấp hai. Cậu ta ngay lập tức bị thương nặng bởi L&yacute; T&iacute;n v&agrave; gần như đ&atilde; đến bờ vực của c&aacute;i chết.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay khi Đo&agrave;n L&atilde;nh Thi&ecirc;n h&iacute;t hơi thở cuối c&ugrave;ng, L&atilde;nh Thi&ecirc;n tiếp quản to&agrave;n bộ con người của Đo&agrave;n L&atilde;nh Thi&ecirc;n ... bao gồm cả cơ thể v&agrave; k&yacute; ức của Đo&agrave;n L&atilde;nh Thi&ecirc;n.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi ho&agrave;n to&agrave;n h&ograve;a nhập với k&yacute; ức xa lạ, L&atilde;nh Thi&ecirc;n thở d&agrave;i.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn biết, từ giờ ph&uacute;t n&agrave;y trở đi, hắn sẽ sử dụng danh t&iacute;nh của Đo&agrave;n L&atilde;nh Thi&ecirc;n để h&ograve;a nhập v&agrave;o thế giới xa lạ n&agrave;y.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-134ace1d-7fff-3d69-7ee2-c37b13a9a8e0\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Từ h&ocirc;m nay trở đi, hắn ta sẽ l&agrave; Đo&agrave;n L&atilde;nh Thi&ecirc;n, ​​v&agrave; Đo&agrave;n L&atilde;nh Thi&ecirc;n sẽ l&agrave; hắn ta.</span></span></p>\r\n', 'Cà Di', '2020-10-02 09:14:56', 1, 0, 121);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(22, 'Chương 1: Chuyển sinh hệ thống chậm phát triển', 'chuong-1-chuyen-sinh-he-thong-cham-phat-trien', 14, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Luke, đồ lười biếng, anh c&ograve;n chưa thay đồ xong &agrave;? Nếu anh c&ograve;n chần chừ g&igrave; nữa, t&ocirc;i sẽ để anh khỏa th&acirc;n đi dự vũ hội, &rdquo;Robert Greyson h&eacute;t l&ecirc;n khi ngẩng đầu l&ecirc;n về hướng căn ph&ograve;ng tr&ecirc;n lầu từ chỗ ngồi của người l&aacute;i xe. Cửa ghế l&aacute;i mở toang, anh ta h&eacute;t lớn đến nỗi cửa sổ dường như rung l&ecirc;n.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một l&uacute;c sau, một giọng n&oacute;i gần như to bằng tiếng h&eacute;t của anh ta vang l&ecirc;n từ căn ph&ograve;ng tr&ecirc;n lầu. &quot;C&aacute;i qu&aacute;i g&igrave; vậy?&quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đầy tức giận, Robert nhảy ra khỏi ghế l&aacute;i chiếc F150 của m&igrave;nh v&agrave; chạy v&agrave;o t&ograve;a nh&agrave;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tiếp theo, một loạt tiếng &ldquo;bịch&rdquo; vang l&ecirc;n khi anh chạy l&ecirc;n cầu thang. Cuối c&ugrave;ng, c&oacute; một &quot;tiếng nổ&quot; lớn khi anh ta mở cửa.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ah, Luke, chuyện g&igrave; đ&atilde; xảy ra với cậu vậy, nh&oacute;c?&quot; Tiếng h&eacute;t của Robert lại vang l&ecirc;n một lần nữa, sau đ&oacute; l&agrave; giọng của một người phụ nữ.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một ph&uacute;t sau, Robert chạy ra khỏi t&ograve;a nh&agrave; với một người đ&agrave;n &ocirc;ng trẻ tuổi tr&ecirc;n lưng. Theo sau anh ta l&agrave; một phụ nữ trung ni&ecirc;n bối rối với một cậu b&eacute; năm tuổi được k&eacute;o theo.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Catherine, l&aacute;i xe v&agrave; đi với Joseph. H&atilde;y cẩn thận tr&ecirc;n đường v&agrave; đừng vội v&agrave;ng. &quot; Robert quay lại v&agrave; n&oacute;i v&agrave;i c&acirc;u vội v&agrave;ng trước khi đặt người thanh ni&ecirc;n nằm ngửa tr&ecirc;n băng ghế sau. Sau khi đỡ nam thanh ni&ecirc;n nằm xuống, anh ta lao đến ghế l&aacute;i.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh ta khởi động xe, v&agrave; động cơ Ford gầm r&uacute; dữ dội khi chiếc xe ph&oacute;ng nhanh về ph&iacute;a đường cao tốc v&agrave; lao đi.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&hellip;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một ng&agrave;y sau, Luke tỉnh lại.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh ng&acirc;y người nh&igrave;n l&ecirc;n trần nh&agrave; ph&iacute;a tr&ecirc;n v&agrave; chỉ nhớ lại những g&igrave; đ&atilde; xảy ra sau một thời gian ngắn. Khoảnh khắc nhớ lại những g&igrave; đ&atilde; xảy ra, l&ograve;ng anh tr&agrave;o d&acirc;ng niềm phấn kh&iacute;ch.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, anh vẫn nhanh ch&oacute;ng dập tắt cơn phấn kh&iacute;ch v&agrave; b&igrave;nh tĩnh trở lại. Anh quan s&aacute;t xung quanh v&agrave; thấy rằng anh đang ở trong một khu bệnh viện.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngo&agrave;i anh ta, Claire cũng ở trong ph&ograve;ng. Hiện tại c&ocirc; đang ngủ ngon l&agrave;nh tr&ecirc;n ghế s&ocirc; pha c&aacute;ch anh kh&ocirc;ng xa.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khoảnh khắc đặt mắt v&agrave;o Claire, anh thở ph&agrave;o nhẹ nh&otilde;m.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kể từ khi em g&aacute;i của anh ấy ở đ&acirc;y, Robert phải l&agrave; người đưa anh ấy đến bệnh viện.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh tiếp tục lắng nghe những &acirc;m thanh xung quanh m&igrave;nh v&agrave; thấy rằng nơi n&agrave;y gần như trống rỗng v&igrave; kh&ocirc;ng c&oacute; bất kỳ &acirc;m thanh n&agrave;o của hoạt động sống. Từ đ&oacute; anh ph&aacute;n đo&aacute;n c&oacute; lẽ b&acirc;y giờ đ&atilde; l&agrave; đ&ecirc;m khuya. Bệnh viện ch&igrave;m trong im lặng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chỉ sau đ&oacute;, Luke mới nhắm mắt lại v&agrave; h&eacute;t l&ecirc;n trong nội t&acirc;m, với tất cả cảm x&uacute;c của anh ấy dồn v&agrave;o đ&oacute;, &quot;H&atilde;y ra ngo&agrave;i, hệ thống.&quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tiếp theo&hellip; anh ấy kh&ocirc;ng nhận được phản hồi.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hệ thống chết tiệt n&agrave;o đ&acirc;y?</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke ngừng chửi rủa m&igrave;nh v&agrave; một lần nữa gọi với tất cả cảm x&uacute;c của m&igrave;nh, &quot;Đi ra, bố của con!&quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tiếp theo&hellip; vẫn kh&ocirc;ng c&oacute; phản hồi.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke: &ldquo;&hellip; Hệ thống con trai t&ocirc;i?</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Hệ thống ch&aacute;u trai của t&ocirc;i?</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Hệ thống, bạn c&oacute; s * x ở đ&acirc;u đ&oacute;?&quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tốt th&ocirc;i, vẫn kh&ocirc;ng c&oacute; phản hồi.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke bắt đầu chửi rủa trong nội t&acirc;m. &ldquo;Hệ thống, &iacute;t nhất, bạn n&ecirc;n cho t&ocirc;i xem một giao diện hoặc một c&aacute;i g&igrave; đ&oacute;, phải kh&ocirc;ng? Hay một trang nh&acirc;n vật hay g&igrave; đ&oacute;? &Yacute; nghĩa của điều n&agrave;y l&agrave; g&igrave;? Tại sao bạn lại giả vờ chết? &rdquo;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay sau khi anh n&oacute;i những lời đ&oacute;, trong đầu anh hiện l&ecirc;n một chuỗi dữ liệu.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Điều n&agrave;y ho&agrave;n to&agrave;n kh&aacute;c với giao diện hoặc trang nh&acirc;n vật m&agrave; anh đ&atilde; mong đợi. Tất cả dữ liệu đ&atilde; xuất hiện trực tiếp trong n&atilde;o anh ta. Anh ấy kh&ocirc;ng cần phải đọc một số trang nh&acirc;n vật.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người d&ugrave;ng: Luke (b&iacute; danh Luke Coulson)</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sức mạnh: 12 (sức mạnh thể chất)</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kh&eacute;o l&eacute;o: 11 (nhanh nhẹn, hoạt b&aacute;t)</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sức mạnh tinh thần: 12 (sức mạnh tinh thần)</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cấp độ: 0</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kinh nghiệm: 0</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">T&iacute;n dụng: 0</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave; kh&ocirc;ng c&oacute; g&igrave; kh&aacute;c.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke đ&atilde; ph&aacute;t đi&ecirc;n l&ecirc;n v&igrave; tức giận khi nhận ra rằng kh&ocirc;ng c&oacute; g&igrave; kh&aacute;c. Anh cảm thấy muốn nắm lấy hệ thống cho n&oacute; ăn đập. Thật kh&ocirc;ng may, anh ấy kh&ocirc;ng biết hệ thống được đặt ở đ&acirc;u.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Về nguồn gốc của Luke, c&acirc;u chuyện bắt đầu từ 13 năm trước.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave;o một ng&agrave;y b&igrave;nh thường c&aacute;ch đ&acirc;y 13 năm, Luke đột nhi&ecirc;n tỉnh dậy v&agrave; ph&aacute;t hiện ra rằng m&igrave;nh đ&atilde; biến th&agrave;nh một cậu b&eacute; da trắng, một cậu nh&oacute;c 5 tuổi t&ecirc;n l&agrave; Luke Coulson.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh ấy kh&ocirc;ng biết bị lạc hoặc bối rối bao l&acirc;u trước khi từ hệ thống ph&aacute;t hiện ra rằng anh ấy chưa chuyển đổi, với linh hồn của anh ấy tiếp quản một cơ thể mới. Đ&uacute;ng hơn, anh ấy ch&iacute;nh l&agrave; Luke.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng v&igrave; l&yacute; do n&agrave;o đ&oacute;, c&oacute; một y&ecirc;u cầu nhất định m&agrave; bộ n&atilde;o của anh ta phải đ&aacute;p ứng trước khi hệ thống c&oacute; thể được k&iacute;ch hoạt. Do đ&oacute;, hệ thống chỉ tự động k&iacute;ch hoạt khi cậu ấy 5 tuổi. Tr&ecirc;n hết, những k&yacute; ức từ tiền kiếp của anh ấy cũng quay trở lại với anh ấy.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Do đ&oacute;, anh ta tiếp tục cuộc sống h&agrave;ng ng&agrave;y của m&igrave;nh trong sự thiếu hiểu biết, v&agrave; chỉ sau khi anh ta l&ecirc;n năm tuổi, anh ta mới nhận ra rằng anh ta đ&atilde; kh&ocirc;ng sống kiếp trước của m&igrave;nh trong c&ugrave;ng một thế giới n&agrave;y.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong tiền kiếp, anh l&agrave; một người Trung Quốc v&agrave; đ&atilde; sống một cuộc sống b&igrave;nh thường nhưng y&ecirc;n b&igrave;nh cho đến khi anh 30 tuổi. Mỗi ng&agrave;y, anh ấy đều đến nơi l&agrave;m việc của m&igrave;nh, một c&ocirc;ng ty n&agrave;o đ&oacute;, kh&ocirc;ng hề thất bại. Anh ấy đ&atilde; từng l&agrave; một nh&acirc;n vi&ecirc;n h&agrave;nh ch&iacute;nh ở đ&oacute;, một c&ocirc;ng việc được trả lương đủ để đặt thức ăn tr&ecirc;n b&agrave;n của anh ấy. Anh ấy sống theo c&ugrave;ng một lịch tr&igrave;nh v&agrave; kiếm đủ tiền lương để trả c&aacute;c h&oacute;a đơn của m&igrave;nh. Cuộc sống của anh chỉ xoay quanh việc kiếm sống v&agrave; chờ đợi c&aacute;i chết cuối c&ugrave;ng của anh. Cuộc sống của anh ho&agrave;n to&agrave;n b&igrave;nh thường.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau đ&oacute;, v&agrave;o một đ&ecirc;m nọ, anh ta ngủ qu&ecirc;n khi xem một chương tr&igrave;nh truyền h&igrave;nh Mỹ n&agrave;o đ&oacute; ở nh&agrave;. Tập phim đang chiếu v&agrave;o thời điểm anh qua đời n&oacute;i về một vụ &aacute;n nhất định ở một ng&ocirc;i l&agrave;ng nhất định ở Hoa Kỳ.</span></span></p>\r\n<br />\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi nh&igrave;n thấy cảnh đẹp v&agrave; v&ugrave;ng đất rộng lớn tr&ecirc;n TV, anh tự nghĩ nếu được sống ở đ&oacute; th&igrave; thật tuyệt biết bao.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave; đ&oacute; l&agrave; k&yacute; ức cuối c&ugrave;ng anh c&oacute; về thế giới đ&oacute;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">R&otilde; r&agrave;ng l&agrave; sau khi ch&igrave;m v&agrave;o giấc ngủ v&agrave;o đ&ecirc;m h&ocirc;m đ&oacute;, anh đ&atilde; đến Hoa Kỳ của thế giới n&agrave;y kh&ocirc;ng r&otilde; v&igrave; l&yacute; do g&igrave;, v&agrave; đ&oacute; l&agrave; một thế giới kh&aacute;c với kiếp trước của anh. Anh ấy cũng đ&atilde; biến th&agrave;nh một em b&eacute;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh vẫn c&oacute; những k&yacute; ức về Luke từ trước khi l&ecirc;n năm tuổi. K&yacute; ức cũng giống như những đứa trẻ b&igrave;nh thường kh&aacute;c. Những g&igrave; anh n&ecirc;n nhớ th&igrave; anh vẫn nhớ, trong khi những k&yacute; ức từ khi anh c&ograve;n nhỏ, chẳng hạn như thời thơ ấu, đều đ&atilde; bị l&atilde;ng qu&ecirc;n.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng anh nhận thức r&otilde; rằng Hoa Kỳ n&agrave;y kh&ocirc;ng giống với Hoa Kỳ của kiếp trước anh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&oacute; l&agrave; bởi v&igrave; tr&iacute; nhớ của anh ở kiếp trước dừng lại ở năm 2018, nhưng ở đ&acirc;y, anh đ&atilde; được sinh ra v&agrave;o năm 1985.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bằng chứng lớn hơn nữa cho thấy đ&acirc;y l&agrave; một thế giới kh&aacute;c l&agrave; thực tế l&agrave; tổng thống hiện tại của Hoa Kỳ l&agrave; một phụ nữ gốc Latinh, Michelle Borg, 57 tuổi.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mặc d&ugrave; kiếp trước Luke chưa từng để &yacute; nhiều đến c&aacute;c tổng thống Hoa Kỳ, nhưng anh vẫn biết rằng kiếp trước của m&igrave;nh, chưa từng c&oacute; nữ tổng thống hay tổng thống l&agrave; người gốc Latinh, v&agrave; chắc chắn cũng chưa bao giờ l&agrave; nữ tổng thống Latin. hạ xuống.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&igrave; vậy, r&otilde; r&agrave;ng l&agrave; anh ta đ&atilde; kh&ocirc;ng chuyển đổi sang hơn 20 năm trước. Đ&uacute;ng hơn, anh ta đ&atilde; đến một thế giới song song.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke c&oacute; cảm gi&aacute;c cực kỳ phức tạp về tất cả những điều n&agrave;y.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh đ&atilde; từng hy vọng rằng đ&acirc;y l&agrave; c&ugrave;ng một thế giới từ kiếp trước của anh. Nếu vậy, anh sẽ c&oacute; thể một lần nữa gặp lại cha mẹ v&agrave; anh chị em của m&igrave;nh. Tuy nhi&ecirc;n, anh kh&ocirc;ng biết liệu cha mẹ v&agrave; anh chị em của m&igrave;nh c&oacute; chấp nhận rằng giờ đ&acirc;y họ đ&atilde; c&oacute; một người con trai v&agrave; anh trai người da trắng hay kh&ocirc;ng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng thực tế phũ ph&agrave;ng l&agrave; anh kh&ocirc;ng thể quay lại với họ được nữa.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, trong cuộc sống trước đ&acirc;y, anh ấy đ&atilde; từng l&agrave; một thanh ni&ecirc;n vui vẻ v&agrave; kh&ocirc;ng c&oacute; tham vọng. V&igrave; vậy, anh đ&atilde; sớm qu&ecirc;n đi những cảm x&uacute;c phức tạp, ho&agrave;n to&agrave;n v&ocirc; nghĩa.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thứ nhất, d&ugrave; sao th&igrave; anh ta cũng kh&ocirc;ng nghĩ ra c&aacute;ch n&agrave;o để quay lại. Thứ hai, anh chị em của anh ấy sẽ chăm s&oacute;c cha mẹ anh ấy. Anh chỉ hy vọng rằng sự biến mất của m&igrave;nh sẽ kh&ocirc;ng khiến họ qu&aacute; đau khổ, mặc d&ugrave; điều đ&oacute; c&oacute; hơi qu&aacute; hy vọng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi sống ở thế giới n&agrave;y hơn 10 năm, Luke cuối c&ugrave;ng đ&atilde; tr&uacute;t bỏ được khao kh&aacute;t về thế giới trước đ&acirc;y của m&igrave;nh, v&agrave; trở th&agrave;nh một người Mỹ sinh ra v&agrave; lớn l&ecirc;n ở đ&acirc;y.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Th&agrave;nh thật m&agrave; n&oacute;i, anh ấy cũng đ&atilde; c&oacute; rất nhiều lời ph&agrave;n n&agrave;n về m&ocirc;i trường sống của m&igrave;nh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tại sao khi những người kh&aacute;c xuy&ecirc;n kh&ocirc;ng, họ sẽ trở th&agrave;nh con trai của một chủ tập đo&agrave;n t&agrave;i ch&iacute;nh n&agrave;o đ&oacute;, hoặc ở một đ&ocirc; thị như New York, nhưng khi xuy&ecirc;n kh&ocirc;ng, anh ta lại đến một v&ugrave;ng qu&ecirc; n&ocirc;ng th&ocirc;n ở một nơi hoang vắng?</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kể từ khi tỉnh dậy l&uacute;c 5 tuổi, anh đ&atilde; sống ở bang Texas, hay n&oacute;i ch&iacute;nh x&aacute;c l&agrave; ở Th&agrave;nh phố Knox, hay ch&iacute;nh x&aacute;c hơn l&agrave; ở một thị trấn nhỏ b&ecirc;n ngo&agrave;i Th&agrave;nh phố Knox.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chỉ c&oacute; khoảng 2.000 cư d&acirc;n trong thị trấn nhỏ b&eacute; n&agrave;y, tương đương với d&acirc;n số của một ng&ocirc;i l&agrave;ng ở Trung Quốc trong kiếp trước của anh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kh&aacute;c với Trung Quốc ở kiếp trước, nơi n&agrave;y v&ocirc; c&ugrave;ng rộng lớn , v&agrave; sau khi rời thị trấn, người ta c&oacute; thể l&aacute;i xe rất l&acirc;u m&agrave; kh&ocirc;ng thấy một b&oacute;ng người n&agrave;o.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&yacute; do duy nhất khiến anh ta sống ở một v&ugrave;ng n&ocirc;ng th&ocirc;n như vậy trong 13 năm, v&agrave; thậm ch&iacute; c&ograve;n sống s&oacute;t cho đến khi tốt nghiệp trung học phổ th&ocirc;ng, l&agrave; v&igrave;&hellip; anh ta l&agrave; một người c&oacute; hệ thống.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Từ khi l&ecirc;n năm, anh đ&atilde; biết m&igrave;nh c&oacute; một hệ thống.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng hệ thống n&agrave;y chưa bao giờ cho anh ta bất kỳ phản ứng n&agrave;o. Giống như một vi&ecirc;n đ&aacute;, n&oacute; ở đ&oacute; v&agrave; kh&ocirc;ng di chuyển, nhưng v&igrave; l&yacute; do n&agrave;o đ&oacute;, anh c&oacute; thể cảm nhận được rằng hệ thống n&agrave;y thực sự tồn tại.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng anh ta kh&ocirc;ng thể x&aacute;c định ch&iacute;nh x&aacute;c nơi n&agrave;o.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh ta chưa bao giờ đề cập chuyện n&agrave;y với bất kỳ ai kh&aacute;c v&igrave; điều đ&oacute; sẽ chỉ khiến Robert v&agrave; Catherine nghĩ rằng anh ta bị t&acirc;m thần.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong mọi trường hợp, hệ thống đ&atilde; truyền cho cậu một điều, đ&oacute; l&agrave; n&oacute; sẽ chỉ được k&iacute;ch hoạt ho&agrave;n to&agrave;n khi cậu trở th&agrave;nh người lớn.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&igrave; vậy, Luke kh&ocirc;ng c&ograve;n lựa chọn n&agrave;o kh&aacute;c ngo&agrave;i việc tiếp tục chờ đợi.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d17e6f2-7fff-0ab4-8dcf-b524f7b5a98a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hệ thống khăng khăng giả vờ như n&oacute; đ&atilde; chết. Anh ta c&oacute; thể l&agrave;m g&igrave; kh&aacute;c về n&oacute;?</span></span></p>\r\n', 'Cà Di', '2020-10-05 04:58:28', 1, 0, 220);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(23, 'Chương 1: Thoát khỏi đảo Savage', 'chuong-1-thoat-khoi-djao-savage', 7, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong một khu rừng rậm hẻo l&aacute;nh nằm giữa Sở th&agrave;nh v&agrave; th&agrave;nh Bạch Thủy thuộc quốc gia Đại Huyền, một b&oacute;ng người gầy g&ograve; v&agrave; ốm yếu đang dựa v&agrave;o một th&acirc;n c&acirc;y d&agrave;y. Hai ch&acirc;n hắn dang rộng ra một c&aacute;ch thoải m&aacute;i.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">H&igrave;nh d&aacute;ng n&agrave;y thuộc về một thiếu ni&ecirc;n mười ba, mười bốn tuổi.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngo&agrave;i khu&ocirc;n mặt nhợt nhạt bất thường, khu&ocirc;n mặt cậu b&eacute; kh&ocirc;ng c&oacute; g&igrave; đặt biệt. Quần &aacute;o của cậu ta rộng r&atilde;i b&ecirc;n cạnh cậu ta l&agrave; một thanh kiếm bằng th&eacute;p s&aacute;ng ch&oacute;i đ&atilde; bị n&eacute;m một c&aacute;ch bất cẩn.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thanh kiếm, mặc d&ugrave; s&aacute;ng b&oacute;ng, nhưng cũng lấm tấm những vết m&aacute;u đen kh&ocirc;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một dải băng được quấn quanh vai thiếu ni&ecirc;n nhiều lần, tạo th&agrave;nh một chiếc gar&ocirc; tạm thời. D&ugrave; đ&atilde; buộc chặt nhưng m&aacute;u tươi vẫn rỉ ra.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhắm mắt lại, người thiếu ni&ecirc;n bất động tựa v&agrave;o th&acirc;n c&acirc;y, như thể đang chợp mắt.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đột nhi&ecirc;n, một &acirc;m thanh &ldquo;shaa&hellip; shaa&hellip;&rdquo; nhẹ ph&aacute;t ra từ trong khu rừng. Một thứ g&igrave; đ&oacute; đang nhanh ch&oacute;ng tiếp cận người thiếu ni&ecirc;n.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người thiếu ni&ecirc;n mở mắt v&agrave; nhảy l&ecirc;n, trong khi ng&oacute;n ch&acirc;n của hắn ta theo bản năng hất sang một b&ecirc;n.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Bang!&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thanh kiếm th&eacute;p vọt l&ecirc;n v&agrave; nằm gọn trong l&ograve;ng b&agrave;n tay của ch&agrave;ng trai trẻ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người thiếu ni&ecirc;n nh&igrave;n về hướng ph&aacute;t ra &acirc;m thanh, kh&ocirc;ng một ch&uacute;t do dự, nhảy sang hướng kh&aacute;c. Chỉ với v&agrave;i bước ch&acirc;n, cậu đ&atilde; biến mất trong rừng rậm.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một l&uacute;c sau, một nh&oacute;m chiến binh mặc &aacute;o gi&aacute;p đen xuất hiện từ rừng rậm, di chuyển theo đội h&igrave;nh chiến đấu chặt chẽ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chỉ c&oacute; hai mươi chiến binh, nhưng tất cả mọi người trong nh&oacute;m của họ đều cao lớn v&agrave; mạnh mẽ với ngoại h&igrave;nh đều l&agrave; những chiến binh d&agrave;y dạn kinh nghiệm. R&otilde; r&agrave;ng họ l&agrave; những người l&iacute;nh đ&atilde; quen với việc đổ m&aacute;u.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Họ đi ra từ một ph&iacute;a của khu rừng. Sau tiếng h&ocirc; dừng của một trong số họ, tất cả 20 người họ dừng lại v&agrave; đứng thẳng ở c&ugrave;ng một chỗ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ugrave;ng l&uacute;c đ&oacute;, một trong những chiến binh trẻ tuổi vội v&agrave;ng bước tới v&agrave; ngồi xổm xuống trước chỗ từng l&agrave; nơi nghỉ ch&acirc;n của thiếu ni&ecirc;n. Sử dụng đ&ocirc;i tay của m&igrave;nh, anh nhanh ch&oacute;ng đ&agrave;o bới nơi nghỉ ch&acirc;n của thiếu ni&ecirc;n sau đ&oacute; đứng dậy trở lại.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Vương đội trưởng, kẻ chạy trốn chỉ mới chạy trốn gần đ&acirc;y. Nếu ch&uacute;ng ta rời đi b&acirc;y giờ, ch&uacute;ng ta c&oacute; thể bắt được hắn ta. &rdquo; </span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(60, 64, 67); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người được b&aacute;o c&aacute;o l&agrave; người duy nhất trong nh&oacute;m của họ kh&ocirc;ng đội mũ gi&aacute;p đen v&agrave; thay v&agrave;o đ&oacute; đang để lộ c&aacute;i đầu h&oacute;i của m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong khi c&aacute;c chiến binh tương đối cao, so với người khổng lồ hai m&eacute;t b&ecirc;n cạnh, họ thấp hơn nhiều, thực giống như những đứa trẻ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Hừm, kh&ocirc;ng cần đuổi theo hắn. Ch&uacute;ng ta đ&atilde; bảo vệ c&aacute;c th&agrave;nh phố xung quanh. D&ugrave; con nh&oacute;c n&agrave;y c&oacute; gian xảo đến đ&acirc;u, hắn ta cũng sẽ kh&ocirc;ng thể trốn tho&aacute;t. Ở b&ecirc;n đ&oacute;, Tướng Situ đ&atilde; đợi từ l&acirc;u. B&acirc;y giờ, tất cả những g&igrave; ch&uacute;ng ta phải l&agrave;m l&agrave; từ từ t&igrave;m đường đến chỗ hắn. &quot; Người đ&agrave;n &ocirc;ng khổng lồ khịt mũi khi nh&igrave;n chằm chằm về hướng người thiếu ni&ecirc;n đ&atilde; trốn v&agrave;o.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Thưa Ng&agrave;i, đ&acirc;y l&agrave; một t&ecirc;n tội phạm lớn m&agrave; nh&agrave; nước y&ecirc;u cầu phải bắt giữ. Nếu ch&uacute;ng ta bắt được hắn ch&uacute;ng ta sẽ được trọng thưởng. Ch&uacute;ng ta c&oacute; thực sự muốn trao giải thưởng của m&igrave;nh cho Nguy&ecirc;n Duy tướng qu&acirc;n kh&ocirc;ng? &quot; Một chiến binh mặc gi&aacute;p hỏi sau một l&uacute;c ngập ngừng.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Trọng thưởng? Kh&ocirc;ng vấn đề. Nguy&ecirc;n Duy tướng qu&acirc;n c&oacute; nhận được giải thưởng của ch&uacute;ng ta hay kh&ocirc;ng sẽ phụ thuộc v&agrave;o việc liệu anh ta c&oacute; đủ khả năng cần thiết để bắt kẻ chạy trốn hay kh&ocirc;ng. Mặc kệ, ch&uacute;ng ta h&atilde;y chậm lại một ch&uacute;t để c&oacute; thể đỡ mệt cả hai &rdquo;. Người đ&agrave;n &ocirc;ng khổng lồ n&oacute;i với vẻ v&ocirc; cảm trong khi đang lao c&aacute;i đầu trọc của m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Thưa Ng&agrave;i, &yacute; Ng&agrave;i l&agrave; g&igrave;? Nguy&ecirc;n Duy tướng qu&acirc;n c&oacute; nhiều người hơn ch&uacute;ng ta. Ngay cả khi người thiếu ni&ecirc;n đ&oacute; biết một v&agrave;i kỹ năng, l&agrave;m thế n&agrave;o để hắn ta c&oacute; thể chống lại Nguy&ecirc;n Duy tướng qu&acirc;n? &rdquo; Người l&iacute;nh trẻ kinh ngạc v&agrave; nh&igrave;n chằm chằm v&agrave;o sỹ quan chỉ huy của m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Dư T&acirc;n, trước nay ngươi đ&atilde; ở b&ecirc;n cạnh ta kh&aacute; l&acirc;u v&agrave; ta c&oacute; thể n&oacute;i rằng ngươi kh&aacute; mạnh mẽ. Tuy nhi&ecirc;n, nếu ngươi bị ch&iacute;nh phủ truy n&atilde;, </span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(60, 64, 67); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">ngươi c&oacute; thể đối mặt với nhiều nhất l&agrave; bao nhi&ecirc;u người m&agrave; ngươi vẫn c&oacute; thể rời đi an to&agrave;n?</span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&rdquo; Người đ&agrave;n &ocirc;ng to lớn kh&ocirc;ng trả lời trực tiếp người l&iacute;nh trẻ, thay v&agrave;o đ&oacute; &ocirc;ng ta trả lời bằng một c&acirc;u hỏi s&acirc;u sắc.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">TL: Yu Xin kh&ocirc;ng phải l&agrave; một c&aacute;i t&ecirc;n quan trọng - kh&ocirc;ng cần phải nhớ n&oacute;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Nếu t&ocirc;i chiến đấu với c&aacute;c th&agrave;nh vi&ecirc;n trong nh&oacute;m, th&igrave; t&ocirc;i c&oacute; thể xử l&yacute; khoảng bảy hoặc t&aacute;m người trong số họ m&agrave; kh&ocirc;ng c&oacute; vấn đề g&igrave;. Tuy nhi&ecirc;n, nếu số lượng tr&ecirc;n mười người t&ocirc;i nghĩ t&ocirc;i sẽ gặp rắc rối&rdquo;. </span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(60, 64, 67); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người l&iacute;nh trẻ kh&ocirc;ng chắc cấp tr&ecirc;n muốn g&igrave; với c&acirc;u hỏi n&agrave;y nhưng anh ta vẫn trả lời v&ocirc; c&ugrave;ng th&agrave;nh thật.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Bảy hoặc t&aacute;m! Hả. Kể từ khi lệnh bắt giữ đứa trẻ n&agrave;y được ban h&agrave;nh, số lượng c&aacute;c s&aacute;t thủ chuy&ecirc;n nghiệp bị giết trong khi cố gắng truy bắt n&oacute; đ&atilde; vượt qua con số &iacute;t ỏi đ&oacute; gấp mười lần! &rdquo; Người đ&agrave;n &ocirc;ng khổng lồ rống l&ecirc;n một tiếng cười, lạnh l&ugrave;ng giải th&iacute;ch tại sao lại hỏi c&acirc;u hỏi ban đầu của m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;C&aacute;c s&aacute;t thủ chuy&ecirc;n nghiệp đều đ&atilde; được đ&agrave;o tạo bởi c&aacute;c chuy&ecirc;n gia, tuy rằng kh&ocirc;ng thể so với ch&uacute;ng ta đội cận vệ Hắc Hổ, nhưng bọn họ nhất định kh&ocirc;ng yếu như người b&igrave;nh thường!&quot; Khu&ocirc;n mặt của người l&iacute;nh trẻ lộ ra vẻ kh&oacute; tin, anh ta kh&ocirc;ng thể hiểu được l&agrave;m thế n&agrave;o m&agrave; người thiếu ni&ecirc;n n&agrave;y lại c&oacute; thể mạnh mẽ như vậy.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Thiếu ni&ecirc;n n&agrave;y, hắn vốn l&agrave; trốn tho&aacute;t khỏi đảo Savage, tự n&oacute; đ&atilde; l&agrave; một chiến c&ocirc;ng kh&ocirc;ng nhỏ. H&ograve;n đảo ban đầu được thiết kế để trở th&agrave;nh nh&agrave; t&ugrave; d&agrave;nh cho những người xấu xa nhất; mỗi t&ugrave; nh&acirc;n bị nhốt ở đ&oacute; đều sở hữu t&agrave;i năng v&agrave; sức mạnh tuyệt vời. Kh&ocirc;ng bao giờ n&ecirc;n đ&aacute;nh gi&aacute; thấp họ &rdquo;. Người khổng lồ lạnh l&ugrave;ng tuy&ecirc;n bố.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;G&igrave;? Hắn ta đ&atilde; trốn tho&aacute;t khỏi đảo Savage !? &rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">T&ecirc;n l&iacute;nh trẻ h&iacute;t một hơi v&agrave; đột nhi&ecirc;n, một &yacute; nghĩ kh&aacute;c nảy ra trong hắn. Hắn ta vội v&agrave;ng hỏi,</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Thưa Ng&agrave;i, t&ocirc;i nghe n&oacute;i rằng đảo Savage bị ch&igrave;m trong đ&ecirc;m, v&agrave; kh&ocirc;ng phải họ n&oacute;i rằng tất cả c&aacute;c t&ugrave; nh&acirc;n đều ch&igrave;m c&ugrave;ng h&ograve;n đảo sao? Biển Chết kh&eacute;t tiếng bao quanh n&oacute;; Cần c&oacute; một chiếc Thuyền Ebony đặc biệt để đi qua v&ugrave;ng biển. Kh&ocirc;ng c&oacute; g&igrave; kh&aacute;c c&oacute; khả năng tồn tại nổi &rdquo;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Bản th&acirc;n ta cũng kh&ocirc;ng chắc về điều đ&oacute;, nhưng ngo&agrave;i người thiếu ni&ecirc;n đ&oacute;, &iacute;t nhất mười người kh&aacute;c đ&atilde; trốn tho&aacute;t khỏi Biển Chết. Nếu kh&ocirc;ng phải v&igrave; ch&uacute;ng ta v&ocirc; t&igrave;nh t&igrave;m thấy một trong những kẻ trốn tho&aacute;t v&agrave; thẩm vấn hắn ta để biết th&ocirc;ng tin, ta e rằng T&ograve;a &aacute;n sẽ kh&ocirc;ng biết về điều n&agrave;y. Do đ&oacute;, Đội Cận vệ Hắc Hổ cũng sẽ kh&ocirc;ng được cử đi tuần tra nơi n&agrave;y. D&ugrave; sao đi nữa, người thiếu ni&ecirc;n m&agrave; ch&uacute;ng ta phải bắt l&agrave; t&ecirc;n tội phạm yếu nhất đ&atilde; trốn tho&aacute;t. Mặc d&ugrave; hắn ta đ&atilde; thiết lập những lối đi giả v&agrave; khiến ch&uacute;ng ta l&ugrave;i lại nửa th&aacute;ng, nhưng tất cả những g&igrave; ch&uacute;ng ta cần l&agrave;m b&acirc;y giờ l&agrave; đi thẳng v&agrave; ch&uacute;ng ta sẽ t&igrave;m thấy hắn ta. V&agrave; một khi ch&uacute;ng ta l&agrave;m vậy, hắn ta coi như chết chắc&rdquo;. Lắc đầu, người đ&agrave;n &ocirc;ng khổng lồ vỗ v&agrave;o lưng c&acirc;y thương đen của m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Tất nhi&ecirc;n, ai m&agrave; kh&ocirc;ng biết rằng Ng&agrave;i được xếp hạng trong top trăm người đứng đầu to&agrave;n qu&acirc;n?&quot; Người l&iacute;nh trẻ ca ngợi người đ&agrave;n &ocirc;ng khổng lồ, sự t&ocirc;n trọng hiển thị r&otilde; tr&ecirc;n mặt anh ta.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Đừng t&acirc;ng bốc ta nữa! Ch&uacute;ng ta n&ecirc;n đi th&ocirc;i! &quot; Vẫy c&aacute;nh tay như quạt của m&igrave;nh, người đ&agrave;n &ocirc;ng khổng lồ kh&aacute; thẳng thừng chỉ huy qu&acirc;n l&iacute;nh di chuyển ra ngo&agrave;i.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&uacute;i ch&agrave;o, người l&iacute;nh trẻ trở lại vị tr&iacute; của m&igrave;nh trong đội.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay lập tức những người l&iacute;nh trở lại đội h&igrave;nh của họ v&agrave; từng người l&iacute;nh nhanh ch&oacute;ng biến mất v&agrave;o rừng rậm.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một t&aacute;ch tr&agrave; sau đ&oacute;, những người l&iacute;nh rời khỏi khu rừng v&agrave; đi v&agrave;o một đồng cỏ nhỏ nhưng rộng r&atilde;i. Cảnh tượng ch&agrave;o đ&oacute;n họ khiến tất cả đều sững sờ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">TL: T&aacute;ch tr&agrave; sau = thời gian để tr&agrave; nguội / ngấm xong</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đồng cỏ vốn l&agrave; một thảm thực vật m&agrave;u xanh tươi tốt v&agrave; đ&atilde; nở hoa, giờ đ&atilde; bị nhuộm một m&agrave;u đỏ thẫm!</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giữa to&agrave;n bộ m&aacute;u, x&aacute;c của những người l&iacute;nh mặc gi&aacute;p đen giống nhau nằm rải r&aacute;c tr&ecirc;n mặt đất.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tr&ecirc;n khu&ocirc;n mặt của mỗi người vẫn c&ograve;n nguy&ecirc;n những biểu cảm kinh ho&agrave;ng. Như thể họ đ&atilde; nh&igrave;n thấy một cảnh tượng thực sự đ&aacute;ng sợ trước khi chết.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mỗi x&aacute;c chết đều c&oacute; một lỗ to bằng ng&oacute;n tay c&aacute;i tr&ecirc;n cổ họng với m&aacute;u vẫn đang tr&agrave;o ra.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Tổng cộng c&oacute; ba mươi x&aacute;c chết ở đ&acirc;y, tất cả đều dưới sự chỉ huy của Nguy&ecirc;n Duy tướng qu&acirc;n. C&oacute; vẻ như kh&ocirc;ng một ai được tha; tuy nhi&ecirc;n, thi thể của Nguy&ecirc;n Duy tướng qu&acirc;n kh&ocirc;ng nằm trong số những x&aacute;c chết ở đ&acirc;y. &quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người l&iacute;nh trẻ t&ecirc;n Dư T&acirc;n b&aacute;o c&aacute;o với khu&ocirc;n mặt nhợt nhạt k&egrave;m hốt hoảng sau khi đ&atilde; nhanh ch&oacute;ng di chuyển xung quanh c&aacute;c x&aacute;c chết v&agrave; kiểm tra ch&uacute;ng.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khu&ocirc;n mặt của người đ&agrave;n &ocirc;ng khổng lồ trầm xuống khi nghe b&aacute;o c&aacute;o. Liếc sang ph&iacute;a b&ecirc;n kia của đồng cỏ, c&oacute; g&igrave; đ&oacute; đập v&agrave;o mắt hắn. Hắn nhanh ch&oacute;ng vượt qua.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kh&ocirc;ng ch&uacute;t do dự những người l&iacute;nh kh&aacute;c theo sau hắn ta, nhưng họ đ&atilde; đề ph&ograve;ng v&agrave; thận trọng hơn nhiều so với trước đ&acirc;y.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người đ&agrave;n &ocirc;ng khổng lồ nhanh ch&oacute;ng lao tới c&aacute;i c&acirc;y v&agrave; sau khi nh&igrave;n xuống, khu&ocirc;n mặt hắn ta nhăn lại v&igrave; tức giận v&agrave; đau buồn.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ở đ&oacute;, dưới gốc c&acirc;y, l&agrave; cơ thể kh&ocirc; h&eacute;o của một người l&iacute;nh &aacute;o gi&aacute;p đen với một thanh kiếm th&eacute;p xuy&ecirc;n qua cổ họng, ghim anh ta v&agrave;o gốc c&acirc;y.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Gần gốc c&acirc;y, một lưỡi kiếm d&agrave;i m&agrave;u bạc nhạt đ&acirc;m xuống đất.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&ocirc;i tay cứng rắn của người đ&agrave;n &ocirc;ng trung ni&ecirc;n đang nắm lấy lưỡi kiếm. Mỗi ng&oacute;n tay đều mang nhiều vết cắt v&agrave; mắt anh nh&igrave;n thẳng về ph&iacute;a trước mặc d&ugrave; hơi thở đ&atilde; ngừng từ l&acirc;u.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&hellip;&hellip;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lưu Minh đang nhanh ch&oacute;ng nhảy qua khu rừng, to&agrave;n th&acirc;n hắn cảm thấy v&ocirc; c&ugrave;ng đau nhức. Ngay cả chiến lợi phẩm trong trận chiến của hắn ta, một lưỡi kiếm bạc, cũng bắt đầu cảm thấy nặng hơn khi cầm trong tay.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mặc d&ugrave; hắn đ&atilde; tự r&egrave;n luyện kiếm thuật từ 5 đến 6 năm v&agrave; c&oacute; thể giết tất cả những chiến binh đang chờ đợi m&igrave;nh, nhưng sự hung dữ của những chiến binh n&agrave;y nằm ngo&agrave;i dự đo&aacute;n của hắn.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay cả sau khi hắn ta sử dụng chiến thuật đ&aacute;nh nhanh r&uacute;t gọn để ti&ecirc;u diệt tất cả c&aacute;c chiến binh, thủ lĩnh của c&aacute;c chiến binh vẫn tiếp tục truy đuổi hắn ta m&agrave; kh&ocirc;ng sợ h&atilde;i cho t&iacute;nh mạng của ch&iacute;nh m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Điều n&agrave;y buộc hắn phải sử dụng một kỹ thuật b&iacute; mật m&agrave; hắn đ&atilde; học được tr&ecirc;n đảo Savage, một kỹ thuật c&oacute; thể đốt ch&aacute;y năng lượng hiện tại của hắn v&agrave; sẽ ảnh hưởng vết thương cũ của hắn. Ngay cả khi sử dụng kỹ thuật đ&oacute;, hắn ta vẫn chỉ c&oacute; thể giết được thủ lĩnh đ&oacute;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">TL: C&oacute; đang đốt ch&aacute;y tiềm năng của anh ấy (anh ấy sẽ k&eacute;m khả năng / nhanh nhẹn trong v&agrave;i ng&agrave;y nữa)</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sử dụng một kỹ thuật như vậy đ&atilde; l&agrave;m cho cơ thể thiếu ni&ecirc;n của hắn ta hoạt động qu&aacute; mức; hắn sẽ kh&ocirc;ng tồn tại l&acirc;u hơn với tốc độ n&agrave;y.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi Lưu Minh nghĩ về hậu quả, hắn kh&ocirc;ng thể kh&ocirc;ng liếc qua vai m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Những dải băng nhiều lớp quấn quanh vai hắn thấm đẫm m&aacute;u, đồng thời, từng đợt đau đớn kh&ocirc;ng ngừng ph&aacute;t ra từ c&aacute;nh tay hắn.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay cả với t&iacute;nh c&aacute;ch bướng bỉnh của m&igrave;nh, việc n&agrave;y l&agrave; qu&aacute; sức chịu đựng.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cận vệ hắc hổ xứng đ&aacute;ng được nhận danh hiệu l&agrave; tinh hoa của Đại Huyền. Họ vượt trội hơn rất nhiều so với những s&aacute;t thủ ch&iacute;nh phủ b&igrave;nh thường đ&atilde; đuổi theo hắn trước đ&oacute;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn ta l&uacute;c n&agrave;y chỉ c&oacute; thể hy vọng rằng cuộc t&agrave;n s&aacute;t trước đ&oacute; đ&atilde; khiến c&aacute;c cận vệ hắc hổ kh&aacute;c cảnh gi&aacute;c với hắn ta; qu&aacute; sợ h&atilde;i để theo đuổi hắn ta chặt chẽ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi một hoặc hai ng&agrave;y nữa tr&ocirc;i qua, hậu quả của &lsquo;Thuật đ&oacute;ng nguy&ecirc;n kh&iacute;&rsquo; sẽ biến mất v&agrave; hắn ta c&oacute; thể nhảy xuống một con s&ocirc;ng gần đ&oacute; để trốn tho&aacute;t.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mặc d&ugrave; Lưu Minh kh&ocirc;ng gi&agrave; lắm, nhưng hắn đ&atilde; học được một số kỹ thuật hiếm v&agrave; kh&ocirc;ng ch&iacute;nh thống.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">TL: Kh&ocirc;ng ch&iacute;nh thống = kh&ocirc;ng phổ biến / kh&ocirc;ng cực kỳ hữu &iacute;ch trong nhiều trường hợp</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nếu kh&ocirc;ng, ngay cả khi c&oacute; người bảo vệ m&igrave;nh tr&ecirc;n đảo Savage, hắn ta sẽ kh&ocirc;ng thể sống s&oacute;t ở đ&oacute; từ bảy đến t&aacute;m năm khi c&ograve;n nhỏ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lưu Minh vừa nghĩ lại, khu&ocirc;n mặt đầy sẹo của một người đ&agrave;n &ocirc;ng hiện ra trước mắt. Mặc d&ugrave; phong th&aacute;i của &ocirc;ng ta kh&aacute; hung dữ, nhưng khi Lưu Minh nhớ đến &ocirc;ng ta, tr&aacute;i tim lại cảm thấy ấm &aacute;p.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đột nhi&ecirc;n, khu&ocirc;n mặt của Lưu Minh tối sầm lại v&agrave; cơ thể của hắn ta, vốn đang tăng tốc về ph&iacute;a trước, nhanh ch&oacute;ng vặn m&igrave;nh để chuyển sang tr&aacute;i v&agrave; cuộn tr&ograve;n th&agrave;nh một quả b&oacute;ng .</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ugrave;ng l&uacute;c đ&oacute;, &acirc;m thanh &ldquo;shou shou&rdquo; vang l&ecirc;n trước mặt hắn.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một c&acirc;y nỏ d&agrave;i h&agrave;ng chục m&eacute;t bắn ra một loạt t&ecirc;n đến trước mặt hắn. Ch&uacute;ng lao ra khỏi khu rừng với một v&agrave;i tia chớp v&agrave; hầu như kh&ocirc;ng lướt qua người thiếu ni&ecirc;n. Thay v&agrave;o đ&oacute; ch&uacute;ng xuy&ecirc;n v&agrave;o một th&acirc;n c&acirc;y trắng x&aacute;m sau lưng hắn.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Những chiếc bu l&ocirc;ng s&aacute;ng ch&oacute;i dưới &aacute;nh mặt trời - r&otilde; r&agrave;ng l&agrave; ch&uacute;ng được l&agrave;m bằng th&eacute;p cao cấp. Hầu hết c&aacute;c bu l&ocirc;ng đều bị d&iacute;nh chặt v&agrave;o c&acirc;y v&agrave; vẫn c&ograve;n hơi rung.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Kẻ n&agrave;o?&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người thiếu ni&ecirc;n lạnh l&ugrave;ng hỏi khi hắn ta ng&atilde; v&agrave;o một bụi c&acirc;y gần đ&oacute; v&agrave; ấn lưỡi kiếm bạc của m&igrave;nh ra trước mặt.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-08b9bfe0-7fff-9690-2aac-b2f361350669\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Kh&aacute; tốt. Kh&ocirc;ng c&oacute; g&igrave; ngạc nhi&ecirc;n khi ngươi c&oacute; thể sống l&acirc;u như vậy dưới sự truy đuổi của c&aacute;c cận vệ Hổ đen. Tuy nhi&ecirc;n, b&acirc;y giờ ngươi đ&atilde; gặp hai ch&uacute;ng ta, h&atilde;y chuẩn bị chết đi. &quot; Một giọng nữ sắc b&eacute;n vang l&ecirc;n từ ph&iacute;a trước v&agrave; hai b&oacute;ng đen, một của người đ&agrave;n &ocirc;ng v&agrave; một của người phụ nữ, vụt ra từ một c&aacute;i c&acirc;y ph&iacute;a trước người thiếu ni&ecirc;n.</span></span></p>\r\n', 'Cà Di', '2020-10-09 08:21:45', 1, 0, 210);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(26, 'Chương 1: Chiếc nhẫn bí ẩn', 'chuong-1-chiec-nhan-bi-an', 10, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Bố! Bố c&oacute; thể mua cho con một Gaming Pod được kh&ocirc;ng?&quot; Giang Phi hỏi cha m&igrave;nh, khu&ocirc;n mặt đỏ bừng v&igrave; phấn kh&iacute;ch.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Gần như ngay lập tức, c&acirc;u trả lời vang l&ecirc;n trong m&agrave;ng nhĩ của hắn, khi khu&ocirc;n mặt vừa mới vui vẻ ban n&atilde;y của cha hắn tho&aacute;ng cau m&agrave;y &quot;Nằm mơ đi!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Giang Phi, con đ&atilde; học trung học rồi! Con kh&ocirc;ng thể tiếp tục chơi game được!&quot; Mẹ hắn bước đến b&ecirc;n cạnh, hắn vui thầm v&igrave; sẽ c&oacute; cơ hội xin xỏ mẹ m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Mẹ ơi! Tr&ograve; chơi n&agrave;y chẳng giống g&igrave; ... Giống như ... Bất cứ thứ g&igrave;! tập đo&agrave;n Thịnh thị đ&atilde; d&agrave;nh hơn 13 năm tạo ra tr&ograve; chơi thần th&aacute;nh n&agrave;y! N&oacute; thậm ch&iacute; c&ograve;n được mệnh danh l&agrave; tr&ograve; chơi hay nhất thế giới! Họ gọi n&oacute; l&agrave; tr&ograve; chơi thứ hai Thế giới! Dawn Break, đ&oacute; l&agrave; t&ecirc;n của tr&ograve; chơi. Họ thậm ch&iacute; c&ograve;n lập tr&igrave;nh n&oacute; theo c&aacute;ch thay đổi nhận thức của ch&uacute;ng ta về thời gian! Thời gian b&ecirc;n trong thế giới n&agrave;y sẽ bị chậm lại! Những người chơi tr&ograve; chơi sẽ tận hưởng cuộc sống l&acirc;u hơn! &quot; Giang Phi giải th&iacute;ch với cha mẹ m&igrave;nh với vẻ hứng khởi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dawn Break l&agrave; một bước đột ph&aacute; c&ocirc;ng nghệ v&agrave;o thời nay. N&oacute; kh&ocirc;ng chỉ c&oacute; thể m&ocirc; phỏng vật l&yacute; trong thế giới thực, n&oacute; c&ograve;n l&agrave;m như vậy đối với hạt quark. Th&ocirc;ng qua việc sử dụng một c&ocirc;ng nghệ mới được ph&aacute;t triển, tr&ograve; chơi thậm ch&iacute; c&ograve;n cho ph&eacute;p con người chơi trong khi ngủ! Trong t&aacute;m giờ ngủ, một người c&oacute; thể hoạt động đầy đủ trong tr&ograve; chơi! T&aacute;m giờ đ&oacute; sẽ l&agrave; 24 giờ chơi tr&ograve; chơi l&agrave;nh mạnh trong thế giới si&ecirc;u thực tế đ&oacute;!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cha hắn quay lại v&agrave; hướng một ng&oacute;n tay v&agrave;o Giang Phi. Đầu ng&oacute;n tay khẽ rung rẩy n&oacute;i. &quot;Đừng tin những lời giới thiệu ngu ngốc đ&oacute;! Đ&oacute; chỉ l&agrave; chi&ecirc;u tr&ograve; lừa gạt của c&ocirc;ng ty tr&ograve; chơi với những kẻ cả tin như con. Họ chỉ muốn con đổ tất cả số tiền kh&oacute; kiếm được v&agrave;o nồi mật ong của họ! Ồ, nhưng ta kh&ocirc;ng để họ l&agrave;m được bất cứ điều g&igrave;. H&atilde;y để Những kẻ ngốc đ&oacute; tin v&agrave;o bất cứ sự thật n&agrave;o nghe được ngay b&ecirc;n tai m&igrave;nh! Con! L&agrave; con trai của ta, Phi! Kh&ocirc;ng phải l&agrave; một trong những kẻ ngốc cả tin đ&oacute;! Kh&ocirc;ng phải kẻ ngốc th&igrave; kh&ocirc;ng được chơi! Ưu ti&ecirc;n hiện tại của con l&agrave; học hết cấp ba! Một khi con đậu một trường đại học danh tiếng, con c&oacute; thể l&agrave;m bất cứ điều g&igrave; con muốn! &ldquo;. Cha của hắn n&oacute;i bằng giọng điệu cứng rắn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Bố! Bố thật qu&aacute; đ&aacute;ng! Ngay cả ch&iacute;nh phủ cũng t&aacute;n th&agrave;nh tr&ograve; chơi n&agrave;y! L&agrave;m sao họ c&oacute; thể n&oacute;i dối ch&uacute;ng ta?! N&oacute; kh&ocirc;ng chỉ đảm bảo cho ch&uacute;ng ta một giấc ngủ ngon suốt 8 tiếng m&agrave; c&ograve;n kh&ocirc;ng cản trở thời gian học tập của con ! &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giang Phi đ&atilde; đưa ra l&yacute; luận cuối c&ugrave;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cha hắn dường như c&agrave;ng giận dữ hơn. &quot;Ta sẽ chỉ n&oacute;i điều n&agrave;y một lần nữa. Kh&ocirc;ng. đề nghị đ&oacute; bị từ chối! Vậy đ&oacute;. Nếu ta kh&ocirc;ng thấy con trong ph&ograve;ng l&agrave;m b&agrave;i tập sau 20 gi&acirc;y nữa, coi như tiền trợ cấp của con giảm một nửa!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;L&atilde;o gi&agrave; ngu ngốc.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giang Phi c&uacute;i đầu, lẩm bẩm trong hơi thở. Hắn biết rằng hắn sẽ kh&ocirc;ng đi xa hơn với điều n&agrave;y. Hắn biết bố hắn kh&ocirc;ng cấm việc chơi game. &Ocirc;ng gi&agrave; chỉ thấy l&agrave; do gi&aacute; của chiếc gaming pod qu&aacute; đắt đỏ. Cha hắn ta c&ograve;n hơi cổ hủ; như thể chơi game sẽ ảnh hưởng đến tất cả mọi thứ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chu&ocirc;ng reo. Khi gi&aacute;o vi&ecirc;n đi ra, một cậu b&eacute; bụ bẫm chạy đến chỗ Giang Phi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Phi ca, thế n&agrave;o rồi? Dawn Break sẽ ra mắt tối nay! Anh đ&atilde; chuẩn bị pod chơi game của m&igrave;nh chưa?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ugh ... Đừng bắt chuyện với t&ocirc;i. T&ocirc;i sẽ kh&ocirc;ng tham gia c&ugrave;ng c&aacute;c cậu lần n&agrave;y. Đ&uacute;ng hơn, t&ocirc;i kh&ocirc;ng thể,&quot; Giang Phi n&oacute;i với một nụ cười cay đắng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Woah. C&oacute; chuyện g&igrave; vậy? Em kh&ocirc;ng phải l&agrave; người duy nhất tr&ocirc;ng cậy v&agrave;o anh. Ch&uacute;ng ta c&oacute; một nh&oacute;m anh em ở đ&acirc;y!&quot; cậu b&eacute;o lo lắng n&oacute;i.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn ta l&agrave; Triệu Phong, một trong số &iacute;t những người bạn m&agrave; Giang Phi c&oacute;. Năm nay Giang Phi học lớp 9-5. N&oacute; được cho l&agrave; lớp học đầy những học sinh th&ocirc;ng minh. Trớ tr&ecirc;u thay, kết quả của Giang Phi chỉ ở mức trung b&igrave;nh. Hắn ta kh&ocirc;ng bao giờ kh&aacute; hơn, cũng kh&ocirc;ng tệ hơn nữa. Gi&aacute;o dục thể chất của hắn cũng kh&ocirc;ng tốt, khiến hắn trở th&agrave;nh một người đặc trưng kh&eacute;p k&iacute;n kh&oacute; gần. Với tất cả những đặc điểm đ&oacute;, Giang Phi l&agrave; một người gần như &quot;v&ocirc; h&igrave;nh&quot; ho&agrave;n hảo. Loại sẽ kh&ocirc;ng bao giờ thu h&uacute;t sự ch&uacute; &yacute; của bất kỳ ai.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ugh ... Đ&oacute; l&agrave; bố t&ocirc;i. &Ocirc;ng ấy kh&ocirc;ng mua cho t&ocirc;i ổ chơi game. T&ocirc;i kh&ocirc;ng thể l&agrave;m g&igrave; để thay đổi &yacute; định của &ocirc;ng ấy.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giang Phi đảo mắt khi hắn ta ph&agrave;n n&agrave;n. Triệu Phong l&agrave; loại bạn m&agrave; Giang Phi kh&ocirc;ng cần phải giữ lời.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;L&agrave;m c&aacute;ch n&agrave;o b&acirc;y giờ? Hay l&agrave; để em k&ecirc;u gọi c&aacute;c anh em mua cho anh một c&aacute;i?&quot; Triệu Phong n&oacute;i. Cả Triệu Phong v&agrave; Giang Phi đều tầm thường như nhau trong x&atilde; hội n&agrave;y. Cậu thiếu ni&ecirc;n to lớn cũng say sưa như vậy. Đ&oacute; l&agrave; c&aacute;ch những con người v&ocirc; h&igrave;nh n&agrave;y tụ họp lại với nhau.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giang Phi c&agrave;u nh&agrave;u.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Tr&ocirc;ng t&ocirc;i c&oacute; giống một người ăn xin kh&ocirc;ng?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn hạ giọng. &quot;T&ocirc;i sẽ đặt c&aacute;i c&aacute;i m&aacute;y đ&oacute; ở đ&acirc;y khi t&ocirc;i c&oacute; được n&oacute;? C&aacute;i đ&oacute; rất lớn! Bố t&ocirc;i sẽ vung b&uacute;a tạ v&agrave;o n&oacute; khi thấy t&ocirc;i x&aacute;ch n&oacute; về nh&agrave;. &Ocirc;ng ấy sẽ v&agrave;o tận ph&ograve;ng t&ocirc;i nếu c&oacute; thể!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giang Phi thở d&agrave;i bất b&igrave;nh. Hắn biết rằng kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o hắn c&oacute; thể chơi tr&ograve; chơi, d&ugrave; c&oacute; hay kh&ocirc;ng c&oacute; pod, trừ khi hắn c&oacute; thể thuyết phục cha m&igrave;nh cho ph&eacute;p hắn chơi ch&iacute;nh tr&ograve; chơi đ&oacute;.&nbsp;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;&Ocirc;i trời ... Phi ca Anh tội nghiệp! Kh&ocirc;ng c&oacute; anh, t&igrave;nh anh em của ch&uacute;ng ta sẽ mềm nhũn mất!&quot; Triệu Phong gằn giọng, lắc đầu.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Vậy l&agrave; đủ rồi. H&atilde;y cho t&ocirc;i một thời gian. C&oacute; lẽ bố t&ocirc;i sẽ cập nhật kiến thức trong n&atilde;o của &ocirc;ng ấy ...&quot; Giang Phi n&oacute;i, vỗ vai Triệu Phong.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;H&atilde;y hi vọng như vậy.&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Triệu Phong gật đầu.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giang Phi lu&ocirc;n th&iacute;ch chơi game, đặc biệt l&agrave; những tựa game si&ecirc;u thực tế như Dawn Break. L&agrave; một cậu trai trẻ, l&agrave;m sao hắn ta kh&ocirc;ng th&iacute;ch kiếm v&agrave; ma thuật? Khi một người thiếu ni&ecirc;n được trao cơ hội chiến đấu, đ&oacute; l&agrave; cơ hội để ghi dấu ch&acirc;n của một người v&agrave;o những trang lịch sử. N&oacute;i chung, đ&oacute; l&agrave; ước mơ của mọi thanh ni&ecirc;n. Với sự tiến bộ của c&ocirc;ng nghệ m&aacute;y t&iacute;nh, chiến tranh sẽ lỗi thời. Hoặc &iacute;t nhất, n&oacute; sẽ xảy ra tr&ecirc;n kh&ocirc;ng gian kỹ thuật số, nơi c&aacute;c số 0 v&agrave; số một sẽ bay tr&ecirc;n chiến trường kỹ thuật số, thay v&igrave; t&ecirc;n lửa v&agrave; đạn. C&oacute; lẽ, một người thiếu ni&ecirc;n y&ecirc;u chiến tranh như Giang Phi sẽ chỉ c&ograve;n lại lựa chọn duy nhất l&agrave;; một tr&ograve; chơi si&ecirc;u thực tế ho&agrave;n to&agrave;n nhập vai!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&aacute;ng buồn thay, cơ hội đ&oacute; đ&atilde; kh&ocirc;ng c&ograve;n. Kể từ khi Giang Phi v&agrave;o cấp hai, cha của Giang Phi đ&atilde; quấn lấy hắn như một con trăn. Tất cả c&aacute;c m&aacute;y chơi game trong nh&agrave; đ&atilde; được cất đi! Tr&ograve; chơi nhập vai trực tuyến nhiều người chơi (MMORPG)? Đ&oacute; l&agrave; ngo&agrave;i khả năng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&hellip;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi chịu đựng &quot;địa ngục tra tấn&quot; được gọi l&agrave; trường học, Giang Phi đ&atilde; bước về nh&agrave; với t&acirc;m trạng đầy bỡ ngỡ. Mới 6 giờ tối, nhưng thế giới đ&atilde; cảm thấy lạnh lẽo v&agrave; tăm tối. M&ugrave;a đ&ocirc;ng đang nhanh ch&oacute;ng đến gần. Mặt trời đang dần biến mất.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Hmm ... C&aacute;i g&igrave; vậy ...?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giang Phi đi dạo theo con đường quen thuộc của m&igrave;nh về nh&agrave;. Những tia nắng chiếu xuống thấp chiếu v&agrave;o thứ g&igrave; đ&oacute; tr&ecirc;n mặt đất, được giấu bởi rễ của một c&aacute;i c&acirc;y to lớn. N&oacute; lọt v&agrave;o mắt của Giang Phi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Hohoho! Chắc ai đ&oacute; đ&atilde; đ&aacute;nh rơi chiếc nhẫn n&agrave;y! H&ocirc;m nay l&agrave; ng&agrave;y may mắn của ta! Thật l&agrave; một chiếc nhẫn đẹp!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giang Phi nhặt chiếc nhẫn m&agrave;u bạch kim v&agrave; đưa n&oacute; l&ecirc;n ngang tầm mắt. Thiết kế của chiếc nhẫn kh&aacute; đơn giản, kh&ocirc;ng hề c&oacute; chạm khắc cũng như khảm bất cứ vi&ecirc;n đ&aacute; n&agrave;o. D&ugrave; vậy, hắn vẫn t&igrave;m thấy vẻ đẹp trong sự giản dị đ&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Chắc n&oacute; cũng đ&aacute;ng gi&aacute; lắm. Cũng c&oacute; thể ...&quot; Giang Phi n&oacute;i, thản nhi&ecirc;n đeo n&oacute; v&agrave;o.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Qua b&agrave;n ăn tối, Giang Phi một lần nữa cố gắng thuyết phục cha m&igrave;nh. &Yacute; định của hắn ho&agrave;n to&agrave;n bị triệt hạ trong một nốt nhạc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong t&acirc;m tr&iacute; hắn cứ vang vọng sự thật rằng Dawn Break sắp ra mắt v&agrave;o đ&ecirc;m h&ocirc;m nay, Giang Phi đ&atilde; tự đ&oacute;ng cửa trong ph&ograve;ng của m&igrave;nh. Đến 10 giờ, Giang Phi đ&atilde; l&agrave;m xong b&agrave;i tập của m&igrave;nh. Với tr&aacute;i tim nặng trĩu, Giang Phi gục xuống giường v&agrave; nh&igrave;n chằm chằm v&agrave;o đồng hồ đang tr&ocirc;i đi. Kh&ocirc;ng qu&aacute; gần m&agrave; cũng chẳng qu&aacute; xa&hellip;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ư ...&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">M&aacute;y chủ ch&iacute;nh thức của Dawn Break đ&atilde; ra mắt l&uacute;c 10 giờ tối h&ocirc;m đ&oacute;. Từ đ&oacute;, c&aacute;c game thủ sẽ tận hưởng trung b&igrave;nh 8 giờ vui vẻ, kết th&uacute;c phi&ecirc;n chơi l&agrave;nh mạnh của họ l&uacute;c 6 giờ s&aacute;ng. Đ&oacute; l&agrave; khoảng thời gian được đề xuất, &iacute;t nhất. N&oacute; tương đương với 24 giờ đầy đủ trong tr&ograve; chơi!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Mẹ kiếp! Sắp bắt đầu rồi! Tr&ograve; chơi thế kỷ sắp bắt đầu, c&ograve;n ta ở đ&acirc;y, kh&ocirc;ng l&agrave;m g&igrave; tr&ecirc;n giường! URGH!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giang Phi quấn một chiếc gối quanh mặt v&agrave; h&eacute;t l&ecirc;n một tiếng như bị b&oacute;p nghẹt. Ng&oacute;n tay hắn t&igrave;nh cờ bắt ch&eacute;o, một trong số ch&uacute;ng chạm v&agrave;o chiếc nhẫn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Tiếp nhận c&aacute;c xung thần kinh mạnh mẽ ...&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Kết nối mục ti&ecirc;u ... Game: Dawn Break.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Đang kết nối&hellip;&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Kết nối đ&iacute;ch đ&atilde; được thiết lập! Đang nhận dữ liệu&hellip;&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Đ&atilde; nhận dữ liệu! Đang thiết lập giao diện&hellip;&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Giao diện đ&atilde; ho&agrave;n th&agrave;nh! Người d&ugrave;ng c&oacute; thể tiếp tục!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;C&aacute;i qu&aacute;i g&igrave; thế?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giang Phi h&aacute; hốc mồm ngạc nhi&ecirc;n. Hắn đứng thẳng người v&agrave; nh&igrave;n chằm chằm v&agrave;o chiếc nhẫn ph&aacute;t s&aacute;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ầm ầm ầm!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một luồng s&aacute;ng trắng s&aacute;ng rực bật ra, l&agrave;m ch&oacute;i mắt Giang Phi trong một gi&acirc;y ngắn ngủi. Khi &aacute;nh mắt kh&ocirc;i phục, hắn đ&atilde; kh&ocirc;ng c&ograve;n đứng trong ph&ograve;ng của m&igrave;nh nữa!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ding! Ch&agrave;o mừng chủ nh&acirc;n đến với thế giới của Dawn Break! Ch&agrave;o mừng NGƯỜI D&Ugrave;NG # 34784! T&ocirc;i l&agrave; trợ l&yacute; của chủ nh&acirc;n!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tiếng một c&ocirc; g&aacute;i trẻ trung vui vẻ vang l&ecirc;n b&ecirc;n tai hắn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Dawn Break? Đ&acirc;y chẳng phải l&agrave; m&agrave;n h&igrave;nh đăng nhập sao?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giang Phi kh&ocirc;ng thể tin v&agrave;o mắt m&igrave;nh. Sự sợ h&atilde;i v&agrave; lo lắng ngay lập tức được thay thế bằng sự phấn kh&iacute;ch.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Hừm. Đ&acirc;y ch&iacute;nh x&aacute;c kh&ocirc;ng phải l&agrave; m&agrave;n h&igrave;nh đăng nhập. Đ&acirc;y l&agrave; menu tạo nh&acirc;n vật!&quot; trợ l&yacute; n&oacute;i.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;C&oacute; phải ... ta đang mơ kh&ocirc;ng?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Vẫn quay cuồng, một phần n&atilde;o của Giang Phi cố gắng nắm bắt t&igrave;nh h&igrave;nh. Hắn thậm ch&iacute; kh&ocirc;ng c&oacute; một pod chơi game, để bắt đầu. Hắn thấy thế n&agrave;o ở t&igrave;nh thế n&agrave;y? Đ&acirc;y hẳn l&agrave; một giấc mơ! Hắn c&oacute; lẽ đ&atilde; qu&aacute; kiệt sức với đống b&agrave;i tập về nh&agrave;! Tr&ecirc;n hết, hắn vẫn c&ograve;n tr&ecirc;n giường! Nhẫn đột nhi&ecirc;n ph&aacute;t s&aacute;ng!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ced8f96f-7fff-9c24-c7a9-d12cc82dcdf4\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ng&agrave;y d&agrave;nh cho sự thật lạnh l&ugrave;ng, phũ ph&agrave;ng v&agrave; đ&ecirc;m d&agrave;nh cho những giấc mơ v&agrave; tưởng tượng! Đ&oacute; l&agrave; c&aacute;ch duy nhất m&agrave; điều n&agrave;y c&oacute; &yacute; nghĩa. Bộ n&atilde;o của hắn ta phải chuyển đổi mong muốn của m&igrave;nh đối với tr&ograve; chơi ở đ&acirc;u đ&oacute;, bằng c&aacute;ch n&agrave;o đ&oacute; hoặc l&agrave; n&oacute; sẽ nổ tung.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n', 'Cà Di', '2020-09-25 11:03:03', 1, 0, 310);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(27, 'Chương 2: Lần đăng nhập đầu tiên', 'chuong-2-lan-djang-nhap-djau-tien', 10, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Nếu thế giới thức dậy đ&atilde; từ chối t&ocirc;i thi&ecirc;n đường n&agrave;y, t&ocirc;i sẽ nhận bất cứ điều g&igrave; m&agrave; Neverland cung cấp!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tinh thần của anh ấy đột nhi&ecirc;n phấn chấn khi c&aacute;ch tiếp cận &quot;bỏ mọi thứ v&agrave; ngừng suy nghĩ&quot; đ&atilde; được quyết định, anh ấy tiếp tục v&agrave; tạo ra nh&acirc;n vật tr&ograve; chơi đầu ti&ecirc;n của m&igrave;nh theo hướng dẫn của trợ l&yacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;H&atilde;y chọn chủng tộc của bạn!&quot; C&ocirc; g&aacute;i trẻ vui vẻ k&ecirc;u l&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một tiếng chu&ocirc;ng r&otilde; r&agrave;ng vang l&ecirc;n trong kh&ocirc;ng kh&iacute;, v&agrave; c&aacute;c đại diện nam của bốn chủng tộc kh&aacute;c nhau xuất hiện trước mắt anh; Elf, Orc, Human v&agrave; Spectre.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi Giang Phi r&egrave;n giũa nam Y&ecirc;u tinh, một th&ocirc;ng b&aacute;o xuất hiện cho biết:</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Những người t&iacute; hon. Một chủng tộc y&ecirc;u h&ograve;a b&igrave;nh c&oacute; năng khiếu với cơ thể mạnh mẽ, nhanh nhẹn. Họ sở hữu một t&agrave;i năng thi&ecirc;n bẩm về điều khiển ma thuật.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tăng trưởng sức mạnh: B</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tăng trưởng sức khỏe: C</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tăng trưởng nhanh nhẹn: A</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tăng trưởng tr&iacute; tuệ: A</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tăng trưởng tinh thần: B</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Điểm thưởng thuộc t&iacute;nh ban đầu: Nhanh nhẹn +1 Tr&iacute; tuệ +1</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Race Perk: Child of the Forest. Nhận 10% tổng số Điểm thuộc t&iacute;nh thưởng bất cứ khi n&agrave;o nh&acirc;n vật ở trong Địa h&igrave;nh rừng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giang Phi gật đầu sau khi đọc to&agrave;n bộ tin nhắn v&agrave; chuyển sang ba người c&ograve;n lại.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bọn Orc. Được biết đến với sự dũng cảm chỉ huy v&agrave; sức mạnh chế ngự của họ. Một chủng tộc chiến binh coi trọng sức mạnh hơn tất cả. Sở hữu cơ bắp d&agrave;y đặc hơn đ&aacute;ng kể v&agrave; c&ugrave;ng với đ&oacute; l&agrave; sức mạnh to lớn. Để đổi lấy sức mạnh n&agrave;y, họ kh&ocirc;ng c&oacute; bất kỳ năng khiếu n&agrave;o về ph&eacute;p thuật.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tăng trưởng sức mạnh: A</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tăng trưởng sức khỏe: A</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tăng trưởng nhanh nhẹn: B</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tăng trưởng tr&iacute; tuệ: C</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tăng trưởng tinh thần: B</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Điểm thưởng thuộc t&iacute;nh ban đầu: Sức mạnh +1 M&aacute;u +2</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Race Perk: Berserk. Ngay lập tức tăng 50% Sức tấn c&ocirc;ng v&agrave; giảm 50% Ph&ograve;ng thủ. K&eacute;o d&agrave;i trong 10 gi&acirc;y. Thời gian hồi chi&ecirc;u: 10 ph&uacute;t.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Con người. Một tổng hợp tất cả c&aacute;c giao dịch. Một chủng tộc biệt lập theo đuổi kiến ​​thức trong khi trớ tr&ecirc;u thay lại biết rất &iacute;t về c&aacute;c chủng tộc kh&aacute;c. Con người c&oacute; thể bắt đầu ở mức trung b&igrave;nh, nhưng tiềm năng ph&aacute;t triển của họ l&agrave; v&ocirc; hạn.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tăng trưởng sức mạnh: B</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tăng trưởng sức khỏe: B</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tăng trưởng nhanh nhẹn: B</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tăng trưởng tr&iacute; tuệ: B</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tăng trưởng tinh thần: A</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Điểm thưởng thuộc t&iacute;nh ban đầu: Sức khỏe +1 Tinh thần +2</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Race Perk: Tiến h&oacute;a. Một nh&acirc;n vật Con người c&oacute; khả năng ph&aacute;t triển th&agrave;nh một chủng tộc kh&aacute;c th&ocirc;ng qua một nhiệm vụ chủng tộc ẩn. T&igrave;m kiếm v&agrave; ho&agrave;n th&agrave;nh nhiệm vụ ẩn để ph&aacute;t triển th&agrave;nh một chủng tộc kh&aacute;c. (Lưu &yacute;: C&oacute; 36 Chủng tộc kh&aacute;c nhau. Chỉ một người chơi mới c&oacute; thể ho&agrave;n th&agrave;nh Nhiệm vụ Tiến h&oacute;a v&agrave; mỗi Người gi&aacute;m hộ sẽ chỉ đưa ra một Nhiệm vụ Tiến h&oacute;a. N&ecirc;n c&acirc;n nhắc kỹ lưỡng.)</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">The Unyielding One: Ho&agrave;n th&agrave;nh một Nhiệm vụ Tiến h&oacute;a sẽ ban cho người chơi phước l&agrave;nh của The Unyielding One, cấp cho người chơi th&ecirc;m 5% Điểm kinh nghiệm (EXP) cho kẻ th&ugrave; bị giết, phần thưởng nhiệm vụ v&agrave; mặt nạ kỹ năng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&oacute;ng ma. X&aacute;c sống. Chưa chết. Họ đ&atilde; tho&aacute;t khỏi sự kiểm so&aacute;t của Ho&agrave;ng đế B&oacute;ng tối v&agrave; tự do theo &yacute; m&igrave;nh. Cố chấp v&agrave;o dạng tồn tại phi tự nhi&ecirc;n n&agrave;y bằng sức mạnh &yacute; ch&iacute; tuyệt đối, họ chỉ t&igrave;m c&aacute;ch mở rộng sự hiện diện của m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tăng trưởng sức mạnh: A</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tăng trưởng sức khỏe: B</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tăng trưởng nhanh nhẹn: B</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tăng trưởng tr&iacute; tuệ: A</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tăng trưởng tinh thần: C</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Điểm thưởng thuộc t&iacute;nh ban đầu: Sức mạnh +2 Tr&iacute; tuệ +1</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Race Perk: Spectre&#39;s Will. Loại bỏ hiệu ứng của Ph&eacute;p thuật dựa tr&ecirc;n Tinh thần v&agrave; nhận được Miễn dịch trong 5 gi&acirc;y. Thời gian hồi chi&ecirc;u 10 ph&uacute;t.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;T&ocirc;i đang ở trong một giấc mơ; điều n&agrave;y kh&ocirc;ng thể c&oacute; thật. Cũng c&oacute; thể vươn tới c&aacute;c v&igrave; sao! H&atilde;y đi n&agrave;o! V&igrave; lo&agrave;i người!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nếu Giang Phi chơi tr&ograve; chơi trong một nh&oacute;m thực sự th&igrave; đ&oacute; sẽ l&agrave; tộc Elf, kh&ocirc;ng nghi ngờ g&igrave; nữa. Anh ấy chưa bao giờ nghĩ m&igrave;nh l&agrave; một cầu thủ chuy&ecirc;n nghiệp, nơi m&agrave; to&agrave;n bộ c&ocirc;ng ty sẽ t&agrave;i trợ cho sự tiến bộ của anh ấy, thực sự trả tiền cho anh ấy để chơi. Hầu như kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o để anh ta đầu tư nhiều c&ocirc;ng sức v&agrave; đối đầu với những người chơi chuy&ecirc;n nghiệp thực sự để cạnh tranh cho những nhiệm vụ Tiến h&oacute;a Ẩn đ&oacute;. Đ&acirc;y l&agrave; một giấc mơ. Ai giải quyết cho những quyết định tầm thường v&agrave; kh&ocirc;ng c&oacute; rủi ro trong những giấc mơ ?! Nếu bạn kh&ocirc;ng thực hiện hết m&igrave;nh trong giấc mơ, bạn c&oacute; một vấn đề nghi&ecirc;m trọng!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Lựa chọn đ&atilde; được x&aacute;c nhận! Nh&acirc;n loại đ&atilde; được chọn! Vui l&ograve;ng đợi ...&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Vui l&ograve;ng điều chỉnh ngoại h&igrave;nh nh&acirc;n vật của bạn! Vui l&ograve;ng điều chỉnh cấp độ v&agrave; cường độ xuất hiện. Bạn cũng c&oacute; thể tự điều chỉnh ngoại h&igrave;nh của m&igrave;nh! H&atilde;y lưu &yacute;; tất cả c&aacute;c t&iacute;nh năng chỉ c&oacute; thể được tinh chỉnh tối đa 20%!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&acirc;y l&agrave; giai đoạn điều chỉnh ngoại h&igrave;nh nh&acirc;n vật, một t&iacute;nh năng thường thấy trong hầu hết c&aacute;c tr&ograve; chơi trực tuyến. Người chơi nam thường chọn chức năng ngẫu nhi&ecirc;n h&oacute;a, trong khi người chơi nữ sẽ mất nhiều thời gian cần thiết để cải thiện nhiều nhất c&oacute; thể. Nhấn mạnh v&agrave;o c&oacute; thể - nếu kh&ocirc;ng th&igrave; bầu trời l&agrave; giới hạn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Cải thiện tất cả c&aacute;c tab l&ecirc;n 10%!&quot; Giang Phi n&oacute;i kh&ocirc;ng một ch&uacute;t dừng lại. Anh chỉ c&oacute; thể mơ trong rất nhiều giờ, v&agrave; anh c&oacute; thể sẽ kh&ocirc;ng bao giờ quay lại với n&oacute; nữa - hoặc anh nghĩ vậy. Anh ấy sẽ kh&ocirc;ng l&atilde;ng ph&iacute; bất kỳ thời gian n&agrave;o trong giai đoạn tạo nh&acirc;n vật. Nếu anh ta &quot;thức dậy&quot; ở đ&oacute; v&agrave; sau đ&oacute;, tr&ograve; chơi sẽ kết th&uacute;c.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Đang trong qu&aacute; tr&igrave;nh tạo nh&acirc;n vật ... Vui l&ograve;ng đợi!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Xin h&atilde;y nhập t&ecirc;n của bạn!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một hộp nhập liệu với ti&ecirc;u đề &quot;T&ecirc;n&quot; xuất hiện trước mặt anh, cũng như một người đ&agrave;n &ocirc;ng c&oacute; khu&ocirc;n mặt hơi thay đổi của &quot;Giang Phi&quot; đang đứng ở độ cao tương tự như đối t&aacute;c ngo&agrave;i đời của n&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Verdure Glider!&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giang Phi đ&atilde; bị troll ho&agrave;n to&agrave;n. Trong c&aacute;c tr&ograve; chơi kh&aacute;c, Giang Phi sẽ kh&ocirc;ng bao giờ l&agrave; một c&aacute;i t&ecirc;n th&ocirc; lỗ. Tuy nhi&ecirc;n&hellip; Đ&acirc;y l&agrave; một giấc mơ. Tại sao phải d&ugrave;ng tr&iacute; tuệ qu&yacute; gi&aacute; để nghĩ ra một c&aacute;i t&ecirc;n hay ho ?!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Tạo nh&acirc;n vật đ&atilde; ho&agrave;n tất. B&acirc;y giờ bạn sẽ được đưa đến L&agrave;ng d&agrave;nh cho người mới bắt đầu NO2015!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một tiếng chu&ocirc;ng lớn vang l&ecirc;n, v&agrave; một &aacute;nh s&aacute;ng lại l&oacute;e l&ecirc;n. &Aacute;nh s&aacute;ng nhấn ch&igrave;m cả Giang Phi v&agrave; nh&acirc;n vật anh vừa tạo ra. Sau một gi&acirc;y ngắn ngủi, Giang Phi đứng dậy v&agrave; nhận ra xung quanh m&igrave;nh đ&atilde; thay đổi trở lại.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;HARGH! CỨU RỒI!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một người n&agrave;o đ&oacute; ở v&ugrave;ng đồng bằng xa x&ocirc;i đ&atilde; h&eacute;t l&ecirc;n tận c&ugrave;ng phổi khi anh ta băng kiếm với một số kẻ th&ugrave; chưa nh&igrave;n thấy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Đầu giờ s&aacute;ng, thưa ng&agrave;i, t&ocirc;i c&oacute; thể gi&uacute;p g&igrave; được?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người b&aacute;n h&agrave;ng gọi những người chơi đi ngang qua cửa h&agrave;ng của m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&oacute; chỉ l&agrave; một v&agrave;i trong số những tiếng động m&agrave; Giang Phi đ&atilde; nhặt được. Với một nụ cười toe to&eacute;t, Giang Phi trở n&ecirc;n phấn kh&iacute;ch.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Kh&ocirc;ng tệ! Kh&ocirc;ng tồi ch&uacute;t n&agrave;o! Thật l&agrave; một giấc mơ hiện thực.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Bạn đang n&oacute;i g&igrave; vậy? Shengshi đ&atilde; d&agrave;nh nhiều năm để ph&aacute;t triển tr&ograve; chơi n&agrave;y! L&agrave;m thế n&agrave;o n&oacute; c&oacute; thể kh&ocirc;ng c&oacute; thật! Đ&oacute; l&agrave; niềm tự h&agrave;o của tr&ograve; chơi!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một người chơi đứng b&ecirc;n cạnh Giang Phi đ&atilde; khịt mũi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Hmm? L&agrave;m thế n&agrave;o- T&ocirc;i c&oacute; thể- L&agrave;m thế n&agrave;o c&oacute; thể tương t&aacute;c trong một giấc mơ?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giang Phi c&agrave;ng lo lắng. C&oacute; g&igrave; đ&oacute; xa c&aacute;ch. Khung cảnh, người chơi, hội thoại, kh&ocirc;ng kh&iacute;&hellip; Tất cả đều qu&aacute; ch&acirc;n thực!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;T&ocirc;i sẽ thử v&eacute;o m&igrave;nh!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giang Phi đưa tay v&eacute;o lấy ch&acirc;n phải của m&igrave;nh - thật mạnh!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Hở?!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cơn đau nh&oacute;i khắp cơ thể anh. Ch&uacute;ng l&agrave; kiểu đau đớn kh&ocirc;ng thuộc về giấc mơ v&agrave; nếu n&oacute; xảy ra, sẽ khiến những người ngủ s&acirc;u nhất thức giấc!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Chuyện g&igrave; đang xảy ra vậy ?! Đau qu&aacute;!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giang Phi thở hổn hển.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Chuyện qu&aacute;i g&igrave; đang xảy ra vậy ?! Chuyện n&agrave;y c&oacute; thật kh&ocirc;ng ?! T&ocirc;i thực sự đang tham gia tr&ograve; chơi sao?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giang Phi ảo n&atilde;o đứng l&ecirc;n, tự m&igrave;nh vỗ nhẹ bụi đất. Anh ta bắt đầu nhớ lại c&aacute;c quảng c&aacute;o trước đ&acirc;y của tr&ograve; chơi. Đ&atilde; c&oacute; cuộc thảo luận về c&aacute;ch thậm ch&iacute; mức độ cảm gi&aacute;c c&oacute; thể được điều chỉnh từ 20% đến 0%. Nếu một người chọn mức cực đoan tất cả l&agrave; 0%, người ta c&oacute; thể mất một phần lớn những g&igrave; tr&ograve; chơi c&oacute; thể cung cấp. Đương nhi&ecirc;n, hầu hết người chơi nữ sẽ chọn 0%.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;&Ocirc;i trời! T&ocirc;i đang ở trong tr&ograve; chơi!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&uacute;ng l&uacute;c đ&oacute;, Giang Phi ở tr&ecirc;n ch&iacute;n tầng m&acirc;y. Trong khoảnh khắc đ&oacute;, anh đ&atilde; ho&agrave;n to&agrave;n qu&ecirc;n mất chiếc nhẫn b&iacute; ẩn vẫn đeo tr&ecirc;n ng&oacute;n tay m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Hả? Em thế n&agrave;o với anh?&quot; Giang Phi n&oacute;i, đ&ocirc;i mắt của anh ta cuối c&ugrave;ng cũng bắt được tia s&aacute;ng. C&aacute;c đối tượng trong thế giới thực thường kh&ocirc;ng theo người d&ugrave;ng của họ v&agrave;o tr&ograve; chơi. Tuy nhi&ecirc;n&hellip; Nếu chiếc nhẫn chịu tr&aacute;ch nhiệm cho việc anh ấy c&oacute; mặt trong tr&ograve; chơi, th&igrave; n&oacute; cũng c&oacute; mọi quyền ở trong tr&ograve; chơi!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi anh xoa chiếc nhẫn, một cửa sổ bật l&ecirc;n đột ngột khiến Giang Phi giật m&igrave;nh. Đ&oacute; l&agrave; trang kiểm k&ecirc; 36 vị tr&iacute;!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Hohoho ... Đ&acirc;y l&agrave; một may mắn!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giang Phi mỉm cười. L&agrave; một game thủ kỳ cựu l&acirc;u năm, anh ấy đều qu&aacute; hiểu những lợi &iacute;ch đi k&egrave;m với một kho đồ lớn, đặc biệt l&agrave; trong giai đoạn đầu của tr&ograve; chơi!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;T&ocirc;i sẽ kh&ocirc;ng suy nghĩ qu&aacute; nhiều về điều n&agrave;y. N&oacute; ở đ&acirc;y, n&oacute; đang l&agrave;m g&igrave; đ&oacute;, v&igrave; vậy t&ocirc;i sẽ sử dụng n&oacute;!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-6d0c7b40-7fff-a49c-0fd6-2538a343c3ed\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kh&ocirc;ng c&ograve;n g&igrave; ngo&agrave;i việc chơi game, Giang Phi quyết định đi s&acirc;u v&agrave;o tr&ograve; chơi. Nhẫn g&igrave;? C&ograve;n n&oacute; th&igrave; sao?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n', 'Cà Di', '2020-09-25 11:03:28', 1, 0, 410);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(28, 'Chương 1: Chuyên gia vũ khí - Lãnh Thiên - phần 2', 'chuong-1-chuyen-gia-vu-khi-lanh-thien-phan-2', 5, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Thưa chủ nh&acirc;n, Trưởng tộc đ&atilde; gửi một &iacute;t thuốc sang.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một giọng n&oacute;i vang l&ecirc;n từ b&ecirc;n tai, khiến Đo&agrave;n L&atilde;nh Thi&ecirc;n phải nh&igrave;n l&ecirc;n nơi ph&aacute;t ra giọng n&oacute;i.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một c&ocirc; g&aacute;i người hầu vội v&atilde; bước v&agrave;o ph&ograve;ng v&agrave; đưa chiếc hộp nhỏ v&agrave; tinh xảo v&agrave;o tay cho L&yacute; Dung.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một hương thơm thuốc bay trong kh&ocirc;ng kh&iacute; ngay khi chiếc hộp nhỏ được mở ra.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Thi&ecirc;n nhi, ​​đến đ&acirc;y; uống thuốc n&agrave;y. mẫu th&acirc;n sẽ chữa trị vết thương cho con &rdquo;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&yacute; Dung lấy ra một vi&ecirc;n thuốc c&oacute; vẻ ngo&agrave;i sẫm m&agrave;u trong hộp v&agrave; hướng n&oacute; đến trước miệng của Đo&agrave;n L&atilde;nh Thi&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">H&iacute;t một hơi thật s&acirc;u, Đo&agrave;n L&atilde;nh Thi&ecirc;n mở miệng v&agrave; nuốt vi&ecirc;n thuốc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Vi&ecirc;n thuốc tan ra khi v&agrave;o miệng rồi biến th&agrave;nh một d&ograve;ng điện ấm khi đi v&agrave;o cơ thể hắn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau đ&oacute;, Đo&agrave;n L&atilde;nh Thi&ecirc;n nhận thấy rằng L&yacute; Dung đ&atilde; đặt tay b&agrave; l&ecirc;n ngực hắn&hellip;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một luồng điện lạnh băng truyền v&agrave;o cơ thể hắn, l&agrave;m tan biến d&ograve;ng điện ấm m&agrave; vi&ecirc;n thuốc mang lại để chữa trị vết thương cho hắn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một giờ sau, L&yacute; Dung kết th&uacute;c việc chữa trị.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đo&agrave;n L&atilde;nh Thi&ecirc;n nhận thấy rằng một nửa cơn đau nhức tr&ecirc;n cơ thể hắn đ&atilde; biến mất, kh&ocirc;ng c&ograve;n bất kỳ căng thẳng n&agrave;o.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn giật m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&oacute; phải thứ hắn ta uống vừa rồi l&agrave; một loại thần dược n&agrave;o đ&oacute; kh&ocirc;ng?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong khi đ&oacute;, Đo&agrave;n L&atilde;nh Thi&ecirc;n nhận thấy rằng khu&ocirc;n mặt của L&yacute; Dung t&aacute;i nhợt, những giọt mồ h&ocirc;i lạnh chảy ra tr&ecirc;n tr&aacute;n. R&otilde; r&agrave;ng, b&agrave; ấy đ&atilde; ti&ecirc;u tốn qu&aacute; nhiều nguy&ecirc;n kh&iacute; để chữa trị vết thương cho hắn ta.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Mẫu th&acirc;n, mẫu th&acirc;n ổn chứ?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tr&aacute;i tim của Đo&agrave;n L&atilde;nh Thi&ecirc;n cảm thấy ấm &aacute;p.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giọng hắn ta c&oacute; vẻ th&acirc;n mật, kh&ocirc;ng ch&uacute;t giả tạo.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Mẫu th&acirc;n vẫn ổn. Ta chỉ cần nghỉ ngơi một thời gian &rdquo;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&yacute; Dung vừa lắc đầu vừa cười.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&igrave; lợi &iacute;ch của con trai b&agrave;, ti&ecirc;u tốn ch&uacute;t &iacute;t nguy&ecirc;n kh&iacute; n&agrave;y chẳng l&agrave; g&igrave; cả.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nửa giờ sau, người hầu g&aacute;i bước v&agrave;o ph&ograve;ng, nhẹ giọng n&oacute;i: &ldquo;Thưa chủ nh&acirc;n, Trưởng tộc đ&atilde; ph&aacute;i người đến hỏi chủ nh&acirc;n c&oacute; thả T&iacute;n thiếu gia kh&ocirc;ng? Trưởng tộc v&agrave; Thất trưởng l&atilde;o vẫn đang đợi trong Gi&aacute;o h&oacute;a đường. &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Thả hắn ta? Đi n&oacute;i với người m&agrave; Tổ phụ ph&aacute;i tới, chuyện n&agrave;y ta sẽ tự m&igrave;nh giải quyết! &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Vẻ dịu d&agrave;ng v&agrave; h&ograve;a nh&atilde; tr&ecirc;n khu&ocirc;n mặt của L&yacute; Dung ho&agrave;n to&agrave;n biến mất, thay v&agrave;o đ&oacute; l&agrave; vẻ mặt lạnh như băng, khiến kh&ocirc;ng kh&iacute; xung quanh cũng lạnh hơn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tr&aacute;i tim của Đo&agrave;n L&atilde;nh Thi&ecirc;n khuấy động.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn chưa bao giờ nghĩ rằng người phụ nữ xinh đẹp như một đ&oacute;a hoa n&agrave;y lại c&oacute; thể che chở đến vậy&hellip;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Mẫu th&acirc;n, vi&ecirc;n thuốc l&uacute;c n&atilde;y người cho con uống l&agrave; g&igrave; vậy? Con cảm thấy như những vết thương của m&igrave;nh gần như được chữa l&agrave;nh rồi &rdquo;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đo&agrave;n L&atilde;nh Thi&ecirc;n bật dậy khỏi giường, nhẹ nh&agrave;ng duỗi người.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Ngốc tử, đ&oacute; l&agrave;&lsquo; Ho&agrave;ng kim đan &rsquo;,&rdquo; L&yacute; Dung cười nhẹ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ho&agrave;ng kim đan!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đo&agrave;n L&atilde;nh Thi&ecirc;n lướt qua những k&yacute; ức xa lạ trong t&acirc;m tr&iacute;&hellip;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">H&oacute;a ra l&agrave; ngay cả trong L&yacute; gia của T&acirc;n Phong trấn, cũng chỉ c&oacute; hai vi&ecirc;n Ho&agrave;ng kim đan n&agrave;y. B&acirc;y giờ, một vi&ecirc;n đ&atilde; được sử dụng cho hắn ta, giờ chỉ c&ograve;n lại một vi&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&oacute; vẻ như mẫu th&acirc;n hắn ta c&oacute; địa vị kh&aacute; cao trong L&yacute; gia.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Thi&ecirc;n nhi, ​​Tổ phụ đang đợi ch&uacute;ng ta. Đi n&agrave;o.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&yacute; Dung đưa Đo&agrave;n L&atilde;nh Thi&ecirc;n đến một s&acirc;n rộng r&atilde;i trong điền trang của L&yacute; gia.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&aacute;c c&ocirc;ng cụ tra tấn được đặt rải r&aacute;c khắp s&acirc;n rộng lớn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&acirc;y l&agrave; nơi m&agrave; c&aacute;c h&igrave;nh phạt trong L&yacute; gia được thực hiện: Gi&aacute;o h&oacute;a đường!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hiện tại l&agrave; giữa trưa, v&agrave; mặt trời rực ch&aacute;y tr&ecirc;n bầu trời cao ...</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Xung quanh Gi&aacute;o h&oacute;a đường chật n&iacute;ch người. C&aacute;c đệ tử của L&yacute; gia đ&atilde; đến xem buổi x&eacute;t xử.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ở giữa s&acirc;n, một thanh ni&ecirc;n bị tr&oacute;i v&agrave;o cột dưới c&aacute;i nắng ch&oacute;i chang, hai người đ&agrave;n &ocirc;ng trung ni&ecirc;n đứng b&ecirc;n cạnh canh giữ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người đ&agrave;n &ocirc;ng trung ni&ecirc;n c&oacute; cặp mắt diều h&acirc;u, ở ph&iacute;a b&ecirc;n phải c&oacute; n&eacute;t mặt v&ocirc; c&ugrave;ng kh&oacute; coi khi nhận thấy sự xuất hiện của Đo&agrave;n L&atilde;nh Thi&ecirc;n v&agrave; mẹ của hắn ta.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Đo&agrave;n L&atilde;nh Thi&ecirc;n đ&atilde; đến&hellip; Ho&agrave;ng kim đan của gia tộc c&oacute; t&aacute;c dụng mạnh như vậy!&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Đ&uacute;ng vậy, t&ocirc;i đ&atilde; ở đ&oacute; khi Đo&agrave;n L&atilde;nh Thi&ecirc;n bị mang đi, sống dở chết dở, nhưng b&acirc;y giờ, sau khi uống Ho&agrave;ng kim đan, cậu ấy đ&atilde; trở lại đầy mạnh mẽ v&agrave; tr&agrave;n đầy sức sống.&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Th&ocirc;ng thường, L&yacute; T&iacute;n chỉ t&igrave;nh cờ bắt nạt Đo&agrave;n L&atilde;nh Thi&ecirc;n v&agrave; cửu trưởng l&atilde;o sẽ kh&ocirc;ng n&oacute;i g&igrave; về điều đ&oacute;. Tuy nhi&ecirc;n, lần n&agrave;y hắn ta đ&atilde; g&acirc;y ra những vết thương nặng đến nỗi Đo&agrave;n L&atilde;nh Thi&ecirc;n gần như sắp chết&hellip; Tất nhi&ecirc;n l&agrave; cửu trưởng l&atilde;o sẽ rất tức giận. &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&hellip;&hellip;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Từng người một, c&aacute;c đệ tử của L&yacute; gia bắt đầu th&igrave; thầm với nhau khi họ nhận thấy sự xuất hiện của L&yacute; Dung v&agrave; Đo&agrave;n L&atilde;nh Thi&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Tổ phụ!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&yacute; Dung đưa Đo&agrave;n L&atilde;nh Thi&ecirc;n đến để ch&agrave;o người đ&agrave;n &ocirc;ng trung ni&ecirc;n b&ecirc;n tr&aacute;i.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Cửu ca, về chuyện n&agrave;y, con trai t&ocirc;i, L&yacute; T&iacute;n đ&atilde; sai. Mặt kh&aacute;c, con trai của c&ocirc; b&acirc;y giờ ổn rồi, vậy m&agrave; con trai t&ocirc;i đ&atilde; bị nắng ch&aacute;y cả buổi chiều. B&acirc;y giờ ch&uacute;ng ta c&oacute; thể dừng chuyện n&agrave;y lại được kh&ocirc;ng?&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave;o l&uacute;c n&agrave;y, người đ&agrave;n &ocirc;ng trung ni&ecirc;n c&oacute; đ&ocirc;i mắt thập phần đăm chi&ecirc;u, Thất trưởng l&atilde;o của L&yacute; gia th&igrave; vẻ mặt v&ocirc; c&ugrave;ng u &aacute;m.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ai c&oacute; thể nghĩ rằng L&yacute; Dung thậm ch&iacute; kh&ocirc;ng th&egrave;m trả lời v&agrave; trực tiếp phớt lờ &ocirc;ng ta.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&yacute; Dung đưa Đo&agrave;n L&atilde;nh Thi&ecirc;n đến trước L&yacute; T&iacute;n đang bị tr&oacute;i.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f663d27c-7fff-532e-200e-36bdbc44d949\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Thi&ecirc;n nhi, ​​b&acirc;y giờ đ&atilde; đến l&uacute;c con trả </span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(60, 64, 67); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">lại cho hắn những vết thương m&agrave; hắn đ&atilde; g&acirc;y ra cho con.</span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;</span></span></p>\r\n', 'Cà Di', '2020-10-02 09:15:44', 1, 0, 229);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(29, 'Chương 2: Sức mạnh của một con dã tượng', 'chuong-2-suc-manh-cua-mot-con-da-tuong-1', 5, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&atilde;nh Thi&ecirc;n bắt đầu cười lớn.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&aacute;nh bao c&aacute;t, anh ta l&agrave; chuy&ecirc;n gia trong việc đ&oacute;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&yacute; T&iacute;n bằng tuổi Đo&agrave;n L&atilde;nh Thi&ecirc;n, hắn đ&atilde; bị tr&oacute;i chặt v&agrave;o cột dưới &aacute;nh mặt trời ch&oacute;i chang, khiến sắc mặt hắn đ&atilde; t&aacute;i xanh từ l&acirc;u&hellip;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nghe những lời của L&yacute; Dung, hắn ta sợ h&atilde;i, t&acirc;m trạng rơi v&agrave;o trạng th&aacute;i bất ổn v&agrave; g&agrave;o l&ecirc;n, &quot;Phụ th&acirc;n, cứu con!&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;L&yacute; Dung, đừng đi qu&aacute; xa!&quot; Khu&ocirc;n mặt của Thất trưởng l&atilde;o L&yacute; Kh&ocirc;n ch&igrave;m xuống khi &ocirc;ng ta bộc ph&aacute;t cơn thịnh nộ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Đừng đi qu&aacute; xa? L&yacute; Kh&ocirc;n, con trai của ngươi bắt nạt con trai của ta. V&igrave; kỹ năng của n&oacute; k&eacute;m hơn, ta chấp nhận rằng chấn thương nhẹ l&agrave; kh&oacute; tr&aacute;nh khỏi. Nhưng lần n&agrave;y, con trai của ngươi su&yacute;t giết n&oacute;&hellip; Ngươi c&oacute; thực sự nghĩ rằng ta, L&yacute; Dung, l&agrave; một mục ti&ecirc;u dễ bắt nạt kh&ocirc;ng? &rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&yacute; Dung lạnh l&ugrave;ng liếc nh&igrave;n L&yacute; Kh&ocirc;n; giọng n&oacute;i của b&agrave; ấy ẩn chứa tức giận lạnh k&egrave;m theo h&agrave;n kh&iacute;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Thi&ecirc;n nhi, ​​đừng do dự v&agrave; tấn c&ocirc;ng. H&ocirc;m nay, để ta xem ai d&aacute;m cản đường ta kỷ luật t&ecirc;n n&agrave;y! &rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giọng n&oacute;i của L&yacute; Dung tr&agrave;n đầy tinh thần dũng cảm của một nữ anh h&ugrave;ng.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mặc d&ugrave; l&agrave; phụ nữ, nhưng phong th&aacute;i uy nghi&ecirc;m m&agrave; b&agrave; ấy to&aacute;t ra kh&ocirc;ng thua g&igrave; một người đ&agrave;n &ocirc;ng!</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Gi&aacute;o chủ.&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&yacute; Kh&ocirc;n nh&igrave;n về ph&iacute;a Gi&aacute;o chủ, L&yacute; Nam Phong b&ecirc;n cạnh, hy vọng &ocirc;ng sẽ c&oacute; c&aacute;ch n&agrave;o đ&oacute;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Thất trưởng l&atilde;o, chuyện h&ocirc;m nay l&agrave; chuyện ri&ecirc;ng giữa ngươi v&agrave; cửu trưởng l&atilde;o. Trong phạm vi lớn nhất c&oacute; thể, ta hy vọng ngươi c&oacute; thể giải quyết n&oacute; một c&aacute;ch ri&ecirc;ng tư &rdquo;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&yacute; Nam Phong lắc đầu.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Vậy ngươi l&agrave; người đ&atilde; đ&aacute;nh hắn v&agrave;o t&igrave;nh trạng khốn khổ như vậy?&quot; Đo&agrave;n L&atilde;nh Thi&ecirc;n tự lẩm bẩm.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">T&aacute;t!</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tiến l&ecirc;n một bước, trực tiếp t&aacute;t L&yacute; T&iacute;n kh&ocirc;ng n&oacute;i lời n&agrave;o.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một cơn đau nh&oacute;i nhẹ truyền ra từ l&ograve;ng b&agrave;n tay, l&ocirc;ng m&agrave;y Đo&agrave;n L&atilde;nh Thi&ecirc;n hơi nh&iacute;u lại.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Đo&agrave;n L&atilde;nh Thi&ecirc;n&hellip; h&atilde;y để ta xuống, v&agrave; ch&uacute;ng ta c&oacute; thể đấu một chọi một!&rdquo; L&yacute; T&iacute;n gầm gừ, gần như r&uacute; l&ecirc;n, nửa khu&ocirc;n mặt sưng l&ecirc;n v&agrave; đ&ocirc;i mắt chứa đầy hỏa kh&iacute;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Một chọi một? Xin lỗi, ta kh&ocirc;ng c&oacute; hứng th&uacute;&hellip; C&ograve;n về việc ta c&oacute; từ bi hay kh&ocirc;ng, đ&oacute; kh&ocirc;ng phải l&agrave; điều m&agrave; ngươi phải lo lắng. &rdquo;Đo&agrave;n L&atilde;nh Thi&ecirc;n cười khi dang hai tay ra v&agrave; hờ hững nh&uacute;n vai.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">T&aacute;t!</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn ta tặng th&ecirc;m một c&aacute;i t&aacute;t nữa v&agrave;o L&yacute; T&iacute;n.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Khu&ocirc;n mặt của anh ch&agrave;ng n&agrave;y c&oacute; l&agrave;n da rất d&agrave;y nha.&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn cau m&agrave;y khi thổi v&agrave;o l&ograve;ng b&agrave;n tay đau nhức của m&igrave;nh trong khi cảm thấy h&agrave;i l&ograve;ng với h&agrave;nh động v&igrave; sự bất b&igrave;nh của m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi c&aacute;c đệ tử L&yacute; gia xung quanh nh&igrave;n thấy h&agrave;nh động của Đo&agrave;n L&atilde;nh Thi&ecirc;n v&agrave; nghe thấy lời hắn n&oacute;i, họ kh&ocirc;ng n&oacute;i n&ecirc;n lời.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thằng kia đ&atilde; bị tr&oacute;i như bao c&aacute;t cho n&oacute; đ&aacute;nh rồi m&agrave; n&oacute; c&ograve;n k&ecirc;u da mặt d&agrave;y?</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn ta rốt cuộc bị l&agrave;m sao vậy !?</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Rốt cuộc th&igrave; gi&agrave;y vẫn l&agrave; tốt nhất&rdquo;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&uacute;i người xuống, Đo&agrave;n L&atilde;nh Thi&ecirc;n cởi một chiếc gi&agrave;y tr&ecirc;n ch&acirc;n, cầm lấy trong tay, đột nhi&ecirc;n nhắm thẳng v&agrave;o mặt L&yacute; T&iacute;n.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cảnh tượng n&agrave;y khiến cho c&aacute;c đệ tử L&yacute; gia xung quanh cảm thấy lạnh sống lưng.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Đo&agrave;n L&atilde;nh Thi&ecirc;n n&agrave;y, hắn ta kh&ocirc;ng nghĩ l&agrave; sẽ d&ugrave;ng chiếc gi&agrave;y đ&oacute; để t&aacute;t v&agrave;o mặt L&yacute; T&iacute;n chứ?&rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Nếu hắn ta thực sự t&aacute;t v&agrave;o mặt L&yacute; T&iacute;n bằng chiếc gi&agrave;y đ&oacute;, khu&ocirc;n mặt của L&yacute; T&iacute;n sẽ kh&ocirc;ng mở ra như một b&ocirc;ng hoa đang nở sao !?&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Thằng khốn nạn, m&agrave;y đang t&igrave;m c&aacute;i chết!&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&yacute; Kh&ocirc;n kh&ocirc;ng thể kiềm chế được nữa khi bắt gặp cảnh tượng n&agrave;y, th&acirc;n h&igrave;nh bay về ph&iacute;a Đo&agrave;n L&atilde;nh Thi&ecirc;n như một con rệp.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ngươi đang tự t&igrave;m c&aacute;i chết!&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&yacute; Kh&ocirc;n từ l&uacute;c n&agrave;o đ&atilde; di chuyển, tựa như ho&aacute; th&agrave;nh cả gi&oacute;, đến trước mặt Đo&agrave;n L&atilde;nh Thi&ecirc;n sớm hơn một bước.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mở rộng đ&ocirc;i tay mỏng manh tỏa ra &aacute;nh s&aacute;ng rực rỡ của nguy&ecirc;n kh&iacute;, L&yacute; Dung chưởng một chưởng về ph&iacute;a L&yacute; Kh&ocirc;n.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ugrave;ng l&uacute;c đ&oacute;, tr&ecirc;n đầu L&yacute; Dung mơ hồ xuất hiện một l&agrave;n sương trắng.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;L&yacute; Dung, h&ocirc;m nay, h&atilde;y để ta xem ngươi thực sự mạnh mẽ như thế n&agrave;o.&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&yacute; Kh&ocirc;n nghi&ecirc;m nghị nh&igrave;n khi b&agrave;n tay của anh ta hạ xuống như một chiếc quạt khổng lồ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nguy&ecirc;n kh&iacute; b&ugrave;ng nổ v&agrave; d&ugrave;ng to&agrave;n lực chặn lại một chưởng của L&yacute; Dung!</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay lập tức, bốn h&igrave;nh b&oacute;ng mờ mịt của d&atilde; tượng xuất hiện trong kh&ocirc;ng gian ph&iacute;a tr&ecirc;n L&yacute; Kh&ocirc;n.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&yacute; Kh&ocirc;n tấn c&ocirc;ng to&agrave;n lực v&agrave; thu h&uacute;t năng lượng của trời v&agrave; đất, tạo ra bốn b&oacute;ng d&atilde; tượng&hellip;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">N&oacute;i c&aacute;ch kh&aacute;c, sức mạnh hắn ta đang ph&aacute;t huy ngang bằng với sức mạnh của bốn con d&atilde; tượng!</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chỉ một con d&atilde; tượng cổ đại c&oacute; sức mạnh 10.000 pound.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Mẫu th&acirc;n ...&quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đo&agrave;n L&atilde;nh Thi&ecirc;n nh&iacute;u m&agrave;y, cảm thấy lo lắng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&aacute;c đệ tử L&yacute; gia đang chăm ch&uacute; theo d&otilde;i trận chiến giữa Thất trưởng l&atilde;o v&agrave; cửu trưởng l&atilde;o. V&igrave; cực kỳ hiếm khi chứng kiến ​​một sự kiện quy m&ocirc; như vậy.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bang!</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dưới con mắt quan s&aacute;t của những người xung quanh, L&yacute; Dung v&agrave; L&yacute; Kh&ocirc;n tiến s&aacute;t lại gần nhau v&agrave; tặng cho đối phương một chưởng.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cơ thể của cả hai đều giật nảy m&igrave;nh; Cơ thể L&yacute; Dung hơi nghi&ecirc;ng về ph&iacute;a trước, v&agrave; m&agrave;n sương trắng tr&ecirc;n đầu b&agrave; ấy cũng ph&aacute;t triển th&agrave;nh b&oacute;ng d&atilde; tượng&hellip;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tổng cộng l&agrave; s&aacute;u! Sức mạnh của s&aacute;u con d&atilde; tượng!</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sức mạnh của bốn con d&atilde; tượng, L&yacute; Kh&ocirc;n đ&atilde; đọ sức với sức mạnh của s&aacute;u con d&atilde; tượng của L&yacute; Dung. Hơn nữa, n&oacute; thậm ch&iacute; c&ograve;n l&agrave; một cuộc đối đầu trực tiếp&hellip; Kết quả l&agrave; r&otilde; r&agrave;ng.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay lập tức, L&yacute; Kh&ocirc;n giống như một mũi t&ecirc;n rời khỏi d&acirc;y cung. &Ocirc;ng ta bay ra ngo&agrave;i hơn mười m&eacute;t trong t&igrave;nh trạng tiếc nuối trước khi c&oacute; thể ổn định bản th&acirc;n.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Waa!&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khu&ocirc;n mặt của L&yacute; Kh&ocirc;n tho&aacute;ng qua giữa một m&agrave;u t&aacute;i nhợt v&agrave; xanh lục, sau đ&oacute; hắn ta phun ra một ngụm m&aacute;u.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Với vẻ mặt đầy ho&agrave;i nghi, hắn ta liếc nh&igrave;n b&oacute;ng mờ dần của s&aacute;u con d&atilde; tượng ph&iacute;a tr&ecirc;n L&yacute; Dung.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;L&agrave;m sao điều n&agrave;y c&oacute; thể xảy ra&hellip; Sức mạnh của s&aacute;u con d&atilde; tượng&hellip; L&yacute; Dung thực sự l&agrave; một tứ v&otilde; sĩ của giai đoạn kết đan kỳ ?!&rdquo;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&yacute; Kh&ocirc;n giật m&igrave;nh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn chưa bao giờ tưởng tượng được rằng cửu trưởng l&atilde;o, L&yacute; Dung, người hầu như kh&ocirc;ng bao giờ nhấc tay trước những người kh&aacute;c trong Gia tộc họ L&yacute;, lại c&oacute; sức mạnh đ&aacute;ng sợ như vậy!</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&aacute;c đệ tử L&yacute; gia c&oacute; mặt kh&ocirc;ng khỏi h&iacute;t v&agrave;o một luồng kh&iacute; lạnh, &quot;Sức mạnh của s&aacute;u con d&atilde; tượng ... cửu trưởng l&atilde;o thật mạnh mẽ!&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Theo t&iacute;nh to&aacute;n, dựa tr&ecirc;n sức mạnh của s&aacute;u con d&atilde; tượng, sức mạnh của cửu trưởng l&atilde;o l&agrave; khoảng cấp độ thứ tư của Giai đoạn kết đan kỳ.&rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-46918c00-7fff-0579-f43e-92e11a9a8290\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Ai c&oacute; thể ngờ được rằng cửu trưởng l&atilde;o, người thường tốt bụng v&agrave; hiền l&agrave;nh lại thực sự c&oacute; sức mạnh như vậy&hellip;&nbsp;</span></span></p>\r\n', 'Cà Di', '2020-10-02 09:16:27', 1, 0, 123);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(32, 'Chương 2: Cửu Dương Thần Công (Phần 1)', 'chuong-2-cuu-duong-than-cong-phan-1', 1, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&oacute; c&aacute;i g&igrave; đ&oacute; b&ecirc;n trong vi&ecirc;n băng ?!</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n th&iacute;ch th&uacute; nh&igrave;n kỹ hơn. Hắn vẫn kh&ocirc;ng thể nh&igrave;n r&otilde;. Đ&acirc;y l&agrave; mảnh băng duy nhất c&oacute; m&agrave;u xanh lam, thay v&igrave; trong suốt, trong to&agrave;n bộ hang động. Mặc d&ugrave; Dương Định Thi&ecirc;n đ&atilde; l&agrave;m tan chảy một phần lớn, nhưng vẫn kh&ocirc;ng đủ r&otilde; r&agrave;ng để nh&igrave;n thấu.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n quấn lấy cục băng bắt đầu run l&ecirc;n. B&acirc;y giờ hắn sẽ đ&ocirc;ng cứng nếu cơ thể Cửu Dương của hắn kh&ocirc;ng n&oacute;ng như một c&aacute;i l&ograve;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn ng&agrave;y c&agrave;ng lạnh hơn cho đến khi hắn gần như kh&ocirc;ng thể chịu đựng được.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn ta cứ &ocirc;m cục đ&aacute; như vậy trong v&agrave;i giờ, cứ nửa giờ lại nghỉ một ch&uacute;t. Băng tan từng ch&uacute;t một. Khi Dương Định Thi&ecirc;n tiến gần đến cốt l&otilde;i, n&oacute; trở n&ecirc;n lạnh lẽo một c&aacute;ch thống khổ.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n r&ugrave;ng m&igrave;nh li&ecirc;n tục sau năm giờ, trong gi&acirc;y l&aacute;t hắn gần như đen mặt. Hắn ta ngoan cường mặc d&ugrave; biết rằng bị quấn quanh băng qu&aacute; l&acirc;u sẽ c&oacute; nguy hiểm đến t&iacute;nh mạng. Dương Định Thi&ecirc;n c&oacute; một t&iacute;nh c&aacute;ch mạnh mẽ, hắn ta sẽ kh&ocirc;ng bao giờ bỏ cuộc cho đến khi đạt được mục ti&ecirc;u của m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;C&oacute; một người b&ecirc;n trong?&quot; Dương Định Thi&ecirc;n ng&acirc;y ngẩn cả người. Cuối c&ugrave;ng, vi&ecirc;n đ&aacute; gần như tan chảy ho&agrave;n to&agrave;n v&agrave; n&oacute; trong như pha l&ecirc;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thật kỳ quặc khi ph&aacute;t hiện một người bị đ&oacute;ng băng trong hang s&acirc;u h&agrave;ng ngh&igrave;n m&eacute;t.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong băng c&oacute; một &ocirc;ng gi&agrave; t&oacute;c trắng, r&acirc;u rối như cỏ dại b&ecirc;n trong. Tuổi thật của &ocirc;ng dường như kh&ocirc;ng thể đ&aacute;nh gi&aacute; được dưới mọi nếp nhăn. &Ocirc;ng ta tr&ocirc;ng giống như một l&atilde;o ăn xin bẩn thỉu, mặc d&ugrave; quần &aacute;o của &ocirc;ng ta kh&ocirc;ng bị r&aacute;ch. Đ&ocirc;i b&agrave;n tay lộ ra của &ocirc;ng gầy v&agrave; kh&ocirc; như c&agrave;nh c&acirc;y chết kh&ocirc;. &Ocirc;ng ta c&oacute; một luồng kh&iacute; u &aacute;m.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n thấy thật thất vọng v&igrave; lẽ ra người n&agrave;y đ&atilde; chết c&oacute;ng từ l&acirc;u. L&yacute; do &ocirc;ng ta bị phong ấn trong băng vẫn chưa được biết.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cuối c&ugrave;ng, Dương Định Thi&ecirc;n quyết định đưa &ocirc;ng l&atilde;o ra khỏi băng mặc d&ugrave; b&acirc;y giờ &ocirc;ng c&oacute; thể đ&atilde; chết.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n chịu đựng c&aacute;i lạnh thấu tận xương tủy, &ocirc;m lấy c&aacute;i khối băng lạnh m&agrave; hắn kinh h&atilde;i đến mức to&agrave;n th&acirc;n bắt đầu run rẩy.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Về cuối, nhiệm vụ l&agrave;m tan chảy n&agrave;y thực sự th&aacute;ch thức quyết t&acirc;m của một người đối với mức độ m&agrave; cảm gi&aacute;c lạnh như băng kh&ocirc;ng c&ograve;n c&oacute; thể diễn tả bằng lời. Cuối c&ugrave;ng, Dương Định Thi&ecirc;n kh&ocirc;ng thể chịu đựng được m&agrave; đen mặt su&yacute;t ngất.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Crakkk&hellip;&rdquo; Ngay trước khi hắn ngất đi, c&oacute; một tiếng rắc. Lớp băng cuối c&ugrave;ng bao phủ &ocirc;ng gi&agrave; đ&atilde; nứt ra ho&agrave;n to&agrave;n khiến lớp băng d&agrave;y khổng lồ đ&atilde; biến mất.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cơn lạnh xuy&ecirc;n thấu qua đi v&agrave; Dương Định Thi&ecirc;n dần lấy lại nhiệt năng. Điều duy nhất l&agrave; hắn ấy kh&ocirc;ng thể di chuyển v&agrave;o l&uacute;c n&agrave;y. Dương Định Thi&ecirc;n cứ như vậy nằm tr&ecirc;n người l&atilde;o, ngửi được m&ugrave;i kh&ocirc;ng đứng đắn của l&atilde;o. Mặc d&ugrave; n&oacute; kh&ocirc;ng qu&aacute; nặng m&ugrave;i, nhưng n&oacute; cũng đủ để khiến một người kh&oacute; chịu.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đột ngột! &Ocirc;ng gi&agrave; ở ngay trước mặt hắn ta đ&atilde; mở mắt ra! Dương Định Thi&ecirc;n kinh h&atilde;i.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&Ocirc;ng ta đang l&agrave;m giả c&aacute;i chết của m&igrave;nh? C&oacute; phải &ocirc;ng ấy đ&atilde; chết kh&ocirc;ng?</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Vừa l&uacute;c đ&oacute;, l&atilde;o gi&agrave; u &aacute;m n&agrave;y th&ograve; đ&ocirc;i tay kh&ocirc; gầy ra b&oacute;p cổ Dương Định Thi&ecirc;n. Những chiếc m&oacute;ng tay d&agrave;i của &ocirc;ng ta như những con dao sắc nhọn chọc qua c&aacute;c động mạch ch&iacute;nh của hắn. Đ&ocirc;i mắt &ocirc;ng ta tr&ocirc;ng lạnh l&ugrave;ng v&agrave; mất m&aacute;t trong khi khu&ocirc;n mặt gi&agrave; nua đầy tức giận. &Ocirc;ng qu&aacute;t Dương Định Thi&ecirc;n g&igrave; đ&oacute;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giọng &ocirc;ng đanh th&eacute;p đến mức nghe như dao c&agrave;o v&agrave;o tường. Tuy nhi&ecirc;n, Dương Định Thi&ecirc;n kh&ocirc;ng thể hiểu được một chữ &ocirc;ng vừa n&oacute;i. Đ&oacute; l&agrave; ng&ocirc;n ngữ m&agrave; Dương Định Thi&ecirc;n chưa từng nghe qua.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&Ocirc;ng l&atilde;o v&ocirc; c&ugrave;ng tức giận khi Dương Định Thi&ecirc;n kh&ocirc;ng đ&aacute;p lại m&igrave;nh. Sau đ&oacute; &ocirc;ng ta lặp lại những g&igrave; đ&atilde; n&oacute;i trước đ&oacute;, nhưng lần n&agrave;y, c&oacute; vẻ như &ocirc;ng ta đang chất vấn Dương Định Thi&ecirc;n một c&aacute;ch mạnh mẽ.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;T&ocirc;i, t&ocirc;i thậm ch&iacute; kh&ocirc;ng thể hiểu những g&igrave; &ocirc;ng đang n&oacute;i về?&quot; Dương Định Thi&ecirc;n đ&aacute;p.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi nghe Dương Định Thi&ecirc;n trả lời, &ocirc;ng ta trở n&ecirc;n phấn kh&iacute;ch v&agrave; tiếp tục hỏi v&agrave;i c&acirc;u. Sau đ&oacute;, &ocirc;ng đặt khu&ocirc;n mặt gi&agrave; nua v&agrave; xấu x&iacute; của m&igrave;nh lại gần Dương Định Thi&ecirc;n v&agrave; chất vấn kh&ocirc;ng ngừng. Ngay l&uacute;c đ&oacute;, m&oacute;ng tay của &ocirc;ng đ&atilde; cắm s&acirc;u v&agrave;o cổ Dương Định Thi&ecirc;n.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n lập tức cảm thấy đau đớn, m&aacute;u tươi từ cổ chảy xuống. Dương Định Thi&ecirc;n đ&atilde; d&ugrave;ng hết kh&iacute; lực, hiện tại thậm ch&iacute; kh&ocirc;ng thể động đậy.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay l&uacute;c đ&oacute;, &ocirc;ng gi&agrave; hỏi một c&acirc;u hỏi kh&aacute;c.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n cười đau khổ n&oacute;i: &quot;L&atilde;o đại, ta thật sự kh&ocirc;ng hiểu ngươi đang n&oacute;i c&aacute;i g&igrave;?&quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&Ocirc;ng l&atilde;o lại tức giận đến mức b&oacute;p cổ Dương Định Thi&ecirc;n một lần nữa, ngay lập tức để lại vết thương s&acirc;u lộ ra da thịt khiến hắn chảy m&aacute;u. Sau đ&oacute;, kh&ocirc;ng nghi ngờ g&igrave; nữa, hắn ta hỏi lại điều g&igrave; đ&oacute;. Dương Định Thi&ecirc;n vẫn kh&ocirc;ng hiểu.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lần n&agrave;y &ocirc;ng gi&agrave; c&agrave;ng tức giận hơn. Hắn b&oacute;p cổ Dương Định Thi&ecirc;n rồi hỏi lại với giọng điệu lạnh l&ugrave;ng hơn nhiều.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mặc d&ugrave; Dương Định Thi&ecirc;n kh&ocirc;ng thực sự hiểu được &ocirc;ng l&atilde;o đang n&oacute;i g&igrave;, nhưng đại kh&aacute;i c&oacute; thể hiểu được &ocirc;ng ta đang muốn diễn đạt điều g&igrave;. Nếu hắn ta vẫn kh&ocirc;ng chịu n&oacute;i, hắn ta c&oacute; thể sẽ bị &ocirc;ng gi&agrave; b&oacute;p cổ đến chết.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n lập tức nở một nụ cười chua x&oacute;t. &ldquo;Ta liều mạng cứu ngươi, ngươi sắp b&oacute;p cổ ta chết. Tại sao ngươi lại l&agrave;m thế? &quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&Ocirc;ng l&atilde;o cũng kh&ocirc;ng hiểu Dương Định Thi&ecirc;n đang n&oacute;i g&igrave;, nhưng r&otilde; r&agrave;ng đ&oacute; kh&ocirc;ng phải l&agrave; c&acirc;u trả lời m&agrave; &ocirc;ng ta đang t&igrave;m kiếm. &Ocirc;ng bật ra một tiếng cười t&agrave;n nhẫn rồi siết chặt cổ Dương Định Thi&ecirc;n hơn nữa.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n kh&oacute; thở. Mắt hắn ta lồi ra, v&agrave; lưỡi hắn ta th&egrave; ra khỏi miệng. Hắn ta gần như kh&ocirc;ng thể cứu được &ocirc;ng gi&agrave; v&agrave; giờ sắp bị siết cổ cho đến chết. Hắn ta thậm ch&iacute; kh&ocirc;ng c&ograve;n sức để hối hận.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-10c8cba7-7fff-5516-3817-921d1de367ff\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;T&ocirc;i c&oacute; thể chết nhưng chắc chắn kh&ocirc;ng phải trong t&igrave;nh huống kh&ocirc;ng r&otilde; nguy&ecirc;n nh&acirc;n n&agrave;y trong l&ograve;ng cảm thấy rất bực bội. Dương Định Thi&ecirc;n cắn răng, kh&ocirc;ng biết năng lượng từ đ&acirc;u ra. Hắn ta giơ ch&acirc;n l&ecirc;n v&agrave; đ&aacute; v&agrave;o kh&ocirc;ng trung một c&aacute;ch ngẫu nhi&ecirc;n.</span></span></p>\r\n<div>\r\n	&nbsp;</div>\r\n', 'Cà Di', '2020-10-02 09:36:46', 1, 0, 112),
(33, 'Chương 2: Cửu Dương Thần Công (phần 2 chương 2 )', 'chuong-2-cuu-duong-than-cong-phan-2-chuong-2', 1, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Bang&hellip; ..&rdquo; Hắn đ&aacute; v&agrave;o ngực &ocirc;ng gi&agrave;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Crakk&hellip; ..&rdquo; C&oacute; một &acirc;m thanh r&otilde; r&agrave;ng của thứ g&igrave; đ&oacute; đang vỡ giống như tiếng c&agrave;nh c&acirc;y g&atilde;y bị giẫm l&ecirc;n. Đ&oacute; l&agrave; xương sườn của &ocirc;ng gi&agrave; vừa bị g&atilde;y sau c&uacute; đ&aacute;. Dương Định Thi&ecirc;n kh&ocirc;ng khỏe đến thế, nhưng xương của &ocirc;ng gi&agrave; đ&atilde; kh&ocirc; v&agrave; gi&ograve;n n&ecirc;n dễ d&agrave;ng bị g&atilde;y v&igrave; c&uacute; đ&aacute;. Dường như &ocirc;ng l&atilde;o đ&atilde; d&ugrave;ng hết sức lực c&ograve;n lại để b&oacute;p cổ Dương Định Thi&ecirc;n. Khi ng&atilde; xuống v&igrave; c&uacute; đ&aacute;, Dương Định Thi&ecirc;n đ&atilde; được thả tự do.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n lập tức l&ugrave;i lại, giữ khoảng c&aacute;ch với l&atilde;o gi&agrave;. Cuối c&ugrave;ng, hắn dựa lưng v&agrave;o v&aacute;ch hang, thở hổn hển. Hắn nh&igrave;n những vết thương tr&ecirc;n người m&igrave;nh v&agrave; thận trọng đưa mắt nh&igrave;n &ocirc;ng gi&agrave; đ&aacute;ng sợ.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một số vết thương tr&ecirc;n người hắn đ&atilde; ngừng chảy m&aacute;u, nhưng hắn vẫn c&ograve;n cảm gi&aacute;c bỏng r&aacute;t. Tuy nhi&ecirc;n, hang động qu&aacute; nhỏ n&ecirc;n kh&ocirc;ng thể ho&agrave;n to&agrave;n tho&aacute;t khỏi tay &ocirc;ng gi&agrave; đi&ecirc;n kh&ugrave;ng n&agrave;y.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n vừa thở kh&ograve; kh&egrave; vừa sạc lại năng lượng, nhưng &aacute;nh mắt vẫn kh&ocirc;ng rời khỏi l&atilde;o gi&agrave; nguy hiểm. Dương Định Thi&ecirc;n sẽ kh&ocirc;ng kh&aacute;ch s&aacute;o nếu bị l&atilde;o gi&agrave; tấn c&ocirc;ng lần nữa.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng m&agrave;, sau khi l&atilde;o gi&agrave; bị Dương Định Thi&ecirc;n đ&aacute; văng ra hai ngụm m&aacute;u đỏ sẫm. &Ocirc;ng ta cố gắng d&ugrave;ng tay để ngồi thẳng dậy, nhưng &ocirc;ng ta kh&ocirc;ng th&agrave;nh c&ocirc;ng sau nhiều lần cố gắng, c&oacute; thể l&agrave; do ngực &ocirc;ng ta bị thương nặng từ c&uacute; đ&aacute;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&oacute; vẻ như &ocirc;ng ấy chỉ c&oacute; thể cử động tay của m&igrave;nh, đ&oacute; l&agrave; l&yacute; do tại sao &ocirc;ng ấy kh&ocirc;ng thể ngồi dậy cho d&ugrave; &ocirc;ng ấy đ&atilde; cố gắng thế n&agrave;o.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau nhiều lần cố gắng, &ocirc;ng l&atilde;o vẫn kh&ocirc;ng th&agrave;nh c&ocirc;ng. &Ocirc;ng ta trở n&ecirc;n tức giận v&agrave; bắt đầu la h&eacute;t. &Ocirc;ng ta li&ecirc;n tục d&ugrave;ng tay đập v&agrave;o tảng băng tr&ecirc;n mặt đất một c&aacute;ch tức giận, v&agrave; vết thương tr&ecirc;n ngực ng&agrave;y c&agrave;ng trầm trọng hơn. &Ocirc;ng ta phun ra một &iacute;t m&aacute;u đen từ miệng, khiến b&agrave;n tay v&agrave; tảng băng tr&ecirc;n mặt đất cũng biến th&agrave;nh m&aacute;u.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau đ&oacute;, &ocirc;ng ta bỏ cuộc, đặt lưng xuống băng v&agrave; bắt đầu la h&eacute;t. Đương nhi&ecirc;n, Dương Định Thi&ecirc;n kh&ocirc;ng thể hiểu được &ocirc;ng ta đang mắng c&aacute;i g&igrave;, nhưng &acirc;m thanh đầy căm hận v&agrave; đau buồn khiến Dương Định Thi&ecirc;n kh&ocirc;ng thể kh&ocirc;ng bị ảnh hưởng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;&Ocirc;ng gi&agrave; n&agrave;y cũng rất đ&aacute;ng thương.&quot; Dương Định Thi&ecirc;n trong l&ograve;ng tr&agrave;o d&acirc;ng một tia thương cảm.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cuối c&ugrave;ng, &ocirc;ng gi&agrave; ngừng la h&eacute;t v&agrave; chỉ nằm tr&ecirc;n băng lặng lẽ kh&ocirc;ng ph&aacute;t ra một tiếng động n&agrave;o. Những chiếc xương sườn g&atilde;y tr&ecirc;n ngực c&oacute; thể đ&atilde; đ&acirc;m v&agrave;o da thịt khiến ngực &ocirc;ng ấy sưng l&ecirc;n. L&uacute;c đầu, &ocirc;ng cụ c&ograve;n thở. Người ta c&oacute; thể thấy ngực &ocirc;ng ta di chuyển l&ecirc;n xuống. Sau đ&oacute;, c&oacute; vẻ như &ocirc;ng ta cuối c&ugrave;ng đ&atilde; tắt thở.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n tuy biết nguy hiểm như thế n&agrave;o nhưng vẫn kh&ocirc;ng k&igrave;m l&ograve;ng được m&agrave; chậm r&atilde;i đi tới chỗ l&atilde;o gi&agrave;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n từ xa nhẹ nh&agrave;ng d&ugrave;ng ch&acirc;n th&uacute;c v&agrave;o &ocirc;ng l&atilde;o, nhưng kh&ocirc;ng c&oacute; bất kỳ phản ứng n&agrave;o. Sau đ&oacute; anh ta tiến th&ecirc;m một bước nữa v&agrave; chạm v&agrave;o động mạch cổ của &ocirc;ng gi&agrave;. Vẫn c&ograve;n một nhịp tim, nhưng n&oacute; đ&atilde; rất yếu.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n tiến l&ecirc;n n&acirc;ng l&atilde;o gi&agrave; l&ecirc;n. Sau đ&oacute;, hắn cởi c&uacute;c &aacute;o sơ mi để kiểm tra vết thương tr&ecirc;n ngực.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Xương sườn b&ecirc;n phải của l&atilde;o đ&atilde; bị g&atilde;y sau c&uacute; đ&aacute; của hắn. C&oacute; thể thấy r&otilde; hai chiếc xương sườn bị g&atilde;y, người gầy như que củi chỉ c&ograve;n một lớp da bọc xương. Một trong những chiếc xương sườn đ&atilde; đ&acirc;m v&agrave;o thịt &ocirc;ng ta, v&agrave; ngực phải của &ocirc;ng ta đ&atilde; sưng l&ecirc;n v&agrave;i phần. N&oacute; xuất hiện cả m&agrave;u xanh l&aacute; c&acirc;y v&agrave; m&agrave;u t&iacute;m, tr&ocirc;ng thật kinh khủng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n đ&atilde; d&ugrave;ng kiến ​​thức sơ cứu mơ hồ của m&igrave;nh để gi&uacute;p &ocirc;ng l&atilde;o v&agrave; gần như kh&ocirc;ng thể cố định xương g&atilde;y. Trong to&agrave;n bộ qu&aacute; tr&igrave;nh, &ocirc;ng l&atilde;o đ&atilde; run rẩy v&agrave;i lần do qu&aacute; đau đớn, nhưng &ocirc;ng kh&ocirc;ng tỉnh dậy.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Rất may hai chiếc xương sườn bị g&atilde;y kh&ocirc;ng vỡ th&agrave;nh nhiều mảnh. Tuy nhi&ecirc;n, Dương Định Thi&ecirc;n đ&atilde; c&oacute; một khoảng thời gian kh&oacute; khăn để điều chỉnh xương g&atilde;y của l&atilde;o. Ở nơi lạnh như băng tuyết n&agrave;y, Dương Định Thi&ecirc;n to&aacute;t mồ h&ocirc;i hột.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đột nhi&ecirc;n, &aacute;nh mắt Dương Định Thi&ecirc;n c&oacute; phần đau x&oacute;t. Tất cả những g&igrave; hắn c&oacute; thể thấy l&agrave; hai chiếc m&oacute;ng tay d&agrave;i v&agrave; sắc nhọn ngay trước mắt. Khoảnh khắc hắn nh&uacute;c nh&iacute;ch một ch&uacute;t, hai chiếc đinh sắc nhọn n&agrave;y sẽ đ&acirc;m thẳng v&agrave;o nh&atilde;n cầu của hắn, khiến hắn bị m&ugrave; ho&agrave;n to&agrave;n.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn kh&ocirc;ng biết &ocirc;ng gi&agrave; đ&atilde; thức dậy từ bao giờ. Ngay khi bắt gặp &aacute;nh mắt của Dương Định Thi&ecirc;n, &ocirc;ngta bật ra một tiếng cười t&agrave;n nhẫn. Hiện tại, m&aacute;u thịt của Dương Định Thi&ecirc;n vẫn c&ograve;n tr&ecirc;n những chiếc đinh đ&oacute;. C&ugrave;ng với khu&ocirc;n mặt độc &aacute;c v&agrave; t&agrave;n bạo của &ocirc;ng gi&agrave;, n&oacute; c&agrave;ng trở n&ecirc;n r&ugrave;ng rợn hơn.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n căng thẳng đến mức n&iacute;n thở, c&uacute;i gằm mặt, tiếp tục chỉnh lại xương cốt của &ocirc;ng l&atilde;o như thể kh&ocirc;ng biết &ocirc;ng l&atilde;o sắp l&agrave;m m&igrave;nh bị m&ugrave;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, cả hai tay của hắn l&uacute;c n&agrave;y đều run rẩy. Dương Định Thi&ecirc;n h&iacute;t một hơi thật s&acirc;u v&agrave; tiếp tục điều chỉnh xương g&atilde;y của &ocirc;ng l&atilde;o khi tay &ocirc;ng kh&ocirc;ng c&ograve;n run nữa.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn kh&ocirc;ng nhận được g&igrave; từ h&agrave;nh động đ&oacute;. Thay v&agrave;o đ&oacute;, &ocirc;ng l&atilde;o bu&ocirc;ng lời chế nhạo t&agrave;n nhẫn như thể &ocirc;ng ta đang cười nhạo h&agrave;nh động gian xảo của Dương Định Thi&ecirc;n.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cho d&ugrave; vậy, Dương Định Thi&ecirc;n cũng mặc kệ, tiếp tục đối xử cẩn thận với l&atilde;o gi&agrave;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">M&oacute;ng tay sắc b&eacute;n của &ocirc;ng l&atilde;o từ từ đ&acirc;m v&agrave;o da thịt của Dương Định Thi&ecirc;n giống như một con dao sắc b&eacute;n xuy&ecirc;n qua từng thớ thịt.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Những chiếc đinh đ&atilde; đ&acirc;m xuy&ecirc;n gần một cm v&agrave; đ&atilde; xuy&ecirc;n qua lớp da b&ecirc;n ngo&agrave;i. Chẳng bao l&acirc;u nữa, họ sẽ chạm tới nh&atilde;n cầu của Dương Định Thi&ecirc;n.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n nắm đấm bằng tay phải v&agrave; chuẩn bị đấm v&agrave;o xương cốt của &ocirc;ng l&atilde;o để đ&acirc;m xuy&ecirc;n tim v&agrave; giết chết &ocirc;ng ta một lần v&agrave; m&atilde;i m&atilde;i.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, &ocirc;ng l&atilde;o đột nhi&ecirc;n dừng lại v&agrave; r&uacute;t m&oacute;ng tay ra khỏi da thịt Dương Định Thi&ecirc;n. Hắn nhắm mắt để lại mọi chuyện cho Dương Định Thi&ecirc;n.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n h&iacute;t s&acirc;u một hơi d&agrave;i, trấn định t&acirc;m t&igrave;nh, tiếp tục trị liệu cho l&atilde;o gi&agrave;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cuối c&ugrave;ng, sau khi xử l&yacute; xong xương sườn bị g&atilde;y, Dương Định Thi&ecirc;n d&ugrave;ng quần &aacute;o cẩn thận quấn vết thương lại. Mặc d&ugrave; vết thương kh&ocirc;ng nhẹ nh&agrave;ng như vậy, nhưng kh&ocirc;ng c&oacute; sự lựa chọn n&agrave;o kh&aacute;c v&igrave; trong hang kh&ocirc;ng c&oacute; một mảnh gỗ n&agrave;o.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi mọi việc xong xu&ocirc;i, Dương Định Thi&ecirc;n lại giữ khoảng c&aacute;ch với người như cũ. Hắn ngồi xuống ph&iacute;a xa dựa v&agrave;o v&aacute;ch hang.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-e6fa7f67-7fff-585d-13e1-99d7878da272\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ograve;n &ocirc;ng gi&agrave; nguy hiểm th&igrave; nhắm mắt kh&ocirc;ng nh&uacute;c nh&iacute;ch.</span></span></p>\r\n', 'Cà Di', '2020-10-02 09:37:24', 1, 0, 312);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(34, 'Chapter 1: Chapter 1 Vân Triệt, Xuân Triệt', 'chapter-1-chapter-1-van-triet-xuan-triet', 4, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&Yacute; thức của V&acirc;n Triệt dần dần thức tỉnh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chuyện g&igrave; đang xảy ra&hellip; L&agrave;m thế n&agrave;o m&agrave; t&ocirc;i vẫn chưa chết? T&ocirc;i nhớ rất r&otilde; rằng t&ocirc;i đ&atilde; nhảy khỏi V&aacute;ch đ&aacute; cuối của M&acirc;y, l&agrave;m sao t&ocirc;i c&oacute; thể c&ograve;n sống được! Bằng c&aacute;ch n&agrave;o đ&oacute; cơ thể t&ocirc;i kh&ocirc;ng bị đau&hellip; V&agrave; t&ocirc;i thậm ch&iacute; kh&ocirc;ng cảm thấy kh&oacute; chịu ch&uacute;t n&agrave;o? L&agrave;m thế n&agrave;o điều n&agrave;y xảy ra?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&acirc;n Triệt đột nhi&ecirc;n mở mắt v&agrave; nhanh ch&oacute;ng ngồi dậy. Anh ấy thấy m&igrave;nh tr&ecirc;n một chiếc giường &ecirc;m &aacute;i thoải m&aacute;i, với một biểu ngữ m&agrave;u đỏ treo tr&ecirc;n người, tạo n&ecirc;n một bầu kh&ocirc;ng kh&iacute; lễ hội.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ah! V&acirc;n nhỏ! Bạn&hellip; Bạn tỉnh dậy! &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tiếng thở hổn hển ngạc nhi&ecirc;n của một c&ocirc; g&aacute;i vang l&ecirc;n từ tai anh v&agrave; c&ugrave;ng một c&ocirc; g&aacute;i xuất hiện trong tầm mắt anh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nh&igrave;n tho&aacute;ng qua, c&ocirc; g&aacute;i mặc đồ xanh lục tr&ocirc;ng c&oacute; vẻ từ mười lăm đến mười s&aacute;u tuổi. N&agrave;ng đẹp như tuyết với đ&ocirc;i m&ocirc;i hồng h&agrave;o quyến rũ v&agrave; chiếc mũi thanh t&uacute;. Đ&ocirc;i mắt trong suốt v&agrave; trong suốt như một vũng nước suối mờ ảo. Khu&ocirc;n mặt c&ocirc; to&aacute;t l&ecirc;n vẻ dịu d&agrave;ng v&agrave; mềm mại đến ch&oacute;i mắt. Ở độ tuổi trẻ n&agrave;y, c&ocirc; ấy đ&atilde; thể hiện sự quyến rũ như vậy, vậy ai c&oacute; thể tưởng tượng c&ocirc; ấy sẽ xinh đẹp như thế n&agrave;o trong tương lai?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nh&igrave;n chằm chằm v&agrave;o c&ocirc; g&aacute;i gần đ&oacute;, V&acirc;n Triệt nh&igrave;n kỹ hơn v&agrave; bất gi&aacute;c để hai từ tho&aacute;t ra khỏi m&ocirc;i m&igrave;nh, &quot;D&igrave; nhỏ?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc; g&aacute;i hội chợ n&acirc;ng đ&ocirc;i tay như ngọc của m&igrave;nh l&ecirc;n tr&aacute;n V&acirc;n Triệt v&agrave; thư gi&atilde;n ngay sau đ&oacute;. Với một biểu hiện vui mừng, c&ocirc; ấy n&oacute;i, &ldquo;Thật tuyệt vời, nhiệt độ của bạn gần như trở lại b&igrave;nh thường. Anh gần như l&agrave;m t&ocirc;i sợ chết khiếp. Che b&eacute;, con c&oacute; sao kh&ocirc;ng? H&atilde;y n&oacute;i cho t&ocirc;i biết nếu bạn cảm thấy kh&ocirc;ng thoải m&aacute;i ở bất cứ đ&acirc;u &rdquo;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trước &aacute;nh mắt đầy quan t&acirc;m s&acirc;u sắc của c&ocirc; g&aacute;i, V&acirc;n Triệt lắc đầu. Anh mở to mắt nh&igrave;n v&agrave;o khoảng kh&ocirc;ng với &aacute;nh mắt trống rỗng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Ở đ&acirc;y nghỉ ngơi một l&aacute;t, t&ocirc;i đi n&oacute;i với &ocirc;ng nội l&agrave; &ocirc;ng tỉnh rồi. H&ocirc;m nay l&agrave; ng&agrave;y của bạn! Khi bạn ngất đi, &ocirc;ng của bạn gần như ph&aacute;t đi&ecirc;n v&agrave; đ&iacute;ch th&acirc;n đi t&igrave;m b&aacute;c sĩ Situ cho bạn &rdquo;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc; g&aacute;i vội v&agrave;ng đến mức kh&ocirc;ng nhận thấy sự bất thường trong t&igrave;nh trạng hiện tại của V&acirc;n Triệt. C&ocirc; đẩy vai V&acirc;n Triệt, ra hiệu cho anh nằm lại tr&ecirc;n giường v&agrave; vội v&atilde; rời đi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi c&aacute;nh cửa đ&oacute;ng lại, V&acirc;n Triệt ngồi dậy tr&ecirc;n giường, hai tay &ocirc;m lấy đầu.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&acirc;y l&agrave; một trong những th&agrave;nh phố ở viễn đ&ocirc;ng nằm trong một trong Bảy Đế chế của Thi&ecirc;n độc ch&acirc;u &mdash; Th&agrave;nh phố M&acirc;y lơ lửng. V&agrave; anh ấy l&agrave; ch&aacute;u trai duy nhất của trưởng l&atilde;o thứ năm của Xiao Clan &mdash; Xu&acirc;n Triệt! Năm nay anh vừa tr&ograve;n mười s&aacute;u tuổi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&oacute; l&agrave; danh t&iacute;nh hiện tại của anh ấy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đột nhi&ecirc;n, k&yacute; ức của anh ta tr&ugrave;ng lặp với k&yacute; ức hai mươi năm hoặc l&acirc;u hơn của Lục địa m&acirc;y Azure v&agrave; anh ta trở n&ecirc;n bối rối.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nếu t&ocirc;i l&agrave; Xu&acirc;n Triệt&hellip; Vậy th&igrave; tại sao t&ocirc;i lại c&oacute; k&yacute; ức về Lục địa M&acirc;y Azure?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&oacute; phải v&igrave; t&ocirc;i đ&atilde; du h&agrave;nh đến cơ thể n&agrave;y sau khi t&ocirc;i chết ở Lục địa M&acirc;y Azure?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kh&ocirc;ng! T&ocirc;i r&otilde; r&agrave;ng l&agrave; Xu&acirc;n Triệt! To&agrave;n bộ căn ph&ograve;ng n&agrave;y quen thuộc với t&ocirc;i v&agrave; t&ocirc;i nhớ r&otilde; r&agrave;ng tất cả c&aacute;c sự kiện thời thơ ấu của m&igrave;nh. Mọi thứ từ tr&iacute; nhớ của t&ocirc;i đều l&agrave; từ kinh nghiệm c&aacute; nh&acirc;n v&agrave; kh&ocirc;ng đời n&agrave;o t&ocirc;i ăn cắp tr&iacute; nhớ của người kh&aacute;c!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Vậy th&igrave; mọi thứ từ Lục địa m&acirc;y Azure c&oacute; phải chỉ l&agrave; một giấc mơ? C&oacute; phải chỉ sau khi nhảy khỏi V&aacute;ch đ&aacute; cuối M&acirc;y, t&ocirc;i mới thực sự tỉnh dậy?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng những k&yacute; ức sống ở Lục địa M&acirc;y Azure vẫn r&otilde; r&agrave;ng như ban ng&agrave;y. Hai mươi bốn năm y&ecirc;u v&agrave; hận ấy sao c&oacute; thể chỉ l&agrave; một giấc mơ!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&aacute;i qu&aacute;i g&igrave; đang diễn ra vậy?</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&acirc;n Triệt&hellip; Xu&acirc;n Triệt hiện tại đ&atilde; bất động một l&uacute;c l&acirc;u khi anh dần b&igrave;nh tĩnh lại v&agrave; ổn định suy nghĩ của m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trời c&ograve;n s&aacute;ng sớm v&agrave; trời chưa hửng s&aacute;ng. H&ocirc;m nay l&agrave; ng&agrave;y cưới trọng đại của anh ấy v&agrave; Xia Clan. Hai giờ trước, anh được D&igrave; Nhỏ đ&aacute;nh thức dậy v&agrave; mặc chiếc &aacute;o cưới m&agrave;u đỏ. Anh ăn ch&aacute;o do ch&iacute;nh tay D&igrave; Nhỏ nấu v&agrave; mất hết sức lực trong người. Anh kh&ocirc;ng nhớ bất cứ điều g&igrave; xảy ra sau đ&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh ấy đ&atilde; kh&ocirc;ng tỉnh dậy cho đến b&acirc;y giờ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một m&ugrave;i lạ bốc ra từ m&ocirc;i anh v&agrave; m&ocirc;i Xu&acirc;n Triệt hơi m&iacute;m lại. Vẻ mặt anh tối sầm lại.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&acirc;y l&agrave;&hellip; Bột tr&aacute;i tim giết người !!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong những năm sống tr&ecirc;n Lục địa m&acirc;y Azure, với Thi&ecirc;n độc ch&acirc;u trong người, V&acirc;n Triệt đ&atilde; biết về tất cả c&aacute;c chất độc c&oacute; tr&ecirc;n to&agrave;n thế giới v&agrave; c&oacute; thể n&oacute;i rằng kh&ocirc;ng c&oacute; chất độc n&agrave;o m&agrave; anh kh&ocirc;ng quen thuộc. Anh ta c&oacute; thể x&aacute;c định t&ecirc;n v&agrave; t&aacute;c dụng của một loại chất độc chỉ bằng một c&uacute; hu&yacute;ch. Đồng thời, bởi v&igrave; Thi&ecirc;n độc ch&acirc;u, hắn kh&ocirc;ng thấm v&agrave;o đ&acirc;u. Anh kh&ocirc;ng thể bị thương bởi bất kỳ chất độc n&agrave;o, cho d&ugrave; n&oacute; c&oacute; độc hại đến đ&acirc;u.</span></span></p>\r\n<p>\r\n	<br />\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bột tr&aacute;i tim giết người được tạo ra bằng c&aacute;ch kết hợp một loại cỏ linh hồn độc đ&aacute;o v&agrave; v&acirc;n t&iacute;m từ c&acirc;y anh đ&agrave;o-t&aacute;o. Nếu h&ograve;a tan trong nước, chất độc kh&ocirc;ng m&agrave;u, kh&ocirc;ng vị. Một người sẽ mất mạng mười gi&acirc;y sau khi chất độc x&acirc;m nhập v&agrave;o cơ thể v&agrave; kh&ocirc;ng ai biết được nguy&ecirc;n nh&acirc;n c&aacute;i chết v&igrave; đ&oacute; cũng l&agrave; chất độc v&ocirc; gi&aacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&ocirc;i mắt của Xu&acirc;n Triệt mờ đi v&agrave; ngay lập tức hiểu chuyện g&igrave; đ&atilde; xảy ra.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tr&ecirc;n thực tế, anh kh&ocirc;ng thực sự ngất xỉu. Thay v&agrave;o đ&oacute;, anh ta uống ch&aacute;o c&oacute; chứa Bột Tr&aacute;i tim S&aacute;t nh&acirc;n v&agrave; bị đầu độc chết! Anh ta được sinh ra ở Lục địa M&acirc;y Azure nhưng sau khi anh ta nhảy ra khỏi V&aacute;ch đ&aacute; Kết th&uacute;c của Đ&aacute;m m&acirc;y&hellip;. hắn thực sự đ&atilde; đầu thai ở thế giới n&agrave;y b&ecirc;n trong một c&aacute;i x&aacute;c chết!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nếu ai đ&oacute; nghe thấy điều n&agrave;y, nghe c&oacute; vẻ như đ&oacute; l&agrave; một c&acirc;u chuyện viển v&ocirc;ng, nhưng đ&oacute; l&agrave; l&yacute; do duy nhất m&agrave; Xu&acirc;n Triệt c&oacute; thể tưởng tượng ra!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đợi t&iacute;&hellip;. nếu n&oacute; thực sự l&agrave; như vậy, th&igrave; cơ thể n&agrave;y sẽ kh&ocirc;ng c&oacute; khả năng chống độc. L&agrave;m thế n&agrave;o m&agrave; anh ta lại được an to&agrave;n trước Bột tr&aacute;i tim s&aacute;t nh&acirc;n tr&ecirc;n m&ocirc;i khi chủ nh&acirc;n của cơ thể trước đ&oacute; đ&atilde; chết c&aacute;ch đ&acirc;y kh&ocirc;ng l&acirc;u?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một cảm gi&aacute;c hơi lạ đến từ l&ograve;ng b&agrave;n tay tr&aacute;i của anh. Xu&acirc;n Triệt nhấc tay tr&aacute;i l&ecirc;n v&agrave; ngạc nhi&ecirc;n khi thấy c&oacute; một dấu ấn m&agrave;u xanh l&aacute; c&acirc;y h&igrave;nh tr&ograve;n tr&ecirc;n l&ograve;ng b&agrave;n tay m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">H&igrave;nh dạng n&agrave;y, m&agrave;u sắc n&agrave;y, k&iacute;ch thước n&agrave;y&hellip;. r&otilde; r&agrave;ng l&agrave; giống với Thi&ecirc;n độc ch&acirc;u!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trước khi nhảy khỏi V&aacute;ch đ&aacute; cuối c&ugrave;ng của M&acirc;y, anh ta đ&atilde; liều lĩnh nuốt Thi&ecirc;n độc ch&acirc;u nhưng anh ta thực sự kh&ocirc;ng biết điều g&igrave; sẽ xảy ra với m&igrave;nh. Dấu ấn tr&ecirc;n tay anh kh&ocirc;ng ngờ lại l&agrave; Thi&ecirc;n độc ch&acirc;u v&agrave; n&oacute; thực sự đ&atilde; c&ugrave;ng anh vượt qua thế giới n&agrave;y!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Như thể trong cơn m&ecirc;, Xu&acirc;n Triệt nh&igrave;n chằm chằm v&agrave;o Thi&ecirc;n độc ch&acirc;u v&agrave; thầm th&igrave; thầm. &ldquo;Thi&ecirc;n độc ch&acirc;u&hellip;.&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi giọng n&oacute;i của anh ta vừa tắt, dấu t&iacute;ch xanh lục tr&ecirc;n l&ograve;ng b&agrave;n tay anh ta đột nhi&ecirc;n ph&aacute;t ra một tia s&aacute;ng xanh lục. Ngay lập tức, một cơn ch&oacute;ng mặt nhẹ ập đến khiến anh bất gi&aacute;c nhắm mắt lại. Khi anh mở ch&uacute;ng ra, to&agrave;n bộ thế giới của anh ngập trong m&agrave;u xanh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thế giới xanh n&agrave;y rộng mở. Kh&ocirc;ng c&oacute; ranh giới n&agrave;o được nh&igrave;n thấy v&agrave; c&oacute; một luồng kh&iacute; mờ nhạt của Thi&ecirc;n độc ch&acirc;u khắp kh&ocirc;ng gian n&agrave;y. Sau một hồi sững sờ, Tiếu Nhiễm hiểu ra, m&igrave;nh đ&atilde; tiến v&agrave;o thế giới b&ecirc;n trong Bầu trời Độc Ngọc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&oacute; ai ngờ rằng b&ecirc;n trong Thi&ecirc;n độc ch&acirc;u lại c&oacute; một thế giới rộng lớn v&agrave; rộng mở đến vậy! Điều kh&ocirc;ng thể tưởng tượng hơn nữa l&agrave; sau khi anh ta ăn Thi&ecirc;n độc ch&acirc;u m&agrave; kh&ocirc;ng nghĩ đến hậu quả, Thi&ecirc;n độc ch&acirc;u đ&atilde; thực sự đi c&ugrave;ng anh ta, v&agrave; thậm ch&iacute; dường như đ&atilde; trở th&agrave;nh một phần cơ thể của anh ta.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nếu c&oacute; một lối v&agrave;o, th&igrave; phải c&oacute; một lối tho&aacute;t.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Xu&acirc;n Triệt nhắm mắt lại v&agrave; tập trung suy nghĩ của m&igrave;nh. Đột nhi&ecirc;n, thế giới m&agrave;u xanh l&aacute; c&acirc;y vội v&agrave;ng tan biến, khi cậu mở mắt ra lần nữa, trong tầm mắt cậu l&agrave; căn ph&ograve;ng m&agrave; cậu quen thuộc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nh&igrave;n chằm chằm v&agrave;o vết xanh mờ trong l&ograve;ng b&agrave;n tay, Xu&acirc;n Triệt chậm r&atilde;i mỉm cười&hellip; Mặc d&ugrave; kh&ocirc;ng biết tại sao lại xảy ra sự kiện nực cười n&agrave;y, nhưng anh kh&ocirc;ng chỉ đầu thai m&agrave; c&ograve;n giữ lại k&yacute; ức từ cả hai kiếp. C&oacute; lẽ, &ocirc;ng trời cũng kh&ocirc;ng nỡ nh&igrave;n hai kiếp người đau khổ, thương hại, cho hắn cơ hội sống lại!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&acirc;n Triệt l&agrave; nạn nh&acirc;n của sự ngược đ&atilde;i từ v&ocirc; số c&aacute; nh&acirc;n mạnh nhất của Lục địa m&acirc;y Azure. D&ugrave; cuối c&ugrave;ng chết đi, nhưng tự m&igrave;nh l&agrave;m rung chuyển thế giới; điều đ&oacute; thật kinh ngạc v&agrave; đ&aacute;ng kinh ngạc l&agrave;m sao! Tuy nhi&ecirc;n, cơ thể của anh ấy l&uacute;c n&agrave;y, chỉ ở mức trung b&igrave;nh&hellip; kh&ocirc;ng, n&oacute;i m&agrave; kh&ocirc;ng biết kiềm chế, n&oacute; c&oacute; thể được coi l&agrave; mẫu mực của sự yếu đuối.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong Thi&ecirc;n độc ch&acirc;u, sức mạnh s&acirc;u sắc l&agrave; quan trọng nhất. Mặc d&ugrave; Xu&acirc;n Triệt được sinh ra trong Xiao Clan, v&agrave; thậm ch&iacute; l&agrave; ch&aacute;u của Trưởng l&atilde;o thứ năm mạnh nhất Xiao Lie, nhưng thực lực s&acirc;u xa của anh vẫn chỉ ở cấp một của Tiểu giới s&acirc;u sắc mặc d&ugrave; anh đ&atilde; mười s&aacute;u tuổi. Anh ấy bắt đầu tập luyện khi mới 7 tuổi rưỡi, v&agrave;o cấp độ đầu ti&ecirc;n của chương tr&igrave;nh Sơ cấp khi 8 tuổi, v&agrave; sau đ&oacute; kh&ocirc;ng cải thiện trong suốt 8 năm. Anh ấy đ&atilde; bị chế giễu bởi tất cả Xiao Clan. Sau đ&oacute;, Xiao Lie đ&atilde; mời b&aacute;c sĩ h&agrave;ng đầu của Floating Cloud City, b&aacute;c sĩ Situ đến kh&aacute;m cơ thể của m&igrave;nh v&agrave; g&acirc;y sốc khi ph&aacute;t hiện ra rằng Xu&acirc;n Triệt được sinh ra với c&aacute;c tĩnh mạch s&acirc;u bị tổn thương. Thiệt hại nghi&ecirc;m trọng đến mức hầu như kh&ocirc;ng thể phục hồi. Trong trạng th&aacute;i n&agrave;y, Xu&acirc;n Triệt sẽ tr&igrave; trệ ở cấp độ đầu ti&ecirc;n của Vương quốc S&acirc;u sắc Cơ bản, v&agrave; kh&ocirc;ng bao giờ c&oacute; thể tiến bộ hơn nữa cho d&ugrave; anh ta đ&atilde; l&agrave;m việc chăm chỉ đến mức n&agrave;o.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay cả khi anh ta luyện tập với t&iacute;nh mạng của m&igrave;nh, anh ta sẽ ở lại cấp độ đầu ti&ecirc;n của Vương quốc s&acirc;u thẳm cơ bản trong suốt cuộc đời của m&igrave;nh. Loại người n&agrave;y kh&ocirc;ng thể nghi ngờ l&agrave; tồn tại thấp nhất trong Thi&ecirc;n độc ch&acirc;u, v&agrave; ho&agrave;n to&agrave;n l&agrave; một tr&ograve; cười trong Tiểu tộc. Nếu kh&ocirc;ng phải &ocirc;ng nội Xiao Lie l&agrave; người mạnh nhất trong Xiao Clan, người thậm ch&iacute; c&oacute; thể n&oacute;i l&agrave; mạnh nhất trong Floating Cloud City, th&igrave; kh&ocirc;ng ai c&oacute; thể để mắt tới anh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Xiao Clan l&agrave; một trong ba gia tộc lớn được đ&agrave;o tạo theo c&aacute;ch của những người s&acirc;u sắc trong Floating Cloud City v&agrave; c&oacute; v&ocirc; số thực thể mạnh mẽ. Trong số đ&ocirc;ng thanh ni&ecirc;n đến từ c&ugrave;ng một thế hệ, Xu&acirc;n Triệt c&oacute; thể n&oacute;i l&agrave; một sự tồn tại kh&ocirc;ng cần thiết. Ngay cả khi anh ta chết một ng&agrave;y n&agrave;o đ&oacute;, kh&ocirc;ng nhiều ngoại trừ một số sẽ quan t&acirc;m. Nhưng c&oacute; một số người ng&agrave;y nay đ&atilde; thực sự cố gắng giết anh ta bằng c&aacute;ch sử dụng Bột giết người cực kỳ kh&oacute; kiếm v&agrave; đắt tiền. Xu&acirc;n Triệt đ&atilde; biết r&otilde; l&yacute; do tại sao b&acirc;y giờ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bởi v&igrave; h&ocirc;m nay l&agrave; ng&agrave;y cưới giữa anh v&agrave; Xia Qingyue.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Xia Qingyue, bằng tuổi anh, cũng mười s&aacute;u. Tuy nhi&ecirc;n, ở độ tuổi c&ograve;n trẻ như vậy, sức mạnh s&acirc;u xa của c&ocirc; ấy được cho l&agrave; đ&atilde; đạt đến cấp độ thứ mười của C&otilde;i s&acirc;u sơ cấp, gần như vượt ra khỏi cấp độ sơ cấp v&agrave; bước v&agrave;o C&otilde;i s&acirc;u thẳm của Nascent. C&ocirc; l&agrave; người duy nhất trong một trăm năm đạt đến mức độ mạnh mẽ n&agrave;y ở tuổi mười s&aacute;u trong Hạ tộc. Thậm ch&iacute; c&ograve;n c&oacute; tin đồn rằng nếu c&ocirc; ấy tiếp tục ph&aacute;t triển theo c&aacute;ch n&agrave;y, c&ocirc; ấy sẽ l&agrave; người đầu ti&ecirc;n trong lịch sử của Tiểu tộc từng bước v&agrave;o C&otilde;i S&acirc;u Địa Cầu v&agrave;i thập kỷ sau&hellip; hoặc thậm ch&iacute; c&oacute; thể đến C&otilde;i S&acirc;u Bầu Trời m&agrave; kh&ocirc;ng ai ở Th&agrave;nh phố M&acirc;y nổi thậm ch&iacute; nghĩ đến trong v&agrave;i thế kỷ qua!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Quan trọng hơn, c&ocirc; ấy kh&ocirc;ng chỉ sắc sảo đến kinh ngạc m&agrave; c&ograve;n v&ocirc; c&ugrave;ng xinh đẹp, v&agrave; được coi l&agrave; c&ocirc; g&aacute;i xinh đẹp nhất của Floating Cloud City. Tất cả c&aacute;c thanh ni&ecirc;n của Floating Cloud City với một số cấp độ năng lực đều đổ nước d&atilde;i trước c&ocirc; ấy. Nếu Xia Clan bắt đầu t&igrave;m kiếm ch&uacute; rể, h&agrave;ng đợi c&oacute; lẽ sẽ đủ d&agrave;i để k&eacute;o d&agrave;i giữa c&aacute;c cổng ph&iacute;a nam v&agrave; ph&iacute;a bắc của Floating Cloud City.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đỉnh cao của một c&ocirc; g&aacute;i vừa th&ocirc;ng minh vừa xinh đẹp ở Th&agrave;nh Phố M&acirc;y Nổi, đ&atilde; thực sự kết h&ocirc;n với tuổi trẻ v&ocirc; gi&aacute; trị nhất thế hệ, kh&ocirc;ng c&oacute; tương lai n&agrave;o cả. Ai m&agrave; biết được bao nhi&ecirc;u người đang giậm ch&acirc;n v&igrave; tức giận v&agrave; ghen tị&hellip; Đ&acirc;y ho&agrave;n to&agrave;n l&agrave; trường hợp của một b&ocirc;ng hoa sen rực rỡ được trồng tr&ecirc;n một đống ph&acirc;n m&agrave; chẳng ai th&egrave;m ng&oacute; ng&agrave;ng tới.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Những người c&oacute; t&igrave;nh y&ecirc;u đơn phương d&agrave;nh cho Xia Qingyue tất nhi&ecirc;n c&oacute; cả sự ghen tị v&agrave; căm th&ugrave; đối với Xu&acirc;n Triệt. Thậm ch&iacute; nhiều hơn kh&ocirc;ng muốn từ bỏ&hellip; v&agrave; sẽ cố gắng &aacute;m s&aacute;t anh ta bằng chất độc. Như hiện tại Xu&acirc;n Triệt nghĩ về n&oacute;, t&igrave;nh h&igrave;nh n&agrave;y kh&ocirc;ng c&oacute; vẻ g&igrave; l&agrave; kỳ lạ cả.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-11a5768d-7fff-4d25-9fa6-a48d4ca3e79d\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Phụ nữ thực sự l&agrave; gốc rễ của những rắc rối.&quot; Xu&acirc;n Triệt bước xuống giường, đứng dậy v&agrave; giải sầu. Nhưng khi nghĩ đến vẻ đẹp v&agrave; sự quyến rũ của Xia Qingyue c&oacute; thể l&agrave;m chao đảo cả một th&agrave;nh phố, anh ta mở miệng v&agrave; cười đắc &yacute;, &quot;D&ugrave; vậy, t&ocirc;i vẫn kết h&ocirc;n với một c&ocirc; d&acirc;u như vậy, đ&acirc;y thực sự l&agrave; một khởi đầu tốt.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n', 'Cà Di', '2020-09-25 11:10:54', 1, 0, 342);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(35, 'Chương 1.2 : Lão quỷ (1)', 'chuong-12-lao-quy-1-1', 17, '', 1, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Baa aaa&hellip; aaa&hellip; aaa &hellip;&rdquo;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tiếng k&ecirc;u của một ch&uacute; cừu non của người chăn cừu vang vọng khắp d&atilde;y n&uacute;i thường y&ecirc;n tĩnh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&yacute; Thất Dạ b&ograve; l&ecirc;n đỉnh một v&aacute;ch đ&aacute; l&agrave;n gi&oacute; đ&ecirc;m lạnh lẽo thổi mạnh v&agrave;o người hắn. Cơ thể hắn hiện đang ướt đẫm mồ h&ocirc;i. V&agrave;o năm mười ba tuổi, một cậu b&eacute; như L&yacute; Thất Dạ đang d&ugrave;ng hết sức lực của m&igrave;nh để leo l&ecirc;n d&atilde;y n&uacute;i n&agrave;y - cảnh tượng tr&ecirc;n bầu trời đ&ecirc;m nay mang lại cảm gi&aacute;c r&ugrave;ng rợn cho bất cứ ai nh&igrave;n thấy.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mặc d&ugrave; m&agrave;n đ&ecirc;m y&ecirc;n tĩnh, t&acirc;m tr&iacute; của L&yacute; Thất Dạ đang bị d&agrave;y v&ograve; bởi ngọn lửa bất định.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Xuất th&acirc;n trong một gia đ&igrave;nh ngh&egrave;o, cha mẹ hắn đều l&agrave; n&ocirc;ng d&acirc;n. Năm 7 tuổi, hắn bắt đầu cuộc sống chăn cừu. Họ của hắn l&agrave; L&yacute;; T&ecirc;n anh ta l&agrave; L&yacute; Thất Dạ v&igrave; hắn ta đ&atilde; kh&oacute;c trong bảy ng&agrave;y bảy đ&ecirc;m sau khi sinh. [1]</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">H&ocirc;m nay, hắn đ&atilde; ho&agrave;n th&agrave;nh c&ocirc;ng việc h&agrave;ng ng&agrave;y của m&igrave;nh. Tuy nhi&ecirc;n, trong l&uacute;c b&igrave;nh minh đến gần, hắn nhận thấy rằng m&igrave;nh đang bỏ lỡ con cừu đầu đ&agrave;n của m&igrave;nh. Trong l&ograve;ng đầy lo lắng, hắn vội v&agrave;ng quay trở lại d&atilde;y n&uacute;i để t&igrave;m kiếm n&oacute;. Sau khi l&ugrave;ng sục khắp d&atilde;y n&uacute;i, hắn vẫn kh&ocirc;ng thể t&igrave;m thấy d&ugrave; chỉ l&agrave; b&oacute;ng con cừu n&agrave;y.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nghĩ về con cừu mất t&iacute;ch v&agrave; người chủ độc &aacute;c của n&oacute;, Trương Đại Hổ, L&yacute; Thất Dạ lo sợ điều tồi tệ nhất trong l&ograve;ng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đột nhi&ecirc;n, L&yacute; Thất Dạ nghĩ đến một nơi c&oacute; thể c&oacute; bầy cừu. Chỉ c&oacute; một địa điểm duy nhất m&agrave; hắn ta kh&ocirc;ng t&igrave;m kiếm - Động quỷ bất tử!</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi nh&igrave;n về ph&iacute;a Động quỷ bất tử trước mặt m&igrave;nh, hắn nhận thấy d&atilde;y n&uacute;i trong đ&ecirc;m đen giống như một con th&uacute; hung dữ từ Kỷ nguy&ecirc;n hoang vắng cổ đại; miệng n&oacute; dường như đang h&aacute; hốc, th&egrave;m kh&aacute;t thịt người. Nghe tiếng s&oacute;i tru vang l&ecirc;n xung quanh, hắn kh&ocirc;ng khỏi r&ugrave;ng m&igrave;nh sợ h&atilde;i.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hang Quỷ Bất Tử được coi l&agrave; v&ugrave;ng đất bị nguyền rủa trong khu vực xung quanh. Truyền thuyết kể rằng một con quỷ độc &aacute;c đ&atilde; cai trị nơi n&agrave;y, một con quỷ ngay lập tức ti&ecirc;u diệt bất kỳ kẻ x&acirc;m phạm n&agrave;o. Kh&ocirc;ng ai đ&atilde; từng sống s&oacute;t ra khỏi hang.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave;o l&uacute;c n&agrave;y, tiếng roi của Trương Đại Hổ vang vọng trong tai L&yacute; Thất Dạ. Nếu L&yacute; Thất Dạ thực sự l&agrave;m mất con cừu, th&igrave; Trương Đại Hổ nhất định sẽ d&ugrave;ng roi da x&eacute; thịt.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&atilde; đến điểm n&agrave;y, L&yacute; Thất Dạ nghiến răng v&agrave; tiến đến hang động.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">X&aacute;c hắn biến mất trong đ&ecirc;m.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Aaaaaaaahhhhh&hellip;!&rdquo;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">M&agrave;n đ&ecirc;m y&ecirc;n tĩnh bị cắt ngang bởi tiếng h&eacute;t thảm thiết của một cậu b&eacute;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giọng n&oacute;i sợ h&atilde;i của L&yacute; Thất Dạ lại vang l&ecirc;n: &ldquo;Ngươi, ngươi, ngươi muốn g&igrave;? &hellip; &Agrave;&hellip;! &rdquo;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đột nhi&ecirc;n, tiếng h&eacute;t kết th&uacute;c.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kh&ocirc;ng biết bao l&acirc;u sau, ở s&acirc;u trong Động Quỷ Bất Tử, sự im lặng bị ph&aacute; vỡ bởi một giọng n&oacute;i đầy đe dọa: &ldquo;Tốt, tốt, tốt! Hắc &ocirc; nha bất tử của ta cuối c&ugrave;ng đ&atilde; được ho&agrave;n th&agrave;nh. V&igrave; chỉ thiếu một linh hồn n&ecirc;n h&ocirc;m nay, ta sẽ thuận tiện mượn linh hồn một ch&uacute;t! &rdquo;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Phwoosh ... phwoosh ... phwoosh!&quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một l&uacute;c sau, khi từng tiếng vỗ vang dội, một con quạ đen tối kỳ lạ bay khỏi Động Quỷ.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Bay, bay, bay! Ta sẽ sử dụng linh hồn của ngươi để t&igrave;m tất cả c&aacute;c Tử Cấm Th&agrave;nh. Bay qua tất cả c&aacute;c v&ugrave;ng đất! Chỉ cần Cửu giới tồn tại, ta sẽ lại t&igrave;m ngươi! &rdquo; Từ s&acirc;u trong Động Quỷ Bất Tử, giọng n&oacute;i nặng nề lại một lần nữa ph&aacute;t ra v&agrave; lơ lửng trong kh&ocirc;ng kh&iacute;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Từ đ&oacute;, tr&ecirc;n khắp trời đất n&agrave;y, một con Quạ đen bay ngang qua khắp nơi. Từ c&aacute;c th&agrave;nh phố tr&ecirc;n trời đến những v&ugrave;ng đất nguy hiểm nhất v&agrave; những khu vực b&iacute; mật, n&oacute; bay thẳng qua Cửu giới.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thời gian tr&ocirc;i qua, h&agrave;ng triệu năm đến v&agrave; đi. Từ từ, con quạ biến mất. N&oacute; muốn trốn khỏi chủ nh&acirc;n của n&oacute;, n&oacute; muốn t&igrave;m một mục đ&iacute;ch sống của m&igrave;nh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Từ nhẫn giả cho đến </span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Vĩnh sinh đế Phi</span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">, Vĩnh sinh đế </span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">T&acirc;y Học</span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\"> đến Vĩnh sinh đế Mẫn Nh&acirc;n, Vĩnh sinh đế </span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tru&acirc;n L&iacute;</span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\"> đến Vĩnh sinh đế </span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Binh Vũ</span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&hellip; cho đến cả Vua Hắc Long. [2]</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đằng sau mỗi ch&uacute; chim n&agrave;y l&agrave; b&oacute;ng d&aacute;ng của một con quạ, một con vật đang đấu tranh để t&igrave;m tự do.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi những sinh vật mạnh nhất n&agrave;y đến v&agrave; đi, con quạ tiếp tục xuất hiện một c&aacute;ch b&iacute; ẩn trong suốt d&ograve;ng s&ocirc;ng thời gian.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Con quạ kh&ocirc;ng muốn bị kiểm so&aacute;t số phận của m&igrave;nh. N&oacute; muốn chống lại nh&acirc;n vật đ&aacute;ng sợ nhất tr&ecirc;n thế giới n&agrave;y.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave; b&acirc;y giờ, h&agrave;ng triệu năm đ&atilde; tr&ocirc;i qua với nhiều kỷ nguy&ecirc;n&hellip;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&yacute; Thất Dạ đang tr&ocirc;i tr&ecirc;n s&ocirc;ng th&igrave; bất ngờ bị một người k&eacute;o ra ngo&agrave;i.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Aaa!&quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi đang bị l&ocirc;i ra ngo&agrave;i, L&yacute; Thất Dạ đột nhi&ecirc;n tỉnh dậy. Phản ứng đầu ti&ecirc;n của hắn l&agrave; nhảy l&ecirc;n v&igrave; hắn kh&ocirc;ng quen với cơ thể của m&igrave;nh. Kh&ocirc;ng thể t&igrave;m được chỗ đứng của m&igrave;nh, hắn su&yacute;t nữa th&igrave; ng&atilde; xuống đất.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ah, cơ thể của t&ocirc;i!&quot; Nh&igrave;n xuống v&agrave; thấy cơ thể m&igrave;nh vẫn như cũ, L&yacute; Thất Dạ vừa ng&acirc;y ngất vừa sợ h&atilde;i. D&ugrave; trải qua h&agrave;ng ngh&igrave;n lần vật lộn chống chọi với s&oacute;ng gi&oacute; kh&ocirc;ng dứt, hắc &ocirc; nha L&yacute; Thất Dạ vẫn kh&ocirc;ng k&igrave;m n&eacute;n được cảm x&uacute;c sau khi lấy lại được cơ thể của ch&iacute;nh m&igrave;nh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">H&iacute;t một hơi thật s&acirc;u, hắn ngẩng đầu l&ecirc;n v&agrave; ph&aacute;t hiện trước mặt m&igrave;nh l&agrave; một &ocirc;ng gi&agrave;.</span></span></p>\r\n<br />\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Hehehe, ch&iacute;nh &ocirc;ng gi&agrave; n&agrave;y đ&atilde; cứu ngươi khỏi sự diệt vong sắp xảy ra.&quot; &Ocirc;ng l&atilde;o lớn tiếng cười một c&aacute;ch th&acirc;m th&uacute;y, để lộ ba chiếc răng v&agrave;ng c&ograve;n s&oacute;t lại. Phong th&aacute;i của &ocirc;ng khiến người kh&aacute;c cảm thấy nụ cười của &ocirc;ng rất nham hiểm.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ở tr&ecirc;n d&ograve;ng suối, L&yacute; Thất Dạ c&oacute; thể nh&igrave;n thấy cấu tr&uacute;c lờ mờ của Động quỷ bất tử. Đ&ocirc;i mắt hắn ng&agrave;y c&agrave;ng trở n&ecirc;n lạnh l&ugrave;ng v&agrave; h&agrave;o quang của hắn vượt qu&aacute; bất cứ thứ g&igrave; m&agrave; một đứa trẻ mười ba tuổi c&oacute; thể to&aacute;t ra.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&yacute; Thất Dạ h&iacute;t một hơi thật s&acirc;u v&agrave; sau đ&oacute; nh&igrave;n chằm chằm v&agrave;o &ocirc;ng l&atilde;o. Sau một l&uacute;c, cuối c&ugrave;ng hắn hỏi: &quot;T&ocirc;i n&ecirc;n xưng h&ocirc; với &ocirc;ng như thế n&agrave;o, l&atilde;o b&aacute;?&quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Khiết tịnh hương cổ ph&aacute;i, L&atilde;o quỷ.&rdquo; &Ocirc;ng l&atilde;o miệng hớn hở trả lời, vẫn để lộ ba chiếc răng v&agrave;ng, vừa cười vừa khạc nhổ khắp nơi.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Khiết tịnh hương cổ ph&aacute;i&hellip;&rdquo; L&yacute; Thất Dạ th&igrave; thầm trong hơi thở. C&aacute;i t&ecirc;n n&agrave;y khiến hắn nhớ lại những k&iacute; ức bị phong ấn trong t&acirc;m tr&iacute;. Những k&yacute; ức n&agrave;y c&oacute; từ thời hắn c&ograve;n bị giam cầm trong cơ thể của hắc &ocirc; nha.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&yacute; Thất Dạ lấy lại b&igrave;nh tĩnh v&agrave; hỏi &ocirc;ng l&atilde;o: &quot;Hiện giờ, ai l&agrave; người c&oacute; Thi&ecirc;n &yacute;?&quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&Ocirc;ng l&atilde;o vẫn mỉm cười khi trả lời: &ldquo;Thi&ecirc;n &Yacute; hả? Hiện giờ, kh&ocirc;ng ai trong thời đại n&agrave;y c&oacute; thể g&aacute;nh v&aacute;c Thi&ecirc;n &Yacute; cả &rdquo;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ho&agrave;ng đế </span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Th&aacute;i Cảng</span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\"> bất tử ở đ&acirc;u?&quot; Sau khi nghe c&acirc;u trả lời của &ocirc;ng l&atilde;o, phong th&aacute;i của L&yacute; Thất Dạ tối sầm lại. Hắn đ&atilde; ngủ bao l&acirc;u rồi? Hơn một trăm ngh&igrave;n năm? [1]</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Vĩnh sinh đế </span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Th&aacute;i Cảng</span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\"> đ&atilde; mất t&iacute;ch ba mươi ngh&igrave;n năm.&quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&yacute; Thất Dạ hỏi &ocirc;ng l&atilde;o một lần nữa: &quot;C&ograve;n về Hắc Long Vương của Thi&ecirc;n vệ cung th&igrave; sao?&quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Kh&ocirc;ng ai biết. Hắc Long Vương mất t&iacute;ch c&ugrave;ng l&uacute;c với Ho&agrave;ng Đế Th&aacute;i Cảng Bất Tử &rdquo;. L&atilde;o Quỷ lắc đầu.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nghe vậy, biểu hiện của L&yacute; Thất Dạ thay đổi đ&aacute;ng kể. Hắn nh&igrave;n lại Động quỷ bất tử một lần nữa v&agrave; cuối c&ugrave;ng hiểu tại sao hắn đ&atilde; lấy lại được cơ thể của m&igrave;nh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Đi theo t&ocirc;i.&quot; Với vẻ mặt buồn b&atilde;, L&yacute; Thất Dạ quay lại v&agrave; bắt đầu bước đi. Hắn kh&ocirc;ng quan t&acirc;m liệu L&atilde;o Quỷ c&oacute; theo d&otilde;i hắn hay kh&ocirc;ng. Sau khi trải qua trường hợp bất tử giả, hắn biết ch&iacute;nh x&aacute;c m&igrave;nh phải l&agrave;m g&igrave;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thi&ecirc;n vệ cung l&agrave; một d&ograve;ng d&otilde;i cực kỳ mạnh mẽ trong thời hiện tại. Trở lại thời đại m&agrave; Hắc Long Vương - một cao thủ v&ocirc; song - vẫn c&ograve;n sống, kh&ocirc;ng ai c&oacute; thể s&aacute;nh được với hắn tr&ecirc;n khắp Cửu giới. &Ocirc;ng đ&atilde; được k&iacute;nh trọng trong cả ba thế hệ!</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cho d&ugrave; hắn đ&atilde; mất t&iacute;ch ba vạn năm, Thi&ecirc;n vệ cung vẫn ngạo nghễ ở l&atilde;nh địa n&agrave;y.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave;o l&uacute;c n&agrave;y, một cậu b&eacute; khoảng mười ba tuổi v&agrave; một &ocirc;ng gi&agrave; thấp b&eacute; với ba chiếc răng v&agrave;ng đang đứng b&ecirc;n ngo&agrave;i Thi&ecirc;n vệ cung.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay b&ecirc;n ngo&agrave;i chu vi của th&agrave;nh ngo&agrave;i cung điện, L&yacute; Thất Dạ đốt tiền nghi lễ trong khi th&igrave; thầm: &ldquo;Tiểu Hắc Long, ngươi kh&ocirc;ng cần phải lo lắng. Ngươi đ&atilde; gi&uacute;p ta lấy lại cơ thể của ta, cuộc sống của ta. Một ng&agrave;y n&agrave;o đ&oacute;, ta sẽ ph&aacute; hủy v&ugrave;ng đất dữ để t&igrave;m c&aacute;ch trả th&ugrave; cho ngươi &rdquo;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi buổi lễ kết th&uacute;c, L&yacute; Thất Dạ nh&igrave;n chằm chằm v&agrave;o Thi&ecirc;n vệ cung trước mặt m&igrave;nh. Cảnh vẫn vậy, nhưng người kh&ocirc;ng c&ograve;n nữa; mọi thứ đ&atilde; trở n&ecirc;n xa lạ. Hắn hồi tưởng về những ng&agrave;y xưa c&ugrave;ng với Tiểu Hắc Long, k&yacute; ức về việc họ x&acirc;y dựng th&agrave;nh phố n&agrave;y từ đầu bằng nỗ lực của m&igrave;nh vẫn c&ograve;n nguy&ecirc;n trong t&acirc;m tr&iacute; hắn.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thật kh&ocirc;ng may, sau ba mươi ngh&igrave;n năm, kh&ocirc;ng c&ograve;n nhiều người nhớ đến hắc &ocirc; nha ẩn sau bức m&agrave;n.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;H&igrave;, ch&uacute;ng ta h&atilde;y quay trở lại Khiết tịnh hương cổ ph&aacute;i.&rdquo; L&uacute;c n&agrave;y, &ocirc;ng l&atilde;o liếc nh&igrave;n L&yacute; Thất Dạ v&agrave; n&oacute;i r&otilde; mục đ&iacute;ch của m&igrave;nh trong khi để lộ ba chiếc răng v&agrave;ng s&aacute;ng b&oacute;ng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-29eb96e0-7fff-b963-038e-d278beed5358\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Đi theo t&ocirc;i.&quot; L&yacute; Thất Dạ b&igrave;nh tĩnh gật đầu. Bất kể &ocirc;ng gi&agrave; n&agrave;y lừng lẫy hay b&iacute; ẩn đến đ&acirc;u, nguồn gốc của &ocirc;ng ta kh&ocirc;ng thể l&agrave;m L&yacute; Thất Dạ ngạc nhi&ecirc;n. L&yacute; Thất Dạ đ&atilde; trải qua v&ocirc; số kh&oacute; khăn v&agrave; linh hồn của hắn ta đ&atilde; bị mắc kẹt b&ecirc;n trong hắc &ocirc; nha h&agrave;ng triệu năm. Hết thời đại n&agrave;y đến thời đại kh&aacute;c, hắn s&aacute;nh vai c&ugrave;ng nhau b&ecirc;n cạnh c&aacute;c Ho&agrave;ng đế Bất tử v&agrave; trở th&agrave;nh bạn với Thần giả kim, vậy điều g&igrave; thực sự c&oacute; thể l&agrave;m hắn ngạc nhi&ecirc;n?</span></span></p>\r\n', 'Cà Di', '2020-10-14 12:18:00', 1, 0, 142);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(36, 'Chương 1.2 : Lão quỷ (1) phần 2', 'chuong-12-lao-quy-1-phan-2-1', 17, '', 1, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi họ rời đi, một c&ocirc; g&aacute;i v&ocirc; c&ugrave;ng thanh lịch v&agrave; xinh đẹp bước ra khỏi cung điện. C&ocirc; ấy giống một thi&ecirc;n thần đến từ thi&ecirc;n đường, một nữ thần của thế giới kh&aacute;c. Ngay l&uacute;c bước ra ngo&agrave;i, c&ocirc; v&ocirc; t&igrave;nh nhận ra t&agrave;n t&iacute;ch của ngọn lửa từ buổi lễ cũng như một số biểu tượng b&iacute; ẩn b&ecirc;n cạnh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi nh&igrave;n thấy những biểu tượng n&agrave;y, vẻ mặt của c&ocirc; ấy thay đổi rất nhiều: &quot;Ai đang l&agrave;m lễ ở đ&acirc;y?&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một người hầu gi&agrave; gần đ&oacute; lập tức đi xung quanh để t&igrave;m kiếm th&ocirc;ng tin v&agrave; nhận được kết quả: &ldquo;Bảo vệ th&agrave;nh phố n&oacute;i rằng vừa rồi c&oacute; một &ocirc;ng gi&agrave; v&agrave; một cậu b&eacute; khoảng mười ba tuổi ở đ&acirc;y. Họ đ&atilde; đốt tiền nghi lễ. &quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc; g&aacute;i ra lệnh: &quot;H&atilde;y đuổi theo v&agrave; t&igrave;m ch&uacute;ng ngay lập tức.&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Điện hạ phải đến Thi&ecirc;n Sơn ngay b&acirc;y giờ.&quot; Người hầu gi&agrave; ngập ngừng th&uacute;t th&iacute;t.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nữ thần nhẹ nh&agrave;ng h&eacute;t l&ecirc;n: &quot;T&igrave;m ch&uacute;ng!&quot; Sau đ&oacute; c&ocirc; biến mất trong kh&ocirc;ng gian để t&igrave;m hai người.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cuối c&ugrave;ng, c&ocirc; đ&atilde; kh&ocirc;ng thể t&igrave;m thấy ch&uacute;ng. C&ocirc; ch&aacute;n nản trở về cung điện sau cuộc t&igrave;m kiếm của m&igrave;nh. Những k&yacute; hiệu b&ecirc;n cạnh ngọn lửa vẫn c&ograve;n trong đầu c&ocirc;. Những biểu tượng n&agrave;y đ&atilde; kh&ocirc;ng xuất hiện trong một thời gian d&agrave;i, vậy tại sao sau h&agrave;ng vạn năm, ch&uacute;ng lại xuất hiện ở ngoại &ocirc;? Họ l&agrave; bạn hay th&ugrave;?</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một người hầu trung th&agrave;nh đ&atilde; b&aacute;o c&aacute;o: &quot;Thưa Điện hạ, ch&uacute;ng t&ocirc;i kh&ocirc;ng thể t&igrave;m thấy những người đốt tiền nghi lễ.&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nữ thần nghi&ecirc;m nghị ra lệnh: &ldquo;Ra lệnh cho mọi người nhớ rằng nếu c&oacute; tin tức g&igrave; về hai người đ&oacute;, h&atilde;y lập tức b&aacute;o lại cho ta&rdquo;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người hầu v&ocirc; c&ugrave;ng ngạc nhi&ecirc;n khi nghe điều n&agrave;y. Với sức mạnh hiện tại của Thi&ecirc;n vệ cung v&agrave; danh tiếng của nữ thần của họ, sẽ hiếm khi c&ocirc; ấy thể hiện một biểu hiện nghi&ecirc;m t&uacute;c như vậy.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người hầu hỏi: &ldquo;Vậy c&ograve;n chuyến đi đến Thi&ecirc;n Sơn&hellip; th&igrave; sao?&rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Hủy n&oacute; đi!&quot; Nữ thần thốt l&ecirc;n: &ldquo;Ta phải đọc những cuốn s&aacute;ch cổ m&agrave; tổ ti&ecirc;n để lại. C&oacute; điều g&igrave; đ&oacute; kỳ lạ đang xảy ra &rdquo;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau đ&oacute;, c&ocirc; ngay lập tức đi đến tầng s&acirc;u nhất của khu cấm địa trong Thi&ecirc;n vệ cung.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khiết tịnh hương cổ ph&aacute;i cư tr&uacute; tại Thi&ecirc;n Ngọc quốc. Gi&aacute;o ph&aacute;i n&agrave;y thuộc d&ograve;ng d&otilde;i Ho&agrave;ng đế Bất tử với lịch sử l&acirc;u đời. V&agrave;o đầu Kỷ nguy&ecirc;n Ho&agrave;ng đế, Ho&agrave;ng đế Bất tử </span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mẫn Nh&acirc;n</span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\"> ki&ecirc;u h&atilde;nh đứng tr&ecirc;n đỉnh cao v&agrave; th&agrave;nh lập một gi&aacute;o ph&aacute;i, đặt t&ecirc;n l&agrave; Khiết tịnh hương.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thật kh&ocirc;ng may, sau h&agrave;ng triệu năm, n&oacute; kh&ocirc;ng thể chịu được thử th&aacute;ch của thời gian. M&ocirc;n ph&aacute;i đ&atilde; kh&ocirc;ng c&ograve;n thuộc h&agrave;ng Ho&agrave;ng đế Bất tử v&agrave; kh&ocirc;ng c&ograve;n c&oacute; thể trị v&igrave; đất đai như xưa. D&ugrave; c&oacute; cố gắng đến đ&acirc;u, n&oacute; cũng kh&ocirc;ng thể lấy lại vinh quang xưa của họ cũng như ngăn chặn sự sụp đổ kh&ocirc;ng ngừng.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Đại trưởng l&atilde;o, t&ocirc;i c&oacute; một tin xấu. Một người ph&agrave;m n&oacute;i rằng cậu ấy muốn ch&uacute;ng ta nhận hắn l&agrave;m đệ tử ch&iacute;nh . &rdquo; Một đệ tử vội v&agrave;ng b&aacute;o c&aacute;o đại trưởng l&atilde;o của Khiết tịnh hương cổ ph&aacute;i khi vị trưởng l&atilde;o n&agrave;y đang bước ra ngo&agrave;i.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Đ&aacute; hắn ta khỏi n&uacute;i!&quot; Kh&ocirc;ng liếc nh&igrave;n đệ tử một c&aacute;i, vị đại trưởng l&atilde;o tiếp tục: &quot;Tại sao ngươi lại b&aacute;o c&aacute;o một chuyện nực cười như vậy?&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một người ph&agrave;m trần muốn trở th&agrave;nh đệ tử ch&iacute;nh của m&ocirc;n ph&aacute;i họ? Thật l&agrave; một c&acirc;u chuyện h&agrave;i hước. Một đệ tử ch&iacute;nh cũng giống như bảo bối của m&ocirc;n ph&aacute;i, người c&oacute; cơ hội trở th&agrave;nh sư t&ocirc;n tương lai cao nhất. Đương nhi&ecirc;n, khi sư t&ocirc;n m&ocirc;n ph&aacute;i kh&ocirc;ng c&oacute; mặt, đại trưởng l&atilde;o vẫn c&oacute; thể đ&iacute;ch th&acirc;n lo liệu những chuyện n&agrave;y.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người đệ tử lắp bắp: &quot;Nhưng, nhưng cậu ấy đ&atilde; được l&atilde;o quỷ tiến cử.&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhướng m&agrave;y, l&atilde;o đầu kh&ocirc;ng vui lặp lại: &ldquo;L&atilde;o Quỷ?&Ocirc;ng ta c&oacute; bị mua chuộc bởi rượu kh&ocirc;ng? Đ&oacute; c&oacute; phải l&agrave; l&yacute; do tại sao &ocirc;ng ta lại giới thiệu một người ph&agrave;m trần? &quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&atilde;o Quỷ thuộc m&ocirc;n ph&aacute;i, nhưng m&ocirc;n ph&aacute;i kh&ocirc;ng muốn nhận ra l&atilde;o gi&agrave; n&agrave;y.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy c&aacute;i t&ecirc;n nghe rất anh h&ugrave;ng nhưng l&atilde;o gi&agrave; n&agrave;y đ&atilde; khiến m&ocirc;n ph&aacute;i mất hết thể diện.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&atilde;o quỷ c&oacute; ba phẩm chất &quot;tốt&rdquo;. &Ocirc;ng ta rất giỏi trong việc ti&ecirc;u tiền, n&oacute;i dối v&agrave; lừa phỉnh trong c&aacute;c nh&agrave; thổ. Đ&acirc;y l&agrave; l&yacute; do tại sao họ gọi &ocirc;ng ta l&agrave; l&atilde;o quỷ. [2]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cuối c&ugrave;ng th&igrave; l&atilde;o cũng kh&ocirc;ng tu luyện bất kỳ phương ph&aacute;p n&agrave;o, nhưng l&atilde;o c&oacute; một lai lịch rất kinh ngạc trong m&ocirc;n ph&aacute;i. C&oacute; tin đồn rằng l&atilde;o l&agrave; đứa con hoang của sư t&ocirc;n đời trước. Đ&acirc;y l&agrave; l&yacute; do tại sao, khi sư t&ocirc;n cuối c&ugrave;ng của m&ocirc;n ph&aacute;i qua đời, &ocirc;ng đ&atilde; y&ecirc;u cầu sư t&ocirc;n m&ocirc;n ph&aacute;i hiện tại chăm s&oacute;c cho l&atilde;o .</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, c&oacute; một tin đồn kh&aacute;c n&oacute;i rằng l&atilde;o l&agrave; một t&ecirc;n khốn từ sư t&ocirc;n m&ocirc;n ph&aacute;i hai thế hệ trước. Bởi v&igrave; sư t&ocirc;n m&ocirc;n ph&aacute;i trước đ&atilde; nợ người n&agrave;y một &acirc;n huệ lớn, hắn kh&ocirc;ng c&ograve;n c&aacute;ch n&agrave;o kh&aacute;c đ&agrave;nh phải nhận lấy c&aacute;i thứ r&aacute;c rưởi n&agrave;y v&agrave; kh&ocirc;ng cam t&acirc;m chăm s&oacute;c L&atilde;o Quỷ. Trước khi sư t&ocirc;n m&ocirc;n ph&aacute;i trước qua đời, hắn cũng đ&atilde; nhờ sư t&ocirc;n m&ocirc;n ph&aacute;i hiện tại chăm s&oacute;c cho L&atilde;o Quỷ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kh&ocirc;ng cần biết cha l&atilde;o l&agrave; ai, to&agrave;n bộ m&ocirc;n ph&aacute;i v&agrave; c&aacute;c cấp tr&ecirc;n của n&oacute; đều kh&ocirc;ng c&oacute; t&igrave;nh y&ecirc;u với &ocirc;ng l&atilde;o. Họ gh&eacute;t những lời lẽ kh&ocirc;ng hay li&ecirc;n quan đến nh&acirc;n vật L&atilde;o Quỷ v&agrave; kh&ocirc;ng quan t&acirc;m đến những lời đồn đại tr&ecirc;n thế giới.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Từ trưởng l&atilde;o đến đệ tử cấp thấp nhất, to&agrave;n bộ m&ocirc;n ph&aacute;i đều kh&ocirc;ng hoan ngh&ecirc;nh l&atilde;o gi&agrave; kh&ocirc;ng ch&uacute;t tu luyện n&agrave;y.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đại trưởng l&atilde;o bực bội h&eacute;t l&ecirc;n: &ldquo;Vậy nếu đ&oacute; l&agrave; lời đề nghị của L&atilde;o Quỷ th&igrave; sao? Đ&aacute; kẻ đ&oacute; xuống n&uacute;i! &rdquo; Buổi s&aacute;ng v&agrave; t&acirc;m trạng tốt của &ocirc;ng đ&atilde; bị hủy hoại bởi sự kiện n&agrave;y.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Nhưng, nhưng cậu ấy n&oacute;i rằng cậu ấy c&oacute; Tẩy Nhan Cổ Tự từ L&atilde;o Quỷ.&rdquo; Người đệ tử lại n&oacute;i lắp v&igrave; sợ h&atilde;i.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Khiết tịnh hương cổ ph&aacute;i ?!&quot; Sau khi nghe những lời n&agrave;y, vẻ mặt của đại trưởng l&atilde;o tối sầm lại. Sau khi y&ecirc;n lặng suy x&eacute;t t&igrave;nh h&igrave;nh, &ocirc;ng ta nhanh ch&oacute;ng ra lệnh: &ldquo;Tập hợp tất cả c&aacute;c trưởng l&atilde;o v&agrave; bảo người ph&agrave;m đợi b&ecirc;n ngo&agrave;i đại điện.&rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khiết tịnh hương cổ ph&aacute;i c&oacute; tổng cộng s&aacute;u trưởng l&atilde;o. Sau khi nghe thấy bốn chữ &ldquo;Tẩy Nhan Cổ Tự&rdquo;, năm người c&ograve;n lại nhanh ch&oacute;ng đến họp.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Gi&aacute;o chủ của gi&aacute;o ph&aacute;i l&agrave; Ho&agrave;ng đế Bất tử Mẫn Nh&acirc;n, người đ&atilde; để lại ba trong số những mệnh lệnh n&agrave;y. Hai chiếc đ&atilde; được gi&aacute;o ph&aacute;i đ&ograve;i lại, nhưng chiếc thứ ba lại rơi v&agrave;o tay L&atilde;o Quỷ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngo&agrave;i y&ecirc;u cầu từ sư phụ m&ocirc;n ph&aacute;i trước về việc chăm s&oacute;c L&atilde;o Quỷ, l&yacute; do thứ hai khiến c&aacute;c trưởng l&atilde;o bất lực trước &ocirc;ng ta l&agrave; v&igrave; &ocirc;ng ta sở hữu mệnh lệnh cuối c&ugrave;ng.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lệnh đại diện cho Ho&agrave;ng đế Bất tử Mẫn Nh&acirc;n; người nắm giữ c&oacute; thể y&ecirc;u cầu bất cứ thứ g&igrave; từ Khiết tịnh hương cổ ph&aacute;i.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngồi trong căn ph&ograve;ng lớn của Khiết tịnh hương cổ ph&aacute;i v&agrave; nh&igrave;n chằm chằm v&agrave;o bức tượng kh&oacute; nắm bắt được bao phủ bởi l&agrave;n kh&oacute;i v&agrave;ng, L&yacute; Thất Dạ kh&ocirc;ng thể kh&ocirc;ng nhớ lại nhiều c&acirc;u chuyện trong qu&aacute; khứ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bức tượng Ho&agrave;ng đế Bất tử Mẫn Nh&acirc;n sừng sững ở điểm cao nhất của địa điểm n&agrave;y. D&ugrave; đ&atilde; nhiều năm tr&ocirc;i qua, bức tượng vẫn mang một luồng kh&iacute; cổ xưa dường như c&oacute; thể xuy&ecirc;n thủng ch&iacute;n tầng trời. Kh&aacute;n giả kh&ocirc;ng thể kh&ocirc;ng chi&ecirc;m ngưỡng bức tượng n&agrave;y; như thể ho&agrave;ng đế thực sự đang ở trước mặt họ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&yacute; Thất Dạ kh&ocirc;ng biết phải diễn tả cảm x&uacute;c của m&igrave;nh như thế n&agrave;o khi nh&igrave;n chằm chằm v&agrave;o bức tượng n&agrave;y. Ho&agrave;ng đế đ&atilde; ra đi, nhưng L&yacute; Thất Dạ vẫn c&ograve;n sống v&agrave; sẽ m&atilde;i m&atilde;i như vậy. Mặc d&ugrave; hắn đ&atilde; đạt được mục đ&iacute;ch v&agrave; lấy lại được cơ thể của m&igrave;nh, nhưng tất cả những người quen cũ của hắn đ&atilde; dần biến mất trong d&ograve;ng s&ocirc;ng thời gian.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">1. Qi nghĩa l&agrave; bảy, Ye nghĩa l&agrave; đ&ecirc;m</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">2. Fei = bay, Xue Xi = Huyết ấn, Mẫn Nh&acirc;n = Nh&acirc;n từ tươi s&aacute;ng, Tun Ri = Swallow Sun, v&agrave; Bing Yu = Icy Feather.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">1. Ta Kong = Space Trample</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0a700579-7fff-1023-0dc0-03b38824d984\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">2. Ban đầu, ch&uacute;ng t&ocirc;i sử dụng t&ecirc;n Tam L&atilde;o Qu&aacute;i thay v&igrave; L&atilde;o Quỷ, nhưng hiện tại t&ecirc;n n&agrave;y đ&atilde; được thay đổi để ph&ugrave; hợp với c&aacute;c chương gần đ&acirc;y. Tam = Ba, Qu&aacute;i = Quỷ qu&aacute;i, L&atilde;o = &Ocirc;ng gi&agrave;.</span></span></p>\r\n', 'Cà Di', '2020-10-14 12:18:23', 1, 0, 266);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(37, 'Chương Mở đầu', 'chuong-mo-djau', 34, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Splat !!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">M&aacute;u của c&ocirc; đ&atilde; đổ khắp nơi.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&Aacute;nh mắt đờ đẫn của c&ocirc; từ từ lướt xuống ngọn gi&aacute;o cắm v&agrave;o ngực tr&aacute;i.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Da thịt&nbsp; c&ocirc; rung l&ecirc;n, c&ocirc; cảm thấy c&aacute;i lạnh như cắt xuy&ecirc;n thấu v&agrave;o tim m&igrave;nh. To&agrave;n bộ cơ thể c&ocirc; mất sức v&agrave; ng&atilde; xuống đất. C&ocirc; c&oacute; thể nghe thấy tiếng k&ecirc;u tuyệt vọng, đau khổ của ai đ&oacute; khi chủ nh&acirc;n của giọng n&oacute;i lao về ph&iacute;a người cầm gi&aacute;o. Tốc độ của anh nhanh như tia s&aacute;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người cầm gi&aacute;o kinh ngạc, v&agrave; bu&ocirc;ng gi&aacute;o ra. Tuy nhi&ecirc;n, đ&oacute; kh&ocirc;ng phải l&agrave; tất cả. Anh ta xoay người, v&agrave; tung một c&uacute; đấm như trời gi&aacute;n v&agrave;o kẻ th&ugrave; đang tiến đến từ ph&iacute;a sau.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nạn nh&acirc;n lật nh&agrave;o sau c&uacute; va chạm. Người cầm gi&aacute;o kh&ocirc;ng dừng lại v&agrave; nắm đấm đẫm m&aacute;u của anh lại tiếp tục đấm v&agrave;o nạn nh&acirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Pow !!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nạn nh&acirc;n đ&atilde; gục xuống.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, ngay cả khi đ&oacute;, người cầm gi&aacute;o vẫn kh&ocirc;ng dừng lại.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một lần, hai lần, ba lần, một lần nữa, v&agrave; một lần nữa&hellip;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh ta gầm l&ecirc;n một c&aacute;ch man rợ, v&agrave; đập v&agrave;o đầu kẻ th&ugrave; đ&atilde; chết của m&igrave;nh, cho đến khi kh&ocirc;ng c&ograve;n nhận dạng được nạn nh&acirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh ta cuối c&ugrave;ng cũng dừng nắm đấm. Với một đ&ocirc;i mắt đỏ ngầu, anh quan s&aacute;t nhanh xung quanh m&igrave;nh. Sau đ&oacute;, anh ta cầm gi&aacute;o l&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh giẫm l&ecirc;n mặt đất ướt đẫm m&aacute;u.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người đ&agrave;n &ocirc;ng giống như một con quỷ lao v&agrave;o l&agrave;n sương m&ugrave; m&agrave; kh&ocirc;ng do dự &hellip;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">***</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người phụ nữ bị ng&atilde;&nbsp; gắng gượng ra một tiếng ho khan khi tỉnh dậy. N&eacute;t mặt c&ocirc; ấy nhăn lại v&igrave; cơn đau rỉ ra từ từng tấc thịt tr&ecirc;n cơ thể.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng, ngay cả c&aacute;i cau m&agrave;y của c&ocirc; ấy cũng kh&ocirc;ng k&eacute;o d&agrave;i l&acirc;u.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc; ngẩng đầu l&ecirc;n v&agrave; quan s&aacute;t xung quanh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;L&agrave;&hellip;. ai &hellip;?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một cơn gi&oacute; lặng lẽ buồn thổi qua.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;L&agrave;&hellip;. mọi người&hellip; đ&atilde; chết? &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc; ấy chờ đợi nhưng kh&ocirc;ng c&oacute; c&acirc;u trả lời.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kek.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đột ngột c&ocirc; cười kh&uacute;c kh&iacute;ch v&agrave; bắt đầu lẩm bẩm như thể đang h&aacute;t ru.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Mọi người đều chết, mọi người đều chết&hellip;.&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc; nh&igrave;n khu vực xung quanh m&igrave;nh một lần nữa khi sự thất vọng nhuộm đỏ n&eacute;t mặt của c&ocirc;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cổ họng c&ocirc; bắt đầu kh&ocirc; r&aacute;t.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bằng c&aacute;ch n&agrave;o đ&oacute;, c&ocirc; cố gắng gượng m&igrave;nh v&agrave;o tư thế ngồi v&agrave; phun nước bọt ra. Nước da của c&ocirc; ấy s&aacute;ng l&ecirc;n một ch&uacute;t. C&ocirc; từ từ n&acirc;ng tầm nh&igrave;n mờ ảo của m&igrave;nh về ph&iacute;a bầu trời trống rỗng ph&iacute;a tr&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;L&agrave;m thế n&agrave;o ch&uacute;ng t&ocirc;i ...&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&hellip; .L&agrave;m thế n&agrave;o m&agrave; c&ocirc; ấy lại rơi v&agrave;o t&igrave;nh trạng đau khổ như vậy?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một ng&agrave;y nọ, một chủng tộc người ngo&agrave;i h&agrave;nh tinh từ thế giới kh&aacute;c x&acirc;m nhập v&agrave;o thế giới của c&ocirc;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một l&uacute;c sau, người ta ph&aacute;t hiện ra rằng chủng tộc n&agrave;y đ&atilde; bị xua đuổi khỏi thế giới qu&ecirc; hương ban đầu của họ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi trải qua một thất bại cay đắng, chủng tộc n&agrave;y lang thang kh&ocirc;ng mục đ&iacute;ch trong kh&ocirc;ng gian trống rỗng trong một thời gian rất d&agrave;i. Sau đ&oacute;, họ x&acirc;m chiếm h&agrave;nh tinh của c&ocirc; ấy để tạo ra một ng&ocirc;i nh&agrave; mới cho ch&iacute;nh họ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&hellip; .Để trở th&agrave;nh ch&uacute;a tể mới của thế giới n&agrave;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Những con điếm đ&aacute;ng khinh.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Danh t&iacute;nh của người phụ nữ n&agrave;y l&agrave; c&ocirc;ng ch&uacute;a của một vương quốc nhất định từng nằm dưới sự bảo vệ của Đế quốc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tin tức về cuộc x&acirc;m lược của người ngo&agrave;i h&agrave;nh tinh đến với c&ocirc; khi c&ocirc; mới 6 tuổi. V&agrave; sau đ&oacute;, c&ocirc; nghe tin về Đế quốc h&ugrave;ng mạnh sụp đổ khi mới 10 tuổi. Đế quốc, tự h&agrave;o với sức mạnh khoa học v&agrave; ph&eacute;p thuật &aacute;p đảo&hellip;. Đế quốc, thường được gọi l&agrave; &#39;Mặt trời kh&ocirc;ng bao giờ lặn&#39;, đ&atilde; sụp đổ trong bốn năm ngắn ngủi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chẳng bao l&acirc;u, tộc người ngo&agrave;i h&agrave;nh tinh đ&atilde; chiếm lĩnh thế giới, thậm ch&iacute; c&ograve;n đi xa đến mức ti&ecirc;u diệt vị thần được cư d&acirc;n Đế quốc t&ocirc;n thờ, nhanh ch&oacute;ng biến to&agrave;n bộ h&agrave;nh tinh n&agrave;y th&agrave;nh Miền T&acirc;y hoang d&atilde; kh&ocirc;ng c&oacute; chủ nh&acirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&oacute; lẽ, n&oacute; bắt đầu từ khoảng thời gian đ&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chẳng bao l&acirc;u sau, nhiều chủng tộc kh&aacute;c đến v&ugrave;ng đất n&agrave;y v&igrave; sự bảo vệ của c&aacute;c vị thần của n&oacute;. Họ đ&atilde; chờ đợi một cơ hội để nuốt chửng thế giới n&agrave;y từ rất l&acirc;u.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chủng tộc người ngo&agrave;i h&agrave;nh tinh x&acirc;m lược đầu ti&ecirc;n bắt đầu cuộc x&acirc;m lược đẫm m&aacute;u của họ tr&ecirc;n khắp h&agrave;nh tinh; trong khi đ&oacute;, thủ lĩnh của họ cũng tự phong m&igrave;nh l&agrave; vị thần mới.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&oacute; l&agrave; v&agrave;o khoảng thời gian đ&oacute;, khi c&aacute;c &lsquo;đội&rsquo; từ c&aacute;c thế giới kh&aacute;c cũng bắt đầu xuất hiện từng người một.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&oacute; một đội hợp nhất với danh nghĩa &lsquo;sinh tồn&rsquo;, một đội kh&aacute;c tiến về ph&iacute;a trước dưới ngọn cờ &lsquo;chinh phục&rsquo;&hellip;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhớ lại những sự kiện kh&aacute;c nhau trong qu&aacute; khứ, c&ocirc;ng ch&uacute;a bắt đầu cười khẽ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&ugrave;ng đất từng được cai trị bởi lo&agrave;i người giờ đ&acirc;y trở th&agrave;nh một khu vực chiến tranh đẫm m&aacute;u, nơi nhiều chủng tộc ngo&agrave;i h&agrave;nh tinh kh&aacute;c tranh gi&agrave;nh quyền tối cao.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Về số phận của những cư d&acirc;n ban đầu&hellip;. họ bị đẩy đi theo c&aacute;ch n&agrave;y v&agrave; c&aacute;ch kh&aacute;c, bị cuốn đi một c&aacute;ch bất lực bởi những &yacute; tưởng bất chợt của số phận. Cũng giống như ngọn nến lung linh trước gi&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cũng xuất hiện sau đ&oacute;, l&agrave; bảy vị thần. Bảy sinh vật, được sinh ra c&ugrave;ng nhau trong qu&aacute; tr&igrave;nh khai sinh của h&agrave;nh tinh, nhưng v&igrave; l&yacute; do n&agrave;y hay l&yacute; do kh&aacute;c, kh&ocirc;ng được cư d&acirc;n c&ocirc;ng nhận v&agrave; t&ocirc;n thờ cho đến tận b&acirc;y giờ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bảy vị thần n&agrave;y hứa hẹn sự tồn tại của những cư d&acirc;n nguy&ecirc;n thủy c&ograve;n lại, v&agrave; d&acirc;n ch&uacute;ng hứa sẽ t&ocirc;n thờ họ để đ&aacute;p lại. V&agrave; do đ&oacute;, thỏa thuận đ&atilde; được thực hiện - tuy nhi&ecirc;n, sự trợ gi&uacute;p từ những vị thần n&agrave;y l&agrave;&hellip;. kh&aacute; buồn cười.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Phương ph&aacute;p chống trả của họ l&agrave; triệu tập một chủng tộc kh&aacute;c gần giống nhất với cư d&acirc;n ban đầu; sau đ&oacute;, th&agrave;nh lập một đội qu&acirc;n với họ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, kh&ocirc;ng c&oacute; lựa chọn n&agrave;o kh&aacute;c ngo&agrave;i việc l&agrave;m điều n&agrave;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay cả Đế quốc h&ugrave;ng mạnh cũng đ&atilde; sụp đổ trong bốn năm. Vậy, vương quốc của c&ocirc; ấy v&agrave; qu&acirc;n đội của n&oacute; c&oacute; cơ hội n&agrave;o, một vương quốc cần sự bảo vệ của Đế quốc để tồn tại?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&ecirc;n cạnh đ&oacute;, kh&ocirc;ng c&oacute; đủ người sống s&oacute;t do hậu quả của cuộc chiến li&ecirc;n mi&ecirc;n v&agrave; đẫm m&aacute;u.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;&hellip; Những con điếm thối tha đ&oacute;.&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc; ấy vu vơ nh&igrave;n l&ecirc;n bầu trời v&agrave; phun ra những lời nguyền rủa.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ch&uacute;ng ta kh&ocirc;ng n&ecirc;n tin tưởng họ ngay từ đầu.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thực ra, ban đầu n&oacute; kh&ocirc;ng tệ lắm.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một hoặc hai nh&oacute;m đầu ti&ecirc;n được c&aacute;c vị thần triệu hồi đ&atilde; thể hiện tốc độ ph&aacute;t triển đ&aacute;ng kinh ngạc sau khi nhận được &acirc;n sủng của bảy vị thần.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, phạm vi ảnh hưởng của họ cũng ph&aacute;t triển rộng lớn, v&agrave; chẳng bao l&acirc;u, t&igrave;nh h&igrave;nh dần thay đổi khi họ bắt đầu c&ocirc; lập v&agrave; bắt bớ những cư d&acirc;n ban đầu.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhiều l&yacute; do cho điều n&agrave;y: họ bị t&aacute;ch biệt bởi quốc tịch, m&agrave;u da, gi&aacute;o điều, v&agrave; thậm ch&iacute;, sự ủng hộ ch&iacute;nh trị.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng cuối c&ugrave;ng, thủ phạm thực sự l&agrave; &lsquo;lợi &iacute;ch&rsquo;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi sự ph&acirc;n chia trong h&agrave;ng ngũ của c&aacute;c người địa cầu được triệu hồi ng&agrave;y c&agrave;ng s&acirc;u sắc, n&oacute; cũng g&acirc;y ra một hố s&acirc;u h&igrave;nh th&agrave;nh trong c&aacute;c cấu tr&uacute;c từng thống nhất của vương quốc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Li&ecirc;n minh được th&agrave;nh lập với mong muốn tồn tại đ&atilde; sớm tan vỡ th&agrave;nh nhiều mảnh, v&agrave; cấu tr&uacute;c quyền lực được x&acirc;y dựng lại với rất nhiều hy sinh bắt đầu suy yếu một lần nữa do xung đột nội bộ. Một số thậm ch&iacute; c&ograve;n đi xa hơn để tố c&aacute;o c&aacute;c vị thần mới.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tất cả đều l&agrave; những sự kiện thực sự kh&oacute; tin.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng, đ&oacute; l&agrave; tất cả?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Phần lớn c&aacute;c người địa cầu từ chối tham gia v&agrave;o trận chiến cuối c&ugrave;ng n&agrave;y. Họ lạnh l&ugrave;ng bỏ qua những lời cầu xin tuyệt vọng để tham gia, v&agrave; thay v&agrave;o đ&oacute; trở về thế giới qu&ecirc; hương của ch&iacute;nh họ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sự tức giận kh&ocirc;ng bao giờ ngu&ocirc;i của c&ocirc;ng ch&uacute;a đối với họ l&agrave; do lịch sử n&agrave;y.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;C&aacute;c con của&hellip;.&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc; định chửi thề th&ecirc;m một lần nữa, nhưng sau đ&oacute;, c&ocirc; nhanh ch&oacute;ng ngậm miệng lại.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giật g&acirc;n&hellip;. giật g&acirc;n&hellip;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một &acirc;m thanh nhẹ nh&agrave;ng vang l&ecirc;n trong nghĩa địa đang nguội lạnh nhanh ch&oacute;ng của v&ocirc; số x&aacute;c chết. &Acirc;m thanh c&agrave;ng l&uacute;c c&agrave;ng gần, cho đến khi n&oacute; dừng lại ở chỗ ngay b&ecirc;n cạnh c&ocirc;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một x&aacute;c chết ch&aacute;y rụi ở đ&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[T&ocirc;i ngạc nhi&ecirc;n.]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave; ph&iacute;a trước x&aacute;c chết n&agrave;y l&agrave; một b&oacute;ng tối lớn bất chấp mọi m&ocirc; tả.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Điều n&agrave;y thật tuyệt vời l&agrave;m sao! T&ocirc;i kh&ocirc;ng đặt nhiều hy vọng, nhưng bạn đ&atilde; sống s&oacute;t sau chiến trường tuyệt vọng n&agrave;y&hellip;.]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&lsquo;Anh ấy l&agrave; người địa cầu?&rsquo; (C&ocirc;ng ch&uacute;a)</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Như để trả lời c&acirc;u hỏi của c&ocirc;, cơ thể gục xuống của người đ&agrave;n &ocirc;ng từ từ ngẩng đầu l&ecirc;n, mặc d&ugrave; c&oacute; rất nhiều kh&oacute; khăn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một cảm x&uacute;c mạnh mẽ tr&agrave;o d&acirc;ng trong c&ocirc;, nhưng ngay sau đ&oacute;, c&ocirc; phải nuốt n&oacute; xuống.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&hellip; Bởi v&igrave;, t&igrave;nh trạng của người đ&agrave;n &ocirc;ng đ&oacute; đủ kinh khủng để khiến c&ocirc; gần như kh&ocirc;ng nh&igrave;n ra chỗ kh&aacute;c.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Con số c&oacute; thể rất, rất nhỏ, nhưng kh&ocirc;ng c&oacute; nghĩa l&agrave; kh&ocirc;ng c&oacute; người địa cầu n&agrave;o tham gia trận chiến. Đối với anh ta cuối c&ugrave;ng tr&ocirc;ng như vậy, dường như anh ta đ&atilde; t&ocirc;n trọng thỏa thuận theo c&aacute;ch của ri&ecirc;ng m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi nghĩ như vậy, c&ocirc; bắt đầu nảy sinh cảm t&igrave;nh với anh, nhưng đồng thời, c&ocirc; cũng nghĩ đ&oacute; l&agrave; một sự mất m&aacute;t.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nếu tất cả c&aacute;c địa cầu kh&aacute;c giống như người đ&agrave;n &ocirc;ng n&agrave;y&hellip;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Mặc d&ugrave; t&ocirc;i muốn khen ngợi những h&agrave;nh động xứng đ&aacute;ng của bạn trong một thời gian nữa, nhưng thật đ&aacute;ng tiếc, bạn kh&ocirc;ng c&ograve;n nhiều trong cuộc đời của m&igrave;nh.]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f321b294-7fff-2cad-1885-de52acbe9e1b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giọng n&oacute;i trầm thấp như muốn rung chuyển m&agrave;ng nhĩ của c&ocirc;.</span></span></p>\r\n<p>\r\n	<br />\r\n	&nbsp;</p>\r\n', 'Cà Di', '2020-09-25 11:13:08', 1, 0, 153);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(38, 'Chương 1 : thức tỉnh', 'chuong-1-thuc-tinh', 34, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Bạn đ&atilde; t&ocirc;n trọng ph&iacute;a của bạn trong thỏa thuận, v&igrave; vậy t&ocirc;i sẽ t&ocirc;n trọng của t&ocirc;i. Bạn ước g&igrave;?]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&ocirc;i mắt mờ, yếu ớt của anh nh&igrave;n v&agrave;o b&oacute;ng tối đang đến gần.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi đ&ocirc;i m&ocirc;i anh ta hơi h&eacute; mở, m&aacute;u chứa những phần nội tạng b&ecirc;n trong của anh ta tu&ocirc;n ra. Như thể anh thậm ch&iacute; kh&ocirc;ng thể t&igrave;m thấy giọng n&oacute;i của m&igrave;nh, chỉ c&oacute; tiếng gi&oacute; r&iacute;t qua ph&aacute;t ra từ miệng anh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Bạn kh&ocirc;ng cố n&oacute;i cũng được. T&ocirc;i chỉ đơn giản l&agrave; sẽ đọc được suy nghĩ của bạn, thay v&agrave;o đ&oacute;&hellip;. V&agrave; như vậy, bạn c&oacute; muốn được hồi sinh kh&ocirc;ng?]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc;ng ch&uacute;a đột nhi&ecirc;n cảm thấy muốn cười th&agrave;nh tiếng ở đ&acirc;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&oacute;ng tối c&oacute; thực sự hỏi người đ&agrave;n &ocirc;ng rằng anh ta c&oacute; muốn được hồi sinh kh&ocirc;ng? Trong ho&agrave;n cảnh hiện tại?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Vậy th&igrave; anh ta c&oacute; thể l&agrave;m g&igrave; nếu được hồi sinh?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mọi thứ đ&atilde; bị mất v&agrave; bị ph&aacute; hủy rồi, vậy c&ograve;n &iacute;ch lợi g&igrave;?</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Đ&oacute; kh&ocirc;ng phải l&agrave; điều bạn muốn? Thật ngu ngốc l&agrave;m sao, khi cuộc sống của bạn đang treo lơ lửng tr&ecirc;n c&aacute;n c&acirc;n. Trong trường hợp đ&oacute;, bạn c&ograve;n ước g&igrave; nữa? Perchance, bạn c&oacute; đang t&igrave;m kiếm sự gi&agrave;u c&oacute; ngay cả trong t&igrave;nh huống như thế n&agrave;y kh&ocirc;ng? C&oacute; lẽ, bạn t&igrave;m kiếm danh dự v&agrave; vinh quang?]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;&hellip;&hellip;&hellip;&hellip;.&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Bạn n&oacute;i g&igrave;?]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giọng của b&oacute;ng tối tăng l&ecirc;n rất nhiều.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Bạn, muốn bắt đầu lại từ đầu?]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đột nhi&ecirc;n, một cảm gi&aacute;c d&agrave;y đặc, ngột ngạt của điềm xấu cứ thế lan tr&agrave;n trong l&ograve;ng c&ocirc;ng ch&uacute;a.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Đ&oacute; l&agrave; điều kh&ocirc;ng thể!]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một giọng n&oacute;i phẫn nộ khiến kh&ocirc;ng kh&iacute; gợn s&oacute;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Cho d&ugrave; th&agrave;nh t&iacute;ch của bạn c&oacute; vĩ đại đến đ&acirc;u, th&igrave; vẫn kh&ocirc;ng hợp l&yacute; khi y&ecirc;u cầu thời gian quay ngược lại! V&agrave; ngo&agrave;i ra, bạn chỉ mới đạt được một th&agrave;nh t&iacute;ch nhỏ v&agrave; kh&ocirc;ng đ&aacute;ng kể như vậy, nhưng bạn muốn quay lại mọi thứ như ban đầu?]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;&hellip;&hellip;&hellip; ..&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Thật trơ tr&aacute;o! C&oacute; thể, nếu bạn đ&atilde; đạt được những kỳ t&iacute;ch tương tự như c&aacute;c sự kiện ng&agrave;y nay h&agrave;ng chục lần, th&igrave; điều đ&oacute; c&oacute; thể l&agrave; khả thi. Nhưng, với những g&igrave; bạn đ&atilde; đạt được cho đến nay th&igrave; kh&ocirc;ng. Đừng bận t&acirc;m đến linh hồn của bạn, ngay cả một ch&uacute;t thịt của bạn sẽ kh&ocirc;ng trở lại qu&aacute; khứ!]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;&hellip;&hellip;&hellip;.&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Thật ki&ecirc;n tr&igrave;! T&ocirc;i sẽ khoan hồng thay cho những th&agrave;nh t&iacute;ch m&agrave; bạn đ&atilde; đạt được cho đến nay, v&agrave; sự thật rằng cuộc đời bạn sắp kết th&uacute;c. N&oacute;i một điều ước kh&aacute;c.]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau đ&oacute;&hellip;. sự im lặng nặng nề bao tr&ugrave;m.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[&hellip;. Tại sao bạn lại ước một điều như vậy?]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&oacute; thể cảnh tượng c&aacute;i đầu ấp &uacute;ng của người đ&agrave;n &ocirc;ng thật đ&aacute;ng thương, giọng n&oacute;i vang b&ecirc;n tai c&ocirc;ng ch&uacute;a dường như đ&atilde; dịu đi đ&aacute;ng kể.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Con của t&ocirc;i&hellip;. Cầu ch&uacute;c cho sự hồi sinh của bạn trong vội v&agrave;ng. Nếu đ&oacute; l&agrave; mong muốn thiết tha của bạn, vậy chẳng phải tốt hơn bạn n&ecirc;n lập được nhiều kỳ t&iacute;ch hơn nữa trong tương lai v&agrave; sau đ&oacute; thực hiện lại ước muốn đ&oacute; sao? &hellip; .Mặc d&ugrave; vậy, kh&ocirc;ng thể đảm bảo điều ước sẽ được thực hiện hay kh&ocirc;ng.]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&ocirc;i vai của người đ&agrave;n &ocirc;ng khẽ rung l&ecirc;n. C&ocirc; nghĩ rằng anh ta c&oacute; thể đang cười thầm một m&igrave;nh. Sống s&oacute;t tr&ecirc;n chiến trường địa ngục n&agrave;y đ&atilde; đủ l&agrave; một ph&eacute;p m&agrave;u. C&oacute; thể, bạn c&ograve;n c&oacute; thể gọi đ&oacute; l&agrave; sự may mắn &lsquo;bất chấp trời ban&rsquo;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chỉ được ph&eacute;p tr&ecirc;n Creativenovels.com</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng sau đ&oacute;, anh đ&atilde; phải đi v&agrave; đạt được những kỳ t&iacute;ch tương tự như thế n&agrave;y h&agrave;ng chục lần?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người đ&agrave;n &ocirc;ng, c&ocirc;ng ch&uacute;a, thậm ch&iacute; l&agrave; chủ nh&acirc;n của giọng n&oacute;i đ&oacute;, họ đều biết điều đ&oacute; l&agrave; kh&ocirc;ng thể.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau đ&oacute;, người đ&agrave;n &ocirc;ng từ từ ngẩng đầu l&ecirc;n với rất nhiều kh&oacute; khăn. M&ocirc;i anh khẽ mấp m&aacute;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[K&iacute; ức của bạn?]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;&hellip;&hellip; ..&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[&hellip; .V&agrave; bạn muốn cảm x&uacute;c thật của m&igrave;nh l&agrave;&hellip;.?]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;&hellip;&hellip; ..&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Nếu t&acirc;m hồn v&agrave; thể x&aacute;c của bạn l&agrave; điều kh&ocirc;ng thể, th&igrave; bạn c&oacute; muốn cảm x&uacute;c m&agrave; bạn cảm thấy ở đ&acirc;y được gửi lại kh&ocirc;ng?]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Như thể điều ước n&agrave;y kh&aacute; bất ngờ, một cơn im lặng kh&aacute;c ập xuống.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[&hellip; .Chỉ gửi lại những cảm x&uacute;c gắn với kỉ niệm&hellip;. Chắc chắn, những cảm x&uacute;c bạn cảm thấy h&ocirc;m nay chỉ đơn giản l&agrave; một phần của những cảm x&uacute;c bạn cảm thấy gần như h&agrave;ng ng&agrave;y.] (TL: T&ocirc;i thực sự gặp rắc rối với c&acirc;u thoại n&agrave;y. Mặc d&ugrave; bản th&ocirc; nghe rất triết l&yacute; v&agrave; s&acirc;u sắc, nhưng về cốt l&otilde;i, n&oacute; chỉ thuần t&uacute;y BS v&agrave; t&ocirc;i kh&ocirc;ng thể chuyển n&oacute; sang tiếng Anh m&agrave; bản th&acirc;n t&ocirc;i kh&ocirc;ng ph&aacute;t &acirc;m như * s. V&igrave; vậy, đ&acirc;y l&agrave; những g&igrave; bạn sẽ nhận được.)</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau một khoảng thời gian d&agrave;i im lặng, một giọng n&oacute;i n&oacute;i với họ một lần nữa.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Tuy nhi&ecirc;n, ngay cả điều đ&oacute; cũng kh&oacute;.]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chỉ trong gi&acirc;y l&aacute;t, nhưng đ&ocirc;i m&ocirc;i của người đ&agrave;n &ocirc;ng hấp hối đ&atilde; giật giật.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[T&ocirc;i th&agrave;nh thật xin lỗi.]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave; đ&oacute; l&agrave; kết th&uacute;c. Đ&ocirc;i vai chậm r&atilde;i, uể oải run rẩy của anh cuối c&ugrave;ng cũng ngừng cử động.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đi xuống.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đầu anh gục xuống, kh&ocirc;ng bao giờ ngoi l&ecirc;n được nữa.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Thật ngu ngốc&hellip;.]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Từ trong b&oacute;ng tối, một thứ giống như b&agrave;n tay vươn ra.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Như thể n&oacute; thấy sự mất m&aacute;t n&agrave;y kh&aacute; tiếc nuối, n&oacute; từ từ xoa đầu người đ&agrave;n &ocirc;ng đ&atilde; ng&atilde;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;T&ocirc;i hiểu anh ấy.&quot; (c&ocirc;ng ch&uacute;a)</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc;ng ch&uacute;a bất ngờ l&ecirc;n tiếng sau khi xem cảnh n&agrave;y diễn ra từ đầu.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&agrave;n tay của b&oacute;ng tối ngừng vuốt ve đầu người đ&agrave;n &ocirc;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[C&ograve;n bạn&hellip;. mang d&ograve;ng m&aacute;u ho&agrave;ng gia.]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Đ&uacute;ng vậy, thần Gula.&quot; (c&ocirc;ng ch&uacute;a)</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Như thể c&ocirc; ấy thấy to&agrave;n bộ kh&aacute;i niệm về việc trở th&agrave;nh ho&agrave;ng tộc kh&aacute; buồn cười, c&ocirc; ấy lớn tiếng cười khẩy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Vương quốc đ&atilde; sụp đổ. T&ocirc;i chắc rằng b&acirc;y giờ c&aacute;c c&aacute;nh cổng cũng đ&atilde; được tiếp quản. V&igrave; vậy, c&oacute; lẽ, chẳng th&agrave; chết sau khi trải qua một sự kiện khủng khiếp như vậy sao? &Yacute; t&ocirc;i l&agrave;, tất cả đ&atilde; được sắp xếp gọn g&agrave;ng, phải kh&ocirc;ng? V&igrave; k&iacute; ức của anh sẽ bị x&oacute;a sạch v&igrave; lời thề. Ngo&agrave;i ra, anh ấy phải c&oacute; một nơi để trở về. &quot; (c&ocirc;ng ch&uacute;a)</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Kh&ocirc;ng. Đứa trẻ n&agrave;y kh&ocirc;ng muốn quay lại.]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&ocirc;i mắt c&ocirc; tr&ograve;n xoe v&igrave; ngạc nhi&ecirc;n sau khi nghe giọng n&oacute;i nặng nề.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Anh ấy n&oacute;i rằng kh&ocirc;ng c&oacute; nơi n&agrave;o để gọi về nh&agrave; ngay cả khi anh ấy quay trở lại.]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Kh&ocirc;ng c&oacute; nơi n&agrave;o để gọi l&agrave; nh&agrave;&hellip;.&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Những lời n&oacute;i đ&oacute; đ&atilde; chạm đến tr&aacute;i tim c&ocirc;. Chỉ một ch&uacute;t th&ocirc;i.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&acirc;y c&oacute; phải l&agrave; trường hợp của &#39;khổ chủ y&ecirc;u thương&#39; kh&ocirc;ng?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Với sự hủy diệt của vương quốc, c&ocirc; ấy cũng kh&ocirc;ng c&ograve;n nơi n&agrave;o để gọi l&agrave; nh&agrave; nữa. Một v&agrave;i người đi lạc c&oacute; thể đ&atilde; sống s&oacute;t, nhưng chẳng bao l&acirc;u nữa, số phận của họ sẽ trở n&ecirc;n tồi tệ hơn số phận của gia s&uacute;c.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bởi v&igrave;&hellip;. con người l&agrave; kẻ thua cuộc trong cuộc chiến n&agrave;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-fef0aa83-7fff-0bec-26c1-6e9aad4cafb0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Trong trường hợp đ&oacute;, tại sao bạn kh&ocirc;ng thực hiện điều ước của anh ấy?&quot; (c&ocirc;ng ch&uacute;a)</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n', 'Cà Di', '2020-09-25 11:13:45', 1, 0, 289);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(39, 'chương 1 : thức tỉnh phần 2', 'chuong-1-thuc-tinh-phan-2', 34, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trước những lời than phiền nhẹ nh&agrave;ng của c&ocirc;, b&oacute;ng tối nhẹ nh&agrave;ng bật cười.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Ho&agrave;n to&agrave;n v&ocirc; l&yacute;. Mọi kết quả đều c&oacute; nguy&ecirc;n nh&acirc;n. Kết quả đứa trẻ n&agrave;y muốn l&agrave; can thiệp v&agrave;o qu&aacute; khứ bất kể phương ph&aacute;p n&agrave;o.]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc;ng ch&uacute;a cười kh&uacute;c kh&iacute;ch. C&ocirc; thực sự kh&ocirc;ng thể hiểu, cũng như kh&ocirc;ng muốn hiểu n&oacute;. Kh&ocirc;ng, n&oacute; chỉ giống như một c&aacute;i cớ đối với c&ocirc; ấy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Chỉ đơn giản l&agrave;, th&agrave;nh t&iacute;ch của anh ấy kh&ocirc;ng đủ để trở th&agrave;nh nguy&ecirc;n nh&acirc;n.]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Bất kể điều đ&oacute;, c&oacute; vẻ như bạn đang hối tiếc về điều g&igrave; đ&oacute;, phải kh&ocirc;ng?&quot; (c&ocirc;ng ch&uacute;a)</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Tất nhi&ecirc;n, n&oacute; l&agrave; tiếc nuối. Đứa trẻ n&agrave;y, n&oacute; đ&atilde;&hellip;. Anh ta vốn được sinh ra với số phận của kẻ dẫn đường.]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;C&aacute;i&hellip;. ai l&atilde;nh đạo? &rdquo; (c&ocirc;ng ch&uacute;a)</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc;ng ch&uacute;a trở n&ecirc;n kh&aacute; sốc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đặc vụ của bảy vị thần, hay c&ograve;n gọi l&agrave; c&aacute;c t&ocirc;ng đồ - bảy người t&igrave;m kiếm ch&acirc;n l&yacute;, được chọn để chiến đấu chống lại những con qu&aacute;i vật c&oacute; khả năng hủy diệt to&agrave;n bộ thế giới&hellip;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tất nhi&ecirc;n, vấn đề l&agrave; chỉ c&oacute; một trong số những người được gọi l&agrave; t&igrave;m kiếm sự thật n&agrave;y tham gia v&agrave;o trận chiến n&agrave;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Thật vậy, anh ấy l&agrave; một trong những ng&ocirc;i sao s&aacute;ng nhất. Nhưng, anh ấy đ&atilde; cố gắng hủy hoại tất cả những điều đ&oacute; bằng ch&iacute;nh tay m&igrave;nh&hellip;. Tại sao con người c&aacute;c bạn lại chỉ biết đến sự cay đắng của sự hối hận, khi đ&atilde; qu&aacute; muộn?]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một cơn im lặng nặng nề kh&aacute;c đến thăm b&oacute;ng tối.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc;ng ch&uacute;a cũng ngậm miệng lại. L&yacute; do tại sao c&ocirc; buộc m&igrave;nh phải n&oacute;i, l&agrave; v&igrave; c&ocirc; kh&ocirc;ng cảm thấy muốn chết ở đ&acirc;y cảm thấy c&ocirc; đơn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chỉ l&agrave; c&ocirc; ấy bằng c&aacute;ch n&agrave;o đ&oacute; đ&atilde; tỉnh lại. C&ocirc; biết qu&aacute; r&otilde; rằng c&ocirc; cũng kh&ocirc;ng c&ograve;n nhiều thời gian nữa, ngay l&uacute;c c&ocirc; mở mắt.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&Aacute;nh mắt c&ocirc; từ từ lướt về ph&iacute;a người đ&agrave;n &ocirc;ng đ&atilde; chết ở b&ecirc;n cạnh m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kết cục đ&aacute;ng thương của anh dường như v&ocirc; c&ugrave;ng bi thảm đối với c&ocirc;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc; kh&ocirc;ng thể n&oacute;i điều n&agrave;y l&agrave; sự thật hay kh&ocirc;ng, nhưng nếu anh ấy muốn quay ngược thời gian, th&igrave; anh ấy hẳn đ&atilde; vượt qua rất nhiều cuộc khủng hoảng sinh tử thực sự nguy hiểm. Nhưng ngay cả sự cống hiến của anh ấy cũng kh&ocirc;ng đủ để thực hiện điều ước của m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh ta đ&atilde; chiến đấu như một con ch&oacute;, v&agrave; chết như một con người, m&agrave; kh&ocirc;ng nhận được khoản đền b&ugrave; như đ&atilde; hứa.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;&hellip; .Oh, lạy ch&uacute;a, Gula.&rdquo; (c&ocirc;ng ch&uacute;a)</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc;ng ch&uacute;a hơi do dự trước khi th&ograve; tay v&agrave;o t&uacute;i.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Điều ước của Earthling n&agrave;y, l&agrave;m ơn&hellip;. Cho.&quot; (c&ocirc;ng ch&uacute;a)</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Mm?]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Lời hứa của nh&agrave; vua - bạn kh&ocirc;ng qu&ecirc;n n&oacute;, phải kh&ocirc;ng?&quot; (c&ocirc;ng ch&uacute;a)</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&oacute;ng tối tho&aacute;ng chốc bị sa lầy v&agrave;o sự bối rối, nhưng ngay lập tức, n&oacute; dừng lại.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&ograve;ng b&agrave;n tay rộng mở của c&ocirc; ấy đ&oacute;ng vai tr&ograve; chủ nh&agrave; cho một chiếc v&ograve;ng cổ thể hiện sự kh&eacute;o l&eacute;o tinh xảo. Mặc d&ugrave; n&oacute; bị vấy bẩn bởi những đốm m&aacute;u ở đ&acirc;y v&agrave; ở đ&oacute;, nhưng kh&ocirc;ng một thứ g&igrave; c&oacute; thể l&agrave;m giảm đi &aacute;nh s&aacute;ng rực rỡ ph&aacute;t ra từ n&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Đ&oacute; l&agrave;&hellip;.]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Nếu bạn lấy lời hứa m&agrave; cha t&ocirc;i đ&atilde; hứa v&agrave; th&ecirc;m n&oacute; v&agrave;o những th&agrave;nh tựu m&agrave; Earthling n&agrave;y đ&atilde; đạt được, liệu điều ước cuối c&ugrave;ng của &ocirc;ng ấy c&oacute; đủ kh&ocirc;ng? Ngay cả khi việc đảo ngược thời gian cũng kh&oacute; khăn &rdquo;. (c&ocirc;ng ch&uacute;a)</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[C&oacute; l&yacute; do g&igrave; để bạn đi xa như vậy kh&ocirc;ng?]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Tất nhi&ecirc;n.&quot; (c&ocirc;ng ch&uacute;a)</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi c&aacute;c Earthlings vượt qua, ho&agrave;ng gia cũng hứa sẽ thưởng hậu hĩnh cho họ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thậm ch&iacute;, c&ocirc; kh&ocirc;ng cần phải c&acirc;n nhắc đến những con c&aacute; b * phản bội đ&atilde; bỏ chạy trong khi chỉ lo thắt cổ tự vẫn. Nhưng, Earthling n&agrave;y trước mắt c&ocirc; vẫn tiếp tục giằng x&eacute; lương t&acirc;m của c&ocirc;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Việc ho&agrave;ng gia sẽ t&ocirc;n trọng lời hứa đ&oacute; chỉ l&agrave; ch&iacute;nh x&aacute;c v&agrave; đ&uacute;ng đắn, v&igrave; người đ&agrave;n &ocirc;ng n&agrave;y r&otilde; r&agrave;ng đ&atilde; t&ocirc;n trọng lời hứa đ&oacute;. Ngo&agrave;i ra, đ&acirc;y cũng l&agrave; h&agrave;nh động cuối c&ugrave;ng của niềm tự h&agrave;o của nh&agrave; vua đối với c&ocirc;ng ch&uacute;a sắp chết.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Thay v&agrave;o đ&oacute;, điều ước của bạn c&oacute; thể được thực hiện th&igrave; sao?]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;V&agrave; bạn c&oacute; thể l&agrave;m g&igrave; cho t&ocirc;i?&quot; (c&ocirc;ng ch&uacute;a)</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc;ng ch&uacute;a cười th&agrave;nh tiếng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một trong những điều c&ocirc; nhận ra trong cuộc chiến k&eacute;o d&agrave;i n&agrave;y l&agrave; những vị thần được gọi l&agrave; to&agrave;n năng chắc chắn kh&ocirc;ng phải l&agrave; những sinh vật như vậy. V&igrave; vậy, c&ocirc; ấy c&oacute; thể ước g&igrave;, trong thế giới đ&atilde; t&agrave;n lụi n&agrave;y?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[T&ocirc;i sẽ nhắc lại điều n&agrave;y, nhưng đứa trẻ n&agrave;y kh&ocirc;ng thể quay ngược thời gian về mặt vật l&yacute;.] [Chỉ c&oacute; những cảm x&uacute;c m&atilde;nh liệt của khao kh&aacute;t v&agrave; hối tiếc&hellip; Ngay cả những điều đ&oacute;, sẽ chỉ truyền đến cậu như một giấc mơ tho&aacute;ng qua. Thậm ch&iacute; kh&ocirc;ng phải l&agrave; một sự chuyển giao của k&yacute; ức&hellip;.] [Anh ta c&oacute; thể coi n&oacute; như kh&ocirc;ng c&oacute; g&igrave; phải lo lắng. Hoặc, c&oacute; thể, thậm ch&iacute; l&agrave; một giấc mơ m&agrave; anh ấy đ&atilde; mơ trong đ&ecirc;m v&agrave; sau đ&oacute;, đến s&aacute;ng, qu&ecirc;n hết chuyện đ&oacute; đi.] [Nhưng, đ&uacute;ng l&agrave; cả bạn v&agrave; anh ấy đều sẽ chết ở nơi n&agrave;y. Ngay cả khi đ&oacute; - bạn đang n&oacute;i rằng bạn sẽ vẫn chọn con đường n&agrave;y?]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Như thể đ&aacute;nh dấu v&agrave;o từng hộp kiểm của qu&aacute; tr&igrave;nh x&aacute;c nhận, giọng n&oacute;i lọt v&agrave;o tai c&ocirc; nhiều lần.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sẽ l&agrave; dối tr&aacute; nếu c&ocirc; ấy kh&ocirc;ng nảy sinh cảm gi&aacute;c nghi ngờ trong l&ograve;ng. Tuy nhi&ecirc;n&hellip;. c&ocirc; ấy đ&atilde; qu&aacute; kiệt sức. Cuộc chiến n&agrave;y đ&atilde; diễn ra qu&aacute; l&acirc;u. V&agrave; trong suốt thời gian qua, c&ocirc; ấy đ&atilde; phải chịu đựng với tư c&aacute;ch l&agrave; một trong những nh&agrave; l&atilde;nh đạo cuối c&ugrave;ng c&ograve;n sống.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng b&acirc;y giờ, c&ocirc; muốn nghỉ ngơi. C&ocirc; nghĩ rằng sẽ kh&ocirc;ng phải l&agrave; một &yacute; kiến ​​tồi nếu trở về c&otilde;i hư v&ocirc; v&agrave; ngủ qu&ecirc;n m&atilde;i m&atilde;i.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&lsquo;Chỉ khi tất cả Người Tr&aacute;i Đất đều giống như bạn&hellip;.&rsquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&hellip; .V&igrave; vậy, c&ocirc; ấy c&oacute; thể kh&ocirc;ng hối tiếc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Đ&oacute; c&oacute; phải l&agrave; điều bạn mong muốn được anh ấy thực hiện kh&ocirc;ng? Ngay cả với c&aacute;i gi&aacute; phải trả l&agrave; từ bỏ tiền bản quyền của bạn?]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lần đầu ti&ecirc;n trong cuộc tr&ograve; chuyện n&agrave;y, một nụ cười ch&acirc;n th&agrave;nh nở tr&ecirc;n m&ocirc;i c&ocirc;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Đ&uacute;ng.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Điều ước của c&ocirc; đ&atilde; được ho&agrave;n th&agrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Trong trường hợp đ&oacute;, tốt th&ocirc;i.]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">N&oacute; giống như một đ&ocirc;i c&aacute;nh dang rộng trong b&oacute;ng tối.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[Đến gần hơn, con của t&ocirc;i&hellip;]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đột nhi&ecirc;n, to&agrave;n th&acirc;n c&ocirc; cảm thấy trống rỗng. V&agrave;o thời điểm c&ocirc; nhận ra sự thay đổi n&agrave;y, tầm nh&igrave;n của c&ocirc; trở n&ecirc;n mờ nhạt.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cả thế giới dường như quay kh&ocirc;ng ngừng; v&agrave; sau đ&oacute;, vật chất kh&ocirc;ng x&aacute;c định nhanh ch&oacute;ng tiếp cận c&ocirc;. Điều cuối c&ugrave;ng c&ocirc; ấy nh&igrave;n thấy l&agrave;&hellip;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[T&ocirc;i sẽ chờ đợi trong dự đo&aacute;n&hellip;.]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&hellip; .Một mảnh vỡ nhỏ m&agrave;u xanh lam mọc l&ecirc;n ngay ph&iacute;a tr&ecirc;n người đ&agrave;n &ocirc;ng đ&atilde; chết, v&agrave;&hellip;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[&hellip; .Đến ng&agrave;y t&ocirc;i gặp lại cả hai người.]</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-efdffee4-7fff-538a-e604-3f6f2ca4283b\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&hellip; .V&agrave;, b&oacute;ng tối đang cười lớn trong niềm vui sướng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n', 'Cà Di', '2020-09-25 11:14:09', 1, 0, 353);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(40, 'Chương 2 :  Chạm trán bất ngờ', 'chuong-2-cham-tran-bat-ngo', 7, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người phụ nữ to lớn, mập mạp v&agrave; tr&ocirc;ng chừng ba mươi tuổi. C&ocirc; ấy mặc bộ quần &aacute;o m&agrave;u đỏ thẫm v&agrave; một b&ocirc;ng hoa đỏ khổng lồ nằm tr&ecirc;n t&oacute;c. Khu&ocirc;n mặt c&ocirc; ấy th&ocirc; kệch v&agrave; xấu x&iacute;, tr&ecirc;n tay c&ocirc; ấy l&agrave; một c&acirc;y ch&ugrave;y c&oacute; chiều d&agrave;i gần bằng chiều cao của một người đ&agrave;n &ocirc;ng trưởng th&agrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người đ&agrave;n &ocirc;ng b&ecirc;n cạnh c&ocirc; khoảng bốn mươi tuổi v&agrave; khu&ocirc;n mặt cũng kh&ocirc;ng c&oacute; g&igrave; đặc biệt. &Ocirc;ng ta mặc &aacute;o cho&agrave;ng lụa m&agrave;u xanh lam v&agrave; đeo một thanh kiếm ngắn ngang h&ocirc;ng trong một vỏ bọc bằng gỗ m&agrave;u v&agrave;ng. Tr&ecirc;n tay &ocirc;ng ta l&agrave; một c&acirc;y nỏ d&agrave;i ba thước. N&oacute; kh&ocirc;ng c&oacute; tiễn v&igrave; &ocirc;ng ta vừa mới bắn v&agrave;o Lưu Minh như một đ&ograve;n tấn c&ocirc;ng mở m&agrave;n.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Hai người kh&ocirc;ng thuộc Đội Cận vệ Hắc Hổ, phải kh&ocirc;ng?&quot; Lưu Minh h&iacute;t một hơi thật s&acirc;u hỏi, nh&igrave;n kỹ hai người.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi sống tr&ecirc;n đảo Savage trong một khoảng thời gian d&agrave;i như vậy, hắn đ&atilde; học c&aacute;ch thăm d&ograve; điểm yếu của kẻ th&ugrave; trước khi bắt đầu chiến đấu.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&acirc;u hỏi của hắn vừa l&agrave; thăm d&ograve; xem ch&uacute;ng mạnh đến mức n&agrave;o vừa l&agrave; c&aacute;ch để k&eacute;o d&agrave;i thời gian.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tr&ecirc;n thực tế, ngay khi &aacute;nh mắt của họ chạm nhau, bộ n&atilde;o của Lưu Minh đ&atilde; bắt đầu hoạt động.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người phụ nữ, với c&aacute;nh tay d&agrave;y v&agrave; bước ch&acirc;n nặng nề, r&otilde; r&agrave;ng l&agrave; một kiểu sức mạnh. C&ocirc; ấy c&oacute; thể k&eacute;m hơn một ch&uacute;t về tốc độ nhưng với vũ kh&iacute; đ&oacute; trong tay, ngay cả một va chạm nhỏ nhất cũng đừng mong chạm đến c&ocirc; ta. Người đ&agrave;n &ocirc;ng c&oacute; đ&ocirc;i b&agrave;n tay trắng trẻo v&agrave; vững ch&atilde;i với vẻ ngo&agrave;i mờ &aacute;m. &Ocirc;ng ta rất c&oacute; thể c&oacute; một số loại kỹ năng đặc biệt. Để chống lại &ocirc;ng ta, cần phải ch&uacute; &yacute; v&agrave; thận trọng hơn&hellip;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hai người kia đương nhi&ecirc;n kh&ocirc;ng biết rằng thiếu ni&ecirc;n d&aacute;ng vẻ yếu ớt sẽ suy nghĩ nhiều th&ocirc;ng tin như vậy trong thời gian ngắn. Đ&acirc;y l&agrave; lần đầu ti&ecirc;n họ phải đối mặt với một đối thủ trẻ như vậy v&agrave; họ kh&ocirc;ng thể kh&ocirc;ng lộ ra vẻ mặt th&iacute;ch th&uacute;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người đ&agrave;n &ocirc;ng d&ugrave;ng một tay nắm lấy chiếc chốt mới v&agrave; nạp tiễn v&agrave;o chiếc nỏ c&oacute; nhiều lỗ trống, trong khi n&oacute;i một c&aacute;ch vui vẻ, &ldquo;Lưu Dương T&ocirc;ng, đến từ Dương th&agrave;nh ở ph&iacute;a nam. Bảy năm trước, &ocirc;ng ta lừa dối nh&agrave; vua v&agrave; bị tống v&agrave;o ngục ở Nam Lan quận; sau đ&oacute; &ocirc;ng ta chết v&igrave; bệnh tật trong t&ugrave;. Con trai của &ocirc;ng, tuy nhi&ecirc;n, đ&atilde; được trao cơ hội sống do tuổi c&ograve;n trẻ của m&igrave;nh mặc d&ugrave; hắn ta sẽ phải sống phần c&ograve;n lại của cuộc đời m&igrave;nh bị giam cầm tr&ecirc;n đảo Savage. Tuy nhi&ecirc;n, một th&aacute;ng trước, đảo Savage bị ch&igrave;m xuống đ&aacute;y Biển Chết m&agrave; kh&ocirc;ng r&otilde; l&yacute; do. Hầu hết c&aacute;c t&ugrave; nh&acirc;n đều chết trong v&ugrave;ng biển, mặc d&ugrave; chỉ c&oacute; Lưu Minh v&agrave; 11 người kh&aacute;c trốn tho&aacute;t khỏi Biển Chết. Giờ đ&acirc;y, Bộ Tư ph&aacute;p đ&atilde; ban h&agrave;nh tiền thưởng hạng bạc cho những người bị kết &aacute;n trốn tho&aacute;t: chết hoặc sống. Ta n&oacute;i đ&uacute;ng chứ? &rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giọng n&oacute;i của người đ&agrave;n &ocirc;ng nhỏ dần khi người phụ nữ &aacute;o đỏ b&ecirc;n cạnh hắn ta bật ra một tiếng cười sắc lạnh, &ldquo;Thằng nh&oacute;c, người đ&agrave;n &ocirc;ng n&agrave;y đ&atilde; chết bảy ng&agrave;y trước bởi tay ch&uacute;ng ta. Hắn c&oacute; phải l&agrave; đồng đội của ngươi kh&ocirc;ng? &quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc; ấy thản nhi&ecirc;n giật lấy một chiếc t&uacute;i da ở thắt lưng v&agrave; n&eacute;m xuống đất.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Dok.&quot; Chiếc t&uacute;i lộn ngược ra lăn ra một c&aacute;i đầu đầy m&aacute;u.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đầu được che phủ bởi kh&aacute; nhiều l&ocirc;ng mặt, khiến &ocirc;ng ta tr&ocirc;ng khoảng bốn mươi tuổi. C&aacute;i đầu c&oacute; biểu hiện th&ocirc; r&aacute;p đen tối trong khi miệng h&aacute; ra khập khiễng.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lưu Minh liếc nh&igrave;n c&aacute;i đầu, trong l&ograve;ng lập tức trầm xuống. Hắn th&igrave; thầm một c&aacute;i t&ecirc;n, &ldquo;Đầu th&eacute;p&hellip;&rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<br />\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;V&igrave; vậy, ta kh&ocirc;ng sai. C&oacute; vẻ như ngươi nhận ra hắn ta. Chết tiệt, nếu ngươi lặng lẽ để ch&uacute;ng ta bắt, hai ch&uacute;ng ta c&oacute; thể cho ngươi một ch&uacute;t thời gian, v&agrave; ngươi thậm ch&iacute; c&oacute; thể sống s&oacute;t sau khi bị ph&aacute;n x&eacute;t. Tuy nhi&ecirc;n, nếu ngươi quyết định ra tay ngay b&acirc;y giờ, ch&uacute;ng ta sẽ giết ngươi kh&ocirc;ng tiếc lời &rdquo;. Người đ&agrave;n &ocirc;ng mặc &aacute;o xanh n&oacute;i sau khi nạp xong nỏ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Hai người đ&atilde; biết rất nhiều về ta, c&oacute; nghĩa l&agrave; hai người c&oacute; lẽ đến từ Bộ Tư ph&aacute;p. Hai ngươi đang ở tr&igrave;nh độ n&agrave;o? D&ugrave;ng những lời hứa su&ocirc;ng như vậy để lừa dối ta; Hai người đang n&oacute;i dối ta v&igrave; ta c&ograve;n trẻ? Luật ph&aacute;p của Đế quốc kh&ocirc;ng thể bị bất chấp. Với số lượng Vệ binh Hắc Hổ m&agrave; ta đ&atilde; giết, ta e rằng ngay cả khi nh&agrave; vua đứng ra bảo vệ ta, ta vẫn sẽ bị băm th&agrave;nh nhiều mảnh. &quot; Lưu Minh &aacute;nh mắt chớp động, kh&ocirc;ng c&oacute; một gi&acirc;y tin tưởng nam nh&acirc;n &aacute;o lam.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người đ&agrave;n &ocirc;ng khịt mũi trước c&acirc;u trả lời n&agrave;y, kh&ocirc;ng phủ nhận cũng kh&ocirc;ng x&aacute;c nhận bất cứ điều g&igrave;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người phụ nữ &aacute;o đỏ ở b&ecirc;n cạnh cười v&agrave; n&oacute;i, &ldquo;Thật kh&ocirc;ng thể tin được l&agrave; đứa trẻ n&agrave;y c&ograve;n nhỏ m&agrave; vẫn hiểu rất r&otilde; luật lệ của Ho&agrave;ng gia; những người từ đảo Savage thật phi thường. Ngay cả khi họ c&ograve;n trẻ, ta kh&ocirc;ng thể đ&aacute;nh gi&aacute; thấp họ. Ngươi n&oacute;i đ&uacute;ng rằng anh ấy, phu qu&acirc;n ta, v&agrave; ta phục vụ Bộ Tư ph&aacute;p. Cụ thể, ch&uacute;ng t&ocirc;i đang ở cấp Silver Scale. Tiểu đệ, khi ngươi chết m&agrave; đến được ho&agrave;ng tuyền, đừng tr&aacute;ch ch&uacute;ng ta bắt nạt kẻ yếu. Phu qu&acirc;n l&agrave;m đi! &rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">TL: Ho&agrave;ng tuyền = c&aacute;ch giải th&iacute;ch của người Trung Quốc về nơi c&aacute;c linh hồn đi</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người phụ nữ xấu x&iacute; n&oacute;i xong, vẻ mặt đột nhi&ecirc;n lạnh đi. C&ocirc; tấn c&ocirc;ng, c&uacute; vung ch&ugrave;y của c&ocirc; r&iacute;t l&ecirc;n dữ dội trong kh&ocirc;ng kh&iacute; khi n&oacute; lao về ph&iacute;a người thiếu ni&ecirc;n trẻ tuổi.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mặc d&ugrave; c&oacute; th&acirc;n h&igrave;nh to lớn, c&ocirc; ấy nhanh nhẹn một c&aacute;ch lạ thường; Mặc d&ugrave; c&acirc;y ch&ugrave;y trong tay c&ocirc; ấy rất lớn, c&ocirc; ấy vung n&oacute; như thể n&oacute; kh&ocirc;ng trọng lượng.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Với sự phối hợp ho&agrave;n hảo, người đ&agrave;n &ocirc;ng mặc &aacute;o xanh ở ph&iacute;a b&ecirc;n kia n&acirc;ng chiếc nỏ khổng lồ l&ecirc;n v&agrave; bắn ra h&agrave;ng chục tia s&aacute;ng lạnh lẽo, về ph&iacute;a hai b&ecirc;n của Lưu Minh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lưu Minh nhăn mặt. Nếu hắn n&eacute; sang tr&aacute;i hoặc phải, hắn sẽ nhảy thẳng về ph&iacute;a trước của c&aacute;c chốt nỏ, nhưng nếu hắn vẫn ở giữa, th&igrave; hắn sẽ phải đối mặt với sự tấn c&ocirc;ng của người phụ nữ xấu x&iacute;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sự phối hợp của họ thật ho&agrave;n mỹ, kh&ocirc;ng c&oacute; g&igrave; lạ khi hai người họ đ&atilde; l&agrave; vợ chồng.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lưu Minh vừa nh&igrave;n cảnh tượng n&agrave;y, sắc mặt liền thay đổi. Tuy nhi&ecirc;n, t&acirc;m tr&iacute; hắn vẫn tiếp tục chuyển động. Khoảnh khắc tiếp theo, hắn h&iacute;t v&agrave;o một hơi, v&agrave; b&agrave;n tay hắn nắm chặt lấy lưỡi kiếm m&agrave;u bạc của m&igrave;nh. Thanh kiếm của hắn ta trở th&agrave;nh một vệt s&aacute;ng khi hắn ta vung n&oacute; v&agrave;o đầu người phụ nữ xấu x&iacute;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay cả khi đối mặt với c&acirc;y ch&ugrave;y khổng lồ, Lưu Minh cũng kh&ocirc;ng th&egrave;m để &yacute;. Về cơ bản n&oacute; đ&atilde; tạo ra những c&uacute; đ&aacute;nh chết người.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&ocirc;i mắt của người phụ nữ xấu x&iacute; nheo lại. Mặc d&ugrave; c&ocirc; biết rằng kẻ th&ugrave; của c&ocirc; kh&ocirc;ng cố gắng chết c&ugrave;ng với c&ocirc;, nhưng c&ocirc; kh&ocirc;ng sẵn s&agrave;ng thực sự liều mạng. C&ocirc; buộc phải thay đổi tầm v&oacute;c v&agrave; r&uacute;t ch&ugrave;y về ph&iacute;a sau một ch&uacute;t để chặn thanh kiếm bạc.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lưu Minh b&uacute;ng cổ tay v&agrave; thanh kiếm bạc thu lại trong mờ ảo để tr&aacute;nh va chạm với ch&ugrave;y. Sau đ&oacute;, hắn ta hung &aacute;c vung thanh kiếm hai lần, một lần b&ecirc;n tr&aacute;i v&agrave; một lần b&ecirc;n phải.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Đăng Đăng.&quot; Hai luồng lao về ph&iacute;a Lưu Minh lập tức bị đ&aacute;nh bay.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Chết tiệt!&quot; Từ xa, người đ&agrave;n &ocirc;ng mặc &aacute;o xanh kh&ocirc;ng thể cắn lại lời nguyền của m&igrave;nh khi hắn ta thấy bu l&ocirc;ng của m&igrave;nh dừng lại. Nắm chặt lấy thứ g&igrave; đ&oacute; ở thắt lưng, hắn ta lại bắt đầu nạp tiễn cho nỏ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chi&ecirc;u thức đặc biệt m&agrave; hắn sử dụng trước đ&oacute; để b&iacute; mật kiểm so&aacute;t đường đi của những chiếc bu l&ocirc;ng đ&atilde; đối đầu với v&ocirc; số kẻ th&ugrave; mạnh. Hắn ta kh&ocirc;ng bao giờ c&oacute; thể ngờ rằng n&oacute; sẽ kh&ocirc;ng hiệu quả với người thiếu ni&ecirc;n trẻ tuổi n&agrave;y.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<br />\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người phụ nữ xấu x&iacute; cũng bất ngờ nhưng nhanh ch&oacute;ng lấy lại sự tập trung. C&ocirc; ấy giận dữ vung ch&ugrave;y của m&igrave;nh v&agrave; bắt đầu chiến đấu với người thiếu ni&ecirc;n trẻ tuổi một lần nữa.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-01a1ce33-7fff-007a-33d7-0d9f220560dc\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mỗi khi c&ocirc; vung vũ kh&iacute; khổng lồ, gi&oacute; sẽ nổi l&ecirc;n xung quanh n&oacute;. To&agrave;n bộ cơ thể c&ocirc; trở n&ecirc;n to lớn dữ dội, như thể biến th&agrave;nh một con qu&aacute;i vật h&igrave;nh người. Sức mạnh của c&ocirc; ấy l&agrave; kh&ocirc;ng thể ngăn cản.</span></span></p>\r\n', 'Cà Di', '2020-10-09 08:22:15', 1, 0, 124);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(41, 'Chương 2 :  Chạm trán bất ngờ phần 2', 'chuong-2-cham-tran-bat-ngo-phan-2', 7, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngược lại, thanh kiếm bạc của Lưu Minh ho&agrave;n to&agrave;n kh&ocirc;ng đụng độ với c&acirc;y ch&ugrave;y. N&oacute; biến th&agrave;nh một tia s&aacute;ng bạc khi hắn ta đi v&ograve;ng quanh người phụ nữ xấu x&iacute;, cố gắng tr&aacute;nh hầu hết c&aacute;c cuộc tấn c&ocirc;ng.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">D&ugrave; gặp bất lợi nhưng mọi đ&ograve;n tấn c&ocirc;ng của hắn đều nhằm v&agrave;o điểm trọng yếu của người phụ nữ xấu x&iacute;. Mỗi đ&ograve;n tấn c&ocirc;ng đều buộc c&ocirc; phải giảm tốc độ tấn c&ocirc;ng để bảo vệ bản th&acirc;n.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mặc d&ugrave; tr&igrave;nh độ v&otilde; thuật của người phụ nữ n&agrave;y vượt xa Lưu Minh, nhưng t&igrave;nh huống n&agrave;y khiến c&ocirc; ấy tức giận h&eacute;t l&ecirc;n.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lưu Minh kh&ocirc;ng quan t&acirc;m; hắn ta củng cố quyết t&acirc;m của m&igrave;nh v&agrave; tiếp tục kh&ocirc;ng ngừng di chuyển xung quanh người phụ nữ xấu x&iacute;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nh&igrave;n hắn l&uacute;c n&agrave;y, hắn gần như c&oacute; vẻ rất thoải m&aacute;i. Tuy nhi&ecirc;n, tr&ecirc;n thực tế, hắn ta đ&atilde; một lần nữa sử dụng một kỹ thuật b&iacute; mật để vắt kiệt ch&uacute;t sức lực cuối c&ugrave;ng khỏi cơ thể m&igrave;nh. Nếu kh&ocirc;ng, cơ thể yếu ớt của hắn ta sẽ bị thổi bay bởi những cơn gi&oacute; dữ dội chỉ do c&acirc;y ch&ugrave;y tạo ra v&agrave; hắn ta sẽ kh&ocirc;ng thể tấn c&ocirc;ng người phụ nữ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Từ xa, khi người đ&agrave;n &ocirc;ng mặc &aacute;o xanh nh&igrave;n thấy t&igrave;nh h&igrave;nh trước mắt, hắn ta đ&atilde; bị sốc.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn ho&agrave;n to&agrave;n nhận thức được vợ m&igrave;nh c&oacute; sức mạnh như thế n&agrave;o.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mặc d&ugrave; họ thường xuy&ecirc;n luyện tập với nhau, nhưng hắn sẽ kh&ocirc;ng bao giờ d&aacute;m đấu với c&ocirc; một c&aacute;ch thẳng thắn như vậy. Tuy nhi&ecirc;n, cậu nh&oacute;c n&agrave;y - vẫn c&ograve;n ở tuổi thiếu ni&ecirc;n - thực sự c&oacute; thể l&agrave;m được điều đ&oacute;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cứ như thể cậu thiếu ni&ecirc;n n&agrave;y bắt đầu tập luyện v&otilde; thuật khi c&ograve;n trong bụng mẹ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Theo hiểu biết của hắn, một số qu&yacute; tộc gi&agrave;u c&oacute; sẽ cho con ch&aacute;u của họ huấn luyện c&aacute;c kỹ thuật tăng cường cơ thể ngay từ khi c&ograve;n nhỏ. Họ sẽ sử dụng c&aacute;c loại thuốc tắm kh&aacute;c nhau v&agrave; cho họ uống đan dược kh&ocirc;ng ngừng nghỉ, nhưng ngay cả những đợt huấn luyện khắc nghiệt nhất cũng kh&ocirc;ng bao giờ tạo ra được một người mạnh mẽ như người thiếu ni&ecirc;n trước mặt m&igrave;nh l&uacute;c n&agrave;y.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">May mắn thay, ch&agrave;ng trai vẫn c&ograve;n trẻ v&agrave; sức lực r&otilde; r&agrave;ng l&agrave; kh&ocirc;ng đủ. Nếu ba hoặc bốn năm nữa tr&ocirc;i qua, d&ugrave; vợ chồng c&ugrave;ng nhau chiến đấu hết sức m&igrave;nh, họ cũng kh&ocirc;ng thể ra đi an to&agrave;n.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tất nhi&ecirc;n, ch&agrave;ng trai trẻ sẽ kh&ocirc;ng bao giờ c&oacute; cơ hội đ&oacute; - giờ họ đ&atilde; t&igrave;m thấy cậu ta.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi người đ&agrave;n &ocirc;ng mặc &aacute;o xanh nghĩ đến điều n&agrave;y, s&aacute;t kh&iacute; của hắn ta c&agrave;ng tăng th&ecirc;m. Hắn ta giơ nỏ l&ecirc;n bằng một tay, tay kia r&uacute;t thanh kiếm ngắn ra khỏi vỏ gỗ của n&oacute;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thanh kiếm ngắn m&agrave;u x&aacute;m v&agrave; gần như kh&ocirc;ng trọng lượng, n&oacute; được l&agrave;m bằng xương!</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chỉ với một cử động nhỏ nhất, người đ&agrave;n &ocirc;ng đ&atilde; l&eacute;n l&uacute;t lao đến gần chiến trường m&agrave; kh&ocirc;ng một tiếng động.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lưu Minh chỉ cần nh&igrave;n tho&aacute;ng qua l&agrave; c&oacute; thể nhận ra h&agrave;nh động đ&aacute;nh l&eacute;n của người đ&agrave;n &ocirc;ng mặc &aacute;o xanh. Tr&aacute;i tim vốn đ&atilde; căng thẳng của hắn ch&igrave;m s&acirc;u hơn v&agrave;o tuyệt vọng.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn hầu như kh&ocirc;ng thể đối mặt với một trong số họ một m&igrave;nh. Nếu hai người c&ugrave;ng nhau tấn c&ocirc;ng, cuộc đời của hắn ta sẽ thực sự kết th&uacute;c ở đ&acirc;y.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&oacute; vẻ như hắn ta kh&ocirc;ng thể kh&ocirc;ng liều mạng một lần nữa.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nghĩ đến đ&acirc;y, l&ograve;ng hắn kh&ocirc;ng khỏi c&oacute; ch&uacute;t do dự. Chống lại c&acirc;y ch&ugrave;y khổng lồ, hắn ta thực sự ngừng n&eacute; tr&aacute;nh. N&acirc;ng kiếm bằng c&aacute;nh tay của m&igrave;nh, hắn ta th&igrave; thầm, &quot;Qua cổ họng ...&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tĩnh mạch nổi l&ecirc;n tr&ecirc;n c&aacute;nh tay của Lưu Minh v&agrave; c&aacute;nh tay của hắn ta đột nhi&ecirc;n d&agrave;i ra &iacute;t nhất l&agrave; một v&ograve;ng tr&ograve;n đầy đặn.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thanh kiếm bạc thực hiện một số chuyển động kỳ lạ, biến th&agrave;nh một tia bạc trực tiếp xuy&ecirc;n qua cổ họng của người phụ nữ. Tốc độ của n&oacute; cao hơn rất nhiều so với những g&igrave; đ&atilde; chứng minh trước đ&acirc;y.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người phụ nữ giật m&igrave;nh trước cảnh tượng n&agrave;y v&agrave; cố r&uacute;t ch&ugrave;y để tự vệ nhưng đ&atilde; qu&aacute; muộn.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">D&ugrave; rất tức giận nhưng tr&aacute;i tim c&ocirc; đ&atilde; quyết định. C&ocirc; ấy thả lỏng cả hai tay v&agrave; n&eacute;m thẳng ch&ugrave;y của m&igrave;nh v&agrave;o ngực người thiếu ni&ecirc;n trẻ tuổi.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong suy nghĩ của c&ocirc;, nếu c&ocirc; cố gắng sử dụng những đ&ograve;n tấn c&ocirc;ng chết người, rất c&oacute; thể hắn ta sẽ r&uacute;t lui về ph&iacute;a sau để bảo vệ t&iacute;nh mạng của m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, kh&oacute;e mắt của Lưu Minh chỉ khẽ co giật v&agrave; động t&aacute;c của hắn kh&ocirc;ng thay đổi ch&uacute;t n&agrave;o. Hắn h&iacute;t v&agrave;o một ngụm kh&iacute; lớn, v&agrave; vặn eo khiến ngực hắn trở n&ecirc;n phẳng lặng đến kh&oacute; tin.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;K&ecirc;u vang.&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&acirc;y ch&ugrave;y khổng lồ kho&eacute;t s&acirc;u v&agrave;o ngực ch&agrave;ng thanh ni&ecirc;n, để lại những r&atilde;nh s&acirc;u khiến m&aacute;u bắn ra ngay lập tức.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, biểu hiện của Lưu Minh kh&ocirc;ng thay đổi ch&uacute;t n&agrave;o. Như thể người chịu đ&ograve;n nặng như vậy kh&ocirc;ng phải l&agrave; hắn. Bằng một c&uacute; xoay tay, thanh kiếm bạc đ&atilde; được r&uacute;t ra khỏi lỗ tr&ecirc;n cổ người phụ nữ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người phụ nữ h&eacute;t l&ecirc;n khi cả hai tay đang &ocirc;m chặt lấy cổ họng c&ocirc;. Cơ thể mập mạp của c&ocirc; kh&ocirc;ng ngừng co giật tr&ecirc;n mặt đất.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tất cả những điều n&agrave;y xảy ra nhanh như chớp!</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người đ&agrave;n &ocirc;ng mặc &aacute;o xanh vừa tiến ra tiền tuyến khi hắn ta nh&igrave;n r&otilde; mọi thứ. Hắn ta gầm l&ecirc;n kinh ngạc v&agrave; giương nỏ bắn ra mười tia. C&ugrave;ng l&uacute;c đ&oacute;, con dao găm bằng xương trong tay lập tức đ&acirc;m về ph&iacute;a khu trống b&ecirc;n cạnh Lưu Minh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi Lưu Minh chuẩn bị gi&aacute;ng một đ&ograve;n nữa v&agrave;o người phụ nữ, trực gi&aacute;c của hắn đ&atilde; cảnh b&aacute;o hắn về nguy hiểm lớn. Kh&ocirc;ng cần suy nghĩ, hắn lập tức xoay vai sang một b&ecirc;n.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">M&aacute;u lo&eacute; l&ecirc;n v&agrave; một thứ v&ocirc; h&igrave;nh sắc b&eacute;n sượt qua mặt Lưu Minh, cắt một &iacute;t t&oacute;c bay tứ tung của hắn.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Vũ kh&iacute; m&ecirc; nh&acirc;n, ngươi l&agrave; một linh lực sư!&quot; Một c&aacute;i lật người, Lưu Minh tr&ecirc;n ch&acirc;n đ&aacute;p xuống. Khi nh&igrave;n s&acirc;u hơn v&agrave;o đồ vật trong tay người đ&agrave;n &ocirc;ng, hắn ta kh&ocirc;ng thể kh&ocirc;ng thốt l&ecirc;n.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-09206c95-7fff-643a-f092-e44a76dba6ea\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Từ con dao găm trong tay người đ&agrave;n &ocirc;ng, một v&agrave;i tia s&aacute;ng bất thường ph&aacute;t ra, l&oacute;e l&ecirc;n một tia s&aacute;ng trắng mờ.</span></span></p>\r\n', 'Cà Di', '2020-10-09 08:22:41', 1, 0, 341);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(42, 'Chương 1: Bị tình yêu làm cho mê muội', 'chuong-1-bi-tinh-yeu-lam-cho-me-muoi', 12, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Năng lượng l&agrave; bản chất của mọi sự sống.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Năng lượng l&agrave; phương tiện để nu&ocirc;i dưỡng bản chất của cuộc sống.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đạt đến đỉnh cao của tu luyện mang lại một loạt c&aacute;c khả năng kỳ diệu v&agrave; đ&aacute;ng kinh ngạc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&aacute;c chuy&ecirc;n gia nghệ thuật năng lượng c&oacute; thể d&ugrave;ng tay ph&aacute; tường, nghe &acirc;m thanh y&ecirc;n tĩnh như tiếng bước ch&acirc;n của kiến, đi tr&ecirc;n mặt nước, thở ra lửa, trở n&ecirc;n kh&ocirc;ng thấm v&agrave;o mọi loại vũ kh&iacute;, hợp nhất năng lượng của ch&uacute;ng với những đ&aacute;m m&acirc;y, tự duy tr&igrave; bằng c&aacute;ch hấp thụ năng lượng thay v&igrave; ăn thức ăn, v&agrave; thậm ch&iacute; theo d&otilde;i kẻ th&ugrave; bằng c&aacute;ch cảm nhận linh hồn của họ&hellip;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lục địa tươi tốt. Yanhaven.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&oacute; l&agrave; một nơi đ&ocirc;ng d&acirc;n v&agrave; gi&agrave;u c&oacute;; Mặc d&ugrave; đ&oacute; l&agrave; ban đ&ecirc;m, vẫn c&oacute; thể nh&igrave;n thấy những d&ograve;ng ngựa v&agrave; xe ngựa tr&ecirc;n đường phố, v&agrave; th&agrave;nh phố được tắm bằng &aacute;nh đ&egrave;n rực rỡ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&oacute; l&agrave; giữa m&ugrave;a h&egrave;, v&agrave; những đ&aacute;m m&acirc;y đen giăng đầy bầu trời đ&ecirc;m. Từ xa c&oacute; thể nghe thấy tiếng sấm ầm ầm bị b&oacute;p nghẹt, v&agrave; từ độ ẩm d&agrave;y đặc trong kh&ocirc;ng kh&iacute;, c&oacute; vẻ như mưa sẽ sớm rơi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ở một g&oacute;c tương đối hẻo l&aacute;nh của th&agrave;nh phố, mặt đất đột nhi&ecirc;n r&ugrave;ng m&igrave;nh, rồi một c&aacute;i hố nổ tung.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một người đ&agrave;n &ocirc;ng trẻ tuổi nhảy ra khỏi hố, nhanh nhẹn như một con m&egrave;o. Anh ta c&oacute; vẻ khoảng mười bảy hay mười t&aacute;m tuổi, mặc quần &aacute;o đen từ đầu đến ch&acirc;n, v&agrave; mang theo một chiếc hộp được chế t&aacute;c tinh xảo.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người thanh ni&ecirc;n n&agrave;y l&agrave; Dương Kỳ, giống như nhiều người kh&aacute;c, đ&atilde; tu luyện kh&iacute; c&ocirc;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh ta l&agrave; một thiếu gia từ gia tộc Yang, một gia đ&igrave;nh qu&yacute; tộc từ Yanhaven. Anh ta cũng tương đối nổi tiếng trong th&agrave;nh phố với tư c&aacute;ch l&agrave; một bậc thầy kh&iacute; c&ocirc;ng mạnh mẽ. Anh ấy kh&ocirc;ng gi&agrave; lắm, v&agrave; kh&ocirc;ng nhất thiết phải cao v&agrave; tr&aacute;ng kiện. Nhưng từ mỗi cử động v&agrave; h&agrave;nh động của anh ta, r&otilde; r&agrave;ng l&agrave; nguồn năng lượng b&iacute; ẩn chảy trong người anh ta sẵn s&agrave;ng b&ugrave;ng ph&aacute;t bất cứ l&uacute;c n&agrave;o.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi đứng đ&oacute; ở m&eacute;p hố, anh ta lưu chuyển kh&iacute; c&ocirc;ng của m&igrave;nh, v&agrave; những &acirc;m thanh ầm ầm dữ dội c&oacute; thể nghe thấy, g&acirc;y chấn động đến cực điểm.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;T&ocirc;i đo&aacute;n sẽ kh&ocirc;ng l&acirc;u nữa t&ocirc;i sẽ đạt đến giai đoạn thứ năm của kh&iacute; c&ocirc;ng, cấp độ B&ugrave;ng nổ Năng lượng. Sau đ&oacute;, t&ocirc;i sẽ c&oacute; thể truyền năng lượng thực sự của m&igrave;nh ra b&ecirc;n ngo&agrave;i cơ thể. Điều đ&oacute; sẽ khiến t&ocirc;i trở th&agrave;nh một chuy&ecirc;n gia thực sự! Sự tiến bộ của t&ocirc;i sẽ thật đ&aacute;ng kinh ngạc! &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh nh&igrave;n lại bức tường th&agrave;nh cao ngất, tr&ocirc;ng rất h&agrave;i l&ograve;ng v&igrave; đ&atilde; ph&aacute; n&oacute; theo c&aacute;ch anh đ&atilde; l&agrave;m. Sau đ&oacute;, anh ta mờ đi trong chuyển động, biến mất như một con b&aacute;o đen v&agrave;o khu rừng tối xung quanh th&agrave;nh phố.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Kỳ từ l&acirc;u đ&atilde; tu luyện kh&iacute; c&ocirc;ng của m&igrave;nh đến giai đoạn thứ tư, cấp độ Tinh luyện năng lượng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tại lục địa Rich-Lush, c&aacute;c bật thấy&nbsp; tu luyện kh&iacute; c&ocirc;ng&nbsp; đ&atilde; chia ch&uacute;ng th&agrave;nh ch&iacute;n giai đoạn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đầu ti&ecirc;n l&agrave; Tăng trưởng năng lượng, trong đ&oacute; người ta x&acirc;y dựng năng lượng trong cơ thể.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thứ hai l&agrave; Tuần ho&agrave;n năng lượng, trong đ&oacute; một người gửi năng lượng quan trọng chảy qua c&aacute;c k&ecirc;nh kinh tuyến của một người.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thứ ba l&agrave; Hội tụ năng lượng, khi năng lượng quan trọng được tập trung tại một vị tr&iacute; trung t&acirc;m, biển năng lượng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thứ tư l&agrave; Tinh luyện Năng lượng, trong đ&oacute; năng lượng quan trọng thu thập được được tinh luyện v&agrave; thanh lọc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thứ năm l&agrave; Sự b&ugrave;ng nổ năng lượng. Trong giai đoạn đ&oacute;, người ta c&oacute; thể sử dụng nghệ thuật năng lượng b&ecirc;n ngo&agrave;i để g&acirc;y hại cho kẻ th&ugrave;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bốn giai đoạn đầu ti&ecirc;n &mdash; Hấp thụ, Tuần ho&agrave;n, Hội tụ v&agrave; Tinh lọc &mdash; đều l&agrave; nội tại, v&agrave; mặc d&ugrave; ch&uacute;ng c&oacute; thể được sử dụng để tăng cường sức mạnh cho cơ thể của một người, nhưng lại l&agrave;m được rất &iacute;t. Khi đạt đến giai đoạn thứ năm, Năng lượng b&ugrave;ng ph&aacute;t, cơ thể của một người đ&atilde; ho&agrave;n to&agrave;n biến đổi, gi&uacute;p họ c&oacute; thể giải ph&oacute;ng năng lượng nghệ thuật trong chiến đấu.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chỉ khi đạt đến mức Năng lượng B&ugrave;ng nổ, người ta mới c&oacute; thể được coi l&agrave; một chuy&ecirc;n gia thực sự mạnh mẽ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&igrave; vậy, giai đoạn thứ năm được coi l&agrave; một điểm đầu nguồn ch&iacute;nh trong việc nu&ocirc;i dưỡng nghệ thuật năng lượng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ở cấp độ đ&oacute;, c&oacute; thể tung ra Trăm nhịp thần quyền, một kỹ thuật c&oacute; thể g&acirc;y s&aacute;t thương c&aacute;ch xa cả trăm bước ch&acirc;n, thậm ch&iacute; đến mức l&agrave;m đổ c&acirc;y. Năng lực chiến đấu như vậy phi thường đến mức một người sử dụng n&oacute; c&oacute; thể đ&aacute;nh bại một trăm người kh&ocirc;ng thể.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giai đoạn thứ s&aacute;u được gọi l&agrave; Cấp độ vũ kh&iacute; năng lượng, trong đ&oacute; người ta c&oacute; thể sử dụng năng lượng để tạo th&agrave;nh vũ kh&iacute; vật thể.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cấp độ thứ bảy l&agrave; cấp độ Biểu hiện Năng lượng. Trong giai đoạn đ&oacute;, năng lượng thuật c&oacute; thể được sử dụng để triệu hồi những con th&uacute; hung dữ, hoặc đ&ocirc;i c&aacute;nh c&oacute; thể đưa một con bay qua bầu trời.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giai đoạn thứ t&aacute;m, H&oacute;a th&acirc;n năng lượng, dẫn đến một khả năng gọi l&agrave; H&oacute;a th&acirc;n t&acirc;m tr&iacute;, trong đ&oacute; người tập c&oacute; thể tạo ra một h&agrave;ng r&agrave;o năng lượng ph&ograve;ng thủ với sức mạnh gần như kh&ocirc;ng thể xuy&ecirc;n thủng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giai đoạn thứ ch&iacute;n được gọi l&agrave; Cấp độ năng lượng bậc thầy. Thật kh&ocirc;ng may, Dương Kỳ rất kh&oacute; c&oacute; thể trở th&agrave;nh một kiện tướng của nghệ thuật năng lượng như vậy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Theo tin đồn, c&aacute;c chuy&ecirc;n gia ở cấp độ Master of Energy cuối c&ugrave;ng c&oacute; thể gi&agrave;nh lấy sự sống từ tr&ecirc;n trời, v&agrave; do đ&oacute; đạt đến cấp độ Lifeseizing. Những người giải nhiệt về cơ bản đ&atilde; trải qua một sự t&aacute;i sinh, v&agrave; sẽ c&oacute; tuổi thọ l&acirc;u hơn nhiều lần so với những người thực h&agrave;nh nghệ thuật năng lượng th&ocirc;ng thường. Dương Kỳ thậm ch&iacute; chưa bao giờ nh&igrave;n thấy một người như vậy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">**</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi chạy qua khu rừng một l&uacute;c, Dương Kỳ bắt gặp một thiếu nữ mặc &aacute;o m&agrave;u xanh lam, đang đứng đ&oacute; một c&aacute;ch ki&ecirc;u h&atilde;nh, quay lưng về ph&iacute;a anh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Lan&rsquo;er!&rdquo; anh ta gọi lớn. &ldquo;T&ocirc;i c&oacute; Vi&ecirc;n thuốc long ch&acirc;u! Điều đ&oacute; kh&ocirc;ng hề dễ d&agrave;ng, nhưng nhờ c&oacute; mối quan hệ của t&ocirc;i trong th&agrave;nh phố, t&ocirc;i đ&atilde; v&agrave;o được dinh thự của quan t&ograve;a th&agrave;nh phố, đột nhập v&agrave;o kho b&aacute;u của &ocirc;ng ta v&agrave; lấy trộm vi&ecirc;n thuốc! &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nh&igrave;n người phụ nữ trẻ một c&aacute;ch h&agrave;o hứng, anh ta ch&igrave;a chiếc hộp thủ c&ocirc;ng m&agrave; anh ta đ&atilde; mang theo suốt thời gian qua.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thiếu nữ xoay người, lộ ra khu&ocirc;n mặt tuấn mỹ ngoạn mục.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Vậy, Dương Kỳ, cuối c&ugrave;ng bạn đ&atilde; th&agrave;nh c&ocirc;ng.&quot; Với những ng&oacute;n tay đẹp như ng&agrave; voi vươn ra, c&ocirc; ấy n&oacute;i, &quot;H&atilde;y giao n&oacute; lại để t&ocirc;i c&oacute; thể xem qua.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ổn thỏa.&quot; Kh&ocirc;ng ch&uacute;t do dự, Dương Kỳ đưa chiếc hộp cho thiếu nữ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc; nhận lấy, rồi mở ra để lộ một vi&ecirc;n thuốc m&agrave;u đỏ rực. Một m&ugrave;i thơm dược liệu ngay lập tức tr&agrave;n ngập khắp khu vực, v&agrave; vi&ecirc;n thuốc dường như lập l&ograve;e như c&oacute; lửa, chiếu &aacute;nh s&aacute;ng đỏ l&ecirc;n khu&ocirc;n mặt của người phụ nữ trẻ, khiến c&ocirc; ấy tr&ocirc;ng c&ograve;n hấp dẫn hơn trước.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Được rồi, Lan&rsquo;er,&rdquo; Dương Kỳ n&oacute;i. &ldquo;T&ocirc;i lấy cho bạn Vi&ecirc;n thuốc long ch&acirc;u. B&acirc;y giờ ch&uacute;ng ta c&oacute; thể chạy c&ugrave;ng nhau như bạn đ&atilde; hứa, phải kh&ocirc;ng? N&agrave;o, bắt đầu th&ocirc;i. &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;T&ocirc;i xin lỗi, Dương Kỳ,&rdquo; c&ocirc; n&oacute;i, nh&eacute;t chiếc hộp v&agrave;o trong bộ quần &aacute;o của m&igrave;nh. Nh&igrave;n anh một c&aacute;ch lạnh l&ugrave;ng, c&ocirc; tiếp tục, &ldquo;T&ocirc;i c&oacute; một việc quan trọng cần phải giải quyết, v&igrave; vậy t&ocirc;i sẽ kh&ocirc;ng bỏ qua với anh. Tr&ecirc;n thực tế, đừng bao giờ nhắc đến chủ đề n&agrave;y nữa. &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lời n&oacute;i của c&ocirc; đập v&agrave;o anh như một tia chớp rơi xuống từ bầu trời xanh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lảo đảo l&ugrave;i lại v&agrave;i bước, khu&ocirc;n mặt chảy đầy m&aacute;u, anh n&oacute;i, &ldquo;Lan&rsquo;er, t&ocirc;i đ&atilde; tự đặt m&igrave;nh v&agrave;o nguy hiểm lớn cho em! T&ocirc;i đ&atilde; lấy trộm một Vi&ecirc;n thuốc long ch&acirc;u từ biệt thự của quan t&ograve;a th&agrave;nh phố, tất cả chỉ v&igrave; bạn n&oacute;i ch&uacute;ng ta sẽ chạy trốn c&ugrave;ng nhau. B&acirc;y giờ bạn đang thay đổi &yacute; định của m&igrave;nh? Tại sao? Tại sao bạn lại l&agrave;m điều g&igrave; đ&oacute; như thế n&agrave;y? &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đột nhi&ecirc;n, một giọng n&oacute;i vang l&ecirc;n, &ldquo;T&ocirc;i giải th&iacute;ch th&igrave; sao?&rdquo; Một l&uacute;c sau, một b&oacute;ng người xuất hiện từ b&oacute;ng tối của khu rừng. Anh ta l&agrave; một thanh ni&ecirc;n đẹp trai, cao v&agrave; mặc một bộ gi&aacute;p. c&uacute;i gằm mặt nh&igrave;n Dương Kỳ, r&otilde; r&agrave;ng anh ta l&agrave; một kẻ ki&ecirc;u ngạo v&agrave; ngạo mạn đến nhường n&agrave;o.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Bạn c&oacute; thực sự nghĩ rằng Lan&rsquo;er, một c&ocirc;ng ch&uacute;a tuyệt đẹp của Yundale-by-the-Sea, sẽ quan t&acirc;m đến một ch&uacute;t g&igrave; đ&oacute; từ Yang Clan kh&ocirc;ng? Gia tộc của bạn c&oacute; thể rất quan trọng ở Yanhaven, nhưng h&atilde;y để t&ocirc;i n&oacute;i với bạn điều n&agrave;y: Ngay cả c&aacute;c ho&agrave;ng tử của Yanhaven cũng kh&ocirc;ng nghĩ đến việc theo đuổi Lan&rsquo;er. C&ocirc; ấy đ&atilde; sử dụng bạn! Hiểu chưa, đồ ngu ngốc? Bạn biết c&acirc;u n&oacute;i về con c&oacute;c xấu x&iacute; th&egrave;m kh&aacute;t thi&ecirc;n nga? Đ&oacute; l&agrave; bạn!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Tất cả những điều n&agrave;y c&oacute; đ&uacute;ng kh&ocirc;ng, Lan&rsquo;er?&rdquo; Dương Kỳ hỏi, mặt anh ta t&aacute;i đi, nhưng trong l&ograve;ng vẫn giữ lấy những tia hy vọng yếu ớt vẫn tồn tại.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, điều tiếp theo Lan&rsquo;er n&oacute;i đ&atilde; b&oacute;p chết niềm hy vọng kh&ocirc;ng c&ograve;n tồn tại đ&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khu&ocirc;n mặt lạnh l&ugrave;ng v&ocirc; song, c&ocirc; ấy n&oacute;i, &ldquo;Đ&uacute;ng l&agrave;, Dương Kỳ. T&ocirc;i đ&atilde; sử dụng bạn để lấy Vi&ecirc;n thuốc long ch&acirc;u. Điều đ&oacute; đ&atilde; được n&oacute;i ra, t&ocirc;i kh&ocirc;ng muốn l&agrave;m tổn thương bạn, vậy tại sao bạn kh&ocirc;ng rời đi. &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Bạn&hellip;.&quot; Dương Kỳ run l&ecirc;n, tay ch&acirc;n đ&atilde; lạnh như băng. &ldquo;L&agrave;m thế n&agrave;o m&agrave; bạn c&oacute; thể như thế n&agrave;y, Lan&rsquo;er? Đi? T&ocirc;i phải đi đến đ&acirc;u? T&ocirc;i đ&atilde; lấy trộm một vi&ecirc;n thuốc long ch&acirc;u! T&ocirc;i kh&ocirc;ng thể trở về nh&agrave;, v&agrave; những người bảo vệ từ Yanhaven c&oacute; lẽ đ&atilde; đuổi theo t&ocirc;i. C&ograve;n bạn th&igrave; sao, &iacute;t nhất h&atilde;y trả lại Vi&ecirc;n thuốc long ch&acirc;u cho t&ocirc;i, v&agrave; ch&uacute;ng t&ocirc;i gọi n&oacute; l&agrave; đ&uacute;ng? &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thanh ni&ecirc;n &aacute;o gi&aacute;p cười lạnh. &ldquo;Trả lại cho anh? Đừng mơ nữa, punk. Ai trong t&acirc;m tr&iacute; họ sẽ nhổ thịt của họ ra sau khi cắn một miếng? Đ&aacute;nh bại n&oacute;. Nếu bạn thậm ch&iacute; nghĩ đến việc khuấy động rắc rối, t&ocirc;i sẽ khiến bạn phải hối hận&hellip;. &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Bạn&hellip;.&quot; Kh&ocirc;ng c&oacute; bất kỳ cảnh b&aacute;o n&agrave;o, Dương Kỳ đột nhi&ecirc;n b&ugrave;ng nổ h&agrave;nh động. Mạnh mẽ như một con hổ v&agrave; nhẹ như một con rắn, anh ta lao v&agrave;o người thanh ni&ecirc;n mặc &aacute;o gi&aacute;p, hai tay vung l&ecirc;n kh&ocirc;ng trung với vẻ hung &aacute;c như r&igrave;u.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b9663f8e-7fff-557e-a738-96ebf36a1867\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Hổ trắng nuốt x&aacute;c!&rdquo; anh ta h&eacute;t l&ecirc;n, ngay lập tức sử dụng một chi&ecirc;u thức năng lượng chết người độc nhất v&ocirc; nhị của Dương tộc. Ở cấp độ cao nhất, nghệ thuật năng lượng n&agrave;y c&oacute; thể được sử dụng để triệu hồi một con hổ trắng khổng lồ, hung &aacute;c đến cực điểm v&agrave; đủ mạnh để giảm bất kỳ kẻ th&ugrave; n&agrave;o chỉ c&ograve;n l&agrave; x&aacute;c sống.&nbsp;</span></span></p>\r\n<div>\r\n	&nbsp;</div>\r\n', 'Cà Di', '2020-09-25 11:17:03', 1, 0, 123);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(43, 'Chương 1: Bị tình yêu làm cho mê muội phần 2', 'chuong-1-bi-tinh-yeu-lam-cho-me-muoi-phan-2', 12, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tất nhi&ecirc;n, Th&agrave;nh Qu&acirc;n c&ograve;n l&acirc;u mới c&oacute; thể sử dụng n&oacute; theo c&aacute;ch đ&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Muốn chết?&quot; thanh ni&ecirc;n mặc gi&aacute;p n&oacute;i, trong mắt hiện l&ecirc;n vẻ khinh bỉ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&ugrave; v&ugrave;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một luồng ch&acirc;n kh&iacute; trong suốt từ trong tay bắn ra, nện v&agrave;o người Th&agrave;nh Qu&acirc;n. Trước khi c&oacute; thể ho&agrave;n th&agrave;nh việc thi triển Hổ trảo c&ocirc;ng , anh ta đ&atilde; bị đập mạnh xuống đất, nơi anh ta n&ocirc;n ra một ngụm m&aacute;u.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&ocirc;i mắt s&aacute;ng l&ecirc;n v&igrave; kinh ngạc, anh ấy nh&igrave;n l&ecirc;n v&agrave; n&oacute;i, &ldquo;ngươi&nbsp; đang ở giai đoạn thứ năm của luyện ch&acirc;n kh&iacute;, cấp độ B&ugrave;ng nổ Năng lượng&hellip;.&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Đ&uacute;ng vậy,&rdquo; người thanh ni&ecirc;n mặc gi&aacute;p đ&aacute;p. &ldquo;Đối với ta, ngươi chẳng kh&aacute;c g&igrave; một con kiến. &ldquo;Hổ trảo c&ocirc;ng l&agrave; một chi&ecirc;u thức chết người, nhưng khi bạn sử dụng n&oacute;, bạn tr&ocirc;ng giống như một đứa trẻ đang đ&aacute;nh nhau với bạn b&egrave; của m&igrave;nh&rdquo;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Th&agrave;nh Qu&acirc;n v&ugrave;ng vẫy đứng dậy. Đ&ocirc;i mắt &aacute;nh l&ecirc;n sự th&ugrave; hận, anh ấy n&oacute;i, &quot;Em l&agrave; ai ...?&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Ta l&agrave; Song Haishan, anh họ của Lan&rsquo;er. Chỉ v&agrave;i ng&agrave;y trước, c&ocirc; đ&atilde; được nhận l&agrave;m đệ tử cấp cao trong tổ chức số một ở Rich-Lush Continent, Demi-Immortal Institute. Ngươi c&oacute; biết điều đ&oacute; quan trọng như thế n&agrave;o đối với c&ocirc; ấy kh&ocirc;ng? Ngươi thực sự nghĩ rằng ngươi c&oacute; hy vọng đến gần c&ocirc; ấy? Chờ đ&atilde;&hellip; ngươi đ&atilde; hỏi ta l&agrave; ai v&igrave; ngươi nghĩ rằng ngươi sẽ truy l&ugrave;ng ta để trả th&ugrave;? Hừm. Như người ta đ&atilde; n&oacute;i, nếu ngươi diệt cỏ m&agrave; kh&ocirc;ng tận gốc, n&oacute; sẽ mọc lại khi gi&oacute; xu&acirc;n thổi qua. Ta đo&aacute;n để ngươi&nbsp; tự do thực sự kh&ocirc;ng phải l&agrave; một lựa chọn. &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay cả khi lời n&oacute;i vừa rời khỏi miệng, ch&acirc;n kh&iacute; của Song Haishan đ&atilde; bắt đầu t&iacute;ch tụ b&ecirc;n trong anh ta.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;V&igrave; vậy,&rdquo; Th&agrave;nh Qu&acirc;n gầm gừ, &ldquo;ngươi l&agrave; người m&agrave; họ gọi l&agrave; giỏi nhất trong những người giỏi nhất ở Yundale-by-the-Sea. Ta vẫn sẽ giết ngươi. V&agrave; đối với ngươi, Yun Hailan, ngươi đ&atilde; n&oacute;i dối ta, v&agrave; ta thề rằng một ng&agrave;y n&agrave;o đ&oacute;, ta sẽ trả lại cho ngươi điều đ&oacute;. &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;C&oacute; vẻ như để ngươi sống chắc chắn l&agrave; một điều sai tr&aacute;i &hellip;.&rdquo; Song Haishan gầm gừ, chuẩn bị ra tay.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Đủ rồi, anh họ,&rdquo; Yun Hailan n&oacute;i, đưa tay chặn đường anh. Sau đ&oacute;, c&ocirc; nh&igrave;n về hướng Yanhaven. &ldquo;C&aacute;c l&iacute;nh canh đ&atilde; l&ecirc;n đường. Kh&ocirc;ng c&oacute; l&yacute; do g&igrave; để giết anh ta. N&agrave;o, đi th&ocirc;i&hellip;. &rdquo; Sau đ&oacute;, c&ocirc; biến mất v&agrave;o m&agrave;n đ&ecirc;m, để lại một c&acirc;u n&oacute;i duy nhất. &ldquo;Sau khi t&ocirc;i l&agrave; đệ tử ở Viện Bất tử, Th&agrave;nh Qu&acirc;n, t&ocirc;i sẽ giải quyết vấn đề n&agrave;y với anh.&rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Song Haishan nh&igrave;n Th&agrave;nh Qu&acirc;n với vẻ chế nhạo. &ldquo;Hừm. C&oacute; vẻ như ngươi sẽ sống l&acirc;u hơn một ch&uacute;t, punk. Lan&rsquo;er kh&ocirc;ng muốn ngươi chết v&igrave; c&ocirc; ấy kh&ocirc;ng muốn m&aacute;u của ngươi d&iacute;nh tr&ecirc;n tay c&ocirc; ấy. Khỏe. Nhưng thẩm ph&aacute;n th&agrave;nh phố Yanhaven sẽ kh&ocirc;ng bu&ocirc;ng tha cho ngươi . V&igrave; vậy, ngươi sẽ kh&ocirc;ng c&ograve;n sống được bao l&acirc;u nữa. &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cười kh&uacute;c kh&iacute;ch theo kiểu nham hiểm, Hắn ta quay lại theo Yun Hailan.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Yun Hailan&hellip;.&rdquo; Th&agrave;nh Qu&acirc;n lẩm bẩm. Phun ra một &iacute;t m&aacute;u, anh nh&igrave;n về hướng m&agrave; c&ocirc; v&agrave; Song Haishan vừa biến mất, ho&agrave;n to&agrave;n nhận thức được rằng họ hơn hẳn anh về ch&acirc;n kh&iacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đột nhi&ecirc;n, anh nghĩ lại mọi thứ đ&atilde; dẫn đến thời điểm n&agrave;y. Trong qu&aacute; tr&igrave;nh gi&uacute;p đỡ c&ocirc;ng việc kinh doanh của gia tộc, anh đ&atilde; chạm tr&aacute;n với Yun Hailan, người m&agrave; anh đ&atilde; y&ecirc;u ngay từ c&aacute;i nh&igrave;n đầu ti&ecirc;n. C&ocirc; ấy đ&atilde; đối xử với anh ấy bằng sự dịu d&agrave;ng v&agrave; l&ograve;ng trắc ẩn trong một thời gian, sau đ&oacute; c&ocirc; ấy đ&atilde; y&ecirc;u cầu sự gi&uacute;p đỡ của anh ấy để c&oacute; được Vi&ecirc;n thuốc long ch&acirc;u. Anh đ&atilde; c&acirc;n nhắc vấn đề một thời gian trước khi đưa ra quyết định của m&igrave;nh. V&agrave; b&acirc;y giờ, anh ấy đ&atilde; ở đ&acirc;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi đứng đ&oacute;, anh lu&acirc;n chuyển ch&acirc;n kh&iacute; của m&igrave;nh v&agrave; cố gắng quyết định xem m&igrave;nh phải l&agrave;m g&igrave;. Anh biết rằng những người bảo vệ th&agrave;nh phố Yanhaven đang đến đ&oacute;n anh. Anh ta đ&atilde; mang lại sự sỉ nhục cho gia tộc của m&igrave;nh, v&agrave; chắc chắn sẽ bị trừng phạt nghi&ecirc;m khắc nếu anh ta bị bắt.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi đang cố gắng hồi phục, anh đột nhi&ecirc;n nghe thấy tiếng n&oacute;i. V&agrave; rồi, chỉ một l&uacute;c sau, v&agrave;i b&oacute;ng người hiện ra trước mặt anh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một trong số họ l&agrave; một người đ&agrave;n &ocirc;ng mặc bộ gi&aacute;p sắt r&egrave;n lạnh l&ugrave;ng nặng kh&ocirc;ng dưới một trăm c&acirc;n, khiến anh ta tr&ocirc;ng như một con th&uacute; hung &aacute;c. Ngay khi Th&agrave;nh Qu&acirc;n nh&igrave;n thấy người đ&agrave;n &ocirc;ng mặc &aacute;o gi&aacute;p đ&oacute;, tr&aacute;i tim anh đ&atilde; ch&igrave;m v&agrave;o tuyệt vọng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh ta l&agrave; Luo Hun, một trong những l&iacute;nh canh đặc biệt từ dinh thự của quan t&ograve;a th&agrave;nh phố, một người đ&agrave;n &ocirc;ng t&agrave;n bạo v&agrave; hung dữ với tu luyện đ&atilde; vượt qua mức B&ugrave;ng nổ Năng lượng từ l&acirc;u.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Giao vi&ecirc;n thuốc long ch&acirc;u m&agrave; ngươi đ&atilde; trộm đi, Th&agrave;nh Qu&acirc;n. Nếu ngươi l&agrave;m vậy, ta sẽ tha mạng cho ngươi &hellip;. &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&ocirc;i mắt của Luo Hun s&aacute;ng lấp l&aacute;nh như động vật chứ kh&ocirc;ng phải con người v&agrave; bộ gi&aacute;p sắt r&egrave;n lạnh l&ugrave;ng của anh tỏa ra luồng kh&iacute; s&aacute;t thương mạnh mẽ. Kh&ocirc;ng đợi phản hồi, anh ta bước tới v&agrave; đ&aacute;nh v&agrave;o v&ugrave;ng dantian của Th&agrave;nh Qu&acirc;n bằng một đ&ograve;n đ&aacute;nh, nhắm thẳng v&agrave;o hạ bộ của anh ta.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, một lần nữa, m&aacute;u phun ra từ miệng của Th&agrave;nh Qu&acirc;n khi ch&acirc;n kh&iacute; m&agrave; anh ta đ&atilde; tu luyện từ năm mười t&aacute;m tuổi bị t&ecirc; liệt.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Ngươi&hellip; l&agrave;m t&ecirc; liệt ch&acirc;n kh&iacute; của ta&hellip;?&rdquo; Vẻ mặt ho&agrave;n to&agrave;n tuyệt vọng bao tr&ugrave;m l&ecirc;n khu&ocirc;n mặt anh. Với việc ch&acirc;n kh&iacute; trong v&ugrave;ng dantian của anh ta bị ph&aacute; hủy, anh ta sẽ kh&ocirc;ng thể hội tụ năng lượng được. Do đ&oacute;, anh ta sẽ kh&ocirc;ng thể l&agrave; một người b&igrave;nh thường.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Ngươi đ&atilde; đủ t&aacute;o bạo để ăn cắp Vi&ecirc;n thuốc long ch&acirc;u,&rdquo; Luo Hun lạnh l&ugrave;ng n&oacute;i. &ldquo;L&agrave;m t&ecirc; liệt nghệ ch&acirc;n kh&iacute; của ngươi thực ra đang khiến ngươi nhẹ nh&otilde;m hơn một ch&uacute;t. Bởi v&igrave; ngươi l&agrave; thiếu gia của Dương tộc, v&agrave; cũng v&igrave; lợi &iacute;ch của d&igrave; cậu, ta sẽ kh&ocirc;ng giết ngươi ngay lập tức. Mọi người, tr&oacute;i hắn v&agrave;o c&aacute;i c&acirc;y đ&oacute;. Ta sẽ trở lại th&agrave;nh phố để b&aacute;o c&aacute;o với thẩm ph&aacute;n v&agrave; nhận lệnh mới. Ở lại đ&acirc;y v&agrave; canh g&aacute;c hiện trường vụ &aacute;n. Ta sẽ nhờ một số người từ Yang Clan đến để xem x&eacute;t t&igrave;nh h&igrave;nh. &quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;V&acirc;ng, thưa ng&agrave;i,&rdquo; c&aacute;c vệ sĩ đặc biệt kh&aacute;c trả lời. Kh&ocirc;ng ch&uacute;t do dự, họ tr&oacute;i Th&agrave;nh Qu&acirc;n v&agrave;o một c&aacute;i c&acirc;y gần đ&oacute;, v&agrave; sau đ&oacute; bắt đầu tuần tra khu vực.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Về phần Luo Hun, kh&ocirc;ng quan trọng việc anh ta đang mặc một bộ &aacute;o gi&aacute;p sắt r&egrave;n lạnh l&ugrave;ng nặng hơn trăm c&acirc;n. Anh ta lao v&agrave;o chuyển động như một con chim đang bay, trực tiếp quay trở lại Yanhaven.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Xung quanh đ&oacute;, v&ocirc; số tia chớp bắt đầu nổ tung tr&ecirc;n bầu trời tối tăm ph&iacute;a tr&ecirc;n. Cơn mưa k&eacute;o d&agrave;i suốt thời gian qua dường như đ&atilde; đạt đến độ s&ocirc;i, v&agrave; bắt đầu rơi xuống, k&egrave;m theo tia s&eacute;t, tr&ocirc;ng giống như những con rắn bạc đang quất v&agrave;o mặt đất.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Mưa to thế n&agrave;y,&rdquo; một trong những l&iacute;nh canh n&oacute;i, lo lắng nh&igrave;n l&ecirc;n. &quot;V&agrave; điều g&igrave; xảy ra với tất cả tia chớp?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đột nhi&ecirc;n, một tia s&eacute;t đập v&agrave;o một c&aacute;i c&acirc;y ph&iacute;a xa, khiến n&oacute; b&ugrave;ng ch&aacute;y bất chấp cơn mưa lớn. Cảnh tượng c&oacute; rất nhiều tia s&eacute;t rơi xuống trong mưa thực sự l&agrave; một cảnh tượng đ&aacute;ng sợ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tr&ocirc;ng ho&agrave;n to&agrave;n sợ h&atilde;i, một người bảo vệ kh&aacute;c n&oacute;i, &ldquo;Tia s&eacute;t n&agrave;y thực sự c&oacute; vẻ kỳ lạ. T&ocirc;i chưa bao giờ thấy bất cứ điều g&igrave; giống như n&oacute;. T&ocirc;i nghe n&oacute;i rằng ngay cả những người ở cấp độ đỉnh của đỉnh của ch&acirc;n kh&iacute; cũng kh&ocirc;ng thể sống s&oacute;t khi bị s&eacute;t đ&aacute;nh như thế n&agrave;y. Nếu ch&uacute;ng ta ở lại khu rừng n&agrave;y, ch&uacute;ng ta sẽ bị giết! H&atilde;y ra khỏi đ&acirc;y!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Thế c&ograve;n con ch&oacute; nhỏ n&agrave;y?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Đừng lo, v&otilde; c&ocirc;ng của hắn ta đ&atilde; bị t&ecirc; liệt. Hơn nữa, hắn ta đang bị tr&oacute;i. L&agrave;m sao hắn ta c&oacute; thể trốn tho&aacute;t được? Đang bay? &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">R&ugrave;ng m&igrave;nh sợ h&atilde;i, l&iacute;nh canh quay đầu bỏ chạy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, ngay cả khi họ vừa ra khỏi c&acirc;y, một tia chớp s&aacute;ng ch&oacute;i đ&atilde; rơi xuống khu rừng v&agrave; đập v&agrave;o c&aacute;i c&acirc;y m&agrave; Th&agrave;nh Qu&acirc;n đ&atilde; bị tr&oacute;i.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay lập tức, to&agrave;n bộ cơ thể của Th&agrave;nh Qu&acirc;n tr&agrave;n ngập điện lấp l&aacute;nh. Sau đ&oacute;, m&ugrave;i thịt ch&aacute;y bao tr&ugrave;m trong kh&ocirc;ng kh&iacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">1. Yanhaven / Rich-Lush: Yan c&oacute; nghĩa l&agrave; &quot;chim &eacute;n&quot;. Như sẽ được tiết lộ ngay sau đ&acirc;y, cũng c&oacute; một gia tộc c&ugrave;ng t&ecirc;n, đ&oacute; l&agrave; l&yacute; do tại sao t&ocirc;i kh&ocirc;ng dịch nh&acirc;n vật n&agrave;y theo t&ecirc;n của th&agrave;nh phố. Khi n&oacute;i đến c&aacute;c th&agrave;nh phố trong cuốn tiểu thuyết n&agrave;y, t&ocirc;i đang l&agrave;m điều g&igrave; đ&oacute; hơi kh&aacute;c so với c&aacute;c bản dịch kh&aacute;c của t&ocirc;i. Thay v&igrave; chỉ đơn giản dịch ch&uacute;ng l&agrave; một c&aacute;i g&igrave; đ&oacute; giống như Th&agrave;nh phố Yan, t&ocirc;i sẽ sử dụng một số hậu tố phổ biến cho hầu hết c&aacute;c th&agrave;nh phố, chẳng hạn như -haven, -forth, -ville, v.v. L&yacute; do cho việc n&agrave;y l&agrave; sử dụng từ &quot;th&agrave;nh phố&quot; trong t&ecirc;n thực tế của th&agrave;nh phố kh&ocirc;ng phổ biến bằng tiếng Anh. Mặc d&ugrave; c&oacute; Th&agrave;nh phố New York ở Mỹ, nhưng hầu hết c&aacute;c th&agrave;nh phố kh&aacute;c đều c&oacute; một danh từ ri&ecirc;ng duy nhất l&agrave;m t&ecirc;n của ch&uacute;ng. Đ&oacute; l&agrave; ti&ecirc;u chuẩn m&agrave; t&ocirc;i hướng tới trong c&acirc;u chuyện n&agrave;y. Về &quot;Rich-Lush&quot;, t&ocirc;i hiểu rằng n&oacute; nghe c&oacute; vẻ hơi kỳ quặc, nhưng c&oacute; l&yacute; do t&ocirc;i dịch n&oacute; theo c&aacute;ch đ&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">2. Yang Qi: Yang l&agrave; một họ th&ocirc;ng thường cũng c&oacute; nghĩa l&agrave; &quot;c&acirc;y dương&quot;. Qi c&oacute; nghĩa l&agrave; &quot;phi thường, đ&aacute;ng kinh ngạc, tuyệt vời, hiếm c&oacute;, đ&aacute;ng ngạc nhi&ecirc;n, v.v.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">3. &ldquo;ch&acirc;n kh&iacute;&rdquo; vừa l&agrave; t&ecirc;n của một cấp độ tu luyện rộng r&atilde;i, vừa l&agrave; thuật ngữ để m&ocirc; tả việc thực h&agrave;nh &ldquo;thuật thuật&rdquo; trong cấp độ bao qu&aacute;t đ&oacute;. Đối với tất cả c&aacute;c &yacute; định v&agrave; mục đ&iacute;ch, &quot;năng lượng&quot; đồng nghĩa với &quot;v&otilde; thuật&quot;, mặc d&ugrave; cả hai thuật ngữ đều được sử dụng</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">5. Yundale-by-the-Sea: Yun c&oacute; nghĩa l&agrave; m&acirc;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">6. Song Haishan: Song l&agrave; một họ th&ocirc;ng thường. Hai c&oacute; nghĩa l&agrave; &quot;biển&quot; v&agrave; Shan c&oacute; nghĩa l&agrave; &quot;n&uacute;i&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1d6a3044-7fff-4ac4-7bc0-59e062e21150\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">7. Yun Hailan: Yun c&oacute; nghĩa l&agrave; &ldquo;m&acirc;y&rdquo; v&agrave; Hai c&oacute; nghĩa l&agrave; &ldquo;biển&rdquo;. Lan c&oacute; nghĩa l&agrave; &quot;sương n&uacute;i, sương m&ugrave; n&uacute;i, hơi nước, v.v.&quot;</span></span></p>\r\n<div>\r\n	&nbsp;</div>\r\n', 'Cà Di', '2020-09-25 11:17:28', 1, 0, 122);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(44, 'Chương 2: Thu lợi từ thảm họa', 'chuong-2-thu-loi-tu-tham-hoa', 12, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Th&agrave;nh Qu&acirc;n kh&ocirc;ng thể để mất tinh thần hơn nữa.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chỉ với một c&uacute; đ&aacute;nh bằng l&ograve;ng b&agrave;n tay, Luo Hun đ&atilde; l&agrave;m t&ecirc; liệt ch&acirc;n kh&iacute; của m&igrave;nh, ph&aacute; hủy nguy&ecirc;n kh&iacute; của ta v&agrave; đảm bảo rằng ta kh&ocirc;ng thể t&iacute;ch lũy lại được nguy&ecirc;n kh&iacute; thật qu&aacute; độc &aacute;c.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave; ai c&oacute; thể đo&aacute;n được rằng sau đ&oacute; anh ta lại gặp bất hạnh khi bị s&eacute;t đ&aacute;nh?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi tia s&eacute;t mạnh mẽ lao v&agrave;o c&aacute;i c&acirc;y, v&agrave; sau đ&oacute;, mắt anh như muốn nổ tung, buộc anh ta phải nhắm lại.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;M&igrave;nh sắp chết &agrave;?&rdquo; Anh nghĩ. &ldquo;Ch&agrave;, tađo&aacute;n điều đ&oacute; sẽ tốt. Sau đ&oacute;, ta sẽ kh&ocirc;ng phải chịu đựng trở th&agrave;nh tr&ograve; cười trong suốt phần đời c&ograve;n lại của m&igrave;nh. &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi tia s&eacute;t x&acirc;m nhập v&agrave;o cơ thể, anh ta cảm thấy đau đớn v&agrave; n&oacute;ng bỏng chưa từng c&oacute;, như thể h&agrave;ng triệu triệu lưỡi kiếm đang cắt v&agrave;o da thịt anh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, anh ta kh&ocirc;ng chết. Sức mạnh của tia s&eacute;t x&eacute; toạc cơ bắp, khung xương v&agrave; kinh mạch của anh ta, cuối c&ugrave;ng g&acirc;y ra một cơn lốc xo&aacute;y l&ecirc;n trong v&ugrave;ng dantian của anh ta.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kết quả l&agrave;, ch&acirc;n kh&iacute; bị ph&aacute; hủy anh ta đột nhi&ecirc;n trải qua những biến đổi kỳ lạ v&agrave; kỳ qu&aacute;i. D&ograve;ng điện xoắn ốc bắt đầu c&oacute; h&igrave;nh dạng, cho đến khi n&oacute; tr&ocirc;ng giống như một con voi ma m&uacute;t hung h&atilde;n, hung tợn, gần giống như một con rồng trong sự hung dữ của n&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nếu ai đ&oacute; c&oacute; thể chứng kiến ​​những g&igrave; đang xảy ra b&ecirc;n trong anh ta, họ sẽ thấy thứ tr&ocirc;ng giống như một con voi ma m&uacute;t khổng lồ được tạo th&agrave;nh từ tia chớp, thổi k&egrave;n như đi&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tia chớp kh&ocirc;ng tan; n&oacute; đ&acirc;m xuy&ecirc;n qua c&acirc;y v&agrave;o Th&agrave;nh Qu&acirc;n, l&agrave;m cho c&acirc;y b&ugrave;ng ch&aacute;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cuối c&ugrave;ng, Th&agrave;nh Qu&acirc;n nh&igrave;n thấy một người nhỏ m&agrave;u v&agrave;ng trong tia chớp, người bay thẳng v&agrave;o tr&aacute;n của m&igrave;nh. Sau đ&oacute;, t&acirc;m tr&iacute; của anh ấy như đang phồng l&ecirc;n tr&ecirc;n bờ vực b&ugrave;ng nổ, rồi anh ấy ngất đi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Những người l&iacute;nh canh, những người đ&atilde; ra khỏi rừng v&agrave;o thời điểm n&agrave;y, tất cả đều ngạc nhi&ecirc;n. Họ ở đủ gần để c&oacute; thể nh&igrave;n thấy r&otilde; r&agrave;ng tia s&eacute;t đ&aacute;nh v&agrave;o c&acirc;y v&agrave; bắt n&oacute; bốc ch&aacute;y. V&agrave; họ thậm ch&iacute; c&oacute; thể nh&igrave;n thấy Th&agrave;nh Qu&acirc;n bị sụp xuống b&ugrave;n, dường như đ&atilde; chết.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ch&uacute;ng ta l&agrave;m g&igrave; b&acirc;y giờ?&quot; một trong những người bảo vệ n&oacute;i.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một người kh&aacute;c n&oacute;i: &ldquo;Hắn ta bị s&eacute;t đ&aacute;nh kh&ocirc;ng li&ecirc;n quan g&igrave; đến ch&uacute;ng t&ocirc;i.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Ngươi quay trở lại th&agrave;nh phố để b&aacute;o c&aacute;o sự việc với Luo Hun. Ngươi đ&atilde; phạm một tội &aacute;c kh&ocirc;ng thể tha thứ, nhưng d&ugrave; sao th&igrave; ngươi cũng đến từ một gia tộc gi&agrave;u c&oacute; v&agrave; quyền lực. Nếu ngươi chết như thế n&agrave;y v&agrave; vấn đề kh&ocirc;ng được xử l&yacute; đ&uacute;ng c&aacute;ch, n&oacute; c&oacute; thể g&acirc;y ra vấn đề lớn ở Yanhaven. &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một trong những vệ binh kh&aacute;c lập tức quay đầu chạy về th&agrave;nh phố.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, trước khi anh ta c&oacute; thể tiến th&ecirc;m v&agrave;i bước, anh ta nh&igrave;n thấy một b&oacute;ng người khổng lồ ph&iacute;a trước, mặc &aacute;o gi&aacute;p kim loại.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đổ mồ h&ocirc;i lạnh, t&ecirc;n bảo vệ ngồi phịch xuống b&ugrave;n. &quot;Luo Hun, thưa ng&agrave;i!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Bị s&eacute;t đ&aacute;nh chết&hellip;?&rdquo; Luo Hun lẩm bẩm, đ&ocirc;i mắt l&oacute;e s&aacute;ng như động vật khi đi đến chỗ Th&agrave;nh Qu&acirc;n. Đưa tay ra, truyền một luồng ch&acirc;n kh&iacute; v&agrave;o Th&agrave;nh Qu&acirc;n, chỉ thấy kh&ocirc;ng c&oacute; g&igrave; bất thường. Kinh mạch v&agrave; ch&acirc;n kh&iacute; của anh vẫn bị ph&aacute; hủy, v&agrave; cơ thể anh bị ch&aacute;y đen. &ldquo;Ngươi bị thương do bị s&eacute;t đ&aacute;nh, kinh mạch v&agrave; ch&acirc;n kh&iacute; của ngươi bị ph&aacute; hủy ho&agrave;n to&agrave;n. Nhưng ngươi vẫn c&ograve;n sống. &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;C&oacute; thật kh&ocirc;ng?&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay cả khi Luo Hun kiểm tra Th&agrave;nh Qu&acirc;n, một số người đ&agrave;n &ocirc;ng đ&atilde; sải bước l&ecirc;n, mặc những bộ quần &aacute;o được thiết kế ri&ecirc;ng sang trọng. Họ l&agrave; những người quan trọng đến từ Yanhaven, một trong số họ c&oacute; m&aacute;i t&oacute;c d&agrave;i m&agrave;u trắng, v&agrave; dường như l&agrave; quản gi&aacute;o của một trong những gia tộc trong th&agrave;nh phố. Sau khi kiểm tra Th&agrave;nh Qu&acirc;n, anh ta n&oacute;i, &ldquo;Ch&agrave;, b&acirc;y giờ ch&uacute;ng ta kh&ocirc;ng cần phải đưa t&ecirc;n đ&oacute; trở lại để thẩm vấn. V&agrave; ch&uacute;ng t&ocirc;i chắc chắn kh&ocirc;ng phải xử tử anh ta. Đưa anh ta trở lại Yang Clan, v&agrave; trong khi bạn ở đ&oacute;, h&atilde;y gửi cho họ tin nhắn n&agrave;y từ thẩm ph&aacute;n th&agrave;nh phố: bởi v&igrave; một t&ecirc;n du c&ocirc;n của Yang Clan đ&atilde; đ&aacute;nh cắp Vi&ecirc;n thuốc long ch&acirc;u, Yang Clan sẽ bồi thường đầy đủ, ngay cả khi họ phải b&aacute;n mọi thứ thuộc về cuối c&ugrave;ng. &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;V&acirc;ng thưa ng&agrave;i!&quot; C&aacute;c l&iacute;nh canh nắm lấy Th&agrave;nh Qu&acirc;n v&agrave; bắt đầu k&eacute;o anh ta trở lại Yanhaven.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&ecirc;m nhanh ch&oacute;ng tr&ocirc;i qua, chẳng mấy chốc đ&atilde; l&agrave; một ng&agrave;y nắng đẹp.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trận b&atilde;o dữ dội v&agrave;o đ&ecirc;m h&ocirc;m trước đ&atilde; ph&aacute; hủy rất nhiều m&ugrave;a m&agrave;ng v&agrave; gia s&uacute;c, v&agrave; d&ograve;ng s&ocirc;ng Yan h&ugrave;ng vĩ b&ecirc;n ngo&agrave;i th&agrave;nh phố đ&atilde; tr&agrave;n ngập. Nhưng b&acirc;y giờ, cơn mưa đ&atilde; qua đi, v&agrave; nhiệt độ đ&atilde; tăng l&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Gia tộc Yang, một trong những gia tộc gi&agrave;u c&oacute; v&agrave; quyền lực nhất ở Yanhaven, đ&atilde; ho&agrave;n to&agrave;n thất thế. Những người hầu nhốn nh&aacute;o tới lui, những l&iacute;nh canh đặc biệt cảnh gi&aacute;c cao độ, v&agrave; những c&ocirc; g&aacute;i hầu cận, hầu g&aacute;i v&agrave; những người hầu nữ lớn tuổi đang th&igrave; thầm với nhau ở mọi ng&oacute;c ng&aacute;ch của gia tộc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một b&agrave; l&atilde;o c&oacute; lưỡi sắc b&eacute;n hiện đang kể lại một c&aacute;ch sống động về những g&igrave; đ&atilde; xảy ra, v&agrave; mặc d&ugrave; b&agrave; cố gắng hạ giọng, nhưng kh&ocirc;ng thể n&agrave;o b&agrave; ta che được nụ cười nhếch m&eacute;p hả h&ecirc;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Một c&aacute;i g&igrave; đ&oacute; lớn sắp xảy ra cho Dương tộc của ch&uacute;ng ta, bạn c&oacute; nghe thấy kh&ocirc;ng? Đ&ecirc;m qua, con trai của L&atilde;nh ch&uacute;a thị tộc Yang Zhan l&agrave; Th&agrave;nh Qu&acirc;n đ&atilde; đ&aacute;nh cắp một kho b&aacute;u từ quan t&ograve;a th&agrave;nh phố. Anh ta bị đ&aacute;nh đến chết một nửa, Ch&acirc;n kh&iacute; bị t&ecirc; liệt v&agrave; bị tr&oacute;i v&agrave;o một c&aacute;i c&acirc;y. Rồi anh ta bị s&eacute;t đ&aacute;nh! Sau khi họ đưa anh ta về, anh ta nằm tr&ecirc;n giường cả đ&ecirc;m, kh&oacute; thở. Anh ta c&oacute; thể chết bất cứ l&uacute;c n&agrave;o&hellip;. &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tiếp theo n&oacute;i chuyện l&agrave; một trong những người hầu trung th&agrave;nh hơn của gia tộc. &quot;Đ&uacute;ng rồi. V&agrave; quan t&ograve;a th&agrave;nh phố ra tối hậu thư. Dương gia c&oacute; ba ng&agrave;y để trả lại c&aacute;i gi&aacute; của bảo vật bị đ&aacute;nh cắp, đồng nghĩa với việc Dương gia sẽ gần như ph&aacute; sản ho&agrave;n to&agrave;n&hellip;. &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhiều c&ocirc; g&aacute;i trẻ hầu cận ho&agrave;n to&agrave;n c&uacute;i đầu trước tin tức đ&oacute;. &ldquo;Gia tộc sắp ph&aacute; sản? L&agrave;m thế n&agrave;o ch&uacute;ng ta sẽ phục hồi? &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;C&ograve;n những gia tộc gi&agrave;u c&oacute; v&agrave; quyền lực kh&aacute;c th&igrave; sao? Chen, Wang, Hong v&agrave; Li? Họ chưa bao giờ th&iacute;ch Yang Clan của ch&uacute;ng t&ocirc;i, v&agrave; chắc chắn sẽ chớp thời cơ tấn c&ocirc;ng ch&uacute;ng t&ocirc;i khi ch&uacute;ng t&ocirc;i thất bại. &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Ừ, lần n&agrave;y Dương gia đang gặp rắc rối lớn. Th&agrave;nh Qu&acirc;n kh&ocirc;ng nghĩ g&igrave; về những g&igrave; m&igrave;nh đang l&agrave;m, v&agrave; cuối c&ugrave;ng đ&atilde; g&acirc;y ra một thảm họa lớn. Heh heh. Cha của anh ấy, Yang Zhan l&agrave; l&atilde;nh ch&uacute;a của gia tộc, nhưng t&ocirc;i c&aacute; rằng vị tr&iacute; của anh ấy sẽ kh&ocirc;ng ổn định lắm sau vụ n&agrave;y &rdquo;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Một người l&agrave;m việc cho thẩm ph&aacute;n th&agrave;nh phố n&oacute;i với t&ocirc;i rằng Th&agrave;nh Qu&acirc;n đ&atilde; l&agrave;m điều đ&oacute; v&igrave; một c&ocirc; g&aacute;i. Đ&oacute; l&agrave; l&yacute; do tại sao anh ta đ&aacute;nh cắp kho b&aacute;u. &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Bah! Những người chăn lụa như anh ta l&agrave; những t&ecirc;n v&ocirc; lại độc &aacute;c nhất trong tất cả. Họ lu&ocirc;n g&acirc;y ra vấn đề! &rdquo; Một trong những người hầu thậm ch&iacute; c&ograve;n đi xa đến mức n&oacute;i rằng, &ldquo;Thật tốt l&agrave; thuật năng lượng của anh ấy đ&atilde; bị t&ecirc; liệt. Nếu kh&ocirc;ng c&oacute; bất kỳ m&ocirc;n v&otilde; thuật n&agrave;o, anh ta sẽ ho&agrave;n to&agrave;n v&ocirc; dụng. &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một số người hầu hả h&ecirc; trước sự bất hạnh của Th&agrave;nh Qu&acirc;n, những người kh&aacute;c lo lắng về tương lai của gia tộc, v&agrave; một số thậm ch&iacute; c&ograve;n nguyền rủa Th&agrave;nh Qu&acirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b59d3e0b-7fff-e655-f379-8acc6aacb6f0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong khi đ&oacute;, trong một s&acirc;n ở giữa gia tộc, nơi nằm trong một m&ecirc; cung của c&aacute;c h&agrave;nh lang, một người đ&agrave;n &ocirc;ng cao lớn, v&oacute;c d&aacute;ng c&acirc;n đối trong trang phục dệt kim tuyến đang đứng nghe b&aacute;o c&aacute;o về t&igrave;nh h&igrave;nh.</span></span></p>\r\n<div>\r\n	&nbsp;</div>\r\n', 'Cà Di', '2020-09-25 11:17:57', 1, 0, 129),
(45, 'Chương 2 : Thu lợi từ thảm họa phần 2', 'chuong-2-thu-loi-tu-tham-hoa-phan-2', 12, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một đại phu hiện đang ph&aacute;t biểu. &ldquo;Lạy Ch&uacute;a, Thiếu gia Th&agrave;nh Qu&acirc;n đ&atilde; ổn định. Nhưng anh ấy bị bỏng gần như to&agrave;n bộ cơ thể. Kinh mạch của anh ta bị ph&aacute; hủy v&agrave; ch&acirc;n kh&iacute; của anh ta tan vỡ. V&otilde; c&ocirc;ng của hắn cũng đ&atilde; ho&agrave;n to&agrave;n t&ecirc; liệt! Anh ta sẽ bị t&agrave;n tật trong suốt phần đời c&ograve;n lại của m&igrave;nh! &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;V&otilde; c&ocirc;ng của con ta t&ecirc; liệt?&quot; Yang Zhan n&oacute;i. &quot;Ai đ&atilde; ph&aacute; hủy ch&acirc;n kh&iacute; của con ta ...?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">BANG!</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một c&uacute; đ&aacute;nh bằng l&ograve;ng b&agrave;n tay lớn đ&atilde; đập tan một chiếc b&agrave;n gần đ&oacute; th&agrave;nh từng mảnh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Những mảnh vỡ văng vẳng trong kh&ocirc;ng kh&iacute; như những b&oacute;ng ma kh&oacute;c l&oacute;c, nhiều mảnh văng xuống đất gần đ&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Dương gia c&oacute; thể b&ugrave; đắp cho Vi&ecirc;n thuốc long ch&acirc;u đ&atilde; mất,&rdquo; Yang Zhan tiếp tục bốc kh&oacute;i. &ldquo;Nhưng tại sao họ phải l&agrave;m t&ecirc; liệt ch&acirc;n kh&iacute; của con trai ta? Thậm ch&iacute; cắt đứt c&aacute;nh tay của con ta sẽ tốt hơn. Với một chi bị mất, nhưng ch&acirc;n kh&iacute; của con ta vẫn c&ograve;n nguy&ecirc;n vẹn, con ta vẫn c&oacute; thể trở th&agrave;nh một chuy&ecirc;n gia h&agrave;ng đầu. Với việc nguy&ecirc;n kh&iacute; đ&atilde; mất, con ta sẽ kh&ocirc;ng bao giờ c&oacute; thể hội tụ đủ ch&acirc;n kh&iacute;. &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&oacute; mặt cũng l&agrave; người quản l&yacute; gia tộc. &ldquo;Ch&uacute;a ơi, ch&uacute;ng ta đ&atilde; l&agrave;m những g&igrave; c&oacute; thể. Hiện ch&uacute;ng ta kh&ocirc;ng thể l&agrave;m g&igrave; được. Vấn đề quan trọng hiện tại l&agrave; thẩm ph&aacute;n th&agrave;nh phố đ&atilde; y&ecirc;u cầu bồi thường cho Vi&ecirc;n thuốc long ch&acirc;u. T&ocirc;i e rằng c&aacute;c trưởng l&atilde;o sẽ kh&ocirc;ng chỉ đứng nh&igrave;n trong khi gia tộc ph&aacute; sản. Ai đ&oacute; chắc chắn sẽ lợi dụng t&igrave;nh h&igrave;nh n&agrave;y để cố gắng nắm quyền&hellip;. &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Yang Zhan hừ lạnh một tiếng. &ldquo;H&atilde;y nhớ tại sao ta c&oacute; thể nắm giữ vị tr&iacute; l&atilde;nh ch&uacute;a thị tộc ngay từ đầu chứ? N&oacute; kh&ocirc;ng phải bằng &acirc;m mưu, kế hoạch v&agrave; x&acirc;y dựng li&ecirc;n minh. Đ&oacute; l&agrave; bởi v&igrave; ch&acirc;n kh&iacute; v&agrave; nguy&ecirc;n kh&iacute; của ta l&agrave; kh&ocirc;ng g&igrave; s&aacute;nh được. V&agrave; đ&oacute; l&agrave; c&aacute;ch mọi thứ sẽ vẫn tồn tại. H&atilde;y xem c&oacute; ai trong số họ đủ mạnh để đưa t&ocirc;i ra ngo&agrave;i kh&ocirc;ng. Th&ocirc;ng b&aacute;o cho c&aacute;c trưởng l&atilde;o rằng ch&uacute;ng ta sẽ tổ chức họp gia tộc sau ba ng&agrave;y. Ch&uacute;ng ta sẽ trao đổi mọi thứ với c&aacute;c ph&acirc;n đ&agrave; v&agrave; t&igrave;m ra những việc cần l&agrave;m. Thẩm ph&aacute;n th&agrave;nh phố đ&atilde; cho ch&uacute;ng t&ocirc;i đủ thời gian để quy&ecirc;n g&oacute;p số tiền cần thiết &rdquo;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;V&acirc;ng, thưa ng&agrave;i,&rdquo; người quản l&yacute; gật đầu n&oacute;i. Anh ta quay người rời đi, sau đ&oacute; dừng lại v&agrave; hỏi: &ldquo;Thiếu gia Th&agrave;nh Qu&acirc;n th&igrave; sao? Coi như rắc rối do hắn g&acirc;y ra, c&aacute;c trưởng l&atilde;o nhất định sẽ kh&ocirc;ng bu&ocirc;ng tha cho hắn. &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;V&otilde; c&ocirc;ng của anh ấy đ&atilde; bị t&ecirc; liệt v&agrave; anh ấy đ&atilde; bị s&eacute;t đ&aacute;nh!&quot; Yang Zhan tức giận n&oacute;i. &ldquo;Ngay cả thẩm ph&aacute;n th&agrave;nh phố cũng kh&ocirc;ng quan t&acirc;m đến con ta. Trưởng l&atilde;o định l&agrave;m g&igrave;? Anh ta đ&atilde; bị xử tử chưa? Nếu c&oacute; ai đ&oacute; d&aacute;m đề nghị đặt một tay l&ecirc;n con trai t&ocirc;i, t&ocirc;i sẽ kh&ocirc;ng quan t&acirc;m đến vấn đề n&agrave;y. Một điều nữa: T&ocirc;i sẽ viết một l&aacute; thư m&agrave; t&ocirc;i muốn gửi cho d&igrave; của Th&agrave;nh Qu&acirc;n ở Viện Demi-Immortal. &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người quản l&yacute; mắt s&aacute;ng l&ecirc;n. &quot;Đ&uacute;ng! L&agrave;m sao t&ocirc;i c&oacute; thể qu&ecirc;n rằng ch&uacute;ng t&ocirc;i c&oacute; Thiếu nữ ở b&ecirc;n? &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thiếu nữ m&agrave; anh ta nhắc đến l&agrave; em g&aacute;i nu&ocirc;i của Yang Zhan, v&agrave; do đ&oacute; l&agrave; d&igrave; của Th&agrave;nh Qu&acirc;n. C&ocirc; l&agrave; th&agrave;nh vi&ecirc;n của tổ chức độc đo&aacute;n số một tr&ecirc;n Lục địa gi&agrave;u c&oacute;, Viện Demi-Immortal, nơi mang lại cho c&ocirc; một th&acirc;n phận rất kh&aacute;c so với trước đ&acirc;y. C&ocirc; đ&atilde; rời khỏi Dương tộc mười năm, v&agrave; t&agrave;i năng xuất ch&uacute;ng. Rốt cuộc, c&ocirc; ấy đ&atilde; được một trong những trưởng l&atilde;o của Demi-Immortal Institute hướng dẫn tu luyện từ khi c&ograve;n rất nhỏ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;đại phu Cao, Qu&acirc;n bị thương nặng như thế n&agrave;o?&rdquo; Yang Zhan hỏi. &ldquo;C&oacute; loại thuốc n&agrave;o c&oacute; thể đ&aacute;nh thức con ta kh&ocirc;ng? Y thuật của đại phu được n&oacute;i đến ở khắp mọi nơi ở Yanhaven. Đại phu phải biết c&aacute;ch chữa trị vết thương cho con ta &rdquo;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;T&ocirc;i đ&atilde; b&ocirc;i thuốc mỡ,&rdquo; đại phu trả lời, &ldquo;v&agrave; sử dụng ch&acirc;n kh&iacute; của m&igrave;nh để thực hiện một liệu ph&aacute;p xoa b&oacute;p y thuật. Anh ấy sẽ ổn định trở lại trong v&agrave;i ng&agrave;y tới. Nội tạng của anh ấy kh&ocirc;ng bị tổn thương, chỉ l&agrave; kinh mạch v&agrave; ch&acirc;n kh&iacute; của anh ấy. Tất nhi&ecirc;n, sau n&agrave;y anh ấy sẽ kh&ocirc;ng thể tu luyện nữa &rdquo;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Tặng mười lạng v&agrave;ng cho Thần đại phu Cao như một phần thưởng,&rdquo; Yang Zhan xua tay n&oacute;i. &ldquo;Khi Qu&acirc;n thức dậy, t&ocirc;i sẽ gọi lại cho đại phu.&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave;ng được coi như tiền tệ trong tất cả c&aacute;c cửa h&agrave;ng v&agrave; chợ ở Lục địa gi&agrave;u c&oacute;. Ch&uacute;ng cũng c&oacute; thể được sử dụng trong qu&aacute; tr&igrave;nh tu luyện năng lượng thuật để thu thập năng lượng ch&acirc;n ch&iacute;nh, nu&ocirc;i dưỡng kinh mạch v&agrave; mở rộng ch&acirc;n kh&iacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;V&acirc;ng thưa ng&agrave;i&hellip;.&quot; người quản gia gi&agrave; n&oacute;i rồi dẫn đại phu đi.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Yang Zhan vẫn ở lại. Nắm chặt tay th&agrave;nh nắm đấm, anh th&igrave; thầm, &ldquo;Qu&acirc;n, mẹ ngươi về sớm qu&aacute;. V&agrave; b&acirc;y giờ ch&uacute;ng t&ocirc;i đang đối mặt với một số vận rủi lớn. Ta sẽ l&agrave;m mọi thứ c&oacute; thể nghĩ đến để kh&ocirc;i phục ch&acirc;n kh&iacute; của ngươi v&agrave; đảm bảo ngươi c&oacute; thể tu luyện trở lại. Trong thế giới m&agrave; ch&uacute;ng ta đang sống, kh&ocirc;ng phải l&agrave; một lựa chọn để trở n&ecirc;n yếu đuối&hellip;. &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ở một địa điểm kh&aacute;c s&acirc;u trong tộc Yang, Th&agrave;nh Qu&acirc;n đ&atilde; thức giấc v&agrave; nằm y&ecirc;n lặng tr&ecirc;n giường.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Căn ph&ograve;ng được trang ho&agrave;ng bởi đồ nội thất bằng nhung đỏ v&agrave; những bức tranh cổ đắt tiền. Thậm ch&iacute; c&oacute; chiếc lư hương bằng đồng trang tr&iacute; h&igrave;nh r&ugrave;a v&agrave; hạc, đốt loại hương rất đắt tiền. Mức độ gi&agrave;u c&oacute; được trưng b&agrave;y thật đ&aacute;ng kinh ngạc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&ecirc;n ngo&agrave;i trời n&oacute;ng, nhưng một l&agrave;n gi&oacute; m&aacute;t lạnh lướt qua căn ph&ograve;ng, nguồn gốc của n&oacute; l&agrave; c&aacute;i ao lớn phủ sương m&ugrave; ở đ&oacute;. C&acirc;y cối b&ecirc;n cạnh ao tạo b&oacute;ng m&aacute;t, v&agrave; cũng c&oacute; rất nhiều tảng băng được đặt trong khu vực để l&agrave;m m&aacute;t th&ecirc;m. B&ecirc;n cạnh cửa ra v&agrave;o l&agrave; một c&ocirc; g&aacute;i hầu trẻ khoảng mười một hay mười hai tuổi, đầu vẫn tiếp tục nhấp nh&ocirc; khi c&ocirc; cố gắng ngủ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Th&agrave;nh Qu&acirc;n đ&atilde; kh&ocirc;ng c&ograve;n đau nữa. Khi nằm đ&oacute;, anh ta cố gắng x&aacute;c định xem tia s&eacute;t đ&atilde; g&acirc;y ra cho m&igrave;nh điều g&igrave;. Khi nhắm mắt lại, anh c&oacute; thể thấy thứ tr&ocirc;ng giống như một con voi ma m&uacute;t bằng nắm tay được tạo th&agrave;nh từ tia chớp, đang nằm y&ecirc;n trong ch&acirc;n kh&iacute; của anh. Hầu như bất kỳ k&iacute;ch th&iacute;ch b&ecirc;n ngo&agrave;i n&agrave;o cũng c&oacute; thể khiến voi ma m&uacute;t đột ngột thu nhỏ lại cho đến khi n&oacute; chỉ c&ograve;n l&agrave; một chấm nhỏ, đ&oacute; l&agrave; một điều tốt, bởi v&igrave; bất kỳ chuy&ecirc;n gia mạnh mẽ n&agrave;o đ&atilde; kiểm tra n&oacute; sẽ kh&ocirc;ng nhận thấy điều g&igrave; bất thường.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Th&agrave;nh Qu&acirc;n c&oacute; thể cảm nhận được rằng con voi ma m&uacute;t s&eacute;t chứa đựng sức mạnh hủy diệt cực lớn, sức mạnh lớn đến mức, nếu anh ta c&oacute; thể ph&oacute;ng n&oacute; ra trong một cuộc tấn c&ocirc;ng bằng c&aacute;ch n&agrave;o đ&oacute;, n&oacute; c&oacute; thể sẽ ph&aacute; hủy to&agrave;n bộ dinh thự anh ta đang ở.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh ta đ&atilde; bị s&eacute;t đ&aacute;nh, nhưng kh&ocirc;ng chết; thay v&agrave;o đ&oacute;, d&ograve;ng điện đi v&agrave;o anh ta v&agrave; c&oacute; h&igrave;nh dạng của một con voi ma m&uacute;t v&agrave; một con th&uacute;. Cho d&ugrave; Th&agrave;nh Qu&acirc;n xem x&eacute;t t&igrave;nh h&igrave;nh như thế n&agrave;o, anh ta kh&ocirc;ng thể đưa ra bất kỳ lời giải th&iacute;ch n&agrave;o về những g&igrave; đang xảy ra.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tại một thời điểm, Th&agrave;nh Qu&acirc;n lưu chuyển ch&acirc;n kh&iacute; của m&igrave;nh, v&agrave; kim quan xuất hiện tr&ecirc;n tr&aacute;n của anh ta.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau đ&oacute;, imp n&oacute;i v&agrave;o t&acirc;m tr&iacute; anh ta, bằng một giọng n&oacute;i của stentorian m&agrave; kh&ocirc;ng ai kh&aacute;c c&oacute; thể nghe thấy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-966f15c2-7fff-3835-6b2e-3a44be146293\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Kh&ocirc;ng th&agrave;nh c&ocirc;ng, ph&aacute; vỡ để t&igrave;m thấy. Dưới trời v&ocirc; song&hellip;. &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n', 'Cà Di', '2020-09-25 11:18:18', 1, 0, 198);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(46, 'Chương 1: Chuyển sinh hệ thống chậm phát triển Phần 2', 'chuong-1-chuyen-sinh-he-thong-cham-phat-trien-phan-2-1', 14, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&acirc;y kh&ocirc;ng phải l&agrave; lần đầu ti&ecirc;n anh ấy thử gọi hệ thống bằng nhiều t&ecirc;n kh&aacute;c nhau như bố, &ocirc;ng ngoại, v.v. Anh ta thường bắt đầu gọi hệ thống bằng những c&aacute;i t&ecirc;n kh&aacute;c như ch&aacute;u trai, ch&aacute;u nội, v.v. sau khi bị bỏ qua. Mặc d&ugrave; vậy, hệ thống vẫn im lặng trong suốt những năm qua.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi anh ấy đọc về c&aacute;c nh&acirc;n vật ch&iacute;nh trong tiểu thuyết, hệ thống của họ sở hữu tr&iacute; th&ocirc;ng minh hoặc tr&iacute; th&ocirc;ng minh nh&acirc;n tạo, v&agrave; c&oacute; t&iacute;nh c&aacute;ch kh&aacute;c nhau: họ c&oacute; thể xa c&aacute;ch, ki&ecirc;u h&atilde;nh, lạnh l&ugrave;ng, bề ngo&agrave;i tốt bụng nhưng nội t&acirc;m xấu xa, đ&aacute;ng y&ecirc;u, n&oacute;i nhiều hoặc thậm ch&iacute; l&agrave; đ&aacute;ng khinh. Khi c&aacute;c nh&acirc;n vật ch&iacute;nh kh&ocirc;ng c&oacute; g&igrave; để l&agrave;m, họ c&oacute; thể tr&ograve; chuyện tr&ecirc;n hệ thống của m&igrave;nh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng hệ thống n&agrave;y m&agrave; anh ta c&oacute; được kh&ocirc;ng phải l&agrave; tr&iacute; th&ocirc;ng minh nh&acirc;n tạo. Tr&ecirc;n thực tế, đ&acirc;y l&agrave; một cỗ m&aacute;y chậm ph&aacute;t triển. Nếu kh&ocirc;ng, thực sự kh&ocirc;ng c&oacute; lời giải th&iacute;ch n&agrave;o kh&aacute;c cho việc thiếu phản hồi từ hệ thống n&agrave;y.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave; cứ như thế, Luke lớn l&ecirc;n một c&aacute;ch kh&ocirc;ng b&igrave;nh thường, học h&agrave;nh cho đến ng&agrave;y cuối c&ugrave;ng cũng đến ng&agrave;y lễ tốt nghiệp cấp ba của m&igrave;nh. Đ&oacute; cũng l&agrave; ng&agrave;y anh tr&ograve;n 18 tuổi.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay khi anh ấy đang chuẩn bị trong ph&ograve;ng của m&igrave;nh tr&ecirc;n lầu, chuẩn bị cho vũ hội, hệ thống đột nhi&ecirc;n gửi cho anh ấy một tin nhắn: Người chủ tr&igrave; đ&atilde; được ph&aacute;t hiện l&agrave; đ&atilde; đến tuổi trưởng th&agrave;nh. Hệ thống ch&iacute;nh thức được k&iacute;ch hoạt v&agrave; qu&aacute; tr&igrave;nh đồng bộ h&oacute;a sẽ bắt đầu sau 10 gi&acirc;y.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;C&aacute;i qu&aacute;i g&igrave; vậy?&quot; Luke đ&atilde; c&oacute; thể k&ecirc;u l&ecirc;n.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau đ&oacute;, trước khi anh c&oacute; thể nghĩ về bất cứ điều g&igrave; kh&aacute;c, cơn đau dữ dội ập đến n&atilde;o anh. Đ&ocirc;i mắt của anh ấy ngay lập tức tối sầm lại khi cơ chế tự bảo vệ của cơ thể khởi động từ cơn đau dữ dội, khiến anh ấy bất tỉnh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Điều đầu ti&ecirc;n Robert nh&igrave;n thấy sau khi l&ecirc;n lầu l&agrave; Luke nằm bất tỉnh tr&ecirc;n mặt đất.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tiếp đ&oacute;, Robert đưa anh đến bệnh viện, v&agrave; sau một ng&agrave;y h&ocirc;n m&ecirc;, Luke tỉnh dậy.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hiện tại, chỉ c&oacute; chị g&aacute;i của anh, Claire, đang ở đ&acirc;y v&agrave; c&ocirc; ấy đang ngủ say. Đ&acirc;y l&agrave; thời điểm th&iacute;ch hợp để anh ấy tiết lộ &ldquo;giải thưởng&rdquo; của m&igrave;nh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&aacute;i gọi l&agrave; giải thưởng l&agrave; hệ thống.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Suốt những năm qua, anh cảm thấy m&igrave;nh giống như một người biết rằng m&igrave;nh được thừa hưởng khối t&agrave;i sản kếch x&ugrave;, nhưng m&atilde;i nhiều năm sau mới chạm v&agrave;o được.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&acirc;ng, theo một c&aacute;ch n&agrave;o đ&oacute;, đ&oacute; l&agrave; cảm gi&aacute;c của anh ấy.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hệ thống n&agrave;y giống với một di sản m&agrave; kiếp trước của anh đ&atilde; để lại cho anh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong suốt 13 năm d&agrave;i đ&oacute;, Luke đ&atilde; chờ đợi một điều n&agrave;y.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave; sau khi tỉnh dậy, cậu cuối c&ugrave;ng đ&atilde; hiểu tại sao hệ thống sẽ chỉ k&iacute;ch hoạt khi cậu trở th&agrave;nh người lớn.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&oacute; l&agrave; bởi v&igrave; hệ thống cần đồng bộ h&oacute;a với linh hồn của anh ta để gắn kết cả hai lại với nhau. Trước khi trưởng th&agrave;nh, cơ thể của anh ta, hay ch&iacute;nh x&aacute;c l&agrave;, bộ n&atilde;o của anh ta sẽ kh&ocirc;ng thể chịu được c&uacute; sốc từ qu&aacute; tr&igrave;nh đồng bộ h&oacute;a - n&oacute; c&oacute; thể dễ d&agrave;ng giết chết anh ta.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke ho&agrave;n to&agrave;n kh&ocirc;ng n&oacute;i n&ecirc;n lời khi anh rủa thầm trong l&ograve;ng. &ldquo;Bạn c&oacute; thể cho t&ocirc;i biết sớm hơn nếu bạn sẽ đồng bộ h&oacute;a với t&ocirc;i? Giống như c&oacute; thể b&aacute;o trước cho t&ocirc;i một ng&agrave;y? &rdquo; Hệ thống đ&atilde; thực sự cho anh ta đếm ngược đơn giản 10 gi&acirc;y trước khi trực tiếp bắt đầu đồng bộ h&oacute;a.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave; do đ&oacute;, kh&ocirc;ng chỉ anh ấy phải nhập viện, buổi dạ hội tốt nghiệp của anh ấy cũng bị hủy hoại.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Điều tồi tệ hơn nữa l&agrave; thực tế l&agrave; bạn g&aacute;i hiện tại của anh, Jimena, đ&atilde; muốn khi&ecirc;u vũ với anh. Sau khi để c&ocirc; trong t&igrave;nh trạng lảo đảo, anh tự hỏi liệu con m&egrave;o hung dữ đ&oacute; c&oacute; c&agrave;o anh đến chết kh&ocirc;ng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, đ&oacute; l&agrave; điều m&agrave; anh ấy phải lo lắng sau n&agrave;y. Hiện tại, anh vẫn tập trung v&agrave;o hệ thống.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng sự ngu ngốc của hệ thống đ&atilde; vượt qua cả sự mong đợi của anh ta. Sau khi k&iacute;ch hoạt ch&iacute;nh thức, ngay cả sau khi đồng bộ h&oacute;a, hệ thống vẫn giả vờ như n&oacute; kh&ocirc;ng tồn tại!</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dữ liệu m&agrave; anh ấy nhận được từ hệ thống kh&ocirc;ng c&oacute; nhiều như trang k&yacute; tự, bảng điều khiển hay cửa sổ - đ&oacute; chỉ l&agrave; dữ liệu thuần t&uacute;y c&oacute; thể nh&igrave;n thấy được, kh&ocirc;ng c&oacute; mẹo hay gợi &yacute; n&agrave;o kh&aacute;c cho anh ấy.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke kh&ocirc;ng khỏi lầm bầm trong l&ograve;ng, &quot;Hệ thống, &iacute;t nhất ngươi cũng n&ecirc;n c&oacute; một c&aacute;i t&ecirc;n, đ&uacute;ng kh&ocirc;ng?&quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đột nhi&ecirc;n, hệ thống đưa ra một phản ứng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hệ thống th&aacute;m tử si&ecirc;u dũng cảm!</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Được viết tắt l&agrave;: Super Detective System!</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave; kh&ocirc;ng c&oacute; g&igrave; kh&aacute;c.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke thậm ch&iacute; kh&ocirc;ng thể tức giận với hệ thống được nữa.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh b&igrave;nh tĩnh lại v&agrave; bắt đầu nghi&ecirc;n cứu hệ thống.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mặc d&ugrave; anh ta đ&atilde; nguyền rủa hệ thống l&agrave; v&ocirc; dụng v&agrave; thậm ch&iacute; kh&ocirc;ng c&oacute; giao diện người d&ugrave;ng, v.v., nhưng vẫn c&oacute; một c&aacute;i g&igrave; đ&oacute; hữu &iacute;ch về n&oacute;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">N&oacute;i một c&aacute;ch logic, người ta c&oacute; thể suy ra rằng v&igrave; hệ thống đ&atilde; cho anh ta một số con số, n&ecirc;n thật hợp l&yacute; khi những con số đ&oacute; c&oacute; thể được tăng l&ecirc;n.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Từ ba thuộc t&iacute;nh ch&iacute;nh - sức mạnh, sự kh&eacute;o l&eacute;o v&agrave; tr&iacute; lực - Luke c&oacute; thể đ&aacute;nh gi&aacute; rằng kh&ocirc;ng c&oacute; g&igrave; kh&aacute;c thường về c&aacute;c th&ocirc;ng số m&agrave; hệ thống d&ugrave;ng để đo lường một con người.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave; hệ thống cũng đ&atilde; ngầm đồng &yacute; rằng đối với một người b&igrave;nh thường, gi&aacute; trị trung b&igrave;nh cho cả ba thuộc t&iacute;nh l&agrave; 10. Đối với Luke, anh ta c&oacute; sức mạnh l&agrave; 12, kh&eacute;o l&eacute;o l&agrave; 11 v&agrave; tr&iacute; lực l&agrave; 12.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">N&oacute;i c&aacute;ch kh&aacute;c, anh ấy hơi tr&ecirc;n mức trung b&igrave;nh. Nhưng đ&oacute; c&oacute; lẽ l&agrave; do phần lớn người d&acirc;n ở đ&acirc;y lao động ch&acirc;n tay trong khi học sinh ở đ&acirc;y th&iacute;ch tập thể dục.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bản th&acirc;n Luke cũng đ&atilde; tham gia v&agrave;o mốt v&agrave; thỉnh thoảng sẽ theo ch&acirc;n những người nghiện thể h&igrave;nh từ lớp học của m&igrave;nh đến ph&ograve;ng tập.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tất nhi&ecirc;n, với những người đ&oacute;, thể h&igrave;nh l&agrave; một niềm đam m&ecirc;. Với anh, anh chỉ chơi bời. V&igrave; vậy, sức mạnh của anh chỉ l&agrave; 12, một con số chỉ tr&ecirc;n mức trung b&igrave;nh một ch&uacute;t.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đối với sự kh&eacute;o l&eacute;o của 11, đ&oacute; c&oacute; thể l&agrave; do anh ta chưa trải qua bất kỳ kh&oacute;a đ&agrave;o tạo chuy&ecirc;n ng&agrave;nh n&agrave;o. Như vậy, anh chỉ hơn người thường 1 điểm.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đối với sức mạnh tinh thần của 12 tuổi, anh ta đ&atilde; hiểu điều đ&oacute; nghĩa l&agrave; g&igrave;. &ldquo;T&acirc;m thần&rdquo; chắc chắn sẽ li&ecirc;n quan đến bộ n&atilde;o của anh ta, hay n&oacute;i ch&iacute;nh x&aacute;c l&agrave;, với tốc độ m&agrave; bộ n&atilde;o của anh ta hoạt động.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kh&ocirc;ng c&oacute; thuộc t&iacute;nh n&agrave;o trong số ba thuộc t&iacute;nh ch&iacute;nh cao hơn nhiều so với hai thuộc t&iacute;nh c&ograve;n lại, nhưng kh&ocirc;ng c&oacute; thuộc t&iacute;nh n&agrave;o đặc biệt yếu hơn. Anh ấy đ&atilde; tự hỏi liệu hệ thống c&oacute; thể gi&uacute;p cải thiện những thuộc t&iacute;nh n&agrave;y hay kh&ocirc;ng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tiếp theo trong danh s&aacute;ch l&agrave; cấp độ, kinh nghiệm v&agrave; t&iacute;n dụng, cả ba đều bằng kh&ocirc;ng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Điều đ&oacute; cho thấy anh ta c&oacute; một cấp độ, v&agrave; anh ta c&oacute; thể nhận được điểm kinh nghiệm v&agrave; t&iacute;n dụng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Về việc anh ta cần bao nhi&ecirc;u điểm kinh nghiệm để l&ecirc;n cấp, v&agrave; l&agrave;m g&igrave; với số điểm t&iacute;n dụng, anh ta c&oacute; thể từ từ t&iacute;nh to&aacute;n trong tương lai. &Iacute;t nhất, b&acirc;y giờ anh ta biết rằng hệ thống kh&ocirc;ng ho&agrave;n to&agrave;n v&ocirc; gi&aacute; trị.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi d&agrave;nh nửa ng&agrave;y để nghi&ecirc;n cứu n&oacute;, Luke bắt đầu thử nghiệm với hệ thống.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Hệ thống chủ nh&acirc;n, c&oacute; thể cho ta phản ứng sao? Trả lời t&ocirc;i, l&agrave;m thế n&agrave;o t&ocirc;i c&oacute; thể tăng cấp?</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Kinh nghiệm? Tất nhi&ecirc;n t&ocirc;i biết điều đ&oacute; l&agrave; nhờ kinh nghiệm! L&agrave;m thế n&agrave;o để t&ocirc;i c&oacute; được kinh nghiệm?</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Sứ mệnh? Nhiệm vụ ở đ&acirc;u?</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Đăng! Bạn chỉ cho t&ocirc;i xem nhiệm vụ sau khi t&ocirc;i y&ecirc;u cầu? &quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&aacute;c th&iacute; nghiệm lặp đi lặp lại của Luke cuối c&ugrave;ng đ&atilde; thu được phản hồi từ hệ thống v&agrave; cuối c&ugrave;ng anh ấy đ&atilde; nhận được th&ocirc;ng tin bổ sung từ hệ thống. Tất nhi&ecirc;n, vẫn kh&ocirc;ng c&oacute; giao diện người d&ugrave;ng hoặc bất cứ điều g&igrave; tương tự.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhiệm vụ: Ch&iacute;nh thức trở th&agrave;nh cảnh s&aacute;t</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thời hạn: Một th&aacute;ng</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Phần thưởng: 10 điểm kinh nghiệm, 10 điểm t&iacute;n dụng</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi Luke nhận nhiệm vụ, anh cảm thấy v&ocirc; c&ugrave;ng x&uacute;c động, đến mức su&yacute;t rơi nước mắt. &quot;Cảm ơn hệ thống!&quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thật vậy, hệ thống n&agrave;y kh&ocirc;ng phải l&agrave; v&ocirc; dụng!</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thật vậy, anh ấy c&oacute; thể kiếm được điểm kinh nghiệm v&agrave; t&iacute;n dụng!</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng sau một thời gian ngắn, Luke phấn kh&iacute;ch ch&igrave;m v&agrave;o suy nghĩ.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh đ&atilde; đọc rất nhiều tiểu thuyết, rất nhiều tiểu thuyết li&ecirc;n quan đến nh&acirc;n vật ch&iacute;nh c&oacute; hệ thống.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đặt c&aacute;c chi tiết cụ thể sang một b&ecirc;n, c&aacute;c hệ thống thường c&oacute; thể được t&aacute;ch th&agrave;nh một v&agrave;i loại.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Loại hệ thống đầu ti&ecirc;n l&agrave; loại b&iacute; ẩn, nơi người d&ugrave;ng sẽ được cung cấp c&aacute;c kỹ năng ma thuật, v&otilde; thuật, s&aacute;ch hướng dẫn tu luyện bất tử hoặc thậm ch&iacute; l&agrave; si&ecirc;u năng lực.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Loại hệ thống thứ hai l&agrave; loại c&ocirc;ng nghệ, nơi người d&ugrave;ng sẽ được cung cấp vũ kh&iacute;, tiện &iacute;ch, cường h&oacute;a gen, thiết gi&aacute;p hạm giữa c&aacute;c v&igrave; sao, v.v.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Loại thứ ba l&agrave; hệ thống thế giới thực, li&ecirc;n quan đến những cuốn s&aacute;ch y&ecirc;u cầu c&aacute;c nh&acirc;n vật ch&iacute;nh trở th&agrave;nh si&ecirc;u sao, nh&agrave; khoa học, tội phạm, anh h&ugrave;ng c&ocirc;ng l&yacute;, v.v.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Loại thứ ba đ&ocirc;i khi sẽ bị trộn lẫn với loại thứ nhất v&agrave; thứ hai, nhưng trọng t&acirc;m ch&iacute;nh vẫn l&agrave; thế giới thực.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Điều Luke muốn nhất l&agrave; kiểu đầu ti&ecirc;n.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Phần thưởng m&agrave; anh ta đạt được từ kiểu đầu ti&ecirc;n sẽ lu&ocirc;n hấp dẫn nhất.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lựa chọn thứ hai của anh ta sẽ l&agrave; loại thứ hai, nơi anh ta c&oacute; thể nhận được phần thưởng c&ocirc;ng nghệ. Rốt cuộc, khi c&ocirc;ng nghệ đạt đến mức đủ cao, n&oacute; c&oacute; thể kh&ocirc;ng yếu hơn phần thưởng m&agrave; người ta c&oacute; thể nhận được từ loại hệ thống đầu ti&ecirc;n.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave; kiểu thứ ba&hellip; l&agrave; lựa chọn cuối c&ugrave;ng của anh ấy.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave; nếu kh&ocirc;ng c&oacute; lựa chọn n&agrave;o kh&aacute;c ngo&agrave;i việc chọn loại thứ ba, anh ta kh&ocirc;ng ngại c&oacute; được một hệ thống y&ecirc;u cầu anh ta phải l&agrave; một si&ecirc;u sao, một t&aacute;c giả si&ecirc;u hạng, một nh&agrave; bi&ecirc;n kịch si&ecirc;u hạng hoặc một si&ecirc;u mưu đồ. Tất cả đều l&agrave; những lựa chọn c&oacute; thể chấp nhận được.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hoặc trở th&agrave;nh một nh&agrave; khoa học, t&iacute;ch lũy t&agrave;i sản từ những ph&aacute;t minh của m&igrave;nh. Đ&oacute; cũng sẽ l&agrave; một lựa chọn tốt.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-05338051-7fff-5fc8-f1fa-328dbee4a4b4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">===================</span></span></p>\r\n', 'Cà Di', '2020-10-05 04:58:53', 1, 0, 286);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(47, 'Chương 2: Vụ điều tra của cảnh sát, Dấu son môi, và mọi thứ đều lớn hơn', 'chuong-2-vu-djieu-tra-cua-canh-sat-dau-son-moi-va-moi-thu-djeu-lon-hon-1', 14, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bất kể anh trở th&agrave;nh si&ecirc;u sao, si&ecirc;u t&aacute;c giả hay &ocirc;ng tr&ugrave;m c&ocirc;ng nghệ, tất cả đều c&oacute; nghĩa l&agrave; một số lượng lớn phụ nữ thượng lưu sẽ lao v&agrave;o &ocirc;m anh. Anh kh&ocirc;ng c&ograve;n cần phải lo lắng về việc thiếu bạn g&aacute;i.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tr&ecirc;n thực tế, l&agrave; một người chiến thắng trong cuộc sống, anh ấy thậm ch&iacute; sẽ kh&ocirc;ng cần bạn g&aacute;i. Nhiều phụ nữ sẵn s&agrave;ng đ&ugrave;a giỡn với anh ấy m&agrave; kh&ocirc;ng cần anh ấy nhấc ng&oacute;n tay.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng&hellip; Hệ thống th&aacute;m tử si&ecirc;u hạng? V&agrave; nhiệm vụ đầu ti&ecirc;n để anh ch&iacute;nh thức trở th&agrave;nh cảnh s&aacute;t?</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh ấy đ&atilde; rất buồn v&igrave; điều đ&oacute;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tại sao? Bởi v&igrave; Robert l&agrave; một sĩ quan, cảnh s&aacute;t trưởng của thị trấn m&agrave; họ sống.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kh&ocirc;ng, cảnh s&aacute;t trưởng kh&ocirc;ng phải l&agrave; Robert.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&aacute;i gọi l&agrave; cảnh s&aacute;t trưởng &aacute;m chỉ người thực thi ph&aacute;p luật của thị trấn, hay n&oacute;i c&aacute;ch kh&aacute;c, một người n&agrave;o đ&oacute; tương tự như cảnh s&aacute;t trưởng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Việc thực thi ph&aacute;p luật ở những thị trấn n&agrave;y hơi kh&aacute;c so với sở cảnh s&aacute;t của c&aacute;c th&agrave;nh phố lớn hơn. Cả hai đều l&agrave; cơ quan thực thi ph&aacute;p luật, nhưng sự thật th&igrave; cảnh s&aacute;t trưởng được trả tiền từ t&uacute;i ri&ecirc;ng của cư d&acirc;n, v&agrave; theo c&aacute;ch thức l&agrave; cảnh s&aacute;t tư nh&acirc;n.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&agrave; người đ&atilde; sống với Robert hơn 10 năm, Luke cực kỳ r&otilde; r&agrave;ng về số tiền m&agrave; một cảnh s&aacute;t ở một thị trấn nhỏ kiếm được.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Robert chỉ kiếm được khoảng 54.000 USD h&agrave;ng năm, v&agrave; thậm ch&iacute; đ&oacute; c&ograve;n l&agrave; số tiền trước thuế. V&agrave; anh ấy l&agrave; trưởng ph&ograve;ng. Đối với những người được xếp hạng thấp hơn anh ta, lương h&agrave;ng năm của họ c&oacute; thể xuống tới 32.000 USD.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay cả với tiền lương của Robert, anh ấy cũng hầu như kh&ocirc;ng kiếm đủ để nu&ocirc;i cả gia đ&igrave;nh họ.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">May mắn thay, Texas c&oacute; một số loại thuế thấp nhất Hoa Kỳ v&agrave; chi ph&iacute; sinh hoạt ở đ&acirc;y cũng kh&aacute; thấp. Ngo&agrave;i ra, Catherine c&ograve;n l&agrave;m gi&aacute;o vi&ecirc;n. V&igrave; vậy, gia đ&igrave;nh Robert kh&ocirc;ng phải sống trong cảnh ngh&egrave;o đ&oacute;i.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave; b&acirc;y giờ, c&aacute;i hệ thống khốn nạn n&agrave;y thực sự bảo Luke trở th&agrave;nh cảnh s&aacute;t? Đ&oacute; kh&ocirc;ng phải l&agrave; một c&aacute;i bẫy?</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&igrave; đ&acirc;y l&agrave; Hệ thống Th&aacute;m tử Si&ecirc;u hạng, tại sao anh ta kh&ocirc;ng thể đi v&agrave; trở th&agrave;nh một th&aacute;m tử tư hay g&igrave; đ&oacute;?</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&agrave; một th&aacute;m tử tư, nếu l&agrave;m tốt th&igrave; c&oacute; thể kiếm được h&agrave;ng triệu USD mỗi năm. Với sự trợ gi&uacute;p của hệ thống, Luke rất c&oacute; thể trở n&ecirc;n gi&agrave;u c&oacute; với tư c&aacute;ch l&agrave; một th&aacute;m tử tư.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng anh ta c&oacute; thể c&oacute; tương lai g&igrave; nếu trở th&agrave;nh cảnh s&aacute;t? Ngay cả cảnh s&aacute;t trưởng New York cũng kh&ocirc;ng kiếm được hơn 200.000 USD một năm.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tất nhi&ecirc;n, những người c&oacute; thứ hạng cao hơn kh&ocirc;ng chỉ dựa v&agrave;o tiền lương của họ để l&agrave;m gi&agrave;u. Sau khi nghỉ hưu, c&oacute; lẽ họ c&oacute; thể đi diễn thuyết, viết s&aacute;ch hoặc tham gia ch&iacute;nh trị v&agrave; dễ d&agrave;ng kiếm được h&agrave;ng triệu USD mỗi năm. Điều đ&oacute; đ&atilde; được ph&aacute;p luật cho ph&eacute;p.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng Luke năm nay mới 18 tuổi. Việc nghỉ hưu đối với anh ta c&ograve;n bao xa nữa?</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hơn nữa, nếu anh ta chỉ c&oacute; thể gi&agrave;u c&oacute; sau khi đến tuổi nghỉ hưu, th&igrave; cuộc đời của anh ta c&oacute; &iacute;ch lợi g&igrave;?</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thậm ch&iacute; tệ hơn, ba ng&agrave;y trước, Luke su&yacute;t c&atilde;i nhau với Robert khi đang n&oacute;i về c&ocirc;ng việc.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&oacute; l&agrave; bởi v&igrave; Robert muốn anh ta gia nhập sở cảnh s&aacute;t của họ sau khi tốt nghiệp v&agrave; Luke ki&ecirc;n quyết từ chối anh ta.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mục ti&ecirc;u của anh l&agrave; trở th&agrave;nh một người gi&agrave;u c&oacute;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh ta c&oacute; một hệ thống, ngay cả khi đ&oacute; l&agrave; một hệ thống chỉ k&iacute;ch hoạt sau khi anh ta trưởng th&agrave;nh. Theo luật tại Hoa Kỳ, những người từ 21 tuổi trở l&ecirc;n được coi l&agrave; người lớn. Rốt cuộc, họ đ&atilde; c&oacute; thể bắt đầu uống rượu ở độ tuổi đ&oacute; - l&agrave;m sao họ c&oacute; thể kh&ocirc;ng được coi l&agrave; người lớn?</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke đ&atilde; tin rằng anh ta chỉ cần đợi th&ecirc;m ba năm nữa để c&oacute; hệ thống.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh chưa bao giờ ngờ rằng m&igrave;nh chỉ cần đợi ba ng&agrave;y để hệ thống k&iacute;ch hoạt, v&agrave; sau khi k&iacute;ch hoạt, n&oacute; đ&atilde; muốn anh trở th&agrave;nh cảnh s&aacute;t.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&oacute; l&agrave;&hellip; anh ta đ&atilde; l&agrave;m bất kỳ điều &aacute;c n&agrave;o để đ&aacute;ng phải nhận điều n&agrave;y?</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke bắt đầu kh&oacute;c những giọt nước mắt thầm lặng trong tr&aacute;i tim m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kh&ocirc;ng phải l&agrave; anh ấy lo lắng về Robert.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mặc d&ugrave; Robert kh&ocirc;ng phải l&agrave; cha ruột của anh ấy, nhưng anh ấy vẫn tốt như vậy, x&eacute;t về mối quan hệ của họ.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nếu Luke đột nhi&ecirc;n thay đổi &yacute; định v&agrave; đồng &yacute; trở th&agrave;nh cảnh s&aacute;t, Robert sẽ tr&ecirc;u chọc anh ta một ch&uacute;t, v&agrave; vẫn sẽ gi&uacute;p anh ta l&agrave;m điều đ&oacute;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Robert l&agrave; một c&ocirc;ng d&acirc;n Texan b&igrave;nh thường, từng phục vụ trong qu&acirc;n đội v&agrave; từng điều h&agrave;nh một trang trại trước khi trở th&agrave;nh cảnh s&aacute;t. Anh ấy l&agrave; một người th&ocirc; thiển nhưng thẳng thắn v&agrave; bộc trực.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke nằm tr&ecirc;n giường bệnh với những cảm x&uacute;c v&ocirc; c&ugrave;ng phức tạp v&agrave; bắt đầu để suy nghĩ lung tung, rồi kh&ocirc;ng nhận ra, anh lại ch&igrave;m v&agrave;o giấc ngủ.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thật l&acirc;u sau, một giọng n&oacute;i vang l&ecirc;n b&ecirc;n tai anh. &quot;Luke, thức dậy.&quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Vẫn c&ograve;n đang ngủ gật, Luke lầm bầm, &quot;Kh&ocirc;ng, Claire, h&atilde;y để t&ocirc;i ngủ trong. Đ&oacute; l&agrave; ng&agrave;y nghỉ học.&quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave; quả thực, giọng n&oacute;i đ&oacute; đ&atilde; dừng lại.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một l&uacute;c sau, một giọng n&oacute;i kh&agrave;n kh&agrave;n vang l&ecirc;n. &ldquo;Robert, Luke b&acirc;y giờ chắc đ&atilde; ổn, nhưng anh ấy kh&ocirc;ng chịu thức dậy. T&ocirc;i kh&ocirc;ng thể l&agrave;m bất cứ điều g&igrave; về n&oacute;. Oh? Bạn đang đi qua? Được rồi. T&ocirc;i sẽ cắm trại với Shelly v&agrave; những người kh&aacute;c, v&igrave; vậy t&ocirc;i sẽ rời đi sau một giờ nữa bất kể bạn c&oacute; đến đ&acirc;y v&agrave;o l&uacute;c đ&oacute; hay kh&ocirc;ng. &quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tiếp theo, c&oacute; tiếng sột soạt trong ph&ograve;ng, như thể c&oacute; ai đ&oacute; đang n&aacute;o nhiệt xung quanh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau một giờ, một m&ugrave;i thơm thoảng qua v&agrave; đ&ocirc;i m&ocirc;i ấm &aacute;p đặt l&ecirc;n m&aacute; Luke. Sau đ&oacute;, một giọng n&oacute;i vang l&ecirc;n. &ldquo;Luke, những giấc mơ ngọt ng&agrave;o. Robert đang đến, v&igrave; vậy t&ocirc;i sẽ ra ngo&agrave;i trước. &quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke bất m&atilde;n lầm bầm v&agrave; quay lại trước khi tiếp tục ngủ.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người phụ nữ cười kh&uacute;c kh&iacute;ch trước khi bước ra khỏi phường.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Về phần Luke, anh vẫn đang hạnh ph&uacute;c trong c&otilde;i mộng.</span></span></p>\r\n<br />\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong giấc mơ của m&igrave;nh, anh ấy đang cầm một khẩu RPG trong một tay v&agrave; một khẩu s&uacute;ng Gatling ở tay kia. Anh ta bắn cả hai vũ kh&iacute; c&ugrave;ng một l&uacute;c, buộc nhiều người ngo&agrave;i h&agrave;nh tinh phải bỏ chạy. Sau đ&oacute;, anh ta cười tự m&atilde;n. &ldquo;T&ocirc;i l&agrave; một si&ecirc;u cảnh s&aacute;t! C&ograve;n ai nữa - &rdquo;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đột nhi&ecirc;n, anh cảm thấy như to&agrave;n bộ thế giới đang lắc lư, như thể một trận động đất lớn đang xảy ra. Những t&ograve;a nh&agrave; xung quanh anh bắt đầu sụp đổ v&agrave; đổ ập xuống anh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Với một tiếng than kh&oacute;c thảm thiết, Luke ngồi dậy.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bang!</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau một tiếng động bị b&oacute;p nghẹt, Luke gục xuống giường với đ&ocirc;i mắt đờ đẫn. Robert r&uacute;t tay ra khỏi nơi anh đ&atilde; lắc Luke v&agrave; v&ocirc; cảm xoa đầu anh, vốn bị đau do va chạm, trước khi n&oacute;i, &ldquo;Tuyệt vời. C&oacute; vẻ như cậu đang c&oacute; tinh thần tốt. H&agrave;nh hung một sĩ quan cảnh s&aacute;t v&agrave;o s&aacute;ng sớm như vậy? &rdquo;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke từ từ kh&ocirc;i phục lại sự b&agrave;ng ho&agrave;ng của m&igrave;nh, nhưng vẫn đang xoa đầu với khu&ocirc;n mặt nhăn nh&oacute;. Anh chế nhạo theo bản năng. &ldquo;Robert, t&ocirc;i kh&ocirc;ng phải tội phạm, t&ocirc;i cũng kh&ocirc;ng phải l&agrave; cấp dưới của anh. Nhớ lại đi?&quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay sau khi n&oacute;i những lời đ&oacute;, anh ta sững người.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chết tiệt! Anh ta đ&atilde; qu&aacute; quen với việc c&atilde;i nhau với Robert. Anh ấy phải l&agrave;m g&igrave; b&acirc;y giờ?</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhiệm vụ đầu ti&ecirc;n từ hệ thống l&agrave; ch&iacute;nh thức trở th&agrave;nh cảnh s&aacute;t, v&agrave; anh ta chỉ c&oacute; thời hạn một th&aacute;ng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave; Robert l&agrave; người duy nhất c&oacute; thể gi&uacute;p anh trở th&agrave;nh cảnh s&aacute;t trong một th&aacute;ng. Rốt cuộc, Robert l&agrave; cảnh s&aacute;t trưởng ở đ&acirc;y, v&agrave; l&agrave; cảnh s&aacute;t trưởng duy nhất trong thị trấn.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave; nếu Luke trở th&agrave;nh cảnh s&aacute;t, Robert sẽ trở th&agrave;nh &ocirc;ng chủ của m&igrave;nh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng c&oacute; lẽ v&igrave; đang nằm tr&ecirc;n giường bệnh n&ecirc;n Robert kh&ocirc;ng tranh c&atilde;i với &ocirc;ng như trước đ&acirc;y nữa. Thay v&agrave;o đ&oacute;, &ocirc;ng ấy hỏi, &quot;Cậu cảm thấy thế n&agrave;o?&quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke trả lời: &ldquo;Bỏ qua những g&igrave; đ&atilde; xảy ra trước đ&oacute;, t&ocirc;i nghĩ b&acirc;y giờ t&ocirc;i ổn. T&ocirc;i chỉ đ&oacute;i. &quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Robert trả lời, &quot;Vậy ta sẽ đi lấy đồ ăn cho cậu .&quot; &Ocirc;ng ta xoay người rời đi sau khi n&oacute;i xong.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng Luke nhanh ch&oacute;ng ngăn &ocirc;ng ta lại. &quot;Kh&ocirc;ng chờ đợi. T&ocirc;i đ&atilde; ổn. Robert, đưa t&ocirc;i về nh&agrave;. T&ocirc;i nhớ bữa s&aacute;ng của Catherine. &rdquo;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Robert quay lại v&agrave; n&oacute;i, &quot;Cậu c&oacute; chắc l&agrave; m&igrave;nh ổn kh&ocirc;ng?&quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke trả lời: &ldquo;Kh&ocirc;ng ai trong ch&uacute;ng ta l&agrave; b&aacute;c sĩ. Nếu ta muốn rời đi, ta sẽ cần sự đồng &yacute; của b&aacute;c sĩ &rdquo;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Robert đ&atilde; suy nghĩ v&agrave; đồng &yacute;. V&igrave; vậy, &ocirc;ng ấy đ&atilde; rời đi để t&igrave;m b&aacute;c sĩ.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&aacute;c sĩ đến v&agrave; hỏi một số c&acirc;u hỏi đơn giản trước khi n&oacute;i rằng Luke vẫn ổn v&agrave; c&oacute; thể xuất viện.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng trước khi rời đi, b&aacute;c sĩ kh&ocirc;ng thể kh&ocirc;ng cho Luke một lời khuy&ecirc;n. &ldquo;Cậu ổn về mặt thể chất, ngoại trừ một chấn động; nhưng Cậu đ&atilde; kh&ocirc;ng c&oacute; khi cậu được nhận v&agrave;o đ&ecirc;m qua. &quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-82901feb-7fff-d9e0-ac01-b727f1c5d462\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke che mặt kh&ocirc;ng n&oacute;i n&ecirc;n lời. Ngay trước đ&oacute;, đầu của anh ta đ&atilde; va v&agrave;o Robert rất nhiều. B&acirc;y giờ n&oacute; vẫn c&ograve;n đau.</span></span></p>\r\n', 'Cà Di', '2020-10-05 04:59:20', 1, 0, 454);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(48, 'Chương 2: Vụ điều tra của cảnh sát, Dấu son môi, và mọi thứ đều lớn hơn Phần 2', 'chuong-2-vu-djieu-tra-cua-canh-sat-dau-son-moi-va-moi-thu-djeu-lon-hon-phan-2-1', 14, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh ấy chỉ c&oacute; thể gật đầu trong bất lực khi n&oacute;i, &ldquo;Được rồi, b&aacute;c sĩ. Cảm ơn v&igrave; đ&atilde; n&oacute;i với t&ocirc;i điều đ&oacute; &rdquo;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay sau đ&oacute;, Luke v&agrave; Robert đ&atilde; c&oacute; mặt trong chiếc F150 v&agrave; đang tr&ecirc;n đường trở về nh&agrave;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đi được một l&uacute;c, Luke kh&ocirc;ng nhịn được nữa liền mở miệng. &ldquo;Robert, t&ocirc;i c&oacute; chuyện cần thảo luận với anh.&rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Với mắt vẫn hướng về con đường, Robert trả lời, &ldquo;Ồ? N&oacute; l&agrave; g&igrave;?&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke trả lời, &quot;Ừm, sau khi suy nghĩ về n&oacute; trong v&agrave;i ng&agrave;y, t&ocirc;i thấy t&ocirc;i đồng &yacute; với anh.&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ah?&quot; Robert liếc nh&igrave;n Luke với vẻ mặt trống rỗng.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke tiếp tục, &quot;V&igrave; vậy, t&ocirc;i đ&atilde; quyết định ở lại thị trấn để trở th&agrave;nh một cảnh s&aacute;t.&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&uacute;t!</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lốp xe k&ecirc;u r&eacute; l&ecirc;n tr&ecirc;n đ&aacute; sa thạch khi F150 chuyển hướng tr&ecirc;n đường v&agrave;o một bờ c&aacute;t.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave;i gi&acirc;y sau, chiếc xe dừng hẳn.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">May mắn thay, khu vực b&ecirc;n cạnh đường l&agrave; đất bằng phẳng, nhưng ngay cả khi n&oacute; hơi kh&ocirc;ng bằng phẳng, một chiếc F150 sẽ xử l&yacute; tốt. Kh&ocirc;ng bao giờ c&oacute; nguy cơ lật xe từ việc n&agrave;y.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Robert bu&ocirc;ng tay phanh v&agrave; kinh ngạc nh&igrave;n Luke. &quot;Cậu ... cậu vẫn c&ograve;n sốt?&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke đảo mắt. Cơn sốt của anh ấy l&agrave; do đồng bộ h&oacute;a với hệ thống đ&ecirc;m qua. N&oacute; kh&ocirc;ng giống như anh ấy đ&atilde; bị tổn thương n&atilde;o v&igrave; n&oacute;. &ldquo;Kh&ocirc;ng. Anh kh&ocirc;ng nghe thấy những g&igrave; t&ocirc;i n&oacute;i? &rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Robert trả lời, &ldquo;Nếu kh&ocirc;ng, tại sao cậu lại đổi &yacute;? T&ocirc;i tưởng rằng cậu đ&atilde; n&oacute;i rằng cậu th&agrave; chết chứ kh&ocirc;ng phải ở lại đ&acirc;y v&agrave; trở th&agrave;nh cảnh s&aacute;t? V&agrave; cậu thậm ch&iacute; c&ograve;n tuy&ecirc;n bố rằng nếu trở th&agrave;nh cảnh s&aacute;t, bạn sẽ ngh&egrave;o đến mức phải cạp đất ăn trong tương lai &rdquo;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke trả lời, &ldquo;Hehe, đ&oacute; l&agrave; những lời trống rỗng; đừng coi trọng ch&uacute;ng. T&ocirc;i chỉ phản b&aacute;c lại v&igrave; t&ocirc;i kh&ocirc;ng tin anh khi anh m&ocirc; tả việc trở th&agrave;nh cảnh s&aacute;t l&agrave; một c&ocirc;ng việc m&agrave; t&ocirc;i sẽ kh&ocirc;ng được trả lương m&agrave; kh&ocirc;ng l&agrave;m g&igrave; cả &rdquo;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Robert mở to mắt. &ldquo;Nếu cậu đi v&agrave; trở th&agrave;nh một cảnh s&aacute;t ở Th&agrave;nh phố Knox, cậu sẽ thực sự bận rộn. V&agrave; nếu cậu trở th&agrave;nh cảnh s&aacute;t ở một th&agrave;nh phố thậm ch&iacute; c&ograve;n lớn hơn, cậu sẽ qu&aacute; mệt mỏi để l&agrave;m bất cứ điều g&igrave; kh&aacute;c. Nhưng ở thị trấn của ch&uacute;ng ta, cậu sẽ l&agrave;m việc dưới quyền của t&ocirc;i. C&ocirc;ng việc c&oacute; thể mệt mỏi đến mức n&agrave;o? &rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke lắc đầu bất lực khi thừa nhận, &ldquo;Tốt th&ocirc;i, t&ocirc;i thừa nhận rằng t&ocirc;i đ&atilde; sai. Robert, anh l&agrave; &ocirc;ng chủ. T&ocirc;i đ&atilde; chuẩn bị để lắng nghe anh từ b&acirc;y giờ. &quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Robert nh&igrave;n chằm chằm v&agrave;o Luke một ch&uacute;t trước khi đột ngột gầm l&ecirc;n với tiếng cười v&agrave; vỗ vai Luke. &ldquo;Haha, cuối c&ugrave;ng! Đ&acirc;y l&agrave; lần đầu ti&ecirc;n cậu bị thủng lưới! Luke, h&atilde;y nhớ kỹ điều n&agrave;y! Lần n&agrave;y cậu đ&atilde; thủng lưới rồi! &rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke trả lời, &quot;Hehe, anh n&oacute;i g&igrave; cũng được, miễn l&agrave; n&oacute; gi&uacute;p anh ngủ ngon v&agrave;o ban đ&ecirc;m.&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tiếp theo, F150 lại v&agrave;o đường. Họ bắt đầu trở về nh&agrave; v&agrave; tr&ecirc;n đường đi, chỉ c&oacute; thể nghe thấy tiếng cười bộc ph&aacute;t thường xuy&ecirc;n của Robert.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi họ về đến nh&agrave;, Luke kh&ocirc;ng thể bận t&acirc;m để đợi Robert. Anh trực tiếp đẩy cửa bước v&agrave;o nh&agrave; v&agrave; n&oacute;i: &ldquo;Catherine, t&ocirc;i về rồi!&rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một c&aacute;i đầu nhỏ x&iacute;u l&oacute; ra từ ph&ograve;ng kh&aacute;ch v&agrave; một giọng n&oacute;i vang l&ecirc;n. &quot;Huh? Luke, b&acirc;y giờ cậu ổn chứ? &rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke bước đến v&agrave; vỗ nhẹ v&agrave;o c&aacute;i đầu nhỏ b&eacute; trước khi n&oacute;i, &ldquo;V&acirc;ng, b&acirc;y giờ t&ocirc;i ổn. Joseph, anh đang l&agrave;m g&igrave; vậy? Ohh, bạn chết rồi. Bạn đang chơi tr&ograve; chơi điện tử m&agrave; kh&ocirc;ng được ph&eacute;p? &quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khu&ocirc;n mặt nhỏ b&eacute; cong m&ocirc;i n&oacute;i, &ldquo;Catherine đồng &yacute; rằng t&ocirc;i c&oacute; thể chơi trong một giờ. Hơn nữa, cậu kh&ocirc;ng phải l&agrave; Claire. N&oacute; kh&ocirc;ng giống như cậu sẽ n&oacute;i với t&ocirc;i. &quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke nh&uacute;n vai. &ldquo;Tốt th&ocirc;i, t&ocirc;i sẽ kh&ocirc;ng n&oacute;i về cậu. Nhưng v&igrave; cậu đ&atilde; hứa với Catherine, h&atilde;y nhớ nghỉ ngơi khi hết thời gian. &quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Joseph c&oacute; phần phẫn nộ nhưng vẫn gật đầu. &quot;Ừ chắc chắn. Cậu đang trở n&ecirc;n d&agrave;i d&ograve;ng. &quot; Sau đ&oacute;, anh ngồi xuống s&agrave;n v&agrave; tiếp tục nh&igrave;n chằm chằm v&agrave;o TV khi chơi tr&ograve; chơi điện tử của m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave;o l&uacute;c đ&oacute;, Catherine bước ra khỏi bếp v&agrave; nh&igrave;n Luke với vẻ quan t&acirc;m s&acirc;u sắc.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke bước tới v&agrave; &ocirc;m c&ocirc; ấy trước khi n&oacute;i: &ldquo;T&ocirc;i ổn. Nhưng t&ocirc;i đang chết đ&oacute;i. Bữa s&aacute;ng đ&atilde; sẵn s&agrave;ng chưa, Catherine? &rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mặc d&ugrave; đ&atilde; được đảm bảo, Catherine vẫn lo lắng khi c&ocirc; nh&igrave;n Robert, người bước qua cửa ngay l&uacute;c đ&oacute;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Robert dường như đọc được suy nghĩ của c&ocirc; khi anh trả lời: &ldquo;Cậu ấy ổn. T&ocirc;i đ&atilde; kiểm tra với b&aacute;c sĩ rồi. &rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chỉ sau đ&oacute;, Catherine mới nở một nụ cười khi c&ocirc; ấy n&oacute;i, &ldquo;Tốt, tốt qu&aacute;. Đến, đến, bữa s&aacute;ng đ&atilde; sẵn s&agrave;ng. &rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc; ấy quay lại v&agrave; kh&ocirc;ng thể cưỡng lại nụ cười v&agrave; chỉ v&agrave;o mặt Luke trước khi n&oacute;i, &quot;Nhưng trong bữa s&aacute;ng, l&agrave;m ơn cho t&ocirc;i biết cậu đang t&aacute;n tỉnh y t&aacute; trẻ n&agrave;o ở bệnh viện.&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke ho&agrave;n to&agrave;n kh&ocirc;ng biết c&ocirc; ấy đang n&oacute;i g&igrave;. Chỉ sau khi bước v&agrave;o ph&ograve;ng ăn v&agrave; nh&igrave;n v&agrave;o bộ đồ ăn bằng th&eacute;p kh&ocirc;ng gỉ, anh mới nhận thấy c&oacute; một vết đỏ tr&ecirc;n m&aacute; m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh nh&igrave;n kỹ hơn v&agrave; khi nhận ra đ&oacute; l&agrave; một vết son m&ocirc;i, anh lập tức kh&ocirc;ng n&oacute;i n&ecirc;n lời.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tr&ecirc;n đường từ bệnh viện về nh&agrave;, anh đ&atilde; gặp kh&aacute; nhiều người. Nhiều người trong số họ đ&atilde; mỉm cười khi họ nh&igrave;n v&agrave;o khu&ocirc;n mặt của anh ấy.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trước đ&acirc;y anh ấy kh&ocirc;ng ch&uacute; &yacute; nhiều đến n&oacute;. Trong một thị trấn nhỏ như thế n&agrave;y, mọi người rất th&acirc;n thiện với nhau. Anh cho rằng, cộng với sự th&acirc;n thiện của người d&acirc;n nơi đ&acirc;y, mọi người đ&atilde; mỉm cười với anh v&igrave; anh đẹp trai.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng giờ anh đ&atilde; hiểu tại sao. Anh ấy đ&atilde; đi lại với một vết son đỏ tươi tr&ecirc;n m&aacute; v&agrave;o s&aacute;ng sớm. Mọi người sẽ nghĩ g&igrave; khi họ nh&igrave;n thấy điều đ&oacute;?</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Từ c&aacute;ch họ nh&igrave;n anh ta, suy nghĩ của họ chắc hẳn l&agrave; một c&aacute;i g&igrave; đ&oacute; dọc theo d&ograve;ng &quot;Thật tuyệt khi c&ograve;n trẻ.&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh kh&ocirc;ng vội v&agrave;ng lau vết son m&ocirc;i. Thay v&agrave;o đ&oacute;, anh ấy đ&atilde; d&agrave;nh một khoảng thời gian để điều chỉnh lại dấu ấn trước khi nhớ lại mọi thứ từ h&ocirc;m qua v&agrave; h&ocirc;m nay.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&ecirc;n cạnh anh, Robert cũng nh&igrave;n anh tr&ecirc;u chọc v&agrave; n&oacute;i: &ldquo;Tại sao? Cậu đ&atilde; nhớ lại đ&oacute; l&agrave; y t&aacute; n&agrave;o chưa? &rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&ocirc;i mắt của Luke lấp l&aacute;nh khi anh ấy n&oacute;i, &ldquo;T&ocirc;i dường như nhớ lại rằng hai ng&agrave;y trước, trong buổi dạ hội tốt nghiệp trung học phổ th&ocirc;ng, Claire đ&atilde; đi mua một c&acirc;y son m&ocirc;i mới.&rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Robert l&agrave; một cảnh s&aacute;t, v&agrave; l&agrave; cảnh s&aacute;t trưởng. V&igrave; vậy, anh ngay lập tức hiểu được &yacute; nghĩa của những từ đ&oacute;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave; khi anh ấy nghi&ecirc;n cứu dấu son m&ocirc;i đ&oacute;, c&aacute;c đường đen ngay lập tức h&igrave;nh th&agrave;nh tr&ecirc;n khu&ocirc;n mặt anh ấy khi anh ấy n&oacute;i, &quot;C&ocirc; g&aacute;i đi&ecirc;n rồ đ&oacute;!&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke nh&uacute;n vai v&agrave; lấy ra một chiếc khăn ăn v&agrave; lau dấu vết.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&uacute;ng l&uacute;c đ&oacute;, Catherine đến với bữa s&aacute;ng.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng c&ocirc; ấy chỉ mang đồ ăn s&aacute;ng cho Luke.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Robert đ&atilde; ăn trước khi đi l&agrave;m. Tr&ecirc;n thực tế, anh ấy đ&atilde; tr&ecirc;n đường đi l&agrave;m trước khi đi đường v&ograve;ng v&agrave; đến bệnh viện thay cho Luke. Trong mọi trường hợp, đ&acirc;y l&agrave; một thị trấn nhỏ, v&igrave; vậy anh ta c&oacute; thể coi n&oacute; l&agrave; một phần của cuộc tuần tra h&agrave;ng ng&agrave;y của m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Về phần Catherine, c&ocirc; đ&atilde; đi ăn với Joseph trước đ&oacute;. Một đứa trẻ cần phải ăn đ&uacute;ng giờ. V&igrave; vậy, Joseph kh&ocirc;ng thể đợi đến l&uacute;c muộn như thế n&agrave;y để ăn.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke lấy ra một ly sữa ấm từ l&ograve; vi s&oacute;ng v&agrave; đổ v&agrave;o b&aacute;t bột yến mạch trước khi n&oacute;i, &quot;Ừm, t&ocirc;i c&oacute; thể kh&ocirc;ng?&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Catherine mỉm cười v&agrave; gật đầu. &ldquo;Ừ, cứ tự nhi&ecirc;n. Vậy, cậu đ&atilde; nhớ đ&oacute; l&agrave; y t&aacute; n&agrave;o chưa? &rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke ho&agrave;n to&agrave;n v&ocirc; cảm trước c&acirc;u hỏi, nhưng Robert, người đang nhấm nh&aacute;p lon Dr. Pepper m&agrave; anh lấy từ tủ lạnh, bị sặc v&agrave; phun ra một ngụm nước.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">May mắn thay, anh ấy ngồi ở một g&oacute;c kh&aacute;c của b&agrave;n, v&agrave; bữa s&aacute;ng của Luke nhờ đ&oacute; đ&atilde; được cứu khỏi vụ uống rượu.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke tiếp tục ăn s&aacute;ng một c&aacute;ch b&igrave;nh thản, từ từ nh&eacute;t chiếc b&aacute;nh m&igrave; kẹp thịt nguội v&agrave;o miệng.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&oacute; một c&acirc;u n&oacute;i rằng mọi thứ đều lớn hơn ở Texas.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&iacute; dụ, chiếc b&aacute;nh m&igrave; anh ta đang ăn d&agrave;y hơn một chiếc b&aacute;nh m&igrave; kẹp b&igrave;nh thường. K&iacute;ch thước tổng thể cũng lớn hơn, v&agrave; thậm ch&iacute; miếng giăm b&ocirc;ng b&ecirc;n trong c&ograve;n d&agrave;y hơn miếng giăm b&ocirc;ng th&ocirc;ng thường. C&ugrave;ng với hai l&aacute;t rau v&agrave; một số dưa chuột muối do Catherine tự l&agrave;m, chiếc b&aacute;nh sandwich lớn chảy ra với hương vị bao gồm hương vị của b&aacute;nh m&igrave;, thịt, vị ngọt v&agrave; chua, v&agrave; cũng chứa đựng một cảm gi&aacute;c tươi m&aacute;t nhất định từ c&aacute;c l&aacute;t rau. Đ&acirc;y chỉ l&agrave; loại hương vị m&agrave; Luke rất th&iacute;ch.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay cả trong kiếp trước, Luke đ&atilde; rất th&iacute;ch ăn b&aacute;nh m&igrave; kẹp thịt v&agrave; b&aacute;nh m&igrave; kẹp. V&agrave; kh&ocirc;ng giống như nhiều người kh&aacute;c, anh ấy kh&ocirc;ng ăn loại thức ăn đ&oacute; để tiết kiệm thời gian. Đ&uacute;ng hơn, anh thực sự y&ecirc;u họ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave; v&igrave; thế, anh kh&ocirc;ng cảm thấy lạc l&otilde;ng trong cuộc sống mới ở Texas n&agrave;y.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hơn nữa, c&oacute; kh&aacute; nhiều người gốc Hoa cũng sống ở Texas. Điều đ&oacute; đặc biệt đ&uacute;ng ở Houston v&agrave; Fort Worth. Kh&ocirc;ng kh&oacute; để thỉnh thoảng c&oacute; ai đ&oacute; mang đến một số m&oacute;n ăn Trung Quốc từ hai nơi đ&oacute;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Về đồ ăn, đồ ăn Texas thực sự ph&ugrave; hợp với khẩu vị của anh ấy.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">To&agrave;n bộ chiếc b&aacute;nh sandwich nặng khoảng một catty, v&agrave; Luke đ&atilde; ho&agrave;n th&agrave;nh n&oacute; trong một thời gian ngắn. Anh cũng uống hết sữa ấm v&agrave; bột yến mạch trước khi vỗ bụng h&agrave;i l&ograve;ng. &ldquo;Cảm gi&aacute;c thật tuyệt. T&ocirc;i gần như sắp chết đ&oacute;i &rdquo;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ở b&ecirc;n cạnh, Robert đ&atilde; n&oacute;i với Catherine bằng một giọng trầm. Điều đ&oacute; khiến Catherine ph&aacute; l&ecirc;n cười.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi nghe những lời của Luke, c&ocirc; ấy t&ograve; m&ograve; n&oacute;i, &quot;Robert kh&ocirc;ng mang cho cậu một c&aacute;i b&aacute;nh m&igrave; kẹp thịt hay c&aacute;i g&igrave; đ&oacute; sao?&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-13122b3f-7fff-481d-b3ed-05e08f382b0f\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke trả lời: &ldquo;L&agrave;m thế n&agrave;o m&agrave; so s&aacute;nh được với thức ăn của c&ocirc;? T&ocirc;i th&agrave; đợi về nh&agrave; trước khi ăn. &quot;</span></span></p>\r\n', 'Cà Di', '2020-10-05 04:59:49', 1, 0, 163);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(49, 'Chương 2: Ass Freak', 'chuong-2-ass-freak-1', 26, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Han Sen bị cho&aacute;ng bởi giọng n&oacute;i bất ngờ xuất hiện trong đầu, Cậu ta kh&ocirc;ng thể tin đ&oacute; l&agrave; sự thật. Sinh vật hiếm v&agrave; linh hồn qu&aacute;i th&uacute; hiếm: Những thứ thật sự tồn tại ư?</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Những sinh vật của Th&aacute;nh Địa của Thần được chia l&agrave;m 4 loại: thường, nguy&ecirc;n thủy, đột biến v&agrave; hiếm. Điểm geno cũng kh&aacute;c biệt khi ăn thịt mỗi lo&agrave;i sinh vật kh&aacute;c nhau.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hiện tại, Con người chỉ biết c&oacute; bốn giai đoạn ở Th&aacute;nh Địa của Thần. Với mỗi lần tiến h&oacute;a vật l&yacute; ho&agrave;n th&agrave;nh, sẽ c&oacute; thể đi v&agrave;o kh&ocirc;ng gian kh&aacute;c. C&agrave;ng nhiều điểm geno mạnh được d&ugrave;ng trong qu&aacute; tr&igrave;nh tiến h&oacute;a, khả năng sống s&oacute;t trong kh&ocirc;ng gian tiếp theo sẽ cao hơn.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Linh hồn qu&aacute;i th&uacute; hiếm được biết đến l&agrave; loại tốt nhất. Bất k&igrave; loại linh hồn qu&aacute;i th&uacute; hiếm n&agrave;o đều bằng một cả gia t&agrave;i.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi Han Sen cuối c&ugrave;ng nhận ra chuyện g&igrave; đang xảy ra, Cậu ta gần như run rẩy. Cầm con dao găm cậu ta mở lớp vỏ của con bọ c&aacute;nh cứng đen ra, bỏ v&agrave;o miệng những miếng thịt mềm như thạch vậy.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Đ&atilde; ăn thịt bọ c&aacute;nh cứng đen. Đ&atilde; nhận một điểm geno hiếm.&rdquo;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cảm nhận từng phần thịt được chuyển th&agrave;nh năng lượng v&agrave; nghe giọng n&oacute;i cứ li&ecirc;n tục trong đầu của cậu ta, mọi thứ khiến cậu ta bật kh&oacute;c.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cậu ta cầm l&ecirc;n một c&aacute;i ch&acirc;n v&agrave; h&uacute;t tất cả phần thịt b&ecirc;n trong, cảm nhận từng s&oacute;ng năng lượng chảy qua cơ thể v&agrave; m&aacute;u thịt của cậu ta.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Đ&atilde; ăn thịt bọ c&aacute;nh cứng đen. Đ&atilde; nhận một điểm geno hiếm.&rdquo;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Đ&atilde; ăn thịt bọ c&aacute;nh cứng đen. Đ&atilde; nhận một điểm geno hiếm.&rdquo;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giọng n&oacute;i b&ecirc;n trong đầu của Han Sen cứ li&ecirc;n tục cất l&ecirc;n l&agrave;m cậu ta c&oacute; cảm gi&aacute;c ngất ng&acirc;y hạnh ph&uacute;c .</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi Han Sen đang cắt th&igrave; cảm thấy con dao của m&igrave;nh vừa cắt v&agrave;o một c&aacute;i g&igrave; đ&oacute; cứng cứng nghe giống như tiếng của kim loại vậy. Han Sen dừng lại.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Những con bọ c&aacute;nh cứng v&agrave; những con cua giống như kiểu: Nh&igrave;n ch&uacute;ng c&oacute; vẻ rất cứng, nhưng b&ecirc;n trong lớp vỏ của ch&uacute;ng cũng chỉ l&agrave; những phần thịt kh&ocirc;ng xương, Vậy tại sao b&ecirc;n trong lại c&oacute; kim loại được?</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cậu ta r&uacute;t con dao găm ra, k&eacute;o con dao sang một b&ecirc;n phần thịt bị cắt, v&agrave; cứa v&agrave;o một g&oacute;c của miếng sắt đen. Han Sen cắt tất cả phần thịt xung quanh n&oacute;, v&agrave; miếng kim loại m&agrave;u đen cũng sớm được phơi b&agrave;y ra.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ban đầu cậu ta nghĩ đ&oacute; l&agrave; kim loại, nhưng khi lấy n&oacute; ra, cậu kh&aacute;m ph&aacute; ra được đ&oacute; l&agrave; một vi&ecirc;n pha l&ecirc; m&agrave;u đen c&oacute; k&iacute;ch cỡ v&agrave; h&igrave;nh d&aacute;ng giống nhưng một quả trứng bồ c&acirc;u.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Han Sen cầm vi&ecirc;n pha l&ecirc; tr&ecirc;n tay v&agrave; kh&ocirc;ng cảm thấy c&oacute; g&igrave; bất thường. Vi&ecirc;n pha l&ecirc; nh&igrave;n thật đẹp đ&iacute;nh xung quanh to&agrave;n vi&ecirc;n sỏi đen.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng khi nh&igrave;n kỹ hơn, nh&igrave;n n&oacute; giống như h&agrave;ng triệu ng&ocirc;i sao đang rực s&aacute;ng, tạo n&ecirc;n một vẻ đẹp đ&aacute;ng kinh ngạc.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Đ&acirc;y l&agrave; khu vực dễ nhất trong Th&aacute;nh Địa của Thần,&rdquo; Han Sen nghĩ. &ldquo;Ngay cả khi sinh vật loại nguy&ecirc;n thủy c&ograve;n kh&oacute; khăn mới t&igrave;m được, chứ n&oacute;i g&igrave; đến sinh vật huyền thoại. L&agrave;m sao m&agrave; một con bọ c&aacute;nh cứng đen loại hiếm lại xuất hiện đột ngột vậy? V&agrave; m&igrave;nh cũng chưa từng nh&igrave;n thấy một con bọ c&aacute;nh cứng loại hiếm n&agrave;o trước đ&oacute;. D&ugrave; g&igrave; ch&uacute;ng cũng chỉ l&agrave; sinh vật cấp thấp. V&agrave; con bọ c&aacute;nh cứng đen loại hiếm n&agrave;y lại cực k&igrave; yếu. N&oacute; c&oacute; li&ecirc;n quan g&igrave; đến vi&ecirc;n pha l&ecirc; chăng? &rdquo;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cũng chẳng c&oacute; manh mối g&igrave;, cậu ta cũng ăn tất cả phần con lại của con bọ c&aacute;nh cứng. Sau c&ugrave;ng cậu nhận được tất cả bay điểm geno.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bảy điểm geno hiếm gần như một giấc mơ vậy. Với thể trạng của cậu ta, Cậu ta cần phải mạo hiểm mọi thứ ngay cả phải giết một con th&uacute; nguy&ecirc;n thủy, chỉ để lấy một điểm geno hiếm.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lớp vỏ của con bọ c&aacute;nh cứng hiếm n&agrave;y cũng l&agrave; một vật liệu tốt, Han Sen nhặt tất cả ch&acirc;n v&agrave; cất tất cả ch&uacute;ng v&agrave;o t&uacute;i, gom tất cả lại v&agrave; lớp vỏ cậu ta để ở t&uacute;i ph&iacute;a dưới.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Vỏ của con bọ c&aacute;nh cứng loại thường đều kh&ocirc;ng c&oacute; gi&aacute; trị nhưng với vỏ n&agrave;y th&igrave; c&oacute; thể l&agrave;m s&uacute;p c&oacute; thể gi&uacute;p cho cậu ta nhận được th&ecirc;m một đến hai điểm geno hiếm nữa.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nếu cậu ta b&aacute;n c&aacute;i vỏ, cậu ta c&oacute; thể mua được cho m&igrave;nh một chiếc phi cơ ri&ecirc;ng ở Levo Interstellar Alliance.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi đang tr&ecirc;n đường về đến L&acirc;u Đ&agrave;i Steel Armor, mọi người đều nh&igrave;n cậu ta mới &aacute;nh mắt mỉa mai, cười nhạo đến nổi kh&ocirc;ng một ai đến gần cậu ta.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mọi người ở Th&aacute;nh Địa của Thần đều nh&igrave;n cậu ta như qu&aacute;i vật vậy.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Th&ocirc;ng thường một người b&igrave;nh thường ở Th&aacute;nh Địa của Thần ngay cả khi người đ&oacute; ngh&egrave;o, v&otilde; thuật cũng kh&ocirc;ng tệ, th&igrave; họ lu&ocirc;n t&igrave;m được v&agrave;i người bạn đồng h&agrave;nh để c&oacute; thể săn được những con sinh vật loại nguy&ecirc;n thủy c&ugrave;ng nhau, v&agrave; người đ&oacute; cũng kh&ocirc;ng tồi t&agrave;n như Han Sen.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, ở L&acirc;u Đ&agrave;i Steel Armor th&igrave; chẳng c&oacute; ai sẵn s&agrave;ng v&agrave; dũng cảm như Han Sen.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hơn ba th&aacute;ng trước, Han Sen bước ch&acirc;n v&agrave;o Th&aacute;nh Địa của Thần lần đầu ti&ecirc;n. Thời vừa bước ch&acirc;n đầu ti&ecirc;n ra khỏi L&acirc;u Đ&agrave;i Gi&aacute;p Th&eacute;p, cậu ta lại đối đầu với một con S&oacute;i trắng một sừng khổng lồ đang tiến lại gần cậu ta dồn cậu ta v&agrave;o một g&oacute;c th&agrave;nh lũy.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Han Sen liền lấy con dao găm đ&acirc;m v&agrave;o m&ocirc;ng con </span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(60, 64, 67); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">s&oacute;i trắng một sừng</span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\"> đ&oacute; m&agrave; kh&ocirc;ng một ch&uacute;t do dự n&agrave;o.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&uacute; đ&acirc;m đ&oacute; lại đem Han Sen v&agrave; Qin Xuan ra l&agrave;m tr&ograve; cười trong L&acirc;u Đ&agrave;i Steel Armor.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&uacute;ng vậy, Con s&oacute;i trắng kỳ lăng đ&oacute; kh&ocirc;ng phải l&agrave; qu&aacute;i th&uacute;, chỉ l&agrave; một con người giống Han Sen. C&ocirc; ta chỉ đang d&ugrave;ng linh hồn qu&aacute;i th&uacute; để biến h&igrave;nh th&ocirc;i.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Qin Xuan cũng l&agrave; người phụ nữ mạnh nhất ở trong L&acirc;u Đ&agrave;i Steel Armor. C&ocirc; ấy chỉ vừa ho&agrave;n tất qu&aacute; tr&igrave;nh tiến h&oacute;a đầu ti&ecirc;n với điểm geno hiếm v&agrave; chọn v&agrave;o nh&aacute;nh tiến h&oacute;a của m&igrave;nh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bị một t&ecirc;n l&iacute;nh mới đ&acirc;m v&agrave;o b&ecirc;n m&ocirc;ng l&agrave;m c&ocirc; trở th&agrave;nh một tr&ograve; hề ở Khu Tr&uacute; Ẩn Steel Armor. Mặc d&ugrave; chẳng ai d&aacute;m n&oacute;i ra điều n&agrave;y trước mặt Qin Xuan, nhưng thật ra l&agrave; bọn họ lại cười ph&aacute; l&ecirc;n sau lưng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave; c&aacute;i t&ecirc;n &ldquo;Ass Freak&rdquo; trở th&agrave;nh c&aacute;i t&ecirc;n biệt danh nổi tiếng của Han Sen ở Khu Tr&uacute; Ẩn Steel Armor.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cho n&ecirc;n những kẻ m&agrave; d&agrave;nh thời gian cho cậu ta đều bị Qin Xuan xem l&agrave; kẻ th&ugrave;. Ở Khu Tr&uacute; Ẩn Steel Armor, kh&ocirc;ng ai muốn g&acirc;y th&ugrave; với c&ocirc; ta. Cho d&ugrave; người đ&oacute; muốn đi chăng nữa, cũng kh&ocirc;ng một ai m&agrave; liều mạng đối đầu với Qin Xuan v&agrave; Ass Freak.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&igrave; thế Han Sen đ&atilde; c&oacute; khoảng thời gian kh&oacute; khăn, nhưng cậu ta cũng kh&ocirc;ng thể tr&aacute;ch Qin Xuan được. L&agrave; cậu ta sai trước n&ecirc;n th&agrave;nh ra như vậy, đ&oacute; l&agrave; do cậu ta qu&aacute; lo lắng v&igrave; lần đầu bước ch&acirc;n v&agrave;o Th&aacute;nh Địa của Thần, cộng th&ecirc;m chuyện cậu ta lần đầu thấy sinh vật m&agrave; ở thế giới cũ chưa từng xuất hiện hoặc biến h&igrave;nh trước đ&oacute;, N&ecirc;n đ&atilde; dẫn đến c&aacute;i sai lầm tai hại cho cậu ta.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng Han Sen cũng biết ơn v&igrave; Qin Xuan kh&ocirc;ng giết cậu ta trong l&uacute;c tức giận.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-20a0bcc9-7fff-f4fe-26f7-6c5555c27b93\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Điều may mắn đ&oacute; khi dịch chuyển đến Th&aacute;nh Địa của Thần th&igrave; đều ngẫu nhi&ecirc;n, v&agrave; cũng chẳng c&oacute; ai quen biết nhau ở Khu Tr&uacute; Ẩn Steel Armor, cho n&ecirc;n chẳng ai biết được t&ecirc;n thật của cậu ta.</span></span></p>\r\n', 'Phê Kun', '2020-10-06 08:22:20', 1, 0, 156);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(50, 'Chapter 3: Giáp máu thiêng', 'chapter-3-giap-mau-thieng-1', 26, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Han Sen trở về ph&ograve;ng, nh&oacute;m lửa, bắt đầu đun m&oacute;ng v&agrave; vỏ bọ hung đen trong nồi.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kh&ocirc;ng c&oacute; c&ocirc;ng cụ v&agrave; b&iacute; quyết, cậu ta chỉ c&oacute; thể sử dụng những phương ph&aacute;p th&ocirc; sơ nhất để xử l&yacute; vỏ v&agrave; chiết xuất tinh chất geno.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Vỏ của một sinh vật qu&yacute; hiếm sẽ mất hơn một hoặc hai giờ để nấu ch&iacute;n, v&igrave; vậy Han Sen đậy nắp v&agrave; lấy vi&ecirc;n pha l&ecirc; đen ra để xem kỹ hơn.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, kh&ocirc;ng c&oacute; manh mối n&agrave;o.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đột nhi&ecirc;n, Han Sen tho&aacute;ng nh&igrave;n thấy con qu&aacute;i th&uacute; vảy xanh c&oacute; k&iacute;ch thước bằng ch&uacute; ch&oacute; nhỏ trong lồng ở g&oacute;c ph&ograve;ng v&agrave; chợt nảy ra &yacute; tưởng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Như mấy con bọ c&aacute;nh cứng đen, con qu&aacute;i th&uacute; m&agrave;u xanh n&agrave;y l&agrave; một sinh vật b&igrave;nh thường. Han Sen đ&atilde; từng săn mồi v&agrave; mang về nấu ăn để tăng điểm geno của m&igrave;nh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng sau đ&oacute;, qu&aacute;i th&uacute; vảy xanh kh&ocirc;ng c&ograve;n tăng điểm geno của cậu ta nữa, n&ecirc;n Han Sen kh&ocirc;ng ăn con cuối c&ugrave;ng v&agrave; qu&ecirc;n n&oacute; đi. Đ&aacute;ng ngạc nhi&ecirc;n l&agrave; n&oacute; vẫn c&ograve;n sống.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trước sự ngạc nhi&ecirc;n của m&igrave;nh khi nh&igrave;n thấy vi&ecirc;n pha l&ecirc; đen, con th&uacute; vảy xanh đang hấp hối tập trung to&agrave;n bộ sức lực để vươn lưỡi ra, cuộn vi&ecirc;n pha l&ecirc; đen lại v&agrave;o miệng v&agrave; nuốt trọn vi&ecirc;n pha l&ecirc;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Quả thật c&oacute; điều kỳ diệu về vi&ecirc;n pha l&ecirc; đen!&quot; Han Sen đ&atilde; bị cho&aacute;ng v&aacute;ng v&agrave; thấy rằng con th&uacute; vảy xanh c&oacute; vẻ c&oacute; sinh lực hơn đ&aacute;ng kể sau khi nuốt vi&ecirc;n pha l&ecirc;, bốn m&oacute;ng vuốt của n&oacute; c&agrave;o cấu v&agrave; đầu di chuyển để cắn b&agrave;n tay của Han Sen đang ở tr&ecirc;n cổ của n&oacute;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Han Sen n&eacute;m con th&uacute; vảy xanh trở lại lồng v&agrave; cẩn thận quan s&aacute;t n&oacute;. Con th&uacute; kh&ocirc;ng được cho ăn trong nhiều ng&agrave;y, đ&atilde; ho&agrave;n to&agrave;n hồi phục sức sống, tung ho&agrave;nh v&agrave; c&agrave;o cấu trong lồng, giống như lần đầu bị bắt.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Han Sen ngồi đối diện với c&aacute;i lồng v&agrave; theo d&otilde;i h&agrave;nh tung của con qu&aacute;i th&uacute; vảy xanh. Cậu ta đ&atilde; c&oacute; một suy đo&aacute;n, v&agrave; nếu anh ta đ&uacute;ng, cậu ta c&oacute; thể đ&atilde; thu được một thứ g&igrave; đ&oacute; kh&oacute; tin - thứ th&uacute; vị hơn một sinh vật qu&yacute; hiếm.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng Han Sen e rằng cậu ta đ&atilde; sai, cho n&ecirc;n cậu ta chỉ c&oacute; thể nh&igrave;n chằm chằm v&agrave;o con th&uacute; vảy xanh một c&aacute;ch lo lắng, hi vọng c&oacute; sự thay đổi m&agrave; cậu ta mong muốn.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Han Sen lo lắng nh&igrave;n chăm ch&uacute; v&agrave; kh&ocirc;ng nhận ra rằng m&igrave;nh đang đ&oacute;i cho đến khi ngửi thấy m&ugrave;i nước hầm xương từ trong nồi.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nh&igrave;n v&agrave;o thời gian, cậu ta nhận thấy gần 24 giờ đ&atilde; tr&ocirc;i qua. Tiến đến kiểm tra vỏ con bọ c&aacute;nh cứng đen trong nồi th&igrave; thấy vỏ đ&atilde; chuyển m&agrave;u v&agrave;ng, nước d&ugrave;ng c&oacute; m&agrave;u v&agrave;ng. N&oacute; c&oacute; m&ugrave;i thơm đến nỗi miệng của ch&agrave;ng thanh ni&ecirc;n đ&atilde; chảy nước miếng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đang định m&uacute;c một b&aacute;t canh, Han Sen đột nhi&ecirc;n nghe thấy tiếng sắt vỡ từ lồng của con th&uacute; vảy xanh. Cậu ta vội quay lại v&agrave; thấy con th&uacute; vảy xanh đ&atilde; cắn đứt một thanh sắt của lồng v&agrave; vươn đầu ra từ b&ecirc;n trong.&nbsp;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lớp vảy m&agrave;u xanh lục nhạt của n&oacute; giờ đ&atilde; ho&agrave;n to&agrave;n chuyển sang m&agrave;u xanh đậm. H&agrave;m răng của n&oacute; trở n&ecirc;n sắc nhọn v&agrave; bốn đầu m&oacute;ng vuốt của n&oacute; giống như những chiếc m&oacute;c sắt. Con th&uacute; sắp chui ra khỏi lồng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Han Sen ngạc nhi&ecirc;n v&agrave; r&uacute;t con dao găm từ thắt lưng ra. Cậu lao đến trước lồng v&agrave; đ&acirc;m con dao găm v&agrave;o bụng dưới của con th&uacute; vảy xanh. Với một c&uacute; xoay người, con qu&aacute;i vật vảy xanh ngừng v&ugrave;ng vẫy.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Sinh vật nguy&ecirc;n thủy qu&aacute;i th&uacute; vảy xanh bị giết. Kh&ocirc;ng c&oacute; linh hồn th&uacute; n&agrave;o nhận được. Ăn thịt của th&uacute; vảy xanh nguy&ecirc;n thủy để nhận ngẫu nhi&ecirc;n từ 0 đến mười điểm.&quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giọng n&oacute;i kỳ lạ vang l&ecirc;n trong đầu Han Sen khiến cậu ta cho&aacute;ng v&aacute;ng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Qu&aacute;i th&uacute; vảy xanh nguy&ecirc;n thủy&hellip; Sinh vật nguy&ecirc;n thủy&hellip; V&igrave; vậy, vi&ecirc;n pha l&ecirc; thực sự c&oacute; thể khiến những sinh vật n&agrave;y tiến h&oacute;a&hellip;&rdquo; Han Sen ngập tr&agrave;n vui sướng v&agrave; kh&ocirc;ng biết phải phản ứng thế n&agrave;o.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một l&uacute;c l&acirc;u sau, cậu ta bất ngờ d&ugrave;ng dao găm ch&eacute;m con th&uacute; vảy xanh v&agrave; ph&aacute;t hiện ra vi&ecirc;n pha l&ecirc; to bằng quả trứng chim bồ c&acirc;u. Kh&ocirc;ng th&egrave;m để &yacute; đến vết m&aacute;u tr&ecirc;n đ&oacute;, cậu h&ocirc;n l&ecirc;n vi&ecirc;n pha l&ecirc; hai lần rồi lau sạch, cầm n&oacute; trong tay như một bảo bối lớn.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Vi&ecirc;n pha l&ecirc; thực sự l&agrave; l&yacute; do khiến con bọ đen trở th&agrave;nh một sinh vật qu&yacute; hiếm&hellip; Nếu con th&uacute; vảy xanh tiếp tục tiến h&oacute;a, n&oacute; c&oacute; thể trở th&agrave;nh một sinh vật qu&yacute; hiếm nữa kh&ocirc;ng?&rdquo; Han Sen gần như kh&ocirc;ng d&aacute;m nghĩ đến điều đ&oacute;, v&igrave; n&oacute; qu&aacute; tuyệt vời.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cậu ta phấn kh&iacute;ch đến mức hai tay run rẩy khi cầm vi&ecirc;n pha l&ecirc;. Cậu cắn lưỡi v&agrave; k&ecirc;u l&ecirc;n v&igrave; đau để biết rằng đ&oacute; thực sự kh&ocirc;ng phải l&agrave; một giấc mơ.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau một l&uacute;c phấn kh&iacute;ch, Han Sen cẩn thận cất vi&ecirc;n pha l&ecirc; đi, ngấu nghiến lấy phần nước vỏ, v&agrave; đạt được một điểm geno đột biến kh&aacute;c, n&acirc;ng số điểm geno của m&igrave;nh l&ecirc;n t&aacute;m.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nếu kh&ocirc;ng c&oacute; c&ocirc;ng cụ v&agrave; phương tiện, phương ph&aacute;p nguy&ecirc;n thủy của cậu ta kh&oacute; c&oacute; thể chiết xuất tất cả tinh chất geno từ vỏ, nhưng đủ tốt để cậu ta đạt được một điểm geno đột biến.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Han Sen nhận ra rằng m&igrave;nh đ&atilde; c&oacute; được linh hồn qu&yacute; hiếm khi giết con bọ đen v&agrave;ng, v&agrave; cậu nhanh ch&oacute;ng truy cập v&agrave;o trạng th&aacute;i của m&igrave;nh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Han Sen: Kh&ocirc;ng tiến h&oacute;a.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trang th&aacute;i : kh&ocirc;ng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuổi thọ: 200 năm.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Y&ecirc;u cầu để tiến h&oacute;a: 100 điểm gen.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Điểm Geno đạt được: 79 điểm Geno; 8 điểm geno đột biến.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Linh hồn th&uacute; nhận được: Bọ hung đen qu&yacute; hiếm.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Loại linh hồn của con bọ hung đen qu&yacute; hiếm: &Aacute;o gi&aacute;p.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Bộ gi&aacute;p của linh hồn con bọ hung đen m&aacute;u thi&ecirc;ng tr&ocirc;ng như thế n&agrave;o?&quot; Han Sen chưa kịp suy nghĩ th&igrave; một b&oacute;ng v&agrave;ng vọt ra từ kh&ocirc;ng kh&iacute; mỏng, tr&ocirc;ng giống hệt con bọ hung đen v&agrave;ng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Con bọ đen v&agrave;ng bay đến trước ngực Han Sen, h&oacute;a th&agrave;nh một chất lỏng m&agrave;u v&agrave;ng v&agrave; bắt đầu bao phủ to&agrave;n th&acirc;n. Trong nh&aacute;y mắt, Han Sen to&agrave;n th&acirc;n c&ugrave;ng với t&oacute;c đều bị quấn lấy.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bộ gi&aacute;p v&agrave;ng được sắp xếp hợp l&yacute; chứa đầy sức mạnh, v&agrave; bao phủ to&agrave;n bộ cơ thể cậu ta như thể n&oacute; l&agrave; một bộ &aacute;o gi&aacute;p thời Trung Cổ gothic được thấm nhuần kh&iacute; động lực học được cải tiến &mdash; tr&ocirc;ng giống như một t&aacute;c phẩm nghệ thuật.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">To&agrave;n bộ bộ gi&aacute;p mang đầy sức mạnh v&agrave; tốc độ, khiến cho Han Sen tr&ocirc;ng mảnh mai v&agrave; uy nghi&ecirc;m, như thể to&agrave;n th&acirc;n tr&agrave;n đầy sức mạnh b&ugrave;ng nổ.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-9c253287-7fff-47a0-eb51-12ec6d8b0adf\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Thật vậy, đ&acirc;y l&agrave; &aacute;o gi&aacute;p từ linh hồn linh th&uacute; qu&yacute; hiếm. N&oacute; lộng lẫy hơn những bộ gi&aacute;p từ linh hồn qu&aacute;i th&uacute; nguy&ecirc;n thủy m&agrave; m&igrave;nh thường thấy ở Steel Armor Shelter.&quot; Han Sen cảm thấy ng&acirc;y ngẩn cả người. Cậu từng ghen tị với những người mặc &aacute;o gi&aacute;p của linh hồn th&uacute; nguy&ecirc;n thủy v&agrave; chưa bao giờ nghĩ một ng&agrave;y n&agrave;o đ&oacute; cậu sẽ c&oacute; được bộ gi&aacute;p của linh hồn linh th&uacute; qu&yacute; hiếm.</span></span></p>\r\n', 'Phê Kun', '2020-10-06 08:22:35', 1, 0, 156);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(51, 'Chapter 0.1: Phần mở đầu', 'chapter-01-phan-mo-djau', 35, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">D&ugrave; cho bạn l&agrave; một người bi quan hoặc l&agrave; người lạc quan, th&igrave; đối với cuộc sống của Derek Esposito cũng kh&ocirc;ng phải l&agrave; tốt hay tệ. N&oacute; cũng chỉ l&agrave; sự tồn tại nhỏ nhặt kh&ocirc;ng như bao người kh&aacute;c.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bố của cậu ta l&agrave; một g&atilde; đ&agrave;n &ocirc;ng c&oacute; xu hướng lạm dụng lưỡng cực, &ocirc;ng ta c&oacute; khả năng tự nhốt m&igrave;nh trong ph&ograve;ng suốt nhiều ng&agrave;y trời trong suốt giai đoạn bị trầm cảm. &Ocirc;ng ta chỉ dậy để ăn, d&ugrave;ng nh&agrave; vệ sinh v&agrave; thỉnh thoảng lại &ldquo;l&agrave;m cho cuộc sống của bạn khổ sở&rdquo; với những trận giận dữ.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ở giai đoạn hưng phấn, &ocirc;ng ta sẽ l&agrave;m việc một c&aacute;ch đi&ecirc;n cuồng, nhưng chẳng c&oacute; t&agrave;i năng g&igrave; như một doanh nh&acirc;n cũng như những kẻ c&oacute; quen biết trong x&atilde; hội, &ocirc;ng ta điều kh&ocirc;ng c&oacute; khả năng l&agrave;m n&ecirc;n điều g&igrave; đ&oacute; th&agrave;nh c&ocirc;ng v&agrave; tạo ra một c&aacute;i kết nối đ&uacute;ng đắn được.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi ở trạng th&aacute;i tự nhi&ecirc;n, khi m&agrave; &ocirc;ng ta c&oacute; sử dụng thuốc, th&igrave; &ocirc;ng ta giống như một kẻ b&igrave;nh thường đứng l&ecirc;n đi l&agrave;m việc chỉ để tr&aacute;nh những lời ph&agrave;n n&agrave;n từ h&agrave;ng x&oacute;m.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Những khi n&agrave;o m&agrave; &ocirc;ng ta ở t&igrave;nh trạng&nbsp; t&acirc;m thần, th&igrave; &ocirc;ng l&agrave; một v&iacute; dụ điển h&igrave;nh của một kẻ lạm dụng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Những đứa con của &ocirc;ng ta lu&ocirc;n l&agrave; c&aacute;i gai trong mắt của &ocirc;ng ấy vậy.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ch&uacute;ng kh&ocirc;ng bao giờ chịu học h&agrave;nh đầy đủ, kh&ocirc;ng bao giờ c&oacute; kỷ luật, v&agrave; dĩ nhi&ecirc;n l&agrave; kh&ocirc;ng thể hiện được sự t&ocirc;n trọng d&agrave;nh cho &ocirc;ng ta.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave; &ocirc;ng ta lu&ocirc;n nghĩ sai về ch&uacute;ng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&Ocirc;ng ta sẵn s&agrave;ng h&eacute;t v&agrave;o mặt ch&uacute;ng cho d&ugrave; những lỗi nhỏ nhặt, li&ecirc;n tục n&oacute;i ch&uacute;ng chỉ những k&yacute; sinh lu&ocirc;n h&uacute;t m&aacute;u &ocirc;ng ta.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave; dĩ nhi&ecirc;n lời n&oacute;i kh&ocirc;ng đủ th&igrave; &ocirc;ng ta sẽ d&ugrave;ng đến d&acirc;y nịt để dạy dỗ ch&uacute;ng khi ch&uacute;ng c&oacute; điểm thấp hay kh&ocirc;ng l&agrave;m việc nh&agrave;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&igrave; thế, Derek v&agrave; Carl đ&atilde; phải nhanh học c&aacute;ch bảo vệ bản th&acirc;n, từ khi người mẹ lơ đ&atilde;ng của ch&uacute;ng thực tế qu&ecirc;n đi ch&uacute;ng từ khi sinh ra chỉ để t&igrave;m đến sự im lặng v&agrave; b&igrave;nh thản, b&agrave; ta tr&aacute;nh xa người bạn đời của m&igrave;nh xa nhất c&oacute; thể để kh&ocirc;ng bị vạ l&acirc;y.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek cũng chỉ mới hai tuổi, v&agrave; tuyệt vọng kh&ocirc;ng thể chăm s&oacute;c đứa em trai nhỏ của m&igrave;nh nhưng mọi thứ điều kh&ocirc;ng c&oacute; kết quả.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mỗi tuần ch&uacute;ng đều bị bắt đi đến nh&agrave; thờ cầu nguyện ch&uacute;a v&agrave; con trai của ch&uacute;a, vị cứu tinh của nh&acirc;n loại. Nhưng d&ugrave; cầu nguyện cỡ n&agrave;o đi chăng nữa th&igrave; ph&eacute;p m&agrave;u cũng kh&ocirc;ng xảy ra.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cho n&ecirc;n ch&uacute;ng ngừng tin v&agrave;o những anh h&ugrave;ng v&agrave; tốn thời gian với những lời cầu nguyện được nhồi nh&eacute;t v&agrave;o đầu.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trường học l&agrave; nơi duy nhất an to&agrave;n đối với ch&uacute;ng, nhưng n&oacute; chỉ k&eacute;o d&agrave;i khi ch&uacute;ng l&ecirc;n lớp s&aacute;u.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi ở trường trung học, chẳng mất một th&aacute;ng ch&uacute;ng đ&atilde; bị bọn trong trường bắt nạt.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ch&uacute;ng dễ d&agrave;ng trở th&agrave;nh mục ti&ecirc;u bị bắt nạt do quần &aacute;o rẻ r&aacute;ch v&agrave; tầng lớp của ch&uacute;ng. Ch&uacute;ng bị h&agrave;nh hạ v&agrave; bị sỉ nhục nhưng kh&ocirc;ng thể đ&aacute;nh trả được.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong một khoảng thời gian d&agrave;i, Derek nhận ra rằng đ&oacute; l&agrave; khoảng thời gian đen tối v&ocirc; dụng nhất của cậu ta. Sau một th&aacute;ng, cậu ta đ&atilde; kh&ocirc;ng thể chịu được nữa v&agrave; đ&atilde; cố l&agrave;m mọi chuyện tốt hơn.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cậu ta b&aacute;o với những người bảo trợ x&atilde; hội về người cha lạm dụng của m&igrave;nh bằng một email nặc danh, nhưng những người l&agrave;m c&ocirc;ng t&aacute;c x&atilde; hội qu&aacute; bận v&agrave; thiếu nh&acirc;n lực họ chỉ đến thăm một lần v&agrave; kh&ocirc;ng bao giờ trở lại.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau đ&oacute; cậu ta muốn chấm dứt việc m&igrave;nh bị bắt nạt ở trường bằng c&aacute;ch b&aacute;o những t&ecirc;n bắt nạt m&igrave;nh với một gi&aacute;o vi&ecirc;n, v&agrave; b&aacute;o l&ecirc;n cho ban gi&aacute;m hiệu. Nhưng hiệu trưởng lại kh&ocirc;ng muốn can thiệp v&agrave;o những thứ m&agrave; &ocirc;ng cho l&agrave; tr&ograve; đ&ugrave;a của bọn trẻ, cho n&ecirc;n &ocirc;ng gọi điện b&aacute;o về cho cha mẹ của Derek về vấn đề xảy ra, hy vọng họ sẽ bỏ qua. V&agrave; &iacute;t nhất đ&oacute; l&agrave; điều m&agrave; &ocirc;ng hiệu trưởng muốn.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Về phần Derek, đương nhi&ecirc;n bị ăn đ&ograve;n nhiều hơn do&nbsp; cậu ta kh&ocirc;ng đủ bản lĩnh để đối mặt những vấn đề của cậu ta.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;M&agrave;y thật l&agrave; ngu ngốc khi chẳng thể học g&igrave; từ tao. Kh&ocirc;ng bao giờ nhượng bộ, nếu m&agrave;y muốn điều g&igrave; đ&oacute; th&igrave; h&atilde;y tự m&igrave;nh l&agrave;m đi!&rdquo;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek chưa bao giờ cảm thấy tuyệt vọng v&agrave; v&ocirc; dụng như l&uacute;c n&agrave;y, cậu ta đ&atilde; kh&oacute;c sưng mắt cho đến khi ngủ thiếp đi. Điều đ&oacute; l&agrave; l&agrave;m cho giọt nước tr&agrave;n ly.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ng&agrave;y tiếp theo cậu ta cảm thấy kh&aacute;c lạ, minh mẫn hơn bao giờ hết. B&acirc;y giờ kh&ocirc;ng phải l&uacute;c để tuyệt vọng, cậu ta cần một kế hoạch trả th&ugrave;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&atilde; mất nhiều năm khi m&agrave; cậu ta nhận ra rằng c&oacute; những thứ đ&atilde; chết đi b&ecirc;n trong cậu ta. Cậu ta đ&atilde; kh&ocirc;ng c&ograve;n tin tưởng, hy vọng hay tốt bụng với bất k&igrave; ai. Xung quanh cậu ta đều l&agrave; kẻ th&ugrave;, v&agrave; muốn tồn tại cậu ta phải chiến đấu.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&igrave; vậy Derek đ&atilde; xin cha m&igrave;nh học v&otilde; ở một v&otilde; đường, v&agrave; cậu ta ngạc nhi&ecirc;n rằng cậu ta kh&ocirc;ng cần phải cầu xin hay hỏi lại lần thứ hai. &Ocirc;ng gi&agrave; của cậu ta vui vẻ v&igrave; cuối c&ugrave;ng một đứa con ngu ngốc v&agrave; ngờ nghệch của &ocirc;ng ta cũng chịu trở th&agrave;nh một người đ&agrave;n &ocirc;ng. Điều kiện duy nhất của cha Derek đ&oacute; l&agrave; cậu ta kh&ocirc;ng được bỏ cuộc &iacute;t nhất một năm bằng kh&ocirc;ng th&igrave; cậu ta phải tự m&igrave;nh trả tiền học ph&iacute;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek kh&ocirc;ng chỉ học v&otilde; aikido cả ng&agrave;y, cậu ta c&ograve;n thức sớm hơn hai tiếng mỗi ng&agrave;y để h&iacute;t đất, squat, chạy cho đến khi cậu ta thở kh&ocirc;ng ra hơi.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong v&ograve;ng v&agrave;i th&aacute;ng cậu ta đ&atilde; c&oacute; thể h&iacute;t đất 100 c&aacute;i, tập đ&ugrave;i v&agrave; squat, chạy mỗi ng&agrave;y 10km trước khi đến trường.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&otilde; aikido đ&atilde; sớm phơi b&agrave;y đ&oacute; l&agrave; sự lựa chọn ho&agrave;n hảo ở t&igrave;nh trạng của cậu ta. Ở mức độ vỡ l&ograve;ng chỉ tập trung chủ yếu về tự vệ bản th&acirc;n nhưng lại c&oacute; nhiều khoảng trống để tấn c&ocirc;ng v&agrave; đ&aacute;nh trả.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi tập luyện v&otilde;, cậu ta cũng nhận ra được m&igrave;nh giỏi c&aacute;i g&igrave; nhất. Cậu ta kh&ocirc;ng phải l&agrave; người đặc biệt nhanh nhẹn, cũng kh&ocirc;ng phải người tiếp thu nhanh. Cậu ta c&oacute; khả năng ph&aacute;n đo&aacute;n t&igrave;nh huống để ra đ&ograve;n v&agrave; canh chuẩn thời gian ra chi&ecirc;u hay d&ugrave;ng đến tiểu xảo.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay cả khi thầy đang dạy cậu ta về kiếm kỹ v&agrave; kỹ năng dao găm. Th&igrave; Derek lu&ocirc;n nắm được những đ&ograve;n quyết định ngay từ lần đầu ti&ecirc;n, thỉnh thoảng lại hiểu được trước khi thầy dạy xong.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&oacute; cũng l&agrave; điều vui mừng cũng như đ&aacute;ng thất vọng v&igrave; t&agrave;i năng của cậu ta kh&ocirc;ng c&oacute; g&igrave; để d&ugrave;ng trong luyện tập. Ngay cả khi v&otilde; aikido l&agrave; một m&ocirc;n thể thao với những giải đấu với một điều cấm đ&aacute;nh v&agrave;o v&ugrave;ng h&aacute;ng, mắt v&agrave; kh&iacute; quản.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau nhiều th&aacute;ng tập luyện gian khổ trong khi tiếp tục k&iacute;n đ&aacute;o trốn tr&aacute;nh trong trường, cậu ta l&ecirc;n kế hoạch cho bước tiếp theo của m&igrave;nh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi học kỳ đầu ti&ecirc;n kết th&uacute;c, Derek dừng trốn tr&aacute;nh những t&ecirc;n bắt nạt v&agrave; bắt đầu trả lời những lời nhục mạ của bọn ch&uacute;ng d&agrave;nh cho cậu ta, D&ugrave;ng những c&acirc;u n&oacute;i m&agrave; cậu ta đ&atilde; t&igrave;m thấy tr&ecirc;n mạng. Derek cảnh gi&aacute;c kh&ocirc;ng bao giờ bước v&agrave;o nh&agrave; vệ sinh hoặc kh&ocirc;ng n&aacute;n lại một m&igrave;nh qu&aacute; l&acirc;u, Lu&ocirc;n lu&ocirc;n đứng gần những người lớn đang nh&igrave;n thấy được m&igrave;nh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chẳng mất chưa đầy một ng&agrave;y trước khi những kẻ bắt nạt bắt đầu s&ocirc;i m&aacute;u của m&igrave;nh l&ecirc;n. Chỉ khi những d&ograve;ng m&aacute;u n&oacute;ng của ch&uacute;ng hiện r&otilde; tr&ecirc;n mặt bọn ch&uacute;ng, cậu ta bắt đầu giăng bẫy.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Tao chịu đủ ch&uacute;ng m&agrave;y lắm rồi, bọn khốn &agrave;. Gặp tao sau một giờ đằng sau cửa h&agrave;ng tạp h&oacute;a giữa phố Lincoln v&agrave; phố số 3. Hoặc ch&uacute;ng m&agrave;y l&agrave; lũ nh&aacute;t c&aacute;y&rdquo;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;V&igrave; m&agrave;y đ&atilde; t&igrave;m đến c&aacute;i chết, điều ước của m&agrave;y sẽ th&agrave;nh hiện thực thằng mọi &agrave;. Chỉ một m&igrave;nh m&agrave;y đấu với ba ch&uacute;ng tao, chơi kh&ocirc;ng?&rdquo;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek gật đầu kh&ocirc;ng tin t&ecirc;n bắt nạt một ch&uacute;t n&agrave;o. V&agrave; cậu ta đ&atilde; đ&uacute;ng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi bọn ch&uacute;ng bước v&agrave;o con hẻm, bọn ch&uacute;ng c&ograve;n dẫn th&ecirc;m hai người nữa.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek đang đợi bọn ch&uacute;ng, dựa v&agrave;o tường ở cuối con hẻm.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Ch&uacute;ng m&agrave;y đ&acirc;y rồi. Tao đang bắt đầu nghĩ ch&uacute;ng bắt đầu sợ kh&ocirc;ng d&aacute;m đến.&rdquo;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bọn ch&uacute;ng cười ph&aacute; l&ecirc;n. &ldquo;Xin lỗi v&igrave; đến trễ. Hy vọng m&agrave;y kh&ocirc;ng phiền nếu tụi tao đem th&ecirc;m v&agrave;i bạn để vui vẻ buổi tiệc&rdquo;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek nh&uacute;ng vai trong khi cười toe to&eacute;t.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Kh&ocirc;ng vấn đề g&igrave;. Cho d&ugrave; bao nhi&ecirc;u th&igrave; r&aacute;c rưởi cũng chỉ l&agrave; r&aacute;c rưởi. Tao chọn con hẻm n&agrave;y v&igrave; ở đ&acirc;y c&oacute; đầy đủ th&ugrave;ng r&aacute;c cho tất cả lũ ch&uacute;ng m&agrave;y.&rdquo;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&acirc;u n&oacute;i sau c&ugrave;ng của cậu ta l&agrave;m ch&uacute;ng tức đi&ecirc;n l&ecirc;n xong thẳng v&agrave;o cậu ta một c&aacute;ch m&ugrave; qu&aacute;ng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Bao v&acirc;y n&oacute; c&aacute;c cậu đừng để n&oacute; tho&aacute;t! Để xem thằng n&agrave;o l&agrave; r&aacute;c rưởi.&rdquo;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave; như thế, bọn bắt nạt đ&atilde; rơi v&agrave;o bẫy của Derek. Derek đ&atilde; chuẩn bị địa h&igrave;nh con hẻm n&agrave;y, n&oacute; l&agrave; một nơi dễ d&agrave;ng chiến đấu. Một hẻm cụt kh&ocirc;ng lối tho&aacute;t cho bọn bắt nạt, bọn ch&uacute;ng cũng chẳng để &yacute; g&igrave; ngo&agrave;i &aacute;nh s&aacute;ng mờ mờ.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hai t&ecirc;n đầu ti&ecirc;n gục xuống nền gạch, v&agrave; bọn ch&uacute;ng lo lắng để kh&ocirc;ng phải giẫm đạp l&ecirc;n bạn của ch&uacute;ng v&igrave; ch&uacute;ng kh&ocirc;ng thể n&agrave;o ngờ được những đ&ograve;n đ&aacute;nh đến từ ống sắt.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bọn bắt nạt đ&ocirc;ng thật. Nhưng Derek lại trang bị đầy đủ. Cậu ta d&ugrave;ng&nbsp; ống sắt như một c&acirc;y kiếm đ&aacute;nh thật mạnh v&agrave;o đầu, b&ecirc;n gối, v&agrave; v&ugrave;ng h&aacute;ng. Chỉ ngay sau khi cậu ta đ&aacute;nh, hai t&ecirc;n c&ograve;n lại đang cố gắng gượng dậy.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong khi bọn ch&uacute;ng nằm r&ecirc;n rỉ v&agrave; kh&oacute;c l&oacute;c, cậu ta lấy con dao nhỏ cắt đoạn d&acirc;y điện, sau đ&oacute; cậu bắt đầu đ&aacute;nh đi đ&aacute;nh lại những v&ugrave;ng đặc biệt bằng ống sắt.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong l&ograve;ng của Derek biết rằng những điều cậu ta đang l&agrave;m l&agrave; sai tr&aacute;i, nhưng cậu ta kh&ocirc;ng quan t&acirc;m. Nếu thế giới n&agrave;y sinh ra đ&atilde; bất c&ocirc;ng, th&igrave; c&aacute;ch duy nhất l&agrave; biến điều đ&oacute; th&agrave;nh lợi thế của cậu.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&igrave; thế, cậu ta lấy trong m&igrave;nh ra một c&acirc;y ch&iacute;ch điện m&agrave; cậu ta mượn từ cha cậu ch&iacute;ch ch&uacute;ng bất tỉnh. Sau đ&oacute; cậu ta tr&oacute;i ch&uacute;ng lại v&agrave; lột đồ của bọn bắt nạt ra chụp nhiều tấm h&igrave;nh v&agrave; ngay cả quay phim lại sau khi sắp xếp lại giống như bọn ch&uacute;ng đang đ&aacute;nh nhau vậy. Sau đ&oacute; cậu ta hất một x&ocirc; nước lạnh v&agrave;o ch&uacute;ng v&agrave; bắt đầu thỏa thuận.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Xin lỗi v&igrave; ph&aacute; hỏng giấc mơ đẹp của ch&uacute;ng m&agrave;y, nhưng tao cần ch&uacute;ng m&agrave;y ch&uacute; &yacute; một ch&uacute;t.&rdquo;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi bọn bắt nạt tỉnh lại, Cơn đau dữ dội l&agrave;m ch&uacute;ng kh&ocirc;ng nhận ra m&igrave;nh đang trần truồng v&agrave; đang đối mặt nhau. Nhận ra Derek đang cầm tr&ecirc;n tay ống sắt, bọn ch&uacute;ng bắt đầu im lặng v&agrave; nghe cậu ta.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Tao vừa l&agrave;m một cuốn album kỉ niệm cho bọn m&agrave;y, v&agrave; cũng c&oacute; một bộ phim ngắn, đ&atilde; đăng l&ecirc;n tr&ecirc;n m&aacute;y t&iacute;nh của tao v&agrave; thậm ch&iacute; l&agrave; bộ nhớ tr&ecirc;n mạng. V&agrave; n&oacute; sẽ rất khủng khiếp nếu một ai đ&oacute;, tao kh&ocirc;ng biết, tao chẳng hạn, v&iacute; dụ upload ch&uacute;ng l&ecirc;n những trang ảnh lớn nhất tr&ecirc;n mạng. Như ch&uacute;ng m&agrave;y thấy đấy, internet kh&ocirc;ng bao giờ qu&ecirc;n.&rdquo;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bọn bắt nạt bắt đầu kh&oacute;c l&oacute;c v&agrave; van xin.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Tưởng tượng những bức ảnh khủng khiếp thế n&agrave;o! Khi m&agrave; một số người cố t&igrave;nh tra t&ecirc;n của bọn m&agrave;y, c&oacute; thể l&agrave; b&agrave;, bạn g&aacute;i của ch&uacute;ng m&agrave;y hoặc l&agrave; những trường đại học m&agrave; ch&uacute;ng m&agrave;y định đăng k&yacute; nhập học v&agrave;o, điều đầu ti&ecirc;n thật kinh khủng khi những tấm h&igrave;nh đ&oacute; xuất hiện l&ecirc;n sẽ thế n&agrave;o?&rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Anh bạn, đừng!&rdquo; &ldquo;L&agrave;m ơn, t&ocirc;i c&ograve;n chẳng biết cậu l&agrave; ai. T&ocirc;i chỉ muốn gi&uacute;p bạn của m&igrave;nh!&rdquo; &ldquo;Đ&oacute; chỉ l&agrave; một tr&ograve; đ&ugrave;a th&ocirc;i, l&agrave;m ơn tha cho t&ocirc;i!&rdquo;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một d&agrave;n hợp xướng cầu xin cậu ta. Derek chỉ muốn n&ocirc;n v&agrave;o mặt c&aacute;i th&oacute;i đạo đức giả của bọn ch&uacute;ng.&nbsp;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Tao chẳng quan t&acirc;m đến những c&acirc;u van xin đ&aacute;ng thương của bọn m&agrave;y! Bắt đầu từ h&ocirc;m nay, bọn m&agrave;y sẽ để tao y&ecirc;n. V&agrave; tốt hơn l&agrave; bọn n&ecirc;n cầu nguyện để tao chẳng gặp chuyện g&igrave;, v&igrave; cloud của tao đ&atilde; c&agrave;i đặt nếu mỗi ng&agrave;y tao kh&ocirc;ng nhập mật khẩu v&agrave;o, th&igrave; n&oacute; sẽ upload l&ecirc;n tất cả mọi nơi.&rdquo;</span></span><br />\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chẳng đợi bọn ch&uacute;ng trả lời, cậu ta quay mặt v&agrave; đi.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Su&yacute;t nữa th&igrave; qu&ecirc;n mất, Tao đ&atilde; vất đồ của ch&uacute;ng m&agrave;y v&agrave;o hố r&aacute;c rồi, kh&ocirc;ng nhớ l&agrave; đồ n&agrave;o của ch&uacute;ng m&agrave;y. Nếu ch&uacute;ng m&agrave;y kh&ocirc;ng muốn về nh&agrave; với bộ dạng n&agrave;y của ch&uacute;ng m&agrave;y th&igrave; tốt hơn l&agrave; bắt đầu đ&agrave;o đi. Tạm biệt, lũ khốn!&rdquo;</span></span><br />\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2fc4fe30-7fff-ff34-9e53-479972a48fd3\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek trở về nh&agrave; trong vui vẻ, gần như h&aacute;t ng&acirc;n nga. Cậu ta chưa bao giờ cảm thấy tự h&agrave;o bản th&acirc;n như b&acirc;y giờ vậy v&agrave; nghĩ rằng cậu ta sẽ kh&ocirc;ng bao giờ gặp lại bọn ch&uacute;ng nữa.</span></span></p>\r\n', 'Phê Kun', '2020-10-05 04:56:17', 1, 0, 123);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(52, 'Chapter 0.2: Phần mở đầu 2', 'chapter-02-phan-mo-djau-2-1', 35, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Những vụ bắt nạt đ&atilde; lắng xuống, nhưng cuộc sống x&atilde; hội của cậu ta cũng chưa hết khổ sở được. sao cậu ta c&oacute; thể kết bạn khi m&agrave; cuộc sống ri&ecirc;ng tư của m&igrave;nh xấu hổ đến vậy.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cậu ta phải thường xuy&ecirc;n giấu những vết đ&aacute;nh d&agrave;i tr&ecirc;n tay m&igrave;nh v&agrave; những vết bầm tr&ecirc;n mắt nhiều th&aacute;ng trước đ&oacute;. Những người bạn c&ugrave;ng lớp v&agrave; thầy c&ocirc; đều đơn giản giả vờ như kh&ocirc;ng để &yacute; v&agrave; cậu ta cũng giả vờ tin rằng m&igrave;nh l&agrave; một kẻ n&oacute;i dối tốt.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Điều đ&oacute; thật đau đớn, nhưng dễ kiểm so&aacute;t. Cậu ta vẫn c&ograve;n đứa em trai nhỏ của m&igrave;nh để chia sẻ những niềm đau ấy. Carl l&agrave; tất cả g&igrave; cậu ta c&oacute;, gia đ&igrave;nh, bạn b&egrave;. Cậu ta l&agrave; cả thế giới của Derek.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng khi đến độ tuổi trưởng th&agrave;nh, cậu ta bắt đầu th&iacute;ch một c&ocirc; g&aacute;i c&ugrave;ng trường, v&agrave; những cảm x&uacute;c đ&oacute; kh&ocirc;ng dễ bị dập tắt. Lại một lần nữa, cậu ta cảm thấy ho&agrave;n to&agrave;n bất lực.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cậu ta kh&ocirc;ng thể chia sẻ c&ugrave;ng ai về vấn đề của m&igrave;nh, kh&ocirc;ng thể để cho ai đ&oacute; th&acirc;n thiết với m&igrave;nh, nếu kh&ocirc;ng cậu ta phải nghĩ đến những lời giải th&iacute;ch v&igrave; kh&ocirc;ng được mang những người bạn của m&igrave;nh về nh&agrave;. N&oacute;i g&igrave; đến người con g&aacute;i m&agrave; cậu ta th&iacute;ch.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek như đang bị cực h&igrave;nh bởi những suy nghĩ ti&ecirc;u cực, thường đ&ograve;i hỏi Ch&uacute;a tại sao những người kh&aacute;c đều được c&aacute;i họ muốn nhưng đến cậu ta lại l&agrave; giấc mơ xa vời.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Suy nghĩ của tuổi mới lớn b&ecirc;n trong Derek ph&aacute;t triển gay gắt. Cậu ta bắt đầu kh&oacute; ngủ v&igrave; những suy nghĩ về những cặp đ&ocirc;i b&igrave;nh thường ngo&agrave;i kia.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek biết rằng cậu ta kh&aacute;c biệt họ rất nhiều. Cậu ta kh&ocirc;ng c&oacute; biểu hiện của sự ngại ng&ugrave;ng hoặc những triệu chứng về mụn. Vấn đề của cậu ta kh&ocirc;ng b&aacute;c sĩ n&agrave;o c&oacute; thể chữa được, V&igrave; n&oacute; vừa c&oacute; t&ecirc;n v&agrave; họ. Ezio Esposito, người cha cặn b&atilde; của cậu ta.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek đ&atilde; l&ecirc;n kế hoạch để trừ khử &ocirc;ng ta, ngay cả nếu giết được &ocirc;ng ta. Ban đ&ecirc;m cậu ta l&ecirc;n kế hoạch c&ocirc;ng phu v&agrave; tỉ mỉ li&ecirc;n quan đến tra tấn &ocirc;ng gi&agrave; của cậu ta, l&agrave;m &ocirc;ng ta đau đớn như &ocirc;ng ta đ&atilde; l&agrave;m cho Derek trong nhiều năm qua.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng khi trời s&aacute;ng, cậu ta nhận ra đ&oacute; l&agrave; một kế hoạch ngu ngốc. N&oacute; chẳng kh&aacute;c g&igrave; l&agrave; ước mơ.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek qu&ecirc;n rằng &yacute; nghĩa quan trọng v&agrave; điều xảy ra khi loại bỏ Ezio. Cho d&ugrave; cậu ta gh&eacute;t &ocirc;ng ta đến nhường n&agrave;o th&igrave; &ocirc;ng ta vẫn l&agrave; bố của m&igrave;nh v&agrave; đ&oacute; chỉ l&agrave; cơn giận nhất thời của tuổi teen.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giết Ezio? Chắc chắn rồi. V&agrave; sau đ&oacute; th&igrave; sao? Sống cả đời trong tội lỗi &agrave;? Chạy trốn khỏi nh&agrave; để kh&ocirc;ng bị cảnh s&aacute;t bắt? V&agrave; tiền đ&acirc;u m&igrave;nh sống?</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giết Ezio cũng c&oacute; nghĩa l&agrave; từ bỏ Carl m&atilde;i m&atilde;i, v&agrave; c&oacute; thể sẽ ph&aacute; hủy mọi kế hoạch hạnh ph&uacute;c trong tương lai.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&igrave; vậy, mỗi buổi s&aacute;ng Derek nuốt những kế hoạch cậu ta lại trong m&igrave;nh v&agrave; tiếp tục bước tiếp. Điều cậu ta cần l&agrave;m b&acirc;y giờ l&agrave; học thật giỏi, lấy được học bổng v&agrave; biến khỏi nơi đ&oacute; c&agrave;ng nhanh c&agrave;ng tốt.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thời gian tr&ocirc;i qua, trong nh&aacute;y mắt đ&atilde; hết m&ugrave;a h&egrave; trước khi Derek kết th&uacute;c năm học đầu ti&ecirc;n. Mặc cho nhiệt độ n&oacute;ng thế n&agrave;o, tr&aacute;i tim của cậu ta vẫn lạnh như băng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek đ&atilde; đến giai đoạn trưởng th&agrave;nh. Kết hợp những m&ocirc;n v&otilde; v&agrave; tập luyện cơ bắp m&agrave; cậu ta c&oacute; được đ&atilde; cho cậu ta một cơ thể săn chắc.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ezio cũng nhận ra điều đ&oacute;, v&agrave; trở n&ecirc;n nh&uacute;t nh&aacute;t khi muốn đ&aacute;nh Derek v&agrave; chuyển Carl l&agrave;m mục ti&ecirc;u mới.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek đ&atilde; đứng l&ecirc;n bảo vệ em trai m&igrave;nh, nhưng kết quả vẫn bị đ&aacute;nh v&igrave; Ezio mua một c&acirc;y trực đ&ecirc;m của qu&acirc;n đội địa phương thay v&igrave; bị đ&aacute;nh bằng tay kh&ocirc;ng của Ezio.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cậu ta cứu lấy Carl bằng c&aacute;ch kh&oacute;c l&oacute;c v&agrave; van xin, v&agrave; chỉ v&igrave; những điều n&agrave;y sẽ l&agrave;m cho h&agrave;ng x&oacute;m của họ để &yacute;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&igrave; thế, mỗi khi m&agrave; Ezio l&ecirc;n cơn thịnh nộ. Derek chỉ c&oacute; thể nh&igrave;n em trai m&igrave;nh bị đ&aacute;nh lần n&agrave;y đến lần kh&aacute;c.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau đ&oacute;, v&agrave;o một buổi s&aacute;ng Ezio nhận được một chuyến c&ocirc;ng t&aacute;c lớn trong năm. N&ecirc;n, l&atilde;o ta mặc cho m&igrave;nh một bộ đồ sang trọng mặc cho c&aacute;i n&oacute;ng v&agrave; dặn Derek chuẩn bị vali l&agrave;m việc v&agrave; laptop cho l&atilde;o ta.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ezio thật sự vội v&agrave;ng, l&atilde;o ta kh&ocirc;ng muốn những người kh&aacute;c ăn miếng b&aacute;nh n&agrave;y. Đ&acirc;y c&oacute; thể sẽ l&agrave; điều khiến cho sự nghiệp của &ocirc;ng ta l&agrave;m ủy vi&ecirc;n trong tương lai.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek chạy đến c&aacute;nh cửa, Ezio đ&atilde; đợi ở đ&oacute;, v&agrave; gi&uacute;p l&atilde;o ta chỉnh lại t&uacute;i đựng laptop.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ezio chạy đến thang m&aacute;y, nhấn li&ecirc;n tục như một g&atilde; đi&ecirc;n cuồng nhưng c&aacute;i đ&egrave;n chỉ c&ograve;n hiện m&agrave;u đỏ.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&atilde;o ta nguyền rủa c&aacute;i khu căn hộ m&igrave;nh sống, lũ quản l&yacute; v&agrave; sự may mắn của m&igrave;nh, l&atilde;o ta lao m&igrave;nh đến cầu thang.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave; khi Derek chợt nhận ra. Cậu ta quay đầu chạy theo cha của m&igrave;nh, Derek nhận ra những vệt s&aacute;ng b&oacute;ng phản chiếu dưới nền h&agrave;nh lang.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ezio vừa đi trước cậu ta khi cậu ta nhận ra người lao c&ocirc;ng đ&atilde; lau s&agrave;n nh&agrave; m&agrave; qu&ecirc;n để biển cảnh b&aacute;o s&agrave;n ướt.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tất cả diễn ra trong t&iacute;ch tắc. Ezio sải một bước d&agrave;i nặng nhọc, to&agrave;n bộ trọng lượng của l&atilde;o ta dồn xuống ph&iacute;a trước v&agrave; sau đ&oacute; ch&acirc;n l&atilde;o bị trượt.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek đ&atilde; c&oacute; thể cảnh b&aacute;o cho l&atilde;o, c&oacute; thể vươn tay ra để chặn được c&uacute; ng&atilde; nhưng cậu ta chọn c&aacute;ch đứng im.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ezio ng&atilde; xuống cầu thang, bật từng hồi xuống những bậc thang. Cơ thể l&atilde;o ta g&atilde;y ra từng mảnh, tất cả c&aacute;c chi bị g&atilde;y uốn cong sang mọi hướng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Như mọi đứa trẻ vị th&agrave;nh ni&ecirc;n kh&aacute;c, Derek cũng c&oacute; cho m&igrave;nh một chiếc smartphone, v&igrave; thế cậu ta bắt đầu chụp nhiều tấm ảnh để l&agrave;m bằng chứng l&agrave; s&agrave;n nh&agrave; bị ướt v&agrave; kh&ocirc;ng c&oacute; biển b&aacute;o cảnh b&aacute;o.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong đầu cậu ta đ&atilde; l&ecirc;n kế hoạch thu&ecirc; một luật sư tốt nhất để kiện b&ecirc;n quản l&yacute; của khu chung cư chịu tr&aacute;ch nhiệm.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chỉ khi sau đ&oacute; cậu ta bước cẩn thận xuống cầu thang để x&aacute;c thực c&aacute;i chết của l&atilde;o Ezio. L&atilde;o ta nằm đ&oacute;, kh&ocirc;ng thể r&ecirc;n rỉ hay k&ecirc;u sự gi&uacute;p đỡ, nhưng đ&ocirc;i mắt của l&atilde;o ta vẫn d&aacute;n v&agrave;o Derek, cầu xin cậu ta gi&uacute;p m&igrave;nh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek cười toe to&eacute;t v&agrave;o mặt l&atilde;o ta v&agrave; n&oacute;i: &ldquo;&Ocirc;ng thực sự nghĩ t&ocirc;i kh&ocirc;ng học được g&igrave; từ &ocirc;ng ư? &Ocirc;ng đ&atilde; dạy cho t&ocirc;i một lần, kh&ocirc;ng bao giờ ph&oacute; th&aacute;c cho kẻ kh&aacute;c. Nếu &ocirc;ng muốn l&agrave;m điều g&igrave; đ&oacute; đ&uacute;ng, th&igrave; tự m&igrave;nh l&agrave;m đi. Điện thoại của &ocirc;ng nằm trong t&uacute;i, lấy n&oacute; ra v&agrave; gọi 911 đi. T&ocirc;i chỉ l&agrave; đứa con thất bại, t&ocirc;i kh&ocirc;ng muốn l&agrave;m phiền &ocirc;ng, bố &agrave;&rdquo;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&ocirc;i mắt của Ezio đầy kinh ngạc v&agrave; căm phẫn, nhưng điều đ&oacute; chỉ k&eacute;o d&agrave;i kh&ocirc;ng l&acirc;u. đầu l&atilde;o ta mềm nhũn v&agrave; đ&ocirc;i mắt đ&atilde; v&ocirc; hồn.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một tiếng cười su&yacute;t nữa đ&atilde; thốt l&ecirc;n, nhưng Derek đ&atilde; kiếm n&eacute;n n&oacute; lại. Thay v&agrave;o đ&oacute; cậu ta bắt đầu h&eacute;t l&ecirc;n để được gi&uacute;p đỡ v&agrave;o đứa con trai khủng bố ấy.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Như thường lệ, mẹ của Derek qu&aacute; đau buồn với vấn đề của b&agrave; ta, cảm x&uacute;c, v&agrave; bận t&acirc;m t&igrave;m luật sư, đ&atilde; giao tất cả lại cho Derek lo, v&igrave; Ezio đ&atilde; kh&ocirc;ng c&ograve;n nữa.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một cục đ&aacute; tr&uacute;ng ba con chim. Sau khi t&igrave;m hiểu kỹ lưỡng về vị luật sư tốt nhất m&agrave; họ c&oacute; thể trả, Cho n&ecirc;n Derek l&agrave; kh&aacute;ch h&agrave;ng của &ocirc;ng ta thay v&igrave; mẹ của cậu ta.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cậu ta kể tất cả những g&igrave; xảy ra với vị luật sư về tai nạn của bố cậu ta, sự lạm dụng, v&agrave; sự cần thiết để số tiền sẽ chia đều cho cả ba người bọn họ, người mẹ v&agrave; hai đứa con.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cả Derek v&agrave; Carl đều rất ki&ecirc;n định với quyết định của m&igrave;nh, họ muốn luật sư bắt đầu quy tr&igrave;nh để được tự do. Với số tiền từ thừa kế v&agrave; bồi thường, họ c&oacute; đủ khả năng t&agrave;i ch&iacute;nh tự lập cho đến khi Derek c&oacute; thể chu cấp cả hai người.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Những g&igrave; tiếp theo l&agrave; thời kỳ hạnh ph&uacute;c nhất trong cuộc đời của họ. Đầu ti&ecirc;n, họ c&oacute; được quyền thừa kế, v&agrave; ngay sau đ&oacute; họ được tự do v&agrave; rời khỏi nơi ở của mẹ m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<br />\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chưa đầy một th&aacute;ng sau, họ đ&atilde; nhận được lời đề nghị giải quyết rất hậu hĩnh từ người quản l&yacute; t&ograve;a nh&agrave;. Họ sử dụng n&oacute; để ổn định v&agrave; lập kế hoạch th&iacute;ch hợp cho tương lai.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong những năm sau, c&ocirc;n đồ kh&ocirc;ng phải l&agrave; vấn đề.Derek v&agrave; Carl giờ đều l&agrave; những v&otilde; sĩ v&agrave; hỗ trợ lẫn nhau.Cuối c&ugrave;ng họ c&oacute; thể trải nghiệm c&oacute; bạn b&egrave; v&agrave; bạn g&aacute;i, cơn &aacute;c mộng gia đ&igrave;nh cuối c&ugrave;ng cũng kết th&uacute;c.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, họ đ&atilde; rất thất vọng v&igrave; c&oacute; qu&aacute; nhiều người được gọi l&agrave; bạn b&egrave; h&oacute;a ra lại l&agrave; những người muốn sử dụng ng&ocirc;i nh&agrave; kh&ocirc;ng c&oacute; cha mẹ của họ l&agrave;m nơi tổ chức tiệc t&ugrave;ng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngo&agrave;i ra, họ cũng gặp kh&oacute; khăn trong việc h&igrave;nh dung một thiếu ni&ecirc;n b&igrave;nh thường phải như thế n&agrave;o. Trong khi bạn b&egrave; c&ugrave;ng trang lứa dường như chỉ th&iacute;ch vui chơi v&agrave; trốn học, họ lại tập trung v&agrave;o việc học v&agrave; tiết kiệm hết mức c&oacute; thể.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek v&agrave; Carl sẽ d&agrave;nh những ng&agrave;y nghỉ v&agrave; kỳ nghỉ để l&agrave;m những c&ocirc;ng việc b&aacute;n thời gian để l&agrave;m chậm qu&aacute; tr&igrave;nh suy giảm t&agrave;i ch&iacute;nh ng&acirc;n h&agrave;ng của họ. Họ kh&ocirc;ng ngh&egrave;o, nhưng cũng kh&ocirc;ng gi&agrave;u.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Họ biết rằng họ phải tiết kiệm hết mức c&oacute; thể, đề ph&ograve;ng những điều kh&ocirc;ng may xảy đến với họ. Nhưng tất cả họ đều c&oacute; một cuộc sống tốt đẹp.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek nhận được học bổng v&agrave; lấy bằng cử nh&acirc;n h&oacute;a học, ngay sau đ&oacute; l&agrave; thạc sĩ h&oacute;a sinh. Carl cũng đang l&agrave;m tốt, đ&atilde; c&oacute; bằng kỹ sư, sẵn s&agrave;ng học l&ecirc;n thạc sĩ v&agrave; đ&atilde; c&oacute; h&ocirc;n th&ecirc;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">D&ugrave; thực sự hạnh ph&uacute;c cho đứa em trai nhỏ b&eacute; của m&igrave;nh nhưng đ&oacute; thực sự l&agrave; một c&aacute;i gai nhức nhối đối với Derek. Cậu ấy lu&ocirc;n l&agrave; người sống kh&eacute;p k&iacute;n hơn về mặt x&atilde; hội giữa hai người, v&agrave; kh&ocirc;ng bao giờ cố gắng đạt được một mối quan hệ ổn định, kể cả khi chưa học đại học.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek kh&ocirc;ng c&oacute; vấn đề g&igrave; khi n&oacute;i chuyện với c&aacute;c c&ocirc; g&aacute;i, vấn đề lu&ocirc;n nằm ở việc anh ta k&eacute;n chọn v&agrave; kết th&uacute;c ở việc anh ta kh&ocirc;ng thể tin tưởng. Cậu ấy đ&atilde; từng phải l&ograve;ng nhiều người, nhưng chưa bao giờ gọi l&agrave; y&ecirc;u.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&uacute;c đầu cậu ta đổ lỗi cho sự k&eacute;m may mắn của m&igrave;nh, lu&ocirc;n t&igrave;m thấy những c&ocirc; g&aacute;i n&ocirc;ng nổi hoặc ngốc nghếch. Sau khi t&igrave;m được nhiều hơn một c&ocirc; g&aacute;i tốt, anh ta đổ lỗi cho qu&aacute; khứ đen tối của m&igrave;nh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng khi đối mặt với mối quan hệ hạnh ph&uacute;c của Carl, cậu ta chỉ c&oacute; thể tự tr&aacute;ch m&igrave;nh. Trong khi Carl đ&atilde; phải chịu đựng những điều tương tự, th&igrave; Carl đ&atilde; trở n&ecirc;n mạnh mẽ v&agrave; thuần khiết hơn nhiều so với Derek.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek chỉ qu&aacute; sợ bị tổn thương một lần nữa để để bất kỳ mối quan hệ n&agrave;o ph&aacute;t triển tr&ecirc;n một mức nhất định, v&agrave; đ&oacute; l&agrave; kẻ th&ugrave; m&agrave; cậu kh&ocirc;ng thể đ&aacute;nh bại.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek chấp nhận c&ocirc;ng việc được trả lương hậu hĩnh đầu ti&ecirc;n m&agrave; cậu nhận được, l&agrave;m ca đ&ecirc;m của một c&ocirc;ng ty h&oacute;a chất lớn trong bộ phận QA(bộ phận kiểm tra chất lượng).</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Carl đ&atilde; chia sẻ với cậu ấy, như một m&oacute;n qu&agrave; tốt nghiệp, rằng cậu em&nbsp; đ&atilde; sẵn s&agrave;ng cầu h&ocirc;n vị h&ocirc;n phu của m&igrave;nh ngay khi cậu tốt nghiệp. Do đ&oacute;, Derek cần tiền v&igrave; anh chỉ c&oacute; hai năm để d&agrave;nh dụm đủ để tổ chức đ&aacute;m cưới tử tế cho em trai m&igrave;nh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek kh&ocirc;ng th&iacute;ch c&ocirc;ng việc hiện tại của m&igrave;nh, nhưng đ&oacute; l&agrave; một c&aacute;ch để bắt đầu sự nghiệp l&agrave;m việc v&agrave; lấp đầy chương tr&igrave;nh học của m&igrave;nh. Ngo&agrave;i ra, n&oacute; được trả rất cao, v&agrave; cậu ta quyết định sử dụng bảo hiểm của c&ocirc;ng ty để t&igrave;m kiếm sự tư vấn t&acirc;m l&yacute;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cậu ấy biết m&igrave;nh c&oacute; vấn đề v&agrave; muốn giải quyết ch&uacute;ng. Derek đ&atilde; tham khảo &yacute; kiến của một số b&aacute;c sĩ trước khi t&igrave;m được người m&agrave; cậu c&oacute; thể l&agrave;m việc c&ugrave;ng, v&agrave; sau v&agrave;i th&aacute;ng, cậu ta cảm thấy cuối c&ugrave;ng m&igrave;nh đ&atilde; bắt đầu đạt được một số tiến bộ.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8387b883-7fff-973f-2270-b9fb4a543712\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng cuối c&ugrave;ng, th&igrave; c&aacute;i hạnh ph&uacute;c ấy cũng kh&ocirc;ng k&eacute;o d&agrave;i m&atilde;i.</span></span></p>\r\n', 'Phê Kun', '2020-10-05 04:56:52', 1, 0, 139);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(53, 'Chương 1: Ngôi làng bên rừng', 'chuong-1-ngoi-lang-ben-rung-1', 8, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Nhị lăng tử&rdquo; mở mắt v&agrave; nh&igrave;n chằm chằm v&agrave;o b&ugrave;n v&agrave; m&aacute;i tranh tr&ecirc;n đầu. Chiếc chăn b&ocirc;ng phủ tr&ecirc;n người hắn c&oacute; m&agrave;u v&agrave;ng đậm v&agrave; c&oacute; m&ugrave;i mốc. N&oacute; đ&atilde; cũ đến mức kh&ocirc;ng c&ograve;n ph&acirc;n biệt được m&agrave;u sắc ban đầu.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&ecirc;n cạnh hắn ta l&agrave; nhị ca của hắn, H&agrave;n Tr&uacute;c, dường như đang ngủ say. Những tiếng ng&aacute;y li&ecirc;n tục xuất hiện khi anh ngủ.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&aacute;ch giường năm bước ch&acirc;n l&agrave; một bức tường đất đ&atilde; bị thời gian b&agrave;o m&ograve;n. Từ ph&iacute;a b&ecirc;n kia bức tường vọng lại giọng n&oacute;i nũng nịu của mẹ hắn v&agrave; đ&ocirc;i khi l&agrave; tiếng thở s&acirc;u của người cha đang h&uacute;t thuốc l&agrave;o.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Nhị lăng tử&quot; từ từ nhắm mắt lại, cố &eacute;p m&igrave;nh ngủ. Hắn biết rằng nếu hắn kh&ocirc;ng đi ngủ ngay b&acirc;y giờ, hắn sẽ kh&ocirc;ng thể dậy sớm v&agrave;o ng&agrave;y h&ocirc;m sau. Nếu thức dậy muộn, hắn sẽ kh&ocirc;ng thể c&ugrave;ng hảo huynh đệ của m&igrave;nh l&ecirc;n n&uacute;i h&aacute;i củi.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">T&ecirc;n thật của Nhị lăng tử l&agrave; H&agrave;n Lập. C&aacute;i t&ecirc;n tao nh&atilde; n&agrave;y kh&ocirc;ng phải do cha mẹ hắn đặt cho. Khi hắn được sinh ra, cha mẹ hắn đ&atilde; d&acirc;ng hai miếng b&aacute;nh ng&ocirc; cho Trương trưởng l&atilde;o của l&agrave;ng để đổi lấy c&aacute;i t&ecirc;n cho đứa b&eacute; l&agrave; H&agrave;n Lập.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">(TL: &ldquo;Nhị lăng tử&rdquo; [er leng zi 二愣 子] trong tiếng Quan Thoại c&oacute; &acirc;m thanh vui tai bất chấp &yacute; nghĩa của n&oacute;)</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi l&atilde;o Trương c&ograve;n nhỏ, l&atilde;o đ&atilde; học c&ugrave;ng trường với những đứa trẻ gi&agrave;u c&oacute; trong th&agrave;nh. V&igrave; l&atilde;o l&agrave; người duy nhất trong l&agrave;ng biết đọc một v&agrave;i chữ n&ecirc;n hơn một nửa số trẻ em trong l&agrave;ng đều được l&atilde;o đặt t&ecirc;n.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">H&agrave;n Lập được những người trong l&agrave;ng gọi l&agrave; &ldquo;Nhị lăng tử&rdquo;. Bất chấp c&aacute;i t&ecirc;n của m&igrave;nh, hắn ta kh&ocirc;ng hề ngốc nghếch hay ngờ nghệch. Ngược lại, hắn ta thực sự l&agrave; người th&ocirc;ng minh nhất trong l&agrave;ng. Nhưng cũng giống như những đứa trẻ kh&aacute;c, ngo&agrave;i khi ch&uacute;ng ở nh&agrave;, kh&ocirc;ng ai gọi n&oacute; bằng c&aacute;i t&ecirc;n ch&iacute;nh thức l&agrave; &ldquo;H&agrave;n Lập&rdquo;. Thay v&agrave;o đ&oacute;, họ gọi hắn bằng c&aacute;i t&ecirc;n th&uacute; cưng của m&igrave;nh l&agrave; &ldquo;Nhị lăng tử&rdquo;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&yacute; do tại sao anh ấy được đặt biệt danh l&agrave; &ldquo;Nhị lăng tử&rdquo; l&agrave; do đ&atilde; c&oacute; một người t&ecirc;n l&agrave; &ldquo;Lăng tử&rdquo; trong l&agrave;ng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Loại biệt danh n&agrave;y kh&ocirc;ng l&agrave; g&igrave; cả. C&oacute; những đứa trẻ trong l&agrave;ng t&ecirc;n l&agrave; &ldquo;Cẩu&rdquo; v&agrave; &ldquo;Bổn đản&rdquo;. Những c&aacute;i t&ecirc;n n&agrave;y gần như kh&ocirc;ng nghe dễ chịu như &quot;Nhị lăng tử&quot;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&igrave; điều n&agrave;y, H&agrave;n Lập cảm thấy được an ủi phần n&agrave;o mặc d&ugrave; hắn kh&ocirc;ng th&iacute;ch biệt danh của m&igrave;nh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Về mặt thể chất, H&agrave;n Lập rất b&igrave;nh thường. Hắn ta c&oacute; l&agrave;n da r&aacute;m nắng v&agrave; ph&ugrave; hợp với những m&ocirc; tả chung chung về một đứa trẻ sinh ra trong một cộng đồng n&ocirc;ng d&acirc;n. Tuy nhi&ecirc;n, s&acirc;u trong tr&aacute;i tim m&igrave;nh, hắn đ&atilde; trưởng th&agrave;nh nhanh hơn những người kh&aacute;c c&ugrave;ng tuổi. Ngay từ khi c&ograve;n nhỏ, hắn đ&atilde; khao kh&aacute;t một ng&agrave;y n&agrave;o đ&oacute;, hắn c&oacute; thể rời khỏi ng&ocirc;i l&agrave;ng nhỏ của m&igrave;nh v&agrave; kh&aacute;m ph&aacute; những v&ugrave;ng đất m&agrave;u mỡ của thế giới b&ecirc;n ngo&agrave;i m&agrave; l&atilde;o Trương vẫn lu&ocirc;n nhắc đến.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">H&agrave;n Lập chưa bao giờ d&aacute;m n&oacute;i về ước mơ của m&igrave;nh với bất kỳ ai kh&aacute;c trong l&agrave;ng v&igrave; họ sẽ bị sốc nặng. Rốt cuộc, rời khỏi nơi n&agrave;y l&agrave; một kh&aacute;i niệm m&agrave; ngay cả người lớn cũng kh&ocirc;ng dễ d&agrave;ng nghĩ đến, huống chi l&agrave; một đứa trẻ nhỏ. Những đứa trẻ trạc tuổi hắn chỉ biết đuổi g&agrave;, đuổi ch&oacute;. Họ chưa bao giờ th&iacute;ch th&uacute; với &yacute; niệm kỳ lạ về việc rời khỏi l&agrave;ng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Gia đ&igrave;nh của H&agrave;n Lập c&oacute; tổng cộng bảy th&agrave;nh vi&ecirc;n, bao gồm hai anh trai, một chị g&aacute;i v&agrave; một em g&aacute;i. Hắn l&agrave; đứa thứ tư trong gia đ&igrave;nh v&agrave; năm nay tr&ograve;n 10 tuổi. C&ugrave;ng nhau, họ đ&atilde; sống một lối sống kh&oacute; nhưng trung thực. Rất hiếm khi họ được ăn thịt v&agrave; c&aacute;, nhưng cả gia đ&igrave;nh đều h&agrave;i l&ograve;ng với cuộc sống với nguồn t&agrave;i nguy&ecirc;n &iacute;t ỏi m&agrave; họ c&oacute;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&uacute;c n&agrave;y, H&agrave;n Lập đang lơ lửng giữa trạng th&aacute;i ngủ v&agrave; thức. Khi hắn dần ch&igrave;m v&agrave;o giấc ngủ, trong đầu hắn chỉ c&oacute; một &yacute; nghĩ. Khi ở tr&ecirc;n n&uacute;i, hắn phải h&aacute;i nhiều quả mọng đỏ hơn cho tiểu muội m&agrave; hắn y&ecirc;u qu&yacute; nhất!</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">S&aacute;ng h&ocirc;m sau, v&agrave;o buổi trưa, H&agrave;n Lập đang che chắn cho m&igrave;nh khỏi c&aacute;i nắng thi&ecirc;u đốt tr&ecirc;n đầu trong b&oacute;ng r&acirc;m được đổ bởi đống gỗ tr&ecirc;n lưng. Quấn quanh ngực hắn l&agrave; một c&aacute;i t&uacute;i đầy đến v&agrave;nh với những quả mọng đỏ nảy l&ecirc;n theo mỗi bước hắn đi về nh&agrave;. Hắn kh&ocirc;ng biết rằng v&agrave;o l&uacute;c n&agrave;y, c&oacute; một vị kh&aacute;ch trong nh&agrave; hắn, một vị kh&aacute;ch sẽ thay đổi vận mệnh của hắn m&atilde;i m&atilde;i.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Vị kh&aacute;ch n&agrave;y thực ra l&agrave; người c&oacute; quan hệ huyết thống rất th&acirc;n thiết với H&agrave;n Lập. Đ&oacute; l&agrave; Tam th&uacute;c của hắn ta!</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người ta đồn rằng tam th&uacute;c của hắn l&agrave; chủ tiệm của một nh&agrave; h&agrave;ng ở th&agrave;nh gần đ&oacute;. Theo cha mẹ hắn, tam th&uacute;c l&agrave; người c&oacute; năng lực nhất trong gia đ&igrave;nh họ. Sau v&agrave;i trăm năm, d&ograve;ng họ H&agrave;n cuối c&ugrave;ng đ&atilde; sản sinh ra một người giống như tam th&uacute;c của hắn, một nh&acirc;n vật c&oacute; địa vị v&agrave; sự k&iacute;nh trọng v&ocirc; song trong d&ograve;ng họ.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi H&agrave;n Lập c&ograve;n nhỏ, hắn chỉ gặp tam th&uacute;c của m&igrave;nh v&agrave;i lần. Ca ca của H&agrave;n Lập đ&atilde; trở th&agrave;nh người học nghề thợ r&egrave;n trong th&agrave;nh nhờ sự giới thiệu của tam th&uacute;c. Thường xuy&ecirc;n, tam th&uacute;c n&agrave;y thậm ch&iacute; c&ograve;n tặng một số thực phẩm cho cha mẹ hắn để mang về nh&agrave; v&agrave; ăn. V&igrave; tam th&uacute;c đ&atilde; chăm s&oacute;c gia đ&igrave;nh của H&agrave;n Lập rất chu đ&aacute;o, n&ecirc;n H&agrave;n Lập đ&atilde; c&oacute; ấn tượng rất tốt về người th&uacute;c n&agrave;y. Tuy rằng cha mẹ hắn chưa từng n&oacute;i lời n&agrave;o nhưng hắn biết trong l&ograve;ng rất cảm k&iacute;ch.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đại ca của H&agrave;n Lập l&agrave; niềm tự h&agrave;o v&agrave; niềm vui của gia đ&igrave;nh. Khi học nghề thợ r&egrave;n, đại ca hắn c&oacute; thể mang về nh&agrave; 30 đồng mỗi th&aacute;ng sau khi trừ chi ph&iacute; sinh hoạt. V&agrave; sau n&agrave;y khi đ&atilde; học xong sẽ kiếm được nhiều tiền hơn nữa!</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mỗi khi bố mẹ hắn kể về cậu con trai cả, tinh thần của họ lại bay bổng với niềm tự h&agrave;o. Mặc d&ugrave; H&agrave;n Lập c&ograve;n trẻ, nhưng hắn đ&atilde; v&ocirc; c&ugrave;ng ghen tị. C&ocirc;ng việc tốt nhất m&agrave; hắn ta c&oacute; thể t&igrave;m được l&agrave; trở th&agrave;nh người học việc của một bậc thầy thủ c&ocirc;ng v&agrave; dựa v&agrave;o những m&oacute;n đồ thủ c&ocirc;ng hắn ta l&agrave;m để kiếm tiền.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&igrave; vậy, khi H&agrave;n Lập nh&igrave;n thấy chiếc &aacute;o cho&agrave;ng sa tanh mới tinh v&agrave; khu&ocirc;n mặt tr&ograve;n trịa của tam th&uacute;c, H&agrave;n Lập đ&atilde; vui mừng kh&ocirc;n xiết.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đặt củi xuống một g&oacute;c b&ecirc;n ngo&agrave;i ng&ocirc;i nh&agrave;, hắn đi ra trước nh&agrave; để ch&agrave;o tam th&uacute;c của m&igrave;nh. &quot;Tam th&uacute;c, H&agrave;n Lập k&iacute;nh ch&agrave;o ch&uacute;.&quot; Sau khi l&agrave;m xong, n&oacute; ngoan ngo&atilde;n đứng một b&ecirc;n nghe tam th&uacute;c n&oacute;i chuyện với ba mẹ.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tam th&uacute;c cười rạng rỡ với H&agrave;n Lập c&ograve;n hết lời khen ngợi ch&aacute;u trai của m&igrave;nh. &quot;Thật l&agrave; một đứa trẻ tốt!&quot; Sau khi khen H&agrave;n Lập, tam th&uacute;c quay lại ch&uacute; &yacute; đến cha mẹ của H&agrave;n Lập v&agrave; giải th&iacute;ch l&yacute; do cho chuyến thăm của m&igrave;nh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mặc d&ugrave; H&agrave;n Lập kh&ocirc;ng thể hiểu hết những lời m&agrave; tam th&uacute;c của m&igrave;nh đang n&oacute;i khi c&ograve;n qu&aacute; nhỏ, nhưng đại kh&aacute;i hắn vẫn hiểu những g&igrave; th&uacute;c ấy n&oacute;i.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">H&oacute;a ra nh&agrave; h&agrave;ng của tam th&uacute;c c&oacute; sự hậu thuẫn của Thất m&ecirc; đo&agrave;n gi&aacute;o. Gi&aacute;o ph&aacute;i n&agrave;y được chia th&agrave;nh c&aacute;c bộ phận b&ecirc;n trong v&agrave; b&ecirc;n ngo&agrave;i. C&aacute;ch đ&acirc;y kh&ocirc;ng l&acirc;u, tam th&uacute;c đ&atilde; ch&iacute;nh thức được nhận l&agrave;m đệ tử ngoại m&ocirc;n. Điều đ&oacute; c&oacute; nghĩa l&agrave; tam th&uacute;c c&oacute; thể đưa một đứa trẻ từ 7 đến 12 tuổi tham gia Kỳ thi Nội m&ocirc;n.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cứ 5 năm một lần, Thất m&ecirc; đo&agrave;n gi&aacute;o sẽ ch&iacute;nh thức đưa ra lời mời cho những người trẻ tuổi tham gia kỳ thi để trở th&agrave;nh Nội M&ocirc;n. Khảo nghiệm sẽ ch&iacute;nh thức bắt đầu v&agrave;o th&aacute;ng sau. Tam th&uacute;c l&agrave; một người đ&agrave;n &ocirc;ng th&ocirc;ng minh v&agrave; sắc sảo, lại kh&ocirc;ng c&oacute; trẻ con, cho n&ecirc;n &ocirc;ng ta tự nhi&ecirc;n nghĩ đến H&agrave;n Lập người c&oacute; thể đ&aacute;p ứng y&ecirc;u cầu tuổi t&aacute;c.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khoảnh khắc H&agrave;n phụ ng&agrave;y thường ngoan ngo&atilde;n lại nghe thấy hai chữ &ldquo;Giang Hồ&rdquo; v&agrave; &ldquo;M&ocirc;n ph&aacute;i&rdquo;, c&ugrave;ng với nhiều cụm từ kh&aacute;c m&agrave; l&atilde;o chưa từng nghe qua, l&atilde;o cảm thấy rất do dự. Đưa ống h&uacute;t thuốc l&ecirc;n m&ocirc;i ph&igrave; ph&egrave;o rồi ngồi xuống kh&ocirc;ng n&oacute;i lời n&agrave;o.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">(TL: &ldquo;H&agrave;n phụ&rdquo; l&agrave; danh hiệu của cha của H&agrave;n Lập)</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Theo Tam th&uacute;c, Thất m&ecirc; đo&agrave;n gi&aacute;o c&oacute; thể được coi l&agrave; một trong những gi&aacute;o ph&aacute;i lớn nhất trong v&ograve;ng v&agrave;i trăm dặm.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nếu một người trở th&agrave;nh Đệ tử Nội m&ocirc;n, kh&ocirc;ng chỉ c&oacute; thể tập v&otilde; miễn ph&iacute; m&agrave; c&ograve;n được nhận trợ cấp h&agrave;ng th&aacute;ng v&agrave; chu cấp c&aacute;c nhu yếu phẩm kh&aacute;c. Kh&ocirc;ng chỉ vậy, những người kh&ocirc;ng vượt qua kỳ thi Nội m&ocirc;n đệ tử vẫn c&oacute; thể tiến v&agrave;o ngoại m&ocirc;n của m&ocirc;n ph&aacute;i v&agrave; trở th&agrave;nh đệ tử ngoại m&ocirc;n như tam th&uacute;c. Họ vẫn sẽ c&oacute; cơ hội để gi&uacute;p Thất m&ecirc; đo&agrave;n gi&aacute;o xử l&yacute; c&aacute;c c&ocirc;ng việc b&ecirc;n ngo&agrave;i.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi nghe về khả năng con trai m&igrave;nh c&oacute; thể nhận trợ cấp h&agrave;ng th&aacute;ng v&agrave; thậm ch&iacute; trở n&ecirc;n th&agrave;nh c&ocirc;ng như tam th&uacute;c của m&igrave;nh, phụ th&acirc;n của H&agrave;n Lập cuối c&ugrave;ng đ&atilde; quyết định chấp thuận.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi nhận được sự đồng &yacute; từ phụ th&acirc;n H&agrave;n Lập, tam th&uacute;c cảm thấy phấn chấn trong l&ograve;ng. Bỏ lại hai đồng bạc, &ocirc;ng ta n&oacute;i một th&aacute;ng nữa sẽ trở về để hộ tống H&agrave;n Lập đến khu vực thử nghiệm. Trong khoảng thời gian n&agrave;y, phụ th&acirc;n của H&agrave;n Lập phải đảm bảo rằng H&agrave;n Lập được mặc quần &aacute;o v&agrave; ăn uống đầy đủ để cải thiện thể chất của m&igrave;nh khiến H&agrave;n Lập vượt qua b&agrave;i kiểm tra dễ d&agrave;ng hơn. Sau khi đưa ra những chỉ dẫn n&agrave;y, tam th&uacute;c ch&agrave;o tạm biệt H&agrave;n Lập v&agrave; phụ th&acirc;n hắn, vỗ nhẹ v&agrave;o đầu H&agrave;n Lập v&agrave; rời đi về th&agrave;nh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mặc d&ugrave; H&agrave;n Lập kh&ocirc;ng ho&agrave;n to&agrave;n hiểu những lời của tam th&uacute;c , nhưng hắn c&oacute; thể hiểu rằng hắn ta sẽ c&oacute; thể kiếm được tiền ở th&agrave;nh lớn. Dường như giấc mơ ban n&atilde;y của hắn sắp th&agrave;nh hiện thực, khiến hắn phấn kh&iacute;ch đến mức kh&ocirc;ng ngủ được mấy đ&ecirc;m đầu.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&atilde; một th&aacute;ng tr&ocirc;i qua, tam th&uacute;c trở về l&agrave;ng, hộ tống H&agrave;n Lập đến địa điểm khảo nghiệm. Trước khi rời đi, phụ th&acirc;n H&agrave;n Lập đ&atilde; nhiều lần hướng dẫn H&agrave;n Lập c&aacute;ch cư xử đ&uacute;ng mực.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một người phải trung thực, c&oacute; năng lực chịu đựng v&agrave; tr&aacute;nh xung đột kh&ocirc;ng cần thiết với người kh&aacute;c. Trong khi đ&oacute;, mẫu th&acirc;n của H&agrave;n Lập k&ecirc;u gọi hắn chăm s&oacute;c sức khỏe v&agrave; ăn ngủ đầy đủ.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ng&agrave;y cuối c&ugrave;ng cũng đến v&agrave; tam th&uacute;c đến đưa H&agrave;n Lập đi bằng xe ngựa. Khi phụ mẫu dần khuất b&oacute;ng, H&agrave;n Lập cắn chặt m&ocirc;i để ngăn nước mắt chảy d&agrave;i.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mặc d&ugrave; hắn lu&ocirc;n trưởng th&agrave;nh hơn những đứa trẻ c&ugrave;ng tuổi kh&aacute;c, nhưng hắn vẫn chỉ l&agrave; một đứa trẻ mười tuổi. Đ&acirc;y l&agrave; lần đầu ti&ecirc;n hắn đi xa n&ecirc;n tự nhi&ecirc;n hắn cảm thấy ch&aacute;n nản. Một nỗi nhớ nh&agrave; trỗi dậy trong l&ograve;ng hắn. Hắn quyết t&acirc;m lao về nh&agrave; sau khi l&agrave;m gi&agrave;u, kh&ocirc;ng bao giờ phải xa cha mẹ.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b5105d6e-7fff-5f34-465e-2aff8652587e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">H&agrave;n Lập sẽ kh&ocirc;ng bao giờ nghĩ rằng từ l&uacute;c n&agrave;y, tiền bạc sẽ mất đi &yacute; nghĩa g&igrave; đối với hắn. Hắn ta kh&ocirc;ng ngờ lại đi tr&ecirc;n một con đường kh&aacute;c với những người ph&agrave;m tục. Thay v&agrave;o đ&oacute;, hắn ta sẽ đi tr&ecirc;n con đường của ri&ecirc;ng m&igrave;nh hướng tới luyện h&oacute;a vĩnh sinh!</span></span></p>\r\n', 'Cà Di', '2020-10-05 04:51:11', 1, 0, 235);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(54, 'Chương 2: Làng  Lục Ngưu', 'chuong-2-lang-luc-nguu-1', 8, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nh&agrave; của H&agrave;n Lập được gọi l&agrave; một th&agrave;nh nhỏ, nhưng thực ra n&oacute; chỉ l&agrave; một ng&ocirc;i l&agrave;ng lớn c&oacute; t&ecirc;n l&agrave; L&agrave;ng lục ngưu. Chỉ những người sống ở miền n&uacute;i v&agrave; những người bản địa kh&ocirc;ng biết g&igrave; về thế giới b&ecirc;n ngo&agrave;i mới gọi l&agrave; l&agrave;ng l&agrave; Th&agrave;nh lục ngưu. L&yacute; do duy nhất khiến H&agrave;n Lập biết chuyện n&agrave;y l&agrave; v&igrave; anh đ&atilde; được biết th&ocirc;ng tin từ l&atilde;o Trương, người đ&atilde; l&agrave;m g&aacute;c cổng hơn mười năm.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&agrave;ng lục ngưu kh&ocirc;ng lớn lắm. N&oacute; chỉ c&oacute; một con đường ch&iacute;nh, được gọi l&agrave; đường lục ngưu, k&eacute;o d&agrave;i từ ph&iacute;a đ&ocirc;ng sang ph&iacute;a t&acirc;y bi&ecirc;n giới của ng&ocirc;i l&agrave;ng. Cũng chỉ c&oacute; một qu&aacute;n rượu trong l&agrave;ng, nằm ở bi&ecirc;n giới ph&iacute;a t&acirc;y của n&oacute;. Đối với bất kỳ thương gia du lịch n&agrave;o kh&ocirc;ng muốn ngủ ngo&agrave;i trời, qu&aacute;n rượu n&agrave;y l&agrave; lựa chọn duy nhất.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chỉ c&oacute; một con đường d&agrave;nh cho xe ngựa ở ph&iacute;a t&acirc;y của L&agrave;ng lục ngưu. N&oacute; chạy từ cổng l&agrave;ng v&agrave; Qu&aacute;n rượu lục ngưu đến tận nh&agrave; h&agrave;ng hương thơm m&ugrave;a xu&acirc;n, nơi duy nhất m&agrave; mọi người gh&eacute; thăm ngo&agrave;i qu&aacute;n rượu.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nh&agrave; h&agrave;ng hương thơm m&ugrave;a xu&acirc;n kh&ocirc;ng lớn lắm v&agrave; thực sự kh&aacute; lỗi thời. Tuy nhi&ecirc;n, cơ sở n&agrave;y c&oacute; một sức h&uacute;t nhất định thu h&uacute;t nhiều du kh&aacute;ch. Mỗi ng&agrave;y v&agrave;o buổi trưa, lu&ocirc;n lu&ocirc;n c&oacute; một đ&aacute;m đ&ocirc;ng người, l&agrave;m cho nơi n&agrave;y li&ecirc;n tục trở n&ecirc;n s&igrave;nh lầy.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một người đ&agrave;n &ocirc;ng r&acirc;u quai n&oacute;n với khu&ocirc;n mặt tr&ograve;n trĩnh bước ra từ xe ngựa c&ugrave;ng với một cậu b&eacute; da ngăm đen, da ngăm, tr&ocirc;ng khoảng mười tuổi. Cả hai c&ugrave;ng bước v&agrave;o nh&agrave; h&agrave;ng với vẻ mặt v&ecirc;nh v&aacute;o. Tất cả những kh&aacute;ch h&agrave;ng quen thuộc đều biết người đ&agrave;n &ocirc;ng n&agrave;y l&agrave; ai. Anh ấy l&agrave; quản l&yacute; của nh&agrave; h&agrave;ng n&agrave;y, &quot;B&agrave;n H&aacute;n.&quot; Tuy nhi&ecirc;n, cậu b&eacute; kh&ocirc;ng phải l&agrave; người m&agrave; họ quen thuộc.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;H&aacute;n l&atilde;o đại, đứa nhỏ r&aacute;m nắng n&agrave;y rất giống với ngươi. N&oacute; c&oacute; thể l&agrave; một đứa trẻ từ một g&aacute;i điếm m&agrave; ngươi đ&atilde; qua đ&ecirc;m? &quot; ai đ&oacute; n&oacute;i đ&ugrave;a.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khoảnh khắc tr&ograve; đ&ugrave;a được thốt ra, cả nh&agrave; h&agrave;ng vang l&ecirc;n tiếng cười.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Ớ! Đ&acirc;y l&agrave; con trai của anh ruột t&ocirc;i, ch&aacute;u ruột của t&ocirc;i! Tất nhi&ecirc;n anh ấy sẽ giống t&ocirc;i, &rdquo;B&agrave;n H&aacute;n n&oacute;i một c&aacute;ch tự h&agrave;o thay v&igrave; tức giận.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bộ đ&ocirc;i n&agrave;y đ&atilde; đi suốt ba ng&agrave;y kh&ocirc;ng nghỉ trước khi đến l&agrave;ng. Họ l&agrave; H&agrave;n Lập v&agrave; tam th&uacute;c của hắn, người được d&acirc;n l&agrave;ng gọi l&agrave; &ldquo;Tiểu l&yacute;&rdquo;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tiểu l&yacute; ch&agrave;o hỏi v&agrave;i vị kh&aacute;ch s&aacute;o trước khi đưa H&agrave;n Lập ra sau nh&agrave; h&agrave;ng, tiến v&agrave;o một s&acirc;n hẻo l&aacute;nh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Tiểu l&yacute;, con n&ecirc;n nghỉ ngơi ở đ&acirc;y một l&aacute;t. Khi đến thời điểm kiểm tra nội m&ocirc;n đệ tử, ta sẽ gọi cho con. Hiện tại, ta phải rời đi để tham dự một v&agrave;i buổi lễ. &rdquo; Tiểu l&yacute; chỉ v&agrave;o một ph&ograve;ng b&ecirc;n trong s&acirc;n v&agrave; vui l&ograve;ng ra hiệu cho H&agrave;n Lập bước v&agrave;o đ&oacute;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">(TL: &quot;Xiao&quot; trong ngữ cảnh n&agrave;y c&oacute; nghĩa l&agrave; &quot;nhỏ&quot;)</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">N&oacute;i xong, Tam th&uacute;c của H&agrave;n Lập quay lại v&agrave; vội v&atilde; quay v&agrave;o trong nh&agrave; h&agrave;ng để chiều kh&aacute;ch h&agrave;ng của m&igrave;nh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi đến cửa, anh đột nhi&ecirc;n cảm thấy bất an trong l&ograve;ng v&agrave; nhắc nhở H&agrave;n Lập, &ldquo;Đừng chạy nữa. Con c&oacute; thể bị lạc trong l&agrave;ng nếu con đi lang thang. V&igrave; vậy, tốt nhất l&agrave; con kh&ocirc;ng n&ecirc;n rời khỏi s&acirc;n n&agrave;y. &quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;En!&quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nghe thấy c&acirc;u trả lời th&agrave;nh thật của H&agrave;n Lập, &ocirc;ng gật đầu nhẹ nh&otilde;m v&agrave; bước ra khỏi cửa.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi tam th&uacute;c rời s&acirc;n, H&agrave;n Lập đột nhi&ecirc;n cảm thấy kiệt sức. Khoảnh khắc đầu hắn chạm v&agrave;o gối, hắn ch&igrave;m v&agrave;o giấc ngủ s&acirc;u v&agrave; bắt đầu ng&aacute;y, điều đ&aacute;ng ngạc nhi&ecirc;n l&agrave; kh&ocirc;ng c&oacute; ch&uacute;t sợ h&atilde;i như một đứa trẻ b&igrave;nh thường ở một m&igrave;nh trong m&ocirc;i trường xa lạ.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi đ&ecirc;m đến, một người hầu đến mang theo một &iacute;t thức ăn. Mặc d&ugrave; đ&oacute; kh&ocirc;ng phải l&agrave; một bữa ăn xa hoa, nhưng n&oacute; vẫn rất ngon. Sau khi H&agrave;n Lập ăn xong, người hầu đang dọn dẹp đống b&aacute;t đĩa c&ograve;n lại th&igrave; tam th&uacute;c của hắn thong thả bước v&agrave;o.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;N&oacute; thế n&agrave;o? Bữa ăn c&oacute; ph&ugrave; hợp với khẩu vị của con kh&ocirc;ng? C&oacute; nhớ nh&agrave; kh&ocirc;ng? &rdquo;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;V&acirc;ng, con nhớ nh&agrave;&hellip;&rdquo; H&agrave;n Lập trả lời bằng giọng trẻ con.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ch&uacute; Ba c&oacute; vẻ h&agrave;i l&ograve;ng với c&acirc;u trả lời của H&agrave;n Lập. &Ocirc;ng ta bắt đầu n&oacute;i với hắn ta về cuộc sống h&agrave;ng ng&agrave;y của m&igrave;nh v&agrave; khoe khoang về nhiều kinh nghiệm của m&igrave;nh. Dần dần, H&agrave;n Lập bớt nh&uacute;t nh&aacute;t v&agrave; bắt đầu cười n&oacute;i với tam th&uacute;c của m&igrave;nh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Theo c&aacute;ch n&agrave;y, hai ng&agrave;y nhanh ch&oacute;ng tr&ocirc;i qua.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave;o ng&agrave;y thứ ba, sau khi H&agrave;n Lập ăn tối xong, hắn đang chờ nghe chuyện của ch&uacute; m&igrave;nh về giang hồ&nbsp; th&igrave; một chiếc xe ngựa dừng trước cửa nh&agrave; h&agrave;ng.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">(TL: Jiang Hu&nbsp; - Thế Giới V&otilde; L&acirc;m)</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cỗ xe n&agrave;y được sơn một m&agrave;u đen b&oacute;ng v&agrave; ngay cả con ngựa cũng l&agrave; một chiến m&atilde; v&agrave;ng hiếm thấy. Nhưng điều thu h&uacute;t sự ch&uacute; &yacute; nhất l&agrave; tr&ecirc;n khung của cỗ xe l&agrave; d&ograve;ng chữ &ldquo;B&iacute; ẩn&rdquo; được viết bằng k&yacute; tự m&agrave;u bạc ở giữa một h&igrave;nh tam gi&aacute;c m&agrave;u đỏ c&oacute; gắn biểu ngữ m&agrave;u đen. H&igrave;nh ảnh tr&ecirc;n băng r&ocirc;n cũng tỏa ra kh&iacute; tức kh&oacute; d&ograve;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nh&igrave;n thấy tấm biểu ngữ n&agrave;y, mọi chuy&ecirc;n gia v&otilde; thuật trong v&ugrave;ng đều biết rằng cỗ xe n&agrave;y thuộc về một trong hai l&atilde;nh ch&uacute;a địa phương trong v&ugrave;ng, Thất huyền m&ocirc;n. C&oacute; vẻ như một vị kh&aacute;ch qu&yacute; đ&atilde; đến L&agrave;ng lục ngưu.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thất huyền m&ocirc;n trước đ&acirc;y được gọi l&agrave; Bảy Gi&aacute;o ph&aacute;i Tối cao. Hai trăm năm trước, m&ocirc;n ph&aacute;i được th&agrave;nh lập bởi một v&otilde; sư v&ocirc; c&ugrave;ng nổi tiếng t&ecirc;n l&agrave; &quot;Thất Quyền&quot;. Từng qu&eacute;t ngang v&agrave; thống trị Ảnh ch&acirc;u v&agrave; Thư ch&acirc;u gần đ&oacute; trong v&agrave;i thập kỷ, Thất Quyền đ&atilde; nổi tiếng một c&aacute;ch vang dội. Nhưng sau khi anh ta bị bệnh, sức mạnh của Thất huyền m&ocirc;n đ&atilde; bị ảnh hưởng nặng nề v&agrave; ảnh hưởng của n&oacute; giảm mạnh. Cuối c&ugrave;ng, Thất huyền m&ocirc;n bị buộc phải rời khỏi th&agrave;nh ch&iacute;nh của Ảnh ch&acirc;u bởi nỗ lực tổng hợp của c&aacute;c gi&aacute;o ph&aacute;i đối thủ của n&oacute;. Một trăm năm trước, gi&aacute;o ph&aacute;i buộc phải di dời đến một khu vực cực kỳ xa x&ocirc;i gọi l&agrave; Thi&ecirc;n thể th&aacute;i hồng sơn. Từ đ&oacute; trở đi, họ x&acirc;y dựng lại cội nguồn của m&igrave;nh ở v&ugrave;ng tỷ lệ thứ ba đ&oacute; v&agrave; trở th&agrave;nh một cường quốc địa phương.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tại địa phương, sức mạnh duy nhất kh&aacute;c c&oacute; thể s&aacute;nh ngang với Thất huyền m&ocirc;n l&agrave; bang d&atilde; lang.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">bang d&atilde; lang ban đầu l&agrave; một băng nh&oacute;m cướp cưỡi ngựa đến từ Ảnh ch&acirc;u kh&ocirc;ng ngại đốt, giết, cướp b&oacute;c v&agrave; cướp b&oacute;c. Một l&uacute;c sau, một đội qu&acirc;n do triều đ&igrave;nh cử đến bao v&acirc;y, đ&agrave;n &aacute;p quyết liệt bọn cướp. Một số t&ecirc;n cướp chấp nhận lệnh &acirc;n x&aacute; của triều đ&igrave;nh trong khi những t&ecirc;n cướp c&ograve;n lại tự cải tạo th&agrave;nh bang d&atilde; lang. bang d&atilde; lang v&ocirc; c&ugrave;ng t&agrave;n nhẫn v&agrave; kh&aacute;t m&aacute;u, vẫn giữ nguy&ecirc;n đặc điểm cũ của ch&uacute;ng l&agrave; kh&ocirc;ng hề e ngại khi thực hiện h&agrave;nh vi t&agrave;n bạo. V&igrave; vậy, bất cứ khi n&agrave;o họ đụng độ, Thất huyền M&ocirc;n lu&ocirc;n gặp bất lợi.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mặc d&ugrave; bang d&atilde; lang kiểm so&aacute;t nhiều l&agrave;ng hơn Thất huyền m&ocirc;n, nhưng băng nh&oacute;m n&agrave;y kh&ocirc;ng biết c&aacute;ch quản l&yacute; c&aacute;c l&agrave;ng một c&aacute;ch hiệu quả để kinh doanh v&agrave; tạo thu nhập. Trong khi đ&oacute;, sự gi&agrave;u c&oacute; của những ng&ocirc;i l&agrave;ng do Thất huyền m&ocirc;n kiểm so&aacute;t vượt xa những ng&ocirc;i l&agrave;ng dưới sự kiểm so&aacute;t của bang d&atilde; lang. Ghen tị với sự thịnh vượng của Thất huyền m&ocirc;n, bang d&atilde; lang đ&atilde; l&ecirc;n kế hoạch chiếm l&atilde;nh thổ của Thất huyền m&ocirc;n, dẫn đến xung đột k&eacute;o d&agrave;i giữa hai thế lực lớn. Cuộc xung đột đ&atilde; l&agrave;m cho người đứng đầu m&ocirc;n ph&aacute;i hiện tại của&nbsp; Thất huyền m&ocirc;n đau đầu v&ocirc; hạn. Bởi v&igrave; bang d&atilde; lang, Thất huyền m&ocirc;n ph&aacute;i đ&atilde; thu nhận số lượng đệ tử ng&agrave;y c&agrave;ng nhiều trong những năm gần đ&acirc;y.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi xe ngựa dừng lại, một người đ&agrave;n &ocirc;ng bốn mươi tuổi gầy g&ograve; nhảy xuống. Động t&aacute;c của anh ấy cực kỳ nhanh nhẹn, cho thấy anh ấy l&agrave; một chuy&ecirc;n gia mạnh mẽ. Anh ta dường như đ&atilde; rất quen thuộc với nh&agrave; h&agrave;ng của B&agrave;n H&aacute;n v&agrave; bước đi nghi&ecirc;m trang về ph&iacute;a căn ph&ograve;ng m&agrave; H&agrave;n Lập đang ở.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Vừa nh&igrave;n thấy người đ&agrave;n &ocirc;ng bốn mươi tuổi, Tiểu l&yacute; lập tức cung k&iacute;nh ch&agrave;o hỏi.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Hộ vệ Vương, tại sao một người đ&aacute;ng k&iacute;nh như ng&agrave;i lại đ&iacute;ch th&acirc;n thực hiện chuyến đi?&quot;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Hừ!&quot; Hộ vệ Vương hừ lạnh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Những con đường ở đ&acirc;y gần đ&acirc;y kh&ocirc;ng được y&ecirc;n b&igrave;nh. Do đ&oacute;, cần phải tăng cường khả năng ph&ograve;ng thủ. V&igrave; vậy, c&aacute;c trưởng l&atilde;o đ&atilde; ra lệnh cho t&ocirc;i đ&iacute;ch th&acirc;n đến. Đừng n&oacute;i bừa b&atilde;i nữa. Đ&acirc;y c&oacute; phải l&agrave; đứa trẻ m&agrave; ngươi muốn đề cử kh&ocirc;ng? &rdquo;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;V&acirc;ng, v&acirc;ng, đ&acirc;y l&agrave; ch&aacute;u trai của t&ocirc;i. T&ocirc;i hy vọng rằng Hộ vệ Vương sẽ chăm s&oacute;c cho anh ấy. &rdquo;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nh&igrave;n vẻ mặt thiếu ki&ecirc;n nhẫn tr&ecirc;n khu&ocirc;n mặt của Hộ vệ Vương, tam th&uacute;c lập tức lấy một chiếc t&uacute;i nặng k&yacute; v&agrave; b&iacute; mật đưa cho Hộ vệ Vương.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi anh ta đ&aacute;nh gi&aacute; trọng lượng của chiếc t&uacute;i, phong th&aacute;i thiếu ki&ecirc;n nhẫn của Bảo vệ Vương đ&atilde; thoải m&aacute;i r&otilde; rệt.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-52f97ef4-7fff-3e62-07d7-7e2c0a953bd4\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;B&agrave;n H&aacute;n, ngươi thật biết c&aacute;ch ứng xử! Tr&ecirc;n đường trở về, ta sẽ đảm bảo rằng tất cả c&aacute;c nhu cầu của ch&aacute;u trai bạn đều được chăm s&oacute;c chu đ&aacute;o. D&ugrave; sao th&igrave;, b&acirc;y giờ đ&atilde; kh&aacute; muộn. Tốt nhất ch&uacute;ng ta n&ecirc;n nhanh ch&oacute;ng l&ecirc;n đường. &quot;</span></span></p>\r\n', 'Cà Di', '2020-10-05 04:52:06', 1, 0, 111);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(55, 'Chương 1: Thức tỉnh Võ Khí', 'chuong-1-thuc-tinh-vo-khi', 36, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ở Lục địa chương lan, nếu một người muốn trở th&agrave;nh một V&otilde; sư, th&igrave; việc đ&aacute;nh thức một V&otilde; kh&iacute; l&agrave; điều cần thiết. Chỉ th&ocirc;ng qua V&otilde; kh&iacute;, người tu luyện mới c&oacute; thể giao tiếp với Trời Đất để hấp thụ Kh&iacute; v&agrave; tu luyện.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&oacute; nhiều loại v&otilde; kh&aacute;c nhau. Kh&ocirc;ng thể đếm hết từng loại ch&uacute;ng. N&oacute;i chung, V&otilde; được xếp v&agrave;o bốn cấp bậc lớn l&agrave; Thi&ecirc;n, Di, Xu&acirc;n v&agrave; Ho&agrave;ng [1], trong khi mỗi cấp lại được chia th&agrave;nh mười cấp.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cấp bậc của một V&otilde; kh&iacute; sẽ x&aacute;c định tốc độ tu luyện, khả năng v&agrave; tiềm năng của một người tu luyện; do đ&oacute;, một V&otilde; kh&iacute; được xếp hạng cao hơn sẽ cho ph&eacute;p người tu luyện c&oacute; cơ hội trở th&agrave;nh cao thủ trong tương lai.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">... Trong khi đ&oacute;, tại Tần tộc ở th&agrave;nh phố Linshui của Vương quốc Luohe, Lễ đ&aacute;nh thức V&otilde; kh&iacute; đang được tổ chức ...</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một đệ tử đẹp trai mang d&aacute;ng vẻ ki&ecirc;u h&atilde;nh đang khoanh tay ngồi tr&ecirc;n chiếc b&agrave;n tối đen như mực. B&agrave;n được bao quanh bởi c&aacute;c đệ tử v&agrave; trưởng l&atilde;o của Tần tộc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">T&ecirc;n đệ tử l&agrave; Tần Chấn Long, con trai của Trưởng l&atilde;o tối cao của Tần tộc. Trong số c&aacute;c đệ tử của Tần tộc, hắn đứng thứ hai, ngay sau Thiếu gia của Tần gia, Tần Nam.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Như vậy, c&aacute;c đệ tử v&agrave; trưởng l&atilde;o tr&ecirc;n mặt đều lộ ra vẻ mong đợi.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&agrave;n tối đen như mực bắt đầu hoạt động khi những tia s&aacute;ng trắng được ph&aacute;t ra từ n&oacute;, dẫn đến một cảnh tượng kỳ diệu. Đ&aacute;m đ&ocirc;ng c&oacute; thể nh&igrave;n thấy &aacute;nh s&aacute;ng xuy&ecirc;n qua cơ thể Tần Chấn Long v&agrave; di chuyển xung quanh như những con rắn ảo ảnh khổng lồ bằng mắt thường.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">GẦM!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một tiếng gầm gh&ecirc; rợn của d&atilde; th&uacute; đột nhi&ecirc;n ph&aacute;t ra từ cơ thể của Tần Chấn Long.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Những tia s&aacute;ng m&agrave;u v&agrave;ng bắt đầu xuất hiện từ sau lưng của Tần Chấn Long, v&agrave; khi c&oacute; thể nh&igrave;n thấy tổng cộng năm tia s&aacute;ng m&agrave;u v&agrave;ng, b&oacute;ng ma của một con hổ trắng hung dữ khổng lồ xuất hiện với một vầng h&agrave;o quang qu&aacute;i dị, như thể vua của lo&agrave;i th&uacute; đ&atilde; đến.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&aacute;c trưởng l&atilde;o v&agrave; đệ tử c&oacute; thể cảm thấy một &aacute;p lực lớn ngay l&uacute;c đ&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Đ&acirc;y l&agrave;&hellip;&rdquo; Trưởng l&atilde;o phụ tr&aacute;ch h&agrave;nh lễ nhất thời kinh ngạc, trước khi lập tức thu lại suy nghĩ, k&iacute;ch động h&eacute;t l&ecirc;n &ldquo;Năm tia s&aacute;ng m&agrave;u v&agrave;ng! Năm tia v&agrave;ng! Đ&oacute; l&agrave; V&otilde; kh&iacute; cấp năm! cấp thứ năm V&otilde; kh&iacute; cấp cấp Thần th&uacute;! &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lời n&oacute;i đ&oacute; như một quả lựu đạn được n&eacute;m v&agrave;o đ&aacute;m đ&ocirc;ng, khiến c&aacute;c m&ocirc;n đệ v&agrave; trưởng l&atilde;o giật m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;G&igrave;? Một V&otilde; kh&iacute; cấp năm? &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;&Ocirc;i lạy Ch&uacute;a, Tần tộc của ch&uacute;ng ta cuối c&ugrave;ng đ&atilde; sản sinh ra một nh&acirc;n t&agrave;i tuyệt vời!&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;&hellip;&hellip;&rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khu&ocirc;n mặt của tất cả mọi người đều v&ocirc; c&ugrave;ng kinh ngạc. Kh&ocirc;ng ai c&oacute; thể mong đợi điều n&agrave;y xảy ra.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">... Một lưu &yacute; nhỏ l&agrave;, một người c&oacute; V&otilde; kh&iacute; cấp năm được coi l&agrave; t&agrave;i năng h&agrave;ng đầu trong to&agrave;n bộ th&agrave;nh phố Linshui. Những t&agrave;i năng như vậy sẽ chỉ xuất hiện trung b&igrave;nh ba năm một lần. Hơn nữa, những người c&oacute; V&otilde; Linh cấp năm trở l&ecirc;n được đảm bảo sẽ đạt được những th&agrave;nh tựu to lớn trong tương lai.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ban đầu bọn họ nghĩ rằng Tần Chấn Long nhiều nhất sẽ đ&aacute;nh thức một V&otilde; kh&iacute; cấp bốn.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Chấn Long ch&igrave;m trong niềm vui sướng tột độ. Ngay cả bản th&acirc;n hắn cũng kh&ocirc;ng ngờ c&oacute; thể đ&aacute;nh thức một V&otilde; kh&iacute; cấp năm.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi nh&igrave;n thấy sự kinh ngạc trong mắt của c&aacute;c đệ tử v&agrave; c&aacute;c trưởng l&atilde;o, Tần Chấn Long kh&ocirc;ng khỏi cảm thấy tự h&agrave;o. Sau đ&oacute;, anh ta nh&igrave;n lướt qua đ&aacute;m đ&ocirc;ng v&agrave; nh&igrave;n chằm chằm v&agrave;o một đệ tử c&oacute; vẻ ngo&agrave;i b&igrave;nh thường với vẻ chế giễu.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đệ tử kh&ocirc;ng ai kh&aacute;c ch&iacute;nh l&agrave; Thiếu gia của Tần tộc được mệnh danh l&agrave; thi&ecirc;n t&agrave;i h&agrave;ng đầu của gia tộc: Tần Nam.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Tốt lắm . Tiếp theo, Tần Nam! &rdquo; Trưởng l&atilde;o phụ tr&aacute;ch nhanh ch&oacute;ng v&agrave; h&eacute;t l&ecirc;n một tiếng. Sau đ&oacute;, &aacute;nh mắt &ocirc;ng ta tr&agrave;n đầy sự chờ đợi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&acirc;y giờ Tần Chấn Long đ&atilde; được coi l&agrave; một thi&ecirc;n t&agrave;i kiệt xuất, Tần Nam nhất định sẽ đ&aacute;nh thức một V&otilde; kh&iacute; giỏi hơn hắn, dựa v&agrave;o t&agrave;i năng của bản th&acirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nếu l&agrave; như vậy, tương lai Tần tộc kh&ocirc;ng n&ecirc;n coi thường hai đại thi&ecirc;n t&agrave;i n&agrave;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Quả nhi&ecirc;n, sau khi trưởng l&atilde;o h&eacute;t l&ecirc;n c&aacute;i t&ecirc;n đ&oacute;, c&aacute;c đệ tử v&agrave; trưởng l&atilde;o lập tức r&uacute;t lui, liếc về ph&iacute;a thanh ni&ecirc;n c&oacute; vẻ ngo&agrave;i b&igrave;nh thường. Sự chờ đợi hiển hiện trong mắt họ lớn hơn trước.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam kh&ocirc;ng chỉ l&agrave; một huyền thoại đối với Tần tộc, m&agrave; c&ograve;n đối với th&agrave;nh phố Linshui.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam l&agrave; con trai của Tần tộc, người chỉ bắt đầu học đọc v&agrave; viết khi mới 6 tuổi, v&agrave; sau đ&oacute; đ&atilde; g&acirc;y chấn động to&agrave;n th&agrave;nh phố Linshui với th&agrave;nh t&iacute;ch học tập của m&igrave;nh khi mới 8 tuổi. Sau đ&oacute;, anh chuyển sang con đường v&otilde; thuật v&agrave;o năm ch&iacute;n tuổi v&agrave; tập luyện Nắm đấm v&agrave;o năm mười tuổi. Năm mười một tuổi, anh ta săn một con hổ bằng tay kh&ocirc;ng v&agrave; bắt đầu luyện tập Saber Arts. Anh ta đ&atilde; th&agrave;nh thạo mười Kỹ năng V&otilde; thuật v&agrave;o năm mười ba tuổi, v&agrave; khi anh ta bị s&eacute;t đ&aacute;nh v&agrave;o năm mười bốn tuổi, anh ta ho&agrave;n to&agrave;n b&igrave;nh an v&ocirc; sự. Cuối c&ugrave;ng, anh ta đ&atilde; s&aacute;ng tạo ra Saber Art được xếp v&agrave;o loại Kỹ năng V&otilde; thuật Trung cấp, dẫn đến một sự ngạc nhi&ecirc;n lớn cho người d&acirc;n th&agrave;nh phố Linshui.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một lần, một bậc thầy về đế quốc ngay lập tức n&oacute;i sau khi nghe th&agrave;nh t&iacute;ch của Tần Nam, &ldquo;T&agrave;i năng m&agrave; đứa trẻ n&agrave;y thể hiện l&agrave; kh&ocirc;ng thể coi thường. V&otilde; kh&iacute; m&agrave; hắn thức tỉnh trong tương lai chắc chắn sẽ l&agrave; cấp năm, hoặc cao hơn nữa. Tương lai của cậu ấy sẽ rất phi thường &rdquo;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lời n&oacute;i của V&otilde; Đế lập tức ph&aacute;t huy t&aacute;c dụng. Trước khi Tần Nam đ&aacute;nh thức được V&otilde; kh&iacute; của m&igrave;nh, anh được coi l&agrave; thi&ecirc;n t&agrave;i h&agrave;ng đầu của th&agrave;nh phố Linshui.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam mặc một chiếc &aacute;o cho&agrave;ng d&agrave;i m&agrave;u đen, l&agrave;n da hơi t&aacute;i nhợt như vừa mới khỏi bệnh. Dưới &aacute;nh nh&igrave;n của đ&aacute;m đ&ocirc;ng, anh ta tiến đến b&agrave;n thờ với một tốc độ ổn định trong khi mang một vẻ mặt điềm tĩnh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Ph&ugrave;, ng&agrave;y n&agrave;y cuối c&ugrave;ng đ&atilde; đến&hellip;&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam tự lẩm bẩm h&iacute;t s&acirc;u một hơi. Anh phớt lờ những &aacute;nh nh&igrave;n từ đ&aacute;m đ&ocirc;ng v&agrave; ngồi khoanh ch&acirc;n trước khi nhắm mắt lại.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau đ&oacute;, những tia s&aacute;ng m&agrave;u trắng ph&aacute;t ra từ tế đ&agrave;n xuy&ecirc;n qua cơ thể Tần Nam.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay l&uacute;c đ&oacute;, c&aacute;c đệ tử v&agrave; trưởng l&atilde;o xung quanh trở n&ecirc;n phấn kh&iacute;ch sau khi qu&aacute; tr&igrave;nh diễn ra.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Bản năng m&aacute;ch bảo rằng v&otilde; kh&iacute; của thiếu gia Tần Nam sẽ rất đ&aacute;ng ch&uacute; &yacute;!&rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Đ&uacute;ng vậy, Tần Chấn Long đ&atilde; thức tỉnh một V&otilde; kh&iacute; cấp năm. Thiếu gia Tần Nam t&agrave;i năng c&agrave;ng lớn, cấp bậc V&otilde; kh&iacute; của ng&agrave;i ấy chắc chắn sẽ c&agrave;ng cao! &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Kh&iacute; ph&aacute;ch, một V&otilde; kh&iacute; vượt qua cấp năm. Điều n&agrave;y chưa từng xảy ra trong lịch sử Tần tộc của ch&uacute;ng ta &rdquo;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;C&acirc;m miệng, cố gắng đừng quấy rầy Tần Nam thiếu gia. Nếu c&oacute; g&igrave; sai s&oacute;t, ngươi c&oacute; khả năng g&aacute;nh chịu hậu quả kh&ocirc;ng? &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;&hellip;&hellip;&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bầu kh&ocirc;ng kh&iacute; nơi đ&acirc;y trở n&ecirc;n s&ocirc;i động v&ocirc; c&ugrave;ng. Ngo&agrave;i c&aacute;c đệ tử trẻ tuổi, ngay cả c&aacute;c trưởng l&atilde;o cũng kh&ocirc;ng ngớt lời khen ngợi với vẻ mong đợi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, khi Tần Chấn Long nh&igrave;n thấy phản ứng của đ&aacute;m đ&ocirc;ng, niềm vui v&agrave; niềm tự h&agrave;o khi thức tỉnh một cấp V&otilde; kh&iacute; cấp năm lập tức ti&ecirc;u tan, để lại vẻ mặt vặn vẹo.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Hừm, One&rsquo;s Martial Spirit được x&aacute;c định bởi Thi&ecirc;n tộc. Kh&ocirc;ng g&igrave; c&oacute; thể thay đổi c&aacute;c quy tắc &rdquo;. Tần Chấn Long lạnh giọng n&oacute;i: &ldquo;Ngươi thật sự cho rằng một c&aacute;i cấp v&otilde; giả cấp năm lại dễ d&agrave;ng thức tỉnh sao? Đợi đến khi ngươi nh&igrave;n thấy V&otilde; kh&iacute; Tần Nam của thiếu gia ngươi h&oacute;a ra chỉ l&agrave; một V&otilde; kh&iacute; cấp hai! &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Vừa dứt lời, c&aacute;c đệ tử v&agrave; trưởng l&atilde;o xung quanh lập tức khinh bỉ liếc nh&igrave;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">N&oacute;i như vậy, c&aacute;c đệ tử v&agrave; trưởng l&atilde;o đều kh&ocirc;ng n&oacute;i g&igrave;. D&ugrave; sao Tần Chấn Long cũng l&agrave; thi&ecirc;n t&agrave;i c&oacute; cấp V&otilde; kh&iacute; cấp năm, như vậy địa vị của hắn trong tộc cũng kh&ocirc;ng c&ograve;n như trước.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Chấn Long cảm thấy kh&oacute; chịu sau khi nh&igrave;n thấy &aacute;nh mắt ch&aacute;n gh&eacute;t của đ&aacute;m đ&ocirc;ng. Tuy nhi&ecirc;n, n&oacute; đ&atilde; cho ph&eacute;p anh b&igrave;nh tĩnh lại suy nghĩ của m&igrave;nh. T&agrave;i năng của Tần Nam đ&atilde; r&otilde; r&agrave;ng ngay từ khi anh ấy được sinh ra. Ngay cả một V&otilde; Đế cũng ấn tượng, do đ&oacute; sẽ kh&ocirc;ng c&oacute; chuyện hắn đ&aacute;nh thức một V&otilde; kh&iacute; cấp hai của Ho&agrave;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi c&oacute; &yacute; nghĩ n&agrave;y, Tần Chấn Long trừng mắt nh&igrave;n về ph&iacute;a tế đ&agrave;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Tần Nam, V&otilde; kh&iacute; của ngươi phải thấp hơn cấp năm, hoặc &iacute;t nhất kh&ocirc;ng vượt qua cấp năm&hellip;&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Chấn Long kh&ocirc;ng khỏi ph&aacute;t ra một lời nguyền rủa trong l&ograve;ng.Trong khi đ&oacute;, số lượng tia s&aacute;ng trắng ph&aacute;t ra từ b&agrave;n thờ tăng li&ecirc;n tục.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mặt Tần Nam hơi đỏ l&ecirc;n sau khi bị &aacute;nh s&aacute;ng chiếu v&agrave;o, trước khi cơ thể anh vang l&ecirc;n một tiếng vo ve.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&aacute;m đ&ocirc;ng đ&atilde; n&iacute;n thở ngay l&uacute;c đ&oacute;!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau một tiếng nổ lớn, một tia m&agrave;u v&agrave;ng đột ngột bắn ra từ cơ thể anh ta. Dưới &aacute;nh mắt lo lắng của đ&aacute;m đ&ocirc;ng, sau khi tia s&aacute;ng được ph&aacute;t ra, một thanh kiếm khổng lồ ch&igrave;m trong ngọn lửa từ từ xuất hiện trong tia s&aacute;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&aacute;c trưởng l&atilde;o v&agrave; đệ tử lập tức bị kinh ngạc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một tia m&agrave;u v&agrave;ng? Một cấp bậc nhất V&otilde; kh&iacute;? Thứ hạng thấp nhất c&oacute; thể? L&agrave;m thế n&agrave;o điều n&agrave;y c&oacute; thể được?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay cả Tần Chấn Long, người kh&ocirc;ng ngừng thốt ra những lời nguyền rủa trong l&ograve;ng, cũng v&ocirc; c&ugrave;ng kinh ngạc. Kh&ocirc;ng thể n&agrave;o anh ta c&oacute; thể ngờ được thi&ecirc;n t&agrave;i h&agrave;ng đầu của th&agrave;nh phố Linshui chỉ c&oacute; thể đ&aacute;nh thức một V&otilde; kh&iacute; cấp một của Ho&agrave;ng cấp &mdash; V&otilde; kh&iacute; tồi tệ nhất c&oacute; thể!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam đang ngồi tr&ecirc;n tế đ&agrave;n chậm r&atilde;i mở mắt ra, quan s&aacute;t vẻ mặt sững sờ tr&ecirc;n mặt đ&aacute;m người.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Scarlet Flame Sabre, một cấp bậc nhất V&otilde; kh&iacute;, huh&hellip;&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam tự lẩm bẩm một m&igrave;nh, vừa r&uacute;t thanh kiếm nổi l&ecirc;n tr&ecirc;n người, vừa nghĩ. Anh ấy kh&ocirc;ng hề tỏ ra tuyệt vọng hay mất hy vọng qua biểu hiện của m&igrave;nh. Thay v&agrave;o đ&oacute;, &aacute;nh mắt anh tho&aacute;ng qua một ch&uacute;t vui mừng mơ hồ, nhưng anh đ&atilde; sớm che giấu n&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">XephiZ</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">DOCuinn</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-3e60324c-7fff-111b-2255-a69de5864226\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">[1] TL Ch&uacute; th&iacute;ch: Tian, ​​Di, Xuan, Huang (天地 玄 黄) l&agrave; bốn chữ c&aacute;i đầu ti&ecirc;n trong Ng&agrave;n chữ cổ điển, một b&agrave;i thơ chữ H&aacute;n được d&ugrave;ng l&agrave;m cơ sở dạy chữ H&aacute;n cho trẻ em. Thi&ecirc;n = trời / bầu trời, Di = đất, Xu&acirc;n = xanh thẫm / mờ mịt, Ho&agrave;ng = v&agrave;ng / người.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n', 'Cà Di', '2020-09-25 11:25:45', 1, 0, 231);
INSERT INTO `chapters` (`id`, `name`, `slug`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(56, 'Chương 2: Thần chiến đấu', 'chuong-2-than-chien-djau', 36, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&aacute;m đ&ocirc;ng ch&igrave;m trong im lặng trong v&agrave;i nhịp thở.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Chấn Long l&agrave; người đầu ti&ecirc;n phản ứng bằng c&aacute;ch ph&aacute; l&ecirc;n cười, &ldquo;HAHAHA, ta chưa bao giờ nghĩ rằng hắn sẽ đ&aacute;nh thức một cấp bậc một! Ngươi l&agrave; loại thi&ecirc;n t&agrave;i n&agrave;o? Ngươi chỉ l&agrave; một thứ r&aacute;c rưởi v&ocirc; dụng, thứ khiến ta trở th&agrave;nh thi&ecirc;n t&agrave;i h&agrave;ng đầu thực sự của Tần tộc &mdash; thi&ecirc;n t&agrave;i h&agrave;ng đầu của th&agrave;nh phố Linshui! &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&otilde; kh&iacute; cấp bậc một được xếp hạng thấp nhất trong số tất cả c&aacute;c V&otilde; kh&iacute; , thường được coi l&agrave; một V&otilde; kh&iacute; ho&agrave;n to&agrave;n v&ocirc; dụng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay cả trong Tần tộc, chỉ c&oacute; một số &iacute;t đ&atilde; thức tỉnh một V&otilde; kh&iacute;&nbsp; cấp một. Hầu hết mọi người &iacute;t nhất sẽ đ&aacute;nh thức một cấp V&otilde; kh&iacute;&nbsp; cấp hai trở l&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tiếng cười khinh bỉ của Tần Chấn Long ngay lập tức đ&aacute;nh thức đ&aacute;m đ&ocirc;ng khỏi sự kinh ngạc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&Aacute;nh mắt của c&aacute;c đệ tử v&agrave; trưởng l&atilde;o đối với Tần Nam lập tức thay đổi. Sự t&ocirc;n trọng v&agrave; mong đợi ban đầu trong mắt họ được thay thế bằng những dấu hiệu thất vọng v&agrave; coi thường.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;H&oacute;a ra l&agrave; một V&otilde; kh&iacute; cấp một. F * k, đ&uacute;ng l&agrave; một thứ r&aacute;c rưởi! &ldquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Chết tiệt, thật l&atilde;ng ph&iacute; sự mong đợi của ta. Một thi&ecirc;n t&agrave;i h&agrave;ng đầu của ta, ngươi chẳng l&agrave; g&igrave; ngo&agrave;i một t&ecirc;n r&aacute;c rưởi của V&otilde; kh&iacute; ! &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Đ&uacute;ng vậy, anh trai Tần Chấn Long l&agrave; thi&ecirc;n t&agrave;i h&agrave;ng đầu duy nhất. E rằng Tần Nam n&agrave;y chỉ l&agrave; thứ r&aacute;c rưởi h&agrave;ng đầu của Tần tộc! &rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;&hellip;&hellip;&rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chỉ những người c&oacute; sức mạnh đ&aacute;ng gờm mới được t&ocirc;n trọng trong V&otilde; giới, do đ&oacute; phản ứng của đ&aacute;m đ&ocirc;ng l&agrave; c&oacute; thể đo&aacute;n trước được, đến mức giờ đ&acirc;y họ thậm ch&iacute; c&ograve;n &ocirc;m hận với Tần Nam.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&yacute; do ch&iacute;nh l&agrave; Tần Nam đ&atilde; thể hiện t&agrave;i năng xuất ch&uacute;ng từ khi c&ograve;n trẻ, v&igrave; vậy họ đ&atilde; đặt kỳ vọng rất cao v&agrave;o anh trong tiềm thức. V&igrave; vậy, kh&ocirc;ng ai c&oacute; thể ngờ rằng n&oacute; lại đến như vậy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&agrave;m sao c&aacute;c m&ocirc;n đồ v&agrave; trưởng l&atilde;o c&oacute; thể kh&ocirc;ng gh&eacute;t Ng&agrave;i v&igrave; đ&atilde; l&agrave;m họ thất vọng?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Chấn Long ho&agrave;n to&agrave;n bất ngờ. Anh ta lập tức trở n&ecirc;n cao hứng trong khi nh&igrave;n chằm chằm v&agrave;o Tần Nam v&agrave; ph&aacute; l&ecirc;n cười, &ldquo;B&acirc;y giờ c&aacute;c ngươi đ&atilde; phục hết chưa? Ta đ&atilde; n&oacute;i rằng V&otilde; kh&iacute;&nbsp; của mọi người đ&atilde; được x&aacute;c định bởi Thi&ecirc;n tộc. Vậy điều g&igrave; sẽ xảy ra nếu ngươi c&oacute; thể s&aacute;ng tạo ra V&otilde; thuật của ri&ecirc;ng m&igrave;nh? Ngươi vẫn chẳng l&agrave; g&igrave; ngo&agrave;i một thứ r&aacute;c rưởi! &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam liếc hắn một c&aacute;i b&igrave;nh tĩnh m&agrave; kh&ocirc;ng phản b&aacute;c c&acirc;u n&oacute;i của hắn, trước khi rời đi.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&igrave;nh thường sẽ c&oacute; đệ tử hoặc trưởng l&atilde;o đi theo Tần Nam, nhưng h&ocirc;m nay kh&ocirc;ng ai muốn l&agrave;m như vậy, dẫn đến cảnh c&ocirc; độc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi Tần Nam khởi h&agrave;nh, c&aacute;c đệ tử v&agrave; trưởng l&atilde;o lập tức v&acirc;y quanh Tần Chấn Long để vỗ về hắn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;N&agrave;y, Tần Chấn Long, đ&atilde; l&acirc;u rồi kể từ lần cuối anh đến thăm t&ocirc;i. T&ocirc;i đ&atilde; nhớ anh rất nhiều. &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Chấn Long, ta nghĩ ngươi mới xứng đ&aacute;ng l&agrave; Thiếu gia của Tần tộc. C&aacute;i thứ r&aacute;c rưởi đ&oacute; kh&ocirc;ng c&oacute; quyền giữ danh hiệu đ&oacute; &rdquo;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Chấn Long, từ h&ocirc;m nay trở đi em sẽ l&agrave; cấp dưới của anh. em sẽ kh&ocirc;ng bao giờ đi về hướng T&acirc;y nếu hướng Đ&ocirc;ng l&agrave; &yacute; muốn của anh. &quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;&hellip;&hellip;&rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kh&ocirc;ng l&acirc;u sau, tin tức về kết quả của Lễ thức tỉnh V&otilde; kh&iacute;&nbsp; đ&atilde; được to&agrave;n bộ Tần tộc v&agrave; th&agrave;nh phố Linshui biết đến. Kh&ocirc;ng chỉ người của Tần tộc chết lặng, ngay cả cư d&acirc;n của th&agrave;nh phố Linshui cũng v&ocirc; c&ugrave;ng sửng sốt.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bọn họ kh&ocirc;ng bao giờ ngờ được Tần Nam, thi&ecirc;n t&agrave;i h&agrave;ng đầu của th&agrave;nh phố Linshui, chỉ c&oacute; thể đ&aacute;nh thức một V&otilde; kh&iacute;&nbsp; cấp một.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">N&oacute;i như vậy, quả nhi&ecirc;n l&agrave; V&otilde; Giới, trong l&uacute;c kinh ngạc, c&ocirc;ng ch&uacute;ng lập tức đổ dồn sự ch&uacute; &yacute; v&agrave;o Tần Chấn Long.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Rốt cuộc, một V&otilde; kh&iacute;&nbsp; cấp năm đ&atilde; được coi l&agrave; xuất sắc ở th&agrave;nh phố Linshui.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam trở về nơi ở của m&igrave;nh sau khi rời khỏi tế đ&agrave;n. Vẻ mặt anh vẫn b&igrave;nh thản như mọi khi. Ngay cả một ch&uacute;t tức giận cũng kh&ocirc;ng thể nh&igrave;n thấy tr&ecirc;n khu&ocirc;n mặt anh ta, mặc d&ugrave; thực tế chỉ c&oacute; những người c&oacute; sức mạnh v&agrave; t&agrave;i năng đ&aacute;ng gờm mới được mọi người k&iacute;nh trọng tr&ecirc;n thế giới n&agrave;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mặc d&ugrave; trong l&ograve;ng biết r&otilde; điều n&agrave;y, nhưng sau khi nh&igrave;n thấy phản ứng của c&aacute;c đệ tử v&agrave; trưởng l&atilde;o, anh ấy vẫn thất vọng.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;H&atilde;y bắt đầu đi, kh&ocirc;ng c&oacute; l&yacute; do g&igrave; để l&agrave;m phiền ch&uacute;ng. Ta sẽ cố gắng tu luyện với V&otilde; kh&iacute;&nbsp; n&agrave;y ngay b&acirc;y giờ. &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam lập tức trấn định t&acirc;m tr&iacute;, khoanh ch&acirc;n ngồi xuống. Scarlet Flame Sabre từ từ trồi l&ecirc;n sau lưng anh ta trong khi tỏa ra một hơi n&oacute;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay khi Scarlet Flame Sabre xuất hiện, một lực hấp thụ ma thuật được ph&aacute;t ra từ n&oacute;, tập hợp Kh&iacute; của Trời v&agrave; Đất xung quanh anh ta.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bộ d&aacute;ng của Tần Nam kh&ocirc;ng khỏi khẽ r&ugrave;ng m&igrave;nh một c&aacute;i, sắc mặt hơi t&aacute;i nhợt đỏ bừng như thể vừa uống một ch&uacute;t rượu.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Con đường tu luyện bắt đầu với Cảnh giới luyện th&acirc;n, được chia th&agrave;nh mười tầng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tu luyện cơ thể theo nghĩa đen c&oacute; nghĩa l&agrave; qu&aacute; tr&igrave;nh luyện cơ thể bằng Kh&iacute; hấp thụ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cứ như vậy, Tần Nam bắt đầu hấp thu Kh&iacute; v&agrave;o cơ thể, bắt đầu tẩy rửa kinh mạch, cường tr&aacute;ng da thịt.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thời gian dần tr&ocirc;i. Ba giờ sau, Tần Nam mở to mắt. Tr&aacute;n anh lấm tấm mồ h&ocirc;i.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Cấp bậc của một V&otilde; kh&iacute;&nbsp; quả thực kh&aacute; quan trọng&hellip;&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong l&ograve;ng đột nhi&ecirc;n suy nghĩ, Tần Nam cầm lấy Scarlet Flame Sabre trong tay, kiểm tra kỹ c&agrave;ng. Một nụ cười gượng gạo hiện tr&ecirc;n khu&ocirc;n mặt anh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giờ đ&acirc;y, anh đ&atilde; hiểu tầm quan trọng của cấp bậc V&otilde; kh&iacute; .</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&iacute; dụ, anh ấy đ&atilde; kh&ocirc;ng đạt được g&igrave; sau khi tu luyện trong ba giờ. Tuy nhi&ecirc;n, khả năng cao l&agrave; Tần Chấn Long đ&atilde; đạt đến cảnh giới Luyện Kh&iacute; tầng thứ nhất sau khi tu luyện trong c&ugrave;ng một khoảng thời gian.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Th&ecirc;m v&agrave;o đ&oacute;, cấp bậc của một V&otilde; kh&iacute;&nbsp; c&agrave;ng cao th&igrave; khả năng của n&oacute; c&agrave;ng lớn, do đ&oacute; hỗ trợ tốt hơn cho người mang n&oacute;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Tuy nhi&ecirc;n, ta thật may mắn khi đ&atilde; đ&aacute;nh thức được một V&otilde; kh&iacute;&nbsp; cấp một. Nếu kh&ocirc;ng, t&ocirc;i sẽ kh&aacute; mất tư do&hellip; &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam n&oacute;i với một nụ cười b&igrave;nh tĩnh, trước khi nh&igrave;n v&agrave;o chiếc xe ngựa của m&igrave;nh với một suy nghĩ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&ecirc;n trong sơn trang của Tần Nam nổi l&ecirc;n một tia chớp lấp l&aacute;nh to bằng nắm tay. Một luồng kh&iacute; cổ xưa b&iacute; ẩn v&agrave; đ&aacute;ng sợ c&oacute; thể được cảm nhận từ n&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&acirc;y l&agrave; b&iacute; mật lớn nhất của Tần Nam. Ngay cả cha anh cũng kh&ocirc;ng biết g&igrave; về điều đ&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Năm mười bốn tuổi, Tần Nam đ&atilde; ra ngo&agrave;i luyện v&otilde;, nhưng lại gặp phải s&oacute;ng gi&oacute;. Trong khi cố gắng t&igrave;m một nơi tr&uacute; ẩn từ n&oacute;, anh ta đ&atilde; bị một tia s&eacute;t m&agrave;u đỏ đ&aacute;nh tr&uacute;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tr&ecirc;n thực tế, người d&acirc;n th&agrave;nh phố Linshui đ&atilde; biết về vụ việc, nhưng họ kh&ocirc;ng c&oacute; manh mối n&agrave;o rằng sau khi Tần Nam bị s&eacute;t đ&aacute;nh, những k&yacute; ức th&ecirc;m v&agrave;o t&acirc;m tr&iacute; anh khi một đ&aacute;m m&acirc;y đỏ như b&atilde;o tố tr&uacute; ngụ trong cơ thể anh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Theo những mảnh k&yacute; ức, đ&aacute;m m&acirc;y đỏ đầy b&atilde;o tố n&agrave;y l&agrave; một V&otilde; kh&iacute;&nbsp; thừa nhận được gọi l&agrave; Thần Trận Đồ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Để c&oacute; được Thần Trận Đồ n&agrave;y, người ta sẽ phải hy sinh Thần V&otilde; của ch&iacute;nh m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Hơn nữa, cấp bậc V&otilde; kh&iacute;&nbsp; của người đ&oacute; c&agrave;ng cao, cơ hội đ&aacute;nh thức điều n&agrave;y c&agrave;ng thấp. Nếu người n&agrave;o sở hữu được một V&otilde; kh&iacute;&nbsp; cấp một, người đ&oacute; chắc chắn sẽ thức tỉnh Thần Trận đồ! &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nụ cười tr&ecirc;n mặt Tần Nam c&agrave;ng đậm. Đ&acirc;y l&agrave; l&yacute; do tại sao anh ta kh&ocirc;ng hề tỏ ra tuyệt vọng sau khi đ&aacute;nh thức một V&otilde; kh&iacute;&nbsp; cấp một.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Rốt cuộc, hắn kh&ocirc;ng chắc về khả năng của Thần Trận Thần b&iacute; ẩn n&agrave;y. Nếu như hắn t&igrave;nh cờ đ&aacute;nh thức một Ho&agrave;ng cấp V&otilde; kh&iacute;&nbsp; cấp năm, chẳng những cơ hội đ&aacute;nh thức Thần Đấu La c&agrave;ng mỏng, hắn cũng kh&oacute; đưa ra quyết định hơn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một người sẽ cần sự can đảm lớn để hy sinh một V&otilde; kh&iacute;&nbsp; cấp năm.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mặt kh&aacute;c, V&otilde; kh&iacute;&nbsp; cấp một của Tần Nam cấp bậc ho&agrave;n to&agrave;n v&ocirc; dụng. Kh&ocirc;ng chỉ c&oacute; Thần Trận thức tỉnh, Tần Nam cũng sẽ kh&ocirc;ng cảm thấy &aacute;p lực khi l&agrave;m như vậy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam nhanh ch&oacute;ng thu lại nụ cười v&agrave; h&iacute;t một hơi thật s&acirc;u khi cầm tr&ecirc;n tay thanh kiếm Scarlet Flame Sabre, &ldquo;B&acirc;y giờ, đ&atilde; đến l&uacute;c hy sinh thanh kiếm n&agrave;y&hellip;&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam lập tức mang vẻ nghi&ecirc;m nghị đồng thời thốt ra một tr&agrave;ng kinh h&ocirc; cổ qu&aacute;i.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Những c&acirc;u kinh được gọi l&agrave; &lsquo;Kỹ thuật hy sinh tinh thần&rsquo;, đ&atilde; xuất hiện trong t&acirc;m tr&iacute; anh khi anh bị s&eacute;t đ&aacute;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kỹ thuật Hy sinh Tinh thần, như c&aacute;i t&ecirc;n đ&atilde; gợi &yacute;, rất hữu &iacute;ch trong việc cung cấp V&otilde; của người d&ugrave;ng như một vật hiến tế. Kỹ thuật n&agrave;y hơi trừu tượng. N&oacute; kh&ocirc;ng y&ecirc;u cầu bất kỳ đan n&agrave;o hỗ trợ việc thực thi của n&oacute; v&agrave; người d&ugrave;ng cũng kh&ocirc;ng phải trả bất kỳ gi&aacute; n&agrave;o.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi Tần Nam thi triển kỹ thuật, một vầng s&aacute;ng đen từ từ xuất hiện tr&ecirc;n l&ograve;ng b&agrave;n tay anh. N&oacute; trở n&ecirc;n d&iacute;nh ngay sau đ&oacute;, như thể một vũng chất lỏng li&ecirc;n tục bị n&eacute;n lại.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&ocirc;i mắt của Tần Nam s&aacute;ng lấp l&aacute;nh khi anh v&eacute;o thanh kiếm Scarlet Flame Sabre trong tay.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một cảnh tượng kinh ho&agrave;ng đ&atilde; diễn ra. To&agrave;n bộ Scarlet Flame Sabre bắt đầu rung chuyển mạnh mẽ như thể một sức mạnh hủy diệt b&ugrave;ng nổ b&ecirc;n trong n&oacute;, khiến n&oacute; ngay lập tức vỡ ra th&agrave;nh nhiều mảnh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thanh kiếm biến th&agrave;nh v&ocirc; số chấm đỏ tr&ocirc;ng giống như &aacute;nh lửa.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam sắc mặt t&aacute;i nhợt. C&oacute; cảm gi&aacute;c như linh hồn của anh ta đ&atilde; bị r&uacute;t ra khỏi cơ thể của anh ta v&agrave;o khoảnh khắc Scarlet Flame Sabre vỡ tan th&agrave;nh từng mảnh, khiến anh ta gần như chết ngạt.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&igrave; V&otilde; kh&iacute;&nbsp; đ&atilde; được ban cho anh ta từ khi anh ta được sinh ra, n&ecirc;n việc Thần V&otilde; bị hủy diệt cũng giống như cơ thể của anh ta bị ph&aacute; hủy.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam nghiến răng chịu đựng &aacute;p lực lớn từ t&acirc;m tr&iacute;, vung tay về ph&iacute;a trước nắm lấy kh&ocirc;ng trung. Chất lỏng m&agrave;u đen trong tay hắn lập tức bộc ph&aacute;t lực hấp thụ b&iacute; ẩn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&ocirc; số chấm đỏ rải r&aacute;c trong kh&ocirc;ng kh&iacute; được tập hợp lại v&agrave; bị hấp thụ v&agrave;o b&oacute;ng đường của Tần Nam.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&aacute;m m&acirc;y s&eacute;t m&agrave;u đỏ lơ lửng b&ecirc;n trong chiếc dantian của Tần Nam đ&atilde; nuốt chửng những chấm đỏ. Ngay l&uacute;c đ&oacute;, n&oacute; như thể thời gian đ&atilde; đ&oacute;ng băng. Sau đ&oacute;, đ&aacute;m m&acirc;y s&eacute;t m&agrave;u đỏ b&ugrave;ng nổ!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Trận chiến Thần th&aacute;nh chiến đấu chống lại Thi&ecirc;n tộc v&agrave; trận chiến chống lại Tr&aacute;i đất. D&ugrave; họ l&agrave; Thần hay Phật, kh&ocirc;ng c&oacute; ng&agrave;y n&agrave;o kh&ocirc;ng chiến đấu, v&agrave; kh&ocirc;ng c&oacute; lần n&agrave;o kh&ocirc;ng chiến thắng&hellip; &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một giọng n&oacute;i uy nghi&ecirc;m cổ xưa lướt qua t&acirc;m tr&iacute; anh như v&ocirc; số tia chớp.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;AHHHHHH&hellip;&rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-39bce4de-7fff-3c47-7063-cbbaaff69be5\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&ocirc;i mắt mở to của Tần Nam đầy những đường đỏ. Trước khi anh ta c&oacute; thể k&ecirc;u l&ecirc;n v&igrave; đau đớn, c&uacute; va chạm cực lớn đ&atilde; khiến anh ta bất tỉnh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n', 'Cà Di', '2020-09-25 11:26:02', 1, 0, 312),
