INSERT INTO `stories` (`id`, `name`, `slug`, `description`, `image_link`, `category_id`, `created`, `updated`, `status`, `continues`, `view`, `author`, `rate_total`, `rate_count`) VALUES
(1, 'Cửu Dương Kiếm Thánh', 'cuu-duong-kiem-thanh', '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ed2e82f4-7fff-63ad-7a5d-e18d5101e157\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sinh vi&ecirc;n năm nhất Dương Định Thi&ecirc;n sở hữu Cửu Dương Thần C&ocirc;ng, được t&igrave;m thấy chỉ 1 phần triệu. Dương Hỏa anh ta c&oacute; sức sống v&ocirc; song v&agrave; do đ&oacute;, anh ta kh&ocirc;ng thể ở gần sự quyến rũ của phụ nữ trong suốt cuộc đời của m&igrave;nh. V&agrave;o đ&ecirc;m sinh nhật lần thứ 19, anh quyết định từ biệt cuộc sống trinh nguy&ecirc;n của m&igrave;nh. Sau khi ăn trộm v&agrave; nếm tr&aacute;i cấm, kết quả l&agrave; anh ta tự nhi&ecirc;n bốc ch&aacute;y!</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ed2e82f4-7fff-63ad-7a5d-e18d5101e157\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong c&aacute;i chết, anh ta vượt qua một thế giới kh&aacute;c; một thế giới t&ocirc;n trọng v&otilde; c&ocirc;ng. Cửu Dương Thần C&ocirc;ng nguy&ecirc;n bản, Cửu Dương Th&acirc;n kh&ocirc;ng thể nh&igrave;n thấy trong một thi&ecirc;n ni&ecirc;n kỷ! Anh được một &ocirc;ng gi&agrave; b&iacute; ẩn cứu khi anh đang ở độ tuổi cuối c&ugrave;ng v&agrave; &ocirc;ng gi&agrave; nhận anh l&agrave;m đồ đệ, sau đ&oacute; hứa h&ocirc;n con g&aacute;i của m&igrave;nh cho anh. V&igrave; vậy, theo c&aacute;ch kỳ lạ n&agrave;y, Dương Định Thi&ecirc;n đ&atilde; trở th&agrave;nh người kế vị của gia tộc lớn nhất dưới Thi&ecirc;n giới, v&agrave; trở th&agrave;nh vị h&ocirc;n phu số một của người đẹp dưới thi&ecirc;n đường.</span></span></p>\r\n<div>\r\n	&nbsp;</div>\r\n', 'nine-yang-sword-saint.jpg', 1, '2017-04-18 04:04:03', '2020-09-25 10:35:51', 1, 0, 90, 'Cà Di', 0, 0),
(4, 'Đấu Kháng Thiên', 'djau-khang-thien', '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-97d2f655-7fff-9a21-d1f8-16aac9978d67\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">N&uacute;i Abode thần thoại, V&aacute;ch đ&aacute; cuối m&acirc;y, nơi nguy hiểm nhất trong bốn khu vực chết ch&oacute;c của Lục địa Cloud Azure. Căn cứ của Cloud&rsquo;s End Cliff được gọi l&agrave; Nghĩa trang của Tử thần.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-97d2f655-7fff-9a21-d1f8-16aac9978d67\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong v&ocirc; số năm, đ&atilde; c&oacute; v&ocirc; số người rơi xuống v&aacute;ch đ&aacute; n&agrave;y. Kh&ocirc;ng ai trong số họ, kể cả ba vị thần mạnh hơn chủ nh&acirc;n, người c&oacute; sức mạnh c&oacute; thể xuy&ecirc;n thủng thi&ecirc;n đường, c&oacute; thể sống s&oacute;t trở về. Tuy nhi&ecirc;n, một cậu b&eacute; đang bị nhiều người truy đuổi v&igrave; một m&igrave;nh nắm giữ kho b&aacute;u v&ocirc; gi&aacute; đ&atilde; nhảy ra khỏi v&aacute;ch đ&aacute;, nhưng thay v&igrave; chết cậu tỉnh dậy trong th&acirc;n x&aacute;c của một cậu b&eacute; c&ugrave;ng t&ecirc;n ở thế giới kh&aacute;c!</span></span></p>\r\n<p>\r\n	<br />\r\n	<span id=\"docs-internal-guid-97d2f655-7fff-9a21-d1f8-16aac9978d67\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&acirc;y l&agrave; c&acirc;u chuyện về một cậu b&eacute; nắm giữ </span><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thi&ecirc;n độc ch&acirc;u</span><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">, tu luyện sức mạnh để chống lại trời đất, một vị ch&uacute;a tể nh&igrave;n ra thế giới!</span></span></p>\r\n', 'dau-khang-thien.png', 6, '2017-04-18 04:16:24', '2020-09-25 10:40:07', 1, 0, 403, 'Cà Di', 0, 0),
(5, 'Chiến Thần Phi Thiên', 'chien-than-phi-thien', '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4bb7496a-7fff-1eb4-3e88-335c55056e6c\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Linh hồn của chuy&ecirc;n gia vũ kh&iacute; h&agrave;ng đầu của Tr&aacute;i đất vượt qua một thế giới thay thế, hợp nhất với k&yacute; ức của V&otilde; Đế, tu luyện Kỹ thuật, qu&eacute;t sạch mọi phe đối lập bằng sức mạnh bất khả chiến bại!</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4bb7496a-7fff-1eb4-3e88-335c55056e6c\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&oacute; khả năng tinh luyện y thuật, c&oacute; khả năng chế tạo vũ kh&iacute; v&agrave; biết nghệ thuật khắc chữ&hellip;.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4bb7496a-7fff-1eb4-3e88-335c55056e6c\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tinh th&ocirc;ng mọi ng&agrave;nh nghề l&agrave; cung c&aacute;ch của c&aacute;c bậc đế vương!</span></span></p>\r\n<div>\r\n	&nbsp;</div>\r\n', 'war-sovereign-soaring-the-heavens.jpg', 5, '2017-04-18 04:17:00', '2020-09-25 10:34:38', 1, 0, 50, 'Cà Di', 0, 0),
(7, 'Quỷ Vương Ký', 'quy-vuong-ky', '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-957f75a6-7fff-dbe3-9261-9fda697795f7\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lưu Minh, từ khi c&ograve;n nhỏ, đ&atilde; sống trong một nh&agrave; t&ugrave; man rợ t&ecirc;n l&agrave; Đảo Quỷ, nơi c&aacute;c t&ugrave; nh&acirc;n kh&ocirc;ng bị bất kỳ l&iacute;nh canh hay an ninh n&agrave;o kiểm so&aacute;t. Khi h&ograve;n đảo bị ch&igrave;m do những sự kiện &ldquo;b&iacute; ẩn&rdquo;, chỉ một số &iacute;t người sống s&oacute;t - những người sống s&oacute;t sau đ&oacute; bị ch&iacute;nh phủ truy đuổi.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-957f75a6-7fff-dbe3-9261-9fda697795f7\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mặt kh&aacute;c, tại một nơi kh&aacute;c, hai học vi&ecirc;n đang lo lắng về điều g&igrave; sẽ xảy ra với họ bởi v&igrave; vị chủ nh&acirc;n trẻ tuổi m&agrave; họ phải bảo vệ đ&atilde; chết.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-957f75a6-7fff-dbe3-9261-9fda697795f7\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cậu chủ trẻ của họ tr&ocirc;ng gần giống MC của ch&uacute;ng ta</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-957f75a6-7fff-dbe3-9261-9fda697795f7\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chuyện g&igrave; sẽ xảy ra?</span></span></p>\r\n<div>\r\n	&nbsp;</div>\r\n', 'demons-diary.jpg', 6, '2017-05-31 03:07:35', '2020-09-25 10:45:15', 1, 0, 20, 'Cà Di', 0, 0),
(8, 'Vinh Sinh Chi Lữ', 'vinh-sinh-chi-lu', '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-722573a8-7fff-5c68-6ab3-5400ffbbaf63\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một cậu b&eacute; ngh&egrave;o v&agrave; b&igrave;nh thường từ một ng&ocirc;i l&agrave;ng gia nhập một gi&aacute;o ph&aacute;i nhỏ ở Giang Hồ v&agrave; t&igrave;nh cờ trở th&agrave;nh một đệ tử kh&ocirc;ng ch&iacute;nh thức. </span><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">H&agrave;n Lập</span><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">, một thường d&acirc;n, sẽ tạo dựng được chỗ đứng cho m&igrave;nh trong m&ocirc;n ph&aacute;i của m&igrave;nh như thế n&agrave;o? Với năng khiếu tầm thường của m&igrave;nh, l&agrave;m thế n&agrave;o anh ta sẽ th&agrave;nh c&ocirc;ng tr&ecirc;n con đường tu luyện v&agrave; trở th&agrave;nh một người bất tử?</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-722573a8-7fff-5c68-6ab3-5400ffbbaf63\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&acirc;y l&agrave; một c&acirc;u chuyện về một người ph&agrave;m b&igrave;nh thường, chống lại mọi kh&oacute; khăn, đụng độ với những con quỷ ma qu&aacute;i v&agrave; những người cổ đại để t&igrave;m ra con đường bất tử cho ri&ecirc;ng m&igrave;nh.</span></span></p>\r\n<div>\r\n	&nbsp;</div>\r\n', 'journey-to-immortality.jpg', 3, '2017-05-31 03:09:01', '2020-09-25 10:55:28', 1, 0, 50, 'Cà Di', 0, 0),
(10, 'Chiếc Nhẫn Toàn Năng', 'chiec-nhan-toan-nang', '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-689ba4a1-7fff-a396-eb19-19ef3974c157\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một chiếc nhẫn bất chấp thi&ecirc;n đường cho ph&eacute;p c&aacute;c đồ vật được đưa trở lại từ tr&ograve; chơi th&agrave;nh hiện thực!</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-689ba4a1-7fff-a396-eb19-19ef3974c157\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trước khi &quot;Dawn&quot; được ph&aacute;t h&agrave;nh, n&oacute; đ&atilde; c&oacute; được trạng th&aacute;i l&agrave; &quot;tr&aacute;i đất thứ hai&quot;. Giang Phi, một thiếu ni&ecirc;n m&ecirc; game đ&atilde; bước v&agrave;o thế giới v&agrave; nhanh ch&oacute;ng vươn l&ecirc;n về quyền lực, địa vị v&agrave; danh vọng. Với kỹ năng v&ocirc; song của m&igrave;nh, anh ta đ&atilde; ti&ecirc;u diệt tất cả những kẻ th&ugrave; chống lại m&igrave;nh. Tuy nhi&ecirc;n, sự gia tăng như vậy sẽ thu h&uacute;t sự ch&uacute; &yacute; của nhiều người, ngay cả khi anh ấy cố gắng l&agrave;m mọi thứ ở mức thấp, mọi thứ kh&ocirc;ng diễn ra như kế hoạch&hellip;</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-689ba4a1-7fff-a396-eb19-19ef3974c157\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Nếu cố gắng giữ bản th&acirc;n tr&aacute;nh xa &aacute;nh đ&egrave;n s&acirc;n khấu kh&ocirc;ng c&ograve;n t&aacute;c dụng, th&igrave; h&atilde;y đến! H&atilde;y cho thế giới biết đến sự tồn tại của t&ocirc;i! &rdquo; - Giang Phi</span></span></p>\r\n<div>\r\n	&nbsp;</div>\r\n', 'mmorpg-the-almighty-ring.jpg', 6, '2017-05-31 03:12:01', '2020-09-25 10:32:54', 1, 0, 56, 'Cà Di', 0, 0),
(12, 'Sage Monarch', 'sage-monarch', '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f97bc26f-7fff-ab5d-213f-be8b43eda439\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Th&agrave;nh Qu&acirc;n l&agrave; con trai của tộc trưởng của một gia đ&igrave;nh gi&agrave;u c&oacute; từ một thị trấn nhỏ.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f97bc26f-7fff-ab5d-213f-be8b43eda439\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tin rằng m&igrave;nh đang y&ecirc;u, anh ta đ&atilde; lấy trộm một vi&ecirc;n thuốc cho người y&ecirc;u của m&igrave;nh, chỉ để c&ocirc; g&aacute;i phản bội anh ta, bằng c&aacute;ch uống vi&ecirc;n thuốc v&agrave; bỏ lại anh ta.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f97bc26f-7fff-ab5d-213f-be8b43eda439\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bị bắt, chiếc dantian của anh ta bị qu&egrave; v&agrave; chết dưới tia s&eacute;t.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f97bc26f-7fff-ab5d-213f-be8b43eda439\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tia chớp n&agrave;y đ&atilde; gi&uacute;p anh kh&ocirc;i phục lại v&otilde; c&ocirc;ng đ&atilde; mất v&agrave; cũng gi&uacute;p anh c&oacute; được một kỹ thuật tu luyện mới giống như thần th&aacute;nh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f97bc26f-7fff-ab5d-213f-be8b43eda439\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau cơ hội thứ hai n&agrave;y, anh ta c&oacute; th&ecirc;m sức mạnh khi t&igrave;m c&aacute;ch trả th&ugrave; v&agrave; cũng để t&igrave;m về người mẹ đ&atilde; mất t&iacute;ch của m&igrave;nh&hellip;.</span></span></p>\r\n<div>\r\n	&nbsp;</div>\r\n', 'sage-monarch-sage-emperor.jpg', 2, '2017-05-31 03:13:52', '2020-09-25 10:46:10', 1, 0, 40, 'Cà Di', 0, 0),
(14, 'Siêu Thám Tử', 'sieu-tham-tu', '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-605123d3-7fff-5ca7-ff59-f6dbebc008e8\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke đ&atilde; chuyển đến Hoa Kỳ, nhưng cuối c&ugrave;ng nhận ra rằng Hoa Kỳ n&agrave;y kh&aacute;c với Hoa Kỳ của kiếp trước. Ở đ&acirc;y, c&aacute;c si&ecirc;u năng lực v&agrave; c&aacute;c nh&acirc;n vật trong phim trong kiếp trước của anh ta thực sự tồn tại. Luke sẽ l&agrave;m thế n&agrave;o trong một thế giới tồn tại những si&ecirc;u năng lực đ&aacute;ng sợ như vậy?</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-605123d3-7fff-5ca7-ff59-f6dbebc008e8\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">May mắn thay, anh ta c&oacute; một gian lận - Hệ thống si&ecirc;u th&aacute;m tử.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-605123d3-7fff-5ca7-ff59-f6dbebc008e8\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tham gia c&ugrave;ng Luke trong cuộc h&agrave;nh tr&igrave;nh mới để trở th&agrave;nh th&aacute;m tử.</span></span></p>\r\n<div>\r\n	&nbsp;</div>\r\n', 'super-detective-in-the-fictional-world.jpg', 2, '2017-05-31 03:15:35', '2020-09-25 10:47:48', 1, 0, 66, 'Cà Di', 0, 0),
(15, 'Truyện thường ở huyện', 'truyen-thuong-o-huyen', '<p>\r\n	cập nhật th&ocirc;ng tin ở huyện kịp thời nhanh ch&oacute;ng v&agrave; đ&ocirc;i khi kh&ocirc;ng ch&iacute;nh x&aacute;c</p>\r\n', 'Untitled.png', 5, '2017-05-31 03:16:45', '2020-09-15 08:33:44', 1, 0, 207, 'Cà Di', 0, 0),
(17, 'Hoàng Đế Thật Sự', 'hoang-dje-that-su', '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1b9a7a04-7fff-d613-b6f8-2b8613b71ab0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một cậu b&eacute; bị giam cầm h&agrave;ng triệu năm đ&atilde; lấy lại được cơ thể ph&agrave;m trần.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1b9a7a04-7fff-d613-b6f8-2b8613b71ab0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh trở th&agrave;nh một đệ tử của Gi&aacute;o ph&aacute;i Cổ xưa Cleansing Incense đang suy t&agrave;n nơi tổ sư của n&oacute; từng l&agrave; đệ tử của anh. Giờ đ&acirc;y, &ocirc;ng sẽ đưa gi&aacute;o ph&aacute;i n&agrave;y trở lại &aacute;nh h&agrave;o quang trước đ&acirc;y.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1b9a7a04-7fff-d613-b6f8-2b8613b71ab0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&acirc;y l&agrave; cuộc h&agrave;nh tr&igrave;nh của anh để đạt tới đỉnh v&agrave; trả th&ugrave; những kẻ đ&atilde; giam cầm anh.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1b9a7a04-7fff-d613-b6f8-2b8613b71ab0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&acirc;y l&agrave; c&acirc;u chuyện của anh ấy về việc gặp gỡ những người bạn cũ v&agrave; kết bạn mới.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1b9a7a04-7fff-d613-b6f8-2b8613b71ab0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&acirc;y l&agrave; con đường của anh ấy vượt qua Cửu giới v&agrave; trở th&agrave;nh người thống trị tiếp theo của Thi&ecirc;n giới.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1b9a7a04-7fff-d613-b6f8-2b8613b71ab0\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một v&agrave;i milennias đ&atilde; tr&ocirc;i qua v&agrave; thời kỳ ho&agrave;ng kim của c&aacute;c chuy&ecirc;n gia đ&atilde; qua. Một vị sư phụ m&agrave; c&aacute;c đệ tử từng l&agrave; những Vị thần bất tử kiệt xuất nhất trong 9 thế giới đều đ&atilde; rời bỏ &ocirc;ng ta. Với cơ thể ph&agrave;m trần, v&oacute;c d&aacute;ng ph&agrave;m trần v&agrave; b&aacute;nh xe sinh tử. . . anh ta sẽ qu&eacute;t 9 thế giới v&agrave; lấy những g&igrave; thuộc về m&igrave;nh.</span></span></p>\r\n<div>\r\n	&nbsp;</div>\r\n', 'emperors-domination.jpg', 6, '2017-04-18 04:16:24', '2020-09-25 10:41:04', 1, 0, 210, 'Cà Di', 0, 0),
(26, 'Super God Gene', 'super-god-gene', '<p>\r\n	<span id=\"docs-internal-guid-f6d58549-7fff-7ee4-00ec-b3eda36f639a\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tương lai mở ra tr&ecirc;n một quy m&ocirc; tuyệt vời v&agrave;o thời đại giữa c&aacute;c v&igrave; sao. Nh&acirc;n loại cuối c&ugrave;ng đ&atilde; giải quyết được c&ocirc;ng nghệ gen kh&ocirc;ng gian, nhưng khi nh&acirc;n loại tự vận chuyển m&igrave;nh v&agrave;o đầu kia, họ ph&aacute;t hiện ra nơi đ&oacute; kh&ocirc;ng c&oacute; qu&aacute; khứ hay tương lai, cũng kh&ocirc;ng c&oacute; bất kỳ v&ugrave;ng đất n&agrave;o dưới bầu trời đầy sao</span></span></p>\r\n', 'super-god-gene.jpg', 6, '2017-05-31 03:14:49', '2020-09-25 10:50:09', 1, 0, 90, 'Phê Kun', 0, 0),
(27, 'Software developer life', 'software-developer-life-1', '<p>\r\n	Cả trời t&acirc;m tư t&ocirc;i ở đ&acirc;y, vậy m&agrave; chẳng c&oacute; ai biết thứ tui mong mỏi l&agrave; ... L&ecirc;n lương</p>\r\n', 'download.jpg', 9, '2017-05-31 03:15:35', '2020-09-15 08:33:18', 1, 0, 120, 'Miruku Chan', 0, 0),
(28, 'Kẻ ngoại đạo', 'ke-ngoai-djao-1', '<p dir=\"ltr\" style=\"line-height:1.38;text-indent: 18pt;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span style=\"background-color: transparent; color: rgb(0, 0, 0); font-family: Arial; font-size: 11pt; white-space: pre-wrap; text-indent: 18pt;\">Nhạc xập x&igrave;nh ph&aacute;t ra từ hai d&agrave;n loa. Người đ&aacute;nh đ&agrave;n kh&eacute;p hờ đ&ocirc;i mắt, thả hồn theo tiếng nhạc, từng ng&oacute;n tay nhảy m&uacute;a tr&ecirc;n ph&iacute;m đ&agrave;n. Những lời ca vang l&ecirc;n từ miệng n&oacute;, m&agrave; chẳng ai buồn để &yacute; tới nội dung. Mọi người vẫn đang n&acirc;ng ly, ch&uacute;c tụng nhau bằng nhiều từ ngữ hoa mỹ nhất m&agrave; thật sự kh&ocirc;ng biết đối tượng c&oacute; hiểu được kh&ocirc;ng v&igrave; tiếng nhạc đ&atilde; &aacute;t gần như hết mọi &acirc;m thanh kh&aacute;c trong bữa tiệc. N&oacute; cũng kh&ocirc;ng quan t&acirc;m lắm, chỉ đơn giản l&agrave; n&oacute; sẽ h&aacute;t, h&aacute;t v&igrave; hợp đồng với chủ tiệc, h&aacute;t v&igrave; phục vụ mua vui cho kh&aacute;ch, h&aacute;t cho số phận cuộc đời m&igrave;nh. N&oacute; l&agrave;m điệu, cười tươi v&agrave; ra bộ kh&ecirc;u gợi khi kh&aacute;ch đến tr&ecirc;u chọc n&oacute;, sờ m&oacute; m&ocirc;ng v&agrave; ngực n&oacute;. Thi thoảng n&oacute; lại v&eacute;n c&aacute;i &aacute;o ngực ra để mấy &ocirc;ng kh&aacute;ch nồng nặc m&ugrave;i rượu kia nh&eacute;t t&iacute; tiền boa v&agrave;o. Rồi n&oacute; h&aacute;t, n&oacute; nhảy m&uacute;a đến khuya trong tiếng cười sằng sặc, tiếng vỗ tay v&agrave; phấn kh&iacute;ch của mọi người. Ừ, mặc kệ họ nghĩ g&igrave;, chỉ cần n&oacute; thấy vui v&igrave; đ&atilde; mang niềm vui đến cho người kh&aacute;c, v&agrave; n&oacute; c&oacute; tiền để trang trải cuộc sống, thực hiện ước mơ của m&igrave;nh, thế l&agrave; đủ.</span></p>\r\n<div>\r\n	&nbsp;</div>\r\n', 'ke_-ngoai-dao.jpg', 1, '2017-05-31 03:16:45', '2020-09-15 09:11:43', 1, 0, 160, '明智', 0, 0),
(30, 'Đao Bất Tử', 'djao-bat-tu', '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-eb832611-7fff-5d4b-0729-081c74502ea3\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Gripes cũng c&oacute; nguồn gốc của ch&uacute;ng v&agrave; c&aacute;c khoản nợ cũng vậy&nbsp; c&oacute; chủ nợ của ch&uacute;ng. Ch&uacute;ng ta đ&atilde; kh&ocirc;ng vượt qua nhau trong những ng&agrave;y trong qu&aacute; khứ hay hiện tại, cũng như kh&ocirc;ng h&igrave;nh th&agrave;nh bất kỳ mối hận th&ugrave; n&agrave;o. T&ocirc;i nhưng c&oacute; nghĩa vụ phải thực hiện điều n&agrave;y, v&igrave; vậy xin lỗi trước!</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-eb832611-7fff-5d4b-0729-081c74502ea3\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong kiếp trước, </span><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Thần</span><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\"> l&agrave; một người hiền l&agrave;nh nhưng cả đời bị &aacute;p bức.</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-eb832611-7fff-5d4b-0729-081c74502ea3\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giờ đ&acirc;y khi được t&aacute;i sinh, anh ấy đ&atilde; chọn trở th&agrave;nh một đao phủ v&agrave; cắt đứt mối quan hệ v&agrave; mối hận th&ugrave; bằng lưỡi kiếm của t&ecirc;n đao phủ của m&igrave;nh, giết chết tất cả những người leo l&ecirc;n Bệ của Đao phủ Bất tử!</span></span></p>\r\n<br />\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-eb832611-7fff-5d4b-0729-081c74502ea3\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đầu sẽ lăn! Con người &rsquo;trong c&otilde;i người, quỷ&rsquo; trong c&otilde;i quỷ, quỷ &rsquo;trong c&otilde;i quỷ v&agrave; những người bất tử&rsquo; trong c&otilde;i bất tử!</span></span></p>\r\n<div>\r\n	&nbsp;</div>\r\n', 'zhanxian.jpg', 6, '2020-07-14 12:35:52', '2020-09-25 10:38:01', 1, 0, 132, 'Cà Di', 8, 2),
(31, 'The fate of rune', 'the-fate-of-rune', '<div>\r\n	<p dir=\"ltr\" style=\"line-height:1.38;margin-left: 36pt;text-indent: 36pt;margin-top:0pt;margin-bottom:0pt;\">\r\n		<span id=\"docs-internal-guid-689ba9ba-7fff-fb47-80d5-44a175d50808\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong một thế giới m&agrave; c&aacute;c vị thần dần mất đi sức mạnh của m&igrave;nh. Đ&oacute; l&agrave; l&uacute;c thế lực ma tộc dần th&ocirc;n t&iacute;nh Midgard. Chỉ c&oacute; một đấng cứu thế c&oacute; thể tạo ra một tương lai tho&aacute;t khỏi b&oacute;ng đ&ecirc;m của ma tộc.</span></span></p>\r\n	<p dir=\"ltr\" style=\"line-height:1.38;margin-left: 36pt;text-indent: 36pt;margin-top:0pt;margin-bottom:0pt;\">\r\n		<span id=\"docs-internal-guid-689ba9ba-7fff-fb47-80d5-44a175d50808\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một người được định sinh ra mang trong m&igrave;nh sứ mệnh giải cứu thế giới, một người vốn dĩ c&oacute; số mệnh b&igrave;nh thường lại trở th&agrave;nh anh h&ugrave;ng giải cứu thế giới.</span></span></p>\r\n	<p dir=\"ltr\" style=\"line-height:1.38;margin-left: 36pt;text-indent: 36pt;margin-top:0pt;margin-bottom:0pt;\">\r\n		<span id=\"docs-internal-guid-689ba9ba-7fff-fb47-80d5-44a175d50808\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hai con người c&ugrave;ng chung vận mệnh sẽ thế n&agrave;o khi bản ng&atilde; v&agrave; ho&agrave;n cảnh kh&aacute;c nhau. Đến cuối c&ugrave;ng ai mới l&agrave; người tạo ra tương lai mới cho nh&acirc;n loại.</span></span></p>\r\n	<div>\r\n		&nbsp;</div>\r\n</div>\r\n<p>\r\n	&nbsp;</p>\r\n', 'caotu_n.jpg', 5, '2020-08-27 09:17:57', '2020-09-25 10:54:06', 1, 0, 98, 'Miruku Chan', 5, 1),
(32, 'Triệu đóa hồng', 'trieu-djoa-hong-1', '<p>\r\n	<span id=\"docs-internal-guid-bcbb94f0-7fff-1c55-4dba-81333594f701\"><span style=\"font-size: 11pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ch&agrave;ng họa sĩ tiễn ch&acirc;n vị kh&aacute;ch ra khỏi căn nh&agrave; nhỏ xinh. Người kh&aacute;ch sang trọng kia cũng ng&atilde; n&oacute;n ch&agrave;o anh, &aacute;nh mắt chứa đựng sự &aacute;y n&aacute;y. &ldquo;Anh chắc chứ?&rdquo; &ndash; Vị kh&aacute;ch hỏi lại. &ldquo;V&acirc;ng thưa &ocirc;ng, t&ocirc;i đ&atilde; quyết định rồi&rdquo;. Anh trả lời kh&ocirc;ng ch&uacute;t lưỡng lự nuối tiếc. &ldquo;Vậy, ch&uacute;c anh may mắn&rdquo;. Vị kh&aacute;ch gật đầu, bước đi xa dần.</span></span></p>\r\n', 'trieu-doa-hong.jpg', 4, '2020-09-11 05:42:17', '2020-09-16 06:22:08', 1, 1, 220, '明智', 0, 0),
(34, 'Kẻ Háo Ăn Trở Lại', 'ke-hao-an-tro-lai', '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2d70f669-7fff-087b-a502-dc5a213a3eb1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh ta l&agrave; một kẻ nghiện ngập, một kẻ thất bại, một con người đ&aacute;ng khinh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2d70f669-7fff-087b-a502-dc5a213a3eb1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng, một giấc mơ tho&aacute;ng qua c&oacute; thể kh&ocirc;ng phải l&agrave; giấc mơ đ&atilde; đ&aacute;nh thức lại những gi&aacute;c quan đ&atilde; mất một thời của anh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2d70f669-7fff-087b-a502-dc5a213a3eb1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sở hữu một khả năng rất đặc biệt, anh ta sẽ sử dụng n&oacute;, v&agrave; giấc mơ, để tạo ra con đường của m&igrave;nh trong thế giới m&agrave; b&acirc;y giờ được gọi l&agrave; Thi&ecirc;n đường đ&atilde; mất.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2d70f669-7fff-087b-a502-dc5a213a3eb1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">==============================</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2d70f669-7fff-087b-a502-dc5a213a3eb1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Con trai của thần Gula đ&atilde; trở lại.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2d70f669-7fff-087b-a502-dc5a213a3eb1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">T&ocirc;i đ&atilde; lạc v&agrave;o thế giới của cờ bạc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2d70f669-7fff-087b-a502-dc5a213a3eb1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">T&ocirc;i quay lưng với gia đ&igrave;nh, thậm ch&iacute; phản bội người y&ecirc;u.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2d70f669-7fff-087b-a502-dc5a213a3eb1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">T&ocirc;i đ&atilde; l&atilde;ng ph&iacute; mỗi ng&agrave;y trong cuộc đời m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2d70f669-7fff-087b-a502-dc5a213a3eb1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&oacute; l&agrave; một cuộc đời r&aacute;c rưởi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2d70f669-7fff-087b-a502-dc5a213a3eb1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thực tế cho t&ocirc;i biết như vậy:</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2d70f669-7fff-087b-a502-dc5a213a3eb1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Rằng t&ocirc;i sẽ chẳng l&agrave; g&igrave; bất kể t&ocirc;i đ&atilde; l&agrave;m g&igrave;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2d70f669-7fff-087b-a502-dc5a213a3eb1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Để thay đổi cuộc sống thảm hại của m&igrave;nh, thay v&agrave;o đ&oacute; t&ocirc;i đ&atilde; chọn tưởng tượng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2d70f669-7fff-087b-a502-dc5a213a3eb1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay cả khi đ&oacute;, n&oacute; vẫn l&agrave; một c&acirc;u chuyện tương tự.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2d70f669-7fff-087b-a502-dc5a213a3eb1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">T&ocirc;i tự hỏi liệu sự cứu rỗi sẽ đến ở cuối con đường d&agrave;i.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2d70f669-7fff-087b-a502-dc5a213a3eb1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng, t&ocirc;i buộc phải thừa nhận thất bại trước một thực thể h&ugrave;ng mạnh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2d70f669-7fff-087b-a502-dc5a213a3eb1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">T&ograve;a th&aacute;p do ch&iacute;nh tay t&ocirc;i g&acirc;y dựng giờ đ&atilde; vỡ vụn th&agrave;nh hư v&ocirc;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2d70f669-7fff-087b-a502-dc5a213a3eb1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chỉ một lần th&ocirc;i, t&ocirc;i v&ocirc; c&ugrave;ng mong muốn được biết sự thật về bản th&acirc;n m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2d70f669-7fff-087b-a502-dc5a213a3eb1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">- Lại gần, con t&ocirc;i&hellip;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-2d70f669-7fff-087b-a502-dc5a213a3eb1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">T&ocirc;i sẽ kh&ocirc;ng giữ lại lần n&agrave;y.</span></span></p>\r\n<div>\r\n	&nbsp;</div>\r\n', 'the-second-coming-of-gluttony.jpg', 1, '2020-09-25 10:43:01', '2020-09-25 10:43:01', 1, 0, 66, 'Cà Di', 0, 0),
(35, 'Supreme Magus', 'supreme-magus', '<p>\r\n	<span style=\"color: rgb(0, 0, 0); font-family: Nunito, sans-serif; font-size: 21.3333px; white-space: pre-wrap;\">Derek McCoy l&agrave; một người đ&agrave;n &ocirc;ng m&agrave; ngay từ thời trẻ đ&atilde; phải đối mặt với nhiều nghịch cảnh. Thường buộc phải giải quyết với việc tồn tại hơn l&agrave; sống, cuối c&ugrave;ng đ&atilde; t&igrave;m thấy vị tr&iacute; của m&igrave;nh tr&ecirc;n thế giới, cho đến khi mọi thứ bị tước đoạt khỏi anh ta lần cuối c&ugrave;ng. Sau khi mất mạng để trả th&ugrave; cho anh trai m&igrave;nh, anh t&aacute;i sinh cho đến khi t&igrave;m thấy một thế giới đ&aacute;ng sống, một thế giới đầy ma thuật v&agrave; qu&aacute;i vật. Theo ch&acirc;n anh ấy trong cuộc h&agrave;nh tr&igrave;nh của m&igrave;nh, từ người anh đau buồn đến người l&iacute;nh ngo&agrave;i h&agrave;nh tinh. Từ trẻ sơ sinh đến Ph&aacute;p sư tối cao.</span></p>\r\n', '00384-supreme-magus-webnovel.jpg', 10, '2020-09-25 10:53:19', '2020-09-25 10:53:19', 1, 0, 156, 'Phê Kun', 0, 0),
(36, 'Vô song đích chiến tinh thần', 'vo-song-djich-chien-tinh-than', '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-161b72b6-7fff-fd67-e33f-6e7cfb3b8093\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ở Lục địa chương lan, tồn tại một quy tắc: chỉ những người c&oacute; thể đ&aacute;nh thức được V&otilde; Kh&iacute; mới c&oacute; thể theo đuổi con đường tu luyện v&agrave; cấp bậc của V&otilde; Thần được x&aacute;c định khi n&oacute; được thức tỉnh. Sinh ra ở th&agrave;nh phố Linshui, Tần Nam l&agrave; một thi&ecirc;n t&agrave;i v&ocirc; song, sở hữu t&agrave;i năng tuyệt vời v&agrave; được đ&aacute;nh gi&aacute; cao sẽ trở th&agrave;nh một nh&agrave; tu luyện vĩ đại trong tương lai. Tuy nhi&ecirc;n, mọi thứ đ&atilde; thay đổi khi anh ta đ&aacute;nh thức V&otilde; Thần cấp thấp nhất, dẫn đến việc anh ta bị coi l&agrave; r&aacute;c rưởi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-161b72b6-7fff-fd67-e33f-6e7cfb3b8093\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">May mắn thay, l&uacute;c c&ograve;n trẻ Tần Nam bị một tia s&eacute;t đ&aacute;nh tr&uacute;ng, ngẫu nhi&ecirc;n lấy được Thần Kh&iacute; trận trọng yếu, c&oacute; khả năng thăng cấp, ph&aacute; vỡ quy tắc tối thượng của Lục địa chương lan. Anh ta kh&ocirc;ng hề biết rằng V&otilde; kh&iacute; của anh ta sở hữu một b&iacute; mật lớn, điều n&agrave;y sẽ tiếp tục được tiết lộ khi anh ta t&igrave;m hiểu th&ecirc;m về c&acirc;u chuyện của Thần kh&iacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-161b72b6-7fff-fd67-e33f-6e7cfb3b8093\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&iacute; mật đằng sau Thần Kh&iacute; l&agrave; g&igrave;? Lời nguyền của Lục địa chương lan l&agrave; g&igrave;? Ch&iacute;nh x&aacute;c th&igrave; ai mới xứng đ&aacute;ng sở hữu thần kh&iacute;?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-161b72b6-7fff-fd67-e33f-6e7cfb3b8093\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tham gia c&ugrave;ng Tần Nam v&agrave; những người bạn đồng h&agrave;nh của anh ấy trong cuộc h&agrave;nh tr&igrave;nh của họ, chiến đấu chống lại nhiều đối thủ kh&aacute;c nhau, đ&aacute;nh bại kẻ th&ugrave; của họ v&agrave; t&igrave;m kiếm c&acirc;u trả lời để v&eacute;n m&agrave;n những b&iacute; ẩn!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-161b72b6-7fff-fd67-e33f-6e7cfb3b8093\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Chủ nh&acirc;n, xin h&atilde;y mang t&ocirc;i theo trong cuộc h&agrave;nh tr&igrave;nh chiến đấu chống lại Cửu Thi&ecirc;n, được kh&ocirc;ng?&rdquo;</span></span></p>\r\n<div>\r\n	&nbsp;</div>\r\n', 'peerless-battle-spirit.jpg', 6, '2020-09-25 10:56:18', '2020-09-25 10:56:18', 1, 0, 162, 'Cà Di', 0, 0);

