INSERT INTO `stories` (`id`, `name`, `slug`, `description`, `image_link`, `category_id`, `created`, `updated`, `status`, `continues`, `view`, `author`, `rate_total`, `rate_count`) VALUES
(1, 'Cửu Dương Kiếm Thánh', 'cuu-duong-kiem-thanh', '<div>\r\n	Sinh vi&ecirc;n năm nhất Yang Dingtian sở hữu Cửu Dương Th&acirc;n, được t&igrave;m thấy chỉ 1 phần triệu. Dương Hỏa của anh ta c&oacute; sức sống v&ocirc; song v&agrave; do đ&oacute;, anh ta kh&ocirc;ng thể ở gần sự quyến rũ của phụ nữ trong suốt cuộc đời của m&igrave;nh. V&agrave;o đ&ecirc;m sinh nhật lần thứ 19, anh quyết định từ biệt cuộc sống trinh nguy&ecirc;n của m&igrave;nh. Sau khi ăn trộm v&agrave; nếm tr&aacute;i cấm, kết quả l&agrave; anh ta tự ph&aacute;t ch&aacute;y!</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Trong c&aacute;i chết, anh ta vượt qua một thế giới kh&aacute;c; một thế giới t&ocirc;n trọng Quyền năng V&otilde;. Cửu Dương Th&acirc;n nguy&ecirc;n bản, th&ecirc; thảm của hắn, ch&iacute;nh l&agrave; Cửu Dương Th&acirc;n kh&ocirc;ng thể nh&igrave;n thấy trong một thi&ecirc;n ni&ecirc;n kỷ! Anh đ&atilde; được một &ocirc;ng gi&agrave; b&iacute; ẩn cứu khi anh ở v&agrave;o thời khắc cuối c&ugrave;ng v&agrave; &ocirc;ng gi&agrave; đ&atilde; nhận anh l&agrave;m đồ đệ, sau đ&oacute; hứa h&ocirc;n con g&aacute;i của m&igrave;nh cho anh. V&igrave; vậy, theo c&aacute;ch kỳ lạ n&agrave;y, Yang Dingtian đ&atilde; trở th&agrave;nh người kế vị của gia tộc lớn nhất dưới Thi&ecirc;n giới, v&agrave; trở th&agrave;nh vị h&ocirc;n phu số một của mỹ nh&acirc;n dưới thi&ecirc;n đường.</div>\r\n', 'nine-yang-sword-saint.jpg', 3, '2017-04-18 04:04:03', '2020-09-15 09:00:32', 1, 0, 8, 'Cà Di', 0, 0),
(4, 'Chống lại các vị thần', 'chong-lai-cac-vi-than', '<div>\r\n	N&uacute;i Abode thần thoại, V&aacute;ch đ&aacute; cuối m&acirc;y, nơi nguy hiểm nhất trong bốn khu vực chết ch&oacute;c của Lục địa m&acirc;y Azure. Căn cứ của Cloud&rsquo;s End Cliff được gọi l&agrave; Nghĩa trang của Tử thần.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Trong v&ocirc; số năm, số lượng người đ&atilde; rơi xuống v&aacute;ch đ&aacute; n&agrave;y l&agrave; qu&aacute; nhiều để đếm. Kh&ocirc;ng ai trong số họ, kể cả ba vị thần mạnh hơn chủ nh&acirc;n, người c&oacute; sức mạnh c&oacute; thể xuy&ecirc;n thủng thi&ecirc;n đường, c&oacute; thể sống s&oacute;t trở về. Tuy nhi&ecirc;n, một cậu b&eacute; đang bị nhiều người truy đuổi v&igrave; một m&igrave;nh nắm giữ kho b&aacute;u v&ocirc; gi&aacute; đ&atilde; nhảy ra khỏi v&aacute;ch đ&aacute;, nhưng thay v&igrave; chết cậu tỉnh dậy trong th&acirc;n x&aacute;c của một cậu b&eacute; c&ugrave;ng t&ecirc;n ở thế giới kh&aacute;c!</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Đ&acirc;y l&agrave; c&acirc;u chuyện về một cậu b&eacute; nắm giữ Sky Poison Pearl, tu luyện sức mạnh để chống lại trời đất, một vị ch&uacute;a tể nh&igrave;n ra thế giới!</div>\r\n', '00007-against-the-gods-web-novel.jpg', 6, '2017-04-18 04:16:24', '2020-09-15 08:33:02', 1, 0, 41, 'Cà Di', 0, 0),
(5, 'Vua Chiến Tranh', 'vua-chien-tranh', '<div>\r\n	Linh hồn của chuy&ecirc;n gia vũ kh&iacute; h&agrave;ng đầu của Tr&aacute;i đất đ&atilde; vượt qua một thế giới thay thế, hợp nhất với k&yacute; ức của V&otilde; Đế T&aacute;i sinh, tu luyện Kỹ thuật Chủ quyền Chiến tranh Cửu Long, qu&eacute;t sạch mọi phe đối lập bằng sức mạnh bất khả chiến bại!</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	C&oacute; khả năng tinh luyện y học, c&oacute; khả năng chế tạo vũ kh&iacute; v&agrave; biết nghệ thuật khắc chữ&hellip;.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Tinh th&ocirc;ng mọi ng&agrave;nh nghề l&agrave; cung c&aacute;ch của c&aacute;c bậc đế vương!</div>\r\n', 'war-sovereign-soaring-the-heavens.jpg', 5, '2017-04-18 04:17:00', '2020-09-15 08:59:07', 1, 0, 1, 'Cà Di', 0, 0),
(7, 'Nhật Kí Của Quỷ', 'nhat-ki-cua-quy', '<div>\r\n	Liu Ming, từ khi c&ograve;n nhỏ, đ&atilde; sống trong một nh&agrave; t&ugrave; man rợ t&ecirc;n l&agrave; Savage Island, nơi c&aacute;c t&ugrave; nh&acirc;n kh&ocirc;ng bị bất kỳ l&iacute;nh canh hay an ninh n&agrave;o kiểm so&aacute;t. Khi h&ograve;n đảo bị ch&igrave;m do những sự kiện &ldquo;b&iacute; ẩn&rdquo;, chỉ một số &iacute;t người sống s&oacute;t - những người sống s&oacute;t sau đ&oacute; bị ch&iacute;nh phủ truy đuổi.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Mặt kh&aacute;c, tại một nơi kh&aacute;c, hai học vi&ecirc;n đang lo lắng về điều g&igrave; sẽ xảy ra với họ bởi v&igrave; vị chủ nh&acirc;n trẻ tuổi m&agrave; họ phải bảo vệ đ&atilde; chết.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Cậu chủ trẻ của họ tr&ocirc;ng gần giống MC của ch&uacute;ng ta</div>\r\n<div>\r\n	Chuyện g&igrave; sẽ xảy ra?</div>\r\n', 'demons-diary.jpg', 6, '2017-05-31 03:07:35', '2020-09-15 08:56:44', 1, 0, 2, 'Cà Di', 0, 0),
(8, 'Cuộc Hành Trình Của Người Bất Tử', 'cuoc-hanh-trinh-cua-nguoi-bat-tu', '<div>\r\n	Một cậu b&eacute; ngh&egrave;o v&agrave; b&igrave;nh thường từ một ng&ocirc;i l&agrave;ng gia nhập một gi&aacute;o ph&aacute;i nhỏ ở Giang Hồ v&agrave; t&igrave;nh cờ trở th&agrave;nh một Đệ tử kh&ocirc;ng ch&iacute;nh thức. Han Li, một thường d&acirc;n, sẽ tạo dựng được chỗ đứng cho m&igrave;nh trong m&ocirc;n ph&aacute;i của m&igrave;nh như thế n&agrave;o? Với năng khiếu tầm thường của m&igrave;nh, l&agrave;m thế n&agrave;o anh ta sẽ th&agrave;nh c&ocirc;ng tr&ecirc;n con đường tu luyện v&agrave; trở th&agrave;nh một người bất tử?</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Đ&acirc;y l&agrave; một c&acirc;u chuyện về một người ph&agrave;m b&igrave;nh thường, chống lại mọi kh&oacute; khăn, đụng độ với những con quỷ ma quỷ v&agrave; những người cổ đại để t&igrave;m ra con đường bất tử của ri&ecirc;ng m&igrave;nh.</div>\r\n', 'journey-to-immortality.jpg', 3, '2017-05-31 03:09:01', '2020-09-15 08:55:10', 1, 0, 3, 'Cà Di', 0, 0),
(9, 'Truyện Về Ác Quỷ Và Thần Linh', 'truyen-ve-ac-quy-va-than-linh', '<div>\r\n	Bị giết bởi một vị Ho&agrave;ng đế hiền triết v&agrave; t&aacute;i sinh th&agrave;nh ch&iacute;nh m&igrave;nh năm 13 tuổi, Nie Li đ&atilde; c&oacute; cơ hội sống thứ hai. Cơ hội thứ hai để thay đổi mọi thứ v&agrave; cứu những người th&acirc;n y&ecirc;u v&agrave; th&agrave;nh phố th&acirc;n y&ecirc;u của m&igrave;nh. Anh sẽ một lần nữa chiến đấu với Sage Emperor để trả th&ugrave; cho c&aacute;i chết của m&igrave;nh v&agrave; những người anh y&ecirc;u. Với kiến thức rộng lớn về h&agrave;ng trăm năm của cuộc sống m&agrave; anh ấy t&iacute;ch lũy được ở kiếp trước, điều khiển những tinh linh quỷ mạnh nhất, anh ấy sẽ đạt đến đỉnh cao của V&otilde; thuật.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Những th&ugrave; hận trong qu&aacute; khứ sẽ được giải quyết trong kiếp sống mới n&agrave;y. &ldquo;V&igrave; t&ocirc;i đ&atilde; trở lại, n&ecirc;n trong kiếp n&agrave;y, t&ocirc;i sẽ trở th&agrave;nh Vua của c&aacute;c vị thần thống trị mọi thứ. H&atilde;y để mọi thứ kh&aacute;c run rẩy dưới ch&acirc;n t&ocirc;i! &rdquo;</div>\r\n', 'tales-of-demons-and-gods.jpg', 3, '2017-05-31 03:09:35', '2020-09-15 08:35:37', 1, 0, 5, 'Cà Di', 0, 0),
(10, 'Chiếc Nhẫn Toàn Năng', 'chiec-nhan-toan-nang-1', '<div>\r\n	Một chiếc nhẫn bất chấp thi&ecirc;n đường cho ph&eacute;p c&aacute;c đồ vật được đưa trở lại từ tr&ograve; chơi th&agrave;nh hiện thực!</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Trước khi &lsquo;Dawn&rsquo; được ph&aacute;t h&agrave;nh, n&oacute; đ&atilde; c&oacute; được trạng th&aacute;i l&agrave; &lsquo;tr&aacute;i đất thứ hai&rsquo;. Jiang Fei, một thiếu ni&ecirc;n m&ecirc; game đ&atilde; bước v&agrave;o thế giới v&agrave; nhanh ch&oacute;ng vươn l&ecirc;n về quyền lực, địa vị v&agrave; danh vọng. Với kỹ năng v&ocirc; song của m&igrave;nh, anh ta đ&atilde; ti&ecirc;u diệt tất cả những kẻ th&ugrave; chống lại m&igrave;nh. Tuy nhi&ecirc;n, sự gia tăng như vậy sẽ thu h&uacute;t sự ch&uacute; &yacute; của nhiều người, ngay cả khi anh ấy cố gắng l&agrave;m mọi thứ ở mức thấp, mọi thứ kh&ocirc;ng diễn ra như kế hoạch&hellip;</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	&ldquo;Nếu cố gắng giữ m&igrave;nh tr&aacute;nh xa &aacute;nh đ&egrave;n s&acirc;n khấu kh&ocirc;ng c&ograve;n t&aacute;c dụng, th&igrave; h&atilde;y đến! H&atilde;y cho thế giới biết đến sự tồn tại của t&ocirc;i! &rdquo;</div>\r\n', 'mmorpg-the-almighty-ring.jpg', 6, '2017-05-31 03:12:01', '2020-09-16 11:23:05', 1, 0, 0, 'Cà Di', 0, 0),
(12, 'Sage Monarch', 'sage-monarch-1', '<div>\r\n	Yang Qi l&agrave; con trai của tộc trưởng một gia đ&igrave;nh gi&agrave;u c&oacute; từ một thị trấn nhỏ.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Tin rằng m&igrave;nh đang y&ecirc;u, anh ta đ&atilde; lấy trộm một vi&ecirc;n thuốc cho người y&ecirc;u của m&igrave;nh, chỉ để c&ocirc; g&aacute;i phản bội anh ta, bằng c&aacute;ch uống vi&ecirc;n thuốc v&agrave; bỏ lại anh ta.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Bị bắt, chiếc dantian của anh ta bị qu&egrave; v&agrave; chết dưới tia s&eacute;t.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Tia chớp n&agrave;y đ&atilde; gi&uacute;p anh kh&ocirc;i phục lại v&otilde; c&ocirc;ng đ&atilde; mất v&agrave; cũng gi&uacute;p anh c&oacute; được một kỹ thuật tu luyện mới giống như thần th&aacute;nh.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Sau cơ hội thứ hai n&agrave;y, anh ta c&oacute; th&ecirc;m sức mạnh khi t&igrave;m c&aacute;ch trả th&ugrave; v&agrave; cũng để t&igrave;m về người mẹ đ&atilde; mất t&iacute;ch của m&igrave;nh&hellip;.</div>\r\n', 'sage-monarch-sage-emperor.jpg', 2, '2017-05-31 03:13:52', '2020-09-15 08:33:56', 1, 0, 4, 'Cà Di', 0, 0),
(14, 'Siêu Thám Tử Trong Thế Giới Ảo', 'sieu-tham-tu-trong-the-gioi-ao-1', '<div>\r\n	Luke đ&atilde; chuyển đến Hoa Kỳ, nhưng cuối c&ugrave;ng nhận ra rằng Hoa Kỳ n&agrave;y kh&aacute;c với Hoa Kỳ của kiếp trước. Ở đ&acirc;y, những si&ecirc;u năng lực v&agrave; c&aacute;c nh&acirc;n vật trong phim trong kiếp trước của anh ta thực sự tồn tại. Luke sẽ l&agrave;m thế n&agrave;o trong một thế giới tồn tại những si&ecirc;u năng lực đ&aacute;ng sợ như vậy?</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	May mắn thay, anh ta c&oacute; một gian lận - Hệ thống si&ecirc;u th&aacute;m tử.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Tham gia c&ugrave;ng Luke trong cuộc h&agrave;nh tr&igrave;nh mới để trở th&agrave;nh th&aacute;m tử.</div>\r\n', 'super-detective-in-the-fictional-world.jpg', 2, '2017-05-31 03:15:35', '2020-09-15 08:33:49', 1, 0, 3, 'Cà Di', 0, 0),
(15, 'Truyện thường ở huyện', 'truyen-thuong-o-huyen', '<p>\r\n	cập nhật th&ocirc;ng tin ở huyện kịp thời nhanh ch&oacute;ng v&agrave; đ&ocirc;i khi kh&ocirc;ng ch&iacute;nh x&aacute;c</p>\r\n', 'Untitled.png', 5, '2017-05-31 03:16:45', '2020-09-15 08:33:44', 1, 0, 27, 'Cà Di', 0, 0),
(17, 'Hoàng Đế Thật sự', 'hoang-dje-that-su-1', '<div>\r\n	Một cậu b&eacute; bị giam cầm h&agrave;ng triệu năm đ&atilde; lấy lại được cơ thể ph&agrave;m trần.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Anh ta trở th&agrave;nh đệ tử của Gi&aacute;o ph&aacute;i cổ xưa Tẩy rửa Hương trầm, nơi tổ sư của n&oacute; từng l&agrave; đệ tử của anh ta. B&acirc;y giờ &ocirc;ng sẽ đưa gi&aacute;o ph&aacute;i n&agrave;y trở lại &aacute;nh h&agrave;o quang trước đ&acirc;y.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Đ&acirc;y l&agrave; cuộc h&agrave;nh tr&igrave;nh của anh để đạt tới đỉnh v&agrave; trả th&ugrave; những kẻ đ&atilde; giam cầm anh.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Đ&acirc;y l&agrave; c&acirc;u chuyện của anh ấy về việc gặp gỡ những người bạn cũ v&agrave; kết bạn mới.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Đ&acirc;y l&agrave; con đường của anh ấy vượt qua Cửu giới v&agrave; trở th&agrave;nh người thống trị tiếp theo của Thi&ecirc;n giới.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Một v&agrave;i milennias đ&atilde; tr&ocirc;i qua v&agrave; thời kỳ ho&agrave;ng kim của c&aacute;c chuy&ecirc;n gia cũng đ&atilde; qua. Một vị sư phụ m&agrave; c&aacute;c đệ tử từng l&agrave; những Vị thần bất tử kiệt xuất nhất trong 9 thế giới đều đ&atilde; rời bỏ &ocirc;ng ta. Với cơ thể ph&agrave;m trần, v&oacute;c d&aacute;ng ph&agrave;m trần v&agrave; b&aacute;nh xe sinh tử. . . anh ta sẽ qu&eacute;t 9 thế giới v&agrave; lấy những g&igrave; thuộc về m&igrave;nh.</div>\r\n', 'emperors-domination.jpg', 6, '2017-04-18 04:16:24', '2020-09-15 08:33:32', 1, 0, 19, 'Cà Di', 0, 0),
(26, 'Super God Gene', 'super-god-gene-1', '<p>\r\n	<span font-size:=\"\" nunito=\"\" style=\"color: rgb(33, 35, 37); font-family: \">The future unfolded on a magnificent scale into the Interstellar Age. Humanity finally solved the space warp technology, but when humanity transported themselves into the other end, they discovered that place neither had a past nor future, nor was there any land under the starry skies</span></p>\r\n', 'super-god-gene.jpg', 6, '2017-05-31 03:14:49', '2020-09-15 08:33:26', 1, 0, 7, 'Phê Kun', 0, 0),
(27, 'Software developer life', 'software-developer-life-1', '<p>\r\n	Cả trời t&acirc;m tư t&ocirc;i ở đ&acirc;y, vậy m&agrave; chẳng c&oacute; ai biết thứ tui mong mỏi l&agrave; ... L&ecirc;n lương</p>\r\n', 'download.jpg', 9, '2017-05-31 03:15:35', '2020-09-15 08:33:18', 1, 0, 9, 'Miruku Chan', 0, 0),
(28, 'Kẻ ngoại đạo', 'ke-ngoai-djao-1', '<p dir=\"ltr\" style=\"line-height:1.38;text-indent: 18pt;text-align: justify;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span style=\"background-color: transparent; color: rgb(0, 0, 0); font-family: Arial; font-size: 11pt; white-space: pre-wrap; text-indent: 18pt;\">Nhạc xập x&igrave;nh ph&aacute;t ra từ hai d&agrave;n loa. Người đ&aacute;nh đ&agrave;n kh&eacute;p hờ đ&ocirc;i mắt, thả hồn theo tiếng nhạc, từng ng&oacute;n tay nhảy m&uacute;a tr&ecirc;n ph&iacute;m đ&agrave;n. Những lời ca vang l&ecirc;n từ miệng n&oacute;, m&agrave; chẳng ai buồn để &yacute; tới nội dung. Mọi người vẫn đang n&acirc;ng ly, ch&uacute;c tụng nhau bằng nhiều từ ngữ hoa mỹ nhất m&agrave; thật sự kh&ocirc;ng biết đối tượng c&oacute; hiểu được kh&ocirc;ng v&igrave; tiếng nhạc đ&atilde; &aacute;t gần như hết mọi &acirc;m thanh kh&aacute;c trong bữa tiệc. N&oacute; cũng kh&ocirc;ng quan t&acirc;m lắm, chỉ đơn giản l&agrave; n&oacute; sẽ h&aacute;t, h&aacute;t v&igrave; hợp đồng với chủ tiệc, h&aacute;t v&igrave; phục vụ mua vui cho kh&aacute;ch, h&aacute;t cho số phận cuộc đời m&igrave;nh. N&oacute; l&agrave;m điệu, cười tươi v&agrave; ra bộ kh&ecirc;u gợi khi kh&aacute;ch đến tr&ecirc;u chọc n&oacute;, sờ m&oacute; m&ocirc;ng v&agrave; ngực n&oacute;. Thi thoảng n&oacute; lại v&eacute;n c&aacute;i &aacute;o ngực ra để mấy &ocirc;ng kh&aacute;ch nồng nặc m&ugrave;i rượu kia nh&eacute;t t&iacute; tiền boa v&agrave;o. Rồi n&oacute; h&aacute;t, n&oacute; nhảy m&uacute;a đến khuya trong tiếng cười sằng sặc, tiếng vỗ tay v&agrave; phấn kh&iacute;ch của mọi người. Ừ, mặc kệ họ nghĩ g&igrave;, chỉ cần n&oacute; thấy vui v&igrave; đ&atilde; mang niềm vui đến cho người kh&aacute;c, v&agrave; n&oacute; c&oacute; tiền để trang trải cuộc sống, thực hiện ước mơ của m&igrave;nh, thế l&agrave; đủ.</span></p>\r\n<div>\r\n	&nbsp;</div>\r\n', 'ke_-ngoai-dao.jpg', 1, '2017-05-31 03:16:45', '2020-09-15 09:11:43', 1, 0, 16, '明智', 0, 0),
(30, 'Đao Bất Tử', 'djao-bat-tu-1', '<div>\r\n	Ch&uacute;ng ta đ&atilde; kh&ocirc;ng vượt qua nhau trong những ng&agrave;y trong qu&aacute; khứ hay hiện tại, cũng như kh&ocirc;ng h&igrave;nh th&agrave;nh bất kỳ mối hận th&ugrave; n&agrave;o. T&ocirc;i nhưng c&oacute; nghĩa vụ phải thực hiện điều n&agrave;y, v&igrave; vậy xin lỗi trước!</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Kiếp trước Dương Trần l&agrave; một người hiền l&agrave;nh, cả đời bị &aacute;p bức.</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Giờ đ&acirc;y, khi được t&aacute;i sinh, anh ta đ&atilde; chọn trở th&agrave;nh một đao phủ v&agrave; cắt đứt mối quan hệ v&agrave; mối hận th&ugrave; với lưỡi kiếm của t&ecirc;n đao phủ của m&igrave;nh, giết chết tất cả những ai leo l&ecirc;n Bệ Đao H&agrave;nh Tử Bất Tử!</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	Đầu sẽ lăn! Con người &rsquo;trong c&otilde;i người, quỷ&rsquo; trong c&otilde;i quỷ, quỷ &rsquo;trong c&otilde;i quỷ v&agrave; những người bất tử&rsquo; trong c&otilde;i bất tử!</div>\r\n', 'zhanxian.jpg', 6, '2020-07-14 12:35:52', '2020-09-15 08:33:10', 1, 0, 124, 'Cà Di', 8, 2),
(31, 'The fate of rune', 'the-fate-of-rune-1', '<div>\r\n	- Trong một thế giới m&agrave; c&aacute;c vị thần dần mất đi sức mạnh của m&igrave;nh. Đ&oacute; l&agrave; l&uacute;c thế lực ma tộc dần th&ocirc;n t&iacute;nh Midgard. Chỉ c&oacute; một đấng cứu thế c&oacute; thể tạo ra một tương lai tho&aacute;t khỏi b&oacute;ng đ&ecirc;m của ma tộc.</div>\r\n<div>\r\n	- Một người được định sinh ra mang trong m&igrave;nh sứ mệnh giải cứu thế giới, một người vốn dĩ c&oacute; số mệnh b&igrave;nh thường lại trở th&agrave;nh anh h&ugrave;ng giải cứu thế giới.</div>\r\n<div>\r\n	- Hai con người c&ugrave;ng chung vận mệnh sẽ thế n&agrave;o khi bản ng&atilde; v&agrave; ho&agrave;n cảnh kh&aacute;c nhau. Đến cuối c&ugrave;ng ai mới l&agrave; người tạo ra tương lai mới cho nh&acirc;n loại.</div>\r\n<div>\r\n	&nbsp;</div>\r\n', 'caotu_n.jpg', 5, '2020-08-27 09:17:57', '2020-09-15 09:02:22', 1, 0, 96, 'Miruku Chan', 5, 1),
(32, 'Triệu đóa hồng', 'trieu-djoa-hong-1', '<p>\r\n	<span id=\"docs-internal-guid-bcbb94f0-7fff-1c55-4dba-81333594f701\"><span style=\"font-size: 11pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ch&agrave;ng họa sĩ tiễn ch&acirc;n vị kh&aacute;ch ra khỏi căn nh&agrave; nhỏ xinh. Người kh&aacute;ch sang trọng kia cũng ng&atilde; n&oacute;n ch&agrave;o anh, &aacute;nh mắt chứa đựng sự &aacute;y n&aacute;y. &ldquo;Anh chắc chứ?&rdquo; &ndash; Vị kh&aacute;ch hỏi lại. &ldquo;V&acirc;ng thưa &ocirc;ng, t&ocirc;i đ&atilde; quyết định rồi&rdquo;. Anh trả lời kh&ocirc;ng ch&uacute;t lưỡng lự nuối tiếc. &ldquo;Vậy, ch&uacute;c anh may mắn&rdquo;. Vị kh&aacute;ch gật đầu, bước đi xa dần.</span></span></p>\r\n', 'trieu-doa-hong.jpg', 4, '2020-09-11 05:42:17', '2020-09-16 06:22:08', 1, 1, 22, '明智', 0, 0);

