(1, 'Ecchi', 'ecchi', 0, 1, 2, '2017-04-05 08:59:19', '2020-10-18 13:36:24', 'Thường có những tình huống nhạy cảm nhằm lôi cuốn người xem'),
(2, 'Adventure', 'adventure', 0, 5, 6, '2017-04-05 09:01:52', '2020-10-18 13:31:51', 'Thể loại phiêu lưu, mạo hiểm, thường là hành trình của các nhân vật'),
(3, 'Shoujo', 'shoujo-1', 0, 3, 4, '2017-04-18 02:58:17', '2020-10-21 09:35:14', 'Thể loại tình cảm nhẹ nhàng'),
(4, 'Romance', '', 0, 7, 8, '2017-04-18 03:31:10', '2017-04-18 03:31:10', 'Romance'),
(5, 'Manga/Comic', 'mangacomic-1', 0, 9, 10, '2017-04-18 03:31:30', '2020-10-18 13:34:01', 'Các thề loại truyện tranh'),
(6, 'Action', 'action', 0, 11, 12, '2017-04-18 03:32:07', '2020-10-18 13:31:03', 'Thể loại này thường có nội dung về đánh nhau, bạo lực.'),
(8, 'Magic ', 'magic', 0, 13, 14, '2017-05-31 03:57:38', '2020-10-18 13:39:43', 'Thể loại này thường có nội dung về ma thuật, phép thuật...'),
(9, 'Oneshot', 'oneshot-1', 0, NULL, NULL, '2020-09-14 06:41:53', '2020-10-18 13:38:15', 'Truyện ngắn kiểu của developer'),
(10, 'Fantasy', 'fantasy-1', 0, NULL, NULL, '2020-09-25 10:52:47', '2020-10-18 13:36:38', 'Thể loại xuất phát từ trí tưởng tượng phong phú, từ pháp thuật đến thế giới trong mơ thậm chí là những câu chuyện thần tiên'),
(11, 'Đam Mỹ', 'djam-my-1', 0, NULL, NULL, '2020-10-12 04:01:30', '2020-10-18 13:36:08', 'Truyện tình cảm giữa nam và nam.'),
(12, 'Ngôn tình', 'ngon-tinh-1', 0, NULL, NULL, '2020-10-15 01:40:56', '2020-10-18 13:37:55', 'Truyện thuộc kiểu lãng mạn, kể về những sự kiện vui buồn trong tình yêu của nhân vật chính.'),
(13, 'Anime', 'anime', 0, NULL, NULL, '2020-10-18 13:32:18', '2020-10-18 13:32:18', 'Truyện đã được chuyển thể thành film Anime'),
(14, 'Chuyển Sinh', 'chuyen-sinh', 0, NULL, NULL, '2020-10-18 13:32:53', '2020-10-18 13:32:53', 'Thể loại này là những câu chuyện về người ở một thế giới này xuyên đến một thế giới khác, có thể là thế giới mang phong cách trung cổ với kiếm sĩ và ma thuật, hay thế giới trong game, hoặc có thể là bạn chết ở nơi này và được chuyển sinh đến nơi khác'),
(15, 'Comedy', 'comedy', 0, NULL, NULL, '2020-10-18 13:33:26', '2020-10-18 13:33:26', 'Thể loại có nội dung trong sáng và cảm động, thường có các tình tiết gây cười, các xung đột nhẹ nhàng'),
(16, 'Horror', 'horror', 0, NULL, NULL, '2020-10-18 13:35:47', '2020-10-18 13:35:47', 'Horror là: rùng rợn, nghe cái tên là bạn đã hiểu thể loại này có nội dung thế nào. Nó làm cho bạn kinh hãi, khiếp sợ, ghê tởm, run rẩy, có thể gây sock - một thể loại không dành cho người yếu tim'),
(17, 'Harem', 'harem', 0, NULL, NULL, '2020-10-18 13:37:33', '2020-10-18 13:37:33', 'Thể loại truyện tình cảm, lãng mạn mà trong đó, nhiều nhân vật thích một nhân vật chính'),
(18, 'School Life', 'school-life', 0, NULL, NULL, '2020-10-18 13:40:29', '2020-10-18 13:40:29', 'Trong thể loại này, ngữ cảnh diễn biến câu chuyện chủ yếu ở trường học'),
(19, 'Psychological', 'psychological', 0, NULL, NULL, '2020-10-18 13:40:56', '2020-10-18 13:40:56', 'Thể loại liên quan đến những vấn đề về tâm lý của nhân vật ( tâm thần bất ổn, điên cuồng ...)'),
(20, 'Trinh Thám', 'trinh-tham', 0, NULL, NULL, '2020-10-18 13:41:52', '2020-10-18 13:41:52', 'Các truyện có nội dung về các vụ án, các thám tử cảnh sát điều tra...'),
(21, 'Sports', 'sports', 0, NULL, NULL, '2020-10-18 13:43:01', '2020-10-18 13:43:01', 'Đúng như tên gọi, những môn thể thao như bóng đá, bóng chày, bóng chuyền, đua xe, cầu lông,... là một phần của thể loại này');

