(189, 'Phần 3. Trời đánh cũng không tránh được đứa mình ghét', 'phan-3-troi-djanh-cung-khong-tranh-djuoc-djua-minh-ghet', NULL, NULL, NULL, 27, 'Comic_003.png', 0, '<p>\r\n	<img alt=\"\" src=\"/public/upload/images/Comic_003.png\" style=\"width: 921px; height: 1334px;\" /></p>\r\n', 'Miruku Chan', '2020-10-20 03:25:23', 1, 0, 17),
(190, 'Chap 2. Lạc đường', 'chap-2-lac-djuong', NULL, NULL, NULL, 38, '', 0, '<p>\r\n	<img alt=\"\" src=\"/public/upload/images/001%282%29.jpg\" style=\"width: 1000px; height: 1467px;\" /><img alt=\"\" src=\"/public/upload/images/002%282%29.jpg\" style=\"width: 1000px; height: 1000px;\" /><img alt=\"\" src=\"/public/upload/images/003%282%29.jpg\" style=\"width: 1000px; height: 1467px;\" /><img alt=\"\" src=\"/public/upload/images/004%282%29.jpg\" style=\"width: 1000px; height: 1467px;\" /><img alt=\"\" src=\"/public/upload/images/005%282%29.jpg\" style=\"width: 1000px; height: 1467px;\" /><img alt=\"\" src=\"/public/upload/images/006%282%29.jpg\" style=\"width: 1000px; height: 1467px;\" /><img alt=\"\" src=\"/public/upload/images/007%282%29.jpg\" style=\"width: 1000px; height: 1467px;\" /><img alt=\"\" src=\"/public/upload/images/008%282%29.jpg\" style=\"width: 1000px; height: 1467px;\" /><img alt=\"\" src=\"/public/upload/images/009%282%29.jpg\" style=\"width: 1000px; height: 1467px;\" /><img alt=\"\" src=\"/public/upload/images/010%282%29.jpg\" style=\"width: 1000px; height: 1467px;\" /><img alt=\"\" src=\"/public/upload/images/011%282%29.jpg\" style=\"width: 1000px; height: 1467px;\" /><img alt=\"\" src=\"/public/upload/images/012%282%29.jpg\" style=\"width: 1000px; height: 1467px;\" /><img alt=\"\" src=\"/public/upload/images/013%281%29.jpg\" style=\"width: 1000px; height: 1467px;\" /><img alt=\"\" src=\"/public/upload/images/014%281%29.jpg\" style=\"width: 1000px; height: 1467px;\" /><img alt=\"\" src=\"/public/upload/images/015%281%29.jpg\" style=\"width: 1000px; height: 1467px;\" /><img alt=\"\" src=\"/public/upload/images/016%281%29.jpg\" style=\"width: 1000px; height: 1467px;\" /><img alt=\"\" src=\"/public/upload/images/017%281%29.jpg\" style=\"width: 1000px; height: 1467px;\" /><img alt=\"\" src=\"/public/upload/images/018%281%29.jpg\" style=\"width: 1000px; height: 1467px;\" /><img alt=\"\" src=\"/public/upload/images/020%281%29.jpg\" style=\"width: 1000px; height: 1467px;\" /><img alt=\"\" src=\"/public/upload/images/021%281%29.jpg\" style=\"width: 1000px; height: 1467px;\" /><img alt=\"\" src=\"/public/upload/images/022%281%29.jpg\" style=\"width: 1000px; height: 1467px;\" /></p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/images/023%281%29.jpg\" style=\"width: 1000px; height: 1467px;\" /><img alt=\"\" src=\"/public/upload/images/024%281%29.jpg\" style=\"width: 1000px; height: 1467px;\" /><img alt=\"\" src=\"/public/upload/images/025%281%29.jpg\" style=\"width: 1000px; height: 1467px;\" /><img alt=\"\" src=\"/public/upload/images/026%281%29.jpg\" style=\"width: 1000px; height: 1467px;\" /><img alt=\"\" src=\"/public/upload/images/027%281%29.jpg\" style=\"width: 1000px; height: 1467px;\" /><img alt=\"\" src=\"/public/upload/images/028%281%29.jpg\" style=\"width: 1000px; height: 1467px;\" /><img alt=\"\" src=\"/public/upload/images/029%281%29.jpg\" style=\"width: 1000px; height: 1467px;\" /></p>\r\n', 'Miruku Chan', '2020-10-18 12:24:12', 1, 0, 34),
(191, 'Chap 3. Anh hùng', 'chap-3-anh-hung-1', NULL, NULL, NULL, 38, '', 0, '<p>\r\n	<img alt=\"\" src=\"/public/upload/images/001%283%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/002%283%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/003%283%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/004%283%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/005%283%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/006%283%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/007%283%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/008%283%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/009%283%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/010%283%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/011%283%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/012%283%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/013%282%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/014%282%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/015%282%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/016%282%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/017%282%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/018%282%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/019%281%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/020%282%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/021%282%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/022%282%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/023%282%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/024%282%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/025%282%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/026%282%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/027%282%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/028%282%29.jpg\" style=\"width: 960px; height: 1400px;\" /><img alt=\"\" src=\"/public/upload/images/029%282%29.jpg\" style=\"width: 630px; height: 630px;\" /></p>\r\n', 'Miruku Chan', '2020-10-18 12:24:32', 1, 0, 19),
(192, 'Chương 4. Câu chuyện kỳ bí ở học viện.', 'chuong-4-cau-chuyen-ky-bi-o-hoc-vien', NULL, NULL, NULL, 31, '', 0, '<p>\r\n	&nbsp; &nbsp; &nbsp;S&aacute;ng h&ocirc;m sau, sau khi tạm biệt cha mẹ. Aiden c&ugrave;ng Syringa thu dọn h&agrave;nh l&yacute; l&ecirc;n đường. Dọc đường đi cả hai đều kh&ocirc;ng biết phải n&oacute;i g&igrave; với nhau n&ecirc;n cứ im lặng suốt. Tho&aacute;ng thấy cứ c&aacute;i đ&agrave; im lặng n&agrave;y th&igrave; sẽ căng thẳng chết mất, Aiden b&egrave;n hắng giọng v&agrave;i tiếng rồi hỏi.</p>\r\n<ul>\r\n	<li role=\"presentation\">\r\n		N&egrave;, ch&uacute;ng ta đang đi đ&acirc;u vậy.</li>\r\n	<li role=\"presentation\">\r\n		Đi về hướng kia, qua khỏi khu rừng n&agrave;y. - Syringa chỉ tay về hướng trước mặt.</li>\r\n	<li role=\"presentation\">\r\n		Hướng kia, chẳng phải l&agrave;..hướng th&agrave;nh phố sao? - Aiden hỏi lại đầy nghi hoặc.</li>\r\n	<li role=\"presentation\">\r\n		Đ&uacute;ng rồi, ch&iacute;nh l&agrave; hướng đ&oacute;. - Syringa khẳng định chắc như đinh đ&oacute;ng cột.</li>\r\n	<li role=\"presentation\">\r\n		Nếu vậy sao ngay l&uacute;c đầu gặp c&ocirc; kh&ocirc;ng n&oacute;i lu&ocirc;n đi, lại mất c&ocirc;ng v&ograve;ng v&egrave;o rồi quay về đ&acirc;y nữa. - Aiden n&oacute;i với vẻ ch&aacute;n chường.</li>\r\n	<li role=\"presentation\">\r\n		T&ocirc;i c&ograve;n phải quan s&aacute;t anh, ai biết anh c&oacute; phải l&agrave; người được chọn kh&ocirc;ng. Với lại..- Syringa ngập ngừng.</li>\r\n	<li role=\"presentation\">\r\n		Với lại t&ocirc;i khi đ&oacute; cũng kh&ocirc;ng cảm nhận được vị tr&iacute; của phong ấn nằm ở đ&acirc;u. - Syringa ngại ng&ugrave;ng đ&aacute;p lại.</li>\r\n	<li role=\"presentation\">\r\n		X&ugrave;y. .tưởng c&ocirc; thế n&agrave;o, h&oacute;a ra cũng chỉ c&oacute; thế.</li>\r\n	<li role=\"presentation\">\r\n		&Yacute; anh l&agrave; thế n&agrave;o? - Syringa nắm chặt nắm đấm trong tay, dường như c&ograve;n c&oacute; thể nh&igrave;n thấy được tia lửa trong mắt c&ocirc;.</li>\r\n</ul>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Tho&aacute;ng thấy t&iacute;nh mạng của bản th&acirc;n sắp bị đe dọa, Aiden vội v&agrave;ng xua xua tay l&aacute;i sang chủ đề kh&aacute;c.</p>\r\n<ul>\r\n	<li role=\"presentation\">\r\n		Đ&acirc;u c&oacute;, đ&acirc;u c&oacute; &yacute; g&igrave; đ&acirc;u. C&ocirc; xem, chẳng phải l&agrave; sắp đến th&agrave;nh phố rồi sao.</li>\r\n</ul>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Nh&igrave;n về ph&iacute;a trước, th&agrave;nh phố Mimir đ&atilde; xuất hiện ở ph&iacute;a trước. Th&agrave;nh phố v&ocirc; c&ugrave;ng n&aacute;o nhiệt v&agrave; sầm uất, từ xa đ&atilde; c&oacute; thể nghe thấy được tiếng người cười n&oacute;i r&ocirc;m rả. Cả hai nhanh ch&oacute;ng tiến v&agrave;o th&agrave;nh phố, hai người dừng ch&acirc;n ở một qu&aacute;n tr&agrave; nhỏ, qu&aacute;n được trang tr&iacute; theo phong c&aacute;ch phục hưng với c&aacute;c khung cửa sổ được bao bọc bởi c&aacute;c thanh sắt uốn lượn đẹp mắt. B&agrave;n ghế, ly t&aacute;ch cũng theo c&ugrave;ng một phong c&aacute;ch ấy tr&ocirc;ng v&ocirc; c&ugrave;ng trang nh&atilde;. Hai người chọn một chiếc b&agrave;n ngồi ngay m&aacute;i hi&ecirc;n để tiện cho việc quan s&aacute;t khung cảnh xung quanh.</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;L&uacute;c n&agrave;y đ&atilde; l&agrave; buổi xế chiều, kh&ocirc;ng kh&iacute; n&aacute;o nhiệt cũng đ&atilde; giảm xuống, l&uacute;c n&agrave;y cả th&agrave;nh phố y&ecirc;n lặng hơn hẳn. Cả hai đang ngồi suy nghĩ mong lung th&igrave; nghe thấy ở b&agrave;n b&ecirc;n cạnh c&oacute; một nh&oacute;m thanh ni&ecirc;n cả nam lẫn nữ đang ngồi b&agrave;n t&aacute;n r&ocirc;m rả.</p>\r\n<ul>\r\n	<li role=\"presentation\">\r\n		Hồi tối đ&uacute;ng l&agrave; sợ thật, x&eacute;m t&iacute; nữa l&agrave; ta đ&atilde; bị con ma đ&oacute; h&ugrave; cho hồn v&iacute;a bay mất rồi. - Một thanh ni&ecirc;n vừa n&oacute;i, vẻ mặt vẫn c&ograve;n tho&aacute;ng ch&uacute;t sợ h&atilde;i.</li>\r\n	<li role=\"presentation\">\r\n		Đ&aacute;ng sợ thế, h&ocirc;m qua ch&uacute;ng t&ocirc;i chỉ d&aacute;m đứng ở ngo&agrave;i kh&ocirc;ng d&aacute;m bước v&agrave;o, nhưng vẫn nghe thấy mấy &acirc;m thanh v&ocirc; c&ugrave;ng đ&aacute;ng sợ. Giờ nhớ lại vẫn nổi hết cả gai ốc. - Một c&ocirc; g&aacute;i cũng n&oacute;i.</li>\r\n</ul>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Hai người như bắt được v&agrave;ng, hai &aacute;nh mắt đồng loạt nh&igrave;n nhau. Aiden vội đứng dậy, bước đến chỗ nh&oacute;m thanh ni&ecirc;n kia ra vẻ cười n&oacute;i.</p>\r\n<ul>\r\n	<li role=\"presentation\">\r\n		Ban n&atilde;y t&ocirc;i ở b&agrave;n b&ecirc;n, nghe c&aacute;c cậu n&oacute;i về hồn ma g&igrave; đ&oacute;, t&iacute;nh t&ocirc;i rất l&agrave; th&iacute;ch nghe mấy chuyện kỳ b&iacute;, kh&ocirc;ng biết mọi người c&oacute; thể kể cho t&ocirc;i nghe một ch&uacute;t được kh&ocirc;ng?</li>\r\n</ul>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Đ&aacute;m thanh ni&ecirc;n nh&igrave;n Aiden một lượt như đang suy nghĩ g&igrave; đ&oacute;. Một thanh ni&ecirc;n c&oacute; d&aacute;ng người nhỏ nhắn, niềm nở bắt chuyện.</p>\r\n<ul>\r\n	<li role=\"presentation\">\r\n		Nh&igrave;n cậu l&agrave; biết từ nơi xa đến đ&acirc;y rồi, n&agrave;o n&agrave;o ngồi xuống đ&acirc;y, t&ocirc;i sẽ kể cho cậu nghe chuyện ly kỳ ở học viện. - Người thanh ni&ecirc;n nhỏ con n&oacute;i rồi chỉ chỉ c&aacute;i ghế b&ecirc;n cạnh &yacute; bảo Aiden ngồi xuống.</li>\r\n</ul>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Aiden kh&ocirc;ng ch&uacute;t kh&aacute;ch kh&iacute; k&eacute;o ghế ngồi xuống chăm ch&uacute; nh&igrave;n người thanh ni&ecirc;n nhỏ con.</p>\r\n<ul>\r\n	<li role=\"presentation\">\r\n		C&acirc;u chuyện phải bắt đầu v&agrave;o hai mươi năm trước, đột nhi&ecirc;n c&oacute; một ng&agrave;y đang nắng gắt th&igrave; trời bỗng xuất hiện dị tượng, m&acirc;y đen k&eacute;o đến, sấm chớp nổi l&ecirc;n. Rồi bỗng đ&ugrave;ng một c&aacute;i, một tia s&eacute;t gi&aacute;ng xuống. M&acirc;y đen bỗng biến mất, cả bầu trời lại trong l&agrave;nh trở lại - Người thanh ni&ecirc;n nhỏ con dừng một l&aacute;t rồi kể tiếp.</li>\r\n	<li role=\"presentation\">\r\n		Sau khi dị tượng đ&oacute; xảy ra kh&ocirc;ng biết từ đ&acirc;u bắt đầu nổi l&ecirc;n tin đồn rằng, cứ v&agrave;o đ&ecirc;m khuya ở tầng hầm của học viện sẽ xuất hiện một b&oacute;ng ma nữ đi lang thang, n&oacute; cứ vừa đi vừa kh&oacute;c. N&oacute; mặc một bộ đồ nữ sinh của học viện, m&aacute;i t&oacute;c d&agrave;i đen &aacute;nh x&otilde;a xuống, nếu gặp n&oacute; m&agrave; kh&ocirc;ng đi qua lu&ocirc;n th&igrave; n&oacute; sẽ b&aacute;m theo v&agrave; hỏi rằng &ldquo;Ngươi thấy ta c&oacute; đẹp kh&ocirc;ng?&rdquo;, c&ograve;n nếu hỏi n&oacute; th&igrave; n&oacute; sẽ xoay mặt lại nh&igrave;n người đ&oacute;. Nhưng điều đ&aacute;ng sợ nhất l&agrave; con ma nữ đ&oacute; n&oacute; kh&ocirc;ng c&oacute; mặt.&nbsp;</li>\r\n	<li role=\"presentation\">\r\n		Ahhhh&hellip;- Một c&ocirc; g&aacute;i la l&ecirc;n sợ h&atilde;i.</li>\r\n	<li role=\"presentation\">\r\n		Đừng kể nữa, cậu l&agrave;m cậu ấy sợ rồi k&igrave;a. Một c&ocirc; g&aacute;i kh&aacute;c &ocirc;m lấy c&ocirc; g&aacute;i đang &ocirc;m mặt sợ h&atilde;i kia vỗ về an ủi.</li>\r\n	<li role=\"presentation\">\r\n		Chỉ c&oacute; bấy nhi&ecirc;u th&ocirc;i sao? - Aiden hơi hụt hẫng hỏi.</li>\r\n	<li role=\"presentation\">\r\n		Ch&uacute;ng t&ocirc;i chỉ biết c&oacute; bấy nhi&ecirc;u, h&ocirc;m qua ch&uacute;ng t&ocirc;i cũng v&igrave; t&ograve; m&ograve; muốn thử xem lời đồn đ&oacute; c&oacute; thực kh&ocirc;ng. Kết quả l&agrave; cậu thấy rồi đ&oacute;, đ&aacute;m ch&uacute;ng t&ocirc;i bị dọa sợ mất mật. - Người thanh ni&ecirc;n nhỏ con nh&uacute;n vai n&oacute;i.</li>\r\n	<li role=\"presentation\">\r\n		Vậy sao, t&ocirc;i cũng muốn đến đ&oacute; xem thử. - Aiden h&agrave;o hứng n&oacute;i.</li>\r\n	<li role=\"presentation\">\r\n		Kh&ocirc;ng được đ&acirc;u, chỉ c&oacute; học vi&ecirc;n của học viện mới được đi v&agrave;o học viện. Cậu kh&ocirc;ng v&agrave;o được đ&acirc;u. - Một thanh ni&ecirc;n cao lớn n&oacute;i.</li>\r\n	<li role=\"presentation\">\r\n		Vậy..cảm ơn mọi người nh&eacute;! - Thấy kh&ocirc;ng c&ograve;n thu được th&ocirc;ng tin n&agrave;o kh&aacute;c, Aiden vội tạm biệt đ&aacute;m thanh ni&ecirc;n quay lại b&agrave;n của m&igrave;nh.&nbsp;</li>\r\n</ul>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Cậu kể cho Syringa nghe to&agrave;n bộ c&acirc;u chuyện vừa n&atilde;y. Syringa lắng nghe rồi trầm mặc suy tư rất l&acirc;u.</p>\r\n<ul>\r\n	<li role=\"presentation\">\r\n		Nếu l&agrave; c&acirc;u chuyện xảy ra v&agrave;o hai mươi năm trước, c&oacute; thể l&agrave; c&oacute; li&ecirc;n quan đến phong ấn. - Syringa n&oacute;i.</li>\r\n	<li role=\"presentation\">\r\n		C&ocirc; c&oacute; chắc kh&ocirc;ng? - Aiden hỏi đầy ho&agrave;i nghi.</li>\r\n	<li role=\"presentation\">\r\n		T&ocirc;i cũng kh&ocirc;ng chắc lắm, nhưng ch&uacute;ng ta phải đến đ&oacute; xem thử xem chuyện l&agrave; như thế n&agrave;o.</li>\r\n	<li role=\"presentation\">\r\n		Nhưng b&acirc;y giờ phải đi bằng c&aacute;ch n&agrave;o? Ch&uacute;ng ta đ&acirc;u phải học vi&ecirc;n đ&acirc;u, chẳng lẽ lại tr&egrave;o tường v&agrave;o &agrave;? Đ&oacute; l&agrave; học viện ph&eacute;p thuật, l&agrave;m g&igrave; dễ tr&egrave;o như vậy. - Aiden n&oacute;i.</li>\r\n	<li role=\"presentation\">\r\n		T&ocirc;i c&oacute; thể đưa hai người v&agrave;o đ&oacute;. - Một giọng n&oacute;i vang l&ecirc;n, thanh &acirc;m mang v&agrave;i phần lạnh nhạt.</li>\r\n</ul>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Cả hai đồng loạt xoay người nh&igrave;n về hướng c&oacute; tiếng n&oacute;i. C&aacute;ch hai người kh&ocirc;ng xa l&agrave; một c&ocirc; g&aacute;i với m&aacute;i t&oacute;c &aacute;nh t&iacute;m c&ugrave;ng với đ&ocirc;i m&agrave;u hổ ph&aacute;ch, m&aacute;i t&oacute;c được cắt ngắn xoăn th&agrave;nh từng lọn, tr&ecirc;n đầu c&agrave;i một chiếc nơ to kết hợp với chiếc đầm x&ograve;e kiểu d&aacute;ng cầu kỳ. Th&ecirc;m v&agrave;o đ&oacute; l&agrave; khu&ocirc;n mặt c&ugrave;ng với d&aacute;ng người nhỏ nhắn tr&ocirc;ng c&ocirc; g&aacute;i chẳng kh&aacute;c n&agrave;o một con b&uacute;p b&ecirc; được trưng trong tủ k&iacute;nh ở c&aacute;c cửa h&agrave;ng b&aacute;n đồ chơi thiếu nữ.</p>\r\n<ul>\r\n	<li role=\"presentation\">\r\n		Em g&aacute;i, em bao nhi&ecirc;u tuổi rồi? C&oacute; phải l&agrave; đi lạc rồi kh&ocirc;ng? C&oacute; cần anh đ&acirc;y đưa về nh&agrave; kh&ocirc;ng? - Aiden nhanh như chớp đ&atilde; chạy đến b&ecirc;n c&ocirc; g&aacute;i nhỏ xoa xoa m&aacute;i t&oacute;c x&ugrave; của c&ocirc;.</li>\r\n	<li role=\"presentation\">\r\n		Mau lấy c&aacute;i tay dơ bẩn của anh ra khỏi người t&ocirc;i ngay. - C&ocirc; g&aacute;i n&oacute;i rồi, hất tay Aiden đang xoa xoa đầu c&ocirc; ra.</li>\r\n	<li role=\"presentation\">\r\n		Kh&ocirc;ng cho sờ th&igrave; th&ocirc;i, &iacute;ch kỷ vậy. - Aiden bĩu m&ocirc;i.</li>\r\n	<li role=\"presentation\">\r\n		C&ograve;n nữa, t&ocirc;i đ&acirc;y đ&atilde; mười t&aacute;m tuổi rồi, kh&ocirc;ng phải l&agrave; đứa trẻ ranh như anh n&oacute;i đ&acirc;u. - C&ocirc; g&aacute;i n&oacute;i rồi đứng dậy khỏi ghế, thoạt chừng c&ocirc; chỉ cao đến vai của anh, c&ograve;n thấp hơn cả Syringa.</li>\r\n	<li role=\"presentation\">\r\n		C&aacute;i g&igrave;? C&ocirc;? Mười t&aacute;m tuổi?. - Aiden ngạc nhi&ecirc;n hỏi.</li>\r\n	<li role=\"presentation\">\r\n		Phải, th&igrave; sao? Kh&ocirc;ng giống &agrave;? - C&ocirc; g&aacute;i n&oacute;i với vẻ giận dỗi, khu&ocirc;n mặt trắng trẻo tho&aacute;ng ửng hồng.</li>\r\n	<li role=\"presentation\">\r\n		Mười t&aacute;m tuổi. - Aiden liếc nh&igrave;n Syringa th&acirc;n h&igrave;nh đầy đặn, v&ograve;ng n&agrave;o ra v&ograve;ng đ&oacute; rồi lại nh&igrave;n sang c&ocirc; g&aacute;i nhỏ. - Mười t&aacute;m tuổi? - V&oacute;c người nhỏ nhắn, chẳng kh&aacute;c n&agrave;o thiếu nữ chưa ph&aacute;t triển xong.</li>\r\n	<li role=\"presentation\">\r\n		&Yacute; anh l&agrave; sao? - Syringa c&ugrave;ng c&ocirc; g&aacute;i nhỏ c&ugrave;ng đồng thanh hỏi, c&ograve;n cho Aiden một c&uacute; đ&aacute; đau điếng v&agrave;o ch&acirc;n.</li>\r\n	<li role=\"presentation\">\r\n		&Acirc;y da..c&aacute;c c&ocirc; kh&ocirc;ng thể nhẹ nh&agrave;ng hơn được &agrave;? - Aiden khu&ocirc;n mặt nhăn nh&oacute; n&oacute;i.</li>\r\n	<li role=\"presentation\">\r\n		Kh&ocirc;ng. - Cả hai lại đồng thanh trả lời.</li>\r\n	<li role=\"presentation\">\r\n		Quay lại chuyện ch&iacute;nh, xin tự giới thiệu t&ocirc;i l&agrave; Lycoris. - Lycoris n&oacute;i rồi ch&igrave;a tay về ph&iacute;a Aiden.</li>\r\n	<li role=\"presentation\">\r\n		Ch&agrave;o c&ocirc;, t&ocirc;i l&agrave; Syringa. - Syringa nhanh ch&oacute;ng nắm lấy tay Lycoris. - C&ograve;n đ&acirc;y l&agrave; Aiden.</li>\r\n	<li role=\"presentation\">\r\n		T&ocirc;i nghe n&oacute;i hai người muốn v&agrave;o học viện c&oacute; đ&uacute;ng kh&ocirc;ng?&nbsp;</li>\r\n	<li role=\"presentation\">\r\n		Đ&uacute;ng vậy, ch&uacute;ng t&ocirc;i muốn đến đ&oacute; v&igrave; nghe đồn về c&acirc;u chuyện kỳ b&iacute; ở học viện n&ecirc;n muốn đi xem thực hư thế n&agrave;o. - Aiden n&oacute;i.</li>\r\n	<li role=\"presentation\">\r\n		C&ocirc; n&oacute;i l&agrave; c&oacute; thể dẫn ch&uacute;ng t&ocirc;i đến đ&oacute; sao? - Syringa hỏi đầy ngờ vực.</li>\r\n	<li role=\"presentation\">\r\n		Đ&uacute;ng vậy nhưng với một điều kiện. - Lycoris n&oacute;i, miệng nở một nụ cười b&iacute; hiểm. - Hai người phải đưa t&ocirc;i đi theo c&ugrave;ng.</li>\r\n	<li role=\"presentation\">\r\n		Được th&ocirc;i. - Aiden chưa kịp suy nghĩ đ&atilde; đồng &yacute;.</li>\r\n	<li role=\"presentation\">\r\n		N&egrave;, ngộ nhỡ c&ocirc; ta kh&ocirc;ng phải người tốt rồi sao? - Syringa thấy vậy vội k&eacute;o tay Aiden n&oacute;i nhỏ.</li>\r\n	<li role=\"presentation\">\r\n		C&ocirc; y&ecirc;n t&acirc;m đi, kh&ocirc;ng c&oacute; người xấu n&agrave;o lại đ&aacute;ng y&ecirc;u như vậy đ&acirc;u. - Aiden n&oacute;i, mặt tho&aacute;ng vẻ ngại ng&ugrave;ng.</li>\r\n	<li role=\"presentation\">\r\n		Anh&hellip; - Syringa c&oacute; vẻ rất tức giận với c&aacute;i suy nghĩ thiển cận n&agrave;y của Aiden.</li>\r\n	<li role=\"presentation\">\r\n		C&ocirc; y&ecirc;n t&acirc;m, t&ocirc;i sẽ tr&ocirc;ng chừng c&ocirc; ta m&agrave;. - Aiden lại n&oacute;i xong quay sang n&oacute;i với Lycoris. - Vậy b&acirc;y giờ ch&uacute;ng ta l&agrave;m g&igrave;?</li>\r\n	<li role=\"presentation\">\r\n		B&acirc;y giờ cứ nghỉ ngơi đi, t&ocirc;i sẽ đưa hai người đến qu&aacute;n trọ ở gần đ&acirc;y. Chờ đến khi trời tối ch&uacute;ng ta sẽ v&agrave;o đ&oacute;.</li>\r\n</ul>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;V&agrave; thế l&agrave; ba người rời khỏi qu&aacute;n tr&agrave;, c&ugrave;ng nhau đi đến một qu&aacute;n trọ ở gần đ&oacute;.</p>\r\n', 'Miruku Chan', '2020-10-19 01:32:53', 1, 0, 32),
(193, 'Phần 4. Ai cũng có giới hạn.', 'phan-4-ai-cung-co-gioi-han-1', NULL, NULL, NULL, 27, 'Comic_003a.jpg', 0, '<p>\r\n	<img alt=\"\" src=\"/public/upload/images/Comic_003a.jpg\" style=\"width: 921px; height: 1334px;\" /></p>\r\n', 'Miruku Chan', '2020-10-20 03:27:34', 1, 0, 22);
INSERT INTO `chapters` (`id`, `name`, `slug`, `site_title`, `meta_desc`, `meta_key`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(194, 'Chương 3: Chính thức bắt đầu công việc, kinh nghiệm đầu tiên và tín dụng kiếm được', 'chuong-3-chinh-thuc-bat-djau-cong-viec-kinh-nghiem-djau-tien-va-tin-dung-kiem-djuoc', NULL, NULL, NULL, 14, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Catherine mỉm cười đứng dậy, Luke cũng đứng dậy v&agrave; nhặt bộ đồ ăn anh đ&atilde; d&ugrave;ng; Catherine sẽ dọn dẹp ch&uacute;ng sau.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Robert cũng đứng l&ecirc;n v&agrave; n&oacute;i: &ldquo;Được rồi, nếu kh&ocirc;ng c&oacute; g&igrave; kh&aacute;c, t&ocirc;i sẽ quay lại ph&ograve;ng. Luke, h&atilde;y nghỉ ngơi ở nh&agrave; ngay b&acirc;y giờ. &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke gọi anh ta. &quot;Chờ đ&atilde;, t&ocirc;i sẽ đi với anh.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Robert rất t&ograve; m&ograve;. &quot;Chuyện g&igrave; đang xảy ra vậy?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke nh&uacute;n vai v&agrave; n&oacute;i, &ldquo;D&ugrave; sao th&igrave; t&ocirc;i cũng kh&ocirc;ng c&oacute; g&igrave; tốt hơn để l&agrave;m. T&ocirc;i cũng c&oacute; thể đi với anh v&agrave; ho&agrave;n th&agrave;nh qu&aacute; tr&igrave;nh tuyển dụng. Nhiều nhất, t&ocirc;i sẽ kh&ocirc;ng bắt đầu ngay lập tức, nhưng sau v&agrave;i ng&agrave;y. Điều đ&oacute; c&oacute; được chấp nhận kh&ocirc;ng? &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Robert gật đầu. &quot;Chắc chắn rồi. Trong mọi trường hợp, cậu sẽ chỉ được trả tiền kể từ ng&agrave;y cậu bắt đầu l&agrave;m việc. Nhưng cậu c&oacute; chắc l&agrave; cậu muốn đi ngay b&acirc;y giờ?Cậu kh&ocirc;ng muốn suy nghĩ th&ecirc;m về n&oacute;? &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke trả lời, &ldquo;T&ocirc;i đ&atilde; d&agrave;nh v&agrave;i ng&agrave;y để nghĩ về n&oacute;. T&ocirc;i đ&atilde; quyết định. Đi n&agrave;o.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&ecirc;n cạnh họ, Catherine kh&ocirc;ng thể kh&ocirc;ng xen v&agrave;o, &ldquo;Bắt đầu? Cậu đang bắt đầu những g&igrave;? &rdquo;Luke cười n&oacute;i: &ldquo;T&ocirc;i nhận ra rằng những g&igrave; Robert n&oacute;i với t&ocirc;i v&agrave;i ng&agrave;y trước l&agrave; đ&uacute;ng. Do đ&oacute;, t&ocirc;i đ&atilde; quyết định ở lại v&agrave; trở th&agrave;nh cảnh s&aacute;t ở đ&acirc;y. Bạn c&oacute; vui khi nghe điều đ&oacute; kh&ocirc;ng, Catherine? &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Catherine ho&agrave;n to&agrave;n chết lặng. C&ocirc; ng&acirc;y người ra trong v&agrave;i gi&acirc;y trước khi c&oacute; thể n&oacute;i. &ldquo;Ch- nhưng v&agrave;i ng&agrave;y trước, cậu vẫn c&ograve;n tranh c&atilde;i rất nhiều với Robert, thậm ch&iacute; c&ograve;n tuy&ecirc;n bố rằng c&ocirc;ng việc qu&aacute; k&eacute;m n&ecirc;n cậu sẽ kh&ocirc;ng l&agrave;m!&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke nh&uacute;n vai v&agrave; trả lời: &ldquo;Tất cả ch&uacute;ng t&ocirc;i đều l&agrave; người d&acirc;n qu&ecirc;. Ai trong ch&uacute;ng ta kh&ocirc;ng c&oacute; m&ugrave;i h&ocirc;i như bụi bẩn? V&agrave; ăn bẩn vẫn hơn kh&ocirc;ng c&oacute; g&igrave; để ăn &rdquo;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Catherine: &ldquo;&hellip;&rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Robert: &ldquo;&hellip;&rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Catherine kh&ocirc;ng thể cưỡng lại hỏi th&ecirc;m, &ldquo;Nhưng cậu vừa được xuất viện. Tại sao kh&ocirc;ng&hellip; tại sao cậu kh&ocirc;ng nghỉ ngơi một hoặc hai ng&agrave;y đầu ti&ecirc;n? &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke cười đ&aacute;p: &ldquo;Đừng lo, Catherine. H&ocirc;m qua, kh&ocirc;ng, n&oacute;i ch&iacute;nh x&aacute;c l&agrave; ng&agrave;y h&ocirc;m kia, t&ocirc;i qu&aacute; phấn kh&iacute;ch v&agrave; qu&ecirc;n mất bữa trưa của m&igrave;nh trong sự phấn kh&iacute;ch. T&ocirc;i chỉ ngất đi v&igrave; qu&aacute; đ&oacute;i. Cơ thể t&ocirc;i ho&agrave;n to&agrave;n ổn. Trong mọi trường hợp, t&ocirc;i vẫn sẽ nghỉ ngơi trong v&agrave;i ng&agrave;y sau khi đăng k&yacute; l&agrave;m việc. Bạn c&oacute; lo lắng rằng Robert sẽ &eacute;p t&ocirc;i bắt đầu sớm hơn kh&ocirc;ng? &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Catherine lườm anh ta v&agrave; n&oacute;i, &ldquo;Anh thậm ch&iacute; c&oacute; thể qu&ecirc;n bữa ăn của m&igrave;nh? L&agrave;m thế n&agrave;o t&ocirc;i c&oacute; thể kh&ocirc;ng lo lắng cho cậu ? Ừm, đợi một ch&uacute;t. T&ocirc;i sẽ chuẩn bị một hộp cơm trưa. H&atilde;y mang theo n&oacute; để cậu kh&ocirc;ng bị qu&ecirc;n. &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke mỉm cười gật đầu. &quot;Chắc chắn rồi. Với điều đ&oacute;, t&ocirc;i chắc chắn sẽ nhớ. &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Robert hỏi, &quot;C&ograve;n t&ocirc;i th&igrave; sao?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Catherine trả lời: &ldquo;H&ocirc;m nay cậu cũng mang theo bữa trưa phải kh&ocirc;ng? Chắc chắn rồi, cậu muốn g&igrave;? B&aacute;nh m&igrave; kẹp thịt? Hay một chiếc b&aacute;nh m&igrave;? &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Robert c&agrave;u nh&agrave;u, &quot;T&ocirc;i cũng định ăn bất cứ thứ g&igrave; m&agrave; đứa trẻ n&agrave;y đang ăn?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke cười m&agrave; kh&ocirc;ng n&oacute;i g&igrave;, v&agrave; phớt lờ một người đ&agrave;n &ocirc;ng trung ni&ecirc;n đang ghen tị.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Catherine nhanh ch&oacute;ng chuẩn bị ba chiếc b&aacute;nh m&igrave; nhưng lần n&agrave;y ch&uacute;ng kh&ocirc;ng phải l&agrave; b&aacute;nh m&igrave; thịt nguội. Đ&uacute;ng hơn, ch&uacute;ng l&agrave; b&aacute;nh m&igrave; thịt b&ograve;. Đ&acirc;y sẽ l&agrave; bữa trưa cho hai người.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ba mươi ph&uacute;t sau, cả hai đến thị trấn. Robert đ&atilde; đậu xe trước sở cảnh s&aacute;t trước khi xuống xe.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mọi thứ đều lớn hơn ở Texas, kể cả c&aacute;c b&atilde;i đậu xe.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hay n&oacute;i ch&iacute;nh x&aacute;c l&agrave; ở những thị trấn nhỏ như thế n&agrave;y, kh&ocirc;ng c&oacute; b&atilde;i đậu xe cụ thể. Sở cảnh s&aacute;t chẳng qua l&agrave; một t&ograve;a nh&agrave; nhỏ hai tầng với một khoảng đất trống ph&iacute;a sau đủ lớn để chứa khoảng 200 đến 300 xe.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng thị trấn n&agrave;y chỉ c&oacute; 2.000 d&acirc;n. V&agrave; kể cả Robert, c&oacute; &iacute;t hơn 12 người l&agrave;m việc tại sở cảnh s&aacute;t n&agrave;y.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave; mới đ&acirc;y, một trong số họ - một linh hồn đ&aacute;ng thương đ&atilde; sống độc th&acirc;n trong một thời gian d&agrave;i - thậm ch&iacute; đ&atilde; ph&aacute;t đi&ecirc;n v&agrave; cam chịu. Anh sẽ đến Dallas với hy vọng t&igrave;m được t&igrave;nh y&ecirc;u đ&iacute;ch thực của m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Điều duy nhất Luke phải n&oacute;i l&agrave;: &ldquo;Anh bạn, thức dậy. Đừng mơ nữa &rdquo;.Hầu hết thời gian, vẻ ngo&agrave;i của một ch&agrave;ng trai l&agrave; một yếu tố quan trọng trong việc anh ta c&oacute; bạn g&aacute;i hay kh&ocirc;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ở Hoa Kỳ, tiền bạc đ&ocirc;i khi cũng đ&oacute;ng một vai tr&ograve; quan trọng. Nhưng l&agrave; một người kh&ocirc;ng c&oacute; ngoại h&igrave;nh v&agrave; tiền bạc, ngay cả khi người đ&oacute; đến Dallas, anh ta vẫn sẽ kh&oacute; kiếm được bạn g&aacute;i.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&oacute; cũng l&agrave; l&yacute; do tại sao một vị tr&iacute; tuyển dụng đột nhi&ecirc;n được mở ra tại sở cảnh s&aacute;t.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke c&oacute; thể xem thường vị tr&iacute; trống n&agrave;y, nhưng một khi đ&atilde; được quảng c&aacute;o, chỉ mất chưa đầy một ng&agrave;y để lấp đầy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ở đ&acirc;y trong thị trấn rộng r&atilde;i nhưng d&acirc;n cư thưa thớt n&agrave;y, kh&ocirc;ng c&oacute; nhiều cảnh s&aacute;t l&agrave;m việc. Th&ecirc;m v&agrave;o đ&oacute;, sự nguy hiểm khi trở th&agrave;nh cảnh s&aacute;t ở đ&acirc;y thấp hơn nhiều so với việc trở th&agrave;nh cảnh s&aacute;t ở c&aacute;c thị trấn bi&ecirc;n giới ph&iacute;a T&acirc;y Nam.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&acirc;y l&agrave; một c&ocirc;ng việc ổn định v&agrave; thư gi&atilde;n, v&agrave; l&agrave; một c&ocirc;ng việc cực kỳ phổ biến đối với người d&acirc;n địa phương.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Robert tr&ecirc;n thực tế đ&atilde; sử dụng những g&igrave; được cho ph&eacute;p trong phạm vi quyền hạn của m&igrave;nh để mở ra một con đường cho Luke.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Miễn l&agrave; c&aacute;c hoạt động h&agrave;ng ng&agrave;y kh&ocirc;ng bị ảnh hưởng, sẽ kh&ocirc;ng c&oacute; bất kỳ vấn đề n&agrave;o khi anh ấy l&agrave;m điều đ&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hoa Kỳ cũng l&agrave; một quốc gia m&agrave; mạng lưới quan trọng, v&agrave; ở một số kh&iacute;a cạnh, n&oacute; c&ograve;n đ&oacute;ng một vai tr&ograve; lớn hơn cả ở Trung Quốc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&agrave; người đ&atilde; từng l&agrave; trưởng thị trấn n&agrave;y hơn 10 năm, Robert c&oacute; đủ tư c&aacute;ch để trao cho Luke đặc &acirc;n n&agrave;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&atilde; gần đến giờ ăn trưa, v&agrave; khi Robert đến, c&aacute;c sĩ quan trực ca đ&atilde; được thả. Sau khi ch&agrave;o nhau bằng những nụ cười ấm &aacute;p, c&aacute;c sĩ quan rời đi ăn trưa tại một nh&agrave; h&agrave;ng thức ăn nhanh gần đ&oacute;. Ở đ&oacute;, họ cũng sẽ tận hưởng hai giờ nghỉ ngơi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Về phần Robert, anh ấy đ&atilde; gọi cho một sĩ quan đ&atilde; ở lại v&agrave; n&oacute;i, &quot;Bob, h&atilde;y gi&uacute;p Luke trong qu&aacute; tr&igrave;nh đăng k&yacute;.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bob l&agrave; một người đ&agrave;n &ocirc;ng da trắng, khoảng 30 tuổi, v&agrave; giống như nhiều người d&acirc;n Texas kh&aacute;c, anh ta kh&aacute; r&aacute;m nắng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh ta kh&ocirc;ng phải l&agrave; một cao bồi l&agrave;m việc trong một trang trại, nhưng l&agrave; một cảnh s&aacute;t, anh ta vẫn cần phải đi tuần tra. V&agrave; tất nhi&ecirc;n, một người đ&agrave;n &ocirc;ng to cao, th&ocirc; kệch như anh ta sẽ chẳng buồn d&ugrave;ng kem chống nắng bảo vệ. V&igrave; vậy, cổ của &ocirc;ng đặc biệt r&aacute;m nắng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nghe những lời của Robert, Bob hu&yacute;t s&aacute;o v&agrave; n&oacute;i, &ldquo;T&ocirc;i hiểu rồi, &ocirc;ng Skywalker thực sự sẽ tham gia lực lượng? H&ocirc;m nay c&oacute; phải l&agrave; Ng&agrave;y C&aacute; th&aacute;ng Tư kh&ocirc;ng? &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Robert kh&ocirc;ng buồn trả lời v&agrave; chỉ vẫy tay trước khi đi v&agrave;o văn ph&ograve;ng.</span></span></p>\r\n<p>\r\n	<br />\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bob kh&ocirc;ng cảm thấy kh&oacute; chịu khi bị phớt lờ. Với một nụ cười, anh đưa Luke đi c&ugrave;ng để kết th&uacute;c qu&aacute; tr&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Với Robert ở đ&acirc;y, th&ocirc;ng tin c&aacute; nh&acirc;n của Luke đ&atilde; c&oacute; sẵn. R&otilde; r&agrave;ng Robert đ&atilde; chuẩn bị trước cho họ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Điều duy nhất Luke cần l&agrave;m l&agrave; k&yacute; khoảng 10 t&agrave;i liệu v&agrave; qu&aacute; tr&igrave;nh đăng k&yacute; đ&atilde; ho&agrave;n tất.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave;o đ&uacute;ng thời điểm anh k&yacute; v&agrave;o văn bản cuối c&ugrave;ng, hệ thống đ&atilde; phản ứng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ho&agrave;n th&agrave;nh nhiệm vụ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Điểm kinh nghiệm nhận được: 10</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">T&iacute;n dụng: 10</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke vui mừng kh&ocirc;n xiết. &quot;Như mong đợi.&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đương nhi&ecirc;n, h&ocirc;m nay anh ấy vội v&agrave;ng đăng k&yacute; v&igrave; nhiệm vụ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Với hệ thống chậm ph&aacute;t triển đ&oacute;, anh ta kh&ocirc;ng c&oacute; khả năng ngồi xung quanh v&agrave; chờ đợi điều g&igrave; đ&oacute; tốt đẹp rơi v&agrave;o l&ograve;ng m&igrave;nh. Anh phải chủ động v&agrave; kiểm tra hệ thống thường xuy&ecirc;n để t&igrave;m ra những lợi &iacute;ch m&agrave; anh c&oacute; thể thu được từ hệ thống c&agrave;ng sớm c&agrave;ng tốt.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&iacute; dụ, th&ocirc;ng qua nhiệm vụ n&agrave;y, anh ta chỉ muốn xem liệu anh ta c&oacute; cần ch&iacute;nh thức bắt đầu c&ocirc;ng việc hay chỉ cần một danh t&iacute;nh ch&iacute;nh thức l&agrave; cảnh s&aacute;t để ho&agrave;n th&agrave;nh nhiệm vụ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Rốt cuộc th&igrave; suy đo&aacute;n của anh ấy đ&atilde; đ&uacute;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thời điểm anh ta k&yacute; v&agrave;o văn bản cuối c&ugrave;ng, anh ta l&agrave; một cảnh s&aacute;t hợp ph&aacute;p của thị trấn n&agrave;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay cả khi anh ấy chưa bắt đầu c&ocirc;ng việc, b&acirc;y giờ anh ấy đ&atilde; ch&iacute;nh thức l&agrave; một cảnh s&aacute;t.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đối với ng&agrave;y bắt đầu thực sự của anh ấy, điều đ&oacute; sẽ chỉ ảnh hưởng đến tiền lương của anh ấy, nhưng sẽ kh&ocirc;ng ảnh hưởng đến việc ho&agrave;n th&agrave;nh nhiệm vụ của anh ấy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng&hellip; hệ thống lại biến mất sau đ&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một lần nữa, hệ thống chậm ph&aacute;t triển cho thấy n&oacute; v&ocirc; dụng như thế n&agrave;o. Sau khi gửi th&ocirc;ng b&aacute;o đ&oacute;, n&oacute; lại giả vờ như đ&atilde; chết. N&oacute; dường như kh&ocirc;ng c&oacute; &yacute; định thực hiện nhiệm vụ tiếp theo.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, Luke kh&ocirc;ng bận t&acirc;m. Đ&uacute;ng hơn, anh ta đ&atilde; rời khỏi Bob trước khi rời khỏi t&ograve;a nh&agrave;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh ấy hiện đang qu&aacute; phấn kh&iacute;ch v&agrave; cần b&igrave;nh tĩnh lại.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh cần một khoảng thời gian ở một m&igrave;nh để b&igrave;nh tĩnh lại.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&ecirc;n ngo&agrave;i t&ograve;a nh&agrave;, nhiệt độ buổi chiều n&oacute;ng như thi&ecirc;u như đốt, nhưng Luke dường như kh&ocirc;ng nhận ra. Anh ta li&ecirc;n tục đi đi lại lại trong khu vực c&oacute; b&oacute;ng r&acirc;m trước t&ograve;a nh&agrave;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi đi từng bước, anh ta đang bận rộn l&agrave;m phiền c&aacute;i hệ thống ngu ngốc đ&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh muốn xem liệu anh c&oacute; thể k&iacute;ch hoạt nhiệm vụ tiếp theo hay kh&ocirc;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&acirc;y giờ anh ấy đ&atilde; c&oacute; được điểm kinh nghiệm v&agrave; điểm t&iacute;n dụng, anh ấy cần phải t&igrave;m hiểu xem khi n&agrave;o m&igrave;nh c&oacute; thể l&ecirc;n cấp.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cấp độ của anh ấy vẫn l&agrave; 0, v&agrave; r&otilde; r&agrave;ng l&agrave; cấp độ c&oacute; thể tăng l&ecirc;n. Anh ta chỉ kh&ocirc;ng biết phải c&oacute; bao nhi&ecirc;u điểm kinh nghiệm để tăng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay l&uacute;c Luke đang bận rộn l&agrave;m phiền hệ thống, tiếng bước ch&acirc;n vang l&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-37094337-7fff-8ca0-b241-6f844c5cbda1\"><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Luke phớt lờ &acirc;m thanh đ&oacute; v&igrave; anh vẫn đang bận rộn l&agrave;m bất cứ điều g&igrave; c&oacute; thể để l&agrave;m phiền hệ thống. V&igrave; vậy, anh ho&agrave;n to&agrave;n kh&ocirc;ng để &yacute; đến bất cứ ai đang ở gần đ&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n', 'Cà Di', '2020-10-19 01:53:30', 1, 0, 5);
INSERT INTO `chapters` (`id`, `name`, `slug`, `site_title`, `meta_desc`, `meta_key`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(195, 'Chương 4.  Đùa chút thôi', 'chuong-4-djua-chut-thoi-1', NULL, NULL, NULL, 42, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hiện tại Diệp Hữu chẳng nhớ ai cả, tất nhi&ecirc;n sẽ kh&ocirc;ng c&oacute; ấn tượng về vị gi&aacute;o chủ Ma Gi&aacute;o kia, nhưng y cũng sẽ kh&ocirc;ng th&agrave;nh thật m&agrave; thừa nhận, chỉ n&oacute;i: &quot;C&oacute; hơi quen tai, ta cần nghĩ một ch&uacute;t.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Văn Nh&acirc;n Hằng cũng kh&ocirc;ng &eacute;p y, ngồi tr&ecirc;n ghế đ&aacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người trong đ&igrave;nh đ&atilde; sớm t&ograve; m&ograve; về Diệp Hữu, t&aacute;n gẫu mấy c&acirc;u kia xong liền dời lực ch&uacute; &yacute; l&ecirc;n người y, người vừa mở miệng l&uacute;c n&atilde;y hỏi: &quot;Vị n&agrave;y l&agrave;?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Văn Nh&acirc;n Hằng t&igrave;m được sư đệ về tất nhi&ecirc;n rất vui sướng, nghe bọn họ hỏi, giọng n&oacute;i đầy vẻ sung sướng n&oacute;i: &quot;Sư đệ ta, A Hiểu.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;...&quot; Những người kia bất ngờ kh&ocirc;ng kịp trở tay, &quot;G&igrave;?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Phản ứng của bọn họ cũng như Tần Nguyệt Mi&ecirc;n vậy, suy nghĩ đầu ti&ecirc;n trong đầu đều l&agrave; Văn Nh&acirc;n Hằng c&oacute; sư đệ cơ đấy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bọn họ hỏi: &quot;V&igrave; sao chưa từng nghe ngươi nhắc đến?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Văn Nh&acirc;n Hằng n&oacute;i: &quot;Thất lạc nhiều năm, vừa mới gặp lại.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;&Agrave;, ra l&agrave; thế...&quot; Mấy người n&oacute;i xong mới nhớ đến vẻ chờ xem kịch vui của Tần Nguyệt Mi&ecirc;n l&uacute;c sắp rời đi, cứ thấy kh&ocirc;ng th&iacute;ch hợp lắm, xoay tr&ograve;ng mắt cười n&oacute;i, &quot;Chuyện tốt như vậy m&agrave; A Mi&ecirc;n cứ thần thần b&iacute; b&iacute;, ch&uacute;ng ta c&ograve;n tưởng c&aacute;c ngươi muốn đi hố người.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Văn Nh&acirc;n Hằng biết r&otilde; đ&aacute;m người kia rất nhạy b&eacute;n, giải th&iacute;ch đơn giản trước sau, n&oacute;i: &quot;Ngọc bội kia l&agrave; ta v&agrave; sư đệ mỗi người một khối, A Mi&ecirc;n thấy ngọc bội tr&ecirc;n người sư đệ ta c&ograve;n tưởng l&agrave; ta đưa.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hai người biết r&otilde; chuyện im lặng nh&igrave;n hắn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nguyệt Mi&ecirc;n thầm nghĩ sư đệ ngươi khủng bố như vậy, ngươi lại n&oacute;i dối lần n&agrave;y đến lần kh&aacute;c trước mặt y, kh&ocirc;ng sợ sư đệ ngươi nghi ngờ những lời ngươi n&oacute;i l&uacute;c trước đều l&agrave; giả sao?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Diệp Hữu th&igrave; nghĩ s&acirc;u hơn, biết r&otilde; Văn Nh&acirc;n Hằng kh&ocirc;ng muốn những người n&agrave;y nghi ngờ m&igrave;nh giống như Tần Nguyệt Mi&ecirc;n, cũng c&oacute; thể l&agrave; sợ xảy ra chuyện, vậy n&ecirc;n mới c&oacute; thể bỏ qua chuyện mất ngọc bội.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Quả nhi&ecirc;n, những người kia được thỏa m&atilde;n l&ograve;ng hiếu kỳ, thấy Văn Nh&acirc;n Hằng c&oacute; &yacute; chuyển đề t&agrave;i liền thức thời theo hắn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Diệp Hữu im lặng ngồi, ph&aacute;t hiện địa vị của Văn Nh&acirc;n Hằng trong đ&oacute; h&igrave;nh như rất cao, c&oacute; tư th&aacute;i của &quot;đầu lĩnh&quot;, kh&ocirc;ng khỏi th&ecirc;m phần t&ograve; m&ograve; về th&acirc;n phận hắn, l&uacute;c n&agrave;y liền thấy một người đối diện xoay người x&aacute;ch một b&igrave;nh rượu đặt l&ecirc;n b&agrave;n đ&aacute;, &quot;cạch&quot; một tiếng nhỏ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&igrave;nh rượu m&agrave;u trắng sữa, tr&ecirc;n th&agrave;nh b&igrave;nh c&oacute; một chữ &quot;Phong&quot; m&agrave;u v&agrave;ng rồng bay phượng m&uacute;a, chữ kia ti&ecirc;u s&aacute;i t&ugrave;y &yacute;, như c&oacute; thể bay ra được vậy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong đầu Diệp Hữu lập tức hiện l&ecirc;n một c&aacute;i t&ecirc;n, n&oacute;i: &quot;Phong T&uacute;y.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Gần như c&ugrave;ng l&uacute;c, Tần Nguyệt Mi&ecirc;n cũng k&ecirc;u l&ecirc;n: &quot;Phong T&uacute;y!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người mang b&igrave;nh rượu đến cười nhướng m&agrave;y: &quot;Đặc biệt mang đến uống, được rồi chứ.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nguyệt Mi&ecirc;n cười mắng trong l&ograve;ng, hiểu rất r&otilde; t&iacute;nh to&aacute;n của đ&aacute;m bạn hồ bằng cẩu hữu của hắn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tr&ograve; hay của Văn Nh&acirc;n Hằng t&aacute;m trăm năm kh&oacute; gặp một lần, giống như hắn muốn thấy vậy, những người n&agrave;y tất nhi&ecirc;n cũng kh&ocirc;ng muốn bỏ qua, m&agrave; hắn xưa nay kh&ocirc;ng c&oacute; sức chống cự với rượu ngon, v&igrave; thế bọn họ v&igrave; ph&ograve;ng ngừa việc bị đuổi đi, liền mang theo &quot;Phong T&uacute;y&quot; c&oacute; thể s&aacute;nh với ho&agrave;ng kim kia để hối lộ hắn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Những người n&agrave;y cũng l&agrave; người c&oacute; tiền, bọn họ th&iacute;ch bại gia, vậy th&igrave; hắn sẽ kh&ocirc;ng kh&aacute;ch kh&iacute; nữa, sai gia đinh mang ch&eacute;n rượu đến, kh&ocirc;ng đợi được nữa m&agrave; r&oacute;t xuống.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">M&ugrave;i rượu thuần hậu nhanh ch&oacute;ng lan tỏa, như l&agrave; tr&ecirc;u chọc thần kinh người ta vậy. Diệp Hữu h&iacute;t h&iacute;t, bưng ch&eacute;n của m&igrave;nh l&ecirc;n nhấp một ngụm, th&iacute;ch th&uacute; h&iacute;p mắt lại: &quot;L&agrave; &quot;Thập tam giai&quot; a.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Những người kia kinh ngạc một l&uacute;c, người mang rượu đến lập tức n&oacute;i: &quot;Kh&ocirc;ng tồi, đ&uacute;ng l&agrave; &quot;Thập tam giai&quot; trong &quot;Phong T&uacute;y&quot;, A Hiểu sư đệ cũng th&iacute;ch uống rượu?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Diệp Hữu kh&ocirc;ng nhớ r&otilde; c&oacute; th&iacute;ch hay kh&ocirc;ng, chỉ n&oacute;i: &quot;Thỉnh thoảng, hương vị của &quot;Thập tam giai&quot; rất độc đ&aacute;o.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Tất nhi&ecirc;n rồi, &quot;Thập tam giai&quot; ch&iacute;nh l&agrave; một ngọn cờ ri&ecirc;ng trong &quot;Phong T&uacute;y&quot; đấy,&quot; Người mang rượu đến cười n&oacute;i, &quot;Rất nhiều người n&oacute;i &quot;Thập tam giai&quot; giống với &quot;Trần duy&ecirc;n&quot;, nhưng ta thấy hai b&ecirc;n c&aacute;ch xa nhau, theo Diệp gi&aacute;o chủ n&oacute;i th&igrave;, nhiều t&ecirc;n ngu kh&ocirc;ng ph&acirc;n biệt r&otilde; hai loại rượu n&agrave;y.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Diệp Hữu l&uacute;c n&atilde;y nghe hắn lu&ocirc;n nhắc đến Diệp gi&aacute;o chủ đ&atilde; thấy kỳ qu&aacute;i rồi, l&uacute;c n&agrave;y kh&ocirc;ng khỏi nh&igrave;n lại hắn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&aacute;m người kia thấy thế n&oacute;i: &quot;Để A Hiểu sư đệ ch&ecirc; cười rồi, L&yacute; thiếu một ng&agrave;y kh&ocirc;ng n&oacute;i về Diệp gi&aacute;o chủ mấy lần liền kh&oacute; chịu trong người, ph&agrave;m l&agrave; thứ g&igrave; c&oacute; thể n&oacute;i đến Diệp gi&aacute;o chủ, hắn đều nhắc đến, kh&ocirc;ng nghe l&agrave; được.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&yacute; thiếu hừ một tiếng: &quot;Ta th&iacute;ch y đấy, th&igrave; sao?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Diệp Hữu rất ngạc nhi&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mấy ng&agrave;y trước y đ&atilde; nghe từ miệng gia đ&igrave;nh biết được Tầm Liễu sơn trang l&agrave; m&ocirc;n ph&aacute;i bạch dạo, những người n&agrave;y l&agrave; bạn của trang chủ, nghĩ đến th&igrave; cũng hẳn l&agrave; bạch đạo, theo l&yacute; m&agrave; n&oacute;i bạch đạo v&agrave; Ma Gi&aacute;o kh&ocirc;ng phải như nước với lửa sao? Thẳng thắn như vậy kh&ocirc;ng sợ bị người ta xem l&agrave; t&agrave; ph&aacute;i sao?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Y n&eacute;n nghi ngờ nhấp một ngụm rượu, kh&ocirc;ng tiếp lời.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&yacute; thiếu như mở hộp n&oacute;i, lẩm bẩm n&oacute;i muốn gặp Diệp gi&aacute;o chủ một mặt rất kh&oacute;, trừ lần tr&ecirc;n Ngọc Sơn đ&agrave;i kia, v&agrave;i năm qua hắn cũng chỉ thấy b&oacute;ng d&aacute;ng người ta hai lần, nếu kh&ocirc;ng được nữa hắn liền định gia nhập Ma Gi&aacute;o. Đ&aacute;m hồ bằng cẩu hữu kia thấy nhưng kh&ocirc;ng thể tr&aacute;ch, theo th&oacute;i quen chen v&ocirc; v&agrave;i c&acirc;u, một đ&aacute;m người đ&atilde; tụ lại một đống rất nhanh, cười n&oacute;i vang một v&ugrave;ng, nhưng c&oacute; thể thấy t&igrave;nh cảm rất tốt.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nguyệt Mi&ecirc;n kh&ocirc;ng giống ng&agrave;y thường xen v&agrave;o c&ugrave;ng, m&agrave; l&agrave; im lặng cầm ch&eacute;n rượu, kh&ocirc;ng c&oacute; t&acirc;m trạng phẩm rượu.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&aacute;ch thảo lộ của Kỷ thần y rất nổi tiếng, A Hiểu nhớ r&otilde; th&igrave; kh&ocirc;ng hề g&igrave;, nhưng &quot;Phong T&uacute;y&quot; kh&ocirc;ng phải l&agrave; một người b&igrave;nh thường c&oacute; thể uống được, c&oacute; thể thấy cuộc sống trước đ&acirc;y của A Hiểu rất tốt.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nghi ngờ trong l&ograve;ng hắn d&acirc;ng l&ecirc;n tận đỉnh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người n&agrave;y vừa th&ocirc;ng minh lại c&oacute; tiền, c&ograve;n c&oacute; khu&ocirc;n mặt h&uacute;t người, kh&ocirc;ng thể n&agrave;o y&ecirc;n lặng v&ocirc; danh được đ&uacute;ng kh&ocirc;ng? Sao lại chưa từng nghe qua?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn kh&ocirc;ng nhịn được nh&igrave;n Văn Nh&acirc;n Hằng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&uacute;c n&agrave;y Văn Nh&acirc;n Hằng đang nh&igrave;n sư đệ, hơi nhăn m&agrave;y lại. Tần Nguyệt Mi&ecirc;n cảm thấy vui mừng, đang nghĩ vị m&ocirc;n chủ đại nh&acirc;n n&agrave;y cuối c&ugrave;ng cũng xem trọng việc n&agrave;y, liền thấy hắn vươn tay ngăn sư đệ nh&agrave; hắn lại, cũng đổi một ly tr&agrave; qua cho người ta.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Văn Nh&acirc;n Hằng khuy&ecirc;n nhủ: &quot;Tr&ecirc;n người ngươi c&oacute; vết thương, tạm thời đừng uống rượu, chờ khỏi hẳn hẵng t&iacute;nh.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;...&quot; Tần Nguyệt Mi&ecirc;n đờ đẫn quay mặt đi, thầm nghĩ Văn Nh&acirc;n Hằng đ&uacute;ng l&agrave; c&oacute; d&aacute;ng vẻ sư huynh... Mẹ n&oacute;, ngươi kh&ocirc;ng sợ bị người ta hại sao?!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Diệp Hữu biết nghe lời đ&uacute;ng bưng tr&agrave; l&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&acirc;u &quot;Thập tam giai&quot; l&uacute;c n&atilde;y y n&oacute;i cũng kh&ocirc;ng phải l&agrave; t&ugrave;y tiện n&oacute;i, b&acirc;y giờ đ&atilde; được chứng thật rằng m&igrave;nh kh&ocirc;ng n&oacute;i sai, kh&ocirc;ng khỏi nhớ lại chuyện b&aacute;ch thảo lộ, lại t&igrave;m t&igrave;m trong đầu k&yacute; ức về &quot;Phong T&uacute;y&quot;, nghĩ ngợi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Y nhớ r&otilde; b&aacute;ch thảo lộ rất nổi tiếng, cũng nhớ r&otilde; n&oacute; rất qu&yacute; gi&aacute;, nhưng lại kh&ocirc;ng biết g&igrave; về người chế ra n&oacute;, nếu danh tiếng người nọ kh&ocirc;ng lớn hơn b&aacute;ch thảo lộ th&igrave; c&ograve;n dễ hiểu, nhưng lại kh&ocirc;ng phải vậy. Cũng c&ugrave;ng một t&igrave;nh huống, y nhớ r&otilde; &quot;Phong T&uacute;y&quot;, thậm ch&iacute; c&oacute; thể ch&iacute;nh x&aacute;c ph&acirc;n biệt ra &quot;Thập tam giai&quot;, nhưng lại kh&ocirc;ng c&oacute; ấn tượng về người nhưỡng rượu, n&oacute;i c&aacute;ch kh&aacute;c, y nhớ r&otilde; những g&igrave; đ&atilde; nghe qua, xem qua hoặc d&ugrave;ng qua, chỉ kh&ocirc;ng nhớ được người.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tại sao lại như vậy?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Y chậm r&atilde;i uống tr&agrave;, bắt đầu tự hỏi khả năng m&igrave;nh bị hạ dược mới c&oacute; thể mất tr&iacute; nhớ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mưa nhỏ t&iacute; t&aacute;ch từ từ dừng lại, đ&igrave;nh viện được nước rửa qua ti&ecirc;n diễm s&aacute;ng ngời, thoang thoảng m&ugrave;i thơm ng&aacute;t sau cơn mưa. Một đ&aacute;m người uống ba tuần rượu mới th&ocirc;i, Tần Nguyệt Mi&ecirc;n vốn định bảo bọn họ ở lại d&ugrave;ng bữa tối, nhưng nh&oacute;m người n&agrave;y thấy kh&ocirc;ng xem được việc vui của Văn Nh&acirc;n Hằng liền vội v&atilde; muốn đi xem cảnh xấu hổ khi bị từ chối của Thiệu Nguy&ecirc;n ở chỗ Đ&agrave;o c&ocirc; nương, s&ocirc;i nổi chạy đi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nguyệt Mi&ecirc;n kh&ocirc;ng c&ograve;n g&igrave; để n&oacute;i, nh&igrave;n hai người b&ecirc;n cạnh, x&aacute;ch b&igrave;nh rượu thức thời chạy đi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&uacute;c n&agrave;y Văn Nh&acirc;n Hằng mới c&oacute; cơ hội n&oacute;i cho sư đệ m&ocirc;n ph&aacute;i của hắn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn l&agrave; m&ocirc;n chủ Song Cực m&ocirc;n, đ&oacute; l&agrave; m&ocirc;n ph&aacute;i m&agrave; một tay hắn th&agrave;nh lập, đến nay đ&atilde; được bảy t&aacute;m năm.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Diệp Hữu hỏi: &quot;Lợi hại kh&ocirc;ng?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Văn Nh&acirc;n Hằng cười cười: &quot;Sau n&agrave;y từ từ ngươi sẽ biết, ta n&oacute;i lợi hại, sợ l&agrave; ngươi cũng kh&ocirc;ng tin.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Diệp Hữu định phản b&aacute;c theo bản năng, thấy &yacute; cười dịu d&agrave;ng trong mắt sư huynh, cũng kh&ocirc;ng nghĩ nữa, chấp nhận lời hắn n&oacute;i, lại hỏi: &quot;Từ &quot;Song Cực&quot; n&agrave;y c&oacute; h&agrave;m nghĩa g&igrave; sao?&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ừ, sư phụ v&agrave; sư b&aacute; trước đ&acirc;y l&uacute;c c&ograve;n ở giang hồ rất nổi tiếng, được người gọi l&agrave; &quot;Song cực&quot;, ta được bọn họ nu&ocirc;i lớn, n&ecirc;n cũng lấy t&ecirc;n n&agrave;y,&quot; Văn Nh&acirc;n Hằng nh&igrave;n y, &quot;Sư b&aacute; vẫn c&ograve;n tại thế, sau n&agrave;y c&oacute; rảnh đi kinh th&agrave;nh, ta sẽ mang ngươi đến b&aacute;i phỏng &ocirc;ng.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Diệp Hữu hỏi: &quot;&Ocirc;ng ấy ở kinh th&agrave;nh?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;&Ocirc;ng ấy ở phủ tướng qu&acirc;n,&quot; Văn Nh&acirc;n Hằng thấy vẻ kinh ngạc l&oacute;e qua trong mắt y, n&oacute;i tiếp, &quot;Sư b&aacute; ch&iacute; tại sa trường, từ rất l&acirc;u trước đ&acirc;y đ&atilde; nhập ngũ, mười năm trước sư phụ qua đời, ngươi mất t&iacute;ch, sư b&aacute; nhận được tin sư phụ qua đời liền đến đ&oacute;n ta về. Kh&ocirc;ng phải l&uacute;c n&atilde;y ngươi rất ngạc nhi&ecirc;n khi L&yacute; thiếu n&oacute;i thẳng l&agrave; th&iacute;ch gi&aacute;o chủ Ma Gi&aacute;o sao? Thật ra hắn l&agrave; thế tử vương phủ, v&agrave;o giang hồ chỉ để vui chơi, ta quen hắn l&uacute;c ở kinh th&agrave;nh.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Những nghi ngờ của Diệp Hữu đ&atilde; được giải đ&aacute;p, ngo&agrave;i miệng lại n&oacute;i: &quot;Sư huynh đo&aacute;n sai rồi, ta kh&ocirc;ng t&ograve; m&ograve;.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Văn Nh&acirc;n Hằng bị y phản b&aacute;c lại như vậy, t&acirc;m t&igrave;nh v&ocirc; c&ugrave;ng tốt: &quot;Ừ, l&agrave; ta đ&atilde; đo&aacute;n sai.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Diệp Hữu kh&ocirc;ng r&otilde; hắn vui sướng v&igrave; c&aacute;i g&igrave;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Văn Nh&acirc;n Hằng liếc thấy gia đ&igrave;nh đến gọi bọn họ ăn cơm, liền mang sư đệ rời khỏi tiểu đ&igrave;nh, kh&ocirc;ng biết l&agrave; n&oacute;i cho y nghe hay l&agrave; lẩm bẩm: &quot;Trước đ&acirc;y l&uacute;c ngươi ngu dại lu&ocirc;n th&iacute;ch n&oacute;i ta đo&aacute;n sai, sư huynh rất ho&agrave;i niệm.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Diệp Hữu: &quot;...&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lại n&oacute;i vấn đề m&agrave; y kh&ocirc;ng muốn nghĩ đến.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tr&ecirc;n người Diệp Hữu c&oacute; vết thương, cơm chiều xong liền đi nghỉ ngơi. Cả buổi chiều Tần Nguyệt Mi&ecirc;n su&yacute;t nữa n&ocirc;n n&oacute;ng đến nội thương, thấy hai người n&agrave;y rốt cuộc cũng t&aacute;ch ra liền lập tức gọi Văn Nh&acirc;n Hằng đến thư ph&ograve;ng, nghi&ecirc;m t&uacute;c hỏi: &quot;N&oacute;i lời thật cho ta, ngọc bội mất thật sao?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Văn Nh&acirc;n Hằng n&oacute;i: &quot;Đ&uacute;ng vậy, đ&atilde; được v&agrave;i ng&agrave;y rồi.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nguyệt Mi&ecirc;n &acirc;m thầm h&uacute;t kh&iacute;, phỏng đo&aacute;n của hắn đều dựa tr&ecirc;n việc ngọc bội bị mất đi, nếu việc n&agrave;y l&agrave; đ&uacute;ng th&igrave; chứng minh lo lắng của hắn kh&ocirc;ng sai ch&uacute;t n&agrave;o. V&igrave; thế hắn &quot;b&ugrave;m b&ugrave;m&quot; n&oacute;i ra những nghi ngờ của m&igrave;nh như đảo đậu, hối hận n&oacute;i: &quot;Ta chỉ sợ chui v&agrave;o bẫy của người ta.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn vốn định thương lượng v&agrave;i đối s&aacute;ch, lại thấy t&ecirc;n ngụy qu&acirc;n tử kia cười nh&atilde; nhặn vạn phần, trấn an n&oacute;i: &quot;Kh&ocirc;ng sao, c&oacute; thể chỉ l&agrave; tr&ugrave;ng hợp.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một hơi của Tần Nguyệt Mi&ecirc;n su&yacute;t nữa nghẹn lại, &yacute; đ&aacute;nh người đều c&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Văn Nh&acirc;n Hằng kh&ocirc;i phục vẻ đứng đắn: &quot;Ngươi n&oacute;i kỹ chuyện cảy ra h&ocirc;m đ&oacute; cho ta.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nguyệt Mi&ecirc;n miễn cưỡng bu&ocirc;ng tha hắn, tự thuật từ đầu đến đu&ocirc;i một lần, nhưng chi tiết hắn c&oacute; thể nhớ đến rất &iacute;t, l&uacute;c hắn đi v&agrave;o liền thấy mặt vị c&ocirc;ng tử kia, ng&acirc;y người một l&uacute;c, sau thấy ống trụ hoa nện xuống liền vội đến cứu người, mang người ta x&ocirc;ng ra ngo&agrave;i, kh&ocirc;ng c&ograve;n t&acirc;m trạng ch&uacute; &yacute; chuyện kh&aacute;c.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn nh&iacute;u m&agrave;y: &quot;Ngươi c&oacute; t&iacute;nh to&aacute;n g&igrave; kh&ocirc;ng?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Văn Nh&acirc;n Hằng n&oacute;i: &quot;Trước mang sư đệ ta t&igrave;m Kỷ thần y trị thương, ng&agrave;y mai sẽ l&ecirc;n đường.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;...&quot; Tần Nguyệt Mi&ecirc;n nghi ngờ hỏi, &quot;L&agrave; sư đệ ngươi thật sao?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Văn Nh&acirc;n Hằng cười n&oacute;i: &quot;Tất nhi&ecirc;n.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nguyệt Mi&ecirc;n vẫn giữ th&aacute;i độ nghi ngờ, thấy hắn muốn đứng dậy về ph&ograve;ng, kh&ocirc;ng nhịn được gọi hắn lại: &quot;Ngươi c&oacute; thể cho ta một lời n&oacute;i thật nữa được kh&ocirc;ng? Về chuyện n&agrave;y, c&aacute;i g&igrave; cũng được.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Văn Nh&acirc;n Hằng n&oacute;i: &quot;Được.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đầu Tần Nguyệt Mi&ecirc;n rung rung. Thứ m&agrave; Văn Nh&acirc;n Hằng kh&ocirc;ng muốn n&oacute;i, d&ugrave; l&agrave;m thế n&agrave;o cũng kh&ocirc;ng hỏi ra được, kh&ocirc;ng bằng hắn lui m&agrave; cầu tiếp theo, d&ugrave; sao cũng chỉ nhằm v&agrave;o chuyện n&agrave;y, từ miệng t&ecirc;n n&agrave;y moi được một c&acirc;u, c&ograve;n lại hắn c&oacute; thể tự m&igrave;nh ph&acirc;n t&iacute;ch.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng m&agrave; chung quy hắn đ&atilde; đ&aacute;nh gi&aacute; thấp t&ecirc;n n&agrave;y, Văn Nh&acirc;n Hằng n&oacute;i xong, hắn lại chẳng đo&aacute;n ra được g&igrave; cả, bởi v&igrave; Văn Nh&acirc;n Hằng n&oacute;i cho hắn: &quot;Ta c&ograve;n rất th&iacute;ch nghe y n&oacute;i sư huynh ta, đ&acirc;y l&agrave; lời thật.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nguyệt Mi&ecirc;n: &quot;...&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sư huynh đệ ch&oacute; m&aacute; g&igrave; chứ, đ&uacute;ng l&agrave; ngươi coi trọng y chứ g&igrave;!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Văn Nh&acirc;n Hằng biết bạn tốt lo lắng cho m&igrave;nh, trước khi ra cửa thực l&ograve;ng an ủi một c&acirc;u, &quot;Y&ecirc;n t&acirc;m đi, ngươi thấy ta chịu thiệt khi n&agrave;o chưa.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">S&aacute;ng sớm h&ocirc;m sau, Diệp Hữu nghe sư huynh n&oacute;i muốn đi t&igrave;m thần y th&igrave; kh&ocirc;ng phản đối g&igrave; cả, y đ&atilde; ở đ&acirc;y hơn nửa th&aacute;ng, đ&atilde; sớm muốn ra ngo&agrave;i đi dạo rồi, huống hồ g&igrave; nơi c&oacute; nhiều người th&igrave; tin tức cũng nhiều hơn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Y mang theo t&acirc;m trạng sung sướng l&ecirc;n xe ngựa, đi một l&uacute;c lại dừng, th&iacute;ch th&uacute; v&ocirc; c&ugrave;ng, kết quả buổi tối lại bị dội một chậu nước lạnh, bởi vi y nghe được người n&agrave;o đ&oacute; n&oacute;i chỉ cần một gian ph&ograve;ng trọ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Người l&agrave;m ngươi bị thương c&ograve;n chưa t&igrave;m ra được, b&ecirc;n ngo&agrave;i kh&ocirc;ng thể so s&aacute;nh với an to&agrave;n trong sơn trang, để ngươi ngủ một gian sư huynh rất lo,&quot; Văn Nh&acirc;n Hằng nghi&ecirc;m trang chững chạc giải th&iacute;ch với y, &quot;Cho n&ecirc;n ch&uacute;ng ta ngủ một gian đi.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c029633c-7fff-52e1-1b2e-fb97f4cecbac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;...&quot; Diệp Hữu nhếch m&ocirc;i, cười tủm tỉm trả lời, &quot;Cũng được th&ocirc;i, nhưng sư huynh, ta mơ hồ nhớ l&agrave; ta th&iacute;ch nam nh&acirc;n, sư huynh lại c&oacute; vẻ ngo&agrave;i tốt như vậy, nếu đ&ecirc;m h&ocirc;m khuya khoắt ta kh&ocirc;ng nhịn được l&agrave;m g&igrave; đ&oacute; với ngươi, sư huynh h&atilde;y tha thứ.&quot;</span></span></p>\r\n<div>\r\n	&nbsp;</div>\r\n', 'Cà Di', '2020-10-19 01:56:45', 1, 0, 9);
INSERT INTO `chapters` (`id`, `name`, `slug`, `site_title`, `meta_desc`, `meta_key`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(196, 'Chapter 0.4 : Mùa thu và thăng thiên', 'chapter-04-mua-thu-va-thang-thien-1', NULL, NULL, NULL, 35, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&oacute; l&agrave; một đ&ecirc;m giống như những đ&ecirc;m kh&aacute;c kh&aacute;c kể từ khi anh ấy nghỉ việc. Derek sẽ mặc một trong những bộ đồ mới của m&igrave;nh v&agrave; đi lang thang khỏi th&agrave;nh phố, để xem thứ g&igrave; sẽ giết chết anh ta đầu ti&ecirc;n, căn bệnh ung thư hay một t&ecirc;n đi&ecirc;n ngẫu nhi&ecirc;n n&agrave;o đ&oacute;. Sau khi kiệt sức hoặc chỉ đơn giản l&agrave; buồn ch&aacute;n, anh ta sẽ bắt taxi v&agrave; trở về nh&agrave;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi nh&igrave;n thấy hắn, Derek đang bước đi với những sải ch&acirc;n nhanh ch&oacute;ng, với liều lượng cao. Chris Wainright. Hắn ta đang cầm một chai rượu, được giấu k&iacute;n b&ecirc;n trong một chiếc t&uacute;i giấy, từ đ&oacute; hắn ta sẽ uống từng ngụm lớn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chris đang n&oacute;i chuyện v&agrave; cười lớn với một c&ocirc; g&aacute;i tuổi teen đang để lộ da thịt. C&ocirc; ấy đ&atilde; nắm giữ một doanh nghiệp, nhận những c&uacute; h&iacute;ch lớn, cho đến khi họ giao dịch v&agrave; l&ecirc;n xe hơi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&oacute; l&agrave; một chiếc xe thể thao được sơn thủ c&ocirc;ng. Kh&ocirc;ng phải l&agrave; chiếc Camaro m&agrave; Chris đ&atilde; d&ugrave;ng để giết Carl, n&oacute; thậm ch&iacute; c&ograve;n lớn hơn v&agrave; đắt hơn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong khoảnh khắc đ&oacute;, Derek muốn hộc m&aacute;u. L&agrave;m thế n&agrave;o m&agrave; anh ta c&oacute; thể qu&ecirc;n được t&ecirc;n nh&oacute;c khốn kiếp đ&oacute;? Liệu căn bệnh ung thư của anh ấy c&oacute; thực sự khiến n&atilde;o anh ấy trở n&ecirc;n tồi tệ đến mức sẵn s&agrave;ng để cho một kết th&uacute;c lỏng lẻo như vậy trượt đi?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tiếng lốp xe vang l&ecirc;n v&agrave; chiếc xe nổ m&aacute;y đột ngột, gần như lao qua một phụ nữ đang băng qua đường. C&ocirc; g&aacute;i ăn mặc thiếu vải nằm lăn xuống cửa sổ, h&eacute;t l&ecirc;n những lời lăng mạ v&agrave;o người phụ nữ vẫn đang chết lặng v&igrave; sợ h&atilde;i.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek gần như c&oacute; thể nghe thấy tiếng cười của cặp đ&ocirc;i đ&oacute;. Nghiến răng, anh gọi một chiếc taxi v&agrave; bắt đầu l&ecirc;n kế hoạch cho h&agrave;nh động cuối c&ugrave;ng của m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đầu ti&ecirc;n, anh ta bắt đầu theo d&otilde;i Chris tr&ecirc;n tất cả c&aacute;c mạng x&atilde; hội, t&igrave;m hiểu mọi th&oacute;i quen v&agrave; sinh hoạt của hắn. Sau đ&oacute;, Derek bắt đầu theo d&otilde;i anh ta, v&agrave; đặt một thiết bị theo d&otilde;i GPS b&ecirc;n dưới Camaro để lu&ocirc;n biết vị tr&iacute; ch&iacute;nh x&aacute;c của hắn ta.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chỉ cần lướt qua Chris &#39;Chirper, anh ta đ&atilde; t&igrave;m thấy &iacute;t nhất năm mươi vi phạm thỏa thuận tạm tha. Trong khi theo d&otilde;i hắn ta, Derek đ&atilde; chụp nhiều bức ảnh Chris lạm dụng rượu v&agrave; ma t&uacute;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng Derek kh&ocirc;ng c&oacute; &yacute; định nộp bằng chứng cho cảnh s&aacute;t. Anh ta c&oacute; thể thu được g&igrave; từ điều đ&oacute;? Chris sẽ chỉ nhận được một c&aacute;i t&aacute;t nữa v&agrave;o cổ tay v&agrave; sau đ&oacute; bắt đầu cẩn thận hơn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek kh&ocirc;ng c&ograve;n nhiều thời gian, cũng như kh&ocirc;ng sẵn s&agrave;ng l&agrave;m những g&igrave; được gọi l&agrave; hệ thống c&ocirc;ng l&yacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chưa đầy một tuần sau, bằng c&aacute;ch xem trang c&aacute; nh&acirc;n của Chris, Derek biết được một lời t&aacute;n dương m&agrave; hắn ta sẽ tham gia. Derek kiểm tra lại thiết bị của m&igrave;nh v&agrave; nhảy v&agrave;o chiếc xe mới của m&igrave;nh, một chiếc Chevrolet Impala 1967 m&agrave;u đen.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chiếc xe tốt nhất để săn qu&aacute;i vật. Chuyến đi săn sẽ l&agrave; &ldquo;b&iacute; mật&rdquo;, do đ&oacute; n&oacute; sẽ diễn ra ở một số địa điểm bị bỏ hoang.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek theo s&aacute;t Chris, v&agrave; ngay khi họ rời khỏi c&aacute;c camera giao th&ocirc;ng, anh ta lao v&agrave;o chiếc Camaro từ b&ecirc;n cạnh, buộc Chris phải dừng lại.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay khi Chris bước xuống xe, Derek đ&atilde; hạ gục hắn bằng một khẩu s&uacute;ng điện, sau đ&oacute; nhanh ch&oacute;ng tiến h&agrave;nh kiểm tra xem chiếc Camaro c&oacute; th&ecirc;m h&agrave;nh kh&aacute;ch hay kh&ocirc;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&oacute; dường như l&agrave; một đ&ecirc;m may mắn của anh, Chris đ&atilde; l&aacute;i xe một m&igrave;nh. Derek lục so&aacute;t người v&agrave; nghiền n&aacute;t mọi thiết bị điện tử anh ta c&oacute; thể t&igrave;m thấy, v&iacute;, điện thoại th&ocirc;ng minh, thậm ch&iacute; cả m&oacute;c kh&oacute;a của Chris.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek tr&oacute;i tay ch&acirc;n hắn ta v&agrave; quả b&oacute;ng bịt miệng hắn ta lại. Sau đ&oacute;, anh ta ph&aacute; hủy điện thoại th&ocirc;ng minh của ch&iacute;nh m&igrave;nh v&agrave; thiết bị theo d&otilde;i GPS, n&eacute;m mọi thứ ra b&ecirc;n ngo&agrave;i đường.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau đ&oacute;, anh ta di chuyển Chris trong th&ugrave;ng sau của chiếc Impala, hướng tới một bữa tiệc chỉ d&agrave;nh cho hai người họ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek l&aacute;i xe đến một nh&agrave; kho bỏ hoang trong khu c&ocirc;ng nghiệp cũ. Anh đ&atilde; gỡ bỏ ổ kh&oacute;a v&agrave; d&acirc;y x&iacute;ch đ&oacute;ng những c&aacute;nh cửa lớn bằng kim loại, thay v&agrave;o đ&oacute; l&agrave; của anh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&ecirc;n trong nh&agrave; kho chỉ c&oacute; hai chiếc ghế, cả hai đều được bắt chặt v&agrave;o đất, một c&aacute;i x&ocirc; v&agrave; một v&agrave;i th&ugrave;ng nước.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek mở cốp xe, ph&aacute;t hiện ra rằng Chris đ&atilde; lấy lại được cảm gi&aacute;c của m&igrave;nh n&ecirc;n anh lại ch&iacute;ch điện v&agrave;o hắn ta. Sau đ&oacute;, anh ta đưa Chris đến một chiếc ghế v&agrave; bắt đầu tr&oacute;i chặt tay v&agrave; ch&acirc;n của hắn v&agrave;o đ&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau đ&oacute;, Derek tạt v&agrave;o người hắn một x&ocirc; nước buộc hắn phải lấy lại &yacute; thức.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Xin ch&agrave;o, Chris. T&ecirc;n tao l&agrave; Derek Esposito, v&agrave; m&agrave;y đ&atilde; giết em trai tao. C&oacute; v&agrave;i chuyện ch&uacute;ng ta cần b&agrave;n đến.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chris cố gắng tho&aacute;t ra khỏi sự kiềm chế, v&agrave; trong khi lấy nỗ lực của hắn ta, Derek đ&atilde; đ&aacute;nh hắn&nbsp; một c&aacute;ch th&ocirc; bạo bởi c&acirc;y gậy baton. Nỗi đau l&agrave;m hắn t&ecirc; liệt.&nbsp;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;C&oacute; phải tao kh&ocirc;ng? Ồ, v&acirc;ng. Lần cuối ch&uacute;ng ta gặp nhau l&agrave; trong phi&ecirc;n t&ograve;a x&eacute;t xử tr&ograve; hề của m&agrave;y. M&agrave;y c&oacute; nhớ tao kh&ocirc;ng?&quot; Chris &#39;thở hổn hển ng&agrave;y c&agrave;ng mạnh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Tốt. Ch&uacute;ng ta đi thẳng v&agrave;o vấn đề.&quot; Derek lấy hai chiếc đồng hồ bấm giờ kỹ thuật số ra khỏi xe, c&agrave;i đặt thứ nhất l&agrave; ba mươi ph&uacute;t v&agrave; thứ hai l&agrave; hai giờ, bốn mươi bốn ph&uacute;t mười s&aacute;u gi&acirc;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau đ&oacute;, anh ta r&uacute;t s&uacute;ng ra v&agrave; bắn hai ph&aacute;t v&agrave;o gan của Chris. Tiếng h&eacute;t của hắn ta đ&atilde; bị b&oacute;p nghẹt bởi quả b&oacute;ng bịt miệng, nhưng những tiếng s&uacute;ng vang dội trong nh&agrave; kho trống rỗng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek khởi động đồng thời cả hai bộ hẹn giờ v&agrave; đến gần, kiểm tra m&aacute;u. N&oacute; đặc v&agrave; c&oacute; m&agrave;u đen, một dấu hiệu r&otilde; r&agrave;ng của một l&aacute; gan bị dập n&aacute;t.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;B&acirc;y giờ trước khi nỗi đau thực sự lắng xuống, tao cần m&agrave;y nh&igrave;n kỹ bộ đếm thời gian, ch&uacute;ng thực sự rất quan trọng.&quot; Chris vừa kh&oacute;c vừa la h&eacute;t, v&igrave; vậy Derek phải t&aacute;t hắn một lần nữa v&agrave; k&eacute;o t&oacute;c hắn để thu h&uacute;t sự ch&uacute; &yacute; của hắn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Bộ đếm thời gian đầu ti&ecirc;n đ&aacute;nh dấu thời gian m&agrave;y c&ograve;n lại. Sau khi n&oacute; đổ chu&ocirc;ng, ngay cả khi ai đ&oacute; c&oacute; thể đột nhập c&aacute;nh cửa đ&oacute; một c&aacute;ch thần kỳ v&agrave; giải cứu m&agrave;y, th&igrave; m&agrave;y vẫn sẽ chết. M&agrave;y chỉ c&oacute; ngần ấy thời gian cho đến khi hệ thần thần kinh của m&agrave;y bị ngập bởi chất độc kh&ocirc;ng được lọc bởi l&aacute; gan, đến mức kh&ocirc;ng thể cấy gh&eacute;p n&agrave;o c&oacute; thể cứu được m&agrave;y. Bộ đếm thời gian thứ hai l&agrave; một điều bất ngờ. Ch&uacute;ng ta sẽ tận dụng khoảng thời gian qu&yacute; b&aacute;o đ&oacute;. Hiện tại, nhiệm vụ duy nhất của m&agrave;y l&agrave; tỉnh t&aacute;o v&agrave; tận hưởng từng gi&acirc;y ph&uacute;t đau đớn, giống như Carl đ&atilde; l&agrave;m. &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thời gian tr&ocirc;i nhanh, Chris tiếp tục la h&eacute;t qua vải bịt miệng, v&agrave; ngay sau đ&oacute; đồng hồ bấm giờ bằng nắm đấm vang l&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chris bắt đầu thở mạnh hơn, đ&ocirc;i khi dừng lại chỉ v&igrave; bị ngất bởi cơn đau li&ecirc;n tục d&acirc;ng l&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek kh&ocirc;ng c&ograve;n n&oacute;i chuyện với hắn ta nữa, anh ta sẽ chỉ đi tới đi lui, thỉnh thoảng kiểm tra đồng hồ thứ hai.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mỗi khi Chris ngất xỉu, Derek sẽ tạt nước v&agrave;o người hắn v&agrave; buộc hắn phải tỉnh t&aacute;o trước khi đổ đầy x&ocirc; v&agrave;o.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi đồng hồ thứ hai reo l&ecirc;n, Derek cuối c&ugrave;ng cũng n&oacute;i lại.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Tao c&oacute; tin xấu v&agrave; c&oacute; tin tốt. Tin xấu l&agrave; tao đ&atilde; n&oacute;i dối. Tao đ&atilde; nghi&ecirc;n cứu rất kỹ về vết thương ở gan, v&agrave; với l&aacute; gan bị dập n&aacute;t như vậy, m&agrave;y kh&ocirc;ng c&oacute; hy vọng n&agrave;o đ&acirc;u. Ngay cả khi tao đ&atilde; bắn m&agrave;y trước một bệnh viện tốt nhất ở Mỹ, trừ khi họ c&oacute; l&aacute; gan tương th&iacute;ch trong tay, m&agrave;y sẽ chết. Tao muốn cho m&agrave;y một hy vọng h&atilde;o huyền, như đ&atilde; xảy ra với em trai tao trong khi chờ đợi sự gi&uacute;p đỡ. Tin tốt l&agrave; m&agrave;y chỉ chịu đựng như Carl đ&atilde; l&agrave;m . Tao c&oacute; thể l&agrave;m nhiều thứ, kh&ocirc;ng ngừng, b&aacute;o th&ugrave;, một kẻ n&oacute;i dối, một kẻ giết người nhưng tao cũng rất c&ocirc;ng bằng. V&igrave; vậy, đau khổ của m&agrave;y sẽ chấm dứt ngay b&acirc;y giờ. &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek chĩa s&uacute;ng v&agrave;o đầu Chris v&agrave; bắn hai ph&aacute;t v&agrave;o hắn ta.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau đ&oacute;, anh ta chỉ n&oacute; v&agrave;o đầu của m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Em trai, anh tới đ&acirc;y. Chờ anh.&quot; V&agrave; b&oacute;p c&ograve; lần cuối.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong khi cơ thể của Derek vẫn đang rơi xuống, &yacute; thức của anh ta được chiếu s&aacute;ng v&agrave; anh ta cảm thấy bị k&eacute;o về ph&iacute;a bầu trời.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau nhiều th&aacute;ng đau buồn, cả cuộc đời khốn khổ v&agrave; đau đớn, Derek cảm thấy rằng tất cả những tổn thương v&agrave; th&ugrave; hận của anh đang dần tan biến.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek chưa bao giờ trải qua niềm hạnh ph&uacute;c như vậy. Trong h&igrave;nh dạng mới n&agrave;y, anh kh&ocirc;ng cảm thấy c&oacute; cảm x&uacute;c ti&ecirc;u cực n&agrave;o, anh b&igrave;nh y&ecirc;n với qu&aacute; khứ của m&igrave;nh, kh&ocirc;ng sợ h&atilde;i về tương lai của m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek đang tận hưởng một m&oacute;n qu&agrave; m&agrave; anh cảm thấy c&oacute; thể dẫn đến v&ocirc; số khả năng, kh&ocirc;ng c&oacute; đ&uacute;ng sai, th&agrave;nh c&ocirc;ng hay thất bại. Anh ấy sẽ đơn giản l&agrave;, kh&ocirc;ng r&agrave;ng buộc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cảm gi&aacute;c say đ&oacute; k&eacute;o d&agrave;i cho đến khi anh đột ngột tỉnh dậy, c&ograve;n sống v&agrave; thở được.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tất cả những cảm x&uacute;c ti&ecirc;u cực của anh ta quay trở lại, đẩy anh ta trở lại tuyệt vọng. Derek rủa thầm trong khi cố tập trung mắt. C&oacute; thể l&agrave; do bị bắn v&agrave;o đầu m&agrave; tầm nh&igrave;n của anh ta bị mờ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Qu&aacute; nhiều cho một kế hoạch ho&agrave;n hảo. Một t&ecirc;n ngốc n&agrave;o đ&oacute; hẳn đ&atilde; cứu t&ocirc;i v&agrave; bằng c&aacute;ch n&agrave;o đ&oacute; t&ocirc;i đ&atilde; sống s&oacute;t sau chuyến h&agrave;nh tr&igrave;nh đến bệnh viện. T&ocirc;i vẫn c&ograve;n sống. T&ocirc;i vẫn bị ung thư. T&ocirc;i vẫn c&ocirc; đơn.&quot; Nhưng cuối c&ugrave;ng khi mắt anh s&aacute;ng tỏ, họ ho&agrave;n to&agrave;n kh&ocirc;ng đồng &yacute; với l&yacute; lẽ của anh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Derek đang ở trong một h&agrave;nh lang kim loại khổng lồ n&agrave;o đ&oacute; được bao quanh bởi những x&aacute;c chết. Ch&iacute;nh x&aacute;c l&agrave; x&aacute;c chết của người ngo&agrave;i h&agrave;nh tinh. Tất cả họ đều mặc một loại &aacute;o gi&aacute;p to&agrave;n th&acirc;n, giống như một loại bộ đồ kh&ocirc;ng gian khoa học viễn tưởng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;T&ocirc;i đang ở đ&acirc;u? C&aacute;i n&agrave;y c&oacute; nghĩa l&agrave; g&igrave;?&quot; Anh ta h&eacute;t l&ecirc;n trong khi cố gắng đứng dậy, chỉ để rơi xuống s&agrave;n nh&agrave;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh ấy ng&atilde; tay trước, v&agrave; chỉ sau đ&oacute; anh ấy nhận thấy rằng anh ấy cũng đang mặc một bộ đồ vũ trụ, v&agrave; b&agrave;n tay của anh ấy, cả bốn người, mỗi tay c&oacute; ba ng&oacute;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-142144fb-7fff-5953-7a02-59e6dae53748\"><span style=\"font-size: 14pt; font-family: Arial; background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;C&Aacute;I QU&Aacute;I G&Igrave; ĐANG XẢY RA!&rdquo;</span></span></p>\r\n<p>\r\n	<br />\r\n	&nbsp;</p>\r\n', 'Phê Kun', '2020-10-19 01:56:52', 1, 0, 4);
INSERT INTO `chapters` (`id`, `name`, `slug`, `site_title`, `meta_desc`, `meta_key`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(197, 'Chương 2: Sức mạnh của một con dã tượng - phần 2', 'chuong-2-suc-manh-cua-mot-con-da-tuong-phan-2-1', NULL, NULL, NULL, 5, '', 1, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nếu L&yacute; T&iacute;n kh&ocirc;ng su&yacute;t giết chết Đo&agrave;n L&atilde;nh Thi&ecirc;n, ch&uacute;ng ta c&oacute; lẽ sẽ kh&ocirc;ng bao giờ c&oacute; cơ hội nh&igrave;n thấy cửu trưởng l&atilde;o ra tay.&rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&hellip;&hellip;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&yacute; Dung vẫn đứng y&ecirc;n, v&agrave; d&aacute;ng người mảnh khảnh của b&agrave; ấy c&oacute; vẻ cao cả v&ocirc; song.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Ta thực sự kh&ocirc;ng bao giờ tưởng tượng rằng mẫu th&acirc;n thực sự c&oacute; sức mạnh như vậy. Dựa tr&ecirc;n năng lượng của trời v&agrave; đất v&agrave; ph&aacute;t triển s&aacute;u b&oacute;ng d&atilde; tượng, b&agrave; ấy chắc chắn đang ở cấp độ thứ tư của Giai đoạn h&igrave;nh th&agrave;nh kim đan. &rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đo&agrave;n L&atilde;nh Thi&ecirc;n thở ph&agrave;o nhẹ nh&otilde;m.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong thế giới n&agrave;y, giai đoạn đầu ti&ecirc;n để trở th&agrave;nh một v&otilde; sĩ được gọi l&agrave; Giai đoạn luyện cơ thể, giai đoạn n&agrave;y được chia th&agrave;nh ch&iacute;n cấp độ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tu luyện đến cấp ch&iacute;n của Giai đoạn luyện th&acirc;n sẽ cho ph&eacute;p cơ thể x&aacute;c thịt c&oacute; sức mạnh vạn c&acirc;n.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Với to&agrave;n bộ sức mạnh, việc sử dụng năng lượng của trời v&agrave; đất sẽ trở n&ecirc;n khả thi. N&oacute; c&oacute; thể được ph&aacute;t triển th&agrave;nh một h&igrave;nh b&oacute;ng d&atilde; tượng, cho ph&eacute;p một người sở hữu sức mạnh của một con d&atilde; tượng.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ph&iacute;a tr&ecirc;n tr&uacute;c cơ kỳ l&agrave; kết đan kỳ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sự kh&aacute;c biệt lớn nhất giữa tr&uacute;c cơ kỳ v&agrave; kết đan kỳ l&agrave; nguy&ecirc;n kh&iacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi đạt được cấp ch&iacute;n của tr&uacute;c cơ kỳ, việc tạo ra nguy&ecirc;n kh&iacute; sẽ cho ph&eacute;p một người tiến l&ecirc;n cấp độ đầu ti&ecirc;n của kết đan kỳ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sức mạnh của nguy&ecirc;n kh&iacute; ở cấp độ một của kết đan kỳ bằng với sức mạnh của một con d&atilde; tượng, v&agrave; kết hợp với sức mạnh hiện c&oacute; của cơ thể x&aacute;c thịt, người tu luyện sẽ c&oacute; thể tạo ra sức mạnh của hai con d&atilde; tượng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi sự tu luyện của họ tăng l&ecirc;n, mức độ sức mạnh c&oacute; thể được kiểm so&aacute;t cũng sẽ tăng l&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nghĩ đến đ&acirc;y, kh&oacute;e miệng Đo&agrave;n L&atilde;nh Thi&ecirc;n nở một nụ cười.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Thi&ecirc;n nhi, ​​b&acirc;y giờ sẽ kh&ocirc;ng c&oacute; ai l&agrave;m phiền con... Con c&oacute; thể tiếp tục.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giọng n&oacute;i dịu d&agrave;ng của L&yacute; Dung truyền v&agrave;o tai Đo&agrave;n L&atilde;nh Thi&ecirc;n, nhắc nhở hắn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Phụ th&acirc;n, cứu con!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nghe những lời của L&yacute; Dung, khu&ocirc;n mặt của L&yacute; T&iacute;n t&aacute;i xanh, v&agrave; đ&ocirc;i mắt của hắn to&aacute;t ra một cảm gi&aacute;c sợ h&atilde;i s&acirc;u sắc&hellip;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chiếc gi&agrave;y trong tay Đo&agrave;n L&atilde;nh Thi&ecirc;n nhất định sẽ gi&aacute;ng xuống hắn, kh&ocirc;ng phải chuyện đ&ugrave;a!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khu&ocirc;n mặt của L&yacute; Kh&ocirc;n rất dữ tợn, nhưng do qu&aacute; sợ h&atilde;i trước L&yacute; Dung, &ocirc;ng ta kh&ocirc;ng d&aacute;m ra tay.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đo&agrave;n L&atilde;nh Thi&ecirc;n bật cười.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">T&aacute;t!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chiếc gi&agrave;y tr&ecirc;n tay hắn tụt xuống kh&ocirc;ng thương tiếc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay lập tức, L&yacute; T&iacute;n ph&aacute;t ra những tiếng k&ecirc;u ch&oacute;i tai, giống như một con lợn bị giết thịt, như một dấu gi&agrave;y r&otilde; r&agrave;ng xuất hiện tr&ecirc;n mặt hắn ta.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">T&aacute;t! T&aacute;t! T&aacute;t! T&aacute;t! T&aacute;t!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&hellip;&hellip;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cầm một chiếc gi&agrave;y trong tay, Đo&agrave;n L&atilde;nh Thi&ecirc;n hết lần n&agrave;y đến lần kh&aacute;c vung tay đ&aacute;nh v&agrave;o mặt L&yacute; T&iacute;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mỗi khi chiếc gi&agrave;y của hắn hạ xuống mặt L&yacute; T&iacute;n, c&aacute;c đệ tử của L&yacute; gia c&oacute; mặt tại đ&acirc;y sẽ cảm thấy một cơn r&ugrave;ng m&igrave;nh kh&ocirc;ng thể kiểm so&aacute;t được chạy khắp cơ thể họ. Chỉ cần nghe thấy tiếng gi&agrave;y đập v&agrave;o mặt L&yacute; T&iacute;n cũng đủ khiến họ quặn l&ograve;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chỉ sau khi L&yacute; T&iacute;n ngất đi, Đo&agrave;n L&atilde;nh Thi&ecirc;n mới chịu dừng lại.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đo&agrave;n L&atilde;nh Thi&ecirc;n n&oacute;i thầm trong l&ograve;ng, cũng chỉ c&oacute; hắn biết m&igrave;nh đang n&oacute;i chuyện với ai.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi theo sau mẫu th&acirc;n v&agrave; rời khỏi s&acirc;n, Đo&agrave;n L&atilde;nh Thi&ecirc;n nhận thấy &aacute;nh mắt của Thất trưởng l&atilde;o, &aacute;nh l&ecirc;n cảm gi&aacute;c kh&aacute;t m&aacute;u v&agrave; hận th&ugrave;; n&oacute; như thể &ocirc;ng ta đang chờ đợi một cơ hội để nuốt chửng m&igrave;nh&hellip;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tr&aacute;i tim của hắn lặng đi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn đ&atilde; từng nh&igrave;n thấy loại &aacute;nh mắt n&agrave;y v&ocirc; số lần trong kiếp trước.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một người c&oacute; &aacute;nh mắt n&agrave;y l&agrave; một người v&ocirc; lương t&acirc;m, người sẽ sử dụng mọi c&aacute;ch c&oacute; sẵn để đạt được mục đ&iacute;ch của m&igrave;nh!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&oacute; l&agrave; l&yacute; do tại sao, trong kiếp trước, bất cứ khi n&agrave;o ai đ&oacute; nh&igrave;n hắn theo c&aacute;ch n&agrave;y, hắn sẽ loại bỏ họ trước khi họ c&oacute; thể g&acirc;y ra mối đe dọa cho hắn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đo&agrave;n L&atilde;nh Thi&ecirc;n cảm thấy &aacute;p lực.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cho d&ugrave; L&yacute; Kh&ocirc;n kh&ocirc;ng phải l&agrave; đối tượng xứng đ&ocirc;i với mẫu th&acirc;n hắn, nhưng mẫu th&acirc;n hắn sẽ kh&ocirc;ng thể lu&ocirc;n ở b&ecirc;n cạnh hắn. B&agrave; sẽ kh&ocirc;ng thể chăm s&oacute;c cho hắn cả đời được.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Để sống trong thế giới n&agrave;y, nơi chỉ c&oacute; kẻ mạnh được t&ocirc;n k&iacute;nh, hắn chỉ c&oacute; thể dựa v&agrave;o ch&iacute;nh m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thứ m&agrave; hắn v&ocirc; c&ugrave;ng cần l&agrave; sức mạnh ... Sức mạnh gh&ecirc; gớm!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&agrave; Cửu trưởng l&atilde;o của L&yacute; gia, L&yacute; Dung c&oacute; một khoảng s&acirc;n ri&ecirc;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&oacute; ba ph&ograve;ng trong s&acirc;n; L&yacute; Dung, Đo&agrave;n L&atilde;nh Thi&ecirc;n v&agrave; c&ocirc; hầu g&aacute;i mỗi người ở trong ph&ograve;ng ri&ecirc;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đo&agrave;n L&atilde;nh Thi&ecirc;n ch&agrave;o L&yacute; Dung sau đ&oacute; quay về ph&ograve;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Sau khi bị thương nặng lần n&agrave;y, Thi&ecirc;n nhi c&oacute; vẻ hơi kh&aacute;c một ch&uacute;t&hellip;&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&agrave; người th&acirc;n thiết nhất với Đo&agrave;n L&atilde;nh Thi&ecirc;n, L&yacute; Dung nhận thấy sự kh&aacute;c biệt của Đo&agrave;n L&atilde;nh Thi&ecirc;n sau khi hắn tỉnh dậy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc; kh&ocirc;ng suy nghĩ g&igrave; th&ecirc;m v&agrave; cho rằng con trai c&ocirc; đ&atilde; trải qua một sự thay đổi sau khi chịu đựng một c&uacute; sốc như vậy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&agrave;m sao b&agrave; c&oacute; thể biết được rằng cơ thể của con trai b&agrave; vẫn c&ograve;n hiện diện, nhưng linh hồn của n&oacute; đ&atilde; bị chuyển sang một nơi kh&aacute;c.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi trở về ph&ograve;ng, Đo&agrave;n L&atilde;nh Thi&ecirc;n ngồi tr&ecirc;n giường, vẻ mặt ảm đạm.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Từ những k&yacute; ức xa lạ đ&oacute;, hắn ph&aacute;t hiện ra rằng nguy&ecirc;n chủ của cơ thể n&agrave;y l&agrave; người ốm yếu v&agrave; mỏng manh từ khi c&ograve;n rất nhỏ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nếu hắn muốn luyện th&acirc;n th&agrave;nh c&ocirc;ng, đạt tới tầng thứ nhất Luyện kh&iacute; kỳ, trở th&agrave;nh v&otilde; giả, kh&oacute; như thăng thi&ecirc;n!</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đo&agrave;n L&atilde;nh Thi&ecirc;n trong l&ograve;ng mắng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;HeHeHeHe&hellip;&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đột nhi&ecirc;n, một chuỗi tiếng cười kh&agrave;n kh&agrave;n kỳ qu&aacute;i, giống như của một b&oacute;ng ma, vang l&ecirc;n trong t&acirc;m tr&iacute; Đo&agrave;n L&atilde;nh Thi&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;AI?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đo&agrave;n L&atilde;nh Thi&ecirc;n m&eacute;o mặt.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-cf7e325d-7fff-a6ff-2b8e-64c9a9beca80\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Nh&oacute;c con, mẫu th&acirc;n kh&ocirc;ng nghĩ con lại may mắn như vậy! Nhưng kh&ocirc;ng c&oacute; g&igrave; thay đổi&hellip; Chờ đ&atilde;, ngươi kh&ocirc;ng phải l&agrave; đứa trẻ đ&oacute;. Ngươi l&agrave; ai?! Chết tiệt! Ngươi thực sự d&aacute;m cướp đi cơ thể m&agrave; ta muốn. Chết!!&quot; giọng n&oacute;i kh&agrave;n kh&agrave;n đột nhi&ecirc;n chứa đầy sự t&agrave;n nhẫn v&agrave; thịnh nộ.</span></span></p>\r\n<div>\r\n	&nbsp;</div>\r\n', 'Phê Kun', '2020-10-19 01:58:03', 1, 0, 5);
INSERT INTO `chapters` (`id`, `name`, `slug`, `site_title`, `meta_desc`, `meta_key`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(198, 'Chap 4 : Căn nhà cũ', 'chap-4-can-nha-cu', NULL, NULL, NULL, 26, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Chủ tịch Tập đo&agrave;n Yate, Zhao Yalong, đ&atilde; ho&agrave;n th&agrave;nh qu&aacute; tr&igrave;nh tiến h&oacute;a lần thứ ba, bước v&agrave;o Th&aacute;nh địa thứ tư của Thần v&agrave; trở th&agrave;nh người thứ 83 trong lịch sử v&agrave;o Th&aacute;nh địa thứ tư của Thần. Tuổi thọ của &ocirc;ng ta đ&atilde; l&ecirc;n tới 500 năm v&agrave; &ocirc;ng ta đ&atilde; đạt được trạng th&aacute;i &aacute; thần&hellip;&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Thần đồng đến từ Raikot Galaxy, Lange, bước v&agrave;o Th&aacute;nh địa thứ hai của Thần với 100 điểm geno đột biến. Tuổi thọ của anh ta đ&atilde; l&ecirc;n đến 300 năm v&agrave; anh ta đ&atilde; đạt được trạng th&aacute;i tiến h&oacute;a ...&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Người ph&aacute;t biểu của Hạ viện, Hemingway, đ&atilde; giết một ng&ocirc;i sao sinh vật &aacute; thần của đại dương ng&agrave;y h&ocirc;m qua, v&agrave; l&agrave; người đầu ti&ecirc;n đạt được kỳ t&iacute;ch n&agrave;y ...&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Theo c&aacute;c chuy&ecirc;n gia c&oacute; thẩm quyền, trong mười năm nữa, ai đ&oacute; sẽ lần đầu ti&ecirc;n ho&agrave;n th&agrave;nh qu&aacute; tr&igrave;nh tiến h&oacute;a thứ tư v&agrave; tiến v&agrave;o Th&aacute;nh địa thứ năm của Thần ...&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;....&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Han Sen vừa dịch chuyển từ Steel Armor Shelter v&agrave; tin tức đang chiếu tr&ecirc;n m&agrave;n h&igrave;nh ảo.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Người tiến h&oacute;a, những kẻ chinh phục v&agrave; &aacute; thần. Với vi&ecirc;n pha l&ecirc; đen, m&igrave;nh sẽ c&oacute; thể l&agrave;m tất cả những điều đ&oacute;, v&agrave; thậm ch&iacute; c&ograve;n đi xa hơn nữa.&quot; Han Sen lắc tay, bước ra khỏi trạm dịch chuyển v&agrave; bắt chuyến t&agrave;u maglev về nh&agrave;.</span></span></p>\r\n<p>\r\n	<br />\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trước khi v&agrave;o s&acirc;n, Han Sen đ&atilde; nghe thấy một giọng nữ ch&oacute;i tai.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Luo Sulan, chị c&oacute; muốn lấy ng&ocirc;i nh&agrave; cũ của Han kh&ocirc;ng? Ng&ocirc;i nh&agrave; n&agrave;y l&agrave; của cha mẹ ch&uacute;ng t&ocirc;i, hai anh trai t&ocirc;i v&agrave; t&ocirc;i mỗi người một phần. B&acirc;y giờ anh cả của t&ocirc;i đ&atilde; qua đời. Ch&uacute;ng t&ocirc;i sẽ kh&ocirc;ng bắt nạt chị v&igrave; chị l&agrave; một g&oacute;a phụ, v&agrave; sẽ để lại cho chị phần của anh ấy. Nhưng nếu chị muốn lấy mọi thứ, ch&uacute;ng t&ocirc;i sẽ kh&ocirc;ng bao giờ đồng &yacute;. &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Đ&uacute;ng rồi!&quot; Giọng một người đ&agrave;n &ocirc;ng trung ni&ecirc;n cất l&ecirc;n &quot;Ng&ocirc;i nh&agrave; cũ đ&oacute; l&agrave; di sản của cha mẹ ch&uacute;ng t&ocirc;i. Ai cũng c&oacute; quyền đối với n&oacute;, chị kh&ocirc;ng thể giữ n&oacute; cho ri&ecirc;ng m&igrave;nh. Gia đ&igrave;nh chị đ&atilde; sống ở đ&acirc;y hơn 20 năm, vậy l&agrave; chị đ&atilde; lợi dụng ch&uacute;ng t&ocirc;i rồi, đ&atilde; đến l&uacute;c phải ph&acirc;n chia gia sản rồi. &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Anh trai t&ocirc;i đ&atilde; n&oacute;i rồi,&rdquo; một giọng nam nhẹ nh&agrave;ng tiếp tục. &quot;Chị ở căn nh&agrave; cũ hơn 20 năm, ch&uacute;ng ta sẽ kh&ocirc;ng thu tiền thu&ecirc;, nhưng căn nh&agrave; cũ n&agrave;y l&agrave; của ta thừa kế v&agrave; n&oacute; phải được chia ra.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&oacute; thể nghe thấy giọng một phụ nữ trẻ n&oacute;i: &quot;Ch&uacute;ng t&ocirc;i c&oacute; ước t&iacute;nh từ luật sư, v&agrave; ng&ocirc;i nh&agrave; cổ n&agrave;y hiện trị gi&aacute; hơn ba triệu. Ch&uacute;ng t&ocirc;i l&agrave; ba hộ gia đ&igrave;nh, n&ecirc;n mỗi hộ nhận được một triệu. Chị c&oacute; thể cho mỗi người ch&uacute;ng t&ocirc;i. một triệu v&agrave; sở hữu ho&agrave;n to&agrave;n căn nh&agrave; cũ n&agrave;y, hoặc b&aacute;n căn nh&agrave;, sau đ&oacute; ch&uacute;ng t&ocirc;i chia đ&ocirc;i tiền. Ch&uacute;ng t&ocirc;i ổn với một trong hai phương &aacute;n đ&oacute;. &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ch&iacute;nh x&aacute;c, b&acirc;y giờ anh cả của ch&uacute;ng t&ocirc;i đ&atilde; mất, ch&uacute;ng t&ocirc;i sẽ kh&ocirc;ng bắt nạt người vợ g&oacute;a v&agrave; những đứa trẻ của anh ấy. Chị c&oacute; thể cho ch&uacute;ng t&ocirc;i tiền hoặc b&aacute;n nh&agrave; t&ugrave;y th&iacute;ch&quot;, giọng n&oacute;i đầu ti&ecirc;n khắc nghiệt lại bắt đầu.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Vậy th&igrave; đăng b&aacute;n nh&agrave;&quot;, người phụ nữ gay gắt n&oacute;i ngay.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ch&uacute;ng t&ocirc;i sẽ sống ở đ&acirc;u nếu kh&ocirc;ng c&oacute; nh&agrave;? Han Sen mới tốt nghiệp v&agrave; Han Yan sắp đi học&quot;, người phụ nữ buồn b&atilde; n&oacute;i.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Chị d&acirc;u, điều n&agrave;y kh&ocirc;ng hợp l&yacute;.Chị kh&ocirc;ng phải l&agrave; người duy nhất gặp rắc rối? Con của chị Han Sen v&agrave; Han Yan chỉ đăng k&yacute; v&agrave;o chương tr&igrave;nh gi&aacute;o dục bắt buộc t&iacute;ch hợp, c&ograve;n Han Hao của em th&igrave; học trường tư. Tiền học ph&iacute; của n&oacute; l&agrave;m cho em phải đau đầu mỗi năm. &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Chị ơi, chị n&oacute;i đ&uacute;ng. Tất cả ch&uacute;ng ta đều đang gặp kh&oacute; khăn v&agrave; ai cũng cần tiền. Tại sao ch&uacute;ng ta kh&ocirc;ng b&aacute;n căn nh&agrave; đi? Tất cả ch&uacute;ng ta c&oacute; thể lấy một &iacute;t v&agrave; chị c&oacute; thể trang trải việc học của Han Yan&quot;, giọng n&oacute;i ẻo lả n&oacute;i l&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;D&ugrave; sao, chị cho ch&uacute;ng t&ocirc;i tiền hoặc b&aacute;n nh&agrave;. Sự lựa chọn của chị&quot;, thiếu nữ lạnh l&ugrave;ng n&oacute;i.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&ecirc;n ngo&agrave;i ng&ocirc;i nh&agrave;, Han Sen trở n&ecirc;n tức giận khi anh lắng nghe. Anh đẩy cửa bước v&agrave;o v&agrave; thấy mẹ anh đang rơi nước mắt khi &ocirc;m em g&aacute;i anh, Han Yan. C&ocirc; b&eacute; năm tuổi cuộn tr&ograve;n trong v&ograve;ng tay của mẹ, đ&ocirc;i mắt to tr&ograve;n đầy sợ h&atilde;i. B&ecirc;n cạnh bọn họ, hai nam hai nữ đều đang cười v&ocirc; tư.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Sao c&aacute;c người d&aacute;m tới đ&acirc;y đ&ograve;i nh&agrave; cũ?&quot; Han Sen chỉ v&agrave;o người phụ nữ b&eacute;o tr&ocirc;ng dữ tợn v&agrave; tức giận thốt l&ecirc;n. &quot;C&ocirc; ơi, c&ocirc; từng l&agrave; nh&acirc;n vi&ecirc;n kế to&aacute;n của c&ocirc;ng ty v&agrave; mang về khoản nợ h&agrave;ng chục triệu. Sẽ thế n&agrave;o nếu cha t&ocirc;i kh&ocirc;ng gi&uacute;p c&ocirc; l&uacute;c đ&oacute;?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Han Sen, vớ vẩn. Ai đ&atilde; g&acirc;y ra nợ ? Đừng n&oacute;i dối nữa,&quot; t&ecirc;n gầy g&ograve; với giọng n&oacute;i ẻo lả k&ecirc;u l&ecirc;n, chỉ v&agrave;o Han Sen.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ch&uacute;, ch&uacute; biển thủ c&ocirc;ng quỹ của c&ocirc;ng ty đi đầu tư, mất mấy triệu.&quot; Han Sen chỉ v&agrave;o người gầy. &quot;Ch&uacute; kh&ocirc;ng đến nh&agrave; t&ocirc;i m&agrave; quỳ gối cầu xin ba t&ocirc;i gi&uacute;p sao?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;C&ograve;n ch&uacute;, ch&uacute; &agrave;, ch&uacute; nợ cờ bạc nhiều đến nỗi su&yacute;t phải tự tử. Ai đ&atilde; gi&uacute;p ch&uacute;? Khi ch&uacute; su&yacute;t bị đ&aacute;nh chết, ai đ&atilde; cứu sống ch&uacute;?&quot; Han Sen chỉ v&agrave;o người đ&agrave;n &ocirc;ng trung ni&ecirc;n mập mạp.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Tập đo&agrave;n Starry muốn mua lại c&ocirc;ng việc kinh doanh của gia đ&igrave;nh ch&uacute;ng t&ocirc;i. Nếu bố t&ocirc;i kh&ocirc;ng đại diện cho tất cả c&aacute;c người v&agrave; sau đ&oacute; r&uacute;t khỏii Tập đo&agrave;n Starry, liệu &ocirc;ng ấy c&oacute; chết một c&aacute;ch mờ &aacute;m như vậy kh&ocirc;ng?&quot; Han Sen nghiến răng, nh&igrave;n bốn người. &quot;Nhưng c&ograve;n c&aacute;c người, c&aacute;c người kh&ocirc;ng đến đ&aacute;m tang của bố t&ocirc;i v&agrave; b&aacute;n c&ocirc;ng ty cho Tập đo&agrave;n Starry sau khi &ocirc;ng qua đời. C&aacute;c người c&oacute; cho ch&uacute;ng t&ocirc;i bất kỳ khoản tiền n&agrave;o c&aacute;c người nhận được từ việc b&aacute;n c&ocirc;ng ty kh&ocirc;ng? Ngo&agrave;i ra, ai đ&atilde; trả tiền mua nh&agrave; của c&aacute;c người khi c&aacute;c người kết h&ocirc;n? rẻ hơn c&aacute;i nh&agrave; cổ n&agrave;y sao? C&aacute;c người c&oacute; chăm s&oacute;c cha mẹ c&aacute;c người một ng&agrave;y n&agrave;o khi họ c&ograve;n sống kh&ocirc;ng? Sao c&aacute;c người d&aacute;m đến đ&acirc;y đ&ograve;i chia phần căn nh&agrave; cũ? &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Th&ocirc;i đi! Cha của m&agrave;y chỉ l&agrave;m những g&igrave; &ocirc;ng ấy muốn l&agrave;m. C&ocirc;ng ty thuộc về cha tao, v&agrave; ch&uacute;ng tao đều c&oacute; cổ phần. &Ocirc;ng ấy kh&ocirc;ng thể quyết định cho ch&uacute;ng tao. V&agrave; chắc chắn, ch&uacute;ng tao c&oacute; thể ti&ecirc;u tiền của c&ocirc;ng ty&quot;, g&atilde; Han Lei n&oacute;i một c&aacute;ch trơ trẽn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Tất cả l&agrave; lỗi của bố m&agrave;y...&quot; Họ bắt đầu buộc tội người cha qu&aacute; cố của Han Sen.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Han Sen run l&ecirc;n v&igrave; tức giận v&agrave; cảm thấy c&oacute; lỗi với cha m&igrave;nh. Khi cha anh tiếp quản c&ocirc;ng ty, n&oacute; chỉ l&agrave; một cơ sở kinh doanh nhỏ, trị gi&aacute; một hai triệu. &Ocirc;ng ấy đ&atilde; bỏ ra bao nhi&ecirc;u c&ocirc;ng sức để c&ocirc;ng ty nở rộ v&agrave; phải chăm s&oacute;c gia đ&igrave;nh, b&ugrave; đắp lỗi lầm. Một v&agrave;i lần c&ocirc;ng ty đứng tr&ecirc;n bờ vực ph&aacute; sản do vấn đề thanh khoản. V&agrave; &ocirc;ng ấy thậm ch&iacute; đ&atilde; chết v&igrave; c&ocirc;ng ty. Tuy nhi&ecirc;n, sau khi &ocirc;ng qua đời, vợ v&agrave; con của &ocirc;ng bị bắt nạt bởi những người th&acirc;n m&agrave; &ocirc;ng từng gi&uacute;p đỡ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi cha của Han Sen c&ograve;n sống, &ocirc;ng lu&ocirc;n n&oacute;i rằng họ l&agrave; gia đ&igrave;nh v&agrave; kh&ocirc;ng cần phải b&agrave;n c&atilde;i nhau. V&agrave; thật l&agrave; trớ tr&ecirc;u!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0042ddd3-7fff-bfc9-2fc3-ed07b4df7f22\"><span style=\"font-size: 14pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nếu họ thực sự cần tiền v&agrave; gặp kh&oacute; khăn, Han Sen sẽ đồng &yacute; cho. Nhưng tất cả c&aacute;c m&aacute;y bay tư nh&acirc;n m&agrave; họ l&aacute;i ở đ&acirc;y đều trị gi&aacute; hơn một triệu USD. V&agrave; &iacute;t nhất họ cũng c&oacute; được mười triệu từ việc b&aacute;n c&ocirc;ng ty, tất cả đều rơi v&agrave;o t&uacute;i của họ, v&agrave; kh&ocirc;ng c&oacute; g&igrave; được đưa cho gia đ&igrave;nh của Han Sen. Kh&ocirc;ng thể n&oacute;i l&agrave; họ thậm ch&iacute; kh&ocirc;ng c&oacute; một triệu.</span></span></p>\r\n<div>\r\n	&nbsp;</div>\r\n', 'Phê Kun', '2020-10-19 01:59:08', 1, 0, 4);
INSERT INTO `chapters` (`id`, `name`, `slug`, `site_title`, `meta_desc`, `meta_key`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(199, 'Chương 4 : Gia tộc Bạch thị', 'chuong-4-gia-toc-bach-thi', NULL, NULL, NULL, 7, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Hả! Rời nước Th&aacute;i Hi&ecirc;n? Ngươi thực sự nghĩ rằng Vi&ecirc;n thuốc Dưỡng T&acirc;m m&agrave; Trưởng tộc đưa cho ch&uacute;ng ta l&agrave; Thần dược &agrave;? H&atilde;y nghĩ về những g&igrave; đ&atilde; xảy ra với những người trước đ&acirc;y đ&atilde; phản bội tộc Bạch thị. Ngo&agrave;i ra, cả hai gia đ&igrave;nh ch&uacute;ng ta vẫn đang sống trong tộc Bạch thị. Nếu b&acirc;y giờ ch&uacute;ng ta rời đi, họ sẽ sống như thế n&agrave;o? Nếu ta nhớ kh&ocirc;ng nhầm, ngươi đ&atilde; kết h&ocirc;n với một phụ nữ xinh đẹp v&agrave;o năm ngo&aacute;i v&agrave; c&ocirc; ấy c&oacute; thai v&agrave;o đầu năm nay. Theo Dương đại phu, c&oacute; 80% khả năng đ&oacute; l&agrave; một b&eacute; trai &rdquo;. Quan l&atilde;o đại lớn tiếng n&oacute;i, nhưng vẻ mặt nghi&ecirc;m trọng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Việc n&agrave;y&hellip;&rdquo; Nghe những g&igrave; hắn ta n&oacute;i khiến Cổ l&atilde;o tam cảm thấy như bị dội một g&aacute;o nước lạnh v&agrave;o đầu. Hắn đứng bất động kh&ocirc;ng n&oacute;i một lời.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Nếu chuyện n&agrave;y thực sự kh&ocirc;ng th&agrave;nh c&ocirc;ng, ta đ&atilde; c&oacute; một kế hoạch theo hướng tuyệt vọng. Mặc d&ugrave; cơ hội sống của ch&uacute;ng ta kh&ocirc;ng vượt qu&aacute; hai mươi phần mười. Kế hoạch n&agrave;y cũng sẽ sử dụng hết số tiền tiết kiệm m&agrave; ch&uacute;ng ta đ&atilde; t&iacute;ch trữ trong nhiều năm. &rdquo; Đ&ocirc;i mắt của Quan l&atilde;o đại l&oacute;e l&ecirc;n một ch&uacute;t trước khi n&oacute;i.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Sử dụng hết số tiền tiết kiệm của ch&uacute;ng ta v&agrave; vẫn chỉ c&oacute; hai mươi phần mười cơ hội sống s&oacute;t?&quot; Nghe vậy, gương mặt của Cổ l&atilde;o tam trở n&ecirc;n cay đắng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Hừm. Nếu ta kh&ocirc;ng c&oacute; quan hệ th&acirc;n th&iacute;ch n&agrave;o đ&oacute; với vợ cả của Tộc Trưởng, th&igrave; ngay cả cơ hội nhỏ n&agrave;y cũng sẽ kh&ocirc;ng c&oacute;. Nếu ngươi kh&ocirc;ng muốn, ta sẽ kh&ocirc;ng &eacute;p ngươi. &rdquo; Quan l&atilde;o đại b&igrave;nh tĩnh đ&aacute;p.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Ngươi hiểu lầm rồi, Quan l&atilde;o đại. L&agrave;m sao ta c&oacute; thể từ chối chứ? Nếu việc n&agrave;y c&oacute; thể bảo to&agrave;n mạng sống của ch&uacute;ng ta, th&igrave; việc sử dụng hết của cải cũng kh&ocirc;ng phải l&agrave; vấn đề lớn &rdquo;. Cổ l&atilde;o tam nhanh ch&oacute;ng n&oacute;i trong sự ngạc nhi&ecirc;n v&agrave; mỉm cười hối lỗi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Nếu ngươi hiểu th&igrave; tốt! Đừng l&atilde;ng ph&iacute; thời gian nữa, trước khi c&oacute; tin tức về tộc, ch&uacute;ng ta cần phải l&eacute;n quay lại để nhanh ch&oacute;ng chuẩn bị. &quot; Vẻ mặt của Quan l&atilde;o đại gi&atilde;n ra một ch&uacute;t.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lần n&agrave;y, t&ecirc;n Cổ l&atilde;o tam chỉ biết gật đầu, kh&ocirc;ng d&aacute;m lộ ra vẻ ngo&agrave;i &yacute; muốn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hai người b&agrave;n kế hoạch th&ecirc;m một ch&uacute;t nữa l&agrave; trước ng&ocirc;i mộ mới. Từ trong ngực, Quan l&atilde;o đại r&uacute;t ra một đ&ocirc;i găng tay m&agrave;u xanh lục, l&agrave;m bằng chất liệu g&igrave; kh&ocirc;ng r&otilde; v&agrave; h&eacute;t l&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Từ đ&ocirc;i găng tay m&agrave;u xanh l&aacute; c&acirc;y, một tầng &aacute;nh s&aacute;ng trắng, s&aacute;ng ch&oacute;i lao về ph&iacute;a mặt đất trước mặt hắn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Boom&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đất v&agrave; v&ocirc; số cỏ hoang bay về c&aacute;c hướng kh&aacute;c nhau khi một c&aacute;i hố s&acirc;u ba thước xuất hiện trước mặt hai người.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cổ l&atilde;o tam đi v&ograve;ng quanh c&aacute;i hố trước khi đ&aacute; những x&aacute;c chết v&agrave;o đ&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi bước tới chỗ x&aacute;c chết của người thanh ni&ecirc;n, hắn ta do dự một ch&uacute;t trước khi đưa tay ra sau lưng v&agrave; r&uacute;t kiếm ra khỏi vỏ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Với một c&aacute;i vung tay của m&igrave;nh tr&ecirc;n thanh kiếm, một &aacute;nh s&aacute;ng m&agrave;u xanh l&aacute; c&acirc;y ph&acirc;n t&aacute;n từ bề mặt của n&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">T&ecirc;n Cổ l&atilde;o tam xoay cổ tay, hắn ta chuẩn bị d&ugrave;ng kiếm để l&agrave;m g&igrave; đ&oacute; với x&aacute;c chết.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mặc d&ugrave;, Quan l&atilde;o đại chỉ đơn giản l&agrave; quan s&aacute;t từ một b&ecirc;n, tai hắn giật giật v&agrave; vẻ mặt thay đổi. Sau đ&oacute;, hắn ta quay về ph&iacute;a d&ograve;ng s&ocirc;ng v&agrave; h&eacute;t l&ecirc;n, &ldquo;Ai vậy? Ai d&aacute;m lẻn v&agrave;o nghe trộm ch&uacute;ng ta! &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Vừa dứt lời, t&ecirc;n Quan l&atilde;o đại đ&atilde; bắt đầu động thủ. Tr&ecirc;n nắm tay của hắn, một tia s&aacute;ng l&oacute;e l&ecirc;n v&agrave; một khối kh&iacute; tức đ&aacute;ng sợ bay ra.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một tiếng ồn bị b&oacute;p nghẹt!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&aacute;ch đ&oacute; khoảng một trăm bước, c&acirc;y cối b&ecirc;n dưới bắt đầu rung chuyển dữ dội. Một b&oacute;ng người yếu ớt trườn ra, trước khi hắn nằm bất động tr&ecirc;n mặt đất.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Quan l&atilde;o đại thấy vậy liền bước tới. Với một c&uacute; đ&aacute;, hắn ta lật người ngửa mặt l&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&aacute;ng ngạc nhi&ecirc;n, đ&oacute; l&agrave; một thanh ni&ecirc;n đang nhắm mắt. Quần &aacute;o của cậu ta r&aacute;ch n&aacute;t v&agrave; cậu ta ho&agrave;n to&agrave;n bất tỉnh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;C&oacute; thể l&agrave; cậu ta trườn từ con s&ocirc;ng l&ecirc;n? C&oacute; vẻ như ta thậm ch&iacute; kh&ocirc;ng phải l&agrave;m g&igrave; cả v&agrave; cậu ấy sẽ kh&ocirc;ng c&ograve;n sống được bao l&acirc;u nữa &rdquo;. Quan l&atilde;o đại liếc nh&igrave;n người thanh ni&ecirc;n một lần nữa v&agrave; thư gi&atilde;n sau khi x&aacute;c nhận rằng kh&ocirc;ng c&oacute; mối đe dọa n&agrave;o.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Tuy nhi&ecirc;n, h&atilde;y nhanh ch&oacute;ng kết liễu cậu ta. Những g&igrave; ch&uacute;ng ta vừa n&oacute;i kh&ocirc;ng thể bị lọt ra ngo&agrave;i &rdquo;. Nghe được Quan l&atilde;o đại n&oacute;i, Cổ l&atilde;o tam b&ecirc;n cạnh lỗ hổng thoải m&aacute;i một ch&uacute;t. Kh&ocirc;ng ngần ngại hắn ta vẫn k&ecirc;u h&agrave;nh động giết người.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Ngươi kh&ocirc;ng cần phải n&oacute;i với ta điều đ&oacute;, ta biết. Hm? Vẻ ngo&agrave;i của đứa trẻ n&agrave;y&hellip; &rdquo;Quan l&atilde;o đại hơi kh&oacute; chịu với Cổ l&atilde;o tam v&agrave; đ&ocirc;i găng tay tr&ecirc;n nắm đấm của hắn ta lại bắt đầu s&aacute;ng l&ecirc;n. Tuy nhi&ecirc;n, ngay khi &aacute;nh s&aacute;ng qu&eacute;t qua khu&ocirc;n mặt của người thanh ni&ecirc;n, hắn ta đ&atilde; bị sốc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Chuyện g&igrave; đang xảy ra vậy? Khu&ocirc;n mặt của t&ecirc;n n&agrave;y c&oacute; g&igrave; đ&oacute; sai sai? &quot; Khi Cổ l&atilde;o tam nh&igrave;n thấy Quan l&atilde;o đại hạ găng tay xuống, Cổ l&atilde;o tam tự nhi&ecirc;n trở n&ecirc;n t&ograve; m&ograve;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ngươi lại đ&acirc;y rồi sẽ biết!&quot; Quan l&atilde;o đại thu tay lại v&agrave; vẫy Cổ l&atilde;o tam với một biểu hiện lạ l&ugrave;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thấy vậy, sự t&ograve; m&ograve; của Cổ l&atilde;o tam ng&agrave;y c&agrave;ng lớn. Hắn kh&ocirc;ng nghĩ nhiều liền tra kiếm v&agrave;o tay, bước tới chỗ Quan l&atilde;o đại.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Đứa trẻ n&agrave;y l&agrave; ai v&agrave; tại sao n&oacute; tr&ocirc;ng giống hệt như thiếu gia?&quot; Sau khi người đ&agrave;n &ocirc;ng gầy g&ograve; nh&igrave;n thấy d&aacute;ng vẻ của người thanh ni&ecirc;n, hắn ta kh&ocirc;ng khỏi kinh ngạc thốt l&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;N&oacute; gần giống, nhưng khu&ocirc;n mặt của cậu ấy c&oacute; phần nhợt nhạt. L&ocirc;ng m&agrave;y của cậu ta cũng d&agrave;y hơn nhiều v&agrave; cậu ta c&oacute; nhiều vết sẹo tr&ecirc;n mặt. Ngo&agrave;i ra, l&agrave;n da của cậu ta c&oacute; phần th&ocirc; r&aacute;p v&agrave; kh&aacute;c hẳn với l&agrave;n da mịn m&agrave;ng của thiếu gia &mdash; do kh&ocirc;ng bao giờ phải l&agrave;m việc chăm chỉ &rdquo;. Loan l&atilde;o đại h&iacute;t một hơi v&agrave; quan s&aacute;t kỹ trước khi đi đến kết luận.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;C&oacute; rất nhiều người c&oacute; ngoại h&igrave;nh tương tự tr&ecirc;n thế giới n&agrave;y. Kh&ocirc;ng qu&aacute; lạ khi gặp một người c&oacute; ngoại h&igrave;nh giống thiếu gia. Đứa trẻ n&agrave;y tr&ocirc;ng giống với thiếu gia như thế n&agrave;o cũng kh&ocirc;ng quan trọng, v&igrave; thiếu gia đ&atilde; c&oacute; Linh kh&iacute; v&agrave; sẽ trở th&agrave;nh một Linh lực sư như ta v&agrave; ngươi. &quot; Cổ l&atilde;o tam cũng đ&atilde; b&igrave;nh tĩnh lại v&agrave; ph&acirc;n t&iacute;ch kỹ lưỡng t&igrave;nh h&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Tuy nhi&ecirc;n, nếu đứa trẻ n&agrave;y c&oacute; được Linh kh&iacute;, th&igrave; ta c&oacute; một kế hoạch tốt hơn để bảo to&agrave;n mạng sống của ch&uacute;ng ta. Tuy nhi&ecirc;n, ta kh&ocirc;ng nghĩ rằng c&oacute; sự tr&ugrave;ng hợp ngẫu nhi&ecirc;n tr&ecirc;n thế giới n&agrave;y. C&aacute;c tộc đệ tử mượn sức mạnh quan hệ huyết thống của m&igrave;nh để sinh ra những đứa trẻ c&oacute; Linh kh&iacute;. Đối với những người b&igrave;nh thường th&igrave; khả năng họ sẽ c&oacute; Linh kh&iacute; l&agrave; một trong một ngh&igrave;n cơ hội. Nếu một người b&igrave;nh thường biết rằng họ c&oacute; Linh kh&iacute;, nếu kh&ocirc;ng c&oacute; đủ tr&igrave;nh hoặc phương ph&aacute;p luyện tập, họ kh&ocirc;ng thể trở th&agrave;nh Linh lực sư. C&ograve;n nhớ ch&uacute;ng ta? Ngay khi nhận ra m&igrave;nh c&oacute; Linh kh&iacute;, ch&uacute;ng ta đ&atilde; quyết định gia nhập gia tộc Bạch thị, để c&oacute; thể đạt đến đỉnh cao như ng&agrave;y h&ocirc;m nay. &rdquo; Quan l&atilde;o đại vẫn đang than thở về tương lai của m&igrave;nh khi n&oacute;i như vậy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Đ&uacute;ng rồi. Những người đ&atilde; được gửi đến buổi lễ n&agrave;y l&agrave; tốt nhất trong số những người tốt nhất trong gia tộc Linh lực sư. B&acirc;y giờ nghĩ lại, ch&uacute;ng ta kh&ocirc;ng c&oacute; cơ hội để đ&aacute;nh bại những kẻ sinh ra trong gia tộc Bạch thị &rdquo; Cổ l&atilde;o tam kh&ocirc;ng suy nghĩ nhiều khi trả lời.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Kh&ocirc;ng c&oacute; g&igrave; ch&uacute;ng ta n&oacute;i gi&uacute;p &iacute;ch ngay b&acirc;y giờ. Đầu ti&ecirc;n ch&uacute;ng ta h&atilde;y cố gắng hết sức để giữ mạng sống của m&igrave;nh. Thấy đứa trẻ n&agrave;y c&ograve;n kh&aacute; trẻ, ta sẽ để n&oacute; với một cơ thể kh&ocirc;ng c&ograve;n nguy&ecirc;n vẹn &rdquo;. Quan l&atilde;o đại kh&ocirc;ng n&oacute;i nhiều nữa, căng thẳng c&aacute;nh tay. Tr&ecirc;n đầu b&agrave;n tay hắn ta gần như v&ocirc; h&igrave;nh lấp l&aacute;nh m&agrave;u xanh lục.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đương nhi&ecirc;n, Cổ l&atilde;o tam kh&ocirc;ng phản đối việc giết đứa trẻ n&agrave;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Với một c&aacute;i lắc tay, nắm đấm m&agrave;u lục của Quan l&atilde;o đại nhẹ nh&agrave;ng đặt tay xuống Đan Điền của ch&agrave;ng trai trẻ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mặc d&ugrave; đ&ograve;n tấn c&ocirc;ng c&oacute; vẻ cực kỳ yếu ớt, Quan l&atilde;o đại&nbsp; đ&atilde; sử dụng sức mạnh của&nbsp; linh kh&iacute; của m&igrave;nh. Do đ&oacute;, sức mạnh trong đ&ograve;n tấn c&ocirc;ng đủ để ph&aacute; vỡ c&aacute;c cơ quan nội tạng của đứa trẻ v&agrave; do đ&oacute; sẽ lấy đi mạng sống của n&oacute; trong v&ograve;ng v&agrave;i gi&acirc;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay khi Cổ l&atilde;o tam nh&igrave;n thấy &aacute;nh s&aacute;ng xanh tr&ecirc;n nắm đấm của Loan l&atilde;o đại, hắn ta quay lại v&agrave; định &lsquo;xử l&yacute;&rsquo; x&aacute;c chết của thiếu gia&nbsp; của m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, ngay sau đ&oacute;, một tiếng h&eacute;t kinh ngạc vang l&ecirc;n từ ph&iacute;a sau hắn&nbsp; ta.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Kh&ocirc;ng thể n&agrave;o, t&ecirc;n nh&oacute;c n&agrave;y c&oacute; Vi&ecirc;n Lực. Hắn c&oacute; Linh kh&iacute;! &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giọng của Quan l&atilde;o đại hơi run khi hắn ta thốt l&ecirc;n</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay khi nghe giọng của hắn, Cổ l&atilde;o tam cũng ngoảnh mặt lại.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn nh&igrave;n thấy Quan l&atilde;o đại đang nắm b&agrave;n tay lại trong khi &aacute;nh s&aacute;ng m&agrave;u xanh tr&ecirc;n nắm đấm đ&atilde; ho&agrave;n to&agrave;n biến mất.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, tr&ecirc;n bụng của người thanh ni&ecirc;n, một v&ograve;ng &aacute;nh s&aacute;ng trắng gần như v&ocirc; h&igrave;nh đang &acirc;m thầm rung động v&agrave; tr&ocirc;ng như thể n&oacute; tắt bất cứ l&uacute;c n&agrave;o.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Vi&ecirc;n lực bảo hộ. Thằng nh&oacute;c n&agrave;y chắc chắn c&oacute; Linh kh&iacute; v&agrave; c&oacute; lẽ cũng c&oacute; nền tảng Nguy&ecirc;n Kh&iacute; kh&aacute; đ&agrave;ng ho&agrave;ng. &rdquo; Cổ l&atilde;o tam kh&aacute; cho&aacute;ng v&aacute;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Haha, lần n&agrave;y, đ&oacute; l&agrave; một ph&eacute;p m&agrave;u thực sự. Cổ l&atilde;o tam, ch&uacute;ng ta kh&ocirc;ng cần quay lại gia tộc Bạch thị nữa. Với đứa trẻ n&agrave;y, ch&uacute;ng ta c&oacute; thể sống cuộc sống của m&igrave;nh m&agrave; kh&ocirc;ng cần lo lắng &rdquo;. Quan l&atilde;o đại ngơ ng&aacute;c nh&igrave;n nh&oacute;c con c&ograve;n đang h&ocirc;n m&ecirc; mới bắt đầu cười thoải m&aacute;i. Một ch&uacute;t đi&ecirc;n rồ hiện r&otilde; tr&ecirc;n khu&ocirc;n mặt hắn ta.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Quan l&atilde;o đại, &yacute; của ngươi l&agrave;. Tại sao t&ecirc;n nh&oacute;c n&agrave;y lại c&oacute; Linh kh&iacute; cứu mạng ch&uacute;ng ta? &rdquo; Cổ l&atilde;o tam&nbsp; chớp chớp mắt v&agrave; vẫn c&ograve;n kh&aacute; bối rối.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Tại sao Trưởng tộc muốn giết ch&uacute;ng ta?&quot; Quan l&atilde;o đại nhịn cười hỏi ngược lại.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Bởi v&igrave; thiếu gia đ&atilde; chết trong khi ch&uacute;ng ta phải bảo vệ cậu ấy.&quot; Cổ l&atilde;o tam tr&ocirc;ng c&agrave;ng bối rối hơn.</span></span><br />\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Hehe, nhưng thiếu gia kh&ocirc;ng ở đ&acirc;y sao !?&quot; Quan l&atilde;o đại vừa n&oacute;i vừa chỉ v&agrave;o đứa nh&oacute;c trước mặt m&agrave; chế nhạo.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;C&aacute;i g&igrave;, ngươi định&hellip;&rdquo; Cổ l&atilde;o tam kh&ocirc;ng hề ngu ngốc m&agrave; ngay lập tức nhận ra &yacute; đồ của Quan l&atilde;o đại. Tuy nhi&ecirc;n, sắc mặt của hắn trở n&ecirc;n nhợt nhạt hơn trước rất nhiều.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ch&iacute;nh x&aacute;c. Ch&uacute;ng ta sẽ đưa đứa trẻ n&agrave;y đến ph&aacute;i D&atilde; Quỷ v&agrave; để n&oacute; cho người phụ tr&aacute;ch thu thập những thứ sẽ tiến h&agrave;nh buổi lễ. Sau đ&oacute;, ch&uacute;ng ta c&oacute; thể quay lại gia tộc Bạch thị v&agrave; n&oacute;i rằng ch&uacute;ng ta đ&atilde; cử người đến đ&oacute;. Một khi Trưởng Tộc nhận được một bức thư n&oacute;i rằng thiếu gia thực sự đ&atilde; được gửi đến M&ocirc;n ph&aacute;i, ch&uacute;ng ta sẽ kh&ocirc;ng bị trừng phạt v&agrave; thậm ch&iacute; ch&uacute;ng ta c&oacute; thể được thưởng. &rdquo; Quan l&atilde;o đại v&ocirc; c&ugrave;ng b&igrave;nh tĩnh khi n&oacute;i ra những lời đ&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Điều đ&oacute; c&oacute; hiệu quả, nhưng đứa trẻ n&agrave;y tr&ocirc;ng kh&ocirc;ng qu&aacute; giống thiếu gia. Điều g&igrave; sẽ xảy ra nếu cậu ta bị lộ? Ngo&agrave;i ra, ch&uacute;ng ta thậm ch&iacute; kh&ocirc;ng biết cậu ta đến từ đ&acirc;u! L&agrave;m sao ch&uacute;ng ta biết cậu ấy sẽ đồng &yacute; với điều n&agrave;y? &quot; Khu&ocirc;n mặt của Cổ l&atilde;o tam tối sầm lại khi nghĩ về khả năng thất bại.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Hừm, ngươi sợ c&aacute;i g&igrave;? Một khi đứa trẻ n&agrave;y gia nhập ph&aacute;i Dạ Quỷ, sẽ mất v&agrave;i năm trước khi gia tộc Bạch thị gặp lại n&oacute;. Th&ecirc;m v&agrave;o đ&oacute;, chứng kiến Lễ Khai Ấn nguy hiểm như thế n&agrave;o, cậu nh&oacute;c n&agrave;y thậm ch&iacute; kh&ocirc;ng c&oacute; cơ hội sống s&oacute;t v&igrave; cậu ta chưa từng trải qua bất kỳ kh&oacute;a đ&agrave;o tạo chuẩn bị n&agrave;o. Nếu cậu ấy chết, ch&uacute;ng ta về cơ bản được tự do v&agrave; kh&ocirc;ng phải lo lắng. Đối với ngoại h&igrave;nh của cậu ấy, ngươi đ&atilde; qu&ecirc;n những g&igrave; ngươi&nbsp; đ&atilde; l&agrave;m trước khi trở th&agrave;nh một m&ocirc;n đồ?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Cậu ấy kh&ocirc;ng cần phải c&oacute; vẻ ngo&agrave;i ho&agrave;n hảo như thiếu gia, chỉ cần giống nhau l&agrave; đủ. M&ocirc;n ph&aacute;i chỉ c&oacute; h&igrave;nh ảnh của thiếu gia từ một năm trước, c&oacute; thay đổi g&igrave; đ&oacute; l&agrave; chuyện b&igrave;nh thường. &rdquo; Quan L&atilde;o Đại tự tin n&oacute;i.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Nếu hắn thực sự ho&agrave;n th&agrave;nh Lễ Khai Ấn v&agrave; trở th&agrave;nh một Linh đồ, th&igrave; ch&uacute;ng ta n&ecirc;n l&agrave;m g&igrave;?&rdquo; Cổ l&atilde;o tam vẫn c&ograve;n do dự.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-0cfc088d-7fff-b9d3-51d8-7b1f02b89f6d\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Trở th&agrave;nh Tinh linh đồ? Ngươi vẫn c&ograve;n&nbsp; mơ ngủ &agrave;? Trong những năm qua, chỉ c&oacute; một số đệ tử trở th&agrave;nh Linh đồ sứ. Ngay sau khi ch&uacute;ng ta quay lại, ch&uacute;ng ta sẽ cố gắng loại bỏ chất độc m&agrave; Trưởng tộc đ&atilde; cho ch&uacute;ng ta v&agrave; cố gắng rời khỏi tộc c&ugrave;ng với gia đ&igrave;nh của ch&uacute;ng ta. Ngay cả khi đứa trẻ đ&oacute; kh&ocirc;ng chết v&agrave; l&agrave;m điều g&igrave; đ&oacute;, điều đ&oacute; c&oacute; li&ecirc;n quan g&igrave; đến ch&uacute;ng ta? Miễn l&agrave; b&acirc;y giờ ch&uacute;ng ta c&oacute; thể giữ được mạng, mọi thứ kh&aacute;c c&oacute; thể b&agrave;n bạc sau. Đối với việc thuyết phục đứa trẻ n&agrave;y, ch&uacute;ng ta sẽ l&agrave;m điều n&agrave;y&hellip; &rdquo;Quan l&atilde;o đại nghi&ecirc;ng người gần hơn với Cổ l&atilde;o tam v&agrave; th&igrave; thầm những lời kh&ocirc;ng thể nghe r&otilde; được với hắn ta.</span></span></p>\r\n<div>\r\n	&nbsp;</div>\r\n', 'Phê Kun', '2020-10-19 02:00:12', 1, 0, 4);
INSERT INTO `chapters` (`id`, `name`, `slug`, `site_title`, `meta_desc`, `meta_key`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(200, 'Chương 3: Lão bá bí ẩn (Phần 1)', 'chuong-3-lao-ba-bi-an-phan-1', NULL, NULL, NULL, 1, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bầu trời đ&atilde; trở n&ecirc;n tối sầm. V&igrave; hang động qu&aacute; s&acirc;u dưới l&ograve;ng đất, Dương Định Thi&ecirc;n nh&igrave;n l&ecirc;n v&agrave; chỉ nh&igrave;n thấy một mảnh bầu trời c&oacute; k&iacute;ch thước bằng l&ograve;ng b&agrave;n tay.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave;o l&uacute;c n&agrave;y, mặt trăng đ&atilde; l&ecirc;n đỉnh bầu trời v&agrave; đi qua bầu trời tr&ecirc;n đầu của Dương Định Thi&ecirc;n. Tuy nhi&ecirc;n, hắn vẫn nh&igrave;n thấy r&otilde; r&agrave;ng hai mặt trăng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&oacute; một tr&iacute;ch dẫn được viết bởi T&ocirc; Thức rằng tuy&ecirc;n bố &ldquo;Ước c&oacute; cuộc sống l&acirc;u d&agrave;i, mặc d&ugrave; c&ograve;n xa ng&agrave;n dặm&rdquo; Thật đ&aacute;ng buồn l&agrave; ngay cả mặt trăng m&agrave; Dương Định Thi&ecirc;n đang nh&igrave;n cũng kh&aacute;c với mặt trăng m&agrave; cha mẹ hắn sẽ nh&igrave;n. Hơn nữa, thậm ch&iacute; c&ograve;n c&oacute; hai mặt trăng ở đ&acirc;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">(Lưu &yacute;: T&ocirc; thức l&agrave; một nh&agrave; văn nổi tiếng của Trung Quốc.)</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n mệt mỏi v&agrave; buồn bực. Hắn nh&igrave;n l&atilde;o gi&agrave; đang ngồi c&aacute;ch đ&oacute; kh&ocirc;ng xa. &Ocirc;ng vẫn ngồi ở vị tr&iacute; cũ, mắt nhắm nghiền bất động.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong t&igrave;nh huống n&agrave;y, kh&ocirc;ng c&ograve;n g&igrave; phải sợ nữa. Dương Định Thi&ecirc;n từ từ nhắm mắt ch&igrave;m v&agrave;o giấc ngủ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">H&agrave;ng chục giờ sau, hắn tỉnh dậy.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n đ&atilde; muốn đứng dậy v&agrave; tiếp tục t&igrave;m c&aacute;ch tho&aacute;t khỏi hang động. Tuy nhi&ecirc;n, hắn nhận ra rằng m&igrave;nh ho&agrave;n to&agrave;n kh&ocirc;ng thể đứng dậy được. V&agrave;i ng&agrave;y li&ecirc;n tục bị bỏ đ&oacute;i đ&atilde; r&uacute;t hết năng lượng của hắn. Tương tự như vậy, năng lượng nhiệt t&iacute;ch trữ trong cơ thể hắn ta bao năm nay đ&atilde; được sử dụng hết để giải cứu l&atilde;o gi&agrave; khỏi khối băng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong mấy ng&agrave;y n&agrave;y, Dương Định Thi&ecirc;n chỉ uống v&agrave;i ngụm nước đ&aacute; tan. Hắn đ&atilde; kh&ocirc;ng ăn d&ugrave; chỉ một ch&uacute;t thức ăn sau khi mất rất nhiều m&aacute;u. Mặc d&ugrave; hắn muốn đứng dậy ngay b&acirc;y giờ, nhưng hắn ho&agrave;n to&agrave;n kh&ocirc;ng c&ograve;n sức lực để l&agrave;m việc đ&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&uacute;c n&agrave;y, bầu trời ngo&agrave;i kia u &aacute;m v&agrave; đầy m&acirc;y đen v&igrave; mặt trời đ&atilde; lặn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chẳng bao l&acirc;u, trời bắt đầu c&oacute; tuyết, v&agrave; những quả cầu tuyết to bằng nắm tay tr&uacute;t xuống dữ dội từ tr&ecirc;n trời. Sau một thời gian, nền hang bị bao phủ bởi một lớp tuyết d&agrave;y. To&agrave;n bộ cơ thể của &ocirc;ng gi&agrave; cũng dần dần bị tuyết v&ugrave;i lấp. Một giờ sau, &ocirc;ng đ&atilde; trở th&agrave;nh người tuyết, to&agrave;n bộ cơ thể kh&ocirc;ng c&ograve;n nh&igrave;n thấy nữa.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi lớp tuyết phủ tr&ecirc;n người Dương Định Thi&ecirc;n dần dần d&agrave;y l&ecirc;n, th&acirc;n h&igrave;nh bốc lửa của hắn đ&atilde; sớm kh&ocirc;ng c&ograve;n khả năng l&agrave;m tuyết tan nữa. Để tr&aacute;nh bị ch&ocirc;n v&ugrave;i, Dương Định Thi&ecirc;n lắc đầu. Tuy nhi&ecirc;n, một l&uacute;c sau, hắn lại bị tuyết bao phủ, v&agrave; hắn lại phải lắc đầu.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một lần, hai lần, ba lần&hellip;.Cuối c&ugrave;ng, Dương Định Thi&ecirc;n kh&ocirc;ng c&ograve;n sức lực, thậm ch&iacute; lắc đầu. Dấu hiệu nhiệt năng cuối c&ugrave;ng trong cơ thể hắn cũng đ&atilde; bị ti&ecirc;u hao, v&agrave; hắn chỉ c&oacute; thể nh&igrave;n tuyết bao phủ v&agrave; dần dần lấy đi mạng sống của m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đột nhi&ecirc;n, lớp tuyết c&aacute;ch đ&oacute; kh&ocirc;ng xa rung l&ecirc;n. L&atilde;o gi&agrave; cuối c&ugrave;ng đ&atilde; chuyển động. &Ocirc;ng ta th&ograve; tay v&agrave;o trong tay &aacute;o v&agrave; l&ocirc;i ra một thứ g&igrave; đ&oacute; m&agrave;u đỏ v&agrave; n&eacute;m n&oacute; về ph&iacute;a Dương Định Thi&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&oacute; l&agrave; một vi&ecirc;n thuốc m&agrave;u đỏ. Vi&ecirc;n thuốc rơi xuống ngay một đống tuyết v&agrave; ngay lập tức tan chảy. Tr&aacute;i ngược với lớp tuyết trắng sạch phủ tr&ecirc;n nền hang, vi&ecirc;n thuốc m&agrave;u đỏ nổi bật s&aacute;ng rực như ngọn lửa.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave;o l&uacute;c n&agrave;y, &yacute; ch&iacute; sống s&oacute;t của Dương Định Thi&ecirc;n đ&atilde; được tiếp th&ecirc;m sức mạnh cho hắn ta. Hắn lao tới v&agrave; h&aacute; miệng ăn vi&ecirc;n thuốc m&agrave;u đỏ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kh&ocirc;ng biết liệu n&oacute; c&oacute; độc kh&ocirc;ng? Về hậu quả của việc ăn n&oacute; l&agrave; g&igrave;? Dương Định Thi&ecirc;n kh&ocirc;ng c&ograve;n quan t&acirc;m nữa.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mặc d&ugrave; vi&ecirc;n thuốc vừa được đặt v&agrave;o miệng hắn, cảm gi&aacute;c như c&oacute; một ngọn lửa cuồng nộ đ&atilde; tan trong miệng hắn, chảy v&agrave;o bụng v&agrave; lan qua tất cả c&aacute;c tĩnh mạch trong cơ thể hắn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay lập tức, hắn cảm thấy ấm &aacute;p như được ch&agrave;o đ&oacute;n m&ugrave;a xu&acirc;n trở lại. Cơ thể cứng ngắc của Dương Định Thi&ecirc;n bắt đầu phục hồi từng ch&uacute;t một.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chẳng bao l&acirc;u, to&agrave;n bộ cơ thể của hắn đ&atilde; hồi phục. Cuối c&ugrave;ng th&igrave; hắn cũng đ&atilde; kh&ocirc;i phục lại sức sống của m&igrave;nh. Hơn thế nữa, thậm ch&iacute; c&ograve;n c&oacute; một lực mạnh lao qua cơ thể hắn, cố gắng b&ugrave;ng ph&aacute;t một c&aacute;ch tuyệt vọng. Dương Định Thi&ecirc;n kh&ocirc;ng cảm thấy g&igrave; ngo&agrave;i nguồn năng lượng v&ocirc; tận b&ecirc;n trong hắn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Đ&acirc;y l&agrave; thứ g&igrave;? L&agrave;m thế n&agrave;o m&agrave; n&oacute; thần kỳ đến vậy? &rdquo; Dương Định Thi&ecirc;n vừa ngạc nhi&ecirc;n vừa vui mừng. Hắn ta đi về ph&iacute;a l&atilde;o gi&agrave; , c&uacute;i đầu v&agrave; n&oacute;i, &quot;Cảm ơn &ocirc;ng!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Nhưng, cơ thể của &ocirc;ng cũng kh&ocirc;ng c&ograve;n chịu được lạnh nữa. Tại sao &ocirc;ng kh&ocirc;ng uống một trong những vi&ecirc;n thuốc đ&oacute;? &quot; Dương Định Thi&ecirc;n hỏi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&atilde;o b&aacute; vẫn ngồi thẳng lưng, nhắm mắt. &Ocirc;ng ta phớt lờ người thanh ni&ecirc;n v&agrave; để tuyết bao phủ &ocirc;ng ta, biến th&agrave;nh người tuyết một lần nữa.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau đ&oacute;, bất kể Dương Định Thi&ecirc;n n&oacute;i g&igrave;, &ocirc;ng ta kh&ocirc;ng bao giờ đ&aacute;p lại.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n giờ đ&acirc;y tr&agrave;n đầy nhiệt năng v&agrave; kh&ocirc;ng biết phải l&agrave;m g&igrave;. Hang động qu&aacute; lớn, v&igrave; vậy hắn cần phải nhảy m&uacute;a trong tuyết d&agrave;y v&agrave; vẫy tay trong kh&ocirc;ng kh&iacute;. Nếu kh&ocirc;ng sẽ bị đ&ocirc;ng cứng trong t&iacute;t tắt.</span></span></p>\r\n<p>\r\n	<br />\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuyết c&agrave;ng l&uacute;c c&agrave;ng nặng hạt. Chỉ trong nửa ng&agrave;y, tuyết đ&atilde; chồng chất cao hơn khoảng hai m&eacute;t v&agrave; li&ecirc;n tục đe dọa sẽ ch&ocirc;n v&ugrave;i ho&agrave;n to&agrave;n &ocirc;ng l&atilde;o. V&igrave; trường hợp n&agrave;y xảy ra, cứ nửa tiếng, Dương Định Thi&ecirc;n sẽ đến phủi tuyết cho l&atilde;o gi&agrave; kia một lần.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Tuyết qu&aacute; d&agrave;y v&agrave; chồng chất qu&aacute; nhanh. T&ocirc;i sợ rằng chỉ sau một v&agrave;i năm, to&agrave;n bộ hang động sẽ bị lấp đầy ho&agrave;n to&agrave;n, &rdquo;Dương Định Thi&ecirc;n nghĩ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đột nhi&ecirc;n, hắn ta trở n&ecirc;n cực kỳ phấn kh&iacute;ch, chạy đến với l&atilde;o gi&agrave; v&agrave; n&oacute;i: &ldquo;L&atilde;o b&aacute;, l&atilde;o b&aacute;, t&ocirc;i đ&atilde; t&igrave;m ra c&aacute;ch để tho&aacute;t khỏi đ&acirc;y. T&ocirc;i đ&atilde; t&igrave;m ra c&aacute;ch để tho&aacute;t khỏi đ&acirc;y. &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, vẫn kh&ocirc;ng c&oacute; phản hồi từ &ocirc;ng l&atilde;o.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tr&ecirc;n thực tế, Dương Định Thi&ecirc;n đ&atilde; nghĩ ra c&aacute;ch để ra khỏi hang. Hắn ta nghĩ rằng hắn ta c&oacute; thể sử dụng tuyết để x&acirc;y dựng c&aacute;c bậc thang bằng c&aacute;ch d&aacute;n n&oacute; v&agrave;o c&aacute;c bức tường của hang động. V&igrave; hang s&acirc;u khoảng v&agrave;i km, hắn ta sẽ cần x&acirc;y khoảng mười ngh&igrave;n bậc thang để l&ecirc;n đến đỉnh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n bắt đầu &eacute;p tuyết th&agrave;nh những vi&ecirc;n gạch tuyết d&agrave;i một b&agrave;n ch&acirc;n v&agrave; rộng nửa b&agrave;n ch&acirc;n. Sau đ&oacute;, hắn ta ấn những vi&ecirc;n gạch tuyết v&agrave;o một trong những bức tường của hang động băng. Sau đ&oacute;, hắn ta d&ugrave;ng ch&iacute;nh cơ thể của m&igrave;nh để l&agrave;m tan chảy những vi&ecirc;n gạch tuyết th&agrave;nh nước, sau đ&oacute; sẽ đ&ocirc;ng lại th&agrave;nh băng. Mỗi vi&ecirc;n gạch tuyết sẽ biến th&agrave;nh một vi&ecirc;n gạch băng nhỏ gắn chặt v&agrave;o tường.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi trải qua đ&uacute;ng một giờ, Dương Định Thi&ecirc;n đ&atilde; chế tạo th&agrave;nh c&ocirc;ng bậc thang đầu ti&ecirc;n, n&oacute; nh&ocirc; l&ecirc;n cao nửa m&eacute;t so với mặt đất. Dương Định Thi&ecirc;n đ&atilde; leo l&ecirc;n n&oacute;. Đ&uacute;ng như anh dự đo&aacute;n, n&oacute; c&oacute; thể chịu được sức nặng của một người m&agrave; kh&ocirc;ng bị vỡ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau đ&oacute;, Dương Định Thi&ecirc;n bắt đầu thực hiện bước thứ hai. Bước thứ hai được đặt cao hơn ba mươi cm v&agrave; ba mươi cm trước bước thứ nhất. C&aacute;c bậc thang sẽ được sắp xếp để tạo độ dốc để l&ecirc;n đến đỉnh. Nếu anh ta x&acirc;y dựng n&oacute; theo phương thẳng đứng, th&igrave; sẽ kh&ocirc;ng thể đi tr&ecirc;n đ&oacute; được.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mười giờ sau, Dương Định Thi&ecirc;n chỉ thực hiện được 10 bước băng d&ugrave; hắn đ&atilde; nỗ lực rất nhiều. Hắn mệt đến mức chỉ ngồi xuống v&agrave; ngủ thiếp đi. Sau khi tỉnh dậy sau đ&oacute;,hắn trở lại l&agrave;m việc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mười ng&agrave;y tr&ocirc;i qua. Dương Định Thi&ecirc;n đ&atilde; x&acirc;y dựng hơn 120 bậc thang băng. Bậc thang cao nhất chỉ l&ecirc;n tới 40 m&eacute;t so với nền hang. Trong mười ng&agrave;y n&agrave;y, &ocirc;ng l&atilde;o vẫn tiếp tục ngồi im v&agrave; nhắm mắt kh&ocirc;ng thốt l&ecirc;n lời n&agrave;o. Ngược lại, Dương Định Thi&ecirc;n đ&atilde; d&agrave;nh từng ph&uacute;t thức giấc để x&acirc;y c&aacute;c bậc thang tr&ecirc;n băng cho đến khi kiệt sức. Sau đ&oacute;, hắn sẽ nằm tr&ecirc;n tuyết, ngủ v&agrave; tiếp tục khi thức dậy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, sau một thời gian, Dương Định Thi&ecirc;n kh&ocirc;ng thể tiếp tục được nữa. Tuyết đ&atilde; ngừng rơi một ng&agrave;y sau khi hắn bắt đầu, v&agrave; 120 bậc thang băng m&agrave; hắn x&acirc;y dựng cho đến nay đ&atilde; sử dụng hết tuyết trong hang. Nếu hắn muốn tiếp tục thực hiện c&aacute;c bước, hắn sẽ phải đợi trận tuyết rơi tiếp theo.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&ecirc;n cạnh đ&oacute;, năng lượng m&agrave; Dương Định Thi&ecirc;n nhận được từ vi&ecirc;n thuốc đ&atilde; được sử dụng hết. Cơ thể hắn bắt đầu cảm thấy lạnh trở lại v&igrave; hắn kh&ocirc;ng c&ograve;n một ch&uacute;t năng lượng n&agrave;o nữa.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n nh&igrave;n l&ecirc;n bầu trời, cầu mong bầu trời u &aacute;m v&agrave; tuyết rơi như chưa từng c&oacute;. Thật kh&ocirc;ng may, đ&oacute; l&agrave; một ng&agrave;y nắng v&agrave; thậm ch&iacute; kh&ocirc;ng c&oacute; một đ&aacute;m m&acirc;y n&agrave;o tr&ecirc;n bầu trời.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&Ocirc;ng l&atilde;o đột nhi&ecirc;n mở mắt lần đầu ti&ecirc;n trong mười ng&agrave;y qua. &Ocirc;ng liếc nh&igrave;n những bước đi của Dương Định Thi&ecirc;n.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n vỡ &ograve;a v&igrave; phấn kh&iacute;ch. Nh&igrave;n những bậc thang băng do ch&iacute;nh tay m&igrave;nh x&acirc;y dựng, hắn x&uacute;c động n&oacute;i: &ldquo;Nh&igrave;n k&igrave;a, &ocirc;ng gi&agrave;! Đ&acirc;y l&agrave; cầu thang m&agrave; t&ocirc;i đ&atilde; x&acirc;y dựng. Ch&uacute;ng ta sẽ c&oacute; thể tho&aacute;t khỏi đ&acirc;y sớm. Đừng lo lắng. Cầu thang của t&ocirc;i rất chắc chắn v&agrave; t&ocirc;i sẽ mang &ocirc;ng theo đi bằng c&aacute;ch sử dụng ch&uacute;ng. &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cũng như trước đ&oacute;, khu&ocirc;n mặt của &ocirc;ng l&atilde;o v&ocirc; cảm, kh&ocirc;ng c&oacute; bất kỳ dấu hiệu t&aacute;n thưởng n&agrave;o. Sau đ&oacute; &ocirc;ng ấy nhắm mắt lại một lần nữa như thể &ocirc;ng ấy kh&ocirc;ng nh&igrave;n thấy g&igrave; cả. Tuy nhi&ecirc;n, &ocirc;ng ta lấy một vi&ecirc;n thuốc ra khỏi tay &aacute;o v&agrave; n&eacute;m cho Dương Định Thi&ecirc;n.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&oacute; l&agrave; c&ugrave;ng một vi&ecirc;n thuốc m&agrave;u đỏ rực. Dương Định Thi&ecirc;n nhanh ch&oacute;ng nhặt n&oacute; l&ecirc;n v&agrave; cho v&agrave;o miệng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đột nhi&ecirc;n, to&agrave;n bộ cơ thể hắn bắt đầu cảm thấy cảm gi&aacute;c bỏng r&aacute;t một lần nữa lấp đầy hắn với nguồn năng lượng v&ocirc; tận. Rất muốn h&eacute;t to l&ecirc;n, nhưng lại sợ m&igrave;nh chọc tức l&atilde;o nh&acirc;n gia. V&igrave; vậy, hắn ta chỉ đơn giản l&agrave; d&ugrave;ng nắm đấm của m&igrave;nh để giải ph&oacute;ng to&agrave;n bộ năng lượng trong cơ thể.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Vi&ecirc;n thuốc thật tuyệt vời. Một điều kỳ diệu như vậy kh&ocirc;ng nơi n&agrave;o tr&ecirc;n Tr&aacute;i đất c&oacute; được. Đ&aacute;ng lẽ n&oacute; phải v&ocirc; c&ugrave;ng qu&yacute; gi&aacute;, c&oacute; lẽ trị gi&aacute; tr&ecirc;n một vạn lượng v&agrave;ng. Với vi&ecirc;n thuốc n&agrave;y, Dương Định Thi&ecirc;n đ&atilde; tr&agrave;n đầy năng lượng ngay cả khi kh&ocirc;ng ti&ecirc;u thụ thức ăn trong h&agrave;ng chục ng&agrave;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mặc d&ugrave; vi&ecirc;n thuốc rất qu&yacute; gi&aacute;, nhưng Dương Định Thi&ecirc;n thậm ch&iacute; kh&ocirc;ng c&oacute; một ch&uacute;t &yacute; định lấy trộm n&oacute;. Sau khi uống vi&ecirc;n thuốc, Dương Định Thi&ecirc;n c&oacute; rất nhiều năng lượng trong người khiến hắn kh&ocirc;ng thể tr&uacute;t hết ra ngo&agrave;i. B&acirc;y giờ hắn khao kh&aacute;t c&oacute; th&ecirc;m tuyết. Ngay khi tuyết rơi, hắn ta sẽ c&oacute; thể tiếp tục x&acirc;y cầu thang v&agrave; rời khỏi nơi kinh ho&agrave;ng n&agrave;y nhanh hơn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cuối c&ugrave;ng, dường như Ch&uacute;a đ&atilde; nghe thấy lời cầu nguyện của Dương Định Thi&ecirc;n hoặc đ&oacute; c&oacute; thể chỉ l&agrave; một sự tr&ugrave;ng hợp ngẫu nhi&ecirc;n của m&ugrave;a đ&ocirc;ng. V&agrave;o ng&agrave;y thứ năm sau khi Dương Định Thi&ecirc;n uống thuốc, trời bắt đầu c&oacute; tuyết. Mặc d&ugrave; trận tuyết rơi kh&ocirc;ng lớn như trận trước nhưng cũng kh&ocirc;ng nhỏ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n bắt đầu h&eacute;t l&ecirc;n v&igrave; phấn kh&iacute;ch.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn ta bắt đầu l&agrave;m gạch tuyết m&agrave; kh&ocirc;ng cần đợi tuyết chất th&agrave;nh đống.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đột nhi&ecirc;n, cơ thể của &ocirc;ng l&atilde;o di chuyển một ch&uacute;t, v&agrave; tuyết tr&ecirc;n người &ocirc;ng ấy rơi ra. &Ocirc;ng gi&agrave; mở mắt v&agrave; vẫy tay với Dương Định Thi&ecirc;n. Sau đ&oacute; &ocirc;ng ta mở miệng v&agrave; n&oacute;i điều g&igrave; đ&oacute;. Mặc d&ugrave; Dương Định Thi&ecirc;n vẫn kh&ocirc;ng thể hiểu được những từ đ&oacute;, nhưng bằng c&aacute;ch n&agrave;o đ&oacute; hắn&nbsp; biết rằng ch&uacute;ng c&oacute; nghĩa l&agrave; &ldquo;cứ tiếp tục đi&rdquo;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n vội v&agrave;ng chạy tới v&agrave; n&oacute;i. &ldquo;Nh&igrave;n k&igrave;a, &ocirc;ng gi&agrave;! Lại c&oacute; tuyết rơi. Ch&uacute;ng ta c&oacute; thể rời khỏi nơi n&agrave;y ngay lập tức. &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&Ocirc;ng l&atilde;o khẽ cười. Đ&acirc;y l&agrave; lần đầu ti&ecirc;n trong nửa th&aacute;ng qua, &ocirc;ng l&atilde;o mỉm cười.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau đ&oacute;, &ocirc;ng l&atilde;o d&ugrave;ng ng&oacute;n tay viết g&igrave; đ&oacute; l&ecirc;n tuyết. Đ&oacute; l&agrave; một từ m&agrave; Dương Định Thi&ecirc;n kh&ocirc;ng biết. Sau đ&oacute;, &ocirc;ng gi&agrave; đọc n&oacute; th&agrave;nh tiếng v&agrave; chỉ v&agrave;o m&igrave;nh.Dương Định Thi&ecirc;n kh&ocirc;ng thể k&igrave;m được sự phấn kh&iacute;ch của m&igrave;nh v&agrave; hỏi: &quot;&Ocirc;ng gi&agrave;, &ocirc;ng c&oacute; thể dạy t&ocirc;i những từ v&agrave; ng&ocirc;n ngữ được sử dụng tr&ecirc;n thế giới n&agrave;y kh&ocirc;ng?&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&Ocirc;ng l&atilde;o kh&ocirc;ng hiểu Dương Định Thi&ecirc;n đang n&oacute;i g&igrave;. Hắn chỉ v&agrave;o từ tr&ecirc;n tuyết v&agrave; đọc lại th&agrave;nh tiếng. Sau đ&oacute;, hắn lại chỉ v&agrave;o m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Từ &ocirc;ng đ&atilde; viết c&oacute; nghĩa l&agrave;&quot; T&ocirc;i &quot;phải kh&ocirc;ng?&quot; Dương Định Thi&ecirc;n hỏi. Sau đ&oacute;, hắn viết từ &ldquo;T&ocirc;i&rdquo; tr&ecirc;n tuyết, đọc to v&agrave; chỉ v&agrave;o m&igrave;nh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;T&ocirc;i&hellip;&rdquo; &Ocirc;ng l&atilde;o chăm ch&uacute; nghe v&agrave; ph&aacute;t &acirc;m ch&iacute;nh x&aacute;c một c&aacute;ch đ&aacute;ng ngạc nhi&ecirc;n. Sau đ&oacute;, &ocirc;ng ta chỉ v&agrave;o chữ viết tr&ecirc;n tuyết v&agrave; sau đ&oacute; chỉ v&agrave;o ch&iacute;nh m&igrave;nh. Cuối c&ugrave;ng, &ocirc;ng ấy d&ugrave;ng ng&oacute;n tay của m&igrave;nh để viết từ &ldquo;T&ocirc;i&rdquo;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n bị sốc đến mức kh&ocirc;ng n&oacute;i n&ecirc;n lời. &Ocirc;ng gi&agrave; n&agrave;y qu&aacute; tuyệt vời. &Ocirc;ng ấy chỉ nghe v&agrave; nh&igrave;n n&oacute; một lần nhưng đ&atilde; c&oacute; thể đọc v&agrave; viết từ. Chữ viết của Dương Định Thi&ecirc;n được coi l&agrave; tử tế, nhưng hắn kh&ocirc;ng ngờ rằng chữ do &ocirc;ng l&atilde;o viết lần đầu ti&ecirc;n tr&ocirc;ng c&ograve;n đẹp hơn chữ của m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau đ&oacute;, &ocirc;ng l&atilde;o viết chữ m&agrave; trước đ&oacute; &ocirc;ng đ&atilde; viết l&ecirc;n tuyết rồi mới đọc v&agrave; chỉ v&agrave;o m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Dương Định Thi&ecirc;n vội v&agrave;ng đọc từ v&agrave; sau đ&oacute; viết lại từ đ&oacute; trong tuyết. Đ&acirc;y l&agrave; từ &quot;t&ocirc;i&quot; trong thế giới n&agrave;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-b789c54f-7fff-ff18-568f-abe9cb7d5bcb\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&aacute;ch ph&aacute;t &acirc;m v&agrave; viết của Dương Định Thi&ecirc;n kh&aacute; ch&iacute;nh x&aacute;c. &Ocirc;ng l&atilde;o mỉm cười v&agrave; gật đầu. Sau đ&oacute;, &ocirc;ng ấy viết một từ kh&aacute;c l&ecirc;n tuyết, đọc to v&agrave; chỉ v&agrave;o Dương Định Thi&ecirc;n. Lần n&agrave;y, những g&igrave; &ocirc;ng ấy viết n&ecirc;n l&agrave; từ &ldquo;bạn&rdquo; của thế giới n&agrave;y.</span></span></p>\r\n', 'Cà Di', '2020-10-19 02:28:53', 1, 0, 5);
INSERT INTO `chapters` (`id`, `name`, `slug`, `site_title`, `meta_desc`, `meta_key`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(201, 'Chương 4: Tỉnh dậy', 'chuong-4-tinh-day', NULL, NULL, NULL, 41, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4c1c4f82-7fff-e859-1c38-01781967066b\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&uacute;c Thẩm Ngọc tỉnh lại mặt trời đ&atilde; l&ecirc;n cao, Trấn Bắc Vương đ&atilde; sớm rời đi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4c1c4f82-7fff-e859-1c38-01781967066b\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cả người Thẩm Ngọc đau nhức, xương to&agrave;n th&acirc;n giống như rời ra, tay tr&aacute;i của y, bởi v&igrave; bị Trấn Bắc Vương nắm ở sau lưng, đ&atilde; bị trẹo. Hạ th&acirc;n sưng đỏ, Thẩm Ngọc muốn kiểm tra một ch&uacute;t, mới vừa đụng v&agrave;o liền đau giống như bị kim ch&acirc;m vậy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4c1c4f82-7fff-e859-1c38-01781967066b\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một đ&ecirc;m n&agrave;y, gần như muốn nửa c&aacute;i mạng của y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4c1c4f82-7fff-e859-1c38-01781967066b\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thời điểm y đau đến kh&ocirc;ng chịu được, chỉ c&oacute; thể rơi lệ, y thực sự đau, ở trong mắt Trấn Bắc Vương lại l&agrave; l&ecirc; hoa đ&aacute;i vũ, chỉ khiến hắn gi&agrave;y v&ograve; th&ecirc;m m&atilde;nh liệt hơn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4c1c4f82-7fff-e859-1c38-01781967066b\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau nửa đ&ecirc;m, Thẩm Ngọc từ ngất xỉu tỉnh lại, y nhớ kh&ocirc;ng r&otilde; lắm, l&uacute;c th&igrave; v&ocirc; tri v&ocirc; gi&aacute;c l&uacute;c th&igrave; lại thanh tỉnh, cảm nhận thấy Trấn Bắc Vương vẫn c&ograve;n ở tr&ecirc;n người y c&agrave;y cấy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4c1c4f82-7fff-e859-1c38-01781967066b\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">So với th&acirc;n thể đau đớn, &aacute;nh mắt kh&ocirc; khốc chỉ l&agrave; nhỏ nhặt kh&ocirc;ng c&oacute; g&igrave; đ&aacute;ng kể.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4c1c4f82-7fff-e859-1c38-01781967066b\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Run rẩy ngồi dậy, Thẩm Ngọc nh&igrave;n lướt qua liền thấy được tr&ecirc;n đệm vết trắng mờ &aacute;m, ở tr&ecirc;n bắp đ&ugrave;i y vẫn c&ograve;n một mảng vết nhầy, d&iacute;nh ướt nhẹp.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4c1c4f82-7fff-e859-1c38-01781967066b\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thẩm Ngọc muốn leo xuống giường, kết quả bởi v&igrave; th&acirc;n thể yếu ớt m&agrave; ng&atilde; xuống, hai ch&acirc;n y giống như bị r&uacute;t đi hết kh&iacute; lực, đứng kh&ocirc;ng vững, phải vịn m&eacute;p giường đi tới b&agrave;n trang điểm.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4c1c4f82-7fff-e859-1c38-01781967066b\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ở trong chậu đồng c&oacute; nước sạch, Thẩm Ngọc thấm ướt khăn, lau ch&ugrave;i tay với mặt, tỉ mỉ lau sạch vết bẩn tr&ecirc;n người, mới bắt đầu mặc y phục v&agrave; đeo gi&agrave;y v&agrave;o, tr&ecirc;n người đều l&agrave; đau đớn mỗi lần động liền khiến Thẩm Ngọc h&iacute;t v&agrave;o một hơi kh&iacute; lạnh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4c1c4f82-7fff-e859-1c38-01781967066b\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Cũng tốt, ma ma dạy ta những thứ kia, ta cũng kh&ocirc;ng phải d&ugrave;ng đến.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4c1c4f82-7fff-e859-1c38-01781967066b\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thẩm Ngọc tận lực mua vui, an ủi ch&iacute;nh m&igrave;nh, ở tr&ecirc;n giường Trấn Bắc Vương chủ động x&acirc;m chiếm, căn bản kh&ocirc;ng cần y hầu hạ, những thủ đoạn lấy l&ograve;ng nam nh&acirc;n kia cũng kh&ocirc;ng d&ugrave;ng được.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4c1c4f82-7fff-e859-1c38-01781967066b\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Huống chi Trấn Bắc Vương duy tr&igrave; m&atilde;nh liệt, cũng kh&ocirc;ng th&iacute;ch thủ đoạn dư thừa, Thẩm Ngọc tr&aacute;nh bị ph&aacute;t hiện một th&acirc;n nam nhi, ngược lại cũng coi l&agrave; c&oacute; một chỗ tốt. Y chẳng qua l&agrave; một thứ đồ chơi, bị đ&ugrave;a bỡn tr&ecirc;n l&ograve;ng b&agrave;n tay của Trấn Bắc Vương.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4c1c4f82-7fff-e859-1c38-01781967066b\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Thẩm phu nh&acirc;n, d&ugrave;ng đồ ăn s&aacute;ng xong cần phải qua Vương phi thỉnh an.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4c1c4f82-7fff-e859-1c38-01781967066b\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&oacute; người ở b&ecirc;n ngo&agrave;i nhẹ giọng nhắc nhở.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4c1c4f82-7fff-e859-1c38-01781967066b\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thẩm Ngọc vội vội v&agrave;ng v&agrave;ng mặc y phục ngay ngắn, bước v&agrave;o l&agrave; một n&ocirc; t&agrave;i trẻ trung tầm hai mươi tuổi, tr&ecirc;n người c&ograve;n mang theo đao, kh&ocirc;ng giống như l&agrave; một n&ocirc; t&agrave;i th&ocirc;ng thường.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4c1c4f82-7fff-e859-1c38-01781967066b\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;N&ocirc; t&agrave;i Tống Thanh, hầu hạ Thẩm phu nh&acirc;n d&ugrave;ng thuốc.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4c1c4f82-7fff-e859-1c38-01781967066b\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thẩm Ngọc ngồi y&ecirc;n kh&ocirc;ng nh&uacute;c nh&iacute;ch, khe khẽ chớp mắt một c&aacute;i.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4c1c4f82-7fff-e859-1c38-01781967066b\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;&Agrave;, nha ho&agrave;n phu nh&acirc;n mang theo, đều bị Vương phi sai đi l&agrave;m việc kh&aacute;c rồi, phu nh&acirc;n kh&ocirc;ng cần kinh ngạc, đ&acirc;y l&agrave; quy củ từ trước tới nay của Vương phủ.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4c1c4f82-7fff-e859-1c38-01781967066b\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kh&oacute; tr&aacute;ch s&aacute;ng sớm ra kh&ocirc;ng c&oacute; thị nữ hầu hạ, ngược lại thay v&agrave;o đ&oacute; l&agrave; một người nam n&ocirc; đi v&agrave;o.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4c1c4f82-7fff-e859-1c38-01781967066b\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thẩm Ngọc cầm lấy mấy lọ thuốc tr&ecirc;n khay, c&aacute;i n&agrave;o l&agrave; bột c&aacute;i n&agrave;o l&agrave; cao b&ocirc;i, y ph&acirc;n kh&ocirc;ng r&otilde;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4c1c4f82-7fff-e859-1c38-01781967066b\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Tống Thanh hầu hạ Vương gia mười mấy năm nay, Vương gia mỗi lần l&acirc;m hạnh một nữ nh&acirc;n, những thuốc n&agrave;y đều cần d&ugrave;ng đến, cho n&ecirc;n n&ocirc; t&agrave;i đ&atilde; tự m&igrave;nh chuẩn bị sẵn.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4c1c4f82-7fff-e859-1c38-01781967066b\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn kh&ocirc;ng chỉ đối với m&igrave;nh th&ocirc; bạo như vậy, động ph&ograve;ng xong đến cả n&ocirc; t&agrave;i cũng dưỡng th&agrave;nh th&oacute;i quen chuẩn bị thuốc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4c1c4f82-7fff-e859-1c38-01781967066b\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thẩm Ngọc kh&ocirc;ng mong chờ Trấn Bắc Vương thật l&ograve;ng đối đ&atilde;i với y, nhưng vẫn hi vọng Trấn Bắc Vương căn dặn Tống Thanh đưa thuốc tới, kết quả thực ra kh&ocirc;ng phải như vậy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-4c1c4f82-7fff-e859-1c38-01781967066b\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Y l&agrave; một thế th&acirc;n, một &aacute;ch n&ocirc; thay người gả, nơi n&agrave;o hi vọng đoạt được sự quan t&acirc;m của Trấn Bắc Vương? M&igrave;nh vốn cũng kh&ocirc;ng n&ecirc;n hi vọng xa vời nhiều như thế.</span></span></p>\r\n', 'Cà Di', '2020-10-19 03:16:52', 1, 0, 8);
INSERT INTO `chapters` (`id`, `name`, `slug`, `site_title`, `meta_desc`, `meta_key`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(202, 'Chương 3: Bách khoa toàn thư trong game', 'chuong-3-bach-khoa-toan-thu-trong-game', NULL, NULL, NULL, 10, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trước khi bắt đầu, Giang Phi quyết định trấn tĩnh lại tinh thần v&agrave; lấy lại b&igrave;nh tĩnh. Hắn ta h&iacute;t một hơi thật s&acirc;u v&agrave; mở cửa sổ trạng th&aacute;i của m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">T&ecirc;n: Verdure Glider</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cấp độ: 0</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lớp: Sơ cấp</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nghề nghiệp: Kh&ocirc;ng c&oacute;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Điểm kinh nghiệm: 0/100</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Điểm m&aacute;u: 110/110</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Điểm Mana: 100/100</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sức mạnh tấn c&ocirc;ng vật l&yacute;: 10 + 1</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sức mạnh tấn c&ocirc;ng ph&eacute;p thuật: 10</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ph&ograve;ng thủ vật l&yacute;: 5 + 1</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ph&ograve;ng thủ ph&eacute;p thuật: 5</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sức mạnh: 10 (X&aacute;c định Tấn c&ocirc;ng vật l&yacute; của nh&acirc;n vật. Lo&agrave;i người Tăng Tấn c&ocirc;ng vật l&yacute; 1.0, Tấn c&ocirc;ng vật l&yacute; d&agrave;nh cho người mới bắt đầu tăng 1.0.)</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhanh nhẹn: 10 (X&aacute;c định tốc độ tấn c&ocirc;ng, tốc độ di chuyển v&agrave; sự kh&eacute;o l&eacute;o của nh&acirc;n vật. Lo&agrave;i người Tăng độ nhanh nhẹn 1,0, t&iacute;nh nhanh nhẹn cho người mới bắt đầu lại 1,0.)</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Vitality: 11 (X&aacute;c định điểm sức khỏe của nh&acirc;n vật v&agrave; ph&ograve;ng thủ vật l&yacute;. Nh&acirc;n loại tăng điểm sức khỏe 10, ph&ograve;ng thủ vật l&yacute; 0,5. Điểm sức khỏe người mới bắt đầu tăng 1,0, ph&ograve;ng thủ vật l&yacute; 1,0.)</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tr&iacute; th&ocirc;ng minh: 10 (X&aacute;c định đ&ograve;n tấn c&ocirc;ng ph&eacute;p thuật v&agrave; Ph&ograve;ng thủ ph&eacute;p thuật của nh&acirc;n vật. Điểm tấn c&ocirc;ng ph&eacute;p thuật của con người đạt 1,0, Ph&ograve;ng thủ ph&eacute;p thuật tăng 1,0. Cuộc tấn c&ocirc;ng ph&eacute;p thuật cho người mới bắt đầu tăng 1,0, Ph&ograve;ng thủ ph&eacute;p thuật tăng 1,0)</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tinh thần: 12 (X&aacute;c định tốc độ hồi phục điểm M&aacute;u v&agrave; Năng lượng của nh&acirc;n vật, Tốc độ th&agrave;nh thạo kỹ năng. Hồi phục 1 điểm M&aacute;u v&agrave; Năng lượng mỗi 5 gi&acirc;y ở trạng th&aacute;i chiến đấu mỗi điểm Thần. Hồi 0,1 điểm M&aacute;u v&agrave; Năng lượng mỗi 5 gi&acirc;y ở trạng th&aacute;i chiến đấu mỗi điểm Spirit. Mức tăng mới bắt đầu l&agrave; 0.)</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kỹ năng: X&aacute;c định mục ti&ecirc;u</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">X&aacute;c định mục ti&ecirc;u: Tấn c&ocirc;ng kẻ địch ngay lập tức, g&acirc;y 120% s&aacute;t thương. Tăng 10% Cơ hội Ch&iacute; mạng trong 10 gi&acirc;y. Thời gian hồi chi&ecirc;u: 5 gi&acirc;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người mới bắt đầu nh&acirc;n vật ti&ecirc;u chuẩn sẽ c&oacute; hai qu&agrave; tặng hệ thống miễn ph&iacute;; một thanh kiếm gỗ v&agrave; một chiếc &aacute;o b&ocirc;ng, được trang bị theo mặc định. Họ kh&ocirc;ng phải kh&ocirc;ng c&oacute; bất kỳ thuộc t&iacute;nh n&agrave;o, nhưng hắn ta cũng c&oacute; thể khỏa th&acirc;n.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giang Phi nh&igrave;n quanh. Ng&ocirc;i l&agrave;ng của nh&acirc;n vật mới bắt đầu kh&ocirc;ng mang lại điều g&igrave; ấn tượng. Mọi thứ ở đ&oacute; chỉ l&agrave; những thứ cần thiết - b&aacute;nh m&igrave; v&agrave; bơ của tất cả c&aacute;c game MMORPG. C&oacute; tổng cộng 12 NPC xung quanh, v&agrave; chỉ c&oacute; ch&iacute;n nhiệm vụ. Một lần chạy tr&ograve; chuyện v&agrave; lấy nhanh sẽ cho ph&eacute;p người chơi dễ d&agrave;ng chấp nhận tất cả c&aacute;c nhiệm vụ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Hỡi nh&agrave; th&aacute;m hiểm vĩ đại! Bạn đ&atilde; đến nơi! Nh&agrave; h&agrave;ng của ch&uacute;ng t&ocirc;i đ&atilde; cạn kiệt nguồn cung cấp thực phẩm! Bạn c&oacute; thể gi&uacute;p ch&uacute;ng t&ocirc;i thu thập 10 miếng Thịt G&agrave; được kh&ocirc;ng? Bạn sẽ được thưởng xứng đ&aacute;ng!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Hỡi nh&agrave; th&aacute;m hiểm vĩ đại! Bạn đ&atilde; đến nơi! Ch&uacute;ng t&ocirc;i đ&atilde; sử dụng hết số da trong tiệm may. Bạn c&oacute; thể vui l&ograve;ng săn v&agrave; thu thập 10 mảnh da của Hươu kh&ocirc;ng? Bạn sẽ được thưởng xứng đ&aacute;ng!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Hỡi nh&agrave; th&aacute;m hiểm vĩ đại! Bạn đ&atilde; đến nơi! Ch&uacute;ng t&ocirc;i đ&atilde; hết thảo mộc để l&agrave;m thuốc chữa bệnh cho người d&acirc;n! Bạn c&oacute; thể vui l&ograve;ng thu thập 10 Briarthorn được kh&ocirc;ng? Bạn sẽ được thưởng xứng đ&aacute;ng!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tất cả đều giống nhau&hellip; Bắt đầu cuộc tr&ograve; chuyện giống nhau, t&ugrave;y chọn trả lời giống nhau&hellip; Kh&ocirc;ng c&oacute; g&igrave; cũ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&hellip;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Điều n&agrave;y đang vượt qu&aacute; tầm tay. Ch&uacute;ng ta đang qu&aacute; đ&ocirc;ng d&acirc;n số!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giang Phi thở d&agrave;i. Nhiệm vụ d&agrave;nh cho người mới bắt đầu từ L&agrave;ng d&agrave;nh cho người mới bắt đầu sẽ rất dễ d&agrave;ng. Vấn đề b&acirc;y giờ l&agrave; tỷ lệ kh&ocirc;ng c&acirc;n đối giữa d&acirc;n số người chơi / qu&aacute;i vật. Một con qu&aacute;i vật sẽ được ph&eacute;p tồn tại trong v&ograve;ng nửa gi&acirc;y trước khi đầu của n&oacute; lăn xuống đất, bị bất kỳ người n&agrave;o trong một bầy c&aacute; m&ograve;i đầy người chơi lột x&aacute;c trước khi bị dập v&agrave;o bụi đất. Những con qu&aacute;i vật kh&ocirc;ng thể kh&aacute;ng cự được. Việc trở th&agrave;nh con chim đầu đ&agrave;n v&agrave; nhặt lấy chiến lợi phẩm của bạn đ&atilde; nhanh ch&oacute;ng trở th&agrave;nh một nỗi đau lớn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay khi Giang Phi đang tr&ecirc;n đ&agrave; mất b&igrave;nh tĩnh, hắn ta t&igrave;m thấy một con qu&aacute;i vật vẫn chưa t&igrave;m được bạn nhảy. Hắn lao về ph&iacute;a n&oacute;. Đ&oacute; l&agrave; khi một điều th&uacute; vị xảy ra.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">G&agrave; đ&aacute; Plymouth (Qu&aacute;i th&uacute;, B&igrave;nh thường)</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cấp độ 1</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Điểm m&aacute;u: 50</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sức mạnh tấn c&ocirc;ng: 5</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kỹ năng: Kh&ocirc;ng c&oacute;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lưu &yacute;: Qu&aacute;i vật yếu của L&agrave;ng d&agrave;nh cho người mới bắt đầu. Sự th&ugrave; địch thấp. Nhiệm vụ li&ecirc;n quan &quot;Thu thập thịt g&agrave;.&quot; Người khởi xướng nhiệm vụ: Chủ nh&agrave; h&agrave;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Woah! C&aacute;i g&igrave;-? Đ&oacute; l&agrave; rất nhiều th&ocirc;ng tin chi tiết!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giang Phi dừng h&agrave;nh động của m&igrave;nh v&agrave; nh&igrave;n chằm chằm v&agrave;o cửa sổ th&ocirc;ng tin. Trước khi chơi game, Giang Phi đ&atilde; t&igrave;m hiểu kỹ về Dawn Break. Theo th&ocirc;ng tin được c&ocirc;ng bố rộng r&atilde;i, một số kỹ năng cho ph&eacute;p nhận dạng. Chỉ những người chơi c&oacute; kỹ năng n&oacute;i tr&ecirc;n mới c&oacute; thể truy cập v&agrave;o t&ecirc;n, cấp độ v&agrave; Điểm sức khỏe của qu&aacute;i vật. Th&ocirc;ng tin như vậy kh&ocirc;ng phải ai cũng c&oacute; thể truy cập!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, Giang Phi kh&ocirc;ng chỉ c&oacute; thể đọc những th&ocirc;ng tin chi tiết về con qu&aacute;i vật, m&agrave; hắn c&ograve;n c&oacute; thể xem liệu n&oacute; c&oacute; li&ecirc;n quan đến bất kỳ nhiệm vụ n&agrave;o kh&aacute;c kh&ocirc;ng! Ngay cả người khởi xướng nhiệm vụ cũng sẽ được tiết lộ! Về cơ bản n&oacute; l&agrave; một b&aacute;ch khoa to&agrave;n thư về tr&ograve; chơi! Giang Phi nh&igrave;n chằm chằm v&agrave;o chiếc nhẫn tr&ecirc;n ng&oacute;n tay m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chiếc nhẫn hơi ph&aacute;t s&aacute;ng, như thể n&oacute; đang c&ocirc;ng nhận hắn, nhận thức được mọi thứ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;C&ocirc;ng cụ n&agrave;y c&oacute; thể l&agrave; một c&ocirc;ng cụ hack ...?&quot; Giang Phi lầm bầm một m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Argh!&quot; Giang Phi nhẹ t&aacute;t ch&iacute;nh m&igrave;nh. &quot;T&ocirc;i kh&ocirc;ng thể dừng lại nửa chừng để nghĩ về điều n&agrave;y mỗi lần! Thời gian l&agrave;&nbsp; để nghiền n&aacute;t v&agrave; thăng cấp!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giang Phi l&agrave; loại người kh&ocirc;ng bao giờ n&oacute;i kh&ocirc;ng với niềm vui. Bất cứ điều g&igrave; m&agrave; hắn ta kh&ocirc;ng thể giải quyết hoặc hiểu v&agrave;o l&uacute;c n&agrave;y sẽ được n&eacute;m v&agrave;o một g&oacute;c của đầu của m&igrave;nh v&agrave; qu&ecirc;n. Sự ch&uacute; &yacute; của hắn b&acirc;y giờ đang tập trung v&agrave;o việc m&agrave;i giũa!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ding! Bạn đ&atilde; giết Plymouth Rock Chicken. Nhận được 2 điểm kinh nghiệm. Nhận được 1 vật phẩm nhiệm vụ Thịt g&agrave;!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong tr&ograve; chơi, tất cả c&aacute;c vật phẩm nhiệm vụ sẽ tự động được gửi v&agrave;o kho của người chơi. C&aacute;c vật phẩm kh&aacute;c như b&igrave;nh thuốc, thiết bị v&agrave; vật phẩm ETC sẽ y&ecirc;u cầu cướp b&oacute;c thủ c&ocirc;ng. Dấu t&iacute;ch của một vụ giết người gần đ&acirc;y sẽ ph&aacute;t ra &aacute;nh s&aacute;ng mờ nếu chiến lợi phẩm vẫn c&ograve;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&acirc;y giờ hắn ta đ&atilde; t&igrave;m được mục ti&ecirc;u để giết, Giang Phi kh&ocirc;ng l&atilde;ng ph&iacute; thời gian m&agrave; nhảy v&agrave;o con g&agrave; nhỏ tội nghiệp tiếp theo. Kỹ năng n&agrave;y sẽ được cấp cho tất cả người chơi bởi Trưởng l&agrave;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ding! Bạn đ&atilde; sử dụng Thu thập v&agrave; lấy được 1 Thịt g&agrave;!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi thu thập vật phẩm, buổi đầu ti&ecirc;n của Giang Phi trong Dawn Break bắt đầu.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">...</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ding! Xin ch&uacute;c mừng! Người chơi Verdure Glider đ&atilde; đạt đến Cấp độ 1! Tất cả c&aacute;c Điểm thuộc t&iacute;nh +2!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi b&ecirc;n tai vang l&ecirc;n tiếng chu&ocirc;ng của th&ocirc;ng b&aacute;o, sự hưng phấn của Giang Phi nhanh ch&oacute;ng tan biến. C&aacute;i t&ecirc;n đ&oacute;. C&aacute;i t&ecirc;n nực cười đ&oacute;. Hắn m&agrave; biết sớm hơn, hắn sẽ c&oacute; một c&aacute;i t&ecirc;n hay hơn! Tuy nhi&ecirc;n, nếu hắnta x&oacute;a nh&acirc;n vật của m&igrave;nh v&agrave; bắt đầu lại, hắn ta sẽ cần đợi cả tuần để tạo lại một nh&acirc;n vật! Giang Phi sẽ kh&ocirc;ng bao giờ cho ph&eacute;p m&igrave;nh l&atilde;ng ph&iacute; nhiều thời gian như vậy! Kh&ocirc;ng c&ograve;n sự lựa chọn n&agrave;o kh&aacute;c ngo&agrave;i việc nuốt hận v&agrave;o l&ograve;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong tr&ograve; chơi n&agrave;y, c&aacute;c nh&acirc;n vật dưới Cấp 10 sẽ c&oacute; tất cả c&aacute;c Điểm thuộc t&iacute;nh được ph&acirc;n bổ đồng đều. 10 Điểm thuộc t&iacute;nh sẽ được trao cho mỗi cấp độ đạt được. Trước Cấp 10, c&aacute;c Thuộc t&iacute;nh của người chơi sẽ được 2 điểm cho mỗi thuộc t&iacute;nh. Sau đ&oacute;, 10 Điểm thuộc t&iacute;nh đ&oacute; sẽ được chia đ&ocirc;i. Năm sẽ được ph&acirc;n phối đồng đều v&agrave; năm phần c&ograve;n lại sẽ được trao cho người chơi để đầu tư theo c&aacute;ch thủ c&ocirc;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;WOOHOO! Đ&atilde; đến l&uacute;c giết người!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi tăng cấp, Giang Phi l&uacute;c n&agrave;y đ&atilde; nhanh nhẹn hơn một ch&uacute;t, lao về ph&iacute;a một c&aacute;nh đồng Thỏ Hoang.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ding! Bạn đ&atilde; giết Wild Rabbit! Nhận được 5 điểm kinh nghiệm! Nhận được vật phẩm nhiệm vụ: 1 thức ăn cho thỏ may mắn!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ding! Bạn đ&atilde; sử dụng Thu thập v&agrave; lấy được 1 Thịt Thỏ Ngon!&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&hellip;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hai giờ sau, Giang Phi đ&atilde; l&ecirc;n Cấp 4. Tr&ecirc;n đường đi, hắn đ&atilde; c&oacute; trong tay một trang bị kh&aacute;c; một chiếc quần b&ecirc;n dưới &aacute;o gi&aacute;p vải r&aacute;ch mang lại sự khi&ecirc;m tốn hơn so với Ph&ograve;ng thủ.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-c171ca6b-7fff-26f2-02e1-14fa1a12cd4e\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tại thời điểm đ&oacute;, hệ thống đưa ra cảnh b&aacute;o về việc kho của anh ta đ&atilde; hết c&ocirc;ng suất. Nhiều vật phẩm đ&atilde; được chuyển v&agrave;o kho nhẫn b&iacute; ẩn. Chỉ c&oacute; một cảnh b&aacute;o nhỏ về chiếc nhẫn. Vật phẩm từ mặt đất kh&ocirc;ng thể được đặt trực tiếp v&agrave;o kho của chiếc nhẫn. Hắn ta phải tự di chuyển tất cả c&aacute;c vật phẩm từ kho của nh&acirc;n vật của m&igrave;nh v&agrave;o kho của chiếc nhẫn trước khi c&oacute; thể cướp th&ecirc;m bất cứ thứ g&igrave; từ mặt đất. Do đ&oacute;, để dễ d&agrave;ng hơn cho qu&aacute; tr&igrave;nh chơi của m&igrave;nh, Giang Phi đ&atilde; chuyển tất cả c&aacute;c vật phẩm ETC v&agrave;o kho của chiếc nhẫn v&agrave; chỉ đặt c&aacute;c vật phẩm nhiệm vụ trong kho nh&acirc;n vật của m&igrave;nh.</span></span></p>\r\n', 'Cà Di', '2020-10-19 03:19:23', 1, 0, 26);
INSERT INTO `chapters` (`id`, `name`, `slug`, `site_title`, `meta_desc`, `meta_key`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(203, 'Chương 4: Luyện tập bên vách đá', 'chuong-4-luyen-tap-ben-vach-dja', NULL, NULL, NULL, 8, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trước mặt bọn trẻ, Đội trưởng Yến h&eacute;t l&ecirc;n, &ldquo;Mọi người, h&atilde;y nghe đ&acirc;y. B&ecirc;n trong rừng tre, c&oacute; một con đường nhỏ m&agrave; ngươi sẽ tiến về ph&iacute;a trước, nơi cuối c&ugrave;ng ngươi sẽ đến V&aacute;ch đ&aacute; của ph&aacute;i Thất huyền m&ocirc;n. Khu vực đầu ti&ecirc;n của con đường sẽ l&agrave; rừng tre. V&ugrave;ng thứ hai sẽ l&agrave; v&ugrave;ng dốc đ&aacute;. Khu vực cuối c&ugrave;ng sẽ l&agrave; v&aacute;ch đ&aacute;. Chỉ những ai đ&atilde; leo được V&aacute;ch đ&aacute; v&agrave;o buổi trưa mới l&agrave; m&ocirc;n đồ của ph&aacute;i thất huyền m&ocirc;n của ch&uacute;ng t&ocirc;i. Nếu ngươi ho&agrave;n th&agrave;nh sau buổi trưa, mặc d&ugrave; ngươi sẽ kh&ocirc;ng trở th&agrave;nh Đệ tử Nội m&ocirc;n, nhưng ngươi vẫn c&oacute; thể trở th&agrave;nh Đệ tử kh&ocirc;ng ch&iacute;nh thức miễn l&agrave; m&agrave;n tr&igrave;nh diễn của ngươi thể hiện được năng lực vượt trội. &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">H&agrave;n Lập đương nhi&ecirc;n kh&ocirc;ng hiểu trở th&agrave;nh Đệ tử kh&ocirc;ng ch&iacute;nh thức&nbsp; l&agrave; như thế n&agrave;o. Anh chỉ biết rằng tất cả những g&igrave; anh phải l&agrave;m l&agrave; leo l&ecirc;n v&aacute;ch đ&aacute;. Đưa mắt nh&igrave;n về ph&iacute;a con dốc kh&ocirc;ng bằng phẳng, dốc đứng, hắn nh&igrave;n thấy nhiều c&acirc;y măng d&agrave;i mọc ngổn ngang tr&ecirc;n bề mặt v&aacute;ch đ&aacute;. Thấy vậy, c&oacute; vẻ như chướng ngại vật đầu ti&ecirc;n kh&aacute; dễ d&agrave;ng!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Học hỏi những đứa trẻ kh&aacute;c, H&agrave;n Lập kh&ocirc;ng muốn thua những đứa trẻ c&ugrave;ng tuổi. Kh&ocirc;ng kh&iacute; nhanh ch&oacute;ng trở n&ecirc;n căng thẳng đối với những đứa trẻ kh&aacute;c.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đội trưởng Yến liếc nh&igrave;n mặt trời mọc v&agrave; n&oacute;i, &ldquo;Được rồi, sắp đến giờ rồi, chuẩn bị l&ecirc;n đường! Đừng sợ, c&aacute;c tiền bối của ngươi sẽ bảo vệ ngươi từ ph&iacute;a sau trong trường hợp c&oacute; bất kỳ nguy hiểm n&agrave;o. &rdquo;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">H&agrave;n Lập quay đầu lại, liếc mắt nh&igrave;n đ&aacute;m thanh ni&ecirc;n kh&ocirc;ng thể nhận ra kia.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Những người n&agrave;y l&agrave; đệ tử cao cấp, v&igrave; vậy họ n&ecirc;n đến từ đợt trước. H&agrave;n Lập kh&ocirc;ng thể kh&ocirc;ng nghĩ, &quot;Thật ấn tượng, nếu ta tham gia c&ugrave;ng họ, ta c&oacute; thể mặc c&ugrave;ng một chiếc &aacute;o cho&agrave;ng của một Đệ tử Nội m&ocirc;n sao?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi đang m&ugrave; qu&aacute;ng c&acirc;n nhắc, H&agrave;n Lập ph&aacute;t hiện những đứa trẻ kia đ&atilde; x&ocirc;ng v&agrave;o rừng tre. Nh&igrave;n thấy m&igrave;nh bị bỏ lại, H&agrave;n Lập nhanh ch&oacute;ng bắt đầu tiến l&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một khi ba mươi đứa trẻ lao v&agrave;o, ch&uacute;ng ngừng di chuyển th&agrave;nh một nh&oacute;m v&agrave; ph&acirc;n t&aacute;n trong khu rừng tre rộng lớn. Ph&iacute;a sau H&agrave;n Lập l&agrave; một đệ tử cao gầy với vẻ mặt lạnh l&ugrave;ng, lẳng lặng đi theo H&agrave;n Lập. H&agrave;n Lập c&oacute; ch&uacute;t sợ h&atilde;i, kh&ocirc;ng d&aacute;m bắt chuyện, l&atilde;ng ph&iacute; thời gian. Hơi sợ h&atilde;i, anh hạ thấp người v&agrave; tiến l&ecirc;n con dốc cao.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đoạn rừng tre n&agrave;y nh&igrave;n bề ngo&agrave;i rất b&igrave;nh thường, nhưng sau khi H&agrave;n Lập đi được một đoạn, liền ph&aacute;t hiện c&agrave;ng ng&agrave;y c&agrave;ng kh&oacute; đi về ph&iacute;a trước. Bước ch&acirc;n của hắn c&agrave;ng l&uacute;c c&agrave;ng nặng, dần dần, H&agrave;n Lập bắt đầu d&ugrave;ng một tay k&eacute;o c&acirc;y tre, d&ugrave;ng đ&agrave; k&eacute;o c&acirc;y tre trở lại vị tr&iacute; ban đầu để đẩy hắn về ph&iacute;a trước.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">H&agrave;n Lập ki&ecirc;n tr&igrave; theo c&aacute;ch n&agrave;y kh&aacute; l&acirc;u, đến một l&uacute;c n&agrave;o đ&oacute;, hắn đ&atilde; trở n&ecirc;n kiệt sức, v&igrave; vậy hắn kh&ocirc;ng c&ograve;n lựa chọn n&agrave;o kh&aacute;c ngo&agrave;i việc t&igrave;m một chỗ trống để ngồi xuống v&agrave; nghỉ ngơi, hơi thở dồn dập khiến hắn mệt dần.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi h&iacute;t thở một hơi, H&agrave;n Lập đưa mắt nh&igrave;n, nh&igrave;n thấy vị tiền bối cao gầy ph&iacute;a sau. Mặc d&ugrave; mặt đất rất dốc nhưng vị đệ tử cao cấp n&agrave;y vẫn thản nhi&ecirc;n đứng tr&ecirc;n mặt đất như thể đ&acirc;y kh&ocirc;ng l&agrave; g&igrave; đối với hắn. Cơ thể hắn ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; bụi bẩn, đứng thẳng như b&uacute;p măng gần đ&oacute;, trong khi hắn lặng lẽ nh&igrave;n H&agrave;n Lập ở ph&iacute;a xa.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nh&igrave;n &aacute;nh mắt lạnh l&ugrave;ng của vị tiền bối n&agrave;y, H&agrave;n Lập trong l&ograve;ng cảm thấy sợ h&atilde;i, nhanh ch&oacute;ng quay đầu lại. Hắn cũng nghe thấy tiếng thở nặng nhọc ở ph&iacute;a trước v&agrave; suy ra rằng một trong những đứa trẻ nhanh nhẹn hơn cũng đang tận dụng cơ hội n&agrave;y để nghỉ ngơi. Sau một hồi nghỉ ngơi, H&agrave;n Lập nhanh ch&oacute;ng tiếp tục cuộc h&agrave;nh tr&igrave;nh của m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Con dốc n&agrave;y rất dốc v&agrave; năng lượng dự trữ của H&agrave;n Lập ng&agrave;y c&agrave;ng nhỏ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn ta quyết định nằm thẳng tr&ecirc;n mặt đất v&agrave; trường về ph&iacute;a trước thay v&igrave; đi bằng ch&acirc;n; đến nỗi nếu hắn ta vắt kiệt sức lực của m&igrave;nh, hắn ta sẽ kh&ocirc;ng ng&atilde; sấp mặt. May mắn thay, quần &aacute;o của hắn&nbsp; được l&agrave;m bằng chất liệu chắc chắn; nếu kh&ocirc;ng, tay ch&acirc;n v&agrave; đầu gối của hắn ta c&oacute; thể đ&atilde; bị tổn thương do bị k&eacute;o l&ecirc; tr&ecirc;n mặt đất đ&aacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi gần đi đến cuối rừng tre, H&agrave;n Lập cảm thấy rất kh&oacute; khăn để ho&agrave;n th&agrave;nh những bước cuối c&ugrave;ng. Điều n&agrave;y l&agrave; do khi đ&aacute; v&agrave; đ&aacute; tr&ecirc;n mặt đất tăng l&ecirc;n, số lượng tre giảm.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">H&agrave;n Lập rốt cuộc đ&atilde; đến mức kh&ocirc;ng c&ograve;n tre để d&ugrave;ng chống đỡ. H&agrave;n Lập từ từ nh&iacute;ch từng ch&uacute;t một qua đoạn đường cuối c&ugrave;ng n&agrave;y v&agrave; vượt qua chướng ngại vật đầu ti&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&uacute;c bước ra khỏi rừng tre, hắn chỉ thấy một khoảng đất rộng lớn. Trước mặt hắn l&agrave; một ngọn n&uacute;i đ&aacute; khổng lồ kh&ocirc;ng g&igrave; s&aacute;nh được. Tr&ecirc;n đỉnh n&uacute;i khổng lồ l&agrave; một v&agrave;i đứa trẻ gầy g&ograve;, đang từ từ leo l&ecirc;n v&aacute;ch đ&aacute; c&ugrave;ng với một v&agrave;i đệ tử cao cấp đang canh chừng ch&uacute;ng. H&agrave;n Lập kh&ocirc;ng d&aacute;m chần chừ nữa, vội v&agrave;ng đi tới trước ngọn n&uacute;i đ&aacute; khổng lồ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">N&uacute;i bao gồm nhiều lớp v&agrave; nhiều lớp đ&aacute; trầm t&iacute;ch xếp chồng l&ecirc;n nhau v&agrave; bị x&oacute;i m&ograve;n rất nghi&ecirc;m trọng. Ở một số nơi, mặt đất vỡ vụn khi chạm v&agrave;o. Tất nhi&ecirc;n, cũng c&oacute; những phiến đ&aacute; vụn rắn chắc, nhưng việc t&igrave;m kiếm ch&uacute;ng l&agrave; v&ocirc; c&ugrave;ng nguy hiểm v&igrave; ch&uacute;ng sẽ dẫn đến thương t&iacute;ch. H&agrave;n Lập chỉ mới tập những m&ocirc;n v&otilde; thuật th&ocirc; sơ nhất v&agrave; do đ&oacute;, đ&ocirc;i tay của hắn đ&atilde; đầy thương t&iacute;ch sau chuyến đi xuy&ecirc;n rừng tre. C&ugrave;ng l&uacute;c đ&oacute;, quần &aacute;o của hắn ta bị x&eacute; r&aacute;ch v&agrave; tả tơi xung quanh đầu gối, da thịt v&agrave; cơ bắp được che giấu bởi quần &aacute;o cũng bị trầy xước v&agrave; bị thương. Vết thương ở đầu gối tuy nhỏ nhưng H&agrave;n Lập nghiến răng nghiến lợi mỗi khi tiếp x&uacute;c với đ&aacute; lởm chởm; nỗi đau gần như nhiều hơn khả năng chịu đựng của hắn.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một v&agrave;i đứa trẻ dẫn đầu đ&atilde; leo được kh&aacute; cao. Nh&igrave;n thấy những người kh&aacute;c trước mắt, H&agrave;n Lập kh&ocirc;ng chịu thua. Kh&ocirc;ng một &yacute; nghĩ từ bỏ n&agrave;o vụt qua trong đầu hắn, h&igrave;nh ảnh về tam th&uacute;c v&agrave; gia đ&igrave;nh của hắn sẽ hiện ra, tạo cho hắn động lực để tiếp tục. Với k&yacute; ức về những người hắn y&ecirc;u thương th&ocirc;i th&uacute;c hắn tiếp tục, H&agrave;n Lập kh&ocirc;ng ngừng tiến về ph&iacute;a trước.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trước khi H&agrave;n Lập l&ecirc;n đường tham gia Kỳ thi Đệ tử, cha v&agrave; tam th&uacute;c&nbsp; của H&agrave;n Lập đ&atilde; nhắc nhở H&agrave;n Lập rằng b&agrave;i kiểm tra sẽ cực kỳ kh&oacute; khăn. Nếu kh&ocirc;ng ki&ecirc;n tr&igrave; đến c&ugrave;ng, H&agrave;n Lập sẽ kh&ocirc;ng c&oacute; cơ hội gia nhập Thất huyền m&ocirc;n. Giờ ph&uacute;t n&agrave;y, H&agrave;n Lập kh&ocirc;ng c&ograve;n quan t&acirc;m đến việc gia nhập m&ocirc;n ph&aacute;i nữa. Thay v&agrave;o đ&oacute;, nguồn động lực duy nhất th&uacute;c đẩy anh ta tiến về ph&iacute;a trước l&agrave; sự kh&ocirc;ng sẵn s&agrave;ng nhượng bộ v&agrave; sự khẩn trương để bắt kịp những người kh&aacute;c dẫn đầu.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">H&agrave;n Lập ngẩng đầu l&ecirc;n v&agrave; nhận thấy rằng Vũ Nham hiện đang dẫn đầu. Vũ Nham lớn hơn H&agrave;n Lập v&agrave; thậm ch&iacute; đ&atilde; tập luyện v&otilde; thuật; Kh&ocirc;ng c&oacute; g&igrave; đ&aacute;ng ngạc nhi&ecirc;n, hắn ta c&oacute; một cơ thể mạnh mẽ hơn so với những người kh&aacute;c.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một lần nữa, H&agrave;n Lập đưa mắt nh&igrave;n về ph&iacute;a sau v&agrave; thấy m&igrave;nh đ&atilde; vượt qua kh&aacute; nhiều người, nhiều người vẫn đang lao tới. H&iacute;t s&acirc;u một hơi, H&agrave;n Lập tăng tốc độ đi l&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">D&ugrave; đ&atilde; cạn kiệt phần lớn sức lực nhưng hắn vẫn kh&ocirc;ng thể r&uacute;t ngắn khoảng c&aacute;ch giữa hắn v&agrave; những người dẫn đầu. Khi mặt trời ki&ecirc;n cường leo l&ecirc;n trung t&acirc;m của bầu trời, cơ thể của H&agrave;n Lập ng&agrave;y c&agrave;ng nặng nề, khiến hắn c&agrave;ng kh&oacute; l&ecirc;n tới đỉnh. Trong l&uacute;c đ&oacute;, H&agrave;n Lập đ&atilde; l&ecirc;n đến đỉnh n&uacute;i lớn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Gần đỉnh của ngọn n&uacute;i cao h&agrave;ng trăm m&eacute;t l&agrave; V&aacute;ch đ&aacute; tinh luyện xương cực kỳ dốc. Hơn mười sợi d&acirc;y, mỗi sợi c&oacute; k&iacute;ch thước bằng nắm tay, được treo tr&ecirc;n đỉnh v&aacute;ch đ&aacute;. Vũ Nham chọn một chiếc v&agrave; bắt đầu leo ​​l&ecirc;n v&aacute;ch đ&aacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">H&agrave;n Lập nh&igrave;n chằm chằm người dẫn đầu, trong l&ograve;ng cảm thấy cam chịu. Hắn biết rằng hắn kh&ocirc;ng thể đuổi kịp những người ph&iacute;a trước trong khoảng thời gian ngắn ngủi c&ograve;n lại cho đến trưa.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&Yacute; niệm về thất bại nhanh ch&oacute;ng bị x&oacute;a bỏ bởi nỗi đau bất ngờ b&ugrave;ng l&ecirc;n từ những vết thương của hắn . Những cơn đau bốc lửa đ&atilde; cướp đi sức mạnh từ tay ch&acirc;n hắn. Cảm gi&aacute;c được th&acirc;n thể của m&igrave;nh ng&atilde; xuống, H&agrave;n Lập một tay luống cuống tay ch&acirc;n nắm lấy một vi&ecirc;n đ&aacute;. Tim hắn đập thất thường khi hắn nhanh ch&oacute;ng d&aacute;n người v&agrave;o sườn n&uacute;i, kh&ocirc;ng d&aacute;m thực hiện bất kỳ cử động đột ngột n&agrave;o.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một l&uacute;c sau, hắn mới b&igrave;nh tĩnh lại, d&ugrave;ng tay kiểm tra độ chắc của phiến đ&aacute;. Chỉ sau khi x&aacute;c định rằng n&oacute; đ&atilde; được đảm bảo an to&agrave;n tại chỗ, hắn mới c&oacute; thể bu&ocirc;ng bỏ những lo lắng của m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nh&igrave;n xuống ph&iacute;a dưới, H&agrave;n Lập thấy vị tiền bối cao lớn đang ở tư thế ngồi xổm, hai tay dang ra, chuẩn bị đỡ H&agrave;n Lập nếu hắn ta ng&atilde; xuống. Tuy nhi&ecirc;n, thấy rằng H&agrave;n Lập đ&atilde; an to&agrave;n, vị tiền bối đ&atilde; thu tay lại.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">H&agrave;n Lập cảm thấy trong l&ograve;ng nhẹ nh&otilde;m. Nếu thật sự rơi xuống v&aacute;ch n&uacute;i, tất cả c&ocirc;ng sức của hắn ta đều uổng ph&iacute;! Sau một l&uacute;c ngắn, hắn ta từ từ nh&iacute;ch về ph&iacute;a trước v&agrave; b&ograve; về ph&iacute;a những sợi d&acirc;y c&ograve;n lại đang treo xuống v&aacute;ch đ&aacute;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cuối c&ugrave;ng, hắn ta đ&atilde; đến đ&aacute;y của một sợi d&acirc;y. Mặt trời đ&atilde; gần chạm đến t&acirc;m bầu trời, cho thấy chỉ c&ograve;n một giờ nữa l&agrave; hết thời hạn. L&uacute;c n&agrave;y, H&agrave;n Lập đ&atilde; leo l&ecirc;n v&aacute;ch đ&aacute; v&agrave; hắn quay đầu nh&igrave;n những đứa trẻ c&ograve;n lại. Ngay khi H&agrave;n Lập đang leo d&acirc;y, &aacute;nh mắt của anh ấy t&igrave;nh cờ gặp Vũ Nham, chỉ thấy hắn đưa ra một cử chỉ ng&oacute;n tay c&aacute;i với c&aacute;c đối thủ chậm hơn. Sau khi cười đi&ecirc;n cuồng, H&agrave;n Lập tiếp tục đi về ph&iacute;a trước.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Với sự tức giận d&acirc;ng l&ecirc;n trong l&ograve;ng, H&agrave;n Lập nắm lấy sợi d&acirc;y v&agrave; bắt đầu leo ​​l&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, H&agrave;n Lập đ&atilde; d&ugrave;ng hết sức b&igrave;nh sinh từ l&acirc;u. Hiện tại, ngay cả việc giữ chặt sợi d&acirc;y cũng đ&atilde; l&agrave; một thử th&aacute;ch đối với hắn, chứ chưa n&oacute;i đến việc leo d&acirc;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thật kỳ diệu, H&agrave;n Lập đ&atilde; leo l&ecirc;n được n&uacute;t thắt đầu ti&ecirc;n tr&ecirc;n sợi d&acirc;y của m&igrave;nh. Ngồi tr&ecirc;n đ&oacute;, hắn cảm thấy cơ thể như h&oacute;a th&agrave;nh b&ocirc;ng, kh&ocirc;ng thể cử động một ng&oacute;n tay. Hắn quay đầu lại v&agrave; nh&igrave;n tất cả những đứa trẻ ph&iacute;a sau m&igrave;nh. Một số người trong số họ đ&atilde; bỏ cuộc, ngồi xuống n&uacute;i đ&aacute; v&agrave; thở hồng hộc. Giống như H&agrave;n Lập, họ đ&atilde; cạn kiệt sức lực v&agrave; đang đứng tr&ecirc;n bờ vực suy sụp.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">H&agrave;n Lập chỉ c&oacute; thể cười khổ; hắn đ&atilde; đ&aacute;nh gi&aacute; rất thấp thử nghiệm n&agrave;y. May mắn thay, hắn ta kh&ocirc;ng nằm trong số những đứa trẻ cuối c&ugrave;ng. Sau khi nh&igrave;n v&agrave;o &aacute;nh mắt lạnh l&ugrave;ng của c&aacute;c đệ tử cao cấp, hắn ta quyết định tiếp tục. Mặc d&ugrave; hắn kh&ocirc;ng c&oacute; cơ hội ho&agrave;n th&agrave;nh b&agrave;i kiểm tra trước buổi trưa, nhưng ho&agrave;n th&agrave;nh muộn vẫn tốt hơn l&agrave; treo lơ lửng tr&ecirc;n d&acirc;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f4ab1241-7fff-6922-5dfd-99426c93a835\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">H&agrave;n Lập đưa cả hai b&agrave;n tay cứng đờ của m&igrave;nh ra v&agrave; d&ugrave;ng sức lực hồi phục được trong giờ nghỉ để từ từ leo l&ecirc;n d&acirc;y. Nhưng v&agrave;o l&uacute;c n&agrave;y, H&agrave;n Lập tay kh&ocirc;ng ngừng đ&aacute;p ứng &yacute; tứ của hắn; hắn thậm ch&iacute; c&ograve;n thiếu sức mạnh để giữ chặt sợi d&acirc;y. H&agrave;n Lập dừng lại ở đ&oacute; v&agrave; n&aacute;n lại một l&uacute;c trước khi miễn cưỡng quyết định ở đ&oacute; ngồi tr&ecirc;n n&uacute;t thắt.</span></span></p>\r\n', 'Cà Di', '2020-10-19 03:32:44', 1, 0, 5);
INSERT INTO `chapters` (`id`, `name`, `slug`, `site_title`, `meta_desc`, `meta_key`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(204, 'Chương 3: Thử thách đã đến', 'chuong-3-thu-thach-dja-djen', NULL, NULL, NULL, 36, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ng&agrave;y h&ocirc;m sau, Tần Nam từ từ tỉnh lại sau giấc ngủ. Khi chuẩn bị đứng dậy, hắn kh&ocirc;ng thể kh&ocirc;ng h&iacute;t v&agrave;o thật s&acirc;u, v&igrave; n&atilde;o hắn vẫn đang trải qua cơn đau. Như thể bộ n&atilde;o của hắn ta sẽ vỡ ra chỉ với một cử động nhỏ nhất.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Giọng n&oacute;i đ&oacute; thật đ&aacute;ng sợ.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam sắc mặt t&aacute;i nhợt. Cảm gi&aacute;c như giọng n&oacute;i h&ocirc;m qua đ&atilde; thấm v&agrave;o t&acirc;m hồn hắn, khiến hắn cảm thấy rợn người khi nhớ lại cảnh tượng l&uacute;c n&agrave;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Điều đầu ti&ecirc;n, t&ocirc;i sẽ xem x&eacute;t Thần chiến tranh n&agrave;y&hellip;&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam đ&atilde; sớm thu thập xong suy nghĩ, tiến ra khu vực tho&aacute;ng đ&atilde;ng b&ecirc;n ngo&agrave;i nh&agrave; m&igrave;nh. Hắn ngồi bệt xuống đất với vẻ mặt lo lắng. Mặc d&ugrave; Thần Trận Đồ n&agrave;y cực kỳ b&iacute; ẩn, nhưng kh&ocirc;ng c&oacute; g&igrave; đảm bảo cấp bậc của n&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi h&iacute;t một hơi thật s&acirc;u, Tần Nam từ từ nhắm mắt lại trong khi suy nghĩ ch&igrave;m v&agrave;o trong cơ thể khi cố gắng giao tiếp với V&otilde; Thần của m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam chỉ c&oacute; thể nh&igrave;n thấy từng tia m&agrave;u v&agrave;ng được ph&aacute;t ra, đến s&aacute;u tia m&agrave;u v&agrave;ng mới dừng lại.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giữa những tia s&aacute;ng m&agrave;u v&agrave;ng l&agrave; một h&igrave;nh người mờ ảo đang lơ lửng giữa kh&ocirc;ng trung trong tư thế đứng vững. Cứ như thể một vị ho&agrave;ng đế v&ocirc; song gi&aacute;ng thế, khiến tr&aacute;i tim người ta phải chịu &aacute;p lực rất lớn khi nh&igrave;n v&agrave;o bức tranh ấy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam sau khi nh&igrave;n thấy điều n&agrave;y th&igrave; sửng sốt, &quot;Đ&acirc;y l&agrave; ... Thần Trận Đồ?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau đ&oacute;, anh ch&igrave;m đắm trong niềm vui lớn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thần Trận Đồ n&agrave;y chắc chắn kh&ocirc;ng để hắn thất vọng. Cấp bậc của n&oacute; l&agrave; cấp s&aacute;u. Mặc d&ugrave; vẫn c&ograve;n ch&ecirc;nh lệch rất nhiều so với dự kiến ​​của hắn, nhưng luồng kh&iacute; v&ocirc; bi&ecirc;n cổ xưa, trừu tượng, ph&aacute;t ra từ Thần Trận Đồ như một &aacute;m chỉ rằng đ&acirc;y kh&ocirc;ng phải l&agrave; to&agrave;n bộ sức mạnh của n&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Ph&ugrave; ... Ta vẫn chưa biết khả năng của n&oacute; l&agrave; g&igrave;. Ch&agrave;, ta sẽ cố gắng tu luyện với n&oacute; ngay b&acirc;y giờ. &quot; Tần Nam nhịn kh&ocirc;ng được vui mừng trong l&ograve;ng, b&igrave;nh tĩnh lại suy nghĩ, trước khi bắt đầu giao tiếp với Thần kh&iacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một cảnh tượng kinh ngạc ngay lập tức diễn ra. Thần Trận Đồ c&oacute; h&igrave;nh d&aacute;ng con người ngồi khoanh ch&acirc;n tr&ecirc;n mặt đất, trong khi một luồng kh&iacute; đ&aacute;ng sợ nhưng ma thuật ph&aacute;t ra từ cơ thể của n&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kh&oacute;i ở xung quanh lập tức lao về ph&iacute;a b&oacute;ng d&aacute;ng của Tần Nam, khiến một l&agrave;n sương mờ ảo h&igrave;nh th&agrave;nh xung quanh anh ta.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Điều n&agrave;y&hellip;&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam kinh ngạc tột độ. Tốc độ tu luyện như vậy nhanh hơn &iacute;t nhất mười lần so với cấp bậc nhất của hắn l&agrave; tinh hồng kiếm.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tốc độ tu luyện của V&otilde; kh&iacute; cấp s&aacute;u kh&ocirc;ng thể coi thường.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam đ&atilde; sớm đắm ch&igrave;m suy nghĩ của m&igrave;nh v&agrave;o qu&aacute; tr&igrave;nh tu luyện với to&agrave;n bộ sự tập trung khi tiếp tục hấp thụ Kh&iacute; của Trời Đất th&ocirc;ng qua Thần Trận Đồ của m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi kh&iacute; đi v&agrave;o cơ thể hắn ta, n&oacute; di chuyển quanh c&aacute;c kinh mạch của hắn ta, tạo ra &acirc;m thanh giống như tiếng chảy của s&ocirc;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thời gian dần tr&ocirc;i. Ba giờ sau, mắt của Tần Nam mở to khi cơ thể hắn ph&aacute;t ra một &acirc;m thanh tanh t&aacute;ch tương tự như tiếng đậu x&agrave;o.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong v&ograve;ng ba giờ qua, tu luyện của hắn đ&atilde; ngay lập tức đạt tới Cảnh giới Luyện kh&iacute; tầng thứ nhất!</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Với sự gi&uacute;p đỡ của Thần kh&iacute; n&agrave;y, ước mơ trở th&agrave;nh chuy&ecirc;n gia của ta kh&ocirc;ng c&ograve;n l&agrave; điều bất khả thi.&rdquo; Tần Nam nắm chặt tay, trong mắt hiện l&ecirc;n vẻ ki&ecirc;n định c&ugrave;ng chờ mong.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thương Lan đại lục l&agrave; v&ocirc; hạn. </span><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Th&agrave;nh L&acirc;m Thủy</span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\"> &mdash; hay thậm ch&iacute; l&agrave; </span><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">La H&agrave; quốc</span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\"> &mdash; chỉ c&oacute; k&iacute;ch thước bằng một hạt dẻ nổi tr&ecirc;n đại dương.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam đ&atilde; đạt được danh hiệu thi&ecirc;n t&agrave;i h&agrave;ng đầu của </span><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Th&agrave;nh L&acirc;m Thủy</span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\"> từ khi c&ograve;n trẻ, v&igrave; vậy hắn đ&atilde; đặt mục ti&ecirc;u của m&igrave;nh rất cao. Một ng&agrave;y n&agrave;o đ&oacute;, hắn ta sẽ mang tr&ecirc;n lưng một thanh kiếm, cầm một v&ograve; rượu trong tay v&agrave; tự do đi khắp lục địa, sống một cuộc sống kh&ocirc;ng bị giới hạn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay l&uacute;c đ&oacute;, một &acirc;m thanh vang l&ecirc;n từ lối v&agrave;o nơi ở của hắn, &quot;Thiếu gia, t&ocirc;i đến đ&acirc;y để giao tiền trợ cấp trong th&aacute;ng cho anh.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Theo tiếng n&oacute;i, một c&ocirc; hầu g&aacute;i bước v&agrave;o mang theo một c&aacute;i đĩa. Tr&ecirc;n đ&oacute; c&oacute; đặt một chiếc b&igrave;nh ngọc, từ đ&oacute; ph&aacute;t ra một luồng kh&iacute; d&agrave;y đặc của Kh&iacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam giật m&igrave;nh, nhưng lập tức nhớ lại h&ocirc;m nay l&agrave; ng&agrave;y trong tộc ph&acirc;n ph&aacute;t t&agrave;i nguy&ecirc;n cần cho tu luyện.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần tộc l&agrave; một trong hai Đại gia tộc của </span><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Th&agrave;nh L&acirc;m Thủy</span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">, do đ&oacute; kh&ocirc;ng cần n&oacute;i rằng theo đuổi con đường tu luyện l&agrave; một trong những trọng t&acirc;m của gia tộc. Do đ&oacute;, tộc sẽ ph&acirc;n phối t&agrave;i nguy&ecirc;n cần thiết để tu luyện cho c&aacute;c đệ tử của m&igrave;nh mỗi th&aacute;ng một lần.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam tiếp nhận c&aacute;i lọ, thừa nhận để cho người hầu đưa đi. Hắn ta vẫn c&ograve;n kinh ngạc sau khi nh&igrave;n v&agrave;o b&ecirc;n trong c&aacute;i lọ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong lọ c&oacute; mười vi&ecirc;n thuốc s&aacute;ng b&oacute;ng m&agrave;u xanh lục. Một luồng kh&iacute; mạnh mẽ của Đan c&oacute; thể được cảm nhận từ họ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kh&ocirc;ng nghi ngờ g&igrave; nữa, Tần Nam đ&atilde; quen với những vi&ecirc;n thuốc. Đ&acirc;y l&agrave; những vi&ecirc;n thuốc luyện th&acirc;n, đặc biệt được sử dụng để tu luyện trong c&otilde;i luyện th&acirc;n!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">N&oacute;i như vậy, những vi&ecirc;n thuốc cường dương n&agrave;y v&ocirc; c&ugrave;ng qu&yacute; gi&aacute;. C&aacute;c đệ tử b&igrave;nh thường sẽ chỉ được ph&aacute;t một vi&ecirc;n thuốc luyện th&acirc;n trong th&aacute;ng, nhưng Tần Nam đ&atilde; nhận được mười vi&ecirc;n thuốc trong số đ&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Cha&hellip;&rdquo; Tần Nam c&oacute; thể cảm thấy mũi m&igrave;nh ngứa ran. Tin tức về việc hắn ta đ&aacute;nh thức một V&otilde; Thần cấp một chắc chắn đ&atilde; được cả </span><span style=\"font-size: 16pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Th&agrave;nh L&acirc;m Thủy </span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">biết đến &mdash; thi&ecirc;n t&agrave;i đ&atilde; biến th&agrave;nh r&aacute;c rưởi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&igrave; vậy, kh&ocirc;ng c&oacute; chuyện c&aacute;c trưởng l&atilde;o trong tộc đồng &yacute; cho một thứ r&aacute;c rưởi nhiều vi&ecirc;n thuốc n&agrave;y. Lời giải th&iacute;ch duy nhất l&agrave; phụ th&acirc;n hắn ta đ&atilde; lạm dụng quyền lực của m&igrave;nh để lấy cho hắn ta những vi&ecirc;n thuốc.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Phụ th&acirc;n của Tần Nam, Tần Thi&ecirc;n, l&agrave; người đứng đầu của Tần tộc. Kh&ocirc;ng ai d&aacute;m phản b&aacute;c quyết định đưa cho Tần Nam mười vi&ecirc;n thuốc luyện th&acirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cho d&ugrave; Tần Thi&ecirc;n biết con trai Tần Nam của m&igrave;nh đ&atilde; biến th&agrave;nh một thứ r&aacute;c rưởi, nhưng vẫn nhất quyết đưa cho hắn mười vi&ecirc;n Luyện Kh&iacute; Sư. Điều n&agrave;y như một dấu hiệu cho thấy Tần Nam vẫn l&agrave; con trai y&ecirc;u qu&yacute; của Tần Thi&ecirc;n như mọi khi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Phụ th&acirc;n, con của người sẽ kh&ocirc;ng bao giờ l&agrave;m người thất vọng&hellip; Con sẽ tận dụng những vi&ecirc;n thuốc n&agrave;y để thăng cấp l&ecirc;n tầng thứ hai của C&otilde;i luyện cơ thể ngay b&acirc;y giờ!&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam nhặt một vi&ecirc;n thuốc luyện th&acirc;n, ngấu nghiến. Anh lập tức ngồi bệt xuống đất để tiếp thu.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, ngay l&uacute;c đ&oacute;, một điều kỳ lạ đ&atilde; xảy ra. Sau khi vi&ecirc;n luyện kh&iacute; bị nuốt v&agrave;o trong cơ thể, Tần Nam kh&ocirc;ng chỉ kh&ocirc;ng hấp thụ được, m&agrave; c&ograve;n c&oacute; thể cảm nhận được một cỗ lực lượng hấp thu huyền diệu b&ecirc;n trong cơ thể đang triệt để h&uacute;t lấy Kh&iacute; của thuốc luyện th&acirc;n đi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam giật m&igrave;nh. C&aacute;i g&igrave;&hellip; chuyện g&igrave; đang xảy ra vậy?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn ta ngay lập tức lấy ra một vi&ecirc;n đan dược thứ hai v&agrave; nuốt xuống. Tuy nhi&ecirc;n, điều tương tự lại xảy ra. Kh&iacute; của vi&ecirc;n thuốc đ&atilde; bị hấp thụ bởi một lực kh&ocirc;ng x&aacute;c định.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Chẳng lẽ ... Chẳng lẽ l&agrave; Thần Trận đồ?&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi suy nghĩ một ch&uacute;t, Tần Nam nghĩ ra được một c&aacute;i khả năng, liền triệu hồi Thần Trận đồ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau đ&oacute;, anh ta ngấu nghiến vi&ecirc;n đan dược thứ ba. Đ&uacute;ng như dự đo&aacute;n của anh, một lực hấp thụ ma thuật được ph&aacute;t ra từ Thần Trận v&agrave; nuốt chửng vi&ecirc;n thuốc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Thần Trận đồ c&oacute; thể hấp thụ Kh&iacute; của vi&ecirc;n thuốc?&quot; Tần Nam c&ograve;n lại l&agrave; kinh ngạc. Anh chưa bao giờ mong đợi một V&otilde; Thần c&oacute; thể hấp thụ những vi&ecirc;n thuốc.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một điều ho&agrave;n to&agrave;n v&ocirc; l&yacute;.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Nuốt n&oacute; đi, ta sẽ để n&oacute; ngấu nghiến hết mấy vi&ecirc;n thuốc. Ta sẽ xem điều g&igrave; sẽ xảy ra với n&oacute;! &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam nghiến răng. V&igrave; tốc độ tu luyện của Thần kh&iacute; kh&aacute; đ&aacute;ng kể, n&ecirc;n hắn kh&ocirc;ng cần phải dựa v&agrave;o những vi&ecirc;n thuốc để n&acirc;ng cao tu luyện của m&igrave;nh. Hắn nhanh ch&oacute;ng ngấu nghiến hết những vi&ecirc;n thuốc v&agrave;o người.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi ngấu nghiến hết những vi&ecirc;n thuốc c&ograve;n lại, một cảnh tượng kinh ho&agrave;ng đ&atilde; diễn ra.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một tia s&aacute;ng m&agrave;u v&agrave;ng thứ bảy đột nhi&ecirc;n ph&aacute;t ra từ h&igrave;nh người khổng lồ. C&ugrave;ng l&uacute;c đ&oacute;, kh&iacute; tức uy nghi&ecirc;m của Thần Trận đồ c&agrave;ng th&ecirc;m kinh người!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Đ&acirc;y l&agrave;&hellip;&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam tr&ecirc;n mặt tr&agrave;n đầy kinh ngạc. Tia m&agrave;u v&agrave;ng thứ bảy l&agrave; biểu tượng của cấp bảy. N&oacute;i c&aacute;ch kh&aacute;c, cấp bậc của V&otilde; Thần đ&atilde; tăng th&ecirc;m một bậc.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Điều n&agrave;y c&oacute; nghĩa l&agrave;&hellip; Thần Kh&iacute; c&oacute; thể thăng cấp bằng c&aacute;ch ti&ecirc;u thụ những vi&ecirc;n thuốc!&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam kh&ocirc;ng thể diễn tả được sự b&agrave;ng ho&agrave;ng trong l&ograve;ng bằng lời n&oacute;i. Năng lực của Thần Trận Đồ đ&atilde; vượt qu&aacute; sức tưởng tượng của hắn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&otilde; Thần l&agrave; m&oacute;n qu&agrave; từ Thi&ecirc;n tộc. Một khi V&otilde; Thần của một người được đ&aacute;nh thức, n&oacute; sẽ đi c&ugrave;ng họ trong suốt qu&atilde;ng đời c&ograve;n lại. N&oacute;i như vậy, Tần Nam chưa từng nghe n&oacute;i qua bất kỳ V&otilde; Thần c&oacute; thể thăng cấp.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nếu đ&uacute;ng như vậy, điều đ&oacute; c&oacute; nghĩa l&agrave; Thần Trận c&oacute; thể thăng cấp l&ecirc;n cấp Huyền, cấp Địa, hoặc thậm ch&iacute; l&agrave; cấp Thi&ecirc;n huyền thoại nếu c&oacute; đủ vi&ecirc;n thuốc?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một Thi&ecirc;n cấp V&otilde; Thần, sẽ đ&aacute;ng sợ như thế n&agrave;o? Tần Nam thậm ch&iacute; kh&ocirc;ng thể tưởng tượng được lại xảy ra chuyện.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Ph&ugrave;, b&igrave;nh tĩnh, b&igrave;nh tĩnh. Ta phải b&igrave;nh tĩnh lại &rdquo;. Tần Nam h&iacute;t s&acirc;u mấy hơi để kiềm chế cơn thịnh nộ đang tr&agrave;o d&acirc;ng trong l&ograve;ng, buộc bản th&acirc;n phải b&igrave;nh tĩnh suy nghĩ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, trong mắt hắn ta lại chứa đựng sự phấn kh&iacute;ch v&ocirc; tận.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi Chiến kh&iacute; thức tỉnh, mặc d&ugrave; l&agrave; s&aacute;u cấp, Tần Nam cũng c&oacute; thể cảm gi&aacute;c được V&otilde; Thần n&agrave;y c&ograve;n c&oacute; c&aacute;i g&igrave; nữa. Hắn kh&ocirc;ng khỏi cảm thấy Thần Trận c&ograve;n mạnh hơn cấp s&aacute;u.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&acirc;y giờ, sau khi biết rằng Chiến kh&iacute; c&oacute; thể thăng cấp, n&oacute; thậm ch&iacute; c&ograve;n đ&aacute;ng sợ hơn một Huyền kh&iacute; cấp hay thậm ch&iacute; l&agrave; một V&otilde; Thần cấp Địa, v&igrave; n&oacute; sở hữu một tiềm năng v&ocirc; hạn!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">... Trong khi đ&oacute;, Tần Nam đang đắm ch&igrave;m trong phấn kh&iacute;ch ...</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau một tiếng nổ lớn, lối v&agrave;o ch&iacute;nh của nơi ở của Tần Nam bị vỡ th&agrave;nh nhiều mảnh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một giọng n&oacute;i khinh bỉ vang l&ecirc;n ngay sau đ&oacute;, &ldquo;Tần Nam, c&uacute;t qua đ&acirc;y. C&ograve;n kh&ocirc;ng th&igrave; đừng tr&aacute;ch ta v&ocirc; ơn! &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người mặc một chiếc &aacute;o cho&agrave;ng sang trọng v&agrave; vẻ mặt ki&ecirc;u h&atilde;nh. Sau lưng anh ta, một thanh kiếm d&agrave;i 5 feet bay lơ lửng giữa kh&ocirc;ng trung đồng thời ph&aacute;t ra ba tia s&aacute;ng m&agrave;u v&agrave;ng. N&oacute; được đi k&egrave;m với h&agrave;o quang băng gi&aacute; .</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-1734d512-7fff-4bc5-b4e6-073bff65c116\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Vẻ mặt của Tần Nam trở n&ecirc;n lạnh l&ugrave;ng sau khi nh&igrave;n thấy anh ta, &quot;Tần Hiểu, anh l&agrave;m g&igrave; ở đ&acirc;y?&quot;</span></span></p>\r\n', 'Cà Di', '2020-10-19 03:46:56', 1, 0, 11);
INSERT INTO `chapters` (`id`, `name`, `slug`, `site_title`, `meta_desc`, `meta_key`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(205, 'Chương 4: Võ kỹ', 'chuong-4-vo-ky', NULL, NULL, NULL, 36, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam kh&ocirc;ng chỉ biết Tần Hiểu n&agrave;y l&agrave; ai, đối với hắn v&ocirc; c&ugrave;ng quen thuộc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trước đ&acirc;y khi Tần Nam đ&atilde; l&agrave; thi&ecirc;n t&agrave;i h&agrave;ng đầu, khiến hắn ta trở th&agrave;nh &aacute;nh đ&egrave;n s&acirc;n khấu trong số c&aacute;c đệ tử của Tần tộc, th&igrave; Tần Hiểu n&agrave;y đ&atilde; rất ng&ocirc;ng ngh&ecirc;nh. Ng&agrave;y trước, Tần Nam đối xử tử tế với hắn, thậm ch&iacute; c&ograve;n coi hắn như anh trai.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng m&agrave;, Tần Nam chưa bao giờ nghĩ Tần Hiểu sẽ ph&aacute; cửa m&agrave; d&ugrave;ng lời n&oacute;i kh&oacute; nghe như vậy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay l&uacute;c đ&oacute;, Tần Nam đ&atilde; nhận ra. B&acirc;y giờ hắn đ&atilde; biến th&agrave;nh &lsquo;một thứ r&aacute;c rưởi&rsquo;, Tần Hiểu đ&atilde; quyết định quay lưng lại với hắn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Hiểu n&oacute;i với vẻ tự h&agrave;o, &ldquo;T&ocirc;i đến đ&acirc;y v&igrave; một l&yacute; do đơn giản. Đưa cho ta đan dược của ngươi. Đừng n&oacute;i dối ta. Ta biết ngươi đ&atilde; nhận được mười vi&ecirc;n đan dược. &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Anh do dự một l&uacute;c, trước khi n&oacute;i với giọng khinh thường, &ldquo;Tần Nam, đừng tr&aacute;ch ta nhẫn t&acirc;m. B&acirc;y giờ ngươi chẳng l&agrave; g&igrave; ngo&agrave;i r&aacute;c rưởi, thật l&atilde;ng ph&iacute; nếu ngươi giữ những Vi&ecirc;n đan dược đ&oacute;. Tốt hơn l&agrave; ngươi n&ecirc;n đưa ch&uacute;ng đi &rdquo;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam lập tức bật cười, &quot;Nếu ta kh&ocirc;ng đưa th&igrave; sao?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi nghe lời n&agrave;y, vẻ mặt của Tần Hiểu được thay thế bằng vẻ lạnh l&ugrave;ng, &ldquo;Tần Nam, nếu ngươi kh&ocirc;ng giao nộp ch&uacute;ng, t&ocirc;i sẽ kh&ocirc;ng nương tay với ngươi . Mặc d&ugrave; ta vẫn chưa c&oacute; tu luyện g&igrave;, nhưng V&otilde; kh&iacute; của ta l&agrave; cấp ba! &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thanh kiếm đằng sau hắn ta ngay lập tức bay l&ecirc;n kh&ocirc;ng trung đồng thời ph&aacute;t ra &aacute;nh s&aacute;ng băng gi&aacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Tần Hiểu, xem ra ngươi đ&atilde; hạ quyết t&acirc;m.&quot; Tần Nam vẻ mặt b&igrave;nh tĩnh, kh&ocirc;ng hề c&oacute; biểu hiện bị uy hiếp đối mặt với kiếm. &ldquo;Ta đ&atilde; uống đan dược rồi. Ngươi n&ecirc;n rời đi ngay b&acirc;y giờ &rdquo;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam n&oacute;i xong, nhắm mắt lại, trong l&ograve;ng thở d&agrave;i một hơi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kh&ocirc;ng nghi ngờ g&igrave; nữa Tần Nam đ&atilde; rất tức giận sau khi nh&igrave;n thấy h&agrave;nh vi khinh bỉ của Tần Hiểu. B&igrave;nh thường, hắn nhất định sẽ đ&aacute;nh gục cậu, nhưng sau khi nhớ lại mối quan hệ m&agrave; họ đ&atilde; c&oacute; trước đ&acirc;y, hắn kh&ocirc;ng thể l&agrave;m như vậy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Vẻ mặt của Tần Hiểu thay đổi. Một s&aacute;t kh&iacute; bộc ph&aacute;t từ cơ thể hắn , &ldquo;Tần Nam, ngươi phải trả gi&aacute; cho điều đ&oacute;. H&ocirc;m nay ta sẽ dạy cho ngươi b&agrave;i học giữa V&otilde; kh&iacute; cấp 3 v&agrave; V&otilde; kh&iacute; cấp một h&ocirc;m nay! &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thanh kiếm sau lưng Tần Hiểu ph&aacute;t ra một tiếng vang, trước khi biến th&agrave;nh một tia s&aacute;ng băng gi&aacute; ch&eacute;m v&agrave;o b&oacute;ng d&aacute;ng của Tần Nam.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ban đầu, Tần Hiểu kh&ocirc;ng định tấn c&ocirc;ng Tần Nam, kh&ocirc;ng phải v&igrave; mối quan hệ của hai người, m&agrave; l&agrave; do hắn ta vẫn c&ograve;n bị uy hiếp bởi th&acirc;n phận của Tần Nam.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng m&agrave;, Tần Hiểu kh&ocirc;ng ngờ Tần Nam sẽ c&oacute; th&aacute;i độ với hắn cho d&ugrave; hắn b&acirc;y giờ chẳng qua l&agrave; c&aacute;i thứ r&aacute;c rưởi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Tần Nam, ngươi đang t&igrave;m c&aacute;i chết!&quot; Tần Hiểu c&oacute; thể nh&igrave;n thấy một nụ cười gớm ghiếc. Giờ đ&acirc;y hắn c&oacute; thể tưởng tượng ra cảnh tượng thi&ecirc;n t&agrave;i h&agrave;ng đầu trước đ&oacute; đang đau đớn cầu xin hắn tha thứ trong gi&acirc;y l&aacute;t.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;L&agrave;m phiền!&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam kh&ocirc;ng chịu nổi nữa. Cơn giận dữ trong l&ograve;ng hắn ho&agrave;n to&agrave;n b&ugrave;ng nổ khi hắn h&eacute;t l&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn ta tiến một bước lớn về ph&iacute;a trước một c&aacute;ch hung dữ v&agrave; tung một c&uacute; đấm về ph&iacute;a trước như một nh&aacute;t b&uacute;a gi&aacute;ng v&agrave;o thanh kiếm. Sức mạnh của Cảnh giới Luyện kh&iacute; tầng thứ nhất đ&atilde; ph&aacute;t huy hết t&aacute;c dụng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kỹ năng V&otilde; thuật cấp thấp, Nắm đấm!</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nụ cười tr&ecirc;n mặt Tần Hiểu lập tức đ&ocirc;ng cứng lại, để lại vẻ kinh ngạc khi hắn kh&ocirc;ng kịp phản ứng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam tu luyện đ&atilde; đạt tới Cảnh giới luyện h&oacute;a th&acirc;n thể tầng thứ nhất?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay khi &yacute; nghĩ đ&oacute; xuất hiện trong đầu, thanh kiếm đ&atilde; bị c&uacute; đấm của Tần Nam đ&aacute;nh bay m&agrave; kh&ocirc;ng hề c&oacute; ch&uacute;t phản kh&aacute;ng n&agrave;o, tiếp theo l&agrave; một tiếng va chạm lớn, khiến n&oacute; rung l&ecirc;n mạnh mẽ trong khi &aacute;nh s&aacute;ng tr&ecirc;n bề mặt trở n&ecirc;n cực kỳ mờ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;AH&hellip;&rdquo; Một tiếng k&ecirc;u thống khổ bật ra từ miệng Tần Hiểu. V&igrave; V&otilde; khi của hắn ta đ&atilde; bị tổn thương nghi&ecirc;m trọng, cơ thể của hắn ta cũng bị thương.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam lạnh l&ugrave;ng nh&igrave;n hắn ta v&agrave; n&oacute;i, &quot;N&oacute;i cho ta biết b&acirc;y giờ, ai giống một thứ r&aacute;c rưởi hơn.&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Ngươi&hellip; Ngươi&hellip; Ngươi&hellip;&rdquo; Tần Hiểu &ocirc;m lấy ngực sắc mặt t&aacute;i nhợt. Niềm tự h&agrave;o trước đ&oacute; trong mắt hắn ta đ&atilde; được thay thế bằng sự kinh ho&agrave;ng v&agrave; kinh ngạc, &ldquo;L&agrave;m thế n&agrave;o m&agrave; ngươi lại thăng hạng đến Cảnh giới luyện th&acirc;n tầng một? Ngươi chỉ c&oacute; một V&otilde; Thần cấp một v&ocirc; dụng, c&ograve;n ta &mdash; với một V&otilde; Thần cấp ba - thậm ch&iacute; c&ograve;n chưa l&ecirc;n hạng, l&agrave;m sao c&oacute; thể !? &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Hiểu kh&ocirc;ng thể hiểu chuyện g&igrave; đang x&atilde;y ra.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi thức tỉnh một V&otilde; Thần cấp ba , hắn đ&atilde; rất chăm chỉ tu luyện. Hắn ta thậm ch&iacute; đ&atilde; ti&ecirc;u thụ một vi&ecirc;n thuốc cường h&oacute;a cơ thể, nhưng vẫn chưa l&ecirc;n hạng. Tần Nam l&agrave;m sao c&oacute; thể thăng hạng trước hắn?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ta đ&atilde; đề cập rằng ta đ&atilde; ti&ecirc;u thụ tất cả c&aacute;c loại đan dược.&quot; Tần Nam nh&igrave;n tho&aacute;ng qua hắn n&oacute;i: &quot;C&uacute;t đi!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Những lời n&oacute;i như một sắc lệnh ban h&agrave;nh. Tần Hiểu mặc kệ nỗi đau đang chịu đựng m&agrave; đi ra khỏi nơi ở, kh&ocirc;ng d&aacute;m liếc lại.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ban đầu hắn cho rằng m&igrave;nh c&oacute; thể đ&aacute;nh bại Tần Nam bằng c&aacute;ch lạm dụng V&otilde; kh&iacute; cấp ba của m&igrave;nh, cho rằng Tần Nam sẽ kh&ocirc;ng thể thăng cấp trước hắn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nếu Tần Hiểu ở lại chỗ đ&oacute;, hắn kh&ocirc;ng thể tưởng tượng Tần Nam sẽ l&agrave;m g&igrave; hắn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">To&agrave;n bộ gia vi&ecirc;n nhanh ch&oacute;ng ch&igrave;m v&agrave;o im lặng sau khi Tần Hiểu rời đi. Tần Nam đứng tại chỗ thở d&agrave;i một hơi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Theo &yacute; kiến ​​của Tần Nam, hắn sẽ chỉ t&ocirc;n trọng những người t&ocirc;n trọng hắn. Đối với kẻ v&ocirc; ơn bạc nghĩa như Tần Hiểu, ngo&agrave;i cảm thấy thương hại hắn, Tần Nam kh&ocirc;ng c&ograve;n coi l&agrave; anh trai nữa.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nếu như hắn lại đụng phải Tần Hiểu, nếu hắn kh&ocirc;ng c&oacute; biểu hiện hối hận, Tần Nam sẽ kh&ocirc;ng c&ograve;n c&oacute; ch&uacute;t thương x&oacute;t đối với hắn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ta đo&aacute;n ta sẽ kh&ocirc;ng thể tu luyện h&ocirc;m nay.&quot; Tần Nam cười khổ lắc đầu nh&igrave;n c&aacute;nh cửa vỡ n&aacute;t. Hắn sẽ kh&ocirc;ng muốn bất kỳ ai nh&igrave;n thấy Chiến kh&iacute; của hắn khi hắn tu luyện.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Vẫn chưa đến l&uacute;c n&oacute;i cho mọi người biết b&iacute; mật về Thần Trận Đồ .</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Ch&agrave;, ta đo&aacute;n ta sẽ đến thăm nơi c&oacute; V&otilde; Kỹ. Ta n&ecirc;n được ph&eacute;p v&agrave;o tầng hai ngay b&acirc;y giờ. &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam &aacute;nh mắt xẹt qua hưng phấn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hắn đ&atilde; rất quan t&acirc;m đến V&otilde; Kỹ từ khi c&ograve;n nhỏ, do đ&oacute; hắn đ&atilde; th&agrave;nh thạo mười V&otilde; Kỹ v&agrave;o năm mười ba tuổi, v&agrave; đ&atilde; t&igrave;m c&aacute;ch ph&aacute;t minh ra </span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">kiếm thuật </span><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">của ri&ecirc;ng m&igrave;nh v&agrave;o năm mười s&aacute;u tuổi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nắm đấm m&agrave; hắn sử dụng ng&agrave;y h&ocirc;m nay l&agrave; một trong những v&otilde; kỹ m&agrave; hắn đ&atilde; học khi mới mười ba tuổi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, v&igrave; trước đ&oacute; Tần Nam vẫn chưa thức tỉnh V&otilde; kh&iacute; của m&igrave;nh, n&ecirc;n hắn ta chỉ c&oacute; thể học được c&aacute;c v&otilde; kỹ cấp thấp trong tầng đầu ti&ecirc;n của v&otilde; kỹ. B&acirc;y giờ hắn đ&atilde; thức tỉnh V&otilde; kh&iacute; của m&igrave;nh v&agrave; sự tu luyện của hắn đ&atilde; đạt đến Cảnh giới luyện th&acirc;n tầng thứ nhất, hắn giờ đ&atilde; được ph&eacute;p tiến v&agrave;o c&aacute;c tầng cao hơn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam kh&ocirc;ng k&igrave;m được hứng th&uacute; nữa, vội v&agrave;ng đi về ph&iacute;a V&otilde; l&acirc;m viện.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&otilde; Đan được coi l&agrave; một nơi thi&ecirc;ng li&ecirc;ng đối với Tần tộc. Kh&ocirc;ng những kh&ocirc;ng được ph&eacute;p g&acirc;y ồn &agrave;o ở đ&oacute; m&agrave; c&ograve;n kh&ocirc;ng được ph&eacute;p d&ugrave;ng bạo lực. Nếu bất kỳ ai vi phạm c&aacute;c quy tắc, người đ&oacute; sẽ bị trừng phạt bằng c&aacute;ch bị cấm v&agrave;o V&otilde; l&acirc;m truyền kỳ trong suốt phần đời c&ograve;n lại của m&igrave;nh.Căn ph&ograve;ng v&otilde; kỹ được chia th&agrave;nh bốn tầng. Tầng đầu ti&ecirc;n chứa đầy V&otilde; kỹ cấp thấp, tầng thứ hai chứa đầy V&otilde; kỹ trung cấp, trong khi V&otilde; kỹ cấp cao được đặt ở tầng ba.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Theo lời đồn đại, tầng thứ tư c&oacute; một V&otilde; Kh&iacute; tối thượng, được coi l&agrave; bảo vật gia truyền của Tần tộc. Ngay cả thi&ecirc;n t&agrave;i h&agrave;ng đầu Tần tộc hiện tại cũng kh&ocirc;ng c&oacute; tư c&aacute;ch tiến v&agrave;o tầng bốn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">N&oacute;i như vậy, kh&ocirc;ng ai biết liệu những tin đồn c&oacute; n&oacute;i thật hay kh&ocirc;ng.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi Tần Nam đến V&otilde; qu&aacute;n, xung quanh kh&ocirc;ng c&oacute; một b&oacute;ng người n&agrave;o, một sự tương phản lớn so với cảnh thường thấy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, điều n&agrave;y cũng nằm trong dự đo&aacute;n của Tần Nam. Rốt cuộc, chỉ những người c&oacute; tu luyện Cảnh giới luyện h&oacute;a cơ thể tầng thứ nhất trở l&ecirc;n mới được ph&eacute;p v&agrave;o tầng thứ hai.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&aacute;c đệ tử của Tần tộc chỉ mới đ&aacute;nh thức V&otilde; linh của họ hai ng&agrave;y trước. Chỉ một số &iacute;t c&oacute; thể thăng cấp đến Cảnh giới luyện th&acirc;n tầng một.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một l&atilde;o gi&agrave; t&oacute;c trắng ngồi b&ecirc;n trong V&otilde; qu&aacute;n giật m&igrave;nh khi nh&igrave;n thấy Tần Nam, &quot;Tần Nam, sao ngươi lại ở đ&acirc;y?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam kh&aacute; quen thuộc với l&atilde;o gi&agrave; t&oacute;c trắng n&agrave;y. Hắn ta lập tức chắp hai tay lại, &ldquo;Trưởng l&atilde;o, ta đến đ&acirc;y để v&agrave;o tầng hai của V&otilde; qu&aacute;n.&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Tầng hai?&quot; L&atilde;o Ưng sững sờ trước khi sắc mặt trở n&ecirc;n lạnh l&ugrave;ng, &ldquo;Tần Nam, đừng nghĩ rằng ta sẽ cho ph&eacute;p ngươi v&agrave;o tầng hai chỉ v&igrave; ch&uacute;ng ta kh&aacute; th&acirc;n với nhau. Ngươi sẽ phải thăng cấp đến Cảnh giới luyện th&acirc;n tầng thứ nhất trước khi được ph&eacute;p v&agrave;o tầng thứ hai! &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">N&oacute;i xong, L&atilde;o Ưng nhớ lại chuyện Tần Nam đ&atilde; đ&aacute;nh mất danh hiệu thi&ecirc;n t&agrave;i, lập tức đưa ra một lời khuy&ecirc;n: &ldquo;Tần Nam, mặc d&ugrave; ngươi chỉ c&oacute; thể đ&aacute;nh thức một cấp một V&otilde; Kh&iacute;, nhưng đừng qu&aacute; thất vọng. Miễn l&agrave; ngươi l&agrave;m việc chăm chỉ&hellip; &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam kh&ocirc;ng n&oacute;i n&ecirc;n lời khi nghe điều n&agrave;y. Hắn ta bước tới v&agrave; d&ugrave;ng tay nắm lấy chiếc b&agrave;n gỗ, tr&ecirc;n đ&oacute; để lại dấu năm ng&oacute;n tay.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&ldquo;Ngươi&hellip;&rdquo; L&atilde;o Ưng sửng sốt, &ldquo;Tu luyện của ngươi đ&atilde; xếp đến Cảnh giới luyện th&acirc;n tầng thứ nhất?&rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam kh&ocirc;ng trả lời c&acirc;u hỏi của Trưởng l&atilde;o. Hắn ta mỉm cười v&agrave; tiến l&ecirc;n tầng thứ hai của V&otilde; qu&aacute;n, bỏ lại Ưng trưởng l&atilde;o ho&agrave;n to&agrave;n sững sờ ở ph&iacute;a sau.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam vừa đến lầu hai, hai mắt s&aacute;ng ngời.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tầng hai được chống đỡ với t&aacute;m c&acirc;y cột l&agrave;m bằng gỗ đỏ. Trong khi đ&oacute;, những chiếc b&agrave;n l&agrave;m bằng đ&aacute; cẩm thạch trắng c&oacute; thể được nh&igrave;n thấy ở khắp mọi nơi, với những cuốn s&aacute;ch cổ chất chồng l&ecirc;n nhau.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam lập tức đi tới một trong những c&aacute;i b&agrave;n, lấy một quyển sổ tay hướng dẫn, trước khi một giọng n&oacute;i kinh ngạc vang l&ecirc;n, &quot;Tần Nam?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam bất gi&aacute;c quay đầu lại v&agrave; nh&igrave;n thấy một thanh ni&ecirc;n mặc &aacute;o cho&agrave;ng trắng được trang tr&iacute; bằng dấu vết bằng v&agrave;ng đang nh&igrave;n m&igrave;nh với vẻ kinh ngạc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam hơi sững sờ khi nh&igrave;n thấy người đ&agrave;n &ocirc;ng trẻ tuổi, trước khi n&oacute;i với giọng điệu b&igrave;nh tĩnh, &quot;Ta kh&ocirc;ng mong đợi nh&igrave;n thấy ngươi ở đ&acirc;y.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam cũng kh&aacute; quen thuộc với người trước mắt, t&ecirc;n l&agrave; Tần Vũ. Anh ta kh&ocirc;ng ai kh&aacute;c ch&iacute;nh l&agrave; anh trai của Tần Hiểu, người đ&atilde; quản l&yacute; để đ&aacute;nh thức một cấp V&otilde; kh&iacute; cấp bốn, v&agrave; do đ&oacute; được coi l&agrave; một thi&ecirc;n t&agrave;i trong số c&aacute;c đệ tử của Tần tộc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tuy nhi&ecirc;n, điều khiến Tần Nam kh&ocirc;ng n&oacute;i n&ecirc;n lời l&agrave; l&agrave;m sao b&acirc;y giờ hắn lại đ&acirc;m đầu v&agrave;o sư huynh .</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Vũ mang theo vẻ chế giễu sau khi nhận ra người trước mặt m&igrave;nh đ&uacute;ng l&agrave; Tần Nam, &quot;Ai c&oacute; thể nghĩ rằng một thứ r&aacute;c rưởi với V&otilde; kh&iacute; cấp bậc một lại c&oacute; thể thăng cấp đến Cảnh giới luyện th&acirc;n cấp một. . Ta tin rằng mười vi&ecirc;n đan dược m&agrave; trưởng tộc ban cho ngươi rất c&oacute; &iacute;ch. &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi Tần Vũ nhắc tới mười vi&ecirc;n đan dược, trong l&ograve;ng kh&ocirc;ng khỏi cảm thấy tức giận. Mặc d&ugrave; đ&atilde; đ&aacute;nh thức một cấp V&otilde; kh&iacute; cấp bốn, nhưng hắn mỗi th&aacute;ng chỉ được cấp ba vi&ecirc;n đan dược, thậm ch&iacute; kh&ocirc;ng bằng một nửa những g&igrave; Tần Nam được tặng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Như vậy, Tần Vũ kh&ocirc;ng khỏi chế nhạo Tần Nam sau khi đ&acirc;m v&agrave;o hắn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tần Nam b&igrave;nh tĩnh nh&igrave;n anh ta v&agrave; n&oacute;i: &ldquo;Đ&uacute;ng vậy, ch&uacute;ng kh&aacute; hữu &iacute;ch. Ta thậm ch&iacute; đ&atilde; đ&aacute;nh bại anh trai của ta. &rdquo;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-8667dd6b-7fff-eb28-146d-bfd56eefbb29\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Vẻ mặt của Tần Vũ đanh lại sau khi nghe điều n&agrave;y.</span></span></p>\r\n', 'Cà Di', '2020-10-19 03:47:26', 1, 0, 11),
