INSERT INTO `chapters` (`id`, `name`, `slug`, `site_title`, `meta_desc`, `meta_key`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(210, 'Chương 1: Thợ săn hạng E', 'chuong-1-tho-san-hang-e-1', NULL, NULL, NULL, 49, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Moon&quot;sNovel</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Thợ săn hạng E&quot; Sung Jinwoo.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Danh hiệu ấy theo anh ta đến mọi nơi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">D&ugrave; l&agrave; một Thợ Săn, năng lực của anh ta chỉ hơn người thường một ch&uacute;t. Nếu kh&ocirc;ng c&oacute; khả năng chịu đựng v&agrave; khả năng hồi phục cao hơn ch&uacute;t x&iacute;u, th&igrave; anh ta cũng chẳng kh&aacute;c người thường l&agrave; bao. Th&agrave;nh ra, sự nghiệp l&agrave;m Thợ Săn của anh ta gắn liền với v&ocirc; số thương t&iacute;ch. Thậm ch&iacute; v&agrave;i lần anh ta đặt ch&acirc;n đến ranh giới giữa sự sống v&agrave; c&aacute;i chết.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Jinwoo kh&ocirc;ng trở th&agrave;nh Thợ Săn v&igrave; y&ecirc;u th&iacute;ch c&ocirc;ng việc đ&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một c&ocirc;ng việc nguy hiểm, cam go, m&agrave; thu nhập th&igrave; đ&aacute;ng thất vọng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kh&ocirc;ng. Nếu kh&ocirc;ng phải v&igrave; Hiệp hội Thợ Săn hỗ trợ chi trả viện ph&iacute; cho mẹ anh ta, c&oacute; lẽ Jinwoo đ&atilde; từ bỏ v&agrave; chọn một nghề b&igrave;nh thường như bao người kh&aacute;c rồi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khổ nỗi, một ch&agrave;ng trai tuổi đ&ocirc;i mươi như anh ta, kh&ocirc;ng c&oacute; t&agrave;i năng lẫn người &quot;chống lưng&quot;, l&agrave;m sao trang trải nổi viện ph&iacute; gi&aacute; v&agrave;i triệu Won mỗi th&aacute;ng?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&oacute; thể n&oacute;i. anh ta chẳng c&oacute; lựa chọn n&agrave;o kh&aacute;c.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">V&agrave; rồi, như mọi ng&agrave;y, với tr&aacute;i tim đang kh&oacute;c thầm, Jinwoon tham gia v&agrave;o một cuộc đột k&iacute;ch do Hiệp hội gi&aacute;m s&aacute;t.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">- -</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&aacute;c thợ Săn trong c&ugrave;ng một khu vực đều biết r&otilde; nhau. Bởi họ đều tập trung mỗi khi một Cổng mở ra. V&agrave; h&ocirc;m nay, những Thợ Săn tụ tập trước cổng, vừa uống c&agrave; ph&ecirc; vừa ch&agrave;o hỏi lẫn nhau.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;&Ocirc;i, anh Kim! B&ecirc;n n&agrave;y n&egrave;&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ồ, Park! Anh đang l&agrave;m g&igrave; ở đ&acirc;y vậy? T&ocirc;i tưởng anh bỏ nghề rồi chứ.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;&Agrave;... Vợ t&ocirc;i vừa mang thai đứa con thứ hai&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Hahaha, vậy sao? Ừ, c&aacute;ch tốt nhất để lấp đầy t&uacute;i một thợ săn l&agrave; tham gia đột k&iacute;ch m&agrave;!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cả hai c&ugrave;ng cười lớn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Anh c&oacute; để &yacute; kh&ocirc;ng, h&igrave;nh như gần đ&acirc;y Hiệp hội ng&agrave;y c&agrave;ng &iacute;t gọi ch&uacute;ng ta. Phải chăng số lượng Cổng xuất hiện đ&atilde; giảm đi?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ể. L&agrave;m g&igrave; c&oacute;. Đ&oacute; l&agrave; v&igrave; gần đ&acirc;y c&aacute;c C&ocirc;ng hội đ&atilde; t&iacute;ch cực hoạt động hơn Hiệp hội. Mỗi lần c&oacute; cơ hội vớ bẫm l&agrave; c&aacute;c C&ocirc;ng hội lại nhảy dựng l&ecirc;n với con mắt rực lửa.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ừm. Cuộc đột k&iacute;ch n&agrave;y được gi&aacute;m s&aacute;t bởi Hiệp h&ocirc;i, chắc l&agrave; n&oacute; tương đối an to&agrave;n nhỉ?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Với một ch&uacute;t lo lắng, Park đảo mắt nh&igrave;n xung quanh. Một Cổng bị lờ đi một C&ocirc;ng hội nghĩa l&agrave; n&oacute; c&oacute; thu nhập thấp, v&agrave; thu nhập thấp nghĩa l&agrave; độ kh&oacute; thấp. Tất nhi&ecirc;n, ở đời chẳng c&oacute; g&igrave; chắc 100%. Giống như Park, c&aacute;c thợ săn kh&aacute;c đảo mắt xung quanh với vẻ căng thẳng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;C&oacute; lẽ vậy...&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Uống nốt cốc c&agrave; ph&ecirc;, Kim vẫy tay ch&agrave;o một người vừa xuất hiện ph&iacute;a xa.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ồ, chờ đ&atilde;. Xem ai k&igrave;a! Cậu Sung. Sung ơi!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&aacute;c Thợ Săn kh&aacute;c cũng nh&igrave;n người vừa đến với vẻ quen thuộc v&agrave; thư th&aacute;i.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Xin ch&agrave;o.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&oacute; l&agrave; Sung Jinwoo.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&aacute;p lại Kim với một c&aacute;i gật đầu, Jinwoo tiếp tục bước đi. Khi anh ta đi qua, Kim cười khẩy v&agrave; bớt lo &acirc;u.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Jinwoo đ&atilde; đến. Vậy nơi n&agrave;y chắc l&agrave; an to&agrave;n.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Park tr&ograve;n mắt h&ograve;i Kim.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Sao? Tay Sung Jinwoo ấy mạnh thế cơ &agrave;?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;&Agrave;, anh Park kh&ocirc;ng biết Jinwoo nhỉ. Anh ta v&agrave;o nghề sau khi anh nghỉ. Ở đ&acirc;y chẳng ai kh&ocirc;ng biết đến danh tiếng Jung Jinwoo&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Mạnh thế ư? Vậy sao anh ta lại l&agrave;m việc cho Hiệp hội? Sao kh&ocirc;ng l&agrave;m tự do hoặc tham gia một C&ocirc;ng hội lớn?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kim vừa cười vừa nheo mắt.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Anh biết biệt danh của cậu ta l&agrave; g&igrave; kh&ocirc;ng?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ai m&agrave; biết được? Th&ocirc;i n&agrave;o, n&oacute;i đại đi.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Thợ Săn yếu nhất&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;... Yếu nhất? Kh&ocirc;ng phải mạnh nhất sao?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;H&acirc;m &agrave;. Danh hiệu mạnh nhất chỉ d&agrave;nh cho những Thợ Săn hạng S th&ocirc;i. C&ograve;n t&ecirc;n kia l&agrave; Kẻ Yếu Nhất. C&oacute; lẻ l&agrave; thợ săn yếu nhất cả H&agrave;n Quốc.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ể?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Park cau m&agrave;y. Nếu anh ta yếu thế, sao mọi người đều tỏ ra nhẹ nhỏm khi anh ta xuất hiện? Một thợ săn được chờ đợi, phải l&agrave; một người đ&aacute;ng tin cậy trong một cuộc đột k&iacute;ch chứ. Park kh&ocirc;ng hiểu nổi biểu cảm của những Thợ Săn kia.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thấy Park lắc đầu ngạc nhi&ecirc;n, Kim kho&aacute;c vai anh ta v&agrave; cười.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Anh kh&ocirc;ng hiểu sao? Nếu như Sung Jinwoo xuất hiện ở cuộc đột k&iacute;ch h&ocirc;m nay, tức l&agrave; n&oacute; rất dễ. Hiệp hội sẽ kh&ocirc;ng mạo hiểm để anh ta tham gia mấy vụ kh&oacute; nhằn. Chả ai muốn thấy người kh&aacute;c chết một c&aacute;ch v&ocirc; nghĩa.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nghe vậy, khu&ocirc;n mặt Park trở n&ecirc;n tươi tỉnh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ồ, vậy sao?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Quay với c&aacute;c cuộc đột k&iacute;ch sau một thời gian d&agrave;i nghỉ việc, cả Park v&agrave; vợ đều thấy kh&oacute; khăn v&agrave; lo lắng. Nhưng khi nghe những g&igrave; Kim n&oacute;i, t&acirc;m trạng anh ta trở n&ecirc;n thoải m&aacute;i hơn.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kim tiếp tục.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;C&oacute; lần, anh ta bị thương trong một Cổng hạng E v&agrave; phải nhập viện cả tuần liền.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Một Thợ Săn bị thương ở Cổng hạng E?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Thế đ&oacute;. Chả ai ngờ rằng c&oacute; người thật sự bị thương ở Cổng hạng E. Nghe n&oacute;i họ c&ograve;n chẳng mang theo Thợ Săn lớp Trị Liệu&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thấy Park cười lớn, Kim đưa mắt liếc nh&igrave;n Jinwoo.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;N&agrave;o n&agrave;o, đừng to thế chứ. Cậu ta c&oacute; thể nghe thấy đấy&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ờ h&aacute;, qu&ecirc;n mất nhỉ!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Park nh&igrave;n sang Jinwoo v&agrave; ngừng cười. May sao Jinwoo đứng kh&aacute; xa v&agrave; c&oacute; lẻ chả nghe được cuộc n&oacute;i chuyện của họ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng thực ra th&igrave;....</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;T&ocirc;i nghe thấy hết đấy, mấy l&atilde;o gi&agrave;&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cố l&agrave;m lơ những tiếng cười, anh ta bắt gặp c&aacute;i liếc nh&igrave;n v&agrave; nụ cười ngạo mạn của họ. Những l&uacute;c như n&agrave;y, Jinwoo nguyền rủa th&iacute;nh gi&aacute;c nhạy b&eacute;n của m&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;M&igrave;nh đến sớm qu&aacute; ư?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ograve;n ch&uacute;t thời gian trước khi bắt đầu tập k&iacute;ch. Trong khi chờ đợi, Jinwoo đến chỗ nh&acirc;n vi&ecirc;n của Hiệp hội để uống t&iacute; c&agrave; ph&ecirc;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Cho t&ocirc;i một ly nh&eacute;?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;&Agrave;, Thợ Săn Sung Jinwoo... T&ocirc;i xin lỗi, nhưng hết rồi ạ...&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;...&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Gi&oacute; đ&ocirc;ng lạnh lẽo phả v&agrave;o mặt Jinwoo. Anh ta đưa ng&oacute;n tay ch&ugrave;i mũi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một ng&agrave;y đ&aacute;ng buồn, khi cả c&agrave; ph&ecirc; cũng hết ngay trước mũi anh ta.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">====================</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Sao anh cứng đầu về chuyện l&agrave;m Thợ Săn thế, Jinwoo?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Xin lỗi&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Jinwoo c&uacute;i đầu.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lee Juhee vừa trị thương cho anh ta, vừa tỏ vẻ kh&oacute; chịu.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;T&ocirc;i kh&ocirc;ng cần mấy lời xin lỗi của anh. T&ocirc;i chỉ cảm thấy lo cho anh th&ocirc;i, anh biết kh&ocirc;ng? T&ocirc;i thề, cứ thế n&agrave;y c&oacute; ng&agrave;y anh bị thương nặng thật đ&oacute;.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bước qua c&aacute;nh cổng, Cổng bạn sẽ ở b&ecirc;n trong Hầm ngục. Hầm ngục lần n&agrave;y c&oacute; lẽ tầm hạng D.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nh&igrave;n qua vai Junhee, Jinwoo quan s&aacute;t những đồng nghiệp của m&igrave;nh. Khoảng mười Thợ săn đang ti&ecirc;u diệt lũ qu&aacute;i vật xung quanh. Tr&ocirc;ng họ kh&ocirc;ng mấy chật vật. Vậy m&agrave; anh ta lại bị thương.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhiệm vụ của Thợ Săn lớp Trị Liệu l&agrave; đứng ngo&agrave;i v&ograve;ng chiến v&agrave; chữa trị cho c&aacute;c Thợ Săn bị thương. L&agrave; người thường xuy&ecirc;n gặp nạn trong c&aacute;c cuộc tập k&iacute;ch, n&ecirc;n Jinwoo kh&aacute; nổi tiếng giữa c&aacute;c Trị Liệu Sư.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Juhee cẩn thận hỏi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Phải chăng c&oacute; l&yacute; do g&igrave; khiến anh kh&ocirc;ng thể bỏ nghề?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Jinwoo lắc đầu. Anh ta kh&ocirc;ng phải l&agrave; loại người dễ d&agrave;ng tiết lộ chuyện ri&ecirc;ng tư.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;T&ocirc;i l&agrave;m v&igrave; sở th&iacute;ch th&ocirc;i. Kh&ocirc;ng c&oacute; c&aacute;i nghề n&agrave;y, c&oacute; lẽ t&ocirc;i sẽ chết v&igrave; ch&aacute;n mất!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Juhee tỏ ra kh&oacute; chịu.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;C&oacute; ng&agrave;y anh sẽ được sang kiếp sau để tập k&iacute;ch v&igrave; c&aacute;i sở th&iacute;ch n&agrave;y đấy!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bất ngờ với phản ứng đ&oacute;, Jinwoo cười.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;N&agrave;y, đừng cười. Hở vết thương b&acirc;y giờ!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Jinwoo vừa cười kh&uacute;c kh&iacute;ch vừa hỏi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;N&agrave;y. c&ocirc; nghe c&acirc;u đ&oacute; ở đ&acirc;u thế?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;C&ograve;n đ&acirc;u nữa? Từ anh Kim b&ecirc;n kia k&igrave;a.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Hừm, l&atilde;o gi&agrave; ấy...&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong l&uacute;c họ vừa tr&ograve; truyện vừa cười, việc trị thương đ&atilde; ho&agrave;n th&agrave;nh. Nhưng muộn rồi, cuộc tập k&iacute;ch gần như đ&atilde; kết th&uacute;c..</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;H&ocirc;m nay m&igrave;nh chỉ giết được một ma th&uacute;...&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thậm ch&iacute;, n&oacute; chỉ l&agrave; một qu&aacute;i vật hạng E. Anh ta tung cục ma thạch hạng E trong tay. Cục ma thạch từ ma th&uacute; hạng E chỉ đ&aacute;ng gi&aacute; gần 100.000 won. Thật thảm hại khi anh ta đ&atilde; phải mạo hiểm cả mạng sống của m&igrave;nh, để rồi chỉ nhận lại được chừng đ&oacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Nghe n&oacute;i ma thạch từ ma th&uacute; hạng C đ&aacute;ng gi&aacute; &iacute;t nhất v&agrave;i chục triệu won&quot;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng đối với Jinwoo, một thợ săn hạng E, chuyện hạ gục một ma th&uacute; hạng C l&agrave; bất khả thi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đột nhi&ecirc;n, ai đ&oacute; la l&ecirc;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ể, ở đ&acirc;y c&ograve;n c&oacute; một c&aacute;nh cổng n&agrave;y!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&aacute;c thợ săn bu về ph&iacute;a giọng n&oacute;i ph&aacute;t ra.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Đ&uacute;ng rồi&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Thật hả? Một c&aacute;nh cổng nữa?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&uacute;ng như người đầu ti&ecirc;n nhận ra, quả thật c&ograve;n một c&aacute;nh cổng nữa ở đ&acirc;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Một Hầm ngục k&eacute;p... Vậy l&agrave; n&oacute; thật sự tồn tại&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&Ocirc;ng Song - một Thợ Săn với mười năm kinh nghiệm, nh&igrave;n v&agrave;o c&aacute;nh cổng mới ph&aacute;t hiện với vẻ băn khoăn. Kh&ocirc;ng thể nh&igrave;n s&acirc;u v&agrave;o hang động tăm tối, Song b&egrave;n sử dụng kĩ năng ph&eacute;p thuật lửa. Hỏa cầu từ b&agrave;n tay &ocirc;ng ta bay qua hang động v&agrave; xua đi b&oacute;ng tối. Tuy nhi&ecirc;n, hang động s&acirc;u hơn dự đo&aacute;n. Hỏa cầu hết năng lượng, rơi xuống đất v&agrave; vụt tắt. B&oacute;ng tối lại bao phủ tầm mắt mọi người.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Hmm... Mọi người, l&agrave;m ơn tập trung lại&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&agrave; đội trưởng của đội tập k&iacute;ch, Song tập trung mọi người trước mặt &ocirc;ng ta. Juhee v&agrave; Jinwoo, cũng đ&atilde; ho&agrave;n th&agrave;nh việc trị thương v&agrave; tập trung c&ugrave;ng mọi người.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Song n&oacute;i với mọi người.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Như mọi người đ&atilde; biết, Cổng sẽ kh&ocirc;ng đ&oacute;ng lại trừ khi Tr&ugrave;m Hầm ngục bị hạ. Hiện Cổng n&agrave;y vẫn c&ograve;n mở, d&ugrave; ch&uacute;ng ta đ&atilde; giết hết mọi thừ trong khu vực, c&oacute; lẽ con Tr&ugrave;m thật đang nằm trong đường hầm đ&oacute;.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Song đưa mắt về ph&iacute;a c&aacute;nh cổng mới ph&aacute;t hiện. C&aacute;c thợ săn nh&igrave;n nhau v&agrave; gật đầu đồng t&igrave;nh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Th&ocirc;ng thường, thủ tục trong những th&igrave;nh huống như thế n&agrave;y l&agrave; tho&aacute;t khỏi Hầm ngục, li&ecirc;n hệ với Hiệp hội v&agrave; để họ xử l&yacute; tiếp. Nhưng nếu ta l&agrave;m thế, v&agrave; nh&oacute;m thợ săn kh&aacute;c ti&ecirc;u diệt Tr&ugrave;m, c&oacute; thể thu nhập từ cuộc tập k&iacute;ch n&agrave;y của ch&uacute;ng ta sẽ giảm đ&aacute;ng kể.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&aacute;c Thợ Săn tỏ ra kh&oacute; chịu. Park - người c&oacute; mặt ở đ&acirc;y để kiếm tiền phụ vợ - tr&ocirc;ng rất băn khoăn. &quot;Chi ph&iacute; chăm s&oacute;c sản phụ b&acirc;y giờ rất đắt đỏ&quot; Nếu b&acirc;y giờ anh ta rời đi, những g&igrave; anh ta liều mạng sẽ trở th&agrave;nh c&ocirc;ng cốc.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;V&igrave; vậy t&ocirc;i nghĩ l&agrave;... Nếu ch&uacute;ng ta tiếp tục v&agrave; giết con Tr&ugrave;m th&igrave; sao? C&aacute;c anh nghĩ thế n&agrave;o?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&aacute;c Thợ Săn trầm tư suy nghĩ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;...&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;...&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&acirc;y kh&ocirc;ng phải l&agrave; một quyết định dễ d&agrave;ng. V&agrave;i người hiểu rằng, kh&ocirc;ng c&oacute; g&igrave; đảm bảo l&agrave; Hầm Ngục k&eacute;p sẽ an to&agrave;n như c&aacute;i họ vừa qu&eacute;t sạch. Một số th&igrave; nghĩ rằng, nếu c&aacute;i ở ngo&agrave;i dễ thế n&agrave;y th&igrave; c&aacute;i ở trong chắc cũng kh&ocirc;ng đến nỗi n&agrave;o. Tất nhi&ecirc;n, đ&acirc;y kh&ocirc;ng phải l&agrave; một t&igrave;nh huống m&agrave; tất cả mọi người sẽ đồng l&ograve;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Hmm...&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Song hắng giọng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Ở đ&acirc;y hiện c&oacute; 17 người, hay l&agrave; ta biểu quyết nh&eacute;? V&agrave; biểu quyết xong th&igrave; kh&ocirc;ng ph&agrave;n n&agrave;n g&igrave; cả. Thế n&agrave;o?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&aacute;c Thợ Săn đều gật đầu đồng &yacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Song l&agrave; người đầu ti&ecirc;n giơ tay. &quot;T&ocirc;i sẽ đi&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Park nối tiếp ngay sau Song, &quot;T&ocirc;i đi nữa&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;T&ocirc;i, t&ocirc;i nữa..&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Đ&acirc;y nữa.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Th&ecirc;m một người nữa&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kim cũng tham gia v&agrave;o nh&oacute;m đi. Nhưng cũng c&oacute; c&aacute;c &yacute; kiến tr&aacute;i chiều.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Kh&ocirc;ng&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;T&ocirc;i nghĩ ta n&ecirc;n đợi đ&aacute;nh gi&aacute; của Hiệp hội&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&uacute;c tỷ lệ đi/kh&ocirc;ng đi l&agrave; 8-7, tất cả quay lại nh&igrave;n 2 người chưa ra quyết định.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Xin lỗi...&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lắc đầu, Juhee chọn kh&ocirc;ng. Vậy l&agrave; tỷ lệ c&acirc;n bằng: 8-8.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Song quay sang hỏi người c&ograve;n lại.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Cậu Sung?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Quyết định cuối c&ugrave;ng của cả nh&oacute;m dựa v&agrave;o anh ta. Jinwoo tung cục ma thạch hạng E trong tay, nh&igrave;n sang b&ecirc;n cạnh. Juhee lắc đầu khi bắt gặp &aacute;nh mắt của anh.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-f51d638e-7fff-bfc2-57de-fc45996b18ac\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc; c&oacute; linh cảm xấu về chuyện n&agrave;y. Ngay cả Jinwoo cũng vậy. Trong mọi ho&agrave;n cảnh, anh ta kh&ocirc;ng d&aacute;m đặt m&igrave;nh v&agrave;o v&ograve;ng ', 'Jang Sung Lak', '2020-10-19 14:15:35', 1, 0, 39);
