(216, 'Chap 3.', 'chap-3-3', NULL, NULL, NULL, 50, '', 0, '<p>\r\n	<img alt=\"\" src=\"/public/upload/images/001%20%281%29.jpg\" style=\"width: 728px; height: 1209px;\" /><img alt=\"\" src=\"/public/upload/images/002%285%29.jpg\" style=\"width: 728px; height: 1181px;\" /><img alt=\"\" src=\"/public/upload/images/003%285%29.jpg\" style=\"width: 728px; height: 1217px;\" /><img alt=\"\" src=\"/public/upload/images/004%285%29.jpg\" style=\"width: 728px; height: 1254px;\" /><img alt=\"\" src=\"/public/upload/images/005%285%29.jpg\" style=\"width: 728px; height: 1158px;\" /><img alt=\"\" src=\"/public/upload/images/006%285%29.jpg\" style=\"width: 728px; height: 1115px;\" /><img alt=\"\" src=\"/public/upload/images/007%285%29.jpg\" style=\"width: 728px; height: 1136px;\" /><img alt=\"\" src=\"/public/upload/images/008%285%29.jpg\" style=\"width: 728px; height: 1170px;\" /><img alt=\"\" src=\"/public/upload/images/009%285%29.jpg\" style=\"width: 728px; height: 1131px;\" /><img alt=\"\" src=\"/public/upload/images/010%285%29.jpg\" style=\"width: 728px; height: 1176px;\" /><img alt=\"\" src=\"/public/upload/images/011%285%29.jpg\" style=\"width: 728px; height: 1194px;\" /><img alt=\"\" src=\"/public/upload/images/012%285%29.jpg\" style=\"width: 728px; height: 1140px;\" /><img alt=\"\" src=\"/public/upload/images/013%284%29.jpg\" style=\"width: 728px; height: 1166px;\" /><img alt=\"\" src=\"/public/upload/images/014%284%29.jpg\" style=\"width: 728px; height: 1173px;\" /><img alt=\"\" src=\"/public/upload/images/015%284%29.jpg\" style=\"width: 728px; height: 1193px;\" /><img alt=\"\" src=\"/public/upload/images/016%284%29.jpg\" style=\"width: 728px; height: 1118px;\" /><img alt=\"\" src=\"/public/upload/images/017%284%29.jpg\" style=\"width: 728px; height: 1246px;\" /><img alt=\"\" src=\"/public/upload/images/018%284%29.jpg\" style=\"width: 728px; height: 1174px;\" /><img alt=\"\" src=\"/public/upload/images/019%283%29.jpg\" style=\"width: 728px; height: 1200px;\" /><img alt=\"\" src=\"/public/upload/images/020%284%29.jpg\" style=\"width: 728px; height: 1150px;\" /><img alt=\"\" src=\"/public/upload/images/021%284%29.jpg\" style=\"width: 728px; height: 1194px;\" /><img alt=\"\" src=\"/public/upload/images/022%284%29.jpg\" style=\"width: 728px; height: 1219px;\" /><img alt=\"\" src=\"/public/upload/images/023%283%29.jpg\" style=\"width: 728px; height: 1196px;\" /><img alt=\"\" src=\"/public/upload/images/024%283%29.jpg\" style=\"width: 728px; height: 1149px;\" /><img alt=\"\" src=\"/public/upload/images/025%283%29.jpg\" style=\"width: 728px; height: 1184px;\" /><img alt=\"\" src=\"/public/upload/images/026%283%29.jpg\" style=\"width: 728px; height: 1249px;\" /><img alt=\"\" src=\"/public/upload/images/027%283%29.jpg\" style=\"width: 728px; height: 1244px;\" /><img alt=\"\" src=\"/public/upload/images/028%283%29.jpg\" style=\"width: 728px; height: 1074px;\" /><img alt=\"\" src=\"/public/upload/images/029%283%29.jpg\" style=\"width: 728px; height: 1145px;\" /><img alt=\"\" src=\"/public/upload/images/030%281%29.jpg\" style=\"width: 728px; height: 1215px;\" /><img alt=\"\" src=\"/public/upload/images/031%281%29.jpg\" style=\"width: 728px; height: 1079px;\" /><img alt=\"\" src=\"/public/upload/images/032%281%29.jpg\" style=\"width: 728px; height: 1256px;\" /></p>\r\n', 'Miruku Chan', '2020-10-21 09:32:33', 1, 0, 9),
(217, 'Chương 1: Còn muốn trốn sao?', 'chuong-1-con-muon-tron-sao', NULL, NULL, NULL, 51, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Edit: V&acirc;n Anh</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Diệp Oản Oản mở mắt.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đối mặt l&agrave; đ&ocirc;i m&ocirc;i khiến cho c&ocirc; sợ h&atilde;i đến ngay cả linh hồn đều run rẩy con ngươi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;A--&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&ocirc;i tay t&aacute;i nhợt của c&ocirc; g&aacute;i đột nhi&ecirc;n cầm c&aacute;i chăn ngăn chặn vật dưới th&acirc;n thể.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cảm gi&aacute;c t&ecirc; liệt thống khổ, rốt cuộc bản th&acirc;n vẫn sống sờ sờ m&agrave; vẫn phải trải qua một lần nữa.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chẳng lẽ nơi n&agrave;y l&agrave; Địa Ngục sao?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tại sao, r&otilde; r&agrave;ng c&ocirc; đ&atilde; chết, nhưng lại trở về nơi &agrave;y, trở lại b&ecirc;n cạnh t&ecirc;n &aacute;c ma n&agrave;y?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong đầu của c&ocirc; lập tức bị nhiệt độ n&oacute;ng bỏng của người đ&agrave;n &ocirc;ng bốc hơi l&ecirc;n đến hỗn loạn tưng bừng, theo bản năng chống cự, &quot;Đừng đụng v&agrave;o t&ocirc;i!&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Động t&aacute;c của người đ&agrave;n &ocirc;ng ngưng lại một hồi, ngay sau đ&oacute; giống như bị đụng v&agrave;o vảy ngược, khu&ocirc;n mặt giống quỷ lệ kh&aacute;t m&aacute;u nhất thời u &aacute;m, m&ocirc;i mỏng lạnh gi&aacute; t&agrave;n nhẫn cắn x&eacute; đi xuống, giống như muốn nuốt hết m&aacute;u v&agrave; xương của c&ocirc; v&agrave;o bụng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Diệp Oản Oản bị đau đến kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o suy nghĩ bất cứ chuyện g&igrave;, chỉ c&oacute; thể v&ocirc; &yacute; thức lầm bầm,&quot; Tại sao... tại sao l&agrave; t&ocirc;i... Tư Dạ H&agrave;n... Tại sao nhất định phải l&agrave; t&ocirc;i...&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Bởi v&igrave;, t&ocirc;i chỉ cần em.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&ecirc;n tai truyền tới thanh &acirc;m trầm thấp của người đ&agrave;n &ocirc;ng, giống như g&ocirc;ng xiềng, ngay cả linh hồn của c&ocirc; cũng đồng thời bị giam cầm.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nghe c&acirc;u trả lời của người đ&agrave;n &ocirc;ng c&ugrave;ng kiếp trước giống nhau như đ&uacute;c, Diệp Oản Oản ho&agrave;n to&agrave;n l&acirc;m v&agrave;o h&ocirc;n m&ecirc;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">...</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thời điểm mở mắt ra lần nữa, b&ecirc;n ngo&agrave;i cửa sổ đ&atilde; từ đ&ecirc;m tối biến th&agrave;nh ban ng&agrave;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong kh&ocirc;ng kh&iacute; tr&agrave;n ngập một cổ m&ugrave;i hoa, &aacute;nh mặt trời ấm &aacute;p xuy&ecirc;n qua chấn song rơi, khiến cho t&acirc;m t&igrave;nh của con người kh&ocirc;ng tự chủ thanh tĩnh lại.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng m&agrave;, một gi&acirc;y kế tiếp, Diệp Oản Oản đột nhi&ecirc;n thần kinh căng thẳng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cảm gi&aacute;c &aacute;p b&aacute;ch mạnh mẽ theo người đ&agrave;n &ocirc;ng tỉnh lại ở to&agrave;n bộ b&ecirc;n trong kh&ocirc;ng gian lan tr&agrave;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&ecirc;n h&ocirc;ng đột nhi&ecirc;n bị một c&aacute;nh tay nắm chặt, c&ocirc; giống như l&agrave; gối &ocirc;m, bị &ocirc;m v&agrave;o trong ngực của người đ&agrave;n &ocirc;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;C&ograve;n muốn trốn sao?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">B&ecirc;n tai truyền tới thanh &acirc;m l&agrave;m cho người ta rợn cả t&oacute;c g&aacute;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Theo bản năng muốn sống, Diệp Oản Oản lập tức lắc đầu một c&aacute;i.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cũng kh&ocirc;ng biết người đ&agrave;n &ocirc;ng c&oacute; tin c&ocirc; hay kh&ocirc;ng, &aacute;nh mắt ở tr&ecirc;n mặt của c&ocirc; dừng lại một c&aacute;i, chợt k&eacute;o c&ocirc; đến h&ocirc;n m&ocirc;i của c&ocirc;, cằm của c&ocirc;, cổ...</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hơi thở n&oacute;ng bỏng th&ocirc; nặng ch&ocirc;n ở trong cổ của c&ocirc;, t&ugrave;y thời khắc đều c&oacute; thể tản ra t&iacute;n hiệu nguy hiểm.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Diệp Oản Oản giống như nai con bị cắn, kh&ocirc;ng d&aacute;m l&agrave;m một cử động nhỏ n&agrave;o.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kh&ocirc;ng biết qua bao l&acirc;u, người đ&agrave;n &ocirc;ng rốt cuộc thả lỏng c&ocirc; ra.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Một gi&acirc;y kế tiếp, Diệp Oản Oản đ&aacute;y mắt đụng v&agrave;o một h&igrave;nh ảnh v&ocirc; c&ugrave;ng kiều diễm.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Người đ&agrave;n &ocirc;ng lấy th&acirc;n thể nửa trần xuống giường, tia phản quang buộc v&ograve;ng th&acirc;n thể thon d&agrave;i c&ugrave;ng h&ocirc;ng gầy của anh.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cảnh sắc như vậy chỉ c&oacute; một c&aacute;i chớp mắt, người đ&agrave;n &ocirc;ng liền nhặt quần &aacute;o ở b&ecirc;n m&eacute;p giường, những ng&oacute;n tay thon d&agrave;i cẩn thận c&agrave;i chiếc n&uacute;t &aacute;o sơ mi được mang tới vi&ecirc;n thứ nhất.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">R&otilde; r&agrave;ng trước đ&acirc;y c&ograve;n t&agrave;n bạo giống như d&atilde; th&uacute;, vậy m&agrave; giờ khu&ocirc;n mặt lại tuấn mĩ như vậy, lạnh như băng kh&ocirc;ng nhiễm một tia kh&oacute;i lửa nh&acirc;n gian.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cho đến khi thanh &acirc;m đ&oacute;ng cửa vang l&ecirc;n, d&acirc;y thần kinh căng thẳng cuối c&ugrave;ng sắp g&atilde;y mất của Diệp Oản Oản mới bu&ocirc;ng lỏng xuống.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Rốt cuộc c&oacute; thể xử l&yacute; t&igrave;nh cảnh của ch&iacute;nh m&igrave;nh v&agrave;o l&uacute;c n&agrave;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc; chậm r&atilde;i đ&aacute;nh gi&aacute; trang tr&iacute; bốn ph&iacute;a, c&ugrave;ng với ch&iacute;nh m&igrave;nh ở trong gương tr&ecirc;n b&agrave;n trang điểm đối diện&mdash;&mdash;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc; g&aacute;i trong gương m&ocirc;i son m&agrave;u đen bị gậm nhắm đến chỉ c&ograve;n s&oacute;t lại một ch&uacute;t &aacute;m sắc, phấn trang điểm tr&ecirc;n mặt đ&atilde; bị nước mắt v&agrave; mồ h&ocirc;i tẩy đi, th&acirc;n thể phủ đầy dấu t&iacute;m bầm c&ugrave;ng vết h&ocirc;n, cuối c&ugrave;ng l&agrave; một mảng h&igrave;nh xăm m&aacute;u tanh kinh khủng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bộ d&aacute;ng n&agrave;y, r&otilde; r&agrave;ng l&agrave; c&ocirc; khi hai mươi tuổi!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Khi đ&oacute;, v&igrave; muốn n&eacute; tr&aacute;nh Tư Dạ H&agrave;n, c&ocirc; đ&atilde; cố &yacute; đem ch&iacute;nh m&igrave;nh biến th&agrave;nh c&aacute;i bộ dạng xấu x&iacute; ch&aacute;n gh&eacute;t n&agrave;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc; sống lại... Thật sống lại...</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Trong ph&uacute;t chốc, sự sợ h&atilde;i to lớn v&agrave; tuyệt vọng cơ hồ l&agrave;m c&ocirc; h&iacute;t thở kh&ocirc;ng th&ocirc;ng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tại sao...</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tại sao lại muốn cho c&ocirc; trở lại bảy năm trước!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cho d&ugrave; chết, c&ocirc; cũng kh&ocirc;ng muốn lại trở lại nơi n&agrave;y, trở lại b&ecirc;n t&ecirc;n ma quỷ n&agrave;y b&ecirc;n người.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc; nhớ r&otilde;, đ&acirc;y l&agrave; lần đầu ti&ecirc;n c&ocirc; c&ugrave;ng Tư Dạ H&agrave;n ph&aacute;t sinh quan hệ, m&agrave; sau đ&oacute; v&ocirc; số buổi tối, n&agrave;ng đều bị h&agrave;nh hạ đến sống kh&ocirc;ng bằng chết.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc; bị nhốt ở nơi n&agrave;y, mất đi người y&ecirc;u, th&acirc;n nh&acirc;n, mất đi t&ocirc;n nghi&ecirc;m, mất đi tự do, mất đi hết thảy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hết thảy c&aacute;c thứ n&agrave;y, chẳng lẽ c&ocirc; c&ograve;n phải lần nữa trải qua một lần nữa?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-ce874480-7fff-274d-1e2a-8275e34b3ff8\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kh&ocirc;ng, nếu &ocirc;ng trời cho c&ocirc; cơ hội l&agrave;m lại một lần nữa, c&ocirc; nhất định phải thay đổi hết thảy mọi thứ!</span></span></p>\r\n<div>\r\n	&nbsp;</div>\r\n', 'Phê Kun', '2020-10-23 02:12:08', 1, 0, 2);
INSERT INTO `chapters` (`id`, `name`, `slug`, `site_title`, `meta_desc`, `meta_key`, `story_id`, `image_link`, `show_img`, `content`, `author`, `created`, `status`, `ordering`, `view`) VALUES
(218, 'Chương 2: Nuốt được đến miệng', 'chuong-2-nuot-djuoc-djen-mieng', NULL, NULL, NULL, 51, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Edit: V&acirc;n Anh</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">A...</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng m&agrave;, thay đổi, c&ocirc; phải thay đổi như thế n&agrave;o đ&acirc;y?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tư Dạ H&agrave;n quyền thế ng&uacute;t trời, b&oacute;p chết c&ocirc; c&ograve;n dễ hơn so với b&oacute;p chết một con kiến, thứ m&agrave; anh ta đ&atilde; muốn muốn, tuyệt đối kh&ocirc;ng c&oacute; g&igrave; l&agrave; kh&ocirc;ng thể chiếm được.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Diệp Oản Oản h&iacute;t s&acirc;u một hơi, bức lui sợ h&atilde;i trong th&acirc;n thể đối với người đ&agrave;n &ocirc;ng kia.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhất định sẽ c&oacute; biện ph&aacute;p!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&Iacute;t nhất c&ocirc; đ&atilde; kh&ocirc;ng phải l&agrave; thiếu nữ ngu ngốc, ngu xuẩn của bảy năm trước mặc cho người kh&aacute;c định đoạt.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Trời ơi! Oản Oản...&quot; B&ecirc;n tai đột nhi&ecirc;n truyền tới một tiếng k&ecirc;u k&iacute;ch động.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nghe được c&aacute;i &acirc;m thanh quen thuộc n&agrave;y, sống lưng Diệp Oản Oản nhất thời cứng đờ, &aacute;nh mắt lạnh như băng thẳng hướng cửa nh&igrave;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ngay sau đ&oacute;, liền thấy được gương mặt m&agrave; c&ocirc; đến chết cũng kh&ocirc;ng thể n&agrave;o qu&ecirc;n được.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Gương mặt quyến rũ m&ecirc; người để cho c&ocirc; kh&ocirc;ng ch&uacute;t n&agrave;o đề ph&ograve;ng &mdash;&mdash;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ch&iacute;nh l&agrave; người bạn th&acirc;n nhất của c&ocirc; v&agrave;o kiếp trước!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Oản Oản, Tư thiếu anh ta tại sao c&oacute; thể đối với cậu l&agrave;m như vậy!&quot; Thẩm Mộng Kỳ x&ocirc;ng tới, nắm tay của c&ocirc;, &aacute;nh mắt khiếp sợ rơi v&agrave;o đệm giường xốc xếch c&ugrave;ng th&acirc;n thể tr&agrave;n đầy dấu vết t&iacute;m bầm của c&ocirc;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đ&ocirc;i mắt của Diệp Oản Oản, nh&igrave;n b&agrave;n tay của m&igrave;nh bị b&oacute;p đến đau nhức, lần n&agrave;y, c&ocirc; kh&ocirc;ng c&oacute; bỏ lỡ đ&aacute;y mắt của Thẩm Mộng Kỳ l&oacute;e l&ecirc;n một tia căm gh&eacute;t c&ugrave;ng &yacute; cười tr&ecirc;n nổi đau của người kh&aacute;c.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Oản Oản, cậu l&agrave;m sao vậy? Cậu kh&ocirc;ng sao chứ! Oản Oản cậu đừng l&agrave;m m&igrave;nh sợ nha!&quot; Thẩm Mộng Kỳ thấy &aacute;nh mắt của Diệp Oản Oản quỷ dị, cho l&agrave; c&ocirc; bị đả k&iacute;ch, n&oacute;ng nảy kh&ocirc;ng dứt hỏi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Diệp Oản Oản thản nhi&ecirc;n m&agrave; r&uacute;t tay m&igrave;nh về, lắc đầu: &quot;Kh&ocirc;ng việc g&igrave;.&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Năm đ&oacute;, việc c&ocirc; bị Tư Dạ H&agrave;n h&agrave;nh hạ đến thảm như vậy, cũng kh&ocirc;ng thể n&agrave;o bỏ qua c&ocirc;ng lao của Thẩm Mộng Kỳ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Những chuyện của c&ocirc; v&agrave; Tư Dạ H&agrave;n, c&ocirc; kh&ocirc;ng d&aacute;m n&oacute;i cho bất luận k&igrave; ai biết, chỉ n&oacute;i cho một m&igrave;nh Thẩm Mộng Kỳ, coi c&ocirc; ta l&agrave; người bạn m&agrave; bản th&acirc;n t&iacute;n nhiệm nhất, cũng kh&ocirc;ng biết...</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thẩm Mộng Kỳ lại đối với Tư Dạ H&agrave;n tồn t&acirc;m tư như vậy, đ&atilde; sớm mơ ước tới vị tr&iacute; Tư thiếu phu nh&acirc;n, ngo&agrave;i mặt l&agrave; đang gi&uacute;p c&ocirc;, nhưng thật ra l&agrave; lợi dụng c&ocirc; để đến gần Tư Dạ H&agrave;n, hơn nữa khắp nơi khi&ecirc;u kh&iacute;ch quan hệ giữa c&ocirc; v&agrave; Tư Dạ H&agrave;n, để cho mỗi ng&agrave;y c&ocirc; đều bị Tư Dạ H&agrave;n h&agrave;nh hạ dưới cơn thịnh nộ đến sống kh&ocirc;ng bằng chết.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">M&agrave; c&ocirc; thậm ch&iacute; kh&ocirc;ng ph&aacute;t gi&aacute;c ra g&igrave; cả, m&agrave; c&ograve;n cảm tạ &acirc;n đức Thẩm Mộng Kỳ v&igrave; bản th&acirc;n &quot; Xuất mưu b&agrave;y kế&quot;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nh&igrave;n lấy ch&iacute;nh m&igrave;nh trong gương, Diệp Oản Oản kh&ocirc;ng khỏi cười khổ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thật ra th&igrave; ch&iacute;nh Thẩm Mộng Kỳ thay c&ocirc; nghĩ ra c&aacute;i chủ &yacute; &quot;Giả xấu&quot; n&agrave;y, m&agrave; c&ocirc; lại c&ograve;n đồng &yacute;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bất kể người kh&aacute;c thấy thế n&agrave;o, chỉ cần c&oacute; thể để cho Tư Dạ H&agrave;n ch&aacute;n gh&eacute;t c&ocirc; liền l&agrave;m tất cả.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng m&agrave;, c&ocirc; như thế n&agrave;o cũng kh&ocirc;ng ngờ tới, ngay cả khi c&ocirc; trong c&aacute;i bộ d&aacute;ng như quỷ n&agrave;y, Tư Dạ H&agrave;n đều đang c&oacute; thể nuốt được đến miệng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Cậu như vậy rồi c&ograve;n kh&ocirc;ng sao! Oản Oản, cậu đừng lo lắng, m&igrave;nh nhất định sẽ gi&uacute;p cho cậu!&quot; Thẩm Mộng Kỳ l&agrave;m bộ d&aacute;ng t&igrave;nh th&acirc;m nghĩa trọng.</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Diệp Oản Oản trong l&ograve;ng d&acirc;ng l&ecirc;n một c&aacute;i nụ cười lạnh, a, quả nhi&ecirc;n lại đến rồi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kiếp trước Thẩm Mộng Kỳ cũng l&agrave; như vậy, giả bộ trượng nghĩa m&agrave; nghĩ c&aacute;ch gi&uacute;p c&ocirc; rời đi, c&ograve;n cố &yacute; đi t&igrave;m Cố Việt Trạch hỗ trợ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kết quả cuối c&ugrave;ng l&agrave;, Thẩm Mộng Kỳ đ&atilde; sớm l&uacute;c kh&ocirc;ng c&oacute; ai b&aacute;n đứng c&ocirc;, đem tin tức c&ocirc; muốn c&ugrave;ng Cố Việt Trạch &quot;Bỏ trốn &quot; n&oacute;i cho Tư Dạ H&agrave;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chuyện n&agrave;y cũng khiến cho quan hệ giữa c&ocirc; c&ugrave;ng Tư Dạ H&agrave;n c&agrave;ng th&ecirc;m gay gắt, khiến cho t&iacute;nh t&igrave;nh của Tư Dạ H&agrave;n c&agrave;ng n&oacute;ng nảy kh&oacute; chịu, đối với ham muốn chiếm đoạt l&agrave;m của ri&ecirc;ng c&agrave;ng đ&aacute;ng sợ hơn, m&agrave; c&ocirc; cũng ho&agrave;n to&agrave;n mất đi tự do...</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lấy năng lực của c&ocirc; b&acirc;y giờ, c&ugrave;ng Tư Dạ H&agrave;n cứng đối cứng kh&ocirc;ng kh&aacute;c n&agrave;o đi t&igrave;m chết.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Hết lần n&agrave;y tới lần kh&aacute;c kiếp trước c&ocirc; lại quật cường đến ch&iacute;n tr&acirc;u t&aacute;m hổ đều k&eacute;o kh&ocirc;ng trở lại, đối với lời n&oacute;i của Thẩm Mộng Kỳ th&igrave; n&oacute;i g&igrave; nghe nấy, một lần lại một lần m&agrave; bể đầu chảy m&aacute;u.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Th&ugrave; lớn của Diệp gia chưa trả, ba mẹ c&ograve;n đang chờ c&ocirc; về nh&agrave;, c&ocirc; c&ograve;n muốn đem anh trai từ tr&ecirc;n kỳ đồ* k&eacute;o trở về, chuyện c&ocirc; cần l&agrave;m c&ograve;n rất nhiều.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">(*) m&igrave;nh cũng kh&ocirc;ng biết kỳ đồ l&agrave; g&igrave; nữa, ai biết nhớ n&oacute;i để m&igrave;nh chỉnh sửa nha.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nhưng điều kiện đầu ti&ecirc;n l&agrave; c&ocirc; phải lấy lại được tự do.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc; sẽ l&agrave;m dịu đi quan h&ecirc; giữa m&igrave;nh v&agrave; Tư Dạ H&agrave;n, tuyệt đối kh&ocirc;ng thể l&agrave;m tiếp bất kỳ chuyện g&igrave; c&oacute; khả năng chọc giận anh ta, tự tạo cho m&igrave;nh một địch nh&acirc;n đ&aacute;ng sợ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Oản Oản, cậu nhớ đợi m&igrave;nh!&quot; Thẩm Mộng Kỳ tự m&igrave;nh n&oacute;i với c&ocirc; một đống lời n&oacute;i hư t&igrave;nh giả &yacute;, liền rời đi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Sau khi Thẩm Mộng Kỳ rời đi, tr&ecirc;n vẻ mặt bất lực yếu ớt của Diệp Oản Oản nhất thời h&oacute;a th&agrave;nh lạnh l&ugrave;ng c&ugrave;ng chết lặng.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Mới vừa giựt gi&acirc;y c&ocirc; chạy trốn một lần kh&ocirc;ng được, lại bắt đầu b&agrave;y ra lần thứ hai, xem ra l&agrave; kh&ocirc;ng đưa c&ocirc; v&agrave;o chỗ chết l&agrave; kh&ocirc;ng bỏ qua.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Vậy th&igrave; nh&igrave;n một ch&uacute;t...</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-d972d841-7fff-cd88-67c8-4dd1dcda819a\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lần n&agrave;y rốt cuộc mới l&agrave; ai chết!</span></span></p>\r\n<div>\r\n	&nbsp;</div>\r\n', 'Phê Kun', '2020-10-23 02:12:46', 1, 0, 2),
(219, 'Chương 3: Vị hôn phu trước', 'chuong-3-vi-hon-phu-truoc', NULL, NULL, NULL, 51, '', 0, '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Edit: V&acirc;n Anh</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đảo mắt đ&atilde; l&agrave; ba ng&agrave;y sau.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Ba ng&agrave;y n&agrave;y, Diệp Oản Oản phần lớn thời gian đều l&agrave; ngủ v&agrave; sửa sang lại tr&iacute; nhớ của kiếp trước.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tư Dạ H&agrave;n c&ugrave;ng kiếp trước vẫn như thế, suốt ba ng&agrave;y cũng kh&ocirc;ng c&oacute; xuất hiện, từng người trong ph&ograve;ng đều v&ugrave;i đầu v&agrave;o l&agrave;m việc, n&oacute;i chuyện với c&ocirc; cực &iacute;t, thậm ch&iacute; ngay cả &aacute;nh mắt cũng kh&ocirc;ng d&aacute;m c&ugrave;ng với c&ocirc; đối mặt.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nh&agrave; ở lớn như vậy, giống như bị bỏ hoang.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Diệp Oản Oản thay đổi quần &aacute;o ngủ, liếc nh&igrave;n thời gian tr&ecirc;n đồng hồ, ngay sau đ&oacute; xuống lầu hướng trong s&acirc;n đi tới.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tối nay &aacute;nh trăng rất tốt, gi&oacute; m&aacute;t hiu hiu, để cho c&aacute;i nh&agrave; t&ugrave; &acirc;m trầm trong tr&iacute; nhớ cũng kh&ocirc;ng đ&aacute;ng sợ tới như vậy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thật ra th&igrave;, cảnh tr&iacute; của ng&ocirc;i nh&agrave; rất đẹp, d&ugrave; sao cũng l&agrave; Tư Dạ H&agrave;n tự m&igrave;nh thiết kế, c&ograve;n mời cả đo&agrave;n đội kiến tr&uacute;c cấp thế giới, ở vị tr&iacute; phong thủy bảo địa tốt nhất Đế Đ&ocirc;, hao tốn suốt năm năm thời gian mới ho&agrave;n to&agrave;n l&agrave;m xong.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chỉ tiếc, kiếp trước đừng n&oacute;i thưởng thức, đối với c&aacute;i nh&agrave; t&ugrave; nhốt bản th&acirc;n, c&ocirc; chỉ c&oacute; căm gh&eacute;t, chỉ muốn ho&agrave;n to&agrave;n đem n&oacute; ph&aacute; hủy.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đập v&agrave;o mắt liền c&oacute; thể thấy, một mảng lớn cỏ c&acirc;y bị thi&ecirc;u hủy, cố &yacute; k&eacute;o cắt to&agrave;n bộ vườn hoa, c&aacute;i ao cũng l&agrave; một mảnh đục ngầu... Những thứ n&agrave;y đều l&agrave; &quot;Kiệt t&aacute;c&quot; của c&ocirc;.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&quot;Oản Oản &mdash;&mdash; &quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&uacute;c n&agrave;y, một thanh &acirc;m quen thuộc đột ngột vang l&ecirc;n ở trong gi&oacute; đ&ecirc;m.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Diệp Oản Oản thu hồi &aacute;nh mắt tr&ecirc;n một mảnh hoa hồng kh&ocirc; h&eacute;o, thuận theo phương hướng &acirc;m thanh truyền tới ngước mắt nh&igrave;n.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chỉ thấy đối diện &aacute;nh s&aacute;ng, người đ&agrave;n &ocirc;ng mặc một bộ &acirc;u phục xa hoa đắt tiền được chế t&aacute;c ri&ecirc;ng, mặt mũi th&acirc;m th&uacute;y tuấn dật, kh&iacute; độ bất ph&agrave;m.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kh&ocirc;ng khỏi kh&ocirc;ng thừa nhận, chỉ cần gương mặt c&ugrave;ng một th&acirc;n kh&iacute; độ n&agrave;y, Cố Việt Trạch quả thật c&oacute; đem c&ocirc; m&ecirc; đến thần hồn đi&ecirc;n đảo.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Chẳng qua, nếu l&agrave; c&ugrave;ng so với t&ecirc;n y&ecirc;u nghiệt Tư Dạ H&agrave;n, trong nh&aacute;y mắt liền lộ ra nhạt nhẽo rồi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cố Việt Trạch đứng c&aacute;ch xa c&ocirc; mấy bước, nh&igrave;n c&ocirc; một c&aacute;i, ch&acirc;n m&agrave;y nhất thời kh&ocirc;ng dễ ph&aacute;t hiện m&agrave; nh&iacute;u lại.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Diệp Oản Oản tự nhi&ecirc;n ph&aacute;t hiện c&aacute;i phản ứng nhỏ x&iacute;u n&agrave;y của anh ta, v&igrave; vậy đ&ocirc;i mắt liếc nh&igrave;n lối ăn mặc của m&igrave;nh l&uacute;c n&agrave;y.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Giờ ph&uacute;t n&agrave;y c&ocirc; vẫn mặc một bộ trang phục chủ lưu kim loại nặng, tr&ecirc;n mặt vẽ n&ugrave;ng trang h&ugrave; chết người.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Bởi v&igrave; trong tủ treo căn bản kh&ocirc;ng c&oacute; quần &aacute;o b&igrave;nh thường, v&igrave; vậy c&ocirc; mới thẳng thắn trực tiếp giữ vững h&igrave;nh tượng trước, cũng coi l&agrave; tr&aacute;nh cho ch&iacute;nh m&igrave;nh trong thời gian ngắn thay đổi qu&aacute; lớn, đưa tới ho&agrave;i nghi.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cố Việt Trạch &aacute;nh mắt lạnh l&ugrave;ng nh&igrave;n chằm chằm c&ocirc;, tr&ecirc;n khu&ocirc;n mặt anh tuấn tr&agrave;n đầy thất vọng, &quot;Oản Oản! Em l&agrave;m sao c&oacute; thể như thế cam chịu ch&igrave;m v&agrave;o đọa lạc, đưa đi l&ecirc;n cửa mặc cho người ta l&agrave;m nhục!&quot;</span></span></p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cam chịu ch&igrave;m v&agrave;o đọa lạc?</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Diệp Oản Oản cẩn thận tỉ mỉ lấy bốn chữ n&agrave;y, l&ograve;ng tr&agrave;n đầy tự giễu.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kiếp trước, cả tr&aacute;i tim của c&ocirc; đều đặt ở đ&acirc;y tr&ecirc;n người của anh ta, v&igrave; để c&oacute; thể bảo vệ th&acirc;n thể trong sạch, kh&ocirc;ng tiếc đem m&igrave;nh biến th&agrave;nh c&aacute;i bộ d&aacute;ng người kh&ocirc;ng ra người quỷ kh&ocirc;ng ra quỷ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kết quả, chỉ đổi được một c&acirc;u &quot;Ch&igrave;m v&agrave;o đọa lạc&quot; của anh ta.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc; biết Thẩm Mộng Kỳ nhất định sẽ kh&ocirc;ng ở trước mặt Cố Việt Trạch n&oacute;i lời khen cho c&ocirc;, nhưng nếu anh ta c&ograve;n c&oacute; một ch&uacute;t t&igrave;nh &yacute; với c&ocirc;, th&igrave; cũng kh&ocirc;ng thể chỉ tin tưởng lời n&oacute;i một b&ecirc;n của Thẩm Mộng Kỳ.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nghĩ tới đ&acirc;y, Diệp Oản Oản đang muốn mở miệng, sống lưng đột nhi&ecirc;n d&acirc;ng l&ecirc;n một trận lạnh lẽo.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tư Dạ H&agrave;n...</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">L&uacute;c n&agrave;y anh ta đang ở phụ cận gần đ&acirc;y!</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">C&ocirc; cơ hồ l&agrave; theo bản năng ph&aacute;t gi&aacute;c được hơi thở của người đ&agrave;n &ocirc;ng kia.</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Kiếp trước c&ocirc; căn bản cũng kh&ocirc;ng biết Tư Dạ H&agrave;n ngay tại đ&acirc;y &acirc;m thầm nh&igrave;n thấy, nghi&ecirc;m nghi&ecirc;m thật thật cắm sừng cho Tư Dạ H&agrave;n, khiến cho tất cả cơn &aacute;c mộng của c&ocirc; bắt đầu...</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Diệp Oản Oản nhẹ nh&agrave;ng thở ph&agrave;o một c&aacute;i, cưỡng &eacute;p l&agrave;m cho bản th&acirc;n qu&ecirc;n đi sự tồn tại của Tư Dạ H&agrave;n, &aacute;nh mắt l&atilde;nh đạm nh&igrave;n về ph&iacute;a Cố Việt Trạch, khẽ cười một tiếng n&oacute;i, &quot;Kh&ocirc;ng biết Cố c&ocirc;ng tử b&acirc;y giờ l&agrave; lấy th&acirc;n phận g&igrave; chất vấn t&ocirc;i đ&acirc;y? Vị h&ocirc;n phu trước của t&ocirc;i? Hay l&agrave;... Chồng của chị họ t&ocirc;i?&quot;</span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\">\r\n	<span id=\"docs-internal-guid-78408485-7fff-34a8-17cb-2308d12f7798\"><span style=\"font-size: 14pt; font-family: Nunito, sans-serif; color: rgb(43, 43, 43); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nghe được r&otilde; r&agrave;ng Diệp Oản Oản giễu cợt hỏi ngược lại, sắc mặt Cố Việt Trạch trầm xuống, &quot;Oản Oản, anh bi&ecirc;́t em hận anh, nhưng anh cũng l&agrave; th&acirc;n bất do kỉ, v&ocirc; luận như thế n&agrave;o, em biến th&agrave;nh h&ocirc;m nay c&aacute;i bộ d&aacute;ng n&agrave;y, anh cũng c&oacute; một phần tr&aacute;ch nhiệm, em b&acirc;y giờ lập tức theo anh đi, anh đưa em rời đi Đế đ&ocirc;!&quot;</span></span></p>\r\n<div>\r\n	&nbsp;</div>\r\n', 'Phê Kun', '2020-10-23 02:13:14', 1, 0, 4),
(220, 'Chương 7: Anh thợ sửa điện phần 2', 'chuong-7-anh-tho-sua-djien-phan-2', ' Bạn đang đọc truyện chuyện thường ở huyện Chap 7 Tiếng Việt tại website cafesuanovel.com', 'Đọc truyện chuyện thường ở  tác giả Cà Di  Chap 7 Tiếng Việt bản dịch full mới nhất, ảnh đẹp chất lượng cao, đọc miễn phí, cập nhật nhanh và sớm nhất tại website cafesuanovel.com.', ' cafesuanovel.com, chuyện thường ở huyện, Cà Di', 15, 'anh-tho-sua-dien-p2.png', 1, '', 'Cà Di', '2020-10-23 04:08:22', 1, 0, 5);

